/*Table structure for table `os_content` */

DROP TABLE IF EXISTS `os_content`;

CREATE TABLE `os_content` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '',
  `alias` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '',
  `introtext` longtext NOT NULL,
  `fulltext` text NOT NULL,
  `state` tinyint(3) NOT NULL DEFAULT '1',
  `catid` int(10) unsigned NOT NULL DEFAULT '0',
  `created` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `created_by` int(10) unsigned NOT NULL DEFAULT '0',
  `modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `modified_by` int(10) unsigned NOT NULL DEFAULT '0',
  `publish_up` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `publish_down` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `ordering` int(11) NOT NULL DEFAULT '0',
  `metakey` text NOT NULL,
  `metadesc` text NOT NULL,
  `hits` int(10) unsigned NOT NULL DEFAULT '0',
  `featured` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `images` varchar(250) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_state` (`state`),
  KEY `idx_catid` (`catid`),
  KEY `idx_createdby` (`created_by`),
  KEY `idx_featured_catid` (`featured`,`catid`)
) ENGINE=InnoDB AUTO_INCREMENT=26 DEFAULT CHARSET=utf8;

/*Data for the table `os_content` */

insert  into `os_content`(`id`,`title`,`alias`,`introtext`,`fulltext`,`state`,`catid`,`created`,`created_by`,`modified`,`modified_by`,`publish_up`,`publish_down`,`ordering`,`metakey`,`metadesc`,`hits`,`featured`,`images`) values (1,'জাতীয় সম্পদ রক্ষা সম্পর্কে','about-us','<p>প্রকাশক<br />\r\nপ্রকৌশলী শেখ মুহাম্মদ শহীদুল্লাহ<br />\r\nউপদেষ্টা<br />\r\nআবুল কাসেম ফজলুল হক<br />\r\nসম্পাদক<br />\r\nম. ভূইয়া<br />\r\nকারিগরি সহায়তা<br />\r\nরাফি খান<br />\r\nঅক্ষর বিন্যাস<br />\r\nডি. এম সরোয়ার<br />\r\nসহযোগিতায়<br />\r\nসজিব<br />\r\nরাজন<br />\r\nপ্রচ্ছদ<br />\r\nশাহাবুদ্দিন<br />\r\nমুদ্রণে<br />\r\nএস. এস. প্রিন্টার্স<br />\r\nপুরানা পল্টন, ঢাকা</p>\r\n','',1,1,'2013-01-30 00:00:00',1,'2016-01-06 21:03:22',1,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',1,1,NULL),(2,'জাতীয় সম্পদ রক্ষা – সম্পাদকীয়','services','<p>সাম্রাজ্যবাদের সেবাদাস ধনিক শ্রেণীর প্রতিনিধি শাসকগোষ্ঠী পালাবদল করে গত ৪০ বছর ধরে জনগণের সঙ্গে ভাঁওতাবাজি করে ক্ষমতায় আসীন হয়েছে ও সাধারণভাবে দরিদ্র জনগণকে শোষণ নির্যাতন করা ছাড়াও দেশের খনিজ সম্পদ অসম চুক্তির মাধ্যমে সাম্রাজ্যবাদী বহুজাতিক কোম্পানিগুলির দ্বারা অবারিত লুণ্ঠন করার সুযোগ করে দিয়েছে ও দিচ্ছে সামান্য অবৈধ প্রাপ্তির বিনিময়ে। এভাবে একদিকে তারা জনগণের সম্পদ নিঃশেষ করে অন্যদিকে দেশের সার্বভৌমত্ব হরণ করে। খনিজ সম্পদ অনুসদ্ধান ও উত্তোলনে জাতীয় স্বার্থে সদ্ব্যবহারে জন্য জাতীয় সংস্থার সক্ষমতা বিকাশের সুযোগ রুদ্ধ করে দেয় ও বহুজাতিক কোম্পানির তথা সাম্রাজ্যবাদের কাছে আত্মসমর্পণে বাধ্য করে।<br />\r\nনির্লজ্জ দলীয়করণের মাধ্যমে শিক্ষায়, ব্যবসায় ও প্রশাসনে যে ভাবে স্বার্থপর প্রতারক শ্রেণীকে ঢুকানো হচ্ছে এতে মানুষ মানবতার বিরুদ্ধে চলে যাচ্ছে। মানবতার শ্রেষ্ঠ সম্পদ সৎ মানুষের সংখ্যা দিন দিন কমছে, শিক্ষিত প্রতারকেরা, খারাপ মানুষেরা সমাজ রাষ্ট্রকে এমন ভাবে দখল করে রেখেছে সেখান থেকে বেরিয়ে এসে জনগণের ক্ষমতায়ন ধারাবাহিক আন্দোলন সর্বশেষে বিপ্লব ছাড়া সম্ভব নয়। ধনীক শ্রেণীর সরকার দুর্নীতিবাজ, ঘুষখোর, অসৎ, দলবাজ ও তোষামোদকারীদের পৃষ্ঠপোষকতা দান করে। দরকার সৎ দেশপ্রেমিক সরকারের।</p>\r\n\r\n<p>এ পত্রিকাটি জাতীয় সম্পদ রক্ষার ক্ষেত্রে তেল-গ্যাস খনিজ সম্পদ ও বিদ্যুৎ-বন্দর রক্ষা জাতীয় কমিটিকে দৃঢ়ভাবে ও সক্রিয়ভাবে সমর্থন দেয়। জাতীয় সম্পদ লুটপাটের বিরুদ্ধে মাঠে ময়দানের মিছিলে দেশপ্রেমিকদের পক্ষে অবস্থান নেয়। জাতীয় স্বার্থে দুর্নীতিবাজ, লুটপাটকারী, বিদেশি এজেন্ট, লুটেরা ও তোষামোদকারীদের বিরুদ্ধে স্পষ্ট অবস্থান নেয়। পত্রিকাটি বিশ্বাস করে আন্দোলন সংগ্রামের মাধ্যমে জনগণকে সচেতন করে কাক্সিক্ষত বাংলাদেশ প্রতিষ্ঠা এবং জাতীয় সম্পদ রক্ষা করা সম্ভব।</p>\r\n\r\n<p>আন্দোলন সংগ্রামে যুগ যুগ ধরে মেহনতি মানুষের জয় হয়েছে-লুটেরাদের পরাজয় হয়েছে। তাই বাংলাদেশকে ব্যবসায়ী ধনিক শ্রেণীর স্বার্থরক্ষক রাজনীতির হাত থেকে রক্ষা করার জন্য জনগণকে ঐক্যবদ্ধভাবে সংগঠিত থাকার আহ্বান জানাচ্ছে। পথ যতই কঠিন হোক এই লড়াই অবধারিত। বাংলাদেশের জাতীয় সম্পদকে রক্ষার জন্য দরকার গণঐক্যের। বন্ধুরা আন্দোলনের পথ আমাদের পথ। এ আন্দোলন দিয়ে ব্যর্থ করে দিতে হবে বাংলাদেশবিরোধী ষড়যন্ত্র ও জনবিরোধী কার্যক্রম। লুটেরাদের চূড়ান্ত পরাজয় পর্যন্ত আন্দোলন চলবে জাতীয় সম্পদ রক্ষার স্বার্থে। জনগণের স্বার্থেই আমাদের নতুন রাজনৈতিক নেতৃত্ব গড়ে তুলতে হবে।</p>\r\n','',1,14,'2013-01-31 00:00:00',1,'2016-01-06 21:53:27',1,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,1,'1452093372_cover1.jpg'),(3,'প্রাকৃতিক সম্পদ রক্ষার সংগ্রাম','প্রাকৃতিক-সম্পদ-রক্ষার-সংগ্রাম','<p style=\"text-align:justify\"><span style=\"font-size:16px\">আজকের অনুষ্ঠানে প্রধান অতিথি হিসাবে আমাকে সম্মানিত করার জন্য আমি গণস্বাস্থ্য কেন্দ্র কর্তৃপক্ষকে আন্তরিক ধন্যবাদ জানাচ্ছি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">১৯৮২ সাল থেকে গণস্বাস্থ্য কেন্দ্রের সাথে আমার ঘনিষ্ঠ সম্পর্ক। বাংলাদেশের মানুষের স্বাস্থ্য সেবায় এবং দেশের মানুষের স্বার্থে ঔষধ নীতি প্রণয়নে গণস্বাস্থ্য কেন্দ্রের অবদান সকলের জানা। গ্যাস রপ্তানি বন্ধের সংগ্রামে গণস্বাস্থ্য কেন্দ্রের সহযোগিতার একটি ঘটনা আমি আ<br />\r\nআজকের অনুষ্ঠানে প্রধান অতিথি হিসাবে আমাকে সম্মানিত করার জন্য আমি গণস্বাস্থ্য কেন্দ্র কর্তৃপক্ষকে আন্তরিক ধন্যবাদ&nbsp;জানাচ্ছি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">১৯৮২ সাল থেকে গণস্বাস্থ্য কেন্দ্রের সাথে আমার ঘনিষ্ঠ সম্পর্ক। বাংলাদেশের মানুষের স্বাস্থ্য সেবায় এবং দেশের মানুষের স্বার্থে ঔষধ নীতি প্রণয়নে গণস্বাস্থ্য কেন্দ্রের অবদান সকলের জানা। গ্যাস রপ্তানি বন্ধের সংগ্রামে গণস্বাস্থ্য কেন্দ্রের সহযোগিতার একটি ঘটনা আমি আনন্দের সাথে স্মরণ করছি। ১৯৯৯-২০০০ সময়কালে অসত্য তথ্য ও উপাত্তের ভিত্তিতে একটি সুবিধাবাদি গোষ্ঠী বাংলাদেশের সীমিত গ্যাস সম্পদ রপ্তানির জন্য প্রচার-প্রচারণা চালিয়েছিল। সে সময় আমি এ প্রচেষ্টার বিরুদ্ধে দৈনিক অর্থনীতি নামে একটি পত্রিকায় প্রতি সপ্তাহে &lsquo;বাংলাদেশের জ্বালানি সমস্যার বিভিন্ন দিক&rsquo; শীর্ষক একটি কলাম লিখতাম। একাধারে ৬৫ সপ্তাহে লেখার পর অনেকেই আমাকে দৈনিক অর্থনীতিতে প্রকাশিত প্রবন্ধগুলি সমন্বিত করে একটি বই লেখার উপদেশ প্রদান করে। ২০০১ সালের অক্টোবর মাসে &lsquo;জ্বালানি সমস্যার বাংলাদেশ প্রেক্ষিত&rsquo; শীর্ষক একটি বই এর পাণ্ডুলিপি নিয়ে ছাপাবার চেষ্টা করছিলাম, তখন আমার লেখা বই ছাপাতে প্রকাশকরা তেমন আগ্রহী ছিলেন না। এ বিষয়টি নিয়ে ডা. জাফরুল্লাহ্ চৌধুরীর সাথে আলাপ করতে গেলে তিনি আনন্দের সাথে বইটি প্রকাশ করতে রাজি হলেন। আমি তখন তাঁকে শর্ত দিয়েছিলাম যে, মহান বিজয় দিবসের পূর্বে বইটি ছাপা শেষ করতে হবে। তিনি উক্ত শর্তে বইটি ছাপাতে রাজি হলেন। ২০০১ সালের ১৫ ডিসেম্বরে বইটি ছাপা হয়। আমি এজন্য ডা. জাফরুল্লাহ্ চৌধুরী, জনাব শফিক খান, জনাব মো: গোলাম মোস্তফা দুলাল এবং গণমুদ্রণের সংশ্লিষ্ট সকলের কাছে অত্যন্ত কৃতজ্ঞ। আমি শ্রদ্ধার সাথে তাঁদের অবদান স্মরণ করছি। পরবর্তীতে অনেকে আমাকে যখন জানিয়েছেন যে আমার বইটি বাংলাদেশ থেকে গ্যাস রপ্তানি সিদ্ধান্ত পরিবর্তনে কিছুটা হলেও অবদান রেখেছে, তখন আমি আনন্দ অনুভব করি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আমাদের ভাষা দিবসের ষাট বছর পূর্তি এবং মহান স্বাধীনতা দিবসের চল্লিশ বছর পূর্তি উপলক্ষে আয়োজিত &ldquo;প্রাকৃতিক সম্পদ রক্ষার সংগ্রাম&rdquo; শীর্ষক আজকের আলোচনা অনুষ্ঠানকে আমি অত্যন্ত তাৎপর্যপূর্ণ বলে মনে করি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আমাদের মহান স্বাধীনতা যুদ্ধের বীর শহীদদের আত্মত্যাগের বিনিময়ে আমরা আজকে স্বাধীন দেশের নাগরিক এবং মাতৃভাষা বাংলায় লেখা আমাদের সংবিধান। আমাদের সংবিধানের ৩য় অনুচ্ছেদে &ldquo;প্রজাতন্ত্রের রাষ্ট্র ভাষা বাংলা&rdquo; লেখা দেখে ভাষা শহীদের আত্মা শান্তি পাবে যে, তাদের আত্মত্যাগ স্বীকৃতি পেয়েছে। গত ৪০ বছরে নানা প্রতিকূলতা থাকা সত্ত্বেও বিভিন্ন ক্ষেত্রে আমাদের অনেক অগ্রগতি হয়েছে। ১৯৫২ সালে আমাদের ভাষা আন্দোলন, ১৯৭১ এর মহান স্বাধীনতা যুদ্ধ এবং এছাড়া বিভিন্ন সময় দেশের সাধারণ মানুষের স্বার্থ রক্ষা করতে গিয়ে যাঁরা জীবন বিসর্জন দিয়েছেন, আমি তাঁদের আত্মত্যাগ শ্রদ্ধার সাথে স্মরণ করছি এবং তাঁদের আত্মার মাগফিরাত কামনা করছি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">২ অক্টোবর, ২০০৫ তারিখে মেসার্স এশিয়া এনার্জি কর্পোরেশন (বাংলাদেশ) লি: (এইসি), সরকারের নিকট ফুলবাড়ির কয়লা উন্নয়ন প্রকল্পের স্কীম দাখিল করে। জ্বালানি ও খনিজ সম্পদ মন্ত্রণালয় উক্ত স্কীম মূল্যায়ন করে প্রতিবেদন প্রণয়ন করার জন্য প্রফেসর মো: নুরুল ইসলামকে সভাপতি করে ১৭ নভেম্বর, ২০০৫ তারিখে একটি কারিগরি কমিটি গঠন করে। মূল্যায়ন কমিটির সদস্যদের সাথে ২৬ জানুয়ারি, ২০০৬ তারিখে আমার দিনাজপুরের বড়পুকুরিয়া কয়লা খনি এবং ফুলবাড়ি এলাকা পরিদর্শনের সুযোগ হয়েছিল। সে সময় হাজার হাজার স্থানীয় জনগণ আবেগের সাথে তাদের সহায়-সম্পত্তি রক্ষা করার জন্য আমাদের কাছে যে প্রতিবাদ জানিয়েছিলেন তা আমার সারা জীবন মনে থাকবে। ২৪ সেপ্টেম্বর, ২০০৬ তারিখে সরকারের কাছে উক্ত প্রতিবেদন দাখিল করা হয়েছে।<br />\r\nদেশের প্রাকৃতিক সম্পদ রক্ষার সংগ্রামে ২০০৬ সালের ২৬ আগস্ট দিনাজপুরের ফুলবাড়িতে কয়লা সম্পদ রক্ষা করতে গিয়ে তিন জন শহীদ হয়েছেন। আমার বিশ্বাস অতীতের সকল সংগ্রামের ন্যায় তাঁদের আত্মত্যাগও ভবিষ্যতে সফলতা অর্জন করতে সক্ষম হবে। পবিত্র কোরআনের যে বানী বিশ্ব-মানবতার স্বার্থে আমাকে প্রাকৃতিক সম্পদের ব্যবহার সম্বন্ধে চিন্তা ও অনুভব করতে সহায়তা করেছে তা হলো &ldquo;যা কিছু আকাশসমূহে রয়েছে এবং যা কিছু যমীনে আছে, সব আল্লাহরই। &hellip;&hellip;.&rdquo; (সুরা বাকারাহ্ ২৮৪ আয়াত) &ldquo;নভোমণ্ডল ও ভূ-মণ্ডলে যা-কিছু-রয়েছে সবই আল্লাহর&rdquo; (সুরা লোকমান ২৬ আয়াত)।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">মহান আল্লাহতায়ালা প্রাকৃতিক সম্পদের মালিক হওয়ায় বিশ্ববাসীর সুবিধা হলো তারা তাদের প্রয়োজনে প্রাকৃতিক সম্পদ ভোগ করার সুযোগ লাভ করেছে। তবে এ বিষয়ে পবিত্র কোরআনে স্পষ্ট নির্দেশনা আছে যে, আল্লাহ অপচয়কারীদের পছন্দ করেন না (সুরা আল-আনাম ১৪১ আয়াত এবং সুরা আল-আরাফ ৩১ আয়াত) টেলিভিশনে উরংপড়াবৎু এবং ঘধঃরড়হধষ এবড়মৎধঢ়যরপ চ্যানেলে এর অনুষ্ঠান দেখলে আমার মনে হয় যে, বিশ্বব্রহ্মাণ্ডে যেসমস্ত প্রাকৃতিক সম্পদ মহান আল্লাহ সৃষ্টি করেছেন তার অত্যন্ত সীমিত অংশ এখন পর্যন্ত মানুষ আবিষ্কার করতে সক্ষম হয়েছে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আজকের সীমিত সময়ে প্রাকৃতিক সম্পদ সম্বন্ধে বিশদ আলোচনা করা আমার পক্ষে সম্ভব হবে না; আমি প্রথমে বিশ্বপ্রেক্ষাপটে কয়েকটি বিশেষ প্রাকৃতিক সম্পদ সম্বন্ধে আলোচনা করে, পরবর্তীতে বাংলাদেশের &ldquo;প্রাকৃতিক সম্পদ রক্ষার সংগ্রাম&rdquo; সম্বন্ধে আমার মতামত উপস্থাপন করছি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আমাদের প্রিয় পৃথিবীতে বসবাসকারী মানুষ, অন্যান্য প্রাণীসমূহ এবং জানা-অজানা লক্ষ-কোটি জীব-সত্তার (ষরারহম নবরহমং) জীবন ও জীবিকা সূর্য রশ্মির উপর নির্ভরশীল, কিন্তু এর অনেকের আকার ও প্রকৃতি সম্বন্ধে আমাদের ধারণা নাই। ইদানিংকালে পৃথিবীর বিভিন্ন দেশে জ্বালানি সমস্যা সমাধানের লক্ষ্যে সোলার প্যানেল স্থাপন করা হচ্ছে। ২০১০ সাল পর্যন্ত পৃথিবীতে মোট সৌর-বিদ্যুৎ উৎপাদনের ক্ষমতা ছিল ৪০,০০০ (চল্লিশ হাজার) মেগাওয়াট। সে তুলনায় আমাদের দেশে সৌর-বিদ্যুৎ উৎপাদন ছিল ৫০ মেগাওয়াট। অনেকে ধারণা করেন যে, ২০৭০ সাল নাগাদ প্রযুক্তিগত উন্নতি সাধন করে বিভিন্ন দেশ সৌর-বিদ্যুৎ উৎপাদন করে টেকসইভাবে নিজস্ব জ্বালানি চাহিদা মিটাতে সক্ষম হবে।নন্দের সাথে স্মরণ করছি। ১৯৯৯-২০০০ সময়কালে অসত্য তথ্য ও উপাত্তের ভিত্তিতে একটি সুবিধাবাদি গোষ্ঠী বাংলাদেশের সীমিত গ্যাস সম্পদ রপ্তানির জন্য প্রচার-প্রচারণা চালিয়েছিল। সে সময় আমি এ প্রচেষ্টার বিরুদ্ধে দৈনিক অর্থনীতি নামে একটি পত্রিকায় প্রতি সপ্তাহে &lsquo;বাংলাদেশের জ্বালানি সমস্যার বিভিন্ন দিক&rsquo; শীর্ষক একটি কলাম লিখতাম। একাধারে ৬৫ সপ্তাহে লেখার পর অনেকেই আমাকে দৈনিক অর্থনীতিতে প্রকাশিত প্রবন্ধগুলি সমন্বিত করে একটি বই লেখার উপদেশ প্রদান করে। ২০০১ সালের অক্টোবর মাসে &lsquo;জ্বালানি<br />\r\nসমস্যার বাংলাদেশ প্রেক্ষিত&rsquo; শীর্ষক একটি বই এর পাণ্ডুলিপি নিয়ে ছাপাবার চেষ্টা করছিলাম, তখন আমার লেখা বই ছাপাতে প্রকাশকরা তেমন আগ্রহী ছিলেন না। এ বিষয়টি নিয়ে ডা. জাফরুল্লাহ্ চৌধুরীর সাথে আলাপ করতে গেলে তিনি আনন্দের সাথে বইটি প্রকাশ করতে রাজি হলেন। আমি তখন তাঁকে শর্ত দিয়েছিলাম যে, মহান বিজয় দিবসের পূর্বে বইটি ছাপা শেষ করতে হবে। তিনি উক্ত শর্তে বইটি ছাপাতে রাজি হলেন। ২০০১ সালের ১৫ ডিসেম্বরে বইটি ছাপা হয়। আমি এজন্য ডা. জাফরুল্লাহ্ চৌধুরী, জনাব শফিক খান, জনাব মো: গোলাম মোস্তফা দুলাল এবং গণমুদ্রণের সংশ্লিষ্ট সকলের কাছে অত্যন্ত কৃতজ্ঞ। আমি শ্রদ্ধার সাথে তাঁদের অবদান স্মরণ করছি। পরবর্তীতে অনেকে আমাকে যখন জানিয়েছেন যে আমার বইটি বাংলাদেশ থেকে গ্যাস রপ্তানি সিদ্ধান্ত পরিবর্তনে কিছুটা হলেও অবদান রেখেছে, তখন আমি আনন্দ অনুভব করি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আমাদের ভাষা দিবসের ষাট বছর পূর্তি এবং মহান স্বাধীনতা দিবসের চল্লিশ বছর পূর্তি উপলক্ষে আয়োজিত &ldquo;প্রাকৃতিক সম্পদ রক্ষার সংগ্রাম&rdquo; শীর্ষক আজকের আলোচনা অনুষ্ঠানকে আমি অত্যন্ত তাৎপর্যপূর্ণ বলে মনে করি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আমাদের মহান স্বাধীনতা যুদ্ধের বীর শহীদদের আত্মত্যাগের বিনিময়ে আমরা আজকে স্বাধীন দেশের নাগরিক এবং মাতৃভাষা বাংলায় লেখা আমাদের সংবিধান। আমাদের সংবিধানের ৩য় অনুচ্ছেদে &ldquo;প্রজাতন্ত্রের রাষ্ট্র ভাষা বাংলা&rdquo; লেখা দেখে ভাষা শহীদের আত্মা শান্তি পাবে যে, তাদের আত্মত্যাগ স্বীকৃতি পেয়েছে। গত ৪০ বছরে নানা প্রতিকূলতা থাকা সত্ত্বেও বিভিন্ন ক্ষেত্রে আমাদের অনেক অগ্রগতি হয়েছে। ১৯৫২ সালে আমাদের ভাষা আন্দোলন, ১৯৭১ এর মহান স্বাধীনতা যুদ্ধ এবং এছাড়া বিভিন্ন সময় দেশের সাধারণ মানুষের স্বার্থ রক্ষা করতে গিয়ে যাঁরা জীবন বিসর্জন দিয়েছেন, আমি তাঁদের আত্মত্যাগ শ্রদ্ধার সাথে স্মরণ করছি এবং তাঁদের আত্মার মাগফিরাত কামনা করছি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">২ অক্টোবর, ২০০৫ তারিখে মেসার্স এশিয়া এনার্জি কর্পোরেশন (বাংলাদেশ) লি: (এইসি), সরকারের নিকট ফুলবাড়ির কয়লা উন্নয়ন প্রকল্পের স্কীম দাখিল করে। জ্বালানি ও খনিজ সম্পদ মন্ত্রণালয় উক্ত স্কীম মূল্যায়ন করে প্রতিবেদন প্রণয়ন করার জন্য প্রফেসর মো: নুরুল ইসলামকে সভাপতি করে ১৭ নভেম্বর, ২০০৫ তারিখে একটি কারিগরি কমিটি গঠন করে। মূল্যায়ন কমিটির সদস্যদের সাথে ২৬ জানুয়ারি, ২০০৬ তারিখে আমার দিনাজপুরের বড়পুকুরিয়া কয়লা খনি এবং ফুলবাড়ি এলাকা পরিদর্শনের সুযোগ হয়েছিল। সে সময় হাজার হাজার স্থানীয় জনগণ আবেগের সাথে তাদের সহায়-সম্পত্তি রক্ষা করার জন্য আমাদের কাছে যে প্রতিবাদ জানিয়েছিলেন তা আমার সারা জীবন মনে থাকবে। ২৪ সেপ্টেম্বর, ২০০৬ তারিখে সরকারের কাছে উক্ত প্রতিবেদন দাখিল করা হয়েছে।<br />\r\nদেশের প্রাকৃতিক সম্পদ রক্ষার সংগ্রামে ২০০৬ সালের ২৬ আগস্ট দিনাজপুরের ফুলবাড়িতে কয়লা সম্পদ রক্ষা করতে গিয়ে তিন জন শহীদ হয়েছেন। আমার বিশ্বাস অতীতের সকল সংগ্রামের ন্যায় তাঁদের আত্মত্যাগও ভবিষ্যতে সফলতা অর্জন করতে সক্ষম হবে।<br />\r\nপবিত্র কোরআনের যে বানী বিশ্ব-মানবতার স্বার্থে আমাকে প্রাকৃতিক সম্পদের ব্যবহার সম্বন্ধে চিন্তা ও অনুভব করতে সহায়তা করেছে তা হলো &ldquo;যা কিছু আকাশসমূহে রয়েছে এবং যা কিছু যমীনে আছে, সব আল্লাহরই। &hellip;&hellip;.&rdquo; (সুরা বাকারাহ্ ২৮৪ আয়াত) &ldquo;নভোমণ্ডল ও ভূ-মণ্ডলে যা-কিছু-রয়েছে সবই আল্লাহর&rdquo; (সুরা লোকমান ২৬ আয়াত)।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">মহান আল্লাহতায়ালা প্রাকৃতিক সম্পদের মালিক হওয়ায় বিশ্ববাসীর সুবিধা হলো তারা তাদের প্রয়োজনে প্রাকৃতিক সম্পদ ভোগ করার সুযোগ লাভ করেছে। তবে এ বিষয়ে পবিত্র কোরআনে স্পষ্ট নির্দেশনা আছে যে, আল্লাহ অপচয়কারীদের পছন্দ করেন না (সুরা আল-আনাম ১৪১ আয়াত এবং সুরা আল-আরাফ ৩১ আয়াত) টেলিভিশনে উরংপড়াবৎু এবং ঘধঃরড়হধষ এবড়মৎধঢ়যরপ চ্যানেলে এর অনুষ্ঠান দেখলে আমার মনে হয় যে, বিশ্বব্রহ্মাণ্ডে যেসমস্ত প্রাকৃতিক সম্পদ মহান আল্লাহ সৃষ্টি করেছেন তার অত্যন্ত সীমিত অংশ এখন পর্যন্ত মানুষ আবিষ্কার করতে সক্ষম হয়েছে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আজকের সীমিত সময়ে প্রাকৃতিক সম্পদ সম্বন্ধে বিশদ আলোচনা করা আমার পক্ষে সম্ভব হবে না; আমি প্রথমে বিশ্বপ্রেক্ষাপটে কয়েকটি বিশেষ প্রাকৃতিক সম্পদ সম্বন্ধে আলোচনা করে, পরবর্তীতে বাংলাদেশের &ldquo;প্রাকৃতিক সম্পদ রক্ষার সংগ্রাম&rdquo; সম্বন্ধে আমার মতামত উপস্থাপন করছি।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আমাদের প্রিয় পৃথিবীতে বসবাসকারী মানুষ, অন্যান্য প্রাণীসমূহ এবং জানা-অজানা লক্ষ-কোটি জীব-সত্তার (ষরারহম নবরহমং) জীবন ও জীবিকা সূর্য রশ্মির উপর নির্ভরশীল, কিন্তু এর অনেকের আকার ও প্রকৃতি সম্বন্ধে আমাদের ধারণা নাই। ইদানিংকালে পৃথিবীর বিভিন্ন দেশে জ্বালানি সমস্যা সমাধানের লক্ষ্যে সোলার প্যানেল স্থাপন করা হচ্ছে। ২০১০ সাল পর্যন্ত পৃথিবীতে মোট সৌর-বিদ্যুৎ উৎপাদনের ক্ষমতা ছিল ৪০,০০০ (চল্লিশ হাজার) মেগাওয়াট। সে তুলনায় আমাদের দেশে সৌর-বিদ্যুৎ উৎপাদন ছিল ৫০ মেগাওয়াট। অনেকে ধারণা করেন যে, ২০৭০ সাল নাগাদ প্রযুক্তিগত উন্নতি সাধন করে বিভিন্ন দেশ সৌর-বিদ্যুৎ উৎপাদন করে টেকসইভাবে নিজস্ব জ্বালানি চাহিদা মিটাতে সক্ষম হবে।</span></p>\r\n','',1,15,'2013-01-31 00:00:00',1,'2016-01-06 21:21:13',1,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452093673_jsr1-2.png'),(4,'নদী, মানুষ ও উন্নয়ন','নদী,-মানুষ-ও-উন্নয়ন','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">&ldquo;&hellip;আলো আর আকাশের থেকে নদী যতখানি আশা করে আমিও তেমন একদিন করিনি কি? শুধু একদিন তবু? কারা এসে বলে গেল: &lsquo;নেই গাছ নেই&Ntilde;রোদ নেই&Ntilde;মেঘ নেই&Ntilde;তারা নেই&Ntilde;আকাশ তোমার তরে নয়!&rdquo;<br />\r\n&rdquo;&hellip;<br />\r\nনদী কেন বেঁচে থাকে?&Ntilde; একদিন এই নদী&Ntilde; শব্দ করে হৃদয়ে বিস্ময় আনিত পারে না আর; &Ntilde;মানুষের মন থেকে নদীরা হারায়&Ntilde; শেষ হয়!&rdquo;<br />\r\n&Ntilde; নদীরা, জীবনানন্দ দাশ<br />\r\nনদী বন জঙ্গল পাহাড় সমুদ্র এবং সব প্রাণের<br />\r\nসঙ্গে আমরা মানুষেরাও যে মহাবিপর্যয়ের দিকে ধাবিত হচ্ছি তার থেকে আমাদের উদ্ধার কোথায়? বালির মতো বিশ্ব সম্মেলন অনেক কিছু বলে, কিন্তু কিছু করে না; করে না&Ntilde;কারণ যে উন্নয়ন দর্শন, যে সুবিধাভোগীদের কারণে আজ বিশ্বের প্রকৃতি ও মানুষ অস্তিত্বের সংকটে তাদের আধিপত্য বিশ্বজুড়ে এবং বাংলাদেশে এখনো প্রবল। এর দিকে মনোযোগ না দিয়ে বিশ্ব বাঁচবে না, মানুষ তো নয়ই। ঘন ঘন বন্যা, জলোচ্ছ্বাস, জলাবদ্ধতা আর সিডর দেখাচ্ছে বাংলাদেশ কীভাবে ঝুঁকির দিকে যাচ্ছে। এই প্রবন্ধে বিশেষ করে বাংলাদেশের নদী ও নদী নিয়ে<br />\r\nউন্নয়ন পরিকল্পনার ধরন ও তার ফলাফলের দিকে সবার দৃষ্টি আকর্ষণ করবো।<br />\r\nনদীর কি জীবন আছে?<br />\r\nনদীর কি ইচ্ছা&Ntilde;অনিচ্ছা আছে? নদীর কি বয়স আছে? আছে কি তার রাগ কিংবা ভালোবাসার অনুভূতি? নদী কি খুশি হয় কিংবা মন খারাপ করে? নদীর কি মৃত্যু আছে?<br />\r\nএই প্রশ্নগুলোর উত্তর কী হবে? আমি যদি সবগুলোর উত্তর না বলি তাহলে প্রথাগত বিজ্ঞান আপত্তি করবে না। যারা নদীকে দূর থেকে দেখে, বইপুস্তকে, টেলিভিশনে, চলচ্চিত্রে নদী দেখে চিন্তিত বা আনন্দিত হয় কিংবা যারা বিজ্ঞানকে যান্ত্রিকভাবে দেখে অভ্যস্ত তাদের অনেকে এসব প্রশ্ন শুনেই বিরক্ত হতে পারেন। কিন্তু আমি যদি বাংলাদেশের মানুষ, যারা নদীর সঙ্গে নিজের জীবন মিলিয়ে দেখে, যারা অজানা কাল থেকে নদীর সঙ্গে কথা বলে, নদীর সঙ্গে অভিমান করে, নদীর রাগ দেখে তাকে শান্ত করতে চেষ্টা করে, যারা নিজের স্বজনের চেয়ে নদীকে কোনো অংশে কম করে দেখে না, তারা এসব প্রশ্ন শুনলে হ্যাঁ হ্যাঁ করবে বটেই।<br />\r\nআমরা তাদের চোখ দিয়ে দেখলে নদী মানে কেবল নিস্ক্রিয় &lsquo;যে পাত্রে রাখা যায় তার আকার ধারণ করে&rsquo; সেই পানির সমাবেশ নয়। নদী তো পানি বটেই কিন্তু যখন এটি নদী, তখন কেবল পানি নয়। নদী তখন পানির চাইতে বেশি, একটি প্রবল সক্রিয় সত্তা হিসেবে হাজির হয়। আমরা তখন দেখি নদীর জন্মও হয়, মৃত্যুও হয়। একটা নদীতে সকল জীবের মতোই সব পর্বই দেখি&Ntilde; শৈশব, যৌবন, বার্ধক্য। দেখি বৃদ্ধ নদী সন্তান&Ntilde;সন্ততি রেখে যেন ক্রমে মরে যায়। শুকিয়ে যায়।<br />\r\nপ্রবাহ দেখে বোঝা যায় নদী জীবন্ত না মৃত। জীবন্ত নদীতে প্রবাহ থাকে জীবন্ত। তখন নদীর ইচ্ছা&Ntilde;অনিচ্ছা, আনন্দ&Ntilde;ক্রোধ সবই দেখা যায়। বোঝা যায় সন্তোষ&Ntilde;অসন্তোষ। আর যদি নদীতে প্রবাহ না থাকে তাহলে এসব আর কিছুই পাওয়া যায় না। তখন বোঝা যায় জীবিত আর মৃত নদীর তফাৎ। জীবন্ত নদীর জীবন শুধু একা নদীরই থাকে না। এই নদীর পানিও শুধু পানি থাকে না, এটা হয়ে দাঁড়ায় অসংখ্য প্রাণের আধার। নদী বা সমুদ্রের পানিতে যে কত প্রাণের জন্ম হয়, কত প্রাণ যে তার মধ্যে বেঁচে থাকে তার হিসাব করা দুরূহ। শুধু ভেতরে নয় বাইরেও নদীর জীবনের সঙ্গে আরো অনেক জীবন জড়িয়ে থাকে। থাকে বহু মানুষের জীবন, থাকে গাছপালা, পশুপাখি, উদ্ভিদ, লতাপাতা, পোকামাকড় ইত্যাদি যাবতীয় প্রাণের সম্পর্ক। নদী যখন মরে যায় তখন তাই সবারই মরণের দশা হয়। বিষণ&oelig;তা দেখা যায় শুধু নদীর শরীরে নয়, চারদিকে সকল প্রাণের শরীরেই। মৃত্যু তখন জেঁকে বসে সবার শরীরেই।<br />\r\nনদীর জীবনের সাড়া পাওয়া যায় আরেকটি ক্ষেত্রে। সেটি হলো&Ntilde;নদীর ক্রোধ, প্রতিশোধস্পৃহা আর ধ্বংসাত্মক প্রবণতার মধ্যে। নদী যখন তার প্রবাহে বাধাপ্রাপ্ত হয় কিংবা যখন প্রবল নিয়ন্ত্রণের শিকার হয় তখন তার দুটো প্রতিক্রিয়া হয়&Ntilde;যা আঘাতপ্রাপ্ত হলে অন্য যে কোনো প্রাণীর ক্ষেত্রেই হয়। এগুলো হলো: সবল হলে আসে পাল্টা আঘাত বা প্রতিশোধ আর দুর্বল হলে পরাজিত ভেঙ্গে পড়া চেহারা হয়; ক্ষোভে, দুঃখে কাতরতায় মুষড়ে পড়ে, ঘটে একসময়ে মৃত্যু; প্রবাহ থেমে যায়, পানির ভেতরকার সব প্রাণ আস্তে আস্তে মরে যেতে থাকে।<br />\r\nসবল নদীর &lsquo;প্রতিশোধের&rsquo; ভাষা আমরা জানি। যে কোনোভাবে পথ খুঁজে নেবার চেষ্টা-ক্রুদ্ধ রোষে ভাঙন, সে এক ভয়ংকর স্বেচ্ছাচারিতা। বৃক্ষ, জমি, ঘরবাড়ি, হাটবাজার, ধর্মস্থান, মানুষ, পশুপাখি কিছুই দাঁড়াতে পারে না। ভয়ংকর বন্যাও তার প্রতিশোধের একটা ভাষা। প্রতিশোধের এই চেহারা নদীর স্বাভাবিক চেহারা নয়&Ntilde;এটা হলো তার &lsquo;মাথা খারাপ&rsquo; দশা। এই অবস্থা দীর্ঘকাল ধরে চলতে থাকলে নদী নিজেও একসময় ক্লান্ত হয়ে পড়ে, শক্তি হারিয়ে ফেলে। বিকলাঙ্গ হয়ে ক্রমে মৃত্যুর দিকে এগিয়ে যায়।<br />\r\nসবল নদীর উন্মাদনার মধ্য দিয়ে বিকলাঙ্গা দশা আর দুর্বল হলে ক্ষয়ে ক্ষয়ে মৃত্যু এই দুটো দৃশ্যই আমরা বাংলাদেশের নদীর ক্ষেত্রে দেখি। গত কয়েক দশকে এই চেহারা আরো ভয়ংকর হয়ে উঠেছে। ওপর থেকে বাংলাদেশকে দেখলে তাই নদীর বদলে এখন চোখে পড়বে অনেক শবদেহ। সারাদেশ জুড়ে নদ&Ntilde;নদী শাখানদী, উপনদী নামের অনেক শবদেহ, মৃত্যুপথযাত্রী খাল&Ntilde;বিল। এ এক গণহত্যার দৃশ্য। এটাই জালের মতো ছড়িয়ে থাকা আমাদের নদীর চেহারা। ক্ষতচিহ্নের মতো মাঝে মাঝে চরা। শীতকালে দৃশ্যটি আরো ভয়ংকর হয়ে ওঠে। বড় বড় সেতুর নিচ দিয়ে মানুষ&Ntilde; গাড়ি সবই চলতে থাকে। আর বর্ষাকালে নদীর পানি ফুলে&Ntilde;ফেঁপে বারবার মানচিত্র বদলাতে থাকে। এগুলো করে নদী বাঁচতে পারে না। শরীরে পানি রাখার জায়গা যদি না থাকে, যদি শীর্ণ শরীরে ক্ষমতার অতিরিক্ত পানি বারবার ধাক্কা দিতে থাকে তাহলে কতদিন তার টিকে থাকার উপায় থাকে? আর যদি পানি বিষাক্ত হয়ে যায় তাহলে? রক্ত বিষাক্ত হলে মানুষ কি বাঁচে? তাহলে বিষাক্ত পানি নিয়ে নদী কী করে বাঁচবে?<br />\r\n২<br />\r\nপ্রাণবিদ্যার সংজ্ঞা অনুযায়ী নদীর কোনো প্রাণ নেই, কোনোদিনই নদীর প্রাণ আবিষ্কার করা যাবে না। মানুষ এখন ক্রমেই উপলব্ধি করতে পারছে যে, নদীর মধ্যে কোনো অশরীরি শক্তি নেই যাকে পুজো দিয়ে, ভক্তি দিয়ে নদীর ভাব পক্ষে আনা যায়। কিন্তু তারপরও বিভিন্ন অর্থেই নদী জীবন্ত সত্তা, জীবন্ত সত্তা বলেই তার মৃত্যুও আমরা দেখি। নদীর জীবন আর মৃত্যু দুটোই বিচ্ছিন্নভাবে পাওয়া যায় না। তার সঙ্গে বাঁচে কিংবা মরে মানুষ, গাছপালা, লতাপাতা, পশুপাখি, কীটপতঙ্গসহ আরো অনেক কিছু। সে কারণেই অনেকে মিলেই আমরা বাঁচি। মানুষ হিসেবে আমরা একা কখনোই বাঁচতে পারি না। প্রকৃতির মধ্যে মানুষ একটি, আরো লক্ষ জন নিয়ে তার বেঁচে থাকার শর্ত তৈরি হয়। এই সবকিছু মিলেই তৈরি হয় ভারসাম্য। লাখ লাখ বছর ধরেই মানুষ এই ভারসাম্যের ওপরই টিকে থেকেছে<br />\r\nএই পারস্পরিক&Ntilde;আন্তনির্ভরশীলতা&Ntilde;জৈবিক অবিচ্ছিন্নতা স্বীকার করলেই কেবল মানুষ প্রকৃতির আর অন্যদের সঙ্গে একটা সমঝোতার মধ্য দিয়ে নতুন ভারসাম্যের জায়গায় যেতে পারে। কিন্তু বলাই বাহুল্য, ক্রমে মানুষের প্রয়োজন অনেক বেড়েছে, ক্ষমতাও বেড়েছে, বেড়েছে অন্যদের ওপর খবরদারির ক্ষমতা আর সেইসঙ্গে বেড়েছে বিচ্ছিন্ন হবার প্রবণতাও। আর যখন থেকে মানুষ নিজেকে &lsquo;সৃষ্টির সেরা জীব&rsquo; ভাবতে শুরু করেছে তখন থেকেই তার প্রকৃতি থেকে বিচ্ছিন্নতা শুরু। তার ধারণা হয়েছে জগতের সবকিছু আছে তার সেবা করার জন্য, সুতরাং প্রকৃতির অন্যসব কিছু নিয়ে যা খুশি তাই করার, ধ্বংস বা বিনাশ করারও অধিকার তার আছে। এই কর্তৃত্ববোধ মানুষকে প্রকৃতির অন্যদের থেকে আলাদা করে, প্রকৃতির মধ্যে তাকে নিঃসঙ্গ করে। বিপর্যয় শুরু হয় তখনই, যখন এই ভাবনার সঙ্গে তার হাতে ক্ষমতা আসে প্রকৃতিকে পদদলিত করবার।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কোনো সন্দেহ নেই প্রকৃতির মধ্যে মানুষই একমাত্র প্রাণী যে বিকশিত হয়েছে এমনভাবে যে, নিজের অবস্থান নিয়ে পরিকল্পনা করতে পারে। সৃষ্টি করতে পারে। নিজের চারপাশ ও জীবনকে পরিবর্তন করতে পারে। যার ক্ষমতা সম্প্রসারিত হয় তার শরীরের তুলনায় বহু গুণ বেশি, শুধু জ্ঞানের মাধ্যমে। জ্ঞানের মাধ্যমে মানুষ নিজেকে জানতে থাকে আরো বেশি বেশি করে এবং প্রকৃতির যা ছিল এতদিন রহস্য তাও ক্রমাগত উন্মোচন করতে থাকে। মানুষ, মাটি, পানি, ভূগর্ভ থেকে মহাকাশ পর্যন্ত পৌঁছাতে থাকে। বলা দরকার যে, এই ক্ষমতা বৃদ্ধি, এই যাত্রা এবং তার জ্ঞানের বিকাশই মানুষকে প্রকৃতির প্রতিপক্ষ করেনি। মানুষ বরঞ্চ এই জ্ঞান দিয়ে প্রকৃতির নিয়ম আরো ভালোভাবে বুঝে নানাভাবে তার সাথে বোঝাপড়া করতে এখন সক্ষম। মানুষ যখন জানে নদীর ভেতর কোনো দেবতা বা ঐশীশক্তি ঘূর্ণি তৈরি করছে না, নদী নিজে মনের ইচ্ছা দিয়ে চলছে না, প্রার্থনা বা পূজা দিয়ে নদীকে ঠাণ্ডা করা যাবে না, তখন মানুষ এটাও জানবে, কেন কী কারণে নদী উন্মত্ত দশাপ্রাপ্ত হয়। নদীর প্রবাহ বাধাগ্রস্ত না করে তার অবাধ প্রবাহের সঙ্গে তখন মানুষ নিজের প্রয়োজনের নতুন ভারসাম্য তৈরি করতে পারে। কিন্তু প্রকৃতির অনেক রহস্য উন্মোচন করার মতো জ্ঞান অর্জন করেও মানুষ প্রকৃতির প্রতিপক্ষ হয়ে উঠেছে, সব মানুষ নয় একটি ছোট অংশ, তখনই যখন প্রকৃতির সমগ্রতা ও ভারসাম্য অস্বীকার করে তাকে তারা নিজের আধিপত্য প্রতিষ্ঠা ও মুনাফা অর্জনের কাজে লাগাতে চেষ্টা করেছে।<br />\r\nপ্রকৃতির সঙ্গে মানুষের বৈরিতার ঘটনা তাই সব মানুষের নয়। বিশ্লেষণ করলে আমরা দেখব প্রকৃতি&Ntilde;বৈরী সংখ্যালঘু মানুষ আসলে অন্য সংখ্যাগুরু মানুষেরও বৈরী। এই বৈরিতা নিছক মানুষের জ্ঞানের সমস্যা বা বিভ্রান্তির ব্যাপার নয়, নয় নিছক সাংস্কৃতিক সমস্যা। মানুষের লাখ লাখ বছরের ইতিহাসে মানুষ প্রকৃতি জগতে ঘুরেছে, বেঁচেছে, নিজের অবস্থান সম্প্রসারণ করেছে কিন্তু প্রকৃতির ওপর আগ্রাসী ভূমিকায় মানুষকে যে দেখা যায় তা মাত্র কয়েকশ&rsquo; বছরের ব্যাপার। এই অবস্থা যে প্রকট আকার ধারণ করে তার বয়স শত বছরও হবে না। কিন্তু সেখানেও কোন মানুষেরা আসলে জড়িত? লক্ষ্য করলে আমরা দেখব এর সঙ্গে সম্পর্কিত সমাজ সংগঠনের ধরন, মালিকানা ও উৎপাদনের ব্যবস্থা এবং তার সঙ্গে বেড়ে ওঠা বোধ, বিশ্বদৃষ্টিভঙ্গি, চর্চা এবং জ্ঞান&Ntilde;বিজ্ঞান ধারণ ও প্রয়োগের ধরন।<br />\r\nবাংলাদেশ নদীনালা খালবিল ভরা একটি দেশ। আরো সঠিক হবে বললে যে, ভরা ছিল একটি দেশ। এখন যেভাবে আছে নদীনালা, খালবিল তাতে অনেক নদীই শুকিয়ে গেছে কিংবা আধমরা হয়ে আছে। একের পর এক পাকা বাঁধ বা বন্যা নিয়ন্ত্রণ প্রকল্প আটকে দিয়েছে অনেক প্রবাহ। অনেক খালবিলও হয় শুকিয়ে গেছে নতুবা যাওয়ার পথে। ডোবা&Ntilde;জলাশয় যেগুলো আবার নানাভাবে পানিপ্রবাহের একটা গুরুত্বপূর্ণ অংশ হিসেবে কাজ করে সেগুলোর অনেকই এখন অট্টালিকার নিচে হারিয়ে গেছে কিংবা সড়ক বা আশপাশের কোনো নির্মাণকাজের জন্য মুচড়ে গেছে। নদীনালা খালবিল ডোবা জলাশয়ের এই অকাল মৃত্যু কিংবা হাত&Ntilde;পা বাঁধা পঙ্গু অবস্থার কারণে তার প্রতিক্রিয়াও হয়েছে ভয়াবহ। বন্যা আর ভাঙন দুটোই বেড়েছে। বেড়েছে জলাবদ্ধতাও।<br />\r\nএরকম যুক্তি আসতে পারে যে, বন্যা এবং ভাঙন আজকের ঘটনা নয়। যখন নদীর ওপর বা পানির স্বাভাবিক প্রবাহের ওপর মানুষ কোনো হামলা করেনি, যখন মানুষ সেই ঔদ্ধত্য দেখাবার মতো ক্ষমতা অর্জন করেনি বা প্রযুক্তি আবিষ্কার করতে পারেনি তখনো বন্যা হয়েছে, ভাঙন হয়েছে, নদীর গতিপথ বদলে গছে, নদী মরেছেও। তাহলে? এখান থেকে সিদ্ধান্ত একটাই আসতে পারে যে, এসব ঘটনা নিছকই প্রাকৃতিক দুর্যোগ। প্রাকৃতিক ঘটনা। এবং মানুষের দুর্ভাগ্য। মানুষের এখানে কিছু করার নেই।<br />\r\nবিশেষভাবে খেয়াল করার ব্যাপার হলো প্রাকৃতিক দুর্যোগ মোকাবেলা করবার জন্য, নিজেদের অসহায়ত্ব এবং কথিত &lsquo;দুর্ভাগ্য&rsquo; দূর করবার জন্য মানুষ নানা পথ অনুসন্ধান করেছে, নানারকম যে উদ্যোগ নিয়েছে তার বয়স দীর্ঘ। বিভিন্ন সময়ে বিভিন্ন স্থানে এসব উদ্যোগ মানুষকে অনেক দুর্যোগ থেকে আত্মরক্ষার পথ দেখিয়েছে, অনেক &lsquo;দুর্ভাগ্য&rsquo; মানুষ অতিক্রম করেছে। কিন্তু সেই উদ্যোগ যখন হস্তক্ষেপের আকার নিয়েছে, যখন তা প্রকৃতির সঙ্গে বোঝাপড়ার সীমা অতিক্রম করেছে তখন তার ফল হয়েছে উল্টো। দুর্যোগ আর দুর্ভাগ্য আরো বেড়েছে। বাংলাদেশে গত পাঁচ দশকে যা হয়েছে তা এর প্রমাণ।<br />\r\nআমরা ক্রমান্বয়ে পর্যালোচনা করে দেখব যে, বিশেষত নদীর আর সেই সঙ্গে বন, পাখি জগতের বিষণ&oelig; বিপর্যস্ত চেহারা লাভ এই দেশের সামগ্রিক উন্নয়ন প্রবাহের সঙ্গে সম্পর্কিত। যারা এদেশের গত কয়েক দশকের উন্নয়ন প্রবাহের শারীরিক বা জ্ঞানগত অংশীদার এবং দেশী&Ntilde;বিদেশী যারা এর সুফলভোগী তারা বলে থাকেন যে, বন্যা নিয়ন্ত্রণের জন্য নদী শাসন দরকার ছিল, কৃষি ব্যবস্থা উন্নয়নের জন্য এবং খাদ্য উৎপাদন বৃদ্ধির জন্য নদী নিয়ন্ত্রণ ও বাঁধ দরকার ছিল। বলেন, বিদ্যুৎ উৎপাদনের জন্য বাঁধ দরকার ছিল। বলেন, যোগাযোগ ব্যবস্থার উন্নয়নের জন্য বাঁধ ইত্যাদি নিয়ন্ত্রণমূলক ব্যবস্থা অপরিহার্য ছিল। বলেন, বসতির প্রয়োজনে, বৈদেশিক মুদ্রা অর্জনের জন্য বন ধ্বংস না করে পানি লবণাক্ত না করে উপায় কি ছিল?<br />\r\nএগুলো গুরুত্বপূর্ণ যুক্তি। আমাদের সব মানুষের জন্য খাদ্য দরকার, আমাদের জীবন আরো গতিশীল করার জন্য বিদ্যুৎ দরকার, আমাদের বন্যার প্রকোপ থেকে বাঁচা দরকার, আমাদের সীমা বাড়ানো দরকার, যোগাযোগ বাড়ানো দরকার, আমাদের বসতি দরকার। আমরা তো অযান্ত্রিক যুগে ফেরত যেতে পারি না। পারি কি? অনেকে রোমান্টিকভাবে সেই যুগে ফেরত যাওয়ার জন্য পেছন ফিরে বসে আছেন, কল্পনায় সৃষ্টি করছেন এক সুখময় স্বর্গজগৎ, আক্ষেপে কাতর হচ্ছেন। কিন্তু প্রথমত, ইচ্ছা করলেই পেছনের জগতে যাওয়া যাবে না; দ্বিতীয়ত, মানুষ সেই জগতের অসম্পূর্ণতা সীমাবদ্ধতা বৈরিতা শোষণ নিপীড়ন বঞ্চনা ও অপমানের ভয়াবহতা থেকে মুক্তির চেষ্টা করতেই এ পর্যন্ত এসেছে। অতীতের ভয়াবহতা রোমান্টিকতা দিয়ে তাকে ঢেকে ফেলা যায় না। বর্তমানের নতুন অসঙ্গতি দেখে পেছনে যাওয়ার ঘোর সৃষ্টি করে বসে থাকার চেয়ে দরকার এই অসঙ্গতি বোঝা এবং তা দূর করবার রাস্তা খোঁজা। গুরুত্বপূর্ণ প্রশ্নটি হলো, প্রকৃতি ও তার থেকে কৃত্রিমভাবে বিচ্ছিন্ন হয়ে যাওয়া মানুষের সৃজনশীল এবং গতিময় ভারসাম্য কেন সম্ভব হচ্ছে না? বাধাটা কোথায়?<br />\r\nএতদিনের অভিজ্ঞতা থেকে এবং ক্ষমতাবান আন্তর্জাতিক সংস্থা&Ntilde;গবেষক&Ntilde;সরকার&Ntilde;বৃহৎ মালিক প্রভৃতির কথাবার্তা&Ntilde;যুক্তিবিন্যাস থেকে তো এই প্রশ্নই প্রবল হয়ে ওঠে যে, মানুষের জীবনের নিরাপত্তা নিশ্চিত করার জন্য, তার জীবনে আয়েশ আনার জন্য, মানুষের জীবন সম্প্রসারণের জন্য, নতুন দিগন্ত উন্মোচনের জন্য কি তবে নিজের জীবনের প্রাণই বিনাশ করতে হবে? প্রকৃতি ধ্বংস হবে, নদীনালা খালবিলসহ পানি প্রবাহ বিষাক্ত হবে, সবুজ গাছপালা হলুদ হবে কিংবা শুস্ক কাষ্ঠে পরিণত হবে, পাখি ছটফট করতে করতে মরবে কিংবা পালাবে, নতুন সাজে সজ্জিত ফসল&Ntilde;ফল ভেতরে বিষ নিয়ে হাজির হবে চোখ হারানো মানুষের সামনে। এছাড়া আর কোনো গতি নেই? তবে কী জ্ঞান অর্জন করল মানুষ লক্ষ লক্ষ বছরে? এটাই তবে উন্নয়ন? বটেই, বর্তমান অধিপতি জ্ঞান অনুযায়ী এটাই উন্নয়ন। কিন্তু এটা আসলে কী?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৪<br />\r\nবন্যার ইতিহাস যেমন প্রাচীন, বাঁধের ইতিহাসও তেমনি প্রাচীন। বাঁধ ছাড়াও অন্য নানা ব্যবস্থা মানুষ গ্রহণ করেছে জমির ফসল, ঘরবাড়ি, বসতি রক্ষা বা ভাঙন ঠেকানোর চেষ্টায়। সেসব চেষ্টার মধ্যে নানা পরিবর্তন, সংশোধন ও পরিমার্জন দেখা যায়। তবে এসবের একটা বড় বৈশিষ্ট্য ছিল অস্থায়িত্ব এবং স্থানিকতা। নিয়ন্ত্রণ যেহেতু স্থায়ী ছিল না এবং যতটুকু ছিল তাও ছিল স্থানিক তার ফলে নদীর প্রবাহের ওপরও কোনো স্থায়ী প্রতিবন্ধকতা তৈরি হয়নি। ফলে প্রতিক্রিয়া হয়েছে এখনকার তুলনায় অনেক কম, যখন বাঁধ মানেই একটি স্থায়ী প্রতিরোধ।<br />\r\nবাংলাদেশে স্থায়ী বাঁধসহ ব্যাপকভাবে কনক্রিটের নানা কাঠামো নির্মাণের শুরু পঞ্চাশ দশকে। শুধু পাকিস্তান (বর্তমান বাংলাদেশসহ) নয়, ভারতসহ আরো অনেক দেশেই আমরা দেখি এই দশকে বন্যা নিয়ন্ত্রণ বা পানিসম্পদ ব্যবস্থাপনার ক্ষেত্রে সমধর্মী উদ্যোগ। প্রকৌশল দৃষ্টিভঙ্গিতে বন্যা নিয়ন্ত্রণ ও পানি ব্যবস্থাপনার জন্য এটি ছিল অভিন্ন অপরিহার্য ব্যবস্থা। তৎকালীন প্রতিষ্ঠিত উন্নয়ন চিন্তার সঙ্গেও এই হস্তক্ষেপমূলক আক্রমণাত্মক ব্যবস্থাবলি ছিল সঙ্গতিপূর্ণ। কারণঃ<br />\r\n১। যান্ত্রিক প্রকৌশল দৃষ্টিভঙ্গি নদীকে পানি হিসেবে দেখতে অভ্যস্ত, জীবন্ত নদী হিসেবে নয়, সমগ্র প্রকৃতির অংশ হিসেবে নয়।<br />\r\n২। তার ফলে এই দৃষ্টিভঙ্গি নদীর সঙ্গে অন্য আরো হাজারো জীবন্ত সত্তার যোগ দেখতে ব্যর্থ হয়।<br />\r\n৩। এই দৃষ্টিভঙ্গি নদীর বিভিন্ন অংশকে কম্পার্টমেন্ট হিসেবে দেখে, দেখে খণ্ড খণ্ড বিচ্ছিন্ন আকারে। তার ফলে তার কর্মপদ্ধতি উপেক্ষা করে অখণ্ড এবং অবিচ্ছিন্ন নদীর প্রবাহকে।<br />\r\n৪। এই দৃষ্টিভঙ্গি হিসাব করে ঐ কম্পার্টমেন্টে এবং স্বল্প মেয়াদে (কিংবা স্বল্প মেয়াদ থেকে প্রজেকশন করে দীর্ঘমেয়াদে) কত ব্যয় হলো কত লাভ হলো। এই লাভ&Ntilde;ক্ষতির যে আরো এলাকা আছে এবং আরো লম্বা মেয়াদ আছে তা হিসাবে আসে না।<br />\r\nযে উন্নয়ন দর্শন সে সময় বাংলাদেশসহ (তৎকালীন পূর্ব পাকিস্তান) উত্তর&Ntilde;উপনিবেশ দেশগুলোতে আধিপত্য প্রতিষ্ঠা করেছিল তা ছিল এই দৃষ্টিভঙ্গিরই সহযোগী। এই উন্নয়ন দর্শন ছিল উপনিবেশ থেকে সদ্য &lsquo;স্বাধীনতাপ্রাপ্ত&rsquo; দেশগুলোতে পুঁজিবাদী সম্পর্ক ও প্রতিষ্ঠান বিকশিত করা, একই প্রক্রিয়ায় পুঁজিবাদী কেন্দ্র বা ঔপনিবেশিক রাষ্ট্রগুলোর সঙ্গে এগুলোকে নতুনভাবে সমন্বিত করা এবং এমন একটি গোষ্ঠী তৈরি করা যে, চিরস্থায়ী বন্দোবস্ত প্রতিষ্ঠার যুক্তি হিসেবে লর্ড কনওয়ালিস যা বলেছিলেন, &lsquo;নিজেদের স্বার্থেই আমাদের টিকাইয়া রাখিবে&rsquo;। এই উন্নয়ন দর্শন আবার যে অর্থশাস্ত্রীয় ভিত্তির ওপর প্রতিষ্ঠিত সেটি এখন বিশ্ব ক্ষমতায়। এই অর্থশাস্ত্র, প্রথমত, সব তৎপরতার প্রাণ এবং শেষ গন্তব্য হিসেবে মনে করে মুনাফা। যা কিছুই মুনাফার মাধ্যমে মূলধন সংবর্ধন করতে পারে সেটাই এই অর্থশাস্ত্র ধন্য ধন্য রব দিয়ে গ্রহণ করে।<br />\r\nদ্বিতীয়ত, এই শাস্ত্রে ব্যক্তিই মূল ইউনিট। সমষ্টি ব্যক্তির যোগফল ছাড়া আর কিছু নয়।<br />\r\nতৃতীয়ত, প্রকৃতি এখানে মুনাফা অর্জনের ক্ষেত্র ভিন্ন কিছু নয়।<br />\r\nচতুর্থত, দুনিয়ার সকল কিছুই বিক্রিযোগ্য।<br />\r\nপঞ্চমত, দুনিয়ার সকল কিছুই ব্যক্তিগত মালিকানার অধীনে আনা সম্ভব।<br />\r\nএবং ষষ্ঠত, ব্যক্তিগত মালিকানাধীনে মুনাফার লক্ষ্যে উৎপাদন ও বিতরণ সবচেয়ে দক্ষ ও কাক্সিক্ষত ব্যবস্থা।<br />\r\nআমরা বর্তমানে উন্নয়ন তত্ত্ব বা আধুনিকতার তত্ত্ব হিসেবে যেগুলোকে দেখি সেগুলোর উদ্ভব ৪০ দশকের শেষদিকে বা ৫০ দশকে। সময়কালটি তাৎপর্যপূর্ণ। বহুদেশ তখন ঔপনিবেশিক কাঠামো থেকে বেরিয়ে আসছে এবং প্রয়োজন এসব দেশকে বৈশ্বিক অভিন্ন কাঠামোর মধ্যে নিয়ে আসা, পুঁজিবাদী বিশ্বব্যবস্থার মধ্যে সজীব রাখা। সেই প্রক্রিয়ার অংশ হিসেবেই এসব তত্ত্বের উদ্ভব এবং কেন্দ্র সংস্থাগুলো কর্তৃক আদৃত হওয়া। বলাই বাহুল্য যে, সব তত্ত্ব এককথা বলে না বা একইভাবে &lsquo;অনুন্নত&rsquo; দেশগুলোর &lsquo;উন্নয়ন যাত্রা&rsquo; বিশ্লেষণও করে না। কিন্তু তারপরও এসব তত্ত্বের কেন্দ্রীয় সুরের মধ্যে অভিন্ন তাল&Ntilde;লয় পাওয়া যায়। অভিন্ন তাল&Ntilde;লয় আসে অভিন্ন মতাদর্শিক অবস্থান&Ntilde;অভিন্ন অনুমিতি, লক্ষ্য, অগ্রাধিকার এবং অভিন্ন কেন্দ্রের কারণে।<br />\r\nযেমন সবগুলো তত্ত্বেই কমবেশি বলা হচ্ছে: ১. উত্তর&Ntilde;উপনিবেশ দেশগুলো অনুন্নত বিভিন্ন দিক থেকে। এর কেন্দ্রীয় সমস্যা হচ্ছে &lsquo;পুজি&rsquo;র অভাব, দক্ষতার অভাব এবং জনসংখ্যা। ২. কেন্দ্র থেকে যদি পুঁজি ও বুদ্ধি এবং প্রাতিষ্ঠানিক সমর্থন নেয়া হয় তাহলেই কেবল সব সমস্যার সমাধান হতে পারে। ৩. এই পুঁজি ও অন্যান্যের যোগান হতে পারে দুটো পথে: এক. &lsquo;বিদেশী সাহায্য&rsquo; এবং দুই. &lsquo;বিদেশী বিনিয়োগ&rsquo;। বিশ্বাসটা এভাবেই দাঁড়ায় বা দাঁড় করানো হয় যে, যে কোনোভাবে এই পুঁজির প্রবাহ হোক না কেন তা এসব দেশের মূল সমস্যা দূর করবে এবং এসব &lsquo;অনুন্নত&rsquo; দেশ শনৈঃ শনৈঃ উন্নতি করতে থাকবে। আর ৪. বাজার ও ব্যক্তিগত মালিকানাভিত্তিক ব্যবস্থার প্রতিষ্ঠা ও বিকাশই উন্নয়নের পথ। এবং ৫. জ্ঞানের কেন্দ্র যেহেতু ঔপনিবেশিক কেন্দ্র আর উন্নয়ন যেহেতু তাদেরই মস্তিষ্কপ্রসূত সুতরাং এ বিষয়ে শিক্ষা জ্ঞান বুদ্ধি পরামর্শ তারাই দেবেন, সেক্ষেত্রেও &lsquo;বিশেষজ্ঞ&rsquo; &lsquo;তদারককারী&rsquo; প্রধানত সেসব অঞ্চল থেকে আসবেন। ক্রমে তাদের মতো জ্ঞানী&Ntilde;গুণী বিশেষজ্ঞরা এসব দেশেও তৈরি হবে&Ntilde;তখন আরো সুবিধা হবে।<br />\r\n&lsquo;অনুন্নত&rsquo; দেশ শনৈঃ শনৈঃ উন্নতি করতে থাকবে মানে হতে থাকবে ক্রমান্বয়ে শিল্পোন্নত পুঁজিবাদী কেন্দ্র দেশগুলোর &lsquo;মতো&rsquo;। অভিজ্ঞতায় আমরা দেখেছি যে, &lsquo;মতো&rsquo; মানে বাস্তবে যা দাঁড়িয়েছে তা হলো পোশাক, ভাষা, ভবন, শোয়া, খাওয়া, গাড়িবাড়ি, চিন্তা&hellip;.। আর অর্থনৈতিক ক্ষমতা কিংবা শিল্পভিত্তি কিংবা শিক্ষা&Ntilde;স্বাস্থ্য ভিত্তি? দূর-আস্ত! এবং অনাবশ্যক!!<br />\r\nক্রমে এসব তত্ত্ব আরো সমৃদ্ধ হয়েছে, সংযোজিত হয়েছে নতুন অনেক কিছু। বিশ্বে পুঁজিবাদী ব্যবস্থার ব্যবস্থাপক&Ntilde;তদারককারী এবং বহুজাতিক সংস্থার রাস্তা পরিষ্কারকারী ও লবিস্ট হিসেবে বিশ্বব্যাংক&Ntilde;আইএমএফ কেন্দ্রীয় ক্ষমতাধর সংস্থা হিসেবে আবির্ভূত হয়েছে। তার সঙ্গে সঙ্গে আছে বা আগে আছে মার্কিন যুক্তরাষ্ট্র এবং ইউএসএইডসহ তার যাবতীয় প্রতিষ্ঠান এবং এডিবিসহ বিবিধ আন্তর্জাতিক বা আঞ্চলিক ঠিকাদার বা লবিস্ট সংস্থা। একে একে এসেছে কাঠামোগত সমন্বয় কর্মসূচী (স্যাপ), বন্যা নিয়ন্ত্রণ করার নানা কর্মসূচি (ফ্যাপ) এবং দারিদ্র্য বিমোচন কিংবা হ্রাসকরণের নানা আয়োজন (প্যাপ)। সবশেষে আগের সবকিছু মিলিয়ে, স্যাপ ফ্যাপ ও প্যাপ মিশিয়ে, নতুন চিনিমাখা মোড়ক লাগিয়ে আবির্ভূত হয়েছে পিআরএসপি।<br />\r\n৫<br />\r\n৫০ দশক থেকে বাংলাদেশে, তৎকালীন পাকিস্তানের কাঠামো থেকে শুরু করে বাংলাদেশ পর্যন্ত, উন্নতি কম হয়নি। এর মধ্যে লক্ষ লক্ষ কোটি টাকা &lsquo;পুঁজি&rsquo; এসেছে, তার সঙ্গে অসংখ্য &lsquo;বিশেষজ্ঞ&rsquo; এসেছে, আর দেশের মধ্যে সেই জ্ঞান, দৃষ্টি, ভাব দ্বারা আচ্ছন্ন একটি &lsquo;সিভিল সোসাইটি&rsquo; গড়ে উঠেছে। আর এদের, দেশী&Ntilde;বিদেশী সম্মিলিত মহিমায়, কাজ, উন্নয়ন, জ্ঞান, কর্মের ফলাফল আমাদের সামনে। এসবের একটি বড় ক্ষেত্র ছিল এবং আছে বাংলাদেশের পানি সম্পদ ব্যবস্থাপনা এবং বন্যা নিয়ন্ত্রণ।<br />\r\n১৯৫৪ সালে বাংলাদেশে (পূর্ব পাকিস্তানে) ব্যাপক বন্যা হয়। পাকিস্তানে সে সময় প্রবল দাপটে অর্থনৈতিক পরিকল্পনা, উন্নয়ন কৌশল বাস্তবায়নে প্রয়োজনীয় জনশক্তি তৈরি এবং প্রতিষ্ঠান নির্মাণে মার্কিনি বিভিন্ন সংস্থা ও বিশেষজ্ঞরা কাজ করছিল। বস্তুত এই বন্যার পর বাংলাদেশে পানিসম্পদ নিয়ে আন্তর্জাতিক মনোযোগ তৈরি হয়। জে.এ. ক্রুগ&Ntilde;এর নেতৃত্বে একটি কমিশন এসে বন্যা নিয়ন্ত্রণের বিষয়ে অনুসন্ধান এবং সুপারিশ করেন। রিপোর্টে সর্বব্যাপী একটি প্রস্তাবনা উপস্থিত করা হয়। বস্তুত এই প্রস্তাবনাই বাংলাদেশে পানিসম্পদের ওপর একদেশদর্শী যান্ত্রিক আগ্রাসনের ভিত্তি স্থাপন করে। বন্যা নিয়ন্ত্রণ শুধু নয়, এর সাথে ক্রমে যুক্ত হয় সেচ প্রকল্প। বিশ্বব্যাপী &lsquo;সবুজ বিপ্লবে&rsquo;র মাধ্যমে প্রান্তস্থ অর্থনীতিতে বাণিজ্যিক কৃষির প্রবর্তন, ৬০-র দশকে যার শুরু, তার সঙ্গে এর যোগাযোগ ক্রমেই স্পষ্ট হয়ে ওঠে। সুতরাং এটা ধারণা করা খুবই সঙ্গত যে, আন্তর্জাতিক মনোযোগ বন্যা নিয়ন্ত্রণের চেয়ে সামগ্রিক কৃষিব্যবস্থার দিকেই ছিল। আর এই মনোযোগ নিছক বন্যা সমস্যার সমাধান নয়, প্রান্তস্থ দেশগুলোতে নতুন পর্বের ভিত্তি স্থাপনই বটে।<br />\r\nক্রুগ কমিশনের প্রস্তাব অনুযায়ী পানি ও বিদ্যুৎ উন্নয়ন কর্তৃপক্ষ (ওয়াপদা) প্রতিষ্ঠিত হলো ১৯৫৯ সালে। পানি বিষয়ে একটি মাস্টার প্ল্যান তৈরি করার জন্য এই সংস্থার সঙ্গে প্রথম থেকেই কনসালট্যান্ট হিসেবে সয়ংযুক্ত ছিল একটি মার্কিনি প্রকৌশল কোম্পানি, যার নাম: &lsquo;ইন্টারন্যাশনাল ইঞ্জিনিয়ারিং কোম্পানি&rsquo;। বিশ বছরের মাস্টার প্ল্যান তৈরি শেষ হলো ১৯৬৪ সালে। ১৯৮৫&Ntilde;এর মধ্যে ৩২ লাখ একর জমিতে বন্যা নিয়ন্ত্রণ ও পানি নিষ্কাশন ছিল এর লক্ষ্য। এই উদ্দেশ্যে ৫১টি বড় প্রকল্প নেয়া হয়, যার মধ্যে বড় বড় নদীর সবগুলোই ছিল। একদিকে হলো বিভিন্ন ধরনের নলকূপের মাধ্যমে ভূগর্ভস্থ পানির ব্যবহার বৃদ্ধি এবং অন্যাদিকে হলো ভূ&Ntilde;উপরিস্থ পানি প্রবাহকে নিয়ন্ত্রণ করা, বাধাগ্রস্থ করা। নিয়ন্ত্রণ করার উদ্দেশ্য ছিল বন্যা নিয়ন্ত্রণ, সেচ এবং বিদ্যুৎ উৎপাদন।<br />\r\nখরস্রোতা নদীতে বাঁধ দিয়ে বিদ্যুৎ উৎপাদন একটি পরিচিত পদ্ধতি। এতে বিদ্যুৎ উৎপাদন হয় কিন্তু এর ফলে যাদের লাভ হয় আর যাদের ক্ষতি হয় তারা যদি ভিন্ন হয় এবং যে লাভ হয় আর যে ক্ষতি হয় তার হিসাবে ক্ষতি যদি বেশি হয় তাহলে তা ভয়ংকর বিপর্যয় নিয়ে আসতে পারে। বাংলাদেশে এর প্রত্যক্ষ উদাহরণ বর্তমান রাঙ্গামাটি জেলার চন্দ্রঘোনা থানার কাপ্তাই গ্রামে কর্ণফুলী নদীর ওপর নির্মিত কাপ্তাই বাঁধ। ১৯৫৭ সালের অক্টোবর মাসে এর নির্মাণকাজ শুরু হয় এবং শেষহয় ১৯৬২ সালের জানুয়ারি মাসে। এই বাঁধের ঘোষিত লক্ষ্য ছিল: বিদ্যুৎ উৎপাদন, সেচ ও পানি নিষ্কাশন, বন্যা নিয়ন্ত্রণ, নৌপরিবহনের সুবিধা বৃদ্ধি, পার্বত্য চট্টগ্রামের দুর্গম অঞ্চলে প্রবেশ সুবিধা এবং পরে যুক্ত করা হয়েছিল মৎস্যচাষ।<br />\r\nলক্ষ্য আংশিক পূরণ হয়েছে তবে এখন আবার বিদ্যুৎ উৎপাদনে সমস্যা দেখা দিয়েছে। প্রচলিত লাভক্ষতির হিসাবে প্রকল্পের শুরুতে এখানে থেকে যত লাভের কথাই বলা হয়েছে, সেটিও ঠিক প্রমাণিত হয়নি। দেখা গেছে, লাভের দিক কমে গেছে, ব্যয়ের দিক বেড়েছে। কিন্তু এই হিসাবের বাইরেও এর তৎক্ষণাৎ ও দূরবর্তী ফলাফল হয়েছে ভয়ংকর। আন্তর্জাতিক সংস্থাগুলোর পূর্ণ সমর্থন ও জ্ঞান&Ntilde;ঋণ (পুঁজি) দ্বারা প্রতিষ্ঠিত এই বাঁধ, যা বিশাল উন্নয়ন হিসেবে চিহ্নিত, তা খোলা চোখে দেখলে উপস্থিত হবে শুধু মানববিধ্বংসী বা প্রকৃতি বৈরী নয়, বর্ণবাদী জাতিবিদ্বেষী একটি প্রকল্প হিসেবেই। বাঁধের মাধ্যমে কাপ্তাই লেক নামে যে কৃত্রিম জলাধার তৈরি করা হয় তাতে পুরনো রাঙ্গামাটি বিলুপ্ত হয়। এক লাখ একর আবাদি জমি ডুবে যায়। এক লাখ মানুষ উদ্বাস্তু হয়ে পড়ে। আর বিদ্যুৎ, যা হয় চলে যায় বাইরে। কাপ্তাই লেক হয় পর্যটন স্পট! এটি নিয়ে আরো বৃহৎ পর্যটন কেন্দ্র দাঁড় করানোর জন্য দেশী&Ntilde;বিদেশী বিনিয়োগ হতে যাচ্ছে। এই বাঁধ পুরো অঞ্চলে জাতিগত অবিশ্বাস, দূরত্ব, বিদ্বেষ ও সহিংসতার রক্তাক্ত অধ্যায়ের যে একটি কারণ তা কে অস্বীকার করতে পারে? এর মূল্য কত? পরিমাণ করাই খুব কঠিন। সমতল এলাকার বাঁধগুলো অভিজ্ঞতাও বিভিন্ন দিক থেকে কথিত &lsquo;লাভ&rsquo; নিয়েও আমাদের প্রশ্নের সম্মুখীন করে।<br />\r\n৬<br />\r\nবাংলাদেশের জমিতে সেচ আজকের কথা নয়। বিশেষ করে ধান উৎপাদনের ক্ষেত্রে সেচ প্রয়োজন হয়ই। হাজার বছরের এই সেচ ব্যবস্থা বরাবরই ছিল অনাড়ম্বর এবং এতে ভূ&Ntilde;উপরিস্থ পানিই ব্যবহার হতো। তার মানে নদী, নালা, খাল, বিলের পানিই বিভিন্ন অযান্ত্রিক পদ্ধতিতে জমি পর্যন্ত নেয়া হতো। এসব পদ্ধতির মধ্যে খাল খনন, নালা কাটা, পানি জমিয়ে রাখা, সেখান থেকে পানি জমিতে দেওয়ার জন্য মাটি কাঠ বাঁশের নানা মাধ্যম (দোন, সেউতি, কলসি) ব্যবহার করা ছিল অন্যতম। বাংলাদেশে যেসব প্রজাতির ধান চাষ হতো তার সঙ্গে এই ধারার সেচ ছিল সঙ্গতিপূর্ণ। নানা পরিকল্পনার মাধ্যমে ভূ&Ntilde;উপরিস্থ পানি সুলভ করে বিভিন্ন ধরনের পদ্ধিতিতে তাকে ধানের গোড়ায় পৌঁছে দেয়ার এই কাজ সামগ্রিক পানি&Ntilde;জমি&Ntilde;প্রাণী ভারসাম্যে কোনো বিঘ&oelig; ঘটার সম্ভাবনা ছিল না।<br />\r\nএই সেচ পদ্ধতি আকর্ষকর হয়ে গেল নতুন &lsquo;উচ্চ ফলনশীল (উফশী)&rsquo; বীজের চাষের ক্ষেত্রে। হাজার বছরের জৈব সার পদ্ধতিও অকার্ষকর হয়ে গেল এই বীজের ক্ষেত্রে। এই বীজের চাহিদা ভয়ংকর। আর কথিত সবুজ বিপ্লবের কেন্দ্র হচ্ছে এই বীজ। এই বীজের উদ্ভাবন ঘটেছে বিভিন্ন আন্তর্জাতিক সংস্থা সমর্থিত ম্যানিলায় অবস্থিত ইন্টারন্যাশনাল রাইস রিসার্চ ইনস্টিটিউট বা ইরি&Ntilde;তে। সেই জন্য এই বীজের মূল নাম &lsquo;ইরি&rsquo;। এখন এর নানা ধরন অনেক নম্বর দিয়েও প্রকাশ করা হয়। এই বীজ উদ্ভাবন এবং বিভিন্ন দেশে তার প্রতিষ্ঠার ক্ষেত্রে প্রভাব বিস্তার, ঋণ সম্প্রসারণ, কনসালট্যান্ট ব্যবহার ইত্যাদি ক্ষেত্রে মুখ্য ভূমিকা ছিল মার্কিন রাষ্ট্রীয় প্রতিষ্ঠান ইউএসএইড&Ntilde;এর। ইউএসএইড&Ntilde;এর এই আগ্রহের রহস্য হলো এই পুরো প্যাকেজে কৃষিভিত্তিক বহুজাতিক সংস্থাগুলোর বিশাল বাণিজ্যের সম্ভাবনা। এছাড়া রাজনীতিও ছিল। &lsquo;লাল বিপ্লব&rsquo; ঠেকানো তখন খুবই জরুরি ছিল!<br />\r\nসবুজ বিপ্লবের প্যাকেজে ছিল চারটি উপাদান যা একটি আরেকটির সঙ্গে সম্পর্কিত। এগুলোর কোনোটিই এদেশের কৃষিতে পরিচিত নয় এবং সবগুলোই ছিল আমদানি করার পণ্য। এই চারটি উপাদান হলো: ১. উফশী বীজ; ২. রাসায়নিক সার; ৩. যান্ত্রিক সেচ; ৪. কীটনাশক ওষুধ। উফশী বীজ উৎপাদনের জন্য মাটিতে জৈব সার মেশানো মোটেই যথেষ্ট নয়। এর জন্য দরকার রাসায়নিক সার&Ntilde;ইউরিয়া, পটাশ, সুপার ফসফেট ইত্যাদি। পরিমাণও লাগে বেশি। এই বীজের পানির চাহিদাও খুব বেশি আর তা অযান্ত্রিক পদ্ধতিতে যোগান দেয়া সম্ভব নয়। আর ভূ&Ntilde;উপরিস্থ পানি দিয়েও এই যোগান সম্ভব নয়। সুতরাং এলো যান্ত্রিক পদ্ধতি, গভীর নলকূপ, অগভীর নলকূপ; আর পানি তোলা শুরু হলো মাটির নিচ থেকে।<br />\r\nনলকূপ বিদেশ থেকে দেশে আমদানি আর পাতালের পানি ভূমির ওপরে টেনে আনা শুরু হলো। কিন্তু আরো এক সমস্যা দেখা দিল কীটপতঙ্গ নিয়ে। কীটপতঙ্গ সবসময়ই ছিল, দেশী বীজের ধান উৎপাদনের অভিজ্ঞতায় কৃষককে কম কীটপতঙ্গ মোকাবেলা করতে হয়নি। এই মোকাবেলার পদ্ধতি ছিল এখানকারই, গাছ রস লতা পাতা ইত্যাদি, অজানা অতীত থেকে যার চর্চা, ও বিকাশ। এসব কীটপতঙ্গ বেশিরভাগ ক্ষেত্রেই ছিল নিয়ন্ত্রণযোগ্য। কিন্তু ইরি ধান</span></p>\r\n','',1,15,'2013-01-31 00:00:00',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452093645_jsr3-2.png'),(5,'মধ্যপ্রাচ্যে বাংলাদেশের শ্রমিকসম্পদ','মধ্যপ্রাচ্যে-বাংলাদেশের-শ্রমিকসম্পদ','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">গত বছরের অক্টোবরে সৌদি আরবে ৮ বাংলাদেশীর শির&ntilde;েদের ঘটনার পরিপ্রেক্ষিতে বদরুদ্দীন উমর এই নিবন্ধটি লিখে একটি দৈনিকে পাঠালে তারা লেখাটি ছাপতে অক্ষমতা প্রকাশ করে। এর পর আরো দুটি পত্রিকাও একইভাবে লেখাটি তাদের পক্ষে প্রকাশ করা সম্ভব নয় বলে জানায়। এর মধ্য দিয়ে বাংলাদেশে সৌদি আরবের হাত কতটুকু সম্প্রসারিত তার একটি চিত্র মেলে। এখানে সেই লেখাটি আমরা প্রকাশ করছি। [নির্বাহী সম্পাদক]<br />\r\nইসলামের প্রাথমিক যুগে হজরত মুহাম্মদের সময়, চার খলিফার সময়, এমনকি উমাইয়া ও আব্বাসী খলিফাদের সময়ে কোনো অপরাধীর প্রকাশ্যে শির&ntilde;েদ করা হয়েছিল এমন কোনো তথ্য আমাদের জানা নেই। সে সময়ে যে খুনখারাবি হয়নি মানুষের প্রতি কোনো নিষ্ঠুর আচরণ করা হয়নি এমন নয়। চার খলিফার তিনজন হযরত উমর, হযরত ওসমান, হযরত আলী আততায়ীর হাতে<br />\r\nনিহত হয়েছিলেন। হযরত হাসান&Ntilde;হোসেনের প্রতি যে নিষ্ঠুরতা করা হয়েছিল তা সবারই জানা। কিন্তু বিগত ৭ অক্টোবর সৌদি আরবের রাজধানী রিয়াদে সে দেশের তথাকথিত আইন অনুযায়ী প্রকাশ্যে, জনসমক্ষে যেভাবে আট বাঙলাদেশী শ্রমিকের শির&ntilde;েদ করা হয়েছে এ ধরনের কোনো বর্বর হত্যাকাণ্ড খলিফাদের সময়ে হয়নি। অন্তত ইসলামী আইন কার্যকর করার লক্ষ্যে এ ধরনের ঘটনা ঘটেনি।<br />\r\n২০০৭ সালে এক মিসরীয় নাগরিককে হত্যার দায়ে এই শ্রমিকদের মৃত্যুদণ্ড দেওয়া হয়। একজনকে হত্যার অপরাধে আটজনকে মৃত্যুদণ্ড দেওয়া কতখানি যুক্তিযুক্ত এটা এ ক্ষেত্রে অবশ্যই প্রশ্ন করা যেতে পারে। কিন্তু যা এ ক্ষেত্রে বিশেষভাবে লক্ষ্য করার বিষয় তা হলো, এই দণ্ড কার্যকর করার পদ্ধতি। দুনিয়ার অধিকাংশ দেশেই সর্বোচ্চ অপরাধের জন্য মৃত্যুদণ্ডের বিধান আছে এবং এই দণ্ড কার্যকর করার পদ্ধতিও আছে। এই পদ্ধতির একটি দিক হল, তা প্রকাশ্যে কার্যকর না করে কারাগারের মধ্যে, লোকচক্ষুর আড়ালে কার্যকর করা। মৃত্যুদণ্ডের মতো চরম দণ্ড শাস্তি হিসেবে আইনগতভাবে একেবারে রহিত করার পক্ষেও একটা জোরালো মত বিশ্বের বিভিন্ন দেশের জনগণের মধ্যে আছে। মানুষের প্রাণ সংহার করা কোনো সুখের কাজ নয়। কিন্তু যদি ক্ষেত্রবিশেষ এই দণ্ডের ব্যবস্থা থাকে, তা হলে তা কার্যকর করার পদ্ধতির মধ্যেও মানবিকতা, সভ্যতা ইত্যাদির ব্যাপার আছে। প্রাচীন রোমে অপরাধীর মৃত্যুদণ্ড কার্যকর করা হতো অ্যাম্ফিথিয়েটার বা তখনকার স্টেডিয়ামের ভেতর সিংহের সামনে তাকে ছেড়ে দিয়ে। এ কাজ করা হতো অ্যাম্ফিথিয়েটার ভর্তি লোকের সামনে। সিংহ মৃত্যুদণ্ডপ্রাপ্ত আসামির ওপর ঝাঁপিয়ে পড়ে তাকে খেয়ে ফেলত। উপস্থিত দর্শকরা সেই দৃশ্য দেখে মজা পেত! তখনকার দিনে এটা ছিল রোমের নাগরিকদের বিনোদনের একটা উপলক্ষ্য।<br />\r\nসৌদি আরবে জনসমক্ষে শির&ntilde;েদ করে মৃত্যুদণ্ডাদেশ কার্যকর করার ঘটনা এই প্রথম নয়। সেখানকার বর্তমান শাসক শ্রেণীর রাষ্ট্রীয় আইন অনুযায়ীই এই কাজ নিয়মিতভাবেই করা হয়ে থাকে। এভাবে প্রতিবছর অনেক লোকেরই মৃত্যুদণ্ড কার্যকর করা হয়। এই কাজ যে পুণ্যকর্ম বা সওয়াবের কাজ এটা দেখবার জন্য এই হত্যাকাণ্ড হয় কোনো না কোনো শুক্রবার। এ কারণে মৃত্যুদণ্ডাদেশ প্রাপ্ত লোকেরা কারাগারে আটক থাকার সময় শুক্রবার এলে তাদের মধ্যে আতঙ্ক সৃষ্টি হয়। গত ৭ অক্টোবর জুমার নামাজের পর যে আট জনের মৃত্যুদণ্ড কার্যকর করা হয়েছে তার মধ্যে টাঙ্গাইলের শফিকুল ইসলাম নামে একজন ওই দিনই সকালে তার এক আত্মীয়ের সাথে টেলিফোনে কথা বলার সময় কাঁদতে কাঁদতে বলেন, &lsquo;শুক্রবার আসলেই ভয় লাগে&rsquo; (কালের কণ্ঠ, ৯.১০.২০১১)। শুক্রবার এলে ভয় লাগলেও ঠিক ওই শুক্রবারই যে তার ভয় লাগার শেষ হবে এটা তার জানা ছিল না। ৯ অক্টোবরের যুগান্তর পত্রিকার প্রথম পৃষ্ঠায় তাদের একজনের মৃত্যুদণ্ড কার্যকর করার ছবিও প্রকাশিত হয়েছে। এই ঘটনা দেখার জন্য ঘটনাস্থলে জমায়েত হওয়া কিছু লোককেও এই ছবিতে দেখা যাচ্ছে।<br />\r\nঅ্যামনেস্টি ইন্টারন্যাশনালের রিপোর্ট অনুযায়ী প্রতিবছরই সৌদি আরবে বহু লোককে এভাবে মৃত্যুদণ্ড দেওয়া হয়। এই দণ্ডপ্রাপ্তদের অধিকাংশই উন্নয়নশীল দেশগুলো থেকে আসা গরিব শ্রমিক। এ ধরনের অভিযুক্তরা সাধারণত আদালতে নিজেদের আইনজীবী নিযুক্ত করতে পারেন না। তারা নিজেরা উপযুক্ত আরবী না জানার কারণে তাঁদের বিরুদ্ধে অভিযোগ এবং সেই অভিযোগ খণ্ডন করার ক্ষেত্রেও তাঁরা কিছু করতে পারেন না। অনেক সময় তাঁদের বিরুদ্ধে আদালতে যে শুনানি হতে থাকে সে বিষয়েও তাঁরা কিছুই জানতে পারেন না।<br />\r\nঅ্যামনেস্টি ইন্টারন্যাশনালের রিপোর্ট দেখে জানা যায় যে, ২০০৭ সালে সৌদি আরবে ৭৬ বিদেশীসহ ১৫৮ জনের শির&ntilde;েদ করা হয়। ২০০৮ সালে শির&ntilde;েদ শিকার হওয়া লোকের সংখ্যা ১০২ জন। এর মধ্যে ৪০ জন বিদেশী। ২০০৯ সালে ১৯ বিদেশীসহ মোট ৬৯ জন এবং ২০১০ সালে ৬ বিদেশীসহ ২৭ জনের শির&ntilde;েদ করা হয়। এ বছর এ পর্যন্ত ৫৮ জনের শির&ntilde;েদ করা হয়েছে। এর মধ্যে বিদেশীর সংখ্যা ২০ জন। এই তথ্য থেকে বোঝা যায় যে, সৌদি আরবে ব্যাপক সংখ্যক শির&ntilde;েদ করা হয়ে থাকে। অ্যামনেস্টি ইন্টারন্যাশনাল এ নিয়ে উদ্বেগ প্রকাশ করেছে।<br />\r\nগত জুন মাসে এক ইন্টারন্যাশনাল নারী শ্রমিকের শির&ntilde;েদ করা হয়। এর প্রতিবাদে জাকার্তায় সৌদি দুতাবাসের সামনে বিক্ষোভ প্রদর্শন করা হয়। এই হত্যাকাণ্ড রহিত করার জন্য ইন্দোনেশীয় প্রেসিডেন্ট সৌদি আরবের কাছে দণ্ডপ্রাপ্ত এই নারীর প্রাণদণ্ড মওকুফ করার আবেদন করেছিলেন। কিন্তু সৌদি সরকার তার আবেদন প্রত্যাখ্যান করে। বাংলাদেশের রাষ্ট্রপতি আট বাংলাদেশী নাগরিকের প্রাণদণ্ড মওকুফের জন্য আবেদন করেছিলেন। কিন্তু সৌদি সরকার তা অগ্রাহ্য করেই আটজনের মৃত্যুদণ্ড কার্যকর করেছে।<br />\r\nসম্প্রতি সৌদি দূতাবাসের সামনে এক সৌদি ঊর্ধ্বতন কর্মকর্তা খুন হয়েছে। সাংবাদিকরা এর পিছনের কারণ হিসাবে যে সব কারণ থাকতে পারে বলে মনে করছেন তার মধ্য নারী ঘটিত ও মদ্যপানের উশৃংখলতার ব্যাপারটি আছে। তাছাড়া মধ্যরাতে জগিং করতে বের হওয়ার ব্যাপারটিও রহস্যজনক। নারী ঘটিত কেলেংকারীর জন্য যদি ঐ কর্মকর্তার মৃত্যুর কারণ হয়ে থাকে তাহলে সৌদি সরকারের এ খুনের ব্যাপারটি তদন্ত কিম্বা বিচার দাবী করা লজ্জাজনক ব্যাপার।<br />\r\nবাংলাদেশের জন্য এই সৌদী বর্বরতার বিশেষ প্রাসঙ্গিকতা আছে। কারণ এখানকার কিছু ইসলামী মৌলবাদী সংগঠন সৌদি আরবের সঙ্গে আদর্শগতভাবে এবং কার্যক্ষেত্রে সম্পর্কিত আছে। বাংলাদেশে রাজনৈতিক সংগঠনের সঙ্গে সৌদী আরবের এ ধরনের সম্পর্ক নতুনই বলতে হবে। পাকিস্তানি আমলে জামায়াতে ইসলামী থাকলেও তার সঙ্গে সৌদী আরবের এ ধরনের কোনো ঘনিষ্ঠ সম্পর্ক ছিল না। তারা সৌদি আরবের সঙ্গে এক ধরনের আদর্শিক একাত্মতা বোধ করলেও অন্য কোনো দিক দিয়ে সৌদি আরবের সঙ্গে তার কোনো সম্পর্ক ছিল না। কিন্তু বাংলাদেশের পরিস্থিতি দাঁড়িয়েছে অন্য রকম। বাংলাদেশ আমলে জামায়াতে ইসলামীসহ অন্য ইসলাম ধর্মীয় মৌলবাদী সংগঠনে এখানকার কাজকর্মের মধ্যে যে নিষ্ঠুরতার পরিচয় পাওয়া যায় এটা পাকিস্তান আমলে জামায়াতে ইসলামীর মধ্যেও দেখা যেত না। তখন অবশ্য এই নিষ্ঠুরতা করার মতো রাজনৈতিক আবহাওয়া বাংলাদেশে ছিল না। পূর্ব পাকিস্তানে ইসলামী মৌলবাদী রাজনীতি নিম্নগ্রামে তখন বাঁধা থাকত। বাংলাদেশ আমলে এদিক দিয়ে অনেক পরিবর্তন ঘটেছে। সৌদি আরবের সঙ্গে বাংলাদেশের জামায়াতে ইসলামীর ঘনিষ্ঠ সম্পর্ক স্থাপিত হয়েছে।<br />\r\nবাংলাদেশ জামানায় জামায়াতে ইসলামীর সঙ্গে সৌদি আরবের ঘনিষ্ঠ যোগাযোগ ও সম্পর্কের প্রভাব তাদের রাজনীতি ও কর্মকাণ্ডের মধ্যে প্রতিফলিত হয়েছে। আশির দশক থেকে প্রকাশ্যে কাজ করার সাংগঠনিক অনুমতি পাওয়ার পরে জামায়াতে ইসলামীর ছাত্র সংগঠন নিজের নাম পরিবর্তন করে ইসলামী ছাত্রশিবির নামে পরিচিত হয়। এই ছাত্র শিবিরই বাংলাদেশে ছাত্রদের মধ্যে সহিংসার পথ প্রদর্শক।<br />\r\nপ্রেসিডেন্ট জিয়াউর রহমানের সময় মাদ্রাসার সিলেবাসের তথাকথিত আধুনিকায়নে পর মাদ্রাসার ছাত্ররা সাধারণ কলেজ বিশ্ববিদ্যালয়ে প্রথমবারের মতো প্রবেশাধিকার পায়। তারপর তারা কলেজ বিশ্ববিদ্যালয়গুলোতে নিজেদের সংগঠন গড়ে তোলে এবং অন্য ছাত্র সংগঠনগুলোর কর্মীদের ওপর আক্রমণ নির্যাতন শুরু করে। প্রায়ই তাদের দেখা যায় সাধারণ ছাত্রদের পায়ের রগ কেটে তাদের পঙ্গু করে দিতে। এছাড়া আরও অনেক হিংসাত্মক কর্মকাণ্ডেও তারা লিপ্ত হয়। পরে শুধু জামায়াতে ইসলামীর ছাত্র সংগঠনই নয়, অন্যান্য ইসলামী সংগঠনও বেপরোয়াভাবে হিংসাত্মক কার্যকলাপে নিযুক্ত হয়। এদিক দিয়ে আবদুর রহমান ও বাংলা ভাই নামে কথিত লোকদের সহিংস কার্যকলাপ পরিচিত ব্যাপার। তারা গ্রাম ও শহরাঞ্চলে ইসলামের নামে জনগণের ওপর অনেক নিষ্ঠুর নির্যাতন করে। চারদিকে বোমা বিস্ফোরণের ঘটনাও তারা ঘটায়। উত্তর বাংলার রাজশাহী অঞ্চলে বাংলা ভাই, আব্দুর রহমান হয়ে দাঁড়ায় এক আতঙ্কের ব্যাপার। এই সময়েই বাংলাদেশে একবারে ৫০০ জায়গাতে একই সময়ে বোমা বিস্ফোরণ ঘটানো হয়। এ সবই করা হয় ইসলামের নামে। এই তথাকথিত ইসলামী সংগঠনগুলোর সহিংসতা ও নিষ্ঠুরতা এ দেশের রাজনীতিতে এক নতুন ব্যাপার। আওয়ামী লীগ, বিএনপি ইত্যাদি রাজনৈতিক দলের মধ্যে যে হিংসা ও নিষ্ঠুরতা দেখা যায় তার কারণ অন্য। তার সঙ্গে ধর্মের সম্পর্ক নেই। ইসলামী সংগঠনগুলো ধর্মের নামে যে সহিংসতা ও নিষ্ঠুরতা করে সেগুলোর প্রেরণা আসে সৌদি আরবের মতো দেশের সাথে তাদের আদর্শিক সম্পর্ক ও যোগাযোগ থেকে। এ কারণে ইসলামের নামেই সহিংস ও নিষ্ঠুরতা চালিয়ে তারা ইসলামকে কলঙ্কিত করে।<br />\r\nমধ্যপ্রাচ্যে যেসব দেশে স্বৈরতান্ত্রিক ও ফ্যাসিস্ট শাসন ও শাসকগোষ্ঠী আছে তার মধ্যে সব থেকে নিকৃষ্ট হলো সৌদি আরব। মিসর, লেবানন, আলজেরিয়া, তিউনিসিয়া, লিবিয়া, জর্ডান ইত্যাদি দেশে যে সামাজিক পরিবর্তন দেখা যায় সৌদি আরবে তার কিছুই নেই। সেখানে নারী নির্যাতনও সব থেকে বেশী। নারীর প্রতি নিষ্ঠুর আচরণ সেখানে এক সাধারণ ব্যাপার। সেখানে জনসমক্ষে মৃত্যুদণ্ডাদেশ প্রাপ্ত ব্যক্তির শির&ntilde;েদ যেভাবে করা হয় এটা সৌদি সমাজের নিদারুণ পশ্চাদপদতা ও এই পশ্চাদপদতার ওপর দাঁড়িয়ে থাকা ফ্যাসিবাদী শাসন বলবৎ থাকার কারণেই সম্ভব হয়।<br />\r\nমধ্যপ্রাচ্যের শ্রমবাজারে কর্মরত বাংলাদেশী শ্রমিকদের প্রতি মধ্যপ্রাচ্যের সরকারগুলি বিচারের নামে যে বর্বরতা চালায় তার হাত থেকে শ্রমিকদেরকে তথা শ্রম সম্পদকে রক্ষা</span></p>\r\n','',1,15,'2016-01-07 02:30:56',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452155456_jsr4-1.png'),(6,'জাতীয়তাবাদ রাষ্ট্রীয় স্বাধীনতা','জাতীয়তাবাদ-রাষ্ট্রীয়-স্বাধীনতা','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">মার্কিন যুক্তরাষ্ট্র ও পশ্চিমা বৃহৎ শক্তিবর্গ জাতি, জাতীয় সংস্কৃতি, জাতীয়তাবাদ, জাতিরাষ্ট্র ইত্যাদির প্রতি সম্পূর্ণ বিরূপ ও অসহিষ্ণু। বাংলাদেশে যারা এদের অন্ধ অনুগামী তারাও একই মনোভাবের পরিচয় দেয়। এশিয়া, আফ্রিকা, লাতিন আমেরিকার জাতীয়তাবাদীরা ব্রিটেন, ফ্রান্স, পর্তুগাল, হল্যান্ড প্রভৃতি উপনিবেশকারী শক্তির বিরুদ্ধে সংগ্রাম করে স্বাধীনতা অর্জন করেছে। পরে তারা সাম্রাজ্যবাদ বিরোধী আন্দোলন করেছে এবং এখনও করছে। এজন্যই পশ্চিমা বৃহৎ শক্তিবর্গ জাতি, জাতীয়, সংস্কৃতি, জাতীয়তাবাদ, জাতিরাষ্ট্র ইত্যাদির প্রতি বিরূপ ও অসহিষ্ণু। এ অবস্থায় দুর্বল দরিদ্র রাষ্ট্র সমূহের জনসাধারণকে সাম্রাজ্যবাদীদের ইচ্ছা অনুযায়ী চললে হবে না। নিজেদের আত্মরক্ষার ও আত্মবিকাশের প্রয়োজনে এটা তাদের বুঝতে হবে।<br />\r\nঅন্যথায় সমূহ সর্বনাশ।<br />\r\nরাষ্ট্রের উৎপত্তি সম্পর্কে হব্স্, লক্, রুশো ও এঙ্গেলসের অভিমত আমরা রাষ্ট্রবিজ্ঞানের পাঠ্যবইতে পড়ি। আমরা কখনও ভেবে দেখেছি কি যে, আসামে, বাংলায়, কেরালায়, গুজরাটে, মহারাষ্ট্রে, পাঞ্জাবে, সিন্ধুতে ও উত্তর প্রদেশেও এক সময়ে রাষ্ট্রের উৎপত্তি হয়েছিল। এশিয়া, আফ্রিকা, উত্তর ও দক্ষিণ আমেরিকা, ইউরোপ, অস্ট্রেলিয়া সকল মহাদেশের সকল দেশেই আলাদা আলাদা ভাবে রাষ্ট্রের উৎপত্তি ও বিকাশ ঘটেছিল। যাদের আদিবাসী বলা হয় সেই ক্ষুদ্র নৃগোষ্ঠীসমূহের মধ্যেও নিজেদের রাষ্ট্র গঠনের প্রবণতা লক্ষ করা যায়। বাংলা ভাষার দেশে আদিতে গঁয়ী বা গাঁই, জনপদ, জনপদরাজ্য, রাজ্য ইত্যাদির উদ্ভব, বিকাশ ও বিলয়ের কথা ইতিহাসবিদেরা কেউ কেউ বলেছেন। রাষ্ট্রের উদ্ভব, বিকাশ ও বিলয়ের ব্যাপারটি বুঝবার জন্য এঙ্গেলসের ঞযব ঙৎরমরহ ড়ভ ধসরষু চৎরাধঃব চৎড়ঢ়বৎঃু ধহফ ঝঃধঃব (পরিবার ব্যক্তিগত সম্পত্তি ও রাষ্ট্রের উৎপত্তি বইটি খুবই সহায়ক। মানুষের সমাজে প্রভু ও দাস (সধংঃবৎং ধহফ ংষধাবং) &lsquo;সামন্ত প্রভু ও ভূমিদাস ( ভবঁফধষ ষড়ৎফং ধহফ ংবৎভং)&rsquo; রাজা ও প্রজা (শরহমং ধহফ ংঁনলবপঃং) ইত্যাদি ছিল। এসবের মধ্যে ভারত উপমহাদেশে ছিল জাতিভেদ প্রথা ও ব্রাহ্মণ্যবাদ। নারী&Ntilde;পুরুষ সম্পর্ক ও পরিবার ব্যবস্থায়ও বৈচিত্র্য ও বিবর্তন আছে। সতীদাহ প্রথা পর্যন্ত ছিল। মানুষের স্বভাব ও আচরণও বিবর্তন ও রূপান্তরের ধারায় আছে। সামাজিক ও প্রাকৃতিক পরিবেশও বিকাশশীল। বিকাশের ধারায় মানুষের ইচ্ছা&Ntilde;অনিচ্ছা এবং চিন্তাশক্তি ও শ্রমশক্তিও পালন করে গুরুত্বপূর্ণ ভূমিকা।<br />\r\nআধুনিক গণতন্ত্র ও জাতিরাষ্ট্রের উদ্ভব ঘটেছে রেনেসাঁস ও শিল্পবিপ্লবের ধারায়। সমাজতান্ত্রিক রাষ্ট্রের উত্থান-পতনেরও ইতিহাস আছে। আর প্রত্যেক রাষ্ট্রের ইতিহাসেই সুসময় ও দুঃসময় আছে।<br />\r\nদেশ ও রাষ্ট্র এক নয়। দেশ প্রকৃতির সৃষ্টি, রাষ্ট্র মানুষের। পাহাড়, পর্বত, সমুদ্র, মরুভূমি ইত্যাদি দুর্ভেদ্য প্রাকৃতিক নানা কিছু দ্বারা পরিবেষ্টিত এক একটি সুবিশাল এলাকা হল এক একটি দেশ। প্রত্যেক দেশের অভ্যন্তরে ভূপ্রকৃতিগত ও নৃগোষ্ঠীগত নানা বৈচিত্র্য লক্ষ করা যায়। দেশের সীমানাও সমরূপ (ঁহরভড়ৎস) হয় না। রাষ্ট্রের রূপ ও প্রকৃতি রাষ্ট্রবিজ্ঞানীরা নির্ণয় করার চেষ্টা করেছেন। সীমানা দ্বারা চিহ্নিত সুনির্দিষ্ট ভূভাগ, সেই ভূভাগের সুনির্দিষ্ট জনগণ, সেই ভূভাগে সেই জনগণের সরকার এবং সেই ভূভাগ ও সেই জনগণের যে কোনো বিষয়ে সেই জনগণের সিদ্ধান্ত গ্রহণের চূড়ান্ত ক্ষমতা বা সার্বভৌমত্ব এই চারটি বিষয় হল রাষ্ট্রের রূপ ও প্রকৃতি নির্দেশক মূল ব্যাপার। আসলে রাষ্ট্রীয় স্বাধীনতাই হল রাষ্ট্রের সার্বভৌমত্ব যার মালিক একালের ধারণা অনুযায়ী জনগণ। রাষ্ট্রের থাকে বিভিন্ন অঙ্গ প্রত্যঙ্গ, যেমন-আইনসভা ও মন্ত্রিপরিষদ, নির্বাহী বিভাগ বা প্রশাসনব্যবস্থা এবং আইন ও বিচারব্যবস্থা। ইউরোপে রেনেসাঁসের ফলে গির্জার কর্তৃত্ব, ধর্মতন্ত্র ও রাজতন্ত্রের বিরুদ্ধে আধুনিক গণতন্ত্রের ও জনগণের সার্বভৌমত্বের ধারণা দেখা দেয়। আগে একমাত্র ঈশ্বরকেই মনে করা হত সার্বভৌম ক্ষমতার অধিকারী। রাজতন্ত্র ও গির্জার কর্তৃত্বের পক্ষে তখন বাইবেলীয়, কোরানীয়, কৌটিলীয়, বা মনুসংহিতার যুক্তি প্রদর্শন করা হত। আধুনিক গণতন্ত্রের ধারণা বিকশিত হওয়ার আগে ব্যক্তিস্বাতন্ত্র্য, মতপ্রকাশের স্বাধীনতা, সংগঠন করার স্বাধীনতা ইত্যাদি স্বীকার করা হত না। স্টিম ইনজিন, ইনজিন চলিত যানবাহন ও কলকারখানা, মুদ্রণযন্ত্র ইত্যাদির প্রচলন দিয়েই শিল্পবিপ্লব শুরু হয়। শিল্পবিপ্লব এক চলমান প্রক্রিয়া। শিল্পবিপ্লবের আগে মানুষের কর্মকাণ্ড ও জীবনপ্রয়াস দেশব্যাপী বিস্তৃত হয়নি। সবই সীমাবদ্ধ ছিল ক্ষুদ্র গণ্ডিতে, অঞ্চলে, এলাকায়। শিল্পবিপ্লবের ফলে মানুষের বিচরণ ও সম্পদের আনা&Ntilde;নেওয়া দেশব্যাপী বিস্তৃত হয়। মুদ্রিত পত্র&Ntilde;পত্রিকা ও বই&Ntilde;পুস্তক ক্রমেই মানুষের ভাবের আদান&Ntilde;প্রদানকে নিবিড়তর করে। তাতে মানুষের মধ্যে দেশভিত্তিক ঐক্যবোধ দেখা দিতে থাকে। মাতৃভাষার ও আঞ্চলিক ভাষার বৈচিত্র্য অতিক্রম করে গড়ে ওঠে জাতীয় ভাষা। এরই মধ্যে এক পর্যায়ে অনেক দেশে অনেকের মধ্যে ধারণা দেখা দেয়, আমরা যদি আমাদের দেশে আমাদের জন্য একটি ভালো রাষ্ট্র গড়ে তুলতে পারি তা হলে সেই রাষ্ট্রে আমাদের সবচেয়ে ভালো জীবন যাপনের উপায় হবে। এই ধারণাই জাতীয়তার ধারণা, এই বোধই জাতীয়তাবোধ, এই চেতনাই জাতীয় চেতনা। এ থেকে দেখা দেয় জাতীয়তাবাদ বা জাতিরাষ্ট্র গঠনের মতবাদ ও আন্দোলন। ইউরোপে উনিশ শতকে এবং এশিয়ায় বিশ শতকে দেখা দেয় জাতীয়তাবাদী আন্দোলন। ইউরোপে প্রত্যেক দেশে রাজতন্ত্র ও গির্জার কর্তৃত্ব উৎখাত করে গড়ে তোলা হয় জাতিরাষ্ট্র, আর এশিয়ায় প্রত্যেক দেশে জনগনের মধ্যে পাশ্চাত্য শিক্ষা গ্রহণ করে জাতি হয়ে ওঠার এবং স্বাধীনতা-আন্দোলনের মধ্য দিয়ে জাতিরাষ্ট্র প্রতিষ্ঠার প্রয়াস দেখা দেয়। আধুনিক গণতন্ত্রের ও সার্বভৌমত্বের ধারণা, সেই সঙ্গে জাতিরাষ্ট্রের ধারণা ইউরোপ থেকে পৃথিবীর সকল মহাদেশে ও দেশে ছড়িয়ে পড়ে ক্রমে রাষ্ট্রচিন্তা ও রাষ্ট্র গঠনের বহুবিচিত্র ধারণা বিকশিত হয়। অনেকে মত প্রকাশ করেন যে, পৃথিবীতে মানবকল্যাণ সাধনের সর্ববৃহৎ ও সর্বোৎকৃষ্ট অবলম্বন হবে রাষ্ট্র। ইউরোপে প্লেটো থেকেই এই ধারণা বিকশিত হতে থাকে। ভারতবর্ষে কৌটিল্য রাজ্যকে মনে করেছেন সর্ববৃহৎ ও সবচেয়ে গুরুত্বপূর্ণ মানবীয় প্রতিষ্ঠান।<br />\r\nশিল্পবিপ্লব চলমান আছে। বিজ্ঞান ও প্রযুক্তির নতুন নতুন আবিষ্কার উদ্ভাবন মানুষের জীবনধারা, সমাজব্যবস্থা ও রাষ্ট্রব্যবস্থাকে ক্রমাগত বদলে দিচ্ছে।<br />\r\nভারতে কৌটিল্যের রাষ্ট্রচিন্তার ধারাবাহিক বিকাশ ঘটেনি। ইউরোপে খ্রিস্টীয় মধ্যযুগের ব্যবধান সত্ত্বেও প্লেটো এ্যারিস্টটলের রাষ্ট্রচিন্তার ধারাবাহিক বিকাশ ঘটেছে। গোটা পৃথিবীতেই আধুনিক যুগে গ্রেকো-রোমান-ইউরো- আমেরিকান সভ্যতা-সংস্কৃতির ধারা সবচেয়ে প্রভাবশালী। এশিয়ায় ধর্মকে আর ইউরোপে রাষ্ট্রকে মনে করা হয়েছে মানবকল্যাণের সর্বপ্রধান অবলম্বন। আন্তর্জাতিক যোগাযোগ বৃদ্ধি পাওয়ার ফলে পৃথিবীর সর্বত্র সব বিষয়েই মানুষের ধ্যান&Ntilde;ধারণা ও চিন্তাভাবনা পরিবর্তিত হয়ে চলছে।<br />\r\nরাষ্ট্র গড়ে তোলার জন্য উদ্যোগী জনগণের স্বাধীনতা অপরিহার্য। পরাধীন জনগণের স্বাধীনতা সংগ্রাম ও স্বাধীনতা অর্জনের প্রক্রিয়ার মধ্য দিয়েও রাষ্ট্র গঠন সম্ভব হতে পারে। তবে তার জন্য স্বাধীনতা সংগ্রামের কালেই রাষ্ট্র গঠনের প্রস্তুতি দরকার হয়। রাষ্ট্রে নাগরিকদের মৌলিক অধিকার, আঞ্চলিক ও প্রাতিষ্ঠানিক স্বায়ত্তশাসন এবং জাতীয় ঐক্যও অপরিহার্য। এখানে কামাল আতাতুর্কের একটি কথা মনে পড়ছে। ১৯২০-এর দশকের শুরুতে তুরস্কে প্রগতিশীল রাষ্ট্র গঠনের চিন্তা ও চেষ্টায় অগ্রসর হয়ে তিনি অনুভব করেছিলেন, &ldquo;যেদেশে স্বাধীনতা থাকে না, সেদেশে কেবল ধ্বংস আর মৃত্যুর রাজত্ব চলে। সমস্যা থেকে মুক্তির এবং সকল ক্ষেত্রে প্রগতির মূল উৎস স্বাধীনতা।&rdquo; বাংলাদেশকে প্রগতিশীল জনগণের রাষ্ট্র রূপে গড়ে তোলার জন্য যাঁরা চিন্তা ও কাজ করছেন, করবেন, তাঁদেরকে মোস্তফা কামালের এই কথাটির মর্ম বাংলাদেশের বাস্তবের দিকে তাকিয়ে অবশ্যই গভীরভাবে বুঝতে হবে। স্বাধীনতার অভাবে সাম্রাজ্যবাদী শক্তিসমূহের দ্বারা পরিচালিত হওয়ার ফলে বাংলাদেশের সকল ক্ষেত্রেই উন্নতির সম্ভাবনা বিনষ্ট হয়ে যাচ্ছে। রাষ্ট্রীয় স্বাধীনতা ও প্রাতিষ্ঠানিক স্বায়ত্তশাসন, মৌলিক অধিকার ও ব্যক্তিস্বাতন্ত্র্য ছাড়া বাংলাদেশের রাষ্ট্রীয় সমস্যাবলির এবং জনজীবনের সমস্যাবলির সমাধানও অসম্ভব। গণতন্ত্র কায়েম করা যাবে? অপচয়, অপব্যয় ও অনাচার দূর করা যাবে? সকল ক্ষেত্রে প্রগতির সূচনা করা যাবে? ধ্বংস ও মৃত্যু রোধ করা যাবে? বাংলাদেশে মানুষের মর্যাদা ও রাষ্ট্রের গুরুত্ব সম্পর্কে সচেতনতা কোথায়?<br />\r\nআমাদের প্রায় দেড় হাজার বছরের লিখিত ইতিহাস আছে। তার পেছনে আছে দীর্ঘ সময়ের আদিইতিহাস ও দীর্ঘতর সময়ের প্রাকইতিহাস। আজকের সমস্যার ও সম্ভাবনার পটভূমি হিসেবে এই গোটা ইতিহাসেরই বিচার বিশ্লেষণ একান্ত দরকার। অনেক সমস্যারই সূচনা সুদূর অতীতে প্রতিটি ঐতিহাসিক পর্যায়ের প্রগতিশীল, রক্ষণশীল ও প্রতিক্রিয়াশীল শক্তির ক্রিয়া প্রতিক্রিয়া ও বিজয় পরাজয়ের মধ্য দিয়ে ইতিহাসের অগ্রগতিকে বোঝা দরকার। প্রতিক্রিয়াশীলেরা কায়েমি স্বার্থবুদ্ধিবশে কিংবা নিজেদের হীন স্বার্থে যেকোনো নতুন ভালো কিছুকেই বানচাল করে দিতে চায়। বাংলাদেশে গোটা ইতিহাসকে বাদ দিয়ে কেবল সাম্প্রতিক পঞ্চাশ&Ntilde;ষাট বছরের ঐতিহাসিক ঘটনাবলি নিয়ে সত্যকে মিথ্যা দিয়ে ঢেকে হীন উপায়ে স্বার্থসিদ্ধির আয়োজন করে কারো কারো সাময়িক কিছু লাভ হলেও শেষ পর্যন্ত তাতে সকলেরই ক্ষতি হচ্ছে। যে ধারায় সবকিছু চালানো হচ্ছে তাতে বাংলাদেশে কোনো সুস্থ, স্বাভাবিক, প্রগতিশীল রাষ্ট্র গড়ে উঠবে না। পরনির্ভরতা পরাধীনতারই নামান্তর। সবচেয়ে ক্ষতিকর হল রাজনৈতিক পরনির্ভরতা। বাংলাদেশের দৈনন্দিন রাজনীতিও এখন যুক্তরাষ্ট্র, যুক্তরাজ্য, কানাডা প্রভৃতি রাষ্ট্রর স্থানীয় দুতাবাসসমূহের উপর নির্ভরশীল। স্বাধীনতার কোনো বিকল্প নেই। অবশ্যই স্বাধীনতা আমাদের অর্জন করতে হবে। মুক্তিযুদ্ধের মধ্য দিয়ে যে স্বাধীনতা আমরা অর্জন করেছিলাম তা আমরা রক্ষা করতে পারিনি। সাম্রাজ্যবাদী শক্তির উপর ক্রমাগত নির্ভরশীল হতে হতে আমরা আমাদের অর্জিত স্বাধীনতা হারিয়ে ফেলেছি। স্বাধীনতা অর্জনের জন্য সংগ্রামই এখন আমাদের কেন্দ্রীয় কর্তব্য। এখনকার স্বাধীনতা সংগ্রাম আগেকার স্বাধীনতার সংগ্রামের মতো হবে না&Ntilde;এর রূপ ও প্রকৃতি ভিন্ন হবে। সে বিষয়ে চিন্তা ভাবনা দরকার।<br />\r\nবাংলা ভাষার দেশে দীর্ঘকাল ধরে জনগণ নিজেদের রাষ্ট্র আকাক্সক্ষা করেছে। তার জন্য রাজনৈতিক আন্দোলন, সাম্প্রদায়িক দাঙ্গা, স্বাধীনতাযুদ্ধ ইত্যাদি করা হয়েছে। কিন্তু কেমন যেন উন্মাদনার মধ্য দিয়ে, ঈর্ষা&Ntilde;বিদ্বেষ, হানাহানি, রক্তারক্তির মধ্য দিয়ে সময় কেটে গেছে, রাষ্ট্র গঠন হয়নি। রাষ্ট্র গঠন নিয়ে বাস্তবসম্মত গভীর চিন্তা ভাবনাও করা হয়নি। দেশ আছে, জনগণ আছে। রাষ্ট্র কি আছে আমাদের? সরকারের রাজনৈতিক চরিত্র কী?<br />\r\nব্রিটিশ শাসনামলে জাতীয়তাবাদী আন্দোলনের পর্যায়ে আমরা পাই সর্বভারতীয় জাতীয়তাবাদ, হিন্দু জাতীয়তাবাদ এবং মুসলিম জাতীয়তাবাদ। ভারতে বাঙালি, পাঞ্জাবি, রাজপুত, মারাঠি ইত্যাদি অন্তত পনেরোটি স্বতন্ত্র জাতি আছে এবং সেগুলো নিয়ে ভারতে অন্তত পনোরোটি স্বতন্ত্র স্বাধীন সার্বভৌম রাষ্ট্র গড়ে তোলা উচিত&Ntilde;এ চিন্তাও সে সময়ে ছিল। সেটাই ছিল বাস্তবানুগ দৈশিক জাতীয়তাবাদ। এস ওয়াজেদ আলির ভবিষ্যতের বাঙালি গ্রন্থে এই ধারার চিন্তার পরিচ্ছন্ন প্রকাশ আছে। শেরে বাংলা উত্থাপিত ১৯৪০ সালের লাহোর প্রস্তাবের পটভূমিতে এই ধারার চিন্তাই ছিল এবং তাতে ভারতের পূর্ব ও পশ্চিমাঞ্চলের মুসলিম সংখ্যাধিক এলাকাগুলো নিয়ে একটি নয়, একাধিক মুসলিম রাষ্ট্র প্রতিষ্ঠার প্রস্তাব ছিল। কিন্তু এ ধারার চিন্তা সফল হয়নি, সফল হয়েছে হিন্দু মহাসভার এবং মুসলিম লীগের চিন্তা। কংগ্রেস তখন হিন্দু মহাসভার দ্বারাই পরিচালিত হয়েছিল। শেষ পর্যন্ত জিন্নাহর দ্বিজাতিতত্ত্ব অনুযায়ী ভারত দুই ভাগে বিভক্ত হয়ে ইংরেজ শাসন থেকে মুক্ত হয়। ব্রিটিশ শাসনামলে জাতীয়তাবাদ যথেষ্ট বিকশিত হয়নি, জাতীয়তাবাদের স্থলাভিষিক্ত হয়েছিল সাম্প্রদায়িকতাবাদ। হিন্দু মহাসভার প্রভাবে কংগ্রেসের সর্বভারতীয় জাতীয়তাবাদ কার্যক্ষেত্রে হিন্দু&Ntilde;সাম্প্রদায়িকতাবাদে পর্যবসিত হয়েছিল। আর মুসলিম লীগ জাতীয়তাবাদের বদলে গ্রহণ করেছিল মুসলিম সাম্প্রদায়িকতাবাদ। হিন্দু মহাসভা তখন বাংলাকে ভাগ করার জন্য বদ্ধপরিকর হয়ে কাজ করেছিল। ভারত ভাগের জন্যও এই দল তৎপর ছিল। আর গোটা প্রক্রিয়ায় আগাগোড়া ক্রিয়াশীল ছিল ঔপনিবেশিক ব্রিটিশ শাসকদের উরারফব ধহফ জঁষব চড়ষরপু।<br />\r\nআমাদের পাকিস্তানকালে পূর্ববাংলায় পাকিস্তানি জাতীয়তাবাদ দানা বাঁধেনি, আর মুসলিম সম্প্রদায়িকতাবাদও আবেদন হারায়। পূর্ব পাকিস্তানে&Ntilde;পশ্চিম পাকিস্তানে বৈষম্য দ্রুত বেড়ে যেতে থাকে, পূর্ব পাকিস্তান কেবলই বঞ্চনার শিকার হতে থাকে। সে অবস্থায় এখানে স্বতন্ত্র বাঙালি সংস্কৃতি ও বাঙালি জাতীয়তাবাদ আবেদনশীল হয় এবং প্রবল থেকে প্রবলতর হতে থাকে। যুক্তফ্রন্টের একুশ দফায় ছিল বাঙালি জাতীয়তাবাদেরই অভিব্যপ্তি। পরে জনপ্রিয় আওয়ামী লীগ শেখ মুজিবুর রহমানের নেতৃত্বে বাঙালি জাতীয়তাবাদ নিয়ে গড়ে ওঠে ছয়দফা আন্দোলন। তারপর সংঘটিত হয় স্বাধীনতা সংগ্রাম এবং ১৯৭১-এ যুদ্ধের মধ্য দিয়ে স্বাধীন রাষ্ট্র হিসেবে প্রতিষ্ঠিত হয় বাংলাদেশ।<br />\r\nউনিশ ও বিশ শতকে বাঙলায় হিন্দু ও মুসলমান দুই সম্প্রদায়ের অভ্যন্তরে ধর্মসংস্কার ও সমাজসংস্কার আন্দোলন, রঙ্গলাল&Ntilde;হেমচন্দ্র&Ntilde;মধুসুদন&Ntilde;নবীনসেনের কাব্যসাধনা,অক্ষয়কুমার&Ntilde;বিদ্যাসাগর&Ntilde; বিবেকানন্দ ত্রিবেদীর বলিষ্ঠ চিন্তাধারা, বঙ্কিম রবীন্দ্রনাথ&Ntilde;শরৎচন্দ্রের সাহিত্য, বঙ্গভঙ্গ বিরোধী স্বদেশী আন্দোলন, মুসলিম লীগ ও হিন্দু মহাসভার প্রতিষ্ঠা, কংগ্রেস মুসলিম লীগ ও হিন্দু মহাসভার ক্রিয়া&Ntilde;প্রতিক্রিয়া পাকিস্তান আন্দোলন ও পাকিস্তান প্রতিষ্ঠা, পূর্ববাংলার রাষ্ট্রভাষা আন্দোলন, পাকিস্তানবাদ, বাঙালি সংস্কৃতি, গণতন্ত্র, সমাজতন্ত্র ইত্যাদির প্রচার, ছয় দফা আন্দোলন, উনসত্তরের গণঅভ্যুত্থান, একাত্তরের মুক্তিযুদ্ধ ইত্যাদি ঘটনা একটি অন্যটির সঙ্গে অবিচ্ছেদ্য সম্পর্ক যুক্ত। এসবের মধ্যে কারণ&Ntilde;করণীয় করণ সম্পর্ক আছে, স্বার্থের সংঘাত ও অন্ধ ক্রিয়া প্রতিক্রিয়া আছে, মূল্যবোধ যুক্তিবিচার ও সার্বজনীন কল্যাণবুদ্ধি আছে। এসবের ধারাবাহিকতায় বাংলাদেশ প্রতিষ্ঠার পরবর্তী চার দশকের দেশি বিদেশি নানা শক্তির ক্রিয়া&Ntilde;প্রতিক্রিয়ার মধ্য দিয়ে সৃষ্টি হয়েছে আমাদের আজকের আর্থ&Ntilde;সামাজিক&Ntilde;রাষ্ট্রিক ও সংস্কৃতিক বাস্তবতা। সমস্যা ও সম্ভাবনার প্রকৃতি গোটা ইতিহাসব্যাপী বিস্তৃত। নতুন ভবিষ্যত সৃষ্টি করতে হলে অতীত সম্পর্কেও দরকার হয় নতুন উপলব্ধি।<br />\r\nবর্তমান বাস্তবতা থেকে অভিপ্রেত উন্নততর নতুন বাস্তবতায় উত্তীর্ণ হতে হলে দরকার বাংলাদেশ রাষ্ট্রের রূপ ও প্রকৃতি এবং এর স্বাধীনতার স্বরূপ ও গুরুত্ব উপলব্ধি করা। তার জন্য জাতীয়তাবাদ, আন্তর্জাতিকতাবাদ ও বিশ্বায়নের ঐতিহাসিক বিকাশ ও প্রকৃতি বুঝতে হবে। রাষ্ট্রের অপরিহার্যতা এবং রূপ ও স্বরূপ সম্পর্কে গভীর জ্ঞান অর্জন করতে হবে। যুক্তরাষ্ট্র, ইউরোপীয় ইউনিয়ন ও তাদের দালালদের নিষেধ উপেক্ষা করে নিজেদের রাষ্ট্র গঠনের জন্য যা যা দরকার, একে একে সবই আমাদের করতে হবে। চলমান দূতাবাসমুখী রাজনীতি সর্বতোভাবে বর্জন করতে হবে। কথিত সিভিল সোসাইটিসমূহের মাধ্যমে পম্চিমা বৃহৎ শক্তিবর্গের কুটনীতি নির্ভর রাজনীতিও আমাদের বর্জন করতে হবে। বিদেশি সকল শক্তিকে বাংলাদেশের অভ্যন্তরীণ ব্যাপারাদি নির্ধারণ করা থেকে দূরে রাখতে হবে। চলমান রাজনীতির ধারা ধরে বাংলাদেশের&nbsp;রাজনীতির উন্নতি হবে না, সম্পূর্ণ নতুন রাজনীতি লাগবে। আগেকার গণতন্ত্র ও সমাজতন্ত্র দিয়ে কিছু করা হাবে না। অবলম্বন করতে হবে জাতীয়তাবাদ ও তার সম্পূরক আন্তর্জাতিক দৃষ্টিভঙ্গি এবং জনগণের গণতন্ত্র। বিশ্বায়নের বদলে গ্রহণ করতে হবে জনগণের গণতান্ত্রিক জাতিরাষ্ট্র ও আন্তর্জাতিক বিশ্বরাষ্ট্র প্রতিষ্ঠার আদর্শ। জনগণের গণতান্ত্রিক রাজনৈতিক দল গঠনে কেন্দ্রীয় গুরুত্ব দিতে হবে। বর্তমান কোনো দল উন্নত রাজনীতি অবলম্বন করে উন্নত নতুন রাজনৈতিক চরিত্র অর্জন করতে পারে। আবার নতুন দল গঠিতও হতে পারে। দল ও রাষ্ট্রের জন্য রাজনৈতিক আদর্শ অপরিহার্য। আমরা জনগণের গণতন্ত্র বা জনগণতন্ত্র, বা নয়াগণতন্ত্র চাই। এ বিষয়ে আমার কিছু ধারণার প্রকাশ আছে আমার গণতন্ত্র ও নয়াগণতন্ত্র বইতে আদর্শের ভিত্তিতে বাস্তবসম্মত দীর্ঘমেয়াদি ও স্বল্পমেয়াদি কর্মসূচি অবলম্বন করে জাতীয় পর্যায়ে কাজ করতে হবে। দীর্ঘমেয়াদি কর্মসূচির একটি দৃষ্টান্ত স্বদেশ চিন্তা সঙ্ঘ থেকে প্রচারিত আটাশ দফা : আমাদের মুক্তি ও উন্নতির কর্মসূচি। দীর্ঘমেয়াদি কর্মসূচির ধারাবাহিকতায় সময়ে সময়ে গ্রহণ করতে হবে স্বল্পমেয়াদি কর্মসূচি। মাত্র পঞ্চাশ&Ntilde;ষাট বছরের কিছু বিষয়ে দৃষ্টিকে সীমাবদ্ধ না রেখে দেড় হাজার বছরের গোটা ইতিহাসে, বিশেষ করে ইংরেজ শাসন প্রতিষ্ঠার ঘটনাবলি থেকে বাংলাদেশের এখন পর্যন্ত সময়ের ইতিহাসে দৃষ্টিকে প্রসারিত করতে হবে। দরকার তথ্যনিষ্ঠা ও সত্যনিষ্ঠা। বিভিন্ন যুগে বিভিন্ন ঘটনায় প্রগতিশীল রক্ষণশীল ও প্রতিক্রিয়াশীল শক্তির ভূমিকা সম্পর্কে গভীর জ্ঞান অর্জন এবং ইতিহাস থেকে শিক্ষা গ্রহণ। ইতিহাসের শিক্ষাকে কাজে না লাগিয়ে প্রগতি সম্ভব নয়। অতীতের চিন্তায় মনকে আচ্ছন্ন রাখলে চলবে না। অনাচ্ছন্ন মোহমুক্ত স্বাধীন দৃষ্টিভঙ্গি নিয়ে, বাস্তব অবস্থা বিচার করে প্রগতিশীল নতুন চিন্তায় সাহসী হতে হবে। সমাজতন্ত্র, গণতন্ত্র ইত্যাদি সম্পর্কে পুরাতন চিন্তা নিয়ে কাজ করতে গেলে সাফল্য সম্ভব হবে না।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">নতুনভাবে বাস্তবসম্মত সর্বাঙ্গীণ চিন্তা ও কাজ আরম্ভ করা গেলে এবং উন্নত চরিত্রের নেতৃত্ব গড়ে তোলা গেলে আমাদের সামনে বাংলাদেশের এবং বাংলাদেশের জনগণের</span></p>\r\n','',1,15,'2016-01-07 02:36:54',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452155814_jsr5-1.png'),(7,'সমুদ্র সীমানা নির্ধারণে আন্তর্জাতিক আদালত ও ট্রাইব্যুনালের ভূমিকা: নূর মোহাম্মদ','সমুদ্র-সীমানা-নির্ধারণে-আন্তর্জাতিক-আদালত-ও-ট্রাইব্যুনালের-ভূমিকা:-নূর-মোহাম্মদ','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">গত ১৪ মার্চ ২০১২ মিয়ানমারের সঙ্গে বাংলাদেশের সমুদ্র সীমানা নিয়ে বিবাদ নিয়ে বিবাদ নিষ্পত্তিকরণ বিষয়ে সমুদ্র আইনের জন্য গঠিত আন্তর্জাতিক ট্রাইব্যুনালের রায় প্রকাশিত হয়। রায়কে কেন্দ্র করে বিস্তর তর্ক&Ntilde;বিতর্ক ও লেখালেখি হয়েছে। এই সব আলোচনা (প্রধানতঃ টক শো কেন্দ্রিক) বা কলাম ল অব দি সী/ সমুদ্র আইনকে বলতে গেলে একেবারেই প্রাসঙ্গিত করেনি। ফলে সমুদ্র সীমানা নির্ধারণে সমুদ্র আইন বিষয়টি তাৎপর্যহীন হয়ে গেছে। যাঁরা নিতান্তই কাতর ও উত্তেজিত হয়েছিলেন এই সিদ্ধান্তে উপনীত হয়ে যে, বর্তমান সরকারের কারণেই কয়েক হাজার বর্গ কিলোমিটার সমুদ্র অঞ্চল বাংলাদেশ হারিয়েছে তারা যেমন তেমনি যারা ক্রমাগত হুঙ্কার দিয়ে দেশবাসীকে অস্তির করে ফেলেছিলেন এই দাবী করে যে, তাদেরই প্রধানমন্ত্রী একটি সমুদ্র জয় করে এনেছেন উভয় পক্ষই এখন বিষয়টি প্রায় নর্দমায় ফেলে দিয়েছেন। আইনগত প্রেক্ষিত সব সময়ের জন্যই অন্ধকারে থেকে গেল। সংক্ষেপে সোজা কথা হল; ১৪ই মার্চের রায় দিয়েছে সমুদ্র আইনের জন্য গঠিত আন্তর্জাতিক ট্রাইবুন্যাল (ঞযব ওহঃবৎহধঃরড়হধষ ঞৎরনঁহধষ ভড়ৎ ঃযব খধি ড়ভ ঃযব ঝবধ)। ক্ষুদ্র নিবন্ধটি শুধুমাত্র সমুদ্র সীমানা নির্ধারণে আদালত ও ট্রাইব্যুনালের ভূমিকার উপর কিছুটা আলোকপাত করার চেষ্টা চালাবে।<br />\r\nআন্তর্জাতিক সমুদ্র আইন ১৯৮২ উপকূলীয় রাষ্ট্রগুলি জন্য ৩৫০ নটিক্যাল মাইল বরাদ্দ করেছে ঠিকই কিন্তু ঐ এলাকার উপর এখনি রাষ্ট্রের সার্বভৌমত্ব সম্প্রসারিত হবে কি ভাবে? প্রথমে বিষয়টি অত্যন্ত সংক্ষেপে ৩টি দফার মাধ্যমে বলে দেয়া হয়েছে। ৩৫০ নটিক্যাল মাইলকে ৩টি এলাকায় বিভক্ত করে<br />\r\nপ্রতিটি এলাকার সীমানা নির্ধারনের জন্য একটি একটি আলাদা দফা। দফা: ১৫, ৭৪, ৮৩।<br />\r\nদফা-১৫ ঃ রাষ্ট্রীয় বা ভূমন্ডীয় সমুদ্র সীমানা নির্ধারণের জন্য। ভিত্তিরেখা থেকে ১২ নটিক্যাল মাইল (২২.২ কি.মি.) পর্যন্ত যার সীমানা। আবার এই যে ১২ নটিক্যাল তার প্রাপ্যতা অর্জনে প্রথমে প্রয়োজন হবে বেজলাইন/ভিত্তিরেখা নির্ধারণ করা।<br />\r\nযাহোক দফা ৭৪ একচ্ছত্র অর্থনৈতিক অঞ্চলের সীমানা নির্ধারণের জন্য ও দফা- ৮৩ মহীসোপানের সীমানা নির্ধারণের জন্য নির্ধারিত। অবশ্য দফা-১৫ এর সাথে দফা ৭৪ ও ৮৩ এর অন্তত, দুটি বিষয়ে গুরুত্বপূর্ণ পার্থক্য রয়েছে। প্রথমত দফা-১৫ তে সীমানা নির্ধারণের জন্য সমদূরত্ব পদ্ধতিতে একমাত্র বিধান ও আদালতে যাওয়ার বিধান নেই।<br />\r\nদফা-৭৪ ও দফা-৮৩ এর বিষয়বস্তু একই। সংলগ্ন অথবা বিপরীতে থাকা দেশসমূহ হয় ঐক্যমত্যের ভিত্তিতে সীমানা নির্ধারণ করবেন ব্যর্থ হলে কয়েকখণ্ডে বিভক্ত অধ্যায় ১৫ এর শরণাপন্ন হতে বলা হয়েছে (ঞযব ফবষরসরঃধঃরড়হ ড়ভ ঃযব বীপষঁংরাব বপড়হড়সরপ ুড়হব নবঃবিবহ ংঃধঃবং রিঃয ড়ঢ়ঢ়ড়ংরঃব ড়ৎ ধফলধপবহঃ পড়ধংঃং ংযধষষ নব বভভবপঃবফ নু ধমৎববসবহঃ&hellip;.)<br />\r\nওভ হড় ধমৎববসবহঃ পধহ নব ৎবধপযবফ রিঃযরহ ধ ৎবধংড়হধনষব ঢ়বৎরড়ফ ড়ভ ঃরসব, ঃযব ংঃধঃব পড়হপবৎহবফ ংযধষষ ৎবংড়ৎঃ ঃড় ঃযব ঢ়ৎড়পবফঁৎবং ঢ়ৎড়ারফবফ ভড়ৎ রহ ঢ়ধৎঃ ঢঠ) দফা-৮৩ তে শুধু এটুকুই আলাদা: &lsquo;বীপষঁংরাব বপড়হড়সরপ ুড়হব&rsquo; এর যায়গায় হবে পড়হঃরহবহঃধষ ংযবষভ&rsquo;।<br />\r\nঅধ্যায়-১৫<br />\r\nবিবাদ নিষ্পত্তিকরণ<br />\r\nখন্ড-১, সাধারণ আইন ধারাসমূহ/ অনুবিধি&Ntilde;<br />\r\nদফা-২৭৯<br />\r\nশান্তিপূর্ণ উপায়ে বিবাদ নিষ্পত্তির নৈতিক বা আইনগত বাধ্যবাধকতা।<br />\r\n১৫ অধ্যায়ের দফা ২৭৯ এর একটি বিষয় অত্যন্ত পরিষ্কারভাবে উল্লেখ করা হয়েছে, তা হল, পক্ষগণের মধ্যে কনভেনশনের (এক্ষেত্রে বুঝাবে ১৯৮২ সালের সমুদ্র আইন) ব্যাখ্যা ও প্রয়োগের প্রশ্নে যে কোন বিতর্ক বা বিবাদ দেখা দিলে শান্তিপূর্ণ উপায়ে জাতিসংঘের সনদ অনুযায়ী দফা-২, অনুচ্ছেদ-৩, এবং এই পর্যায়ে তা জাতিসংঘ সনদের দফা ৩৩, অনুচ্ছেদ-১ এ নির্দেশিত প্রক্রিয়ার সাহায্য নিয়ে সমাধানের চেষ্টা করতে হবে।<br />\r\nএর পর ২৮০, ২৮১, ২৮২ ও ২৮৩ দফাগুলিতে প্রধানত ঢ়ৎড়পবফঁৎবং ও ড়নষরমধঃরড়হ এর নির্দেশনা দেয়া হয়েছে।<br />\r\nতাহলে একটি বিষয়ের প্রতি বিশেষ নজর না দিলে পক্ষগণ কেন অধ্যায় ১৫এর শরণাপন্ন হবেন এবং শুধুমাত্র কি কারণে ও প্রয়োজনে এবং এক্ষেত্রে কোন বিবেচ্য বিষয়ই কেবল গ্রাহ্য হবে তা একেবারেই বুঝা যাবে না।<br />\r\nদফা-২৭৯ অর্থাৎ ঝবঃঃষবসবহঃ ড়ভ ফরংঢ়ঁঃবং এর প্রথম দফাতেই প্রবেশের অধিকারের বিষয়টি উল্লেখ করা হয়েছে: ঝঃধঃবং ঢ়ধৎঃরবং ংযধষষ ংবঃঃষব ধহু ফরংঢ়ঁঃব নবঃবিবহ ঃযবস পড়হপবৎহং ঃযব রহঃবৎঢ়ৎবঃধঃরড়হ ড়ৎ ধঢ়ঢ়ষরপধঃরড়হ ড়ভ ঃযরং পড়হাবহঃরড়হ নু ঢ়বধপবভঁষ সবধহং. অর্থাৎ অবশ্যই, আনক্লজ-৩ এর বিভিন্ন দফা পরিশিষ্ট ইত্যাদিতে বর্ণিত বিষয়সমূহ ব্যাখ্যা ও প্রয়োগের প্রশ্নে উদ্ভূত বিবাদ নিষ্পত্তিকরণের জন্যই অধ্যায় ১৫ এর শরণাপন্ন হওয়া যাবে।<br />\r\nধরা যাক, বাংলাদেশ ও মিয়ানমার সমুদ্রের সীমানা নির্ধারণের প্রশ্নে নিজেরা আনক্লজ-৩ এর ভিত্তিতেই আলোচনা চালিয়েছিল কিন্তু তারা কোন সিদ্ধান্তে পৌছাতে পারেনি। পক্ষগণের সমস্যা হয়েছিল আনক্লজ-৩ এর প্রযোজ্য ধারাসমূহ ও অনুমিত নির্দেশনার ব্যাখ্যা ও তার প্রয়োগের প্রশ্নে ঐক্যমত্য প্রতিষ্ঠিত হয়নি ফলে, তারা এই প্রক্রিয়ায় চুক্তিবদ্ধ হতে পারেনি। বাংলাদেশ বাধ্য হয়ে অধ্যায় ১৫ তে গেল। গিয়ে দেখে ভারত পূর্বা পর্বেই ভবিষ্যতে বাংলাদেশ বিবাদ নিষ্পত্তির জন্য আনক্লজ-৩ জাতিসংঘের এই নির্দিষ্ট কনভেনশের অধ্যায় ১৫ এর দফা-২৮৭ র (ধ) ও (ন) তে বর্ণিত ট্রাইব্যুনাল ও আদালতে যেন না যেতে পারে সেই প্রশ্নে/ ব্যাপারে আপত্তি দিয়ে রেখেছে। বাংলাদেশের কোন সরকারই এর আগে নাকি কোন খোঁজই রাখত না প্রতিবেশী কথিত বন্ধু রাষ্ট্র ভারত&Ntilde;বাংলাদেশ বিবাদ নিষ্পত্তি প্রক্রিয়ার সুফল যাতে না পেতে পারে তার যথাসাধ্য ব্যবস্থা করে রেখেছে। সে ক্ষেত্রে বাংলাদেশ যেহেতু একই সাথে মিয়ানমার ও ভারতের বিরুদ্ধে অধ্যায় ১৫ এর শরণাপন্ন হয় সেজন্য বাংলাদেশ দফা -২৮৭ এর ৩নং (আইনি প্রক্রিয়া বেছে নেবার অধিকারের ভিত্তিতে) বা গ-একটি সালিশী আদালত যা গঠিত হবে পরিশিষ্ট-৭ অনুযায়ী বিবাদ নিষ্পত্তির জন্য আরজি পেশ করে। পরে অবশ্য মিয়ানমারের সঙ্গে আলোচনার ভিত্তিতে বর্ণিত ট্রাইব্যুনাল থেকে মামলাটি সরিয়ে ক- পরিশিষ্ট -৬ অনুযায়ী সমুদ্র আইনের জন্য প্রতিষ্ঠিত আন্তর্জাতিক ট্রাইবুন্যালে শুনাণীর জন্য আইনি প্রক্রিয়া শুরু করার ব্যবস্থা করে।<br />\r\nতাহলে এই বিষয়টি অর্থাৎ সমুদ্র সীমানা নির্ধারনে যে মাত্র দুটি বিধান রয়েছে সেই বিষয়টি সম্পর্কে পুর্নাঙ্গ ধারণা যদি কারো না থাকে তাহলে তাদের এমনকি ভৌতিক উত্তেজনা থাকলেও মতামত প্রদান করার ব্যাপারে সংযত হলে উনাদের প্রতিভা ক্ষতিগ্রস্থ হবে বলে মনে করিনা। কিন্তু পরিতাপের বিষয় হল দেশের রাজনীতিবিদগণ সহ গণ্যমান্য ভদ্রলোকেরা শোভনতার ধার ধারলেন না। পদাধীকার ও প্রচলিত উচ্চশিক্ষা আমাদেরকে গুরুতর বিষয়ে ফেলে দিল। যাহোক, এবার আমরা সরাসরি হাজির হলাম&Ntilde; দফা-২৮৭ তে দফা-২৮৭ হল অধ্যায় ১৫ এর সেকশন-২ ঈড়সঢ়ঁষংড়ৎু ঢ়ৎড়পবফঁৎবং বহঃধরষরহম উবপরংরড়হং দ্বিতীয় দফা।<br />\r\nযে দ্বিতীয় দফা/২৮৭ হল ঈযড়রপব ড়ভ ঢ়ৎড়পবফঁৎব/ আইনগত বিষয়াদি নির্বাহ করার প্রশ্নে বেছে নেবার সম্ভাবনা/আইনি প্রক্রিয়া বেছে নেবার অধিকার।<br />\r\nএর অনুচ্ছেদ, ১ এর অধীন<br />\r\nকনভেনশনের ব্যাখ্যা অথবা প্রয়োগের প্রশ্নে বিবাদ নিস্পত্তির জন্য একটি রাষ্ট্র সম্পূর্ণ মুক্ত থাকবে লিখিত ঘোষণার মাধ্যমে নিম্নলিখিত এক বা একাধিক উপায় বেছে নিতে।<br />\r\n(ক) পরিশিষ্ট-৬ অনুযায়ী সমুদ্র আইনের জন্য প্রতিষ্ঠিত আন্তর্জাতিক ট্রাইবুন্যাল<br />\r\n(খ) আন্তর্জাতিক বিচার আদালত<br />\r\n(গ) একটি সালিশী আদালত যা গঠিত হবে পরিশিষ্ট -৭ অনুযায়ী<br />\r\n(ঘ) একটি বিশেষ ধরনের সালিশী আদালত যা গঠিত হবে পরিশিষ্ট-৮ অনুযায়ী, যা এক বা একাধিক বিভাগ/ শ্রেণীর নির্দিষ্টকৃত বিবাদ সমূহ আমলে আনবে।<br />\r\nউপরে বর্ণিত একাধিক যে আদালত ও ট্রাইবুন্যালের কথা বলা হয়েছে সে দিকে লক্ষ্য করলে দেখা যাবে,<br />\r\nক, পরিশিষ্ট -৬ এ বর্ণিত আছে করনীয় কি হবে। উল্লেখ থাকে যে, সমুদ্র আইনের জন্য আন্তর্জাতিক ট্রাইবুন্যালের একুশ সদস্য বিশিষ্ট একটি স্থায়ী বিচার কমিশনারের প্রধান কার্যালয় থাকবে জার্মানীর হামবুর্গে। (নিবন্ধে শুধুমাত্র ক ও গ / আন্তর্জাতিক ট্রাইব্যুনাল ও একটি সালিশি আদালত বিষয়ে অতি সম্পূর্ণ বিবরণ। যদিও দফা-২৮৭ এর অধ্যায় রয়েছে মোট আটটি অনুচ্ছেদ&Ntilde;প্রতিটি অনুচ্ছেদই বিবাদ মীমাংসা সংক্রান্ত প্রশ্নে যেমন প্রাসঙ্গিক তেমনি গুরুত্বপূর্ণ। সেগুলোর যথাযথ ব্যাখ্যা ছাড়া অনুচ্ছেদ ১ এর তাৎপর্য অনুধাবন পূর্ণাঙ্গ হবে বলে মনে হয় না)</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">পরিশিষ্ট-৬<br />\r\nসেকশন-১ আন্তর্জাতিক ট্রাইব্যুনালের গঠন (সংগঠন) কাঠামো (পরিচিতির ক্ষুদ্রাংশ) ড়ৎমধহরুধঃরড়হ ড়ভ ঞৎরনঁহধষ আনক্লজ-৩ এ শুদ্ধ এ প্রসঙ্গে রয়েছে ৪ পৃষ্ঠাব্যাপী ১৯টি দফা।<br />\r\nদফা-২<br />\r\n১. ঞযব ঞৎরনঁহধষ ংযধষষ নব পড়সঢ়ড়ংবফ ড়ভ ধ নড়ফু ড়ভ ২১ রহফবঢ়বহফবহঃ সবসনবৎং, বষবপঃবফ ভৎড়স ধসড়হম ঢ়বৎংড়হং বহলড়ুরহম ঃযব যরমযবংঃ ৎবঢ়ঁঃধঃরড়হ ড়ভ ভধরৎ যবহং ধহফ রহঃবমৎরঃু ধহফ ড়ভ ৎবপড়মহরুবফ পড়সঢ়বঃবহপব রহ ঃযব ভরবষফ ড়ভ ঃযব ষধি ড়ভ ঃযব ংবধ.<br />\r\nদফা-৪<br />\r\nঅনুচ্ছেদ, ১ ঊধপয ংঃধঃব ঢ়ধৎঃু সধু হড়সরহধঃব হড়ঃ সড়ৎব ঃযধহ ঃড়ি ঢ়বৎংড়হং যধারহম ঃযব য়ঁধষরভরপধঃরড়হং ঢ়ৎবংপৎরনবফ রহ ধৎঃরপষব ২ ড়ভ ঃযরং অহহবী. ঞযব সবসনবৎং ড়ভ ঃযব ঞৎরনঁহধষ ংযধষষ নব বষবপঃবফ ভৎড়স ঃযব ষরংঃ ড়ভ ঢ়বৎংড়হং ঃযঁং হড়সরহধঃবফ.<br />\r\nঅধ্যায় ১৫ এর অধীন দফা-২৯৩<br />\r\nঅনুচ্ছেদ-১<br />\r\nঅ পড়ঁৎঃ ড়ৎ ঞৎরনঁহধষ যধারহম ঔঁৎরংফরপঃরড়হ ঁহফবৎ ঃযরং ংবপঃরড়হ ংযধষষ ধঢ়ঢ়ষু ঃযরং পড়হাবহঃরড়হ ধহফ ড়ঃযবৎ ৎঁষবং ড়ভ রহঃবৎহধঃরড়হধষ ষধি হড়ঃ রহপড়সঢ়ধঃরনষব রিঃয ঃযরং পড়হাবহঃরড়হ.<br />\r\nঅনুচ্ছেদ-৪<br />\r\nঞযব সবসনবৎ ড়ভ ঃযব ঃৎরনঁহধষ ংযধষষ নব বষবপঃবফ নু ংবপৎবঃ ভধষষড়ঃ. ঊষবপঃরড়হং ংযধষষ নব যবষফ ধঃ ধ সববঃরহম ড়ভ ঃযব ংঃধঃবং ঢ়ধৎঃরবং পড়হাবহবফ নু ঃযব ংবপৎবঃধৎু মবহবৎধষ ড়ভ ঃযব টহরঃবফ ঘধঃরড়হং রহ ঃযব পধংব ড়ভ ঃযব ভরৎংঃ বষবপঃরড়হ ধহফ নু ধ ঢ়ৎড়পবফঁৎব ধমৎববফ ঃড় নু ঃযব ংঃধঃবং ঢ়ধৎঃরবং রহ ঃযব পধংব ড়ভ ভরৎংঃ বষবপঃরড়হ ধহফ নু ধ ঢ়ৎড়পবফঁৎব ধমৎববফ ঃড় নু ঃযব ংঃধঃবং ঢ়ধৎঃরবং রহ ঃযব পধংব ড়ভ ংঁনংবয়ঁবহঃ বষবপঃরড়হং. ঞড়ি ঞযরৎফ ড়ভ ঃযব ংঃধঃবং ঢ়ধৎঃরবং ংযধষষ পড়হংঃরঃঁঃব ধ য়ঁড়ৎঁস ধঃ ঃযধঃ সববঃরহম. ঞযব ঢ়বৎংড়হং বষবপঃবফ ঃড় ঃযব ঞৎরনঁহধষ ংযধষষ নব ঃযড়ংব হড়সরহববং যিড় ড়নঃধরহ ঃযব ষধৎমবংঃ সবসনবৎ ড়ভ াড়ঃবং ধহফ ধ ঃড়ি &ndash;ঃযরৎফং সধলড়ৎরঃু ড়ভ ঃযব ংঃধঃব ঢ়ধৎঃরবং ঢ়বৎপবহঃ ধহফ াড়ঃরহম,</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">চৎড়ারফবফ ঃযধঃ ংঁপয সধলড়ৎরঃু রহপষঁফবং ধ সধলড়ৎরঃু ড়ভ ঃযব ংঃধঃব ঢ়ধৎঃরবং.<br />\r\nদফা&Ntilde;৭, অনুচ্ছেদ-১<br />\r\nঘড় সবসনবৎ ড়ভ ঞৎরনঁহধষ সধু বীবৎপরংব ধহু ঢ়ড়ষরঃরপধষ ড়ৎ ধফসরহরংঃৎধঃরাব ভঁহপঃরড়হ ড়ৎ ধংংড়পরধঃব ধপঃরারঃু রিঃয ড়ৎ নব ভরহধহপরধষষু রহঃবৎবংঃবফ রহ ধহু ড়ভ ঃযব ড়ঢ়বৎধঃরড়হং ড়ভ ধহু বহঃবৎঢ়ৎরংব পড়হপবৎহবফ রিঃয ঃযব বীঢ়ষড়ৎধঃরড়হ ভড়ৎ ড়ৎ বীঢ়ষড়রঃধঃরড়হ ড়ভ ঃযব ৎবংড়ঁৎপবং ড়ভ ঃযব ংবধ ড়ৎ ঃযব ংবধ- ভবফ ড়ৎ ঃযব পড়সসবৎপরধষ ঁংব ড়ভ ঃযব ংবধ ড়ৎ ঃযব ংবধ- ভবফ.<br />\r\nঅনুচ্ছেদ&Ntilde;২<br />\r\nঘড় সবসনবৎ ড়ভ ঃযব ঞৎরনঁহধষ সধু ধপঃ ধং ধমবহঃ, পড়ঁহংবষড়ৎ ধফাড়পধঃব রহ ধহু পধংব.<br />\r\nঅনুচ্ছেদ&Ntilde;৩<br />\r\nঅহু ফড়ঁনঃ ড়হ ঃযবংব ঢ়ড়রহঃং ংযধষষ নব ৎবংড়ষাবফ নু ফবপরংরড়হ ড়ভ ঃযব সধলড়ৎরঃু ড়ভ ঃযব ড়ঃযবৎ সবসনবৎং ড়ভ ঃযব ঞৎরনঁহধষ ঢ়ৎবংবহঃ.<br />\r\nদফা-১১: ঝড়ষবসহ ফবপষধৎধঃরড়হ নু সবসনবৎ<br />\r\nঊাবৎু সবসনবৎ ড়ভ ঃযব ঞৎরনঁহধষ ংযধষষ, নবভড়ৎব ঃধশরহম ঁঢ় ঃযরং ফঁঃরবং, সধশব ধ ংড়ষবসহ ফবপষধৎধঃরড়হ রহ ড়ঢ়বহ ংবংংরড়হ ঃযধঃ যব রিষষ বীবৎপরংব যরং ঢ়ড়বিৎং রসঢ়ধৎঃরধষষু ধহফ পড়হংপরবহঃরড়ঁংষু.<br />\r\nঅহহবী-ঠওও অৎনরঃৎধঃরড়হ<br />\r\nদফা-২<br />\r\nঅনুচ্ছেদ-১<br />\r\nঅ ষরংঃ ড়ভ ধৎনরঃৎধঃড়ৎং ংযধষষ নব ফৎধহি ঁঢ় ধহফ সধরহঃধরহবফ নু ঃযব ংবপৎবঃধৎু মবহবৎধষ ড়ভ ঃযব টহরঃবফ ঘধঃরড়হ. ঊাবৎু ংঃধঃব ঢ়ধৎঃু ংযধষষ নব বহঃরঃষবফ ঃড় ৎঁসরহধঃব ভড়ঁৎ ধৎনরঃৎধঃড়ৎং, বধপয ড়ভ যিড়সব ংযধষষ নব ধ ঢ়বৎংড়হ বীঢ়বৎরবহপবফ রহ সধৎরঃরসব ধভভধরৎং ধহফ বহলড়ুরহম ঃযব যরমযবংঃ ৎবঢ়ঁঃধঃরড়হ ভড়ৎ ভধরৎহবংং, পড়সঢ়বঃবহপব ধহফ রহঃবমৎরঃু. ঞযব হধসবং ড়ভ ঃযব ঢ়বৎংড়হং ংড় হড়সরহধঃবফ ংযধষষ পড়হংঃরঃঁঃব ঃযব ষরংঃ.<br />\r\nদফা-৩, সালিশি ট্রাইব্যুনালের গঠন কাঠামো<br />\r\nঅনুচ্ছেদ-ক, সালিশি ট্রাইব্যুনাল হবে ২/৩ জন সদস্য বিশিষ্ট;<br />\r\nঅনুচ্ছেদ-খ, যে পক্ষ প্রক্রিয়াটি শুরু করেছে সেই একজন সদস্যকে নিয়োগ দিবেন&Ntilde; যিনি নিজদেশের হতে পারেন, প্রদত্ত মনোনয়নের তালিকা থেকে হওয়ায় শ্রেয়।<br />\r\nঅনুচ্ছেদ-গ, অন্য পক্ষ বা দল নোটিশ পাওয়ার ৩০ দিনের মধ্যে এই পরিশিষ্ট এর দফা-১, অনুসরণে উপরোক্ত নিয়মে একজন সদস্যকে নিয়োগ দিবেন।<br />\r\nঅনুচ্ছেদ-ঘ, অন্য ৩ জন সদস্য উভয় পক্ষের সম্মতিতে নিয়োগ করেন। এরপর প্রক্রিয়াটি &hellip;&hellip;&hellip; বড় বিবরণে যুক্ত করা হয়েছে।<br />\r\nঅসমাপ্ত<br />\r\nনোটঃ ১৪ই মার্চের রায়ের পর দেখা গেছে যারা বিতর্ক শুরু করেছেন এবং সংগত কারণেই অনেক ক্ষেত্রে বিতর্ক বাঞ্ছনীয়। লক্ষ্য করা গেছে রায়ল (অধিৎফ) প্রদান করেছে যে আন্তর্জাতিক ট্রাইব্যুনাল যে সম্পর্কে কেউ কোন প্রশ্ন তোলেননি। বাংলাদেশ&Ntilde;ভারত সীমানা বিবাদ নিস্পত্তি হবে সালিশি ট্রাইব্যুনালে&Ntilde; এখন থেকেই মামলাটি কিভাবে চালালে দেশ লাভবান হবে সে ব্যাপারে বিতর্ক শুরু করলে আমরা উপকৃত হতে পারতাম। যারা সমুদ্র আইনের উপর যথাযথভাবে দক্ষ তাদের বিজ্ঞ মতামত আমরা নিশ্চয় আশা করতে পারি। (চলবে)</span></p>\r\n','',1,15,'2016-01-07 02:43:18',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452156198_jsr-6-1.png'),(8,'ত্যাঁদড় সম্পাদকের কুলাঙ্গার পত্রিকা: বিনোদ ভাদুরী','ত্যাঁদড়-সম্পাদকের-কুলাঙ্গার-পত্রিকা:-বিনোদ-ভাদুরী','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">দেশবাসীকে জানাইয়া রাখি, এ বংগ দেশে ইংরেজি ভাষায় একখানি চার রংঙ্গা ঝকঝকে পাক্ষিক পত্রিকা বাহির হইতেছে। তদীয় পত্রিকাটির নাম হইতেছে &lsquo;এনার্জি এন্ড পাওয়ার&rsquo;। কুলাঙ্গার প্রকাশনাটি এই দেশে থাকিয়া এই দেশের খাইয়া তদীয় দেশের বায়ু সেবন করিয়া &lsquo;ইস্ট ইন্ডিয়া কোম্পানি&rsquo;, এশিয়া এনাজির্, শেভরন, ইউনিকল, তাল্লো, কনকো ফিলিপস নামক বর্গীদেরকে সার্পোট করিয়া দুই হপ্তা অন্তর অন্তর অবৈধ প্রসব করিতেছে এ বঙ্গ দেশে। এশিয়া এর্নার্জি যাহাতে ফুলবাড়ী নামক রণাঙ্গনে উন্মুক্ত কয়লা খনি স্থাপন করিতে পারে তাহার দালালী সে করে। শুনিতেছি মাঝে মাঝে দালালীপনার ছুরি দিনাজপুরের উপর দিয়া বহিয়া যায়। তদীয় পত্রিকার ত্যাঁদড় সম্পাদক তাহার ত্যাঁদড় লেখকগুলি দ্বারা জাতীয় কমিটির নেতৃবৃন্দকে &ldquo;সো কল্ড বুদ্ধিজীবী&rdquo; বলিয়া সম্বোধন করিয়া থাকে। জনগণকে বলিয়া রাখি যুগযুগান্তে মেহনতি সংগ্রামী মানুষদের বিপক্ষে যাহারা গিয়াছে তাহারা হইল সেই কিসিমের লোক।<br />\r\nযেখানে এশিয়া এনার্জি কয়লা তুলিয়া ৯৪% নিয়া যাইবে, যেখানে আমার ভাইসাহেবেরা জীবন উৎসর্গ করিয়া দেশপ্রেমিকের পরীক্ষায় উত্তীর্ণ হইয়াছেন, যেখানে তরিকুল, সালেকীন নামক তাগড়া দেশপ্রেমিক জীবন দিয়াছেন, তাহাদের রক্ত যে ভূমিতে প্রবহমান সেভূমিতে থাকিয়া ত্যাঁদড় সম্পাদক তাহার কুলাঙ্গার লেখকেরা ফুলবাড়ীকে এশিয়া এনার্জির কাছে তুলিয়া দিতে চায়। ত্যাঁদড়, কুলাঙ্গারগুলিকে রংপুরে বসবাসকারী সৎ কৃষকের শক্ত পায়ের গোড়ালি দিয়া ঘাড়ে সজোরে আঘাত করিয়া বাংলাদেশ হইতে বাহির করিতে হইবে।<br />\r\nবিভিন্ন অজুহাত দিয়া তাহারা আমাদের খনিগুলিকে তাহাদের বিদেশী দুলাভাইদের দিয়া দিতে চায়। যে অজুহাত দিয়া তাহারা ঐ অকর্মটা করিতে চায় তাহার নাতিদীর্ঘ তালিকার পহেলা নম্বর হইল আমরা পারিব না। সেকেন্ড নম্বর হইল আমাদের অতশত টাকা নাই। তেসরা নম্বর হইল বঙ্গীয় প্রতিষ্ঠান বাপেক্স তুলিবে কি করিয়া জনবল যে নাই। চৌঠা নম্বর হইল গরম ডিমান্ড &ldquo;যাহা অতি বিজ্ঞভাবে গোল টেবিলে বসিয়া বলে&rdquo; &ldquo;জনতার এক্ষুনি বিদ্যুৎ চাই&rdquo; সুতরাং এক্ষুনি দিয়া দাও। তাহাদেরকে বলি ব্যাটা তোমাদের সরকার মহোদয়রা স্বাধীনতার ৪০ বৎসরেও চুলখান ছিঁড়িতে পারে নাই। এ চুল না ছেঁড়া অবস্থায় থাক না আরো ১০ খানা বছর। বলি কি এরই মধ্যে মেহেরুন এর মত কিছু কৃষকের ছোকরা দেশপ্রেমিকের &ldquo;ছাওয়াল&rdquo; দেশে বিদেশে পাঠাইয়া বিশেষজ্ঞ তৈরি করিয়া তবেই এ বঙ্গদেশের খনিজগুলি উত্তোলন করা হোক না। এত তাড়া কিসের ভায়া। সবুর সবুর। সরকার মহোদয়কে বলি কি মাগুরছড়াতে টেংরাটিলাতে বেনিয়ারা আসিয়া ৪৫ হাজার কোটি টাকার সম্পদ পুড়াইয়া নির্বিঘে&oelig; প্রস্থান করিয়াছে। আদায়টা করিতে পারেই না। মোশারফ নামক এক কুলাঙ্গার খনিজ মন্ত্রকের দায়িত্ব পাইয়া গাড়ি একখান উপঢৌকন নিয়াছিলেন নাইকোর কাছ হইতে। না জানি ইত্য সময়ে আরো কত মোশারফ বৎ কুলাঙ্গার বসিয়া আছে আমাদের সঙ্গে। প্রকাশ যে দিন হইবে সেদিন বাংলার সরল জনগণ জানিবেন।<br />\r\nএনার্জি এন্ড পাওয়ার দিয়া শুরু করিয়াছিলাম, আমরা কৃষকের বাচ্চা, ঢাকা ইউনিভার্সিটি নামক একটা অতি পুরাতন বিদ্যাপিঠে পড়ি। আমাদের খানিক ইংরেজি বিদ্যা থাকিলেও আমাদের নেংটি পরা বাপ দাদারা কখনও ইহা বুঝে নাই, কিন্তু লুট কিভাবে ঠেকাইতে হয় তাহা তাঁহারা জানেন। এঁরা বাংলা স্বাধীন করার সময় রক্ত ঝরাইয়াছিলেন, তাঁরা আমাদের শিখাইয়া গিয়াছেন এসব ইংরেজি পত্রিকার বাংলাদেশ ধ্বংসী মতলব কিভাবে থামাইতে হয়।</span></p>\r\n','',1,15,'2016-01-07 02:44:40',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452156280_jsr-7-1.png'),(9,'আন্দোলন: নিরোদ্র সি চৌধুরী','আন্দোলন:-নিরোদ্র-সি-চৌধুরী','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">শক্তিশালী মানুষ সমাজে থাকে। সমাজে দুর্বলের সহায়ক শক্তিধর মানুষ থাকা ভাল। মুশকিল হয় তখন যখন এ শক্তিশালীরা দুর্বলকে আক্রমণ করে। দুর্বলের সম্পত্তি লুটে লয়। দুর্বলের সম্ভ্রমে হাত দেয়, দুর্বলের মন, মনন, সৃষ্টিশীলতাকে নিজেদের সম্পত্তি বলে মনে করে, দুর্বলের স্থাবর অস্থাবর লুটে নেয়।<br />\r\nপ্রথম প্রথম এ দুর্বলেরা শক্তিধরদের হাতে পায়ে ধরে নিজেদেরকে তাদের অধিকার থেকে মুক্ত রাখতে চায়। যখন না পারে তখন কেউ কেউ প্রতিবাদ করে কেউ কেউ ভয়ে চুপ থেকে চোখ টিপে পানি বের করে ঈশ্বরকে বলে রাখে। বোকা ঈশ্বর ঈশ্বরী কায়দায় মুচকি হাসে। প্রতিবাদটা কিন্তু প্রথমে করে না। আগে পিছে অনেক ভেবে, চিন্তা করে অত্যন্ত সুকৌশলে যারা প্রতিবাদটা করে তাদেরও নির্যাতনে নির্যাতনে অনেক সময় পার করতে হয়। শক্তিধরদের নির্যাতন যখন সহ্যের সীমা অতিক্রম করে তখন প্রতিবাদের প্রশ্নটা আসে। এর পরও নির্যাতন সহ্য করে কেউ কেউ খোঁজে, খুঁজতে থাকে, সমাজে আরো কি কেউ আছে যারা শক্তিশালীদের দ্বারা নির্যাতনের শিকার হয়েছে। যখন একাধিক নির্যাতিত একত্রিত হয় তখন ভেবে চিন্তে বুদ্ধি খাটিয়ে,<br />\r\nসুকৌশলে এসব নির্যাতন থেকে মুক্তির একটা উপায় তারা বের করে। একত্রিত হয়ে রাজপথে মিছিল কিম্বা সমন্বিত প্রতিবাদের আগে তাদেরকে অবশ্য অনেক রাস্তা অতিক্রম করতে হয়। শক্তিশালীরা যদি কখনও টের পেয়ে যায় যে একত্রিত হচ্ছে তখন সংঙ্ঘবদ্ধ হবার আগেই তাদেরকে অতি নিয়ন্ত্রণ করে দুর্বলদের ছত্রভঙ্গ করে দেয়। মাঝে মাঝে যে নির্যাতিতরা আপোষ করে না তাও নয়। আপোষ হয়েছে অনেক বার। আপোষের পর আবার নির্যাতন। আবার আপোষ। এক সময় আপোষের প্রতি আর যখন তাদের আস্থা থাকে না তখন যে কাণ্ডটা হয় তার নাম প্রতিরোধ। প্রতিরোধও হঠাৎ করে হয় না। সংঘবদ্ধতা আলোচনা&Ntilde;সংঙ্ঘ&Ntilde;সংগঠন-এর পর যা হয় তার নাম হয় প্রতিরোধের আন্দোলন।<br />\r\nআন্দোলনের বহুমাত্রিক চরিত্র রূপায়ন হয়ে যায় সমসাময়িক এ সময়ে, অতীতে, অতি অতীতে। অতি অতীতে গোত্রীয় বিদ্রোহ (আন্দোলন) ছিল। সর্দাররা যখন গোত্রের সংখ্যাগরিষ্ঠদের সমর্থন হারাতো তখন ঘটত কাণ্ডটা। এমনি এমনিতেই তো আর সর্দাররা সমর্থন হারাত না, এর পিছে অনেক কারণ থাকত। এক কথায় সর্দাররা যখন নিজেরা অনিয়ম করত বা অনিয়মকে প্রশ্রয় দিত, প্রগতিশীলরা মন্দ সর্দারকে উৎখাত করতো। সংঘটিত হতো। আলোচনা হতো। উৎখাতের পর গোত্র চলার নীতিতে কি পরিবর্তন হবে তা ঠিক করে বিদ্রোহ চালানো হত। বিদ্রোহের এ আন্দোলনের ধারা মনে মনে চলত, খুশির ব্যাপার হল আন্দোলনটা যখন শ্রেষ্ঠরূপ ধারণ করত তখন বিদ্রোহটা হয়ে যেত। কেউ কিন্তু হঠাৎ করে বিদ্রোহ করে না।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><br />\r\n<span style=\"font-size:16px\">গোত্রে, সমাজে, নগরে, রাষ্ট্রে আন্দোলনটা হয়। গোত্রের&Ntilde;সমাজের&Ntilde;রাষ্ট্রের আন্দোলনের ধারা ভিন্ন। গোত্রের আন্দোলন যেমন সমাজের আন্দোলনের সাথে মেলে না তেমনি সমাজের আন্দোলনটাও রাষ্ট্রের সাথে মেশে না। যারা আন্তর্জাতিক রাজনীতি করেন (আন্তর্জাতিকতাবাদ রহঃবৎহধঃরড়হধষরংস) তাদের আবার আন্তর্জাতিক আন্দোলনের সাথে রাষ্ট্রীয় আন্দোলন মেলে না। তবে আংশিক আন্দোলন যদি সামগ্রিক আন্দোলনের পাথেয় হয় তখন আন্দোলনে ফলপ্রসূতা নিয়ে প্রশ্ন উঠানো সাজে না। আন্দোলন তখন নতুন পথ পাথেয় দিয়ে সভ্যতা ক্রমবিকাশে সহায়ক হতে থাকে।<br />\r\nঅতীতের সীমাবদ্ধ আন্দোলন হয়েছে। অতীতের সীমাবদ্ধ আন্দোলনগুলি ফলপ্রসূ হয়েছে আবার ব্যর্থও হয়েছে। দুনিয়া জুড়ে অন্যায় হয়েছে প্রতিবাদও হয়েছে। প্রতিবাদটা হয়েছে দুনিয়ার পূর্বে, পশ্চিমে, সমন্বয় বিধান হয় নাই। সমন্বয়হীন আন্দোলন আমাদেরকে ব্যর্থ করে দিয়ে গেছে বারবার। নিজের জন্য স্বাধীনতা চেয়ে যারা অপরকে পরাধীন করে রেখেছে তাদের বিরুদ্ধে কেউ মুখ খোলেনি। যুক্তরাষ্ট্র যেদিন স্বাধীন হয়েছিল তার অনেক আগে থেকেই আন্দোলন দানা বাঁধা শুরু হয়েছিল। যুক্তরাষ্ট্র ছিল নির্যাতিত, সে ঘুরে দাঁড়িয়ে আন্দোলন করে যুদ্ধ করে অধিকার প্রতিষ্ঠা করেছে। এখন সে সারা দুনিয়া জুড়ে অন্যায় করছে। কষ্টের ব্যাপার হল যুক্তরাষ্ট্রের এ আগ্রাসনের বিরুদ্ধে দুনিয়া জুড়ে ফলপ্রসূ আন্দোলন নাই। আংশিক আন্দোলন আছে কিন্তু এ আংশিক আন্দোলনের সাথে সামগ্রিক আন্দোলনের সমন্বয় নাই। সামগ্রিকতাই তাই। অংশরা আন্দোলন করছে নির্যাতিত হয়ে, নিঃশেষ হচ্ছে, একত্রিত হচ্ছে, ভাঙছে, অংশরা ভাঙছে চেতনা সুদৃঢ় হচ্ছে, শুধু সামগ্রিকতা দরকার।<br />\r\nআমরা এমন একটা সময়ে বসবাস করছি, যে সময়টাতে আন্দোলনের চাহিদা আছে কিন্তু মানুষ সামগ্রিক নির্যাতন সহ্য করে মুখ বুঁজে পড়ে আছে। অন্যায় চলছে, দুর্বল মানুষের উপর অত্যাচার চলছে, অত্যাচার চলছে সমাজে, অত্যাচার চলছে রাষ্ট্রে, শক্তিশালীরা রাষ্ট্রের পৃষ্ঠপোষকতা নিয়ে দখল&Ntilde;লুট&Ntilde;খুন-গুম বীরদর্পে চালাচ্ছে। শক্তিশালীরা দুর্বলকে মারছে তো মারছেই, মেরে পিষে রক্ত চুষে আদালতে যাচ্ছে, আদালতে তাদেরই জয় হচ্ছে। অতিমাত্রায় অন্যায়ের ফলে ন্যায় ভুলেছি। অতিমাত্রায় অত্যাচারিত হয়ে প্রতিরোধ ভুলেছি, ভুলেছি প্রতিবাদও। নির্যাতিত-অত্যাচারিত&Ntilde;নিপীড়িত হয়ে হয়ে দুর্বলরা এখন যেখানে এসে দাঁড়িয়েছে সেখানে দাঁড়িয়ে আন্দোলনটা আর মনে ধরে না। দুর্বলরা যারা সংগঠিত তাদেরকে দানা বাঁধতে দিচ্ছে না। একটু সুবিধা দিয়ে তাদেরকে দমিয়ে রাখা হচ্ছে। তবুওত দুর্বল যারা তারা মনের অজান্তেই শক্তিধরদের ওয়াদার সাথে বেঈমানি করে বৃহৎ স্বার্থে আন্দোলনে ফুলে ফেঁপে ওঠে। বৃহৎ আন্দোলন হয়েও যায় মাঝে মাঝে। তখন শক্তিধরদের ঘাড়ে আর মাথা থাকে না। নির্যাতন থেকে জন্ম নেওয়া চেতনা দিয়ে তারা সৃষ্টি করে যে বিশ্ব সে বিশ্বে নতুন করে আবার কলুষতা আসে-এসে-পোড়ায় সব, সত্যকে গ্রাস করে, মিথ্যাকে প্রতিষ্ঠা করে সত্য মিথ্যার খেলায় খেলে বিশ্ব।<br />\r\n&lsquo;আন্দোলন&rsquo; বড় আজব খেলোয়াড়। মৃত্যুর পূর্ব রূপ যে শান্তি সে শক্তি কে সে বিনষ্ট করে। জীবনের স্বরূপ যে অস্থিরতা সে অস্থিরতাকে সে সৃষ্টি করে। ভয়কে জয় করে, জয় করায়ও। আধিপত্যবাদীদের কাছ থেকে সময়টাকে অধিকার করে নেয়। যুদ্ধবাজ অন্যায়কারীদের বিরুদ্ধে পাল্টা ব্যবস্থা গ্রহণ করে। পরাজিত দুর্বলের পাশে এসে সে দাঁড়ায়। সবলকে দুর্বল করে দুর্বলকে সবল করে চেতনা দিয়ে গঠন করে নব থেকে নবতর চেতনা। অযোগ্যদেরকে যোগ্যদের থেকেও যোগ্য করে তোলে, বিপদ নামক পরশ পাথরটাকে জয় করায়, বিপদে পড়তে শেখায়। সভ্যতার জন্য যারা বিধ্বংসী, সময়ের জন্য যারা ক্ষতিকর তাদের হাতে থেকে অধিকতর ক্ষতিকারক হয়ে ধ্বংস করার যে চেতনা আছে &lsquo;আন্দোলন&rsquo; সেটা দেয়। শ্রেষ্ঠতা শেখায়। সবলকে দুর্বল বানিয়ে তাদেরকে শাসন করার মূল মন্ত্র আছে এতে, জয়তু &lsquo;আন্দোলন&rsquo;। চিন্তা শক্তিতে বিপ্লব ঘটায়। পৃথিবীতে এই একটা শব্দ (আন্দোলন) যেটা সব সময় প্রথার বিরুদ্ধে গেছে। অত্যাচারী শাসকদের বিরুদ্ধে হয়েছে। দুর্বল মানুষকে সবল করানোর জন্য হয়েছে।<br />\r\nঅস্থির সময়টা স্থবির হলে পাপ বিরোধী আন্দোলন সময়টাকে আরো বেশি অস্থির করে পৃথিবীটাকে সৃষ্টিশীল করে তোলে। দুর্বলরা আন্দোলনের মাধ্যমে সবল হয়ে পৃথিবীকে যে নতুন অস্থিরতার দিকে টেনে নিয়ে যায় সেখান থেকে সৃষ্টির যে আন্দোলন সে আন্দোলনটা মূলত শুদ্ধ সামগ্রিক বিকাশেরই রাষ্ট্র আন্দোলন হয় সমাজে, আন্দোলন হয় সাহিত্যে, আন্দোলন হয় সংস্কৃতিতে, আন্দোলন হয় সত্যে মিথ্যায়, আন্দোলন হয় রাজনীতিতে, আন্দোলন হয় রাষ্ট্র-প্রতিরাষ্ট্রে, আন্দোলন হয় অংশে, আন্দোলন হয় সমগ্রে। অংশের, সমগ্রের আন্দোলনের সময় যে সামগ্রিক বিকাশ, অচল সময়কে যেমন সচল সচেতন করে তোলে; তেমনি গতিময় করে তোলে সবকিছুকে, গতিময় করে তোলে বিশ্বকে।<br />\r\nকিন্তু আন্দোলন তো আর এমনি এমনিতেই দানা বাঁধে না ও এর বিকাশ ও পরিণতিতে যে নেতার দরকার। নেতা যদি চরিত্রহীন হয় তাহলে আন্দোলন ৫০০ বৎসর করেও ফলহীন প্রস্থান অসম্ভব কিছু নয়। পৃথিবীর ইতিহাসে নেতৃত্বের ব্যর্থতার জন্য বিফল আন্দোলনের পরিণতি নেহাত কম তা বলার কোন সুযোগ নেই। অপরিহার্য আন্দোলনে দরকার ভাল নেতার? নেতা ছাড়াও আন্দোলন ফলপ্রসূ হয়, নেতা বিহীন আন্দোলন অংশের আন্দোলন, কিন্তু সামগ্রিক আন্দোলনের জন্য দরকার নেতার! নেতা আন্দোলনের সামগ্রিকতা প্রকাশ করে, সফলতা ব্যর্থতা তাঁর কাঁধে। জনপ্রীতি জনভীতি তার আছে। এর পরও আন্দোলন জনগণের বিরুদ্ধে গেলে নেতার চরিত্র স্খলন হয়। ইতিহাসে তার ভুরি ভুরি প্রমাণ আছে।<br />\r\nযে সময়টাতে এসে আমরা দাঁড়িয়েছি সে সময়ে সত্যনিষ্ঠ আধিপত্যবাদীরা অসহায় হয়ে গেছে। মিথ্যাশ্রিত শয়তানি কর্মকাণ্ডের রাজারা আধিপত্য বিস্তার করে সমাজে এমন ভাবে তাদের আধিপত্য নামক অন্যায়ের জাল ছড়িয়ে রেখেছে যেখানে সত্য পরাজিত&Ntilde;মিথ্যা বিজড়িত, ন্যায় অসহায়&Ntilde;অন্যায়কারীরা সবল। দুর্বল শোষিত&Ntilde;সবল শোষণকারী। যা নৈতিক তা কোনঠাসা, যা অনৈতিক তা বীরবিক্রম, ক্ষতি করার শক্তিতে যারা শক্তিশালী তারা নিয়ন্ত্রণ করছে বিশ্ব, রাষ্ট্র, সমাজ। ভাল সত্যাশ্রিত মানুষরা এমন অসহায় (কোন কোন সমাজে পাগল বলেও আখ্যা দেয়)। এই যে খারাপ মানুষের একচেটিয়া আধিপত্যতা চলছে, বিশ্বে&Ntilde;রাষ্ট্রে&Ntilde;সমাজে সংসারে যে তাদের অন্যায় শাসন শোষণ চলছে, এ চলাটাকে কি আমরা মেনে নিয়েছি?<br />\r\nপৃথিবী জুড়ে যুক্তরাষ্ট্রীয় যে অন্যায় তা কি আমরা মেনে নেব? তার রেশ ধরে খণ্ডিত পৃথিবীর দুর্বল রাষ্ট্রের যে শোষিত রাষ্ট্রনীতি তা কি আমরা মেনে নেব? অন্যায় হস্তক্ষেপ কি আমরা সহ্য করব? রাষ্ট্রীয় লুটপাট, জনগণের টাকা যারা চুরি করে তাদের বিরুদ্ধে কি আমরা দাঁড়াব না? সংগঠিত হব না? মিছিল করব না? আন্দোলন! আন্দোলন কি হবে না? দানা কি বাঁধবে না? মুক্তি কি আসবে না? আপোষের গায়ে পেরেক ঠুকে আপোষহীন হয়ে কথা কি ফুটবে না? অগণিত নির্যাতিত নিপীড়িতের পক্ষে, অসাম্যর বিরুদ্ধে&Ntilde;লুটপাট এর বিরুদ্ধে&Ntilde;আধিপত্যর বিরুদ্ধে&Ntilde;মিথ্যার বিরুদ্ধে খারাপ এর বিরুদ্ধে&Ntilde;তোষামোদকারীদের বিরুদ্ধে&Ntilde;দালালদের বিরুদ্ধে&Ntilde;অপরাধের বিরুদ্ধে&Ntilde;খুনিদের বিরুদ্ধে, সত্যর পক্ষে রাস্তায় কি নামা হবে না? রাষ্ট্রটা কি সব সময় অন্যায়কারীদের দখলে থাকবে? মিথ্যা প্রতিষ্ঠার সংগ্রামের যে মিছিল সে মিছিল থেকেই বড় মিছিল কি সত্য প্রতিষ্ঠার জন্য হবে না? নির্যাতিতরা কি সব সময় চুপ থাকবে? আন্দোলন কি হবে না?<br />\r\nসুষ্ঠু রাষ্ট্রের জন্য আন্দোলন অপরিহার্য। সুষ্ঠু পৃথিবীর জন্য আন্দোলন অপরিহার্য। অন্যায় যেহেতু আমাদের সমাজে ন্যায় থেকে শক্তিশালী তাই শক্তিশালী আন্দোলন আমাদের দরকার। দরকার আন্দোলনের নামে সত্যর পক্ষ নিয়ে মিথ্যার পৃষ্ঠপোষককে কোনঠাসা করে ধড় থেকে মাথাটা আলাদা করে দেওয়া। আন্দোলন চলুক।<br />\r\nবাংলাদেশের এখন যে অবস্থা সে অবস্থায় কি আন্দোলন চালানো যায়? সামাজিক, রাজনৈতিক, অর্থনৈতিক, মানবিক মূল্যবোধের যে ক্ষয়িত বা অবক্ষয়িত ধারা সে ধারা কি আন্দোলনটাকে সায় দেয়? সামাজিক রীতি নীতি তো অসামাজিকতার দিকে চলে গেছে, রাজনৈতিক অবস্থা কলুষিত হতে হতে তো পুরো রাজনীতিটাই চলে গেছে লুটেরাদের হাতে, অর্থনীতি চলে গেছে সিন্ডিকেটের হাতে, মানবিকতা আজ দানবতার স্বরূপ, সত্য অসত্যে মিশেছে। সুষ্ঠু মিশেছে অসুষ্ঠুর সাথে, ন্যায় মিশেছে অন্যায়ের সাথে, মানুষের বোধশক্তিগুলি ন্যায় থেকে অন্যায়ের বন্দরে নোঙ্গর ফেলেছে। আন্দোলনও চলছে সত্যের বিরুদ্ধে মিথ্যার পক্ষে অসম এক আন্দোলন। কত দিন চলবে এভাবে? অন্ধকারের রাত্রির কি শেষ নেই? ঊষার রাঙ্গা প্রভাত কি দেখা দেবে না? আসলে এগুলির কোন দিনই শেষ হবে না। দেখাও দেবে না, অ-আন্দোলনের যে জয় ধরা সেটাকে ছাপিয়ে ভাল আন্দোলন প্রতিষ্ঠার আগে।</span></p>\r\n','',1,15,'2016-01-07 02:46:27',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452156387_jsr-9-1.png'),(10,'বিদ্যুত সমস্যা','বিদ্যুত-সমস্যা','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ইনডেমনিটি অধ্যাদেশ জারি করা হয়েছে। সরকার থেকে এ ব্যাপারে বলা হচ্ছে বিদ্যুৎ উৎপাদনের জন্য সরকার যেসব নতুন সিদ্ধান্ত নেবে সে সিদ্ধান্তের বিরোধিতা করে কোন বিরোধী পক্ষ যাতে আদালতে মামলা মোকদ্দমা না করতে পারে সেজন্য এটা করা হয়েছে। ইনডেমনিটি আরো যেসব কারণে করা হয়েছে তার মধ্যে অন্যতম কারণগুলি হল বিদ্যুৎ প্লান্ট নতুন করে করতে গেলে (১) টাকা পয়সার উৎস (২) অপব্যবহার (৩) দুর্নীতি (৪) কনট্রাক্টর সংক্রান্ত কোন প্রশ্ন করা যাবে না। সাধারণ নাগরিকরা বিদ্যুৎ উৎপাদনের অস্বচ্ছ্বতা সম্বন্ধে কোন প্রশ্ন উঠাতে পারবে না, উঠালে তার বিরুদ্ধে আদালত ব্যবস্থা নেবে। ইনডেমনিটি অধ্যাদেশ করার পর থেকে আজ পর্যন্ত সরকার তাৎক্ষণিক ভাবে যে সব বিদ্যুৎ কেন্দ্র স্থাপন করছে (রেন্টাল পাওয়ার, পিকিং পাওয়ার, কুইক রেন্টাল) তাদের মধ্যে অধিকাংশ বিদ্যুৎ কেন্দ্রই অধিক হারে পূর্ণ উৎপাদনে যেতে পারে নি। পত্র পত্রিকাতে এ অক্ষমতা নিয়ে লেখা লিখি হয়েছে। দলীয় আনুগত্য অযোগ্যদেরকে এ সব সেনসিটিভ বিদ্যুৎ কেন্দ্র স্থাপনের জন্য অনুমতি সরকার দিয়েছে। কেউ কেউ উৎপাদনে যেতে আংশিক কেউ কেউ সম্পূর্ণ ভাবে ব্যর্থ হয়েছে। সরকার ঘোষিত বিদ্যুৎ উৎপাদনের পূর্ব পরিকল্পনা ব্যর্থ হয়েছে। সরকারের মেয়াদের তিন বৎসর পরও দেশে উল্লেখযোগ্যভাবে বিদ্যুতের চাহিদা মেটে নি। অথচয় সরকার থেকে পত্র পত্রিকা মারফত বলা হচ্ছে আজ এত মেগাওয়াট উৎপাদন হয়েছে, কাল এত মেগাওয়াট উৎপাদন হবে, আগামী ছয় মাসে এত মেগাওয়াট উৎপাদন হবে এবং সরকারের মেয়াদের শেষ বৎসর কোন লোড শেডিং থাকবে না। মানুষকে মিথ্যা আশ্বাস দেওয়া হচ্ছে।<br />\r\nসরকার জরুরি ভিত্তিতে অতি উচ্চ মূল্য বিদ্যুৎ কেন্দ্র স্থাপন করছে এবং পাশাপাশি কথা ছিল স্থায়ী বিদ্যুৎ কেন্দ্রগুলি নির্মাণ হয়ে গেলে ১৩ টাকা প্রতি ইউনিটের বিদ্যুৎ কেন্দ্রগুলি বন্ধ করবে। এখন হচ্ছে যা তা হল স্থায়ী ভাবে যেসব বিদ্যুৎ কেন্দ্র স্থাপন করার কথা সেগুলির কাজ চলছে ঢিলেঢালাভাবে, এতে কুইক রেন্টাল বিদ্যুৎ কেন্দ্রগুলি নির্দিষ্ট সময়ের পরও চালু রাখতে হবে, যদি স্থায়ী বিদ্যুৎ কেন্দ্রগুলি চালু না করা যায় তাহলে কুইক রেন্টাল দিয়েই আমাদের বিদ্যুতের কুইক সমাধান করতে হবে। অতি উচ্চমূল্যায়িত বিদ্যুৎ উৎপাদনের খরচের প্রভাব পড়বে সার্বিক অর্থনীতিতে।<br />\r\nদেশের প্রখ্যাত অর্থনীতিবিদেরা বলছেন কুইক রেন্টাল বিদ্যুৎ উৎপাদনের জন্য যে টাকা ভর্তুকি দিতে হচ্ছে তা সরাসরি দেশের অর্থনীতিতে প্রভাব পড়ছে। এই কুইক রেন্টালের ফলে দুই দশকের অর্থনীতি তছনছ হয়েছে বলে সাপ্তাহিকে সাবেক উপদেষ্টা হোসেন জিল্লুর রহমান মন্তব্য করেছেন। আনু মুহম্মদ, এম. এম. আকাশ, আবু আহমেদ, সাবেক অর্থ উপদেষ্টা মির্জা আজিজুল হকও এ মতামতের সাথে একমত পোষণ করেছেন।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">তেল গ্যাস বিদ্যুৎ বন্দর রক্ষা জাতীয় কমিটির থেকেও এ ধরনের আশংকার কথা বলেছেন। জাতীয় কমিটির আহ্বায়ক প্রকৌশলী শেখ মুহাম্মদ শহীদুল্লাহ দ্ব্যর্থহীন কণ্ঠে বলেছেন বিদ্যুৎ সেক্টরে &ldquo;লুটপাট চলছে&rdquo; চলছে সরকারের পৃষ্ঠপোষকতায়। আমরা নির্বাক হয়ে তা দেখছি। তিনি আরো বলেন দুর্নীতিবাজ উপদেষ্টা তৌফিক&Ntilde;ই&Ntilde;এলাহী চৌধুরী প্রধানমন্ত্রীর নীরব সম্মতিতে এসব করছেন। ধূর্ত এ উপদেষ্টাকে বাদ দিতে প্রধানমন্ত্রীকে বার বার অনুরোধ করা হয়েছে, তবুও প্রধানমন্ত্রী তাকে বহাল রেখেছেন। এখনও বলতে হয় প্রধানমন্ত্রী নিজেই বিদ্যুৎ ও খনিজ সেক্টরে তার উপদেষ্টার নেতৃত্বে লুটপাট করাচ্ছেন। তিনি আরো বলেন এর পিছনে আছে সাম্রাজ্যবাদীদের হাত। তিনি খনিজ সম্পদকে সাম্রাজ্যবাদীদের হাত থেকে রক্ষা করার জন্য তেল গ্যাস জাতীয় কমিটির নেতৃত্বে দুর্বার আন্দোলনেরও কথা ব্যক্ত করেছেন। আগামী প্রজন্মের জন্য খনিজ সম্পদকে রক্ষা করার এ আন্দোলনে ঝাঁপিয়ে পড়ার জন্য তিনি ষোল কোটি মানুষকে উদাত্ত আহ্বান জানাচ্ছেন।<br />\r\nবুয়েটের বিশেষ্ণরা বর্তমান বিদ্যুৎ চাহিদা মেটানোর ক্ষেত্রে বলেন কুইক রেন্টালে যেহেতু টাকা বেশি খরচ হয় তাই সরকার কুইক রেন্টালের পরিবর্তে প্রথম থেকেই পুরাতন পরিত্যক্ত বিদ্যুৎ কেন্দ্রগুলির ওভারহলিং করে (নট মেনটিনেন্স) বিদ্যুৎ উৎপাদন বাড়াতে পারত। এতে টাকা ও সময় দুটাই বাঁচত। তিনি উল্লেখ করেন ঘোড়াশালের ২নং ইউনিটে ৪৫ মেগাওয়াটের, ঘোড়াশাল ৩ ও ৬ নং ইউনিটের প্রতিটিতে ২১০ মেগাওয়াটের সিদ্ধিরগঞ্জে ২১০ মেগাওয়াটের এবং বড় পুকুরিয়ায় ১২৫ মেগাওয়াটের পুরাতন বিদ্যুৎ কেন্দ্রগুলি পরিত্যক্ত অবস্থায় পড়ে আছে। সরকার ইচ্ছা করলেই ওভারহলিং করে অল্প সময়ের মধ্যে (কুইক রেন্টাল থেকেও আগে) ৪৫ + ২১০দ্ধ২ + ২১০ + ১২৫= ৮০০ মেগাওয়াট বিদুৎ উৎপাদন করতে পারে। সাবেক পাওয়ার ও পাউবোর সাবেক ডিজি ম. ইনামুল হক, নুর মুহাম্মদ এনার্জি সেক্টরের সাবেকমেহা পরিচালক বিডি রহমতুল্লা, বুয়েটের বিশেষ্ণদের সাথে একমত পোষণ করেছেন। ঢা:বি: বাংলার সাবেক বিভাগীয় প্রধান এবং চিন্তাশীল লেখক অধ্যাপক আবুল কাসেম ফজলুল হক তেল গ্যাস বিদুৎ বন্দর রক্ষা জাতীয় কমিটির আহ্বায়ক শেখ মুহম্মদ শহীদুল্লা ও সদস্য সচিব আনু মুহাম্মদকে জনগণের স্বার্থে সরকার যেন পরিত্যক্ত বিদ্যুৎ কেন্দ্রগুলিকে অল্প সময়ের মধ্যে নবায়িত করে মানুষের দুর্দশা ঘুচায় তার জন্য কর্মসূচি ঘোষণা করার অনুরোধ জানিয়েছেন। তিনি আরো বলেন আন্দোলের মাধ্যমে যেমন মানব জাতির বিকাশ, আন্দোলনের মাধ্যমে যেমন আমাদের বেড়ে উঠা, প্রতিকূলতা যেমন আমাদের নিত্য সঙ্গী তাই জাতীয় সম্পদ রক্ষার জন্য আন্দোলনই মুখ্য পথ। জনগণকে সাথে নিয়ে এমন আন্দোলন দানা বাঁধাতে হবে যেন সরকার দেশে যে কোন সেক্টরে দুর্নীতি করতে সাহস না করে। তিনি উল্লেখ করেন আন্দোলনের এই সাধনা সংগ্রামের এ যাত্রার পরাজয় নেই। তিনি বিশ্বাস করেন দেশ প্রেমিকদের এ আন্দোলন নতুন করে জাতিকে জাতীয় দৃঢ়তার দিকে নিয়ে যাবে। জয় হোক জাতীয় কমিটির এ আন্দোলনের।</span></p>\r\n','',1,15,'2016-01-07 02:51:19',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452156679_jsr-9-1.png'),(11,'বিশ্বব্যাংক-আইএমএফ-এডিব: গণট্রাইবুনাল','বিশ্বব্যাংক-আইএমএফ-এডিব:-গণট্রাইবুনাল','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাদী<br />\r\nবাংলাদেশের জনগণের পক্ষে<br />\r\nজোনায়েদ সাকি<br />\r\nসমন্বয়কারী, গণসংহতি আন্দোলন এবং<br />\r\nআহ্বায়ক, বাংলাদেশ কৃষক&Ntilde;মজুর সংহতি</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রতিবাদী:<br />\r\n১। বিশ্বব্যাংক<br />\r\n২। আন্তর্জাতিক মুদ্রা তহবিল<br />\r\n৩। এশীয় উন্নয়ন ব্যাংক<br />\r\n৪। উপরোক্ত প্রতিষ্ঠানসমূহের সহযোগী গং</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">অভিযোগের ক্ষেত্র<br />\r\nবাংলাদেশের কৃষিখাত<br />\r\nবাংলাদেশ প্রতিষ্ঠার অব্যবহিত পর থেকেই এই অঞ্চলে সাবেক পূর্ব পাকিস্তান আমলে বিশ্বব্যাংক ও তার সহযোগী প্রতিষ্ঠানসমূহের যে কার্যক্রম অব্যাহত ছিল তার ধারাবাহিকতা রক্ষা শুরু হয়। যদিও একটি রক্তাক্ত যুদ্ধের মধ্য দিয়ে যে স্বাধীনতা ও মুক্তির আকাক্সক্ষা গড়ে উঠেছিল তার সাথে নতুন রাষ্ট্রে উল্লিখিত প্রতিষ্ঠানসমূহের পূর্ববর্তী ভূমিকা অব্যাহত থাকা মোটেই সঙ্গতিপূর্ণ ছিল না। কিন্তু নবগঠিত শাসকশ্রেণী ও তার পরবর্তী উত্তরাধিকারীরা প্রথম দিকের অতি স্বল্প সময়ের অশ্বস্তি বাদ দিলে কোনোভাবেই এই সঙ্গতি রক্ষা করেনি। উপরন্তু জনগণের আকাক্সক্ষাকে পদদলিত করে দ্রুততার সাথে এই প্রতিষ্ঠানসমূহের কার্যক্রমকে স্বাগত জানায়, যা উত্তরোত্তর কেবল নিরঙ্কুশ খবরদারিতেই পরিণত হয়েছে। বাংলাদেশে সাম্রাজ্যবাদী যে দখল&Ntilde;লুণ্ঠন-নিয়ন্ত্রণ জারি আছে, এই প্রতিষ্ঠানসমূহের কার্যক্রম তার অন্যতম ভিত্তি হিসেবে সক্রিয়। বাংলাদেশের যে ক্ষেত্রেই বিশ্বব্যাংক, আইএমএফ, এডিবি এবং তাদের সহযোগী গংদের তৎপরতা রয়েছে, সর্বক্ষেত্রেই মোটের উপরে এর প্রভাব ধ্বংসাত্মক। কৃষিক্ষেত্রেও এর কোনো ব্যতিক্রম নেই, উপরন্তু এক্ষেত্রে বিপর্যয়ের প্রভাব সুদূরপ্রসারী। বাংলাদেশের জনগণের পক্ষ থেকে আমরা এই গণট্রাইব্যুনালের কাছে কৃষিক্ষেত্রে উল্লিখিত প্রতিষ্ঠানসমূহের তৎপরতার বিরুদ্ধে ৪টি সুনির্দিষ্ট অভিযোগ উত্থাপন করছি। আমরা ট্রাইব্যুনালের কাছে এর যথাযথ বিচার প্রার্থনা করি।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">অভিযোগ-১<br />\r\nকৃষি, ভূমি, পানি ও সামগ্রিক প্ররিবেশগত বিপর্যয়<br />\r\nবিশ্বব্যাংক, আইএমএফ, এডিবি ও তাদের সহযোগী গংদের বিভিন্ন সময়ের পরামর্শ, নীতি, প্রকল্প, সংস্কার কর্মসূচি, ঋণ, চাপ বাংলাদেশের কৃষি, মাটি, পানিসহ সামগ্রিক প্রতিবেশকে বিপর্যয়ের দ্বারপ্রান্তে নিয়ে এসেছে। বিপুলসংখ্যক জনগণ এর সরাসরি ভুক্তভোগী এবং সামগ্রিকভাবে সারা দেশের জনগণই এই ক্ষতির শিকার। পরিহাসের বিষয় হলো, এইসব বিপর্যয় ও ক্ষতিই আবার সাধিত হয়েছে &lsquo;উন্নয়নের&rsquo; নামে। ৫০&rsquo;র দশক থেকেই এই অঞ্চলে বিশ্বব্যাংকের সহযোগীদের কার্যক্রম বিস্তার লাভ করতে থাকে। ১৯৫৪ সালে ক্রুগ মিশনের সফর এবং তাদের সুপারিশের ভিত্তিতে ১৯৫৯ সালে গড়ে ওঠে &lsquo;পানি ও বিদ্যুৎ উন্নয়ন কর্তৃপক্ষ&rsquo; (ডঅচউঅ) এবং গ্রহণ করা হয় বহু বন্যা নিয়ন্ত্রণ ও সেচ প্রকল্প যার প্রভাব পড়তে থাকে কৃষিখাতে। শুরু হয় নদী, খাল, নালা বন্ধকরণ যার প্রভাবে স্বাভাবিক বন্যা হয়ে উঠতে থাকে বড় ধরনের &lsquo;প্রকৃতিক বিপর্যয়&rsquo;। ৬০&rsquo;র দশকে শুরু হয় &lsquo;সবুজ বিপ্লব&rsquo; নামক মহাযজ্ঞ যার প্রভাবে চলতে থাকে একক ফসলের বিস্তার, উফশী বীজ, রাসায়নিক সার ও কীটনাশক জনপ্রিয়করণ, বিতরণ ও বাজারজাতকরণের ব্যবস্থা। যান্ত্রিক সেচ প্রচলনের মাধ্যমে শুরু হয় ব্যাপকভিত্তিক ভূগর্ভস্থ পানির ব্যবহার। ১৯৬৩ সালে গড়ে ওঠা ইপিএডিসিকে এই &lsquo;সবুজ বিপ্লব&rsquo; তরাম্বিত করার কাজে নিয়েজিত করা হয়। ১৯৬৫ সাল থেকে বিশ্ব ব্যাংকের সরাসরি উদ্যোগ ও ঋণ সহায়তায় শুরু হয় কৃষি উপকরণ কার্যক্রমের বিস্তার। &lsquo;সবুজ বিপ্লবের&rsquo; অংশ হিসেবে যে সমস্ত প্রকল্পের মাধ্যমে উফশী ধান বীজের বিস্তার ঘটানো হয় সেগুলোর কোনোটাই উন্নত স্থানীয় প্রজাতি নিয়ে গবেষণা কাজ করেনি। ফলে একইসাথে বিস্তার ঘটে এই বীজ চাষের আবশ্যকীয় উপাদান রাসায়নিক সার, কীটনাশক এবং ভূগর্ভস্থ পানিভিত্তিক সেচের কার্যক্রম। এর প্রত্যক্ষ ফল হিসেবে মধ্য ৬০&rsquo;র দশক থেকে আন্তর্জাতিক সংস্থাসমূহের উদ্যোগে কীটনাশক, সার, বীজ এবং টিউবওয়েলের ব্যবসা ব্যাপকভাবে জোরদার হতে থাকে। এরই ধারাবাহিকতায় বিশ্বব্যাংক তার &lsquo;ন্যূনতম প্যাকেজ&rsquo; কার্যক্রমের আওতায় ১৯৭০ সালে চালু করে &lsquo;বর্ধিত ধান উৎপাদন স্কিম&rsquo; যার অধীনে লোভ দেখানো হয় পরবর্তী ২০ বছরে যদি উপকরণ, সেচ এবং পানি নিষ্কাশন ব্যবস্থা অবলম্বন করা হয় তবে একর প্রতি ধান হবে ৫০.৪৮ মে. টন। যদিও পরবর্তীকালে দেখা গেছে সবকিছু ব্যবহার করেও উৎপাদিত হয়েছে ২৬.৭৫ মে. টন অর্থাৎ লক্ষ্যমাত্রার অর্ধেক। বাংলাদেশ প্রতিষ্ঠার পর থেকে বিশ্বব্যাংক এবং তার সহযোগী প্রতিষ্ঠানসমূহের উল্লিখিত কার্যক্রম বাধাগ্রস্ত হয়নি, উপরন্তু আরো ব্যাপকভাবে তা কার্যকর হতে থাকে। ফলে দেখা যায়, ১৯৫৯ থেকে ১৯৯৩ অব্দি কেবল বৃহৎ পানি বাঁধ প্রকল্পের ৪৮২ টির মধ্যে প্রায় ৪০০ টি প্রকল্প বাংলাদেশ আমলেই বাস্তবায়িত হয়েছে। বাংলাদেশে বিশ্বব্যাংক ও তার সহযোগী প্রতিষ্ঠানসমূহ তাদের পূর্বোল্লিখিত প্রকল্পের ধারাবাহিকতা জোরদার করার জন্য বিভিন্ন ক্ষেত্রে নানা প্রকল্প হাজির করতে থাকে এবং একের পর এক তা বাস্তবায়নে হাত দেয়া হয়। ওহঃবমৎধঃবফ জঁৎধষ উবাবষড়ঢ়সবহঃ চৎড়মৎধসসব (ওজউচ), জঁৎধষ ডড়ৎশং চৎড়মৎধসসব (জডচ), ঞযধহধ ওৎৎরমধঃরড়হ চৎড়মৎধসসব (ঞওচ) ইত্যাদি বহু প্রকল্পের মাধ্যমে বিশ্বব্যাংক তার কৃষিখাত সংস্কার কর্মসূচি জোরদার করতে থাকে। জোর করে আমদানি করা কৃষি চাপিয়ে দেয়ার এইসব নীতি কৃষকদের বাধার সম্মুখীন হলেও ব্যাপক সরকারি প্রচার, একের পর এক প্রকল্প এবং আপেক্ষিক ধান উৎপাদন বৃদ্ধির ফলে সারা দেশে বিশ্বব্যাংকের প্রকল্পই বাস্তবায়িত হতে থাকে। কৃষকরা সার, কীটনাশক, নদী, সেচ নির্ভর হয়ে পড়ে। পরিণতিতে দেখা যায়, বিশ্বব্যাংকেরই পরামর্শে সার, বীজ, কীটনাশক, সেচের ওপর থেকে ধীরে ধীরে ভর্তুকি প্রত্যাহার করা হচ্ছে এবং জোরদার করা হচ্ছে এসব উপকরণের ব্যবসা। সার সঙ্কট এমন রূপ নেয় যে এ নিয়ে আন্দোলন গড়ে ওঠে এবং সার না পাওয়া উত্তেজিত কৃষকদের ওপর সরকারি পুলিশ বাহিনীর গুলিতে ১৯৯৫ সালে ১৭ জন কৃষক নিহত হয়। যে উফশী ধানের বীজ বিশ্বব্যাংকেরই প্রকল্পের মাধ্যমে বিস্তার লাভ করেছিল, সেই বিশ্বব্যাংকই এখন নানা প্রকল্পের মাধ্যমে তার বদলে হাইব্রিড এবং টারমিনেটর বীজ প্রবর্তনের ব্যাপক তৎপরতা চালু করেছে এই বীজের বহুজাতিক ব্যবসাদারদের মুনাফার উদ্দেশ্যে। বাংলাদেশে বিশ্বব্যাংক ও তার সহযোগী প্রতিষ্ঠানসমূহের এই চাপিয়ে দেয়া কৃষিনীতি গ্রহণ করতে গিয়ে বাংলাদেশের কৃষি জমি, মাটি দীর্ঘস্থায়ী ক্ষতির শিকার হয়েছে, একক ফসলের বিস্তার ও কীটনাশকের ব্যবহার ব্যাপক জীববৈচিত্র্য ধ্বংস করেছে, দেশের উপরস্থ এবং ভূগর্ভস্থ পানি সম্পদ ব্যাপকভাবে ক্ষতিগ্রস্ত হয়েছে, ভূগর্ভস্থ পানির নির্বিচার উত্তোলনের ফলে ৯০&rsquo;র দশক থেকে আর্সেনিকের প্রকোপ বিরাট আকার নিয়েছে এবং সর্বোপরি বহুজাতিক ব্যবসাদার ও স্থানীয় মধ্যস্বত্বভোগীদের দৌরাত্ম্য বৃদ্ধির কারণে খোদ কৃষকের জন্য কৃষি অলাভজনক খাতে পরিণত হয়েছে। যার প্রত্যক্ষ ফল দাঁড়িয়েছে দেশের অধিকাংশ কৃষকের জীবনে ব্যাপক দারিদ্র্য।<br />\r\nশুধু তাই নয়, ৬০&rsquo;র দশকে &lsquo;কোস্টাল ইমবাঙ্কমেন্ট প্রজেক্ট&rsquo;&Ntilde;এর আওতায় উপকূলের ১৭টি জেলায় ৩৩টি পোল্ডার তৈরি করে বিস্তীর্ণ এলাকাকে কৃষি জমির আওতায় নিয়ে আসার ফলে দক্ষিণ পশ্চিম অঞ্চলের ভূমি গঠন বন্ধ হয়ে যায়। বাংলাদেশ একটি বদ্বীপ হিসেবে কয়েক লক্ষ বছর ধরে গঠিত হয়ে আসছে। হিমালয় ও বঙ্গোপসাগর থেকে আসা পলি যে ভূমি গঠন করে, পোল্ডার তৈরির ফলে উপকূলের ভূমিতে পলি আসা বন্ধ হয়ে যায়। বঙ্গোপসাগরের সঙ্গে যুক্ত নদীগুলোর স্বাভাবিক প্রবাহেও বাধা সৃষ্টি হয়। ফলে মারা যায় শতাধিক নদী। বর্ষায় বৃষ্টির পানি নামার পথও যায় বন্ধ হয়ে। সাতক্ষীরা, যশোর ও খুলনার বিশাল প্লাবন ভূমির মাছের বিচরণ ক্ষেত্র নষ্ট হয়। দক্ষিণ পশ্চিমাঞ্চলের বিশাল এলাকা জুড়ে তৈরি হয় জলাবদ্ধতা। এই জলাবদ্ধতা দূর করতে ৮০&rsquo;র দশকে একই এলাকায় এডিবি&rsquo;র সহায়তায় নেয়া হয় &lsquo;খুলনা&Ntilde;যশোর ড্রেনেজ রিহ্যাবিলিটেশন প্রজেক্ট&rsquo;। প্রকল্পের আওতায় তৈরি হয় আরো নতুন নতুন বেড়ি বাঁধ, স্লুইসগেট। এর ফলে জলাবদ্ধতা আরো বেড়ে যায়। বিল ডাকাতিয়া, বিল খুকশিয়াসহ দক্ষিণ পশ্চিমাঞ্চলে এই প্রকল্পের বিরুদ্ধে স্থানীয় জনগণ আন্দোলন গড়ে তুলতে এবং পোল্ডার কেটে অবাধ জোয়ার&Ntilde;ভাটার মাধ্যমে জলাবদ্ধতা দূর করার দাবি জানালে পুলিশ আন্দোলনকারীদের বিরুদ্ধে মামলা করে।<br />\r\n৮০&rsquo;র দশকে বিশ্বব্যাংক ও তার সহযোগী গংদের আরো দু&rsquo;টি প্রকল্প বাংলাদেশে ভয়ানক বিপর্যয় ডেকে আনে। এর প্রথমটি হচ্ছে দক্ষিণ পশ্চিমাঞ্চলে চিংড়ি চাষের &lsquo;উন্নয়নের&rsquo; নামে নেয়া &lsquo;শ্রিম্প কালচার ডেভেলপমেন্ট প্রজেক্ট&rsquo;। বিশ্বব্যাংকের পরামর্শে ও অর্থায়নে তৈরি করা পোল্ডার কেটে লবণ পানি প্রবেশ করিয়ে চিংড়ি চাষের সুযোগ করে দেয়া হয়। স্থানীয় প্রভাবশালীরা এ সুযোগকে কাজে লাগিয়ে ১০টি পোল্ডার কেটে লবণ পানি প্রবেশ করায়। স্থানীয় অধিবাসীদেরকে উচ্ছেদ করে তাদের জমি দখল করে। বিভিন্ন সন্ত্রাসী গোষ্ঠীর অভয়ারণ্যে পরিণত হয় পুরো এলাকা। ১৯৮৬ সালে নেয়া এ প্রকল্পের ফলে পুরো এলাকায় যে পরিবেশগত বিপর্যয় নেমে আসে বিশ্বব্যাংক প্রথমে তা স্বীকার না করলেও পরে ১৯৯০ সালের এক মূল্যায়নে ঘুরিয়ে তা স্বীকার করে এ প্রকল্পের ফলে পুরো এলাকার মাটি কৃষি কাজের অনুপযোগী হয়ে পড়ে, ম্যানগ্রোভ বন নিধন ও ধ্বংস হয়, ভূমিহীনদের বঞ্চনা বৃদ্ধি পায়, শাকসবজি, ফলমূল চাষ ধ্বংস ও জীবনধারণ চক্রের অবসান ঘটে। পুরো এলাকায় একদিকে যেমন ব্যাপক দারিদ্র্য বৃদ্ধি পায় অন্যদিকে তৈরি হয় বিপুলসংখ্যক কোটিপতি যারা এখন দাবি করছে পুরো দক্ষিণ পশ্চিমাঞ্চলের ১১টি জেলা চিংড়ি চাষের জন্য তাদেরকে দিয়ে দিতে। একই দশকে ১৯৮৮ সালে ভয়াবহ বন্যার পর পূর্ববর্তী &lsquo;বন্যা নিয়ন্ত্রণ প্রকল্প&rsquo;গুলির নিদারুণ ব্যর্থতা এবং বন্যার ভয়াবহ হয়ে ওঠার কারণ হিসেবে চিহ্নিত হওয়া সত্ত্বেও বিশ্বব্যাংক ও তার সহযোগী গং বন্যা ও পানি ব্যবস্থাপনায় বিশ্বের সবচেয়ে বড় প্রকল্প &lsquo;ফ্লাড এ্যাকশন প্ল্যান&rsquo; (ঋঅচ) বাংলাদেশে চাপিয়ে দেয়। সরকারি কর্তারাও নিজেদের পকেট ভর্তির আশায় এই প্রকল্প প্রণয়ন ও বাস্তবায়নে উঠেপড়ে লাগে। দেশের বেশিরভাগ নদীর ওপর কালভার্ট, স্লুইসগেটসহ বিভিন্ন অবকাঠামো নির্মাণ হয় প্রকল্পের আওতায়। দুই বিলিয়ন ডলার ব্যয়ে এ প্রকল্পের বিরুদ্ধে দেশীয় ও আন্তর্জাতিক পর্যায়ে আন্দোলন শুরু হয়। অভিযোগ ওঠে, প্রকল্পের আওতায় তৈরি অবকাঠামোগুলির কারণে দেশের প্রায় তিনশ নদী মারা যাচ্ছে। প্রকল্প চলাকালীন অবস্থাতেই উত্তর ও দক্ষিণ বঙ্গের বেশ কিছু নদী শুকিয়ে যায়। নদী পথে যোগাযোগ ব্যবস্থা ভেঙ্গে পড়ে। শেষ পর্যন্ত দেশী&Ntilde;বিদেশী নানা আন্দোলনের মুখে প্রকল্পটি মাঝ পথে বন্ধ হয়ে গেলেও নতুন নতুন নামে একই ধরনের বিভিন্ন প্রকল্প বাস্তবায়নের তৎপরতা এখনো অব্যাহত আছে।<br />\r\n৯০&rsquo;র দশকে এডিবি&rsquo;র সহায়তায় &lsquo;সুন্দরবন বায়োডাইভারসিটি প্রজেক্ট&rsquo; এবং নর্থ&Ntilde;সাউথ &lsquo;ক্রপ ডাইভারসিফিকেশন প্রজেক্ট&rsquo; নামে যে দু&rsquo;টি প্রকল্প হাতে নেয়া হয় তার ফলে সুন্দরবনের ম্যানগ্রোভ বন বিপর্যস্ত হয় এবং নতুন কৃষি প্রযুক্তি হস্তান্তরের নামে কৃষকদের মধ্যে হাইব্রিড বীজ প্রচলনের তৎপরতা বাড়ে। বিপুল অর্থব্যয়ে এসব প্রকল্প যেমন পরিবেশগত বিপর্যয় ঘটিয়েছে তেমনি আমদানি করা কৃষি চপিয়ে দেয়ার মাধ্যমে কৃষকদেরও সর্বনাশ ঘটিয়েছে।<br />\r\nবিশ্বব্যাংক ও তার সহযোগীরা এখনো পর্যন্ত নানা নামে ইত্যাকার ধ্বংসাত্মক প্রকল্প চাপিয়ে দেয়ার কাজ অব্যাহত রেখেছে। অতি সাম্প্রতিক সময়ে তাদের দু&rsquo;টি প্রকল্প &lsquo;এগ্রিকালচার টেকনোলজি ট্রান্সফার প্রজেক্ট&rsquo; এবং &lsquo;ক্লাইমেট চেঞ্জ এডাপটেশন ফান্ড&rsquo; একই ধ্বংসাত্মক ফলাফল বয়ে আনবে। বিশেষত দুই বিলিয়ন ডলারের দ্বিতীয় প্রকল্পটি যেভাবে উপকূলে বিপুল অবকাঠামো হাতে নিয়েছে তার ফল হবে সুদূরপ্রসারী বিপর্যয়।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">অভিযোগ-২<br />\r\nকৃষিখাতে অপ্রয়োজনীয় ঋণের বোঝা এবং সুদ পরিশোধের নামে বিপুল অর্থ পাচার<br />\r\nপ্রথম অভিযোগে আমরা ইতোমধ্যেই সংক্ষেপে স্পষ্ট করেছি যে, বিশ্বব্যাংক, এডিবি&rsquo;র প্রকল্পসমূহ বাংলাদেশের কৃষি খাতে বিপর্যয়কর ও ধ্বংসাত্মক প্রভাব তৈরি করেছে। কিন্তু এতেই শেষ নয়। বিশ্বব্যাংক ও তার সহযোগীদের প্রকল্পসমূহ বাংলাদেশের জনগণের ওপরে এক বিরাট আর্থিক বোঝাও চাপিয়ে দিয়েছে। একের পর এক গৃহীত প্রকল্পে যে অর্থ ব্যয় হয় তার বিরাট অংশ যায় বিদেশী কনসালট্যান্ট এবং স্থানীয় শাসকগোষ্ঠীর সংশ্লিষ্টদের পকেটে। এই আর্থিক ও বৈষয়িক সুবিধা প্রদান কার্যক্রম একটি পরিকল্পিত তৎপরতা, যার মাধ্যমে বিশ্বব্যাংকের অনুগত গোষ্ঠী সক্রিয় থাকে। প্রকল্পসমূহের একটি বড় অংশই শেষ হয় না কিংবা ক্ষতিকর প্রভাবের কারণে মাঝপথে বন্ধ হয়ে যায়। কিন্তু বিশ্বব্যাংকের প্রকল্প সহায়তা যেহেতু ঋণের টাকা, এর ফলে সরকারকে সুদ দিতে হয় ষোলআনা। যেমন ১৯৭২ সালে বিশ্বব্যাংক অজানা কারণে তাদের নিজেদেরই নেয়া ৬০&rsquo;র দশকের &lsquo;গঙ্গা&Ntilde;কপোতাক্ষ সেচ ও বন্যা নিয়ন্ত্রণ প্রকল্প&rsquo;কে রষষ-ংঁরঃবফ হিসেবে আখ্যায়িত করে। কিন্তু ততদিনে ১৩২ মিলিয়ন মার্কিন ডলার খরচ হয়ে গেছে। বাংলাদেশের জন্মলগ্ন থেকেই বিশ্বব্যাংক ঋণের বোঝা চাপিয়ে দিয়ে আসছে। সেই ধারাবাহিকতা এখনো অব্যাহত আছে। সম্প্রতি গৃহীত &lsquo;ক্লাইমেট চেঞ্জ এডাপটেশন ফান্ড&rsquo; নামে যে প্রকল্প তার ফলাফল যত ক্ষতিকরই হোক, ঋণের বোঝা টানতে হবে বাংলাদেশকেই। জনগণের করের টাকায় সরকার এই অপ্রয়োজনীয় ঋণ শোধ করে চলেছে। ফলে বিপুল অর্থ পাচার হচ্ছে। উল্লেখযোগ্য যে, বাংলাদেশে চলতি বছরের বাজেটে ঋণের সুদ পরিশোধের পরিমাণ মোট রাজস্ব বাজেটের ১৭ শতাংশ যার গুরুত্বপূর্ণ অংশ হচ্ছে বিদেশী ঋণের সুদ।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">অভিযোগ-৩<br />\r\nকৃষিখাতে জাতীয় সক্ষমতা ধ্বংস<br />\r\n১৯৬৩ সালে প্রতিষ্ঠিত ইপিএডিবি বাংলাদেশ প্রতিষ্ঠার পর থেকে বিএডিসি নামে কার্যক্রম শুরু করে। বিশ্বব্যাংক ও তার সহযোগীরা এই প্রতিষ্ঠানটিকে তাদের &lsquo;সবুজ বিপ্লব&rsquo; ও পরবর্তী কার্যক্রম বাস্তবায়নে ব্যবহার করলেও ৮০&rsquo;র দশক থেকে এই প্রতিষ্ঠানকে পঙ্গু করার ব্যাপক উদ্যোগ গ্রহণ করে। বিশ্বব্যাংকের &lsquo;স্ট্রাকচারাল এ্যাডজাস্টমেন্ট প্রোগ্রাম&rsquo; (ঝঅচ)&Ntilde;এর আওতায় দেশের অন্যান্য খাতে যেমন তেমনি কৃষিখাতেও রাষ্ট্রীয় প্রতিষ্ঠানকে সংকুচিত করে সমস্ত কার্যক্রমকে ধীরে ধীরে বেসরকারিকরণের উদ্যোগ শুরু হয়। ৯০&rsquo;র দশকে এসে বিএডিসির বাজেট সংকুচিত করা হয় বিপুলভাবে এবং প্রায় ৭০ ভাগ জনবল ছাঁটাই করা হয়। এসময় বাংলাদেশের প্রায় দু&rsquo;হাজার দক্ষ জনবল চাকরিচ্যুত হয়ে বিদেশে চলে যান। সার, বীজসহ কৃষি উপকরণ সরবরাহের কাজ বিএডিসি&rsquo;র হাত থেকে কেড়ে নিয়ে বেসরকারিকরণ করা হয়। এসব বিতরণের দায়িত্ব ন্যস্ত হয় স্থানীয় রাজনৈতিক টাউট ও ফড়িয়াদের হাতে। বাংলাদেশ ধান গবেষণা ইনস্টিটিউট এবং বাংলাদেশ কৃষি গবেষণা ইনস্টিটিউটও একই পরিণতি লাভ করে। ফলে বন্ধ হয়ে যায় নতুন কোনো গবেষণা। বিশ্বব্যাংক এবং তার সহযোগী সংস্থার কর্মসূচির আওতায় রাষ্ট্রীয় প্রতিষ্ঠান এভাবে ধ্বংস হওয়ার মধ্য দিয়ে বাংলাদেশের জাতীয় সক্ষমতা ব্যাপকভাবে ক্ষতিগ্রস্ত হয়েছে। বহুজাতিক কর্পোরেট পুঁজির দৌরাত্ম্য নিরঙ্কুশ হয়েছে। ফলে খাদ্য নিরাপত্তা এবং জাতীয় সার্বভৌমত্বও হুমকির সম্মুখীন হয়েছে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">অভিযোগ-৪<br />\r\nস্থানীয় জ্ঞান ও কর্তৃত্ব থেকে কৃষকদের উচ্ছেদ এবং কর্পোরেট পুঁজির আধিপত্য কায়েম ৬০&rsquo;র দশক থেকে সবুজ বিপ্লবের হাত ধরে অদ্যাবধি বিশ্বব্যাংক ও তার সহযোগীদের কৃষিখাতে সামগ্রিক তৎপরতা কৃষি কার্যক্রমে দেশের কৃষকদের কর্তৃত্বকে বহুলাংশে খর্ব করেছে। তার স্থলে তৈরি করেছে বহুজাতিক কর্পোরেট পুঁজির কর্তৃত্ব। আমাদের দেশের কৃষকদের হাজার বছরের সঞ্চিত জ্ঞান, তাদের লিপ্ততা, বহুবৈচিত্রপূর্ণ বীজের ওপর তাদের অধিকার সবকিছু থেকে কৃষক সমাজ এবং সাধারণভাবে পুরো দেশ ও জাতি বঞ্চিত হয়েছে এবং পরিপূর্ণভাবে উচ্ছেদ হওয়ার পথে হাঁটছে। এর ফলে ব্যাপকভাবে আগ্রাসনের শিকার হয়েছে আমাদের ঐতিহ্য ও সংস্কৃতিও। বাংলাদেশের পাঁচ হাজার স্থানীয় জাতের ধানবীজ নিয়ে এখন ওহঃবৎহধঃরড়হধষ জরপব জবংবধৎপয ওহংঃরঃঁঃব (ওজজও) গবেষণা করছে যার পেটেন্ট থাকবে মূলত আন্তর্জাতিক সংস্থা ও বহুজাতিক কোম্পানিগুলোর অধিকারে। এ থেকেই বোঝা যায়, বিশ্বব্যাংক ও তার সহযোগীদের প্রকল্প বাংলাদেশের কৃষিখাতকে কোনদিকে নিয়ে যাচ্ছে।<br />\r\nউল্লিখিত অভিযোগের ভিত্তিতে আমরা গণ ট্রাইব্যুনালের কাছে বিশ্বব্যাংক, আইএমএফ, এডিবি ও তার সহযোগী গংদের বিচার প্রার্থনা করি।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বি:দ্র: অভিযোগে ব্যবহৃত তথ্যসমূহের সূত্র এখানে উল্লেখ করা হয়নি। ট্রাইব্যুনাল</span></p>\r\n','',1,15,'2016-01-07 02:56:48',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157008_jsr-10-2.png'),(12,'ঢাকা মহানগরীর পানি সরবরাহ গল্প এবং বাস্তবতা: প্রকৌশলী ম. ইনামুল হক','ঢাকা-মহানগরীর-পানি-সরবরাহ-গল্প-এবং-বাস্তবতা:-প্রকৌশলী-ম.-ইনামুল-হক','<p style=\"text-align:justify\"><span style=\"font-size:16px\">ঢাকা নগরী<br />\r\nঢাকা মোগল আমলে ১৬১২ থেকে ১৭১২ সাল পর্যন্ত বাংলা সুবার রাজধানী ছিলো। ব্রিটিশ আমলে ১৯০৫ থেকে ১৯১১ বাংলা ও আসাম প্রদেশের রাজধানী ছিলো। ১৯৪৭ এর পর ঢাকা পূর্ব পাকিস্তানের রাজধানী হয়ে বিশ্বের অন্যান্য আধুনিক নগরীর মতই একটি নগরীতে রূপ নেবার জন্য এর ভাগ্য নির্ধারিত হলো। ১৯৭১ সাল থেকে ঢাকা স্বাধীন বাংলাদেশের রাজধানী। ঢাকা পৌরসভা হয় ১৮৬৪ সালে। এর প্রধানকে বলা হতো চেয়ারম্যান। ১৯৮৩ সালে ঢাকা নগরীকে সিটি কর্পোরেশন করা হলো এবং তখন থেকে এর প্রধান হলেন মেয়র।<br />\r\nঢাকা মহানগরীর বর্তমান জীবন পৃথিবীর যে কোন নগরীর জীবন থেকে ভিন্ন। ঢাকা জনসংখ্যায় উপচে পড়া, নোংরা এবং গোলমেলে, এবং সকল ক্ষেত্রেই তাড়াহুড়ো। ধনী ও গরীব উভয়েই জনগণের জমি ও উদ্যান দখল করে। সারা নগরী অবৈধ বস্তি আর অবৈধ দালানে পূর্ণ। জলাভূমি এবং পুকুরগুলি ভরাট করে এবং বাড়ী ও দোকানপাট করে নিঃশেষ করা চলছে। কিছু অবশিষ্ট জলাভূমি দুর্গন্ধময় পয়ঃবর্জ্য এবং বিষাক্ত শিল্পবর্জ্যে পূর্ণ। সড়ক ও ফুটপাথগুলি অবৈধ দোকান বসিয়ে আর নির্মাণসামগ্রী রেখে দখল করা হয়েছে। এর পানি সরবরাহ ব্যবস্থা মোটেই ভালো নয়, আর এর পয়ঃ এবং বর্জ্য ব্যবস্থাপনা জঘন্যতম।<br />\r\nঢাকার পানি সরবরাহ<br />\r\nঢাকা নগরীর পরিকল্পনা ও উন্নয়ন পরিক্রমায় ১৯৬৩ সালে ঢাকা ওয়াসার জন্ম, যার কাজ ছিলো নগরবাসীকে নিরাপদ পানি সরবরাহ করা এবং পয়ঃব্যবস্থাপনা। এর আগে ঢাকা পৌরসভা ও জনস্বাস্থ্য প্রকৌশল অধিদপ্তর কিছু এলাকায় পানি সরবরাহ করতো। স্বাধীন বাংলাদেশের রাজধানী হবার পর এর আওতা বেড়ে পূর্বে বালু নদী উত্তরে টঙ্গী খাল পশ্চিমে তুরাগ ও বুড়িগঙ্গা নদী এবং দক্ষিণে নারায়ণগঞ্জ শহর পর্যন্ত বাড়লো। বর্তমানে ঢাকা ওয়াসার এলাকা প্রায় ৩৮০ বর্গকিলোমিটার।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">ঢাকা নগরীতে জনপ্রতি ১০০ লিটার দৈনিক চাহিদার ভিত্তিতে মোট পানির চাহিদা হিসাব করা যায়। মার্কিন যুক্তরাষ্ট্রে জনপ্রতি ২২৫-৪৭৫ লিটার পানির চাহিদা ধরা হয়। ঢাকা নগরীতে প্রতিটি মানুষের গোসল ও স্নানের জন্য চাহিদা ধরা যায় ৫০ লিটার, কাপড় ধোয়া ও পরিষ্কার পরিচ্ছন্নতার জন্য ২৫ লিটার, রান্নার জন্য ১৫ লিটার, পানের জন্য ৫ লিটার ও অন্যান্য ৫ লিটার। এই হিসাবে ২০১০ সালে ঢাকা ও নারায়ণগঞ্জের মোট চাহিদা ছিলো ২১০০ মিলিয়ন লিটার প্রতিদিন যা গ্রীষ্মকালে ২০% বেশি হয়।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">ঢাকা ওয়াসার ৪টি ভূপরিস্থ জল শোধন কারখানা আছে যার দু&rsquo;টি গোদনাইল, নারায়ণগঞ্জে এবং বাকি দুটি ঢাকার চাঁদনীঘাট ও সায়েদাবাদে অবস্থিত। গোদনাইল দুটির মোট উৎপাদন ক্ষমতা দৈনিক ১০ মিলিয়ন লিটার, চাঁদনীঘাট ৩৯.১ এবং ৩০০ বিঘা (৪০৫,০০০ বর্গমিটার) জমির উপর নির্মিত সায়েদাবাদ শোধন কারখানার ক্ষমতা দৈনিক ২২৫ মিলিয়ন লিটার। এই শোধন কারখানাটির ক্ষমতা শীগগীরই দ্বিগুন হবে। এইসব মিলে অবশ্য মোট চাহিদা পূরণ হয় না। ঢাকা নগরীর জনসংখ্যা প্রতিবছর ৬% হারে বাড়ছে, এবং সেইসাথে বাড়ছে পানির চাহিদা। এ কারণে ঢাকা ওয়াসাকে মোট চাহিদার ৮৫% সরবরাহের জন্য ভূগর্ভস্থ জলের উপর নির্ভর করতে হয়। ২০০৯ পর্যন্ত ঢাকায় ওয়াসার ৫১৩টি গভীর নলকূপ ছিলো, আরও ছিলো ১২০০ অনুমোদিত এবং প্রায় ১০০০ অননুমোদিত ব্যক্তিগত পর্যায়ের গভীর নলকূপ (দি ডেইলী স্টার ০৯.০৪.২০০৯)।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">নিরাপত্তার মান<br />\r\nবাংলাদেশ সরকারের স্ট্যান্ডার্ড টেস্টিং ইন্সটিটিউট (ইঝঞও) খাবার পানির মান নির্ধারণ করে। এই মান বিশ্ব স্বাস্থ্য সংস্থা (ডঐঙ) এবং মার্কিন যুক্তরাষ্ট্রের পরিবেশ সংরক্ষণ এজেন্সীর (টঝঊচঅ) নির্ধারিত মানের সাথে তুলনা করা চলে। এসকল তুলনা অবশ্য পানিতে অজৈব পদার্থের বেলায় সম্ভব। পানিতে দ্রবীভূত অজৈব পদার্থের মান টঝঊচঅ থেকে পাওয়া যায়, কিন্তু ডঐঙ এবং ইঝঞও থেকে পাওয়া যায়নি। পানিতে জৈব পদার্থের তুলনা যথা, ইঙউ সম/ষরঃবৎ এবং জৈব পদার্থ থেকে জন্ম নেয়া ঊ. ্ ঋবপধষ ঈড়ষরভড়ৎস ইত্যাদির তুলনা পাওয়া যায় না।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">বাংলাদেশ পরিবেশ সংরক্ষণ আইন ১৯৯৭<br />\r\nখাবার পানি, পয়ঃবর্জ্য এবং শিল্পবর্জ্য নিক্ষেপ এর বিষয়ে মান নির্ধারণ করে দিয়েছে। খাবার পানির মান শিডিউল ৩ বিতে দেয়া আছে। আমরা পরিবেশ অধিদপ্তর এবং বিএসটিআইএর মানে কিছু তারতম্য দেখতে পাই। এর কারণ শোধন কারাখানা থেকে পাওয়া খাবার পানির বেলায় বিএসটিআইএর মান এবং প্রাকৃতিক উৎস থেকে পাওয়া খাবার পানির মান পরিবেশ অধিদপ্তর অনুযায়ী ধরতে হয়।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">পানির উৎস<br />\r\nঢাকার পানি সরবরাহের উৎস এর আশেপাশের নদী এবং ভূগর্ভস্থ জল। ভূপিরিস্থ জল মূলতঃ ডেমরার কাছে শীতলক্ষা নদী; সেখান থেকে পানি তুলে খালের মাধ্যমে সায়েদাবাদের শোধন কেন্দ্রে নেয়া হয়। গোদনাইল এর বেলাতেও তাই। তবে চাঁদনীঘাটের পানির উৎস বুড়িগঙ্গা নদী, যা&rsquo; শীতকালে অকার্যকর থাকে। কারণ ঐসময় বুড়িগঙ্গার পানি এতই নোংরা হয় যে তা&rsquo; কোথায়ও শোধন সম্ভব নয়। খিলক্ষেতের কাছে একটি নতুন শোধন কারখানা নির্মাণ হচ্ছে যেখানকার পানি শীতলক্ষা থেকেই আসবে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">ভূপরিস্থ পানি ঢাকাবাসীদের কাছে শোধন করে সরবরাহ করা হলেও ভূগর্ভস্থ পানি সররাহ করা হয় সরাসরি, শোধন না করেই। ঢাকার ভূগর্ভস্থ জলের উৎস প্রায় ১০০ মিটার মাটির নীচে অবস্থিত প্লায়োসিন যুগের &lsquo;ধূপী টিলা&rsquo; বালির জলস্তর এবং ডিহিং বেলে পাথরের জলস্তর। এখান থেকে প্রতিদিন প্রায়<br />\r\n১৬০০ থেকে ১৮০০ মিলিয়ন লিটার জল তোলা হয়। এই জলস্তরে যে পরিমাণ পানি প্রতিবছর পুনঃভরণ হয় তার পরিমাণ উত্তোলনের চাইতে কম হওয়ায় প্রতিবছর জল প্রাপ্তির গভীরতা বেড়ে যাচ্ছে। ২৩ নভেম্বর ২০০৪ ঢাকার এক সেমিনারে পাওয়া তথ্য মোতাবেক ১৯৭০ সালে ঢাকার ভূগর্ভস্থ জল প্রাপ্তির গড় গভীরতা ছিলো ১১.৩ মিটার, ১৯৯৬ সালে ২৬.৬ মিটার, ১৯৯৭ সালে ২৮.১৫ মিটার, ১৯৯৮ সালে ৩০.৪৫ মিটার, ১৯৯৯ সালে ৩১.৮৫ মিটার, ২০০০ সালে ৩৪.১৮ মিটার, ২০০১ সালে ৩৭.৭৮ মিটার, ২০০২ সালে ৪২ মিটার, ২০০৩ সালে ৪৬.২৪ মিটার এবং ২০০৪ সালে ৫০.৬০ মিটার। দি ডেইলী স্টার বিএডিসির উদ্ধৃতি দিয়ে বলেছে (২৬.০৩.০৮), এই গভীরতা ২০০৫ সালে ৫৭.৪২ মিটার, ২০০৬ সালে ৫৯.৭২ মিটার এবং ২০০৭ সালে ৬১.১৮ মিটার এ দাঁড়িয়েছে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">ভূগর্ভস্থ জল প্রাপ্তির পভীরতা বেড়ে যাওয়ায় টিউবওয়েলের উত্তোলন কমেছে এবং সেই সাথে পাল্লা দিয়ে নতুন টিউবওয়েলও পোঁতা হয়েছে। ঢাকা নগরীতে টিউবওয়েল পোঁতার হার ছিলো ১৯৪৯-৫৯ বছরে ১টি, ১৯৫৯-১৯৮০ বছরে ৩.২৫টি, ১৯৮০-১৯৮৮ বছরে ৬টি, ১৯৮৯-২০০০ বছরে ২৫টি। ২০০০-২০১০ সালে এই হার আরও বেড়েছে। ওয়াসা ২০১০-১১ সালে ৬৬টি টিউবওয়েল পুঁতেছে (দি নিউ নেশন ২০.১১.২০১১)। ওয়াসা এখন সিঙ্গাইর ও সাভার থেকে প্রতিদিন ৩০০ মিলিয়ন লিটার ভূগর্ভস্থ পানি আনার ব্যবস্থা নিচ্ছে। শীতলক্ষার পানি অতিদূষিত হওয়ায় ওয়াসা এমনকি পদ্মা ও যমুনা থেকেও পানি আনার কথা ভাবছে।<br />\r\nসেবা সংস্থাসমূহ<br />\r\nঢাকা নগরীর পানি সররাহ কাজে অনেকগুলি সরকারী সংস্থা জড়িত। পানি উৎস থেকে পরিবহন, শোধন ও নাগরিকদের কাছে সরবরাহের জন্য ভূমি মন্ত্রণালয়ের অধীনস্থ ডেপুটি কমিশনার ঢাকা জমি বরাদ্দ করবেন। পরিবেশ অধিদপ্তর ভূপরিস্থ জলের মান নিয়ন্ত্রণ করবে যাতে ঐ জল সংগ্রহ করা এবং শোধন করা যায়। ঢাকা সিটি কর্পোরেশন শহরের কঠিন বর্জ্যগুলির সঠিক ব্যবস্থা করে জলক্ষেত্রগুলিকে বর্জ্যমুক্ত রাখবে। পানি উন্নয়ন বোর্ড নদীগুলিতে পানি থাকার ব্যবস্থা করবে। অভ্যন্তরীণ নৌ পরিবহন কর্তৃপক্ষ শহরের প্রয়োজনীয় পানি উত্তোলনের পরও নৌপথ চালু রাখার জন্য নৌপথগুলিতে প্রয়োজনীয় ড্রেজিং করবে। রাজধানী উন্নয়ন কর্তৃপক্ষ শহরটাকে এমনভাবে গড়ে তুলবে যাতে আবাসন, শিল্প, বাণিজ্য, বিনোদন ইত্যাদির পাশাপাশি পানি শোধন ও পয়ঃবর্জ্য শোধন কারখানাগুলি বিরাজ করতে পারে।<br />\r\nঢাকা ওয়াসা<br />\r\nঢাকা ওয়াসার ওয়েবসাইটে বলা হয়েছে ১৯৬৩ সালে পূর্ব পাকিস্তান সরকারের ১৯ নং অর্ডিনান্স অনুযায়ী &lsquo;ঢাকা পনি সরবরাহ ও পয়ঃবর্জ্য কর্তৃপক্ষ&rsquo; প্রতিষ্ঠা করা হয়েছে। ১৯৮৯ সালে ঢাকা নগরীর নিষ্কাশন ব্যবস্থা জনস্বাস্থ্য প্রকৌশল অধিদপ্তরের হাত থেকে ঢাকা ওয়াসার কাছে চলে আসে। ১৯৯০ সালে নারায়ণগঞ্জের পানি সরবরাহ, নিষ্কাশন ও বর্জ্য ব্যবস্থা ঢাকা ওয়াসার হাতে চলে আসে। স্বাধীনতার পর দুই দশকে কর্মপরিধির ব্যাপক প্রসার এবং নগরবাসীর জনসংখ্যা বৃদ্ধির জন্য ১৯৯৬ সালের ঢাকা ওয়াসা আইন অনুযায়ী এর পুনর্গঠন করা হয় ও একে একটি সেবামূলক ব্যবসায়িক প্রতিষ্ঠানে পরিণত করা হয়। এখন ঢাকা ওয়াসার আওতাভুক্ত এলাকা ৩৬০ বর্গকিলোমিটার এবং এর জনসংখ্যা ২১ মিলিয়ন।<br />\r\nঢাকা ওয়াসার প্রধান দায়িত্বগুলি হচ্ছে:-<br />\r\n১) পানি সংগ্রহ, শোধন, সংরক্ষণ এবং নগরবাসী, শিল্প ও বাণিজ্যিক প্রতিষ্ঠানকে মানসম্পন্ন পানি সরবরাহের জন্য প্রয়োজনীয় অবকাঠামো নির্মাণ, পরিচালন, উন্নয়ন ও সংরক্ষণ করা।<br />\r\n২) গৃহস্থালী এবং শিল্প তরল বর্জ্য সংগ্রহ, শোধন, এবং মানসম্পন্ন পর্যায়ে পরিত্যাগ করার জন্য প্রয়োজনীয় অবকাঠামো নির্মাণ, পরিচালন, উন্নয়ন ও সংরক্ষণ করা।<br />\r\n৩) নগরীর নিষ্কাশন ব্যবস্থা সচল রাখার জন্য প্রয়োজনীয় অবকাঠামো নির্মাণ, পরিচালন, উন্নয়ন ও সংরক্ষণ করা।<br />\r\nপ্রদত্ত সেবা<br />\r\nঢাকা ওয়াসার আওতায় ২০১০ সাল নাগাদ নারায়ণগঞ্জসহ ঢাকা নগরীর জনসংখ্যা ছিলো ২১ মিলিয়ন, অর্থাৎ গৃহস্থালী পয়বর্জ্যরে পরিমাণ ছিলো ২.১ মিলিয়ন ঘনমিটার প্রতিদিন। ঢাকা ওয়াসার একটিমাত্র পয়ঃবর্জ্য শোধন কেন্দ্র আছে পাগলায়, যার শোধন ক্ষমতা ০.১২ মিলিয়ন ঘনমিটার প্রতিদিন। এই কেন্দ্রটি ৬০% ইঙউ এবং ৮০% ভাসমান কাঠিন্য দূর করে। তবে ঢাকা নগরীর বাকী ১.৯৮ মিলিয়ন ঘনমিটার তরল বর্জ্য প্রতিদিন সরাসরি খালে ও জলাভূমিতে যায়, সেখান থেকে যায় আশেপাশের নদীগুলিতে। এছাড়া ঢাকা নগরীর হাজারীবাগ এলাকা প্রতিদিন ২০,০০০ টন ট্যানারি তরল বর্জ্য ও তেজগাঁও এলাকা ১৫,০০০ টন শিল্প তরল বর্জ্য উৎপাদন করে (২০১০)। এইসকল বিষাক্ত বর্জ্যগুলি একই পথে খাল, জলাভূমি ও নদীতে গিয়ে পড়ে; যা&rsquo; জনজীবনকে ক্ষতিগ্রস্ত করছে ও মাছের বংশ ধ্বংস করছে।<br />\r\nনগরীর কঠিন বর্জ্য সংগ্রহ এবং ব্যবস্থাপনা সিটি কর্পোরেশনের দায়িত্ব। বর্তমানে নগরীতে প্রতিদিন যে কঠিন বর্জ্য উৎপাদন হচ্ছে তার পরিমান ৪০,০০০ টনের কম নয় (ব্যক্তিপ্রতি ১/৫ কেজি), যার অর্ধেকেরও বেশি রাস্তার উপরে পড়ে থাকে। সংগৃহিত বর্জ্যের একটি বিরাট অংশ নিম্নভূমিতে অথবা রাস্তার ধারে ফেলা হয় যা&rsquo; শেষমেষ জলাভূমিগুলিকে বুজিয়ে দেয়। পয়ঃবর্জ্য শোধন কেন্দ্রের অভাব ও কঠিন বর্জ্যরে অব্যবস্থাপনা ঢাকা নগরীর জীবনকে এভাবে অতিশয় নোংরা এবং পর্যটকদের কাছে অনাকর্ষণীয় করে তুলেছে। পর্যাপ্ত পাবলিক টয়লেট না থাকায় অনেক ফুটপাথ এলাকা মুক্ত পাবলিক টয়লেট হিসেবে কাজ করে। ঢাকা নগরীর সড়কগুলির তলায় বসানো সকল বৃষ্টির পানি সংগ্রহ পাইপগুলি গৃহস্থালী ও শিল্পের তরল বর্জ্য সমূহ গ্রহণ করে জলাভূমির পথে নিয়ে যায়। কঠিন বর্জ্যগুলি পচলে তরল অংশ মাটির গভীরে প্রবেশ করে। বর্তমান ঢাকার অগভীর স্তরের ভূগর্ভস্থ জল মারাত্মকভাবে দূষিত এবং এই দূষণ আগামী ২০ বছরের মধ্যে গভীর স্তরের পানিতে পৌঁছে যাওয়ার আশঙ্কা আছে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">আসন্ন পরিস্থিতিতে ঢাকা নগরীর পানি সরবরাহের জন্য অনেকগুলি পানি শোধন কারখানা করার প্রয়োজনীয়তা সহজেই অনুমেয়, সে পানি শীতলক্ষা থেকে আনা হোক বা আনা হোক পদ্মা বা মেঘনা থেকে। কিন্তু ভূমি ব্যবসায়ীরা ঢাকার ভেতর ও আশেপাশের ফাঁকা জায়গাগুলি বিনা বাধায় দখল করে ভরাট করে চলছে। রাজধানী উন্নয়ন কর্তৃপক্ষের &lsquo;ডিটেইলড এরিয়া প্লান&rsquo; ঢাকার পূর্বদিকের নিচু জলাভূমিকে ভরাট করে আবাসিক এলাকা করার অনুমতি দিয়েছে আবার বালু নদী তীরবর্তী গ্রামগুলিকে উচ্ছেদ করে সেখানে লেক তৈরির পরিকল্পনা দিয়েছে। ঢাকা উচ্চ আদালত বিগত ২৫ জুন ২০০৯ নদীগুলির সীমানা সিএস ম্যাপের ভিত্তিতে নির্ধারণ করার আদেশ দেন। কিন্তু রাজধানী উন্নয়ন কর্তৃপক্ষ &lsquo;ডিটেইলড এরিয়া প্লান&rsquo; করার সময় সেই আদেশ উপেক্ষা করে আর এস ম্যাপের ভিত্তিতে নদীর সীমানা দেখায় এবং ডেপুটি কমিশনাররা তার ভিত্তিতেই পিলার পোঁতেন। এর ফলে ডেপুটি কমিশনারদের কাছ থেকে লিজ নেয়া নদী তীরবর্তী জমিগুলি ব্যক্তি মালিকানা হিসেবে রেকর্ড হয়ে যাচ্ছে। সম্প্রতি বিআইডাব্লিউটিএ এক্সকাভেটর দিয়ে বুড়িগঙ্গা নদীর তলার বর্জ্য অপসারণ প্রকল্প হাতে নেয়। একই সময়ে নদীর বিভিন্ন স্থানে কঠিন বর্জ্য ফেলা অব্যাহত থাকায় নদীর তলার ময়লা অপসারন প্রকল্পটি তামাশায় পরিণত হয়।<br />\r\nপানি উন্নয়ন বোর্ড আইন ২০০০ অনুযায়ী ১০০০ হেক্টরের বেশী নিষ্কাশন এলাকার কাজ এই কাজের আওতাভুক্ত হয়। ঢাকা নগরীর এলাকা ৩০,৫০০ হেক্টর, কিন্তু এর নিষ্কাশনের দায়িত্ব ওয়াসার হাতে আছে। ওয়াসা নিষ্কাশন খালগুলির পাড় থেকে অবৈধ দখল দূর করতে পারে কিন্তু তা&rsquo; পরবর্তীতে আবার দখল হবে, বিআইডাব্লিউটিএ নদীর তলা পরিষ্কারের নাটক করতে পারে, কিন্তু নদী ও খালগুলিকে পানি উন্নয়ন বোর্ডই যথাযথভভাবে রক্ষা করতে পারে, যা&rsquo; তারই কাজ। পরিবেশ অধিদপ্তরের ক্ষুদ্র সামর্থ্য দিয়ে দুই একটি আইন লঙ্ঘনকারীকে ধরা যায়, সারা দেশের পরিবেশ দূষণকারীদের ধরা তার সাধ্য নয়।<br />\r\nসরকারি সেবা প্রদানকারী সংস্থাগুলির মধ্যে সমন্বয়হীনতা এবং তাদের কাজের জবাবদিহিতার অভাব ঢাকার নদী, জলাভূমি ও পরিবেশ ধ্বংস করে দিয়েছে। পরিস্থিতি দিন দিন আরও খারাপ হচ্ছে, অন্ধকার হচ্ছে; যা&rsquo; কিনা অসাধু কর্মকারী ও রাজনৈতিক দুর্বৃত্তদের পক্ষে কাজ করে। ঢাকা ওয়াসার একটি এডিবি সাহায্যপুষ্ট প্রকল্প আছে, যা&rsquo; ২০১৪ সালে শেষ হবে। এই প্রকল্পের টাকা প্রধানতঃ বিদেশী পরামর্শকদের পেছনে আর সংস্থার ক্যাপাসিটি বিল্ডিং এর নামে খরচ হচ্ছে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">ভূপরিস্থ পানির যোগান বৃদ্ধি<br />\r\nবাংলাদেশ পানি উন্নয়ন বোর্ড ৮০০ কোটি টাকা ব্যয়ে যমুনা থেকে পানি এনে বুড়িগঙ্গা নদীর প্রবাহ বৃদ্ধি করার একটি প্রকল্প বাস্তবায়ন করছে। এই প্রকল্পের মাধ্যমে ঢাকা ও এর আশেপাশের দূষণকে পানি এনে পাতলা করা হবে এবং দূষণকে আরও ভাটিতে পাঠিয়ে দেয়া হবে, নৌপথ ও বিনোদনের উন্নতি হবে, ভূগর্ভস্থ পানির পুনঃভরণ হবে, এবং জলাভূমিগুলিকে শুকিয়ে যাওয়া থেকে বাঁচাবে। প্রকল্পের মাধ্যমে ধলেশ্বরী নদীর মুখ এবং পুঙ্গলী নদীর অনেকটা দৈর্ঘে ড্রেজিং করে বালি সরানো হবে। কিন্তু প্রকল্পটি ব্যর্থ হতে বাধ্য, কারণ খননকৃত নদীর তলা পরবর্তী বর্ষায় নদীবাহিত বালিতে আবার পূর্ণ হয়ে যাবে। এই ধরনের প্রকল্প ৩৫০ কোটি টাকা প্রতি বছর ব্যয়ে গড়াই নদীতে পরপর দুবছর করা হয়েছে কিন্তু সমস্ত প্রচেষ্টা বিফল গেছে। যমুনা নদীতে ব্যারেজ নির্মাণ করে এই ধরনের প্রকল্প হাতে নিলেই কেবল তা&rsquo; সফল হবে।<br />\r\nবৃষ্টির পানি ধরে রাখা<br />\r\nকিছু বিশেষজ্ঞরা প্রায়ই বৃষ্টির পানি ছাদে ধরে রেখে ব্যবহারের পরামর্শ দিয়ে থাকেন। তারা বলেন বছরে যে পরিমাণ বৃষ্টিপাত হয় (ঢাকায় ২.৩ মিটার), তা&rsquo; যদি ধরে রাখা যায় তাহলে তা কয়েকমাসের জন্য ভূগর্ভস্থ জলের বিকল্প হতে পারে (ডেইলী স্টার ২৪.০৫.২০১০)। রাজধানী উন্নয়ন কর্তৃপক্ষ বিল্ডিং এর ছাদে পানি ধরে রাখার কোড দিতে চাচ্ছে (নিউ এজ ০৭.১০.২০১১), কিন্তু এই ব্যবস্থা সারাবছর কাজ করবে না। ঢাকায় বৃষ্টিপাত শুকনার সময় হয় না, যখন নগরববাসী পানির অভাবে হাহাকার করে। তা&rsquo;ছাড়া বৃষ্টির পানিও শোধন করার প্রয়োজন হবে, যদি তা&rsquo; আধারে মজুদ রাখা হয়।<br />\r\nবৃষ্টির পানি যখন জলাভূমিতে জমে তখন তা&rsquo; ভূগর্ভস্থ পানির স্তরকে যোগান দেয়। সবাইকে বুঝতে হবে যে, বৃষ্টির পানি খালে বিলে গেলে অনেক ক্ষেত্রে উপকার করে যথা, দূষণ দূর করে, জলজ জীবের উপকার করে, ইত্যাদি ইত্যাদি। বৃষ্টির পানি এক জায়গায় জমা হলে সরবরাহের জন্য সংগ্রহ করা ও শোধন করাও সুবিধা, কিন্তু নানা জায়গায় হলে তা&rsquo; সংরক্ষণ ও শোধন করে ব্যবহার করাটা অর্থনৈতিক দিক দিয়ে তেমন সুবিধাজনক হবে না।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">ভূগর্ভস্থ জলের যোগান<br />\r\nবিশেষজ্ঞদের কাছ থেকে আমরা প্রায়ই (ব.ম., ওডগ ংবসরহধৎ, ধঃ চধহ চধপরভরপ ঝড়হধৎমধড়হ ঐড়ঃবষ, উযধশধ ১২ ঔঁহব ২০১১) ভূগর্ভস্থ জলের যোগান বিষয়ে অভিনব ধারণার কথা শুনি। বিগত ঐ সেমিনারে বলা হলো &lsquo;ঢাকার বৃষ্টিপাতের ৬০% বাড়ির ছাদ থেকে ধরতে পারলে ৮৯,৪৯৬ মিলিয়ন লিটার পানি সংগ্রহ করা যাবে। এই পানি ভূগর্ভস্থ জলে যোগান দিলে তা&rsquo; নগরে ২৪৫ মিলিয়ন লিটার প্রতিদিন জল সরবরাহের সমান হবে। এই পরিমাণ পানি সায়েদাবাদ পানি শোধন কারখানা থেকে পাওয়া জলের চেয়ে বেশি&rsquo;। কিন্তু এটি ছিলো একটি অবাস্তব ধারণা এবং বিধ্বংসীও বটে।<br />\r\nভূগর্ভস্থ জল বালি স্তরের ভেতর দিয়ে নীচে নামার সময় প্রাকৃতিকভাবেই পরিশ্র&ldquo;ত হয়ে পানযোগ্য হয়ে যায়। ওপর থেকে সরাসরি ভূগর্ভস্থ জলে প্রবেশ করাতে হলে তা&rsquo;র অবকাঠামো নির্মাণ ও তা&rsquo; সরারবছর সংরক্ষণ করা অতিশয় ব্যয়সাধ্য হবে। উপরের পানি সংগ্রহ করে শোধন না করে প্রবেশ করালে ভূগর্ভস্থ জলের স্তরকেও দূষিত করবে। দেশব্যাপী বিস্তৃত ধূপী টিলা জলস্তরটি দূরবর্তী বিভিন্ন জলাধার ও নদী থেকে জল যোগান নেয়, তাই উপর থেকে ভরে দেয়া জল সামান্যই যোগান দেবে। যে পানি তলা থেকে তুলে ব্যবহার হবে সেই পানি বৃষ্টি থেকে সংগ্রহ করে ভেতরে পাঠিয়ে আবার তা&rsquo; তুলে ব্যবহার করতে জ্বালানীও খরচ হবে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">পানি ফুটিয়ে খাওয়া<br />\r\nঢাকা নগরীতে এখন ঘরে ঘরে পানি ফুটিয়ে খাওয়া সকলের স্বভাবে পরিণত হয়েছে। এই পানি ওয়াসার শোধন করা পানি হোক বা ভূগর্ভস্থ স্তর থেকে সরাসরি তোলা পানি হোক। কিন্তু কোন ক্ষেত্রেই তার প্রয়োজন হয় না। এই ব্যাপারটি শুরু হয় ১৯৮০ র দশকে, যখন পাইপ ফুটো হবার কারণে ওয়াসার পানিতে ড্রেনের ময়লা পানির মিশ্রণ পাওয়া গেলো। ওয়াসার পানি ইঝঞও এর মান অনুযায়ী শোধণ করে সরবরাহ করা হয়, যা&rsquo; পান করলে ক্ষতির কোন সম্ভাবনা নেই। প্রায় ২০০ মিটার মাটির গভীর থেকে যে পানি তুলে সররাহ করা হয় তাও শোধন না করেই উঙঊ মান অনুযায়ী পান যোগ্য হয়। পানি যখন ভূগর্ভস্থ বা ভূপরিস্থ জলাধারে জমা রাখা হয়, তখন ব্যবহারের পূর্বে দূষিত নাও হতে পারে। এই জলাধারগুলি নিয়মিত পরিষ্কার করলে শোধিত পানি নিরাপদ থাকবে। তবে যেহেতু ঘরে সরবরাহকৃত গ্যাসের কোন মিটার নেই, তাই মানুষ পাইপের পানি ফুটিয়েই খেতে থাকবে। উন্নত পৃথিবীর কোথায়ও এই ধরনের অভ্যাস দেখা যায় না।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">প্রতিকারের ব্যবস্থা<br />\r\nযে কোন প্রতিকূল পরিস্থিতিই থাক করণীয় পথ থাকে, তবে তা&rsquo; করতে নাগরিক ও সরকারের বোধসম্পন্ন এবং দায়িত্বশীল হতে হয়। নাগরিকের দায়িত্ববোধ প্রকাশ পায় তার সচেতনতা, রুচি, মুক্তমন এবং সহমর্মিতা থেকে। সরকারের দায়িত্ববোধ প্রকাশ পায় দূরদৃষ্টিতা, স্বচ্ছতা, যথার্থতা ও দ্রুততার সাথে কাজ করার মাধ্যমে।<br />\r\n১) নিষ্কাশন ব্যবস্থাপনা।- ঢাকা নগরীর নিষ্কাশন ব্যবস্থা ২০০০ সালের পানি উন্নয়ন বোর্ড আইন অনুযায়ী অনতিবিলম্বে পানি উন্নয়ন বোর্ডের হাতে ছেড়ে দেয়া হোক। পানি উন্নয়ন বোর্ড এর অধীনস্থ ডিজাইন দপ্তর থেকে খালগুলির প্রয়োজনীয় ডিজাইন প্রস্তুত করে ঐ অনুযায়ী কেটে তা&rsquo; সংরক্ষণ করবে। পানি উন্নয়ন বোর্ডের দেয়া প্লান অনুযায়ী খালগুলির জন্য প্রয়োজনীয় জমি অধিগ্রহণ করে তা&rsquo; দখলমুক্ত করে দিতে হবে।<br />\r\n২) জলাভূমি সংরক্ষণ।- রাজধানী উন্নয়ন কর্তৃপক্ষের &lsquo;ডিটেইলড এরিয়া প্লান&rsquo; সিএস ম্যাপ অনুযায়ী অবিলম্বে পুনর্বিন্যাস করতে হবে, ঢাকার পূর্বদিকের বাড্ডার জলাভূমিগুলিকে অক্ষত রাখতে হবে। বালু নদীর তীর দিয়ে গ্রামের উপর কেটে লেক তৈরির প্লান বাতিল করতে হবে। পানি উন্নয়ন বোর্ড, সিটি কর্পোরেশন ও রাজউক ডেপুটি কমিশনারকে সহযোগিতা করে জলাভূমি ও উদ্যানগুলির সীমানা করে দেবে।<br />\r\n৩) নদীর প্রবাহপথ উদ্ধার।- ভূমি মন্ত্রণালয় ডেপুটি কমিশনারদের মাধ্যমে নদী ও জলাভূমিগুলির সীমানা হাইকোর্টের নির্দেশ অনুযায়ী সিএস ম্যাপের ভিত্তিতে নির্ধারণ করে পিলার পুঁতে দেবে। পানি উন্নয়ন বোর্ড সারাবছর নদীগুলির স্বাভাবিক প্রবাহ অপ্রতিহত রাখার জন্য প্রয়োজনীয় প্রসারতার সীমা দেখিয়ে দেবে।<br />\r\n৪) কঠিন বর্জ্য ব্যবস্থাপনা।- কঠিন বর্জ্য পুড়িয়ে বিদ্যুত উৎপাদন করার লক্ষে দু&rsquo; তিনটি প্লান্ট বসান। বিভিন্ন ধরনের কঠিন বর্জ্য যথা, প্লাস্টিক, কাঁচ, ধাতু, কাগজ ও রান্নাঘরের বর্জ্য উৎস থেকেই আলাদা ভাবে সংগ্রহ করে কাঁচামাল হিসাবে পুনঃপ্রক্রিয়ার মাধ্যমে ব্যবহার করা হোক। বেসরকারী পর্যায়ে ইঁরষফ, ঙহি ্ ঙঢ়বৎধঃব পদ্ধতিতে এই ধরনের পরিবেশ উন্নয়ন প্রকল্পগুলি বাস্তবায়ন করার ব্যাপারে উৎসাহ দিতে হবে।<br />\r\n৫) তরল বর্জ্য ব্যবস্থাপনা।- অপঃরাধঃবফ ঝষঁফমব ঞৎবধঃসবহঃ পদ্ধতিতে বেশ কয়েকটি পয়ঃবর্জ্য শোধন কারখানা করুন। বেসরকারী পর্যায়ে ইঁরষফ, ঙহি ্ ঙঢ়বৎধঃব পদ্ধতিতে এই ধরনের পরিবেশ উন্নয়ন প্রকল্পগুলি করার ব্যাপারে উৎসাহ দিতে হবে। সকল তরল বর্জ্য ঐসকল কারখানায় পাঠানো বাধ্যতামূলক হবে। অন্যথায় প্রত্যেক বাড়ির গ্যারেজের তলায় ও কারখানায় সেপটিক ট্যাংক করে তাদের বর্জ্য শোধন করতে হবে।<br />\r\nউপরোক্ত ব্যবস্থাগুলি নিলে ঢাকা নগরী দূষণমুক্ত হবে, নদী ও জলাভূমিগুলি প্রাণ ফিরে পাবে। তখন ওয়াসাকে পদ্মা যমুনা থেকে পানি আনতে হবে না, নগরীর পাশেই বুড়িগঙ্গা ও শীতলক্ষা নদী থেকে পানি নিয়ে শোধন করে সরবরাহ করতে পারবে।</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">প্রকৌশলী ম. ইনামুল হক, চেয়ারম্যান, জল পরিবেশ ইন্সটিটিউট<br />\r\nঊসধরষ: সরহধসঁষ@মসধরষ.পড়স. ঈবষষ: ০১৮১৭-১২৩৬৩১</span></p>\r\n','',1,15,'2016-01-07 02:59:09',1,'2016-01-07 02:59:29',1,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157169_jsr-11-2.png'),(13,'whose is South Talpatty Island M. Habibur Rahman','whose-is-south-talpatty-island-m.-habibur-rahman','<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Introduction:<br />\r\nSouth Talpatty is a newly formed island in the Bay of Bengal adjoining Bangladesh and India. The two countries have fallen in conflict on the ownership of the island. Both the countries are claiming the island one against the other. The present study is to deal with the practical aspects of asserting claims to this island by the coastal states like Bangladesh and India and in</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">so doing it will critically discuss the international customary law and the law of the sea conventions in the context of the ownership of newly formed island. Finally the study will assess the position of Bangladesh and India as to the assertion of claims to this island.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Bangladesh and India, Their Trends in the Law of the Sea&nbsp;gladesh and India are the signatories to the United Nations Convention on the Law of the Sea 1982.1 Although they have not ratified the convention but they are complying with the convention provisions on the regimes of the territorial sea, the exclusive economic zone (EEZ) and the continental shelf. Both Bangladesh and India are following the conventional 12 n.m. territorial sea,2 200 n.m EEZ3 and continental shelf.4 Bangladesh regards the continental shelf as part of its territory.5 The 1982 Convention does not enable the coastal state to recall the continental shelf as beloning to its territory but Bangladesh by its enactment has substantiated the shelf as part of its territory. From the point of statutory provisions an island emerged in the EEZ/ continental shelf will belong to the state concerned. The purpose of such provisions is to cart sovereign authority not only on the reserves of the EEZ/ continental shelf but also to the island surfaced there. Whatever be the airms and objectives of the statutory provisions, from the conventional point of view, the assertion of claims to the island formed in the EEZ/ continental shelf will be in question.6</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">What differences in the exercise of statutory provisions from the conventional provisions in sea matters are observed, these provisions practically result from self interests. It is out of self interests for the state to substantiate provisions to recall the EEZ/ continental shelf as part of its territory. As far as the conventional and the statutory provisions are concerned, it can fairly be noted that both Bangladesh and India are basically aimed at abiding by the 1982 Convention provisions.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Issues of Conflict<br />\r\nBangladesh and India are states bordering on the Bay of Bengal. As adjacent constal states every sea zone is to be delimited between the two neighboring.The configuration of the of Bangladesh is but the same of India adjoining the Bay of Bengal is convex.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">The delimitation of sea zones involves a conflict of interest between Bangladesh and her neighbors. The issues of conflict arose mainly with India when in 11974 the government of Bangladesh signed production sharing contracts with six oil companies granting there exploration rights of oil and natural gas in her territorial waters in the Bay of Bengal.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">In this respect the Bangladesh line moved toward the south from the real of its land boundary, while the Indian line took a south- easterly direction creating an angle within which lies thousands of square miles of the Bay claimed by each country as its exclusive economic zone (EEZ). It has become the all important matter for Bangladesh and India on which will depend their areas of authority in the seas for example, in the form of the territorial sea, EEZ and the continental shelf. Though negotiations have going on since 1974, Bangladesh and India have not been able to settle the problem of delimitation owing to the concave nature of the Bangladesh coast.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Bangladesh&rsquo;s position is that no rigid principle can be applied in the present case. The basic guideline in equity. India, on the other hand, attempts to apply the principle of &lsquo;equidistance&rsquo; in delimiting the boundary ignoring the physical features of the coast.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">The rivers of Bangladesh and India flew from the highest mountain in the world the Himalayas, and carry with them a colossal discharge of sits to the Bay. This activity, together with heawy monsoon rainfalls, cyclonic storms and tidal surges, have contributed process of and inland and at the month of the rivers. These are a number of government in the of Bangladesh rives, the larger ones seing shabazpur, North, South and Sandwip. Rivers are the greatest natural asset of Bangladesh and they drain area of 45,000 square miles. Whey provide a cheap and conversant from of further a ofand bring in or carry away 24 billion tons of The coast of Bangladesh, which is 310 miles long, is broken and unstable.8</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Bangladesh is effectively a geographically disadvantaged state. By all accepted criteria, it is one of the poorest countries in the world being one of the 49 countries listed in the lowest Income category. In fact, it is considered by the UN as one of 45 countries which are most seriously affected by adverse economic conditions and it had been described as an &lsquo;International basket case.9</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">The Ganges and Brahmaputra Rives flow into the Bay of Bengal where they share a common delta. The fact is an important matter as far as Bangladesh and India are concerned because of its relevance to a delimitation of the continental shelf According to Shepard:10</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">This plain is subject to excessive flooding by storm surges, cussing great loss of life to fishing population living on the higher sand bars. The tides scour out deep and produce much overlapping of sediment on the inside. Huge quantities of sediment are brought in by the rivers from the Himalayas and other high mountain ranges to the north. Many of the in the flood plain have been. The condimental shelf/margin the Bangladesh coast and ends near Sir Lanka and Andaman Islands (India). Thus it is a of concern and importance not only to Bangladesh and India but also to Sri Lanka, and indeed Burma. As neighboring states, they have an interest in the delimitation of the sea zones in the Bay, but this has been made extremely difficult by the peculiar topography of the Bay. In Shemed&rsquo;s words:11</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">There appears to the no place in the deep ocean where the tomography has been so influenced by deposition coming from the land as in the Bay of Bengal. The gently sloping plan extending from the slope base off the great Ganges-Brahmaputra Delta to the 5000 m contour for south of is Further, they Bay is neither a closed bay nor a bay that first the semicircle criterion.12 It is not a historic by and therefore no coastal state can claim territorial rights to its.13</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">The geological features of the Bangladesh coastline create problems at the outset for the fixing of the baselines from which the maritime zones are measured. The results of sedimentary deposits, monsoon rains, cyclonic storms and tidal surges are as follows: the Ganges- Brahmaputra delta is such that no stable water line exists landward or seaward; the continual process of alluvion and sedimentation forms and banks, and the water is so shallow as to be non- navigable by other than small boats; the navigable channels through the mud banks are constantly changing and require soundings, so that they pertain to the character of river mounts and inland waters. Nevertheless, Bangladesh and India must embark on zonal delimitation as adjacent states off the coasts of Bangladesh on the one hand and west Bengal and (India) on the other. Delimitation must also take place between them as opposite states in respect of the Andaman group of islands.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Besides the topographical and geographical features which have been briefly mentioned, new island have formed in the coastal bay. For example, the island of South Talpatty (New Moore) has recently emerged in the estuary of the Hardboards (which borders Bangladesh and India) and the rivers. It is believed that thus island, known as South Talpatty by Bangladesh and New Moore by India, was formed after the and tidal bore of 1970, it being new land rising initially as a14 It has a U-shaped formation with the elongated towards the north. In 1978, it had an approximate area at low tide of two square miles and it has probably grown in size since then. Although it is uninhabited, fisherman from Bangladesh mainland have been observed there during the dry season.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">To be expected, both countries claim that the island forms part of their respective territories. The Indian authorities claim to have notified the British Admiralty about its location in 1971, during the period that the people of Bangladesh were engaged in their struggle for independence. On achieving independence, Bangladesh was faced with the equally challenging task of rehabilitation and national reconstructions. At no time during this period did the Indian government specifically draw to the attentions of the Bangladesh government their claim upon this island as required under international Law and practice despite the close and friendly relations existing between the two countries. Bangladesh lays claim to this island on the assumption that the midstream of the border river Haribhanga flows to the west of the island. While India claim it of the assumption that the midstream flows to the east of the island. This actually has taken place because of having no fixed sea boundary between the two countries. The boundary of the territorial sea in this area is known to be the midstream of the border river Haribhanga. It is to be noted that each of the two countries claim the island as lying in its territorial sea.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">New-Born Island: A Deal of Conflict<br />\r\nIslands may emerge near and off a coastal state. They may be size. The presence of islands between opposite and adjacent states creates problems in the delimitation of maritime zones between these states. This problem of delimitation may now be solved by reference to the 1982 Convention. But unfortunately, the Convention does not deal with the ownership of islands, even when they straddle two different maritime zones.15</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Both Article 10 of the Convention of the Territorial Sea and the Contiguous Zone16 and Article 1(b) of the convention on the continental shelf 17 deal with the regime of islands. They provide a definition for &ldquo;Island&rdquo; and deal with the relationship of islands to their territorial sea and continental shelf. Their approach was adopted by the 1982 convention and Article 121(1) defines an island as &ldquo;a naturally formed area of land, surrounded by water, which is above water at high tide&rdquo;. The 1962 convention goes one step further by providing islands with and EEZ. In other words, an island may claim a continental shelf and an EEZ.18 On the other hand, restrictions have been&nbsp;n formation which are claim a continental shelf and EEZ, they must show that they are able to human habitation or economic life of their own&rdquo;19 Be that as it may, the 1982 convention cannot be regarded as having made much development in the regime for islands.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Whose is the island if it is formed in the EEZ or on the continental shelf? Neither the regime of the EEZ nor of the continental shelf, nor of the islands can answer to this question. If the coastal state is only entitled to ownership of islands which emerge in the territorial sea, then islands emerging in the EEZ or on the continental shelf will be as if they were in the high seas.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">No state then can claim islands which have so emerged unless it can occupy them effecuvety. It signifies that the position of the coastal state concerned is equal to that of any other state.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">The rights of the coastal state over the continental shelf do not depend on occupation, effective or notional. 20 But, if an island which has surfaced in the continental shelf were occupied by a foreign state that stare would have sovereign rights under the regime of the continental shelf. The coastal state could not claim such right unless the island was acquired by &ldquo;occupation&rdquo;. For such islands. It seems that the &ldquo;occupation&rdquo;<br />\r\ncriterion is applicable in the of the continental shelf.<br />\r\nBefore the emergence of the continental shelf doctrine, the coastal state was entitled to safeguard its interests in the territorial sea. But, from the economic point of view, the coastal state can safeguard its interests in the EEZ and the continental shelf better than in the territorial sea. according to the 1982 convention, the coastal state is entitled to exercise sovereign rights over the resources in the EEZ and the continental shelf. In this regard, no state can overlap the coastal states&rsquo; claims<br />\r\n.<br />\r\nIt would not then be rational to substantiate the claims of a foreign state over islands formed in the EEZ or the continental shelf against the coastal state. If the &ldquo;occupation&rdquo; criterion is the deciding factor, the ownership of the islands would be subject to physical force. It would therefore be easy for the big and strong state to assert claims to such islands by physical force against its small, weak neighbor.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Neither the continental shelf convention nor the 1982 convention states whether it is possible for the coastal state to assert claims to the continental shelf or the EEZ as belonging to its territory. However, by implication, 21 it seems that the coastal state may exercise sovereign rights over the resources only. In other words, neither the Continental shelf convention nor the 1982 convention has given authority to the coastal state to regard the continental shelf or the EEZ as part of its sovereign territory. On the other hand, the convention has not adopted any specific provisions against the assertion of claims to the continental shelf and the EEZ as belonging to the territory of the coastal state. Failure to make specific provisions leads to the assumption than measures, if taken by the coastal state to assert claims to the continental shelf and the EEZ as belonging to its territory will not be a violation of the convention.<br />\r\nSo far as is known to this author, 14 states are currently claiming a 200 mile territorial sea.22 Speaking generally, the continental shelf within the 200 mile EEZ cannot be regarded as belonging to the territory of the coastal state. But, If the 200 mile zone is treated as the territorial sea. It is to argue that the shelf lying this zone is a part of the costal state. In fact, the regime of the continental shelf for then states which claim a 200 mile territorial sea can be applicable if there exists either the shelf or slope or rise beyond this zone.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Furthermore, sovereignty over the continental shelf has been claimed explicitly in some proclamations, such as these of Australia (1955), Bahamas (1948), Chile (1947), Costa Rica (1948), Honduras (1950), Jamaica (1948), Korea (1952), Mexico (1945), Nicaragua (1950), Pakistan (1950), and the British Protected Arab Sheikhdoms (1949) All those proclamations aimed to assert claims to the sea-bed and subsoil as part of the coastal state.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">If an island emerged in a maritime zone on these areas of continental shelf, it may be assumed that the coastal state would regard the island as a part of its territory. Otherwise the coastal state would claim it through the criterion of &ldquo;occupation&rdquo; as applicable to the acquiring of territory. In this context, it is submitted that the convention provisions would not be effective to require the claim of the coastal state over the island to be disregarded. The fact is that the 1982 convention fails to decide the status of a new-born island which has surfaced beyond the territorial sea.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Since the newly formed south Talpatty Island is claimed both by Bangladesh and India as lying in the territorial sea, there no question of the application of &ldquo;Occupation&rdquo; for its acquisition. The acquisition of this island for the two countries depends on the demarcation line between the two countries. If the sea zone in this area is delimited the problem on the acquisition of this island will be automatically solved. The island if lies in the territorial sea of Bangladesh will belong to this state and if in the territorial sea of India will belong to this state.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">If the island enlarge and extends beyond the territorial sea, the enlarged part will belong to the state in whose territorial sea the island straddles. The principle of accretion to the acquisition of territory will here be applicable.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">South Talpatty Island Dispute: How and why to Get Settled?<br />\r\nThe South Talpatty Island has been a big problem both for Bangladesh and India. It is the aim of each country to achieve the island. It is natural for everybody to speak for its own interests. The assumption is applicable to national and international level. As to the South Talpatty Island, Bangladesh and India cannot be regarded exception to this assumption. Each of the two countries appears adamant to the ownership of the island. Whatever be the legal position of the island, each country wants the island as belonging to its territory. There is nothing to contradict the view that Bangladesh cannot face India while ownership of the island is subject to physical force.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Whatever importance and whatever enthusiasm exist as to the ownership of the island, it is neither Bangladesh not India can say to have owned the island. Legally the ownership of the island is to be effected by the boundary of the territorial sea between the two countries. Since the island situates in the territorial sea, it is therefore, the boundary of this sea would solve the problem as to its ownership the countries. Unquestionably, it is the boundary of the territorial sea which must be treated to be the only ways and means for the settlement of this disputes between Bangladesh and India. Until the South Talpatty Island is demarcated by the boundary of the territorial sea, there is no scope for any state to assert claims to this island. So long the boundary of the territorial sea will not be determined, assertion of claims to the island will not be effective.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">As each country is claiming the island lying in its territorial sea, there is then no cogent argument to the assertion of claims to the island without the boundary of the territorial sea between Bangladesh and India. Whatever be the claims and counterclaims by Bangladesh and India to the South Talpatty Island all confusions would be removed while the territorial sea boundary will be delimited. As a practical consideration, the South Talpatty Island cannot be recalled to be too complicated, because with the delimitations of the territorial sea between the two countries there will be no scope to raise places to the acquiring of this island one against the other.<br />\r\nSo far Bangladesh and India are going through negotiations for the settlement of maritime boundary disputes.24 Until now, they have not been successful. If this situation continues and if any formation continues to come up in the Bay of Bengal bordering the two countries the situation will be very tough and complicated for its settlement. In other words. It will be a big problem for the settlement of disputes between them.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Since islands are emerging in the maritime zones, it can be assumed that with the passage of time Bangladesh and India will have to face much more complicated problems as to the settlement of maritime boundary disputes. It is a fact that law is not merely a convenient device for settlement of disputes. The law is something that can be an effective instrument at a crisis and left out of account at other times. It is useful as a means of settlement only when, and so far as, a society has accepted the rule of law as its way of life.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">If Bangladesh and India fail to settle the dispute it may be required for the United Nations to take measures for the settlement of disputes in order to maintain international peace and security. But questions arise whether the matter will come to the notice of the United Nations. According to Indian&rsquo;s notification, disputes arising out of the maritime territory will be treated as lying in the internal jurisdiction of India.25 It signifies that no third party settlement can be made effective so long India does not consent to it or is compelled to do the same. Then the question of the third party settlement will arise when the situation results in the breach of international peace and security.26 If so, the situation will be subjected to the purview of the United Nations.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Whatever be the problem as to the settlement of maritime disputes, there are difficulties for the small and weak country like Bangladesh to bring the matter to the notice of the United Nations against her big and strong counterpart like India. If the maritime boundary dispute Between Bangladesh and India is not settled, it may draw the attention of other states who will not only criticize but also blame the big and strong party-India. The reason is that there arises a moral obligation of the states in disputes to take initiatives for the settlement of disputes. This will be applicable both to Bangladesh and India in regard to the maritime boundary dispute. If it is released that a proposal of joint survey is declined, the state declining such survey will be assumed as creating bar for the settlement of disputes.27 It seems that both Bangladesh and India should come together for the settlement of maritime disputes.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Reflecting Thoughts<br />\r\nAs acquainted with the newly formed offshore South Talpatty Island, neither Bangladesh nor India requires to assert claims to it by &ldquo;occupation&rdquo;. So long an island locates in the territorial sea it will automatically belong to the coastal state. Beyond the territorial sea an island cannot be claimed as the part of the coastal state. In order to assert claims to this island it is required to get it by &ldquo;occupation&rdquo;28 In the case of South Talpatty Island the &ldquo;Occupation&rdquo; criterion will not be applicable. Differences between Bangladesh and India on the Island will automatically be removed if the sea boundary between the countries is demarcated. While there will be the boundary there will be no scope to rise pleas on the assertion of claims to the island by a state against the other. because, each state will have to be limited on the claims of the island so the boundary of the territorial sea.<br />\r\nDespite this position of the two countries do not come forward to settle the South Talpatty problem, it will express the want of willingness to its settlement. If these countries do not seriously take the problem for settlement, it will as time passes, be more and more complicated.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">While an island emerges in a state&rsquo;s territorial sea but another state acquires it, the latter state will regarded to have intruded the territory of the former state. In international Law, the latter will be called an &lsquo;aggressor&rsquo;. Be that as it may, Bangladesh and India are friendly to each other. They will not ignore the reality of the problem on the acquiring of South Talpatty Island. From the legal point of view, there is no scope to declare this island to be acquired out of the &ldquo;occupation&rdquo; criterion. This situation has made the two countries not to declare to have the island acquired. The situation will prevail equally so long the is and will exist in the territorial sea.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">As a practical consideration, no state should be careless about the ownership of the island though lying in its territorial sea. Because of its carelessness, it will not be difficult for the foreign state to acquire such islands by &ldquo;dereliction&rdquo; criterion on the authority of which is state can acquire territory of another state if the latter state abandons it.29</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">In spite of the existing legal position of South Talpatty Island if either Bangladesh or India declares to have acquired the island, it would not be an act done through the principles of law, justice and equity. It would rather be an act of physical force-the weapon to the acquiring of right by might. But while a state takes over the island, there will be no legal sanction of that state. Though the island is in the acquisition of the state, its ownership will not pass on it is substantiated the territorial sea boundary. If the territorial boundary does not permit the state to acquire it, there should be no doubt that the ownership of the island will on the state as permitted by this boundary. This proposition must be applicable to the ownership of South Talpatty Island by Bangladesh and India.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">1. Un Doc. A/Cone/62/122, 7, Oct. 1982 hereafter as the 1982 convention.<br />\r\n2. n.m denotes nautical mile. See the 1982 converters, Art 3 and Territorial waters and Maritime Zone Act (Bangladesh), 1974, S3, and the Ministry of Foreign Affairs Circular No. LT-1/3/74 of the Bangladesh Gaztte, Extra., 16 April 1974, The Territorial Waters, Continental Shelf, Exclusive Economic Zone and other maritime Zone act (India), 1976 of the Gazette of India Extraordinary of 26 August 1976, SL(3)<br />\r\n3. See the 1982 Convention, Act 56, Bangladesh Act, S5 and India Act, S7(1)<br />\r\n4. See the 1982 Convention, Act 76, Bangladesh Act, S7 and India Act, S6(1)<br />\r\n5. According to the Bangladesh Act S7(b) the seabed and subsoil of the Submarine areas adjacent to the coasts of any island, rock or any island, rock or any composite group thereof constituting part of the territory of Bangladesh.<br />\r\n6. Infra n 21<br />\r\n7. GT Korian, Encyclopedia of the Third world (Mansell, 1979) i, 106<br />\r\n8. Danzic, &ldquo;A Funny thing Happened to Common Heritage on the Way to the Sea&rdquo; (1975) 12 Sea Diego L, Rev P. 661<br />\r\n9. GI Kurian P. 110<br />\r\n10. FP Shepard, Submarine Geology (3rd ed, Harper &amp; Row, 1973) P. 168<br />\r\n11. Ibid P. 397<br />\r\n12. According to Art 10(2) of the 1982 convention: &ldquo;a bay is a well-marked indentation whose perpetration is in such proportion to the width of its mouth as to contain land-locked waters and constitute more than a mere ourvature of the coast. As indentation shall not, however, be regarded as a bay unless its area is as large as, or larger than, that of the whose diameter is a line drawn the mouth of that indentation&rdquo;.<br />\r\n13. CJ Colombos, The International Law of the Sea(6th ed, Longmans, 1972) p. 185<br />\r\n14. Keesing&rsquo;s Contemporary Archives, 18 Sept. 1981, P. 31090.<br />\r\n15. See the 1982 Convention, Art 121<br />\r\n16. Un Doc A/CONF/13/L 52 (1958)<br />\r\n17. Un Doc A/CONF/13/L 55 (1958)<br />\r\n18. See the 1982 Convention, Art 121<br />\r\n19. Ibid.<br />\r\n20. See the 1982 Convention, Art 77(3)<br />\r\n21. See the Continental Shelf Convention, (1958), Arts, 2, 3; ;the 1982 Convention, Arts 77, 78<br />\r\n22. E.g. Argentina (1977), Benin (1976), Brazil (1970), Cameroon (1974), Colombia (1975), Congo (1977), Ecuador (1966), EL Salvador (1976), Ghana (1977), Guinea (1976), Liberia (1977), Peru (1947), Sierra Leone (1971), Somalia (1972), See Robet B Krueger and Myron H Nordquist, The Evolution of the 200 mile Exclusive Economic Zone: State Practices in the Pacific Basin&rdquo; (1979-80), 19 Va J int. Law PP-390-399 and (1969) 8 ILM pp. 516-554.<br />\r\n23. Abu Dhabi, Ajman, Bahrain, Dubai, Kuwait, Ras-el-Khaimath, Sarjah and Ummal Qaywaya.<br />\r\n24. See N Habibur Rahman, &ldquo;Delimitation of Maritime Boundaries A survey of problems in the Bangladesh case (1984)24/12 Asian Survey pp.1308-10<br />\r\n25. That is to say. the disputes with India concerning or relating to (a) &ldquo;The status of its territory or the modification or delimitation of its frontiers or any other</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">matter concerning boundaries: (b) the territorial sea the continental shelf and the margin, the exclusive fishery zone, the exclusive economic zone, and other zones of national maritime jurisdiction including for regulation and control of marine pollution and the conduct of scientific research by foreign vessels: (c) the condition and status of its islands, bays are gulfs and that of the bays and gulfs that or historical reasons to it; (d) the airspace superjacent to its land and maritime territory and (e) the determination and delimitation of its maritime boundaries&rdquo; are subject to the internal jurisdiction of the United Nations.<br />\r\n26. Sea Arts 1 and 24 of the Charter of the United Nations.<br />\r\n27. M. habibur Rahman, &ldquo;The Law of the Sea and Settlement of Maritime Disputes&rdquo;, (1984) 5/1 Journal of the Bangladesh Institute of International and Strategic Studies (BIISS Journal) (Dhaka) p-67-96<br />\r\n28. See Lauterpacht (ed), Oppenheim&rsquo;s International Law-A Treatise ( 1963) Vol. 1 Longman) p.565<br />\r\n29. Ibid pp. 579-80</span></span></p>\r\n','',1,15,'2016-01-07 03:01:20',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157280_jsr-12-1.png'),(14,'সম্পাদকীয়: ডিসেম্বর সংখ্যা','সম্পাদকীয়:-ডিসেম্বর-সংখ্যা','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সম্পদ রক্ষার রাজনীতি ছাড়া শুধু সামাজিক সংগঠনের কিছু বিক্ষিপ্ত বিচ্ছিন্ন কর্মসূচির দ্বারা জাতীয় সম্পদ রক্ষা করা যাবে না। দরকার এমন এক সরকার প্রতিষ্ঠার যে সরকার জাতীয় সম্পদের সংরক্ষণ, বণ্টন ও সুস্থ ব্যবস্থাপনা সহ পরনির্ভরশীলতা, আমরা পারব না এসব হীনমন্যতার ঊর্ধ্বে উঠে নিজস্ব সক্ষমতা বৃদ্ধির উদ্যোগ নিয়ে সমৃদ্ধশালী এমন এক বাংলাদেশ গড়বে যার মূল চেতনা হবে মুক্তিযুদ্ধ।<br />\r\nআমরা জানি সর্বগ্রাসী দুর্নীতি, লুটতরাজ, রাষ্ট্রীয় সম্পদ হরণ, নিজেদের সম্পদ বিদেশী বশংবদের হাতে কমিশনের বিনিময়ে তুলে দেওয়া, যা মুক্তিযুদ্ধের চেতনার সাথে সাংঘর্ষিক। অথচ স্বাধীনতার পর থেকে বাংলাদেশে রাজনীতির নামে এসবই চলছে।<br />\r\nসুন্দরবন (রামপাল বিদ্যুৎ কেন্দ্রের নামে ধ্বংসের প্রক্রিয়াধীন), পানি, পানির ন্যায্য হিস্যা (যা ভারতের সাথে অন্যান্য অমীমাংসিত বিষয়ের মতই অমীমাংসিত), ফুলবাড়ীর কয়লা (এশিয়া এনার্জির সাথে বিতর্কিত অংশিদারিত্ব চুক্তিতে চুক্তিবদ্ধ, এ নিয়ে জনগণের সংগ্রাম অব্যাহত আছে), সমুদ্রের সম্পদ, ভূমি (যা ভূমি দস্যুদের অধিকারে), নদী ও নদীর পাড় (ঢাকার আশেপাশের নদীর মতই সারা বাংলাদেশে রাজনৈতিক দুর্বৃত্তের দ্বারা দখলকৃত), খেলারমাঠ (ধানমন্ডির শেখ জামাল মাঠসহ সারা দেশে যা অহরহ ঘটছে), বাংলা ভাষা (আমাদের আদর্শিক ও চেতনার বিষয় হওয়া সত্ত্বেও ইংরেজী শেখার নামে রাষ্ট্রীয় ভাবে এই ভাষাই আজ চূড়ান্ত অবহেলার শিকার), অবহেলার শিকার সঠিক রাষ্ট্র গঠন প্রক্রিয়াটি। দরকার উত্তরণের।<br />\r\nপ্রগতি বনাম ধর্মান্ধতার চূড়ান্ত সমাধান ছাড়া, লুটেরাদের পতন ছাড়া, বিপ্লবী সরকার প্রতিষ্ঠা ছাড়া (সমাজতান্ত্রিক বিপ্লব হলে ভালো, জাতীয়তাবাদী বিপ্লবও হতে পারে), বিদ্যমান রাজনীতি ব্যবস্থা উচ্ছেদ ছাড়া জাতীয় সম্পদের স্খলন ঠেকানো যাবে না।<br />\r\nআসুন মুক্তিযুদ্ধের চেতনাকে ধারণ করে জাতীয় সম্পদ রক্ষার নতুন রাজনীতি করি।</span></p>\r\n','',1,14,'2016-01-07 03:02:54',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157374_d14-cover.gif'),(15,'জ্বালানি সম্পদ: সরকারের রোড ম্যাপ উল্টোপথে। আনু মুহাম্মদ','জ্বালানি-সম্পদ:-সরকারের-রোড-ম্যাপ-উল্টোপথে।-আনু-মুহাম্মদ','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কোটি কোটি বছরের তৈরি হলেও তেল, গ্যাস, কয়লাসহ খনিজ জ্বালানি সম্পদের ব্যাপক ব্যবহার মানুষ শুরু করেছে মাত্র কয়েকশ বছর আগে। জানাশোনা, দক্ষতা কোথাও আকাশ থেকে পড়েনি। মানুষ ক্রমেই এগুলো অনুসন্ধান ও উত্তোলনের ক্ষমতা অর্জন করেছে। ইউরোপের শিল্পবিপ্লবে কয়লা ছিল প্রাণ। উনিশ শতকে জ্বালানি তেল আবিষ্কার ও উত্তোলন শুরুর পর এর ওপর ভর করেই বিশ্ব অর্থনীতি বিস্তৃত হয়েছে। প্রাকৃতিক গ্যাসের প্রাপ্তি ও ব্যবহার বৃদ্ধি পেয়েছে আরও পরে। বিশ্বজুড়ে এসব সম্পদ ছড়িয়ে থাকলেও এর মালিকানা ও কর্তৃত্বের অসম বিন্যাস শক্তি ব্যবহারের বৈষম্য তৈরি করেছে, যুদ্ধ-সহিংসতায় সভ্যতা বিপর্যস্ত হয়েছে। মধ্যপ্রাচ্যে দখল, গণহত্যা আর সংঘাতে দেশের পর দেশ যে ছিন্নভিন্ন, তার অন্যতম কারণ এই সম্পদ দখলে নিতে সা&curren;্রাজ্যবাদী তৎপরতা। সীমিত ও নবায়নযোগ্য নয় বলেই খনিজ সম্পদে দখল থাকলে ব্যবসায়িক সংস্থাগুলোর মুনাফা বেশুমার। দখল ও মুনাফাকেন্দ্রিক উন্নয়নে জীবাশ্ম জ্বালানির অপরিমিত ব্যবহারে বিশ্ব-অস্তিত্ব এখন বিপন্ন। এই পরিপ্রেক্ষিতে গত কয়েক দশকে সূর্যরশ্মি, বাতাস, এমনকি বর্জ্য পদার্থও বিদ্যুৎ উৎপাদনের প্রাথমিক জ্বালানি হিসেবে ক্রমেই বেশি মনোযোগ আকর্ষণ করছে। জ্ঞান-বিজ্ঞানের বিকাশের সঙ্গে সঙ্গে এটা নিশ্চিতভাবে বলা যায়, বিশ্বের জন্য যেমন, বাংলাদেশের জন্যও তেমনই এই অসীম নবায়নযোগ্য জ্বালানিই ভবিষ্যৎ।<br />\r\nউর্বর আবাদি জমি ও পানিসম্পদ ছাড়াও উল্লিখিত উভয় ধরনের জ্বালানি সম্পদের দিক থেকে বাংলাদেশের অনেক শক্তির দিক আছে। তবু শাসকদের জনবিরোধী নীতি ও দুর্নীতির কারণে সম্পদহীন, গরিব, অক্ষম হিসেবেই বাংলাদেশ এখনো বিশ্বে পরিচিত। বাংলাদেশের পূর্ব দিক প্রধানত প্রাকৃতিক গ্যাসে সমৃদ্ধ, তেল পাওয়ার সম্ভাবনাও যথেষ্ট। উত্তরাঞ্চলে আছে অনেক কয়লাখনি। আর দক্ষিণে সমুদ্র। এই সমুদ্রে বিপুল সম্পদের মধ্যে প্রাণিজ নবায়নযোগ্য সম্পদ আছে, আবার তেল-গ্যাসের মতো অনবায়নযোগ্য সম্পদও আছে। আন্তর্জাতিক বিশেষজ্ঞরা বলেন, বঙ্গোপসাগরে বিশাল তেল-গ্যাসের ভাণ্ডার আছে। তাঁদের মত বিবেচনায় নিলে বলতে হয়, আগামী শত বছরের জ্বালানি চাহিদা পূরণের মতো সম্পদ বঙ্গোপসাগরে আছে।<br />\r\nকিন্তু সম্পদ থাকলেই আর তা উত্তোলন করলেই একটি দেশ যদি উন্নত হতো, তাহলে বর্তমান বিশ্বে আফ্রিকার দেশগুলোসবচেয়ে উন্নত থাকত। সিঙ্গাপুর, জাপানসহ বেশ কয়েকটি দেশ আছে, যারা জ্বালানি সম্পদ খুব কম কিংবা না থাকা সত্ত্বেও জ্বালানি ও বিদ্যুৎসংকটের টেকসই সমাধান করেছে। আবার মিয়ানমার, নাইজেরিয়া, ঘানা, ত্রিনিদাদসহ বহু দেশ আছে, যারা বিপুল সম্পদ থাকা সত্ত্বেও দারিদ্র্য, বিদ্যুতের সংকট ও সামগ্রিক অনুন্নয়নের বিষচক্রে আবদ্ধ। এসব দেশে বিদেশি বিনিয়োগ হয়েছে অনেক, বহুজাতির কোম্পানিরও ভিড় আছে। কিন্তু দারিদ্র্য, সহিংসতা আর দুর্নীতির বিষচক্রে আটক থেকে এসব দেশ এখন অভিশপ্ত সম্পদের দেশ হিসেবে বিবেচিত। বাংলাদেশকেও এই মডেলে নিয়ে যাওয়ার জন্য প্রায় তিন দশক ধরে বিশ্বব্যাংক, এডিবি, বহুজাতিক কোম্পানি, দেশি কমিশন এজেন্ট, দুর্নীতিবাজ মন্ত্রী, আমলা, কনসালট্যান্টরা ব্যস্ত। জনপ্রতিরোধের কারণে তাঁদের সব ইচ্ছা পূরণ না হলেও দুষ্ট বিশ্বজোট দখল ও লুণ্ঠনের নানা প্রকল্প নিয়ে এখনো সক্রিয়। বহু দেশের অভিজ্ঞতা, যদি কমিশনভোগী আর দুর্নীতিবাজেরা দেশ শাসন করেন, তাহলে দেশের সম্পদই হয়ে দাঁড়ায় সর্বজনের বিপদের কারণ।<br />\r\nবিশ্বজুড়ে জ্বালানি সম্পদে মালিকানার চিত্র পাল্টেছে। সত্তরের দশক পর্যন্ত এর ওপর একচ্ছত্র অধিপত্য ছিল সেভেন সিস্টার্স বলে পরিচিত কতিপয় বহুজাতিক কোম্পানি এবং সেই সূত্রে আমেরিকা, ব্রিটেনসহ সা&curren;্রাজ্যবাদী দেশগুলোর। তখন পর্যন্ত তাদের দখলে ছিল বিশ্বের শতকরা ৮৫ ভাগ তেলসম্পদ। গত চার দশকে এশিয়া, আফ্রিকা, লাতিন আমেরিকা, ইউরোপের বহু দেশ তাদের জাতীয় সংস্থা বিকশিত করেছে। খনিজ সম্পদের ওপর তাদের জাতীয় সংস্থার মালিকানায় উত্তোলনের শতকরা ৬১ ভাগ তাদের নিয়ন্ত্রণে (অয়েল অ্যান্ড গভর্নেন্স, ২০১৪, কেমব্রিজ ইউনিভার্সিটি প্রেস)। ফিন্যান্সিয়াল টাইমস পত্রিকা গত বছর সা&curren;্রাজ্যবাদী বিশ্বের বাইরে নতুন সাত ভগিনীকে চিহ্নিত করেছে, যে গুলো সবই রাষ্ট্রীয় প্রতিষ্ঠান। এগুলো হলো: সৌদি আরামকো, গাজপ্রম (রাশিয়া), চীন কোম্পানি, ন্যাশনাল ইরানিয়ান অয়েল কোম্পানি, পেট্রোনাস (মালয়েশিয়া)। ইউরোপ বিবেচনায় নরওয়ের স্টেট অয়েল অবশ্যই এই তালিকার গুরুত্বপূর্ণ সদস্য হবে। নরওয়ের কল্যাণমূলক অর্থনীতি এই তেলসম্পদের অর্থেই পরিচালিত হচ্ছে।<br />\r\nবাংলাদেশের শাসকদের রোডম্যাপ উল্টো দিকে। পেট্রোবাংলা উপরিউক্ত পেট্রোনাস আর স্টেট অয়েলের সমবয়সী হলেও প্রতিষ্ঠার ৪০ বছর পরও এই প্রতিষ্ঠানকে নিজের সক্ষম ভিত্তি দাঁড় করাতে দেওয়া হয়নি। নির্লজ্জের মতো অক্ষমতার অজুহাতে, পারি না, পারব না, এই আওয়াজের মাধ্যমে লুণ্ঠন ও দুর্নীতির প্রকল্প জায়েজ করা হয়েছে, হচ্ছে।<br />\r\nবিশাল সম্ভাবনার বঙ্গোপসাগরের সম্পদ আকর্ষণীয় &lsquo;প্যাকেজে এমনভাবে বিদেশি&rsquo; কোম্পানিকে দেওয়া শুরু হয়েছে যে সে সম্পদ দেশের উন্নয়নে কাজে লাগার পরিবর্তে হয়ে উঠতে যাচ্ছে বিপদ। আগেও পুঁজির অভাবের যক্তি দিয়ে দেশের সম্পদ বিদেশি কোম্পানির হাতে দেওয়া হয়েছে, যার কারণে ১০ থেকে ৩০ গুণ বেশি দামে গ্যাস কিনতে হচ্ছে। যে পরিমাণ পুঁজি নেই বলে এসব চুক্তি করা হয়েছে, তার চেয়ে বহুগুণ বেশি অর্থ বিদেশি কোম্পানির পক্ষে প্রতিবছর ভর্তুকি দিতে হচ্ছে। ঋণগ্রস্ত হচ্ছে অর্থনীতি, বরাবর গ্যাস ও বিদ্যুতের দাম বাড়ানো হচ্ছে এসব ভুল নীতি ও দুর্নীতির কারণেই। এসব কাজে আইন-আদালতের ঊর্ধ্বে থাকার জন্য সরকার প্রণয়ন করেছে দুর্নীতির দায়মুক্তি আইন।<br />\r\nস্থলভাগে মার্কিন কোম্পানি শেভরনের মোট বিনিয়োগ ১ দশমিক ২ বিলিয়ন ডলার বলে সুখ্যাতি শুনি। কিন্তু তাদের কাছে মাগুরছড়ার ধ্বংসযজ্ঞের শুধু গ্যাসের জন্য পাওনা ২ দশমিক ৫ বিলিয়ন ডলার আদায়ের কথা শোনা যায় না; বরং তার কর্তৃত্ব ও সুবিধা বাড়ানো হয়। কানাডীয় কোম্পানি নাইকোর কাছ থেকে টেংরাটিলা ধ্বংসযজ্ঞের জন্য প্রাপ্য সমপরিমাণ ক্ষতিপূরণ আদায় না করে আন্তর্জাতিক আদালতে তাকে মাঠ ছেড়ে দেওয়া হয়। অথচ এই দুটি কোম্পানি বাংলাদেশের যে পরিমাণ গ্যাসসম্পদ নষ্ট করেছে, তা প্রায় দুই বছরে দেশের বিদ্যুৎ উৎপাদনে ব্যবহৃত গ্যাসের সমান।<br />\r\nবাংলাদেশে সম্পদের অভাব নেই, অভাব জনপন্থী রাজনীতির। দুনিয়ার শিক্ষা, যেসব দেশ আজ উন্নয়নের নতুন দিশা তৈরি করছে, তারা নিজস্ব সম্পদের ওপর জাতীয় মালিকানা ও কর্তৃত্ব প্রতিষ্ঠার পথ নিয়েছে, নিজস্ব সক্ষমতা বৃদ্ধিতে মনোযোগ দিয়েছে। প্রকৃত উন্নয়নের দিশা তৈরিতে বাংলাদেশের জন্য তাই এক নম্বর শর্ত হলো, সমুদ্র ও স্থলভাগের গ্যাসসম্পদ শতভাগ জাতীয় মালিকানায় রেখে অনুসন্ধান ও উত্তোলন করতে হবে। যেখানে ঘাটতি আছে, সেখানে প্রয়োজনে সাব-কন্ট্রাক্ট দিয়ে, বিদেশি ও প্রবাসী বিশেষজ্ঞদের কাজে লাগিয়ে ঘাটতি দূর করতে হবে। দ্বিতীয়ত, খনিজ সম্পদ রপ্তানি নিষিদ্ধকরণ আইন করে শতভাগ সম্পদ দেশের অর্থনীতির কাজে লাগানো নিশ্চিত করতে উন্মুক্ত খনি নিষিদ্ধসহ ফুলবাড়ী চুক্তির পূর্ণ বাস্তবায়ন করতে হবে। জাতীয় সংস্থার মাধ্যমে দেশের মাটি, পানি ও জনবসতির উপযোগী পদ্ধতি নির্ধারণ করতে হবে, প্রযুক্তি নিয়ে নিজস্ব গবেষণার ভিত্তি দাঁড় করাতে হবে। নবায়নযোগ্য আবাদি জমি ও পানিসম্পদে সর্বনাশ করে, জননিরাপত্তা ও খাদ্যনিরাপত্তা বিপর্যস্ত করে কোনো প্রকল্প গ্রহণ করা যাবে না। চতুর্থত, কুইক রেন্টালের মতো দুর্নীতিযুক্ত অদক্ষ, অকার্যকর পথে না গিয়ে রাষ্ট্রীয় খাতের বিদ্যুৎ উৎপাদন কেন্দ্র সঠিকভাবে পরিচালনা করতে হবে। পঞ্চমত, সুন্দরবনধ্বংসী বিদ্যুৎ প্রকল্প আর দুর্নীতিকে দায়মুক্তি দেওয়ার আইন বাতিল করে স্বচ্ছতা, জবাবদিহি ও সর্বজনের অংশগ্রহণ নিশ্চিত করার মাধ্যমে বিদ্যুৎ প্রকল্প অনুমোদন করতে হবে। ষষ্ঠত, নবায়নযোগ্য জ্বালানি ভিত্তি বিদ্যুৎ মূল &macr;্রােত হিসেবে দাঁড় করানোর জন্য স্বল্প, মধ্য ও দীর্ঘমেয়াদি বাস্তবায়নের জন্য যথাযথ উদ্যোগ গ্রহণ করতে হবে। সপ্তমত, এসব কাজ যথাযথভাবে সম্পন্ন করতে জাতীয় সক্ষমতা বিকাশের জন্য বিপর্যস্ত জাতীয় প্রতিষ্ঠানগুলোকে পুনরুদ্ধার, বিশ্ববিদ্যালয় ও গবেষণা প্রতিষ্ঠান স্থাপন করতে হবে।<br />\r\nআনু মুহাম্মদ : অধ্যাপক, অর্থনীতি বিভাগ, জাহাঙ্গীরনগর বিশ্বাবদ্যালয়।</span></p>\r\n','',1,15,'2016-01-07 03:04:18',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157458_09.png'),(16,'এটি কার্যত দুর্নীতির দায়মুক্তি আইন| আনু মুহাম্মদ','এটি-কার্যত-দুর্নীতির-দায়মুক্তি-আইন|-আনু-মুহাম্মদ','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সরকার সম্প্রতি &lsquo;বিদ্যুৎ ও জ্বালানির দ্রুত সরবরাহ বৃদ্ধি (বিশেষ) আইন&rsquo;-এর মেয়াদ আরও চার বছর বাড়ানোর উদ্যোগ নিয়েছে। স্বচ্ছতা ও জবাবদিহির পরিপন্থী এই আইনের মেয়াদ বাড়ানোর উদ্যোগ নানা প্রশ্নের জন্ম দিয়েছে। এই প্রসঙ্গে ও সামগ্রিকভাবে দেশের বিদ্যুৎ খাত নিয়ে বিভিন্ন প্রশ্নের উত্তর দিয়েছেন জাতীয় সম্পদ রক্ষা&rsquo;র আন্দোলনের অন্যতম সংগঠক আনু মুহাম্মদ। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : জ্বালানি খাত নিয়ে এ রকম আইনের ঊর্ধ্বের &lsquo;আইন&rsquo; প্রণয়নের প্রয়োজন হলো কেন?<br />\r\nউত্তর : দুর্নীতি ও অনিয়ম করার জন্য বেপরোয়া না হলে এ রকম আইন করার দরকার ছিল না। এই আইন করার আগের, এমনকি এই সরকারের আগের সরকারগুলোর জ্বালানি ও বিদ্যুৎ খাতে দুর্নীতির অনেক ঘটনা সবাই জানেন। কিন্তু এই আইন অনেক বেশি দুর্নীতির পথ তৈরি করেছে, যথেচ্ছাচারের সুযোগ দিয়েছে। &lsquo;বিদ্যুৎ ও জ্বালানির দ্রুত সরবরাহ বৃদ্ধি&rsquo; নয়, এটি কার্যত &lsquo;দ্রুত লুণ্ঠন ও দুর্নীতি দায়মুক্তি আইন&rsquo; ২০১০ সালে বিদ্যুৎ সংকট নিরসনের অজুহাতে আইন প্রণয়নের সময়ই আমরা আশঙ্কা জানিয়েছি যে, এই আইন বিদ্যুৎ সংকটের টেকসই সমাধান তো দেবেই না, উল্টো সীমাহীন দুর্নীতি ও অনিয়মের জন্ম দেবে। পরের বছরগুলোয় এই আশঙ্কা সত্য বলে প্রমাণিত হয়েছে। এই অনিয়মের ধারাবাহিকতা আরও চার বছর চালিয়ে যেতে চায় বলেই সরকার এর মেয়াদ বৃদ্ধি করেছে। দুর্নীতি, অস্বচ্ছতা, গোপন চুক্তি ও সমঝোতার দুষ্টচক্রের গোড়া জ্বালানি মন্ত্রণালয়কে সব আইনি বাধ্যবাধকতার ওপর স্থাপন করে দুর্নীতি আর জাতীয় স্বার্থবিরোধী তৎপরতাকে অবাধ করতে চায় সরকার।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : জ্বালানি খাতে কী ধরনের দুর্নীতিকে রক্ষা করছে এই আইন?<br />\r\nউত্তর : শতকরা ৮০ ভাগ গ্যাস রপ্তানির সুযোগ রেখে সমুদ্রে গ্যাস ব্লক কনোকোফিলিপসকে প্রদান, রেন্টাল ও কুইক রেন্টালমুখী তৎপরতা, কেয়ার্ন এনার্জি ও হেলিবার্টনের সঙ্গে আত্মঘাতী চুক্তি স্বাক্ষর, শেভরন ও নাইকোর কাছ থেকে গ্যাস ধ্বংসের জন্য প্রাপ্য ৫০ হাজার কোটি টাকা আদায়ের উদ্যোগ না নিয়ে শেভরনের জন্য সুবিধা বৃদ্ধি, সমুদ্রসম্পদ বিভিন্ন দেশের মধ্যে ভাগ-বাঁটোয়ারা করা, ফুলবাড়ী চুক্তির পূর্ণ বাস্তবায়ন না করে এশিয়া এনার্জির বেআইনি শেয়ার ব্যবসা ও লবিস্টদের পৃষ্ঠপোষকতা, সুন্দরবন ধ্বংস করে কয়লাভিত্তিক বিদ্যুৎ কেন্দ্র নির্মাণ এবং ভয়াবহ ঝুঁকি নিয়ে পারমাণবিক বিদ্যুৎ কেন্দ্র ইত্যাদি কমিশন ও দুর্নীতি ছাড়া হওয়া সম্ভব নয়। এসবকে বাধাহীন করতেই এই আইন।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : সমস্যা নিরসনে অন্য বিকল্প কী ছিল?<br />\r\nউত্তর : স্বল্প মেয়াদে পুরোনো বিদ্যুৎ কেন্দ্রের যন্ত্রপাতি পরিবর্তন এবং প্রয়োজনীয় মেরামত ও নবায়ন করলে একই পরিমাণ গ্যাসে কমপক্ষে এক হাজার ৭০০ মেগাওয়াট বেশি বিদ্যুৎ উৎপাদন করা সম্ভব ছিল। এবং এর উৎপাদন খরচ হতো ইউনিট প্রতি দুই টাকা। তা না করে আট গুণ বেশি দামে রেন্টাল ও কুইক রেন্টালের ফাঁদে ফেলা হয়েছে দেশকে। অথচ বিদ্যুৎ পাচ্ছি তুলনায় কম। নাইকোর সঙ্গে বিরোধের জেরে ছাতক ও ফেনী গ্যাস ক্ষেত্রে উত্তোলন বন্ধ রয়েছে। এসব থেকেই প্রতি দিন ৩০ কোটি ঘনফুট বাড়তি গ্যাস মিলত। ব্যয়ও হতো তুলনায় অনেক কম। অথচ এই পথে দুর্নীতি ও কমিশনের সুযোগ কম বলে সরকারের উৎসাহও কম। অর্থের অভাবের কথাও ঠিক নয়। কারণ, এর চেয়ে ২০ গুণ বেশি অর্থ ব্যয়ের পথেই তো সরকার চলছে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : বিদ্যৎ খাত এ রকম দুস্থ দশায় পড়া কি অনিবার্য ছিল?<br />\r\nউত্তর : না, ছিল না। যথাযথ পরিকল্পনা নিয়ে অগ্রসর হলে স্বল্পদামে নিরবচ্ছিন্ন বিদ্যুৎ সরবরাহ খুবই সম্ভব। আশির দশক থেকেই জ্বালানি ও বিদ্যুৎ খাত বহুজাতিক পুঁজির হাতে ছেড়ে দেওয়ার নীতি প্রণয়ন শুরু হয়। বিশ্বব্যাংক গোষ্ঠী বরাবরই জাতীয় মালিকানা ও জাতীয় সক্ষমতার বিরোধী। আর বিভিন্ন সরকার ও দেশি লুটেরা গোষ্ঠী লুণ্ঠন, দুর্নীতি ও কমিশনের লোভে এই নীতি অনুসরণ করেছে। এখান থেকেই দুস্থ দশার শুরু। দেড় দশক আগে &lsquo;বাংলাদেশ গ্যাসের ওপর ভাসছে&rsquo;, &lsquo;গ্যাস রপ্তানি করলে বাংলাদেশ দ্রুত উন্নয়ন করতে সক্ষম হবে, না করলে সর্বনাশ হবে&rsquo;&Ntilde;এমন সব রব তুলে এক সময় দেশের সীমিত গ্যাস সম্পদ বিদেশে পাচারের আয়োজন করা হয়েছিল। সেই প্রচারকেরাই এখন বলছেন, দেশে গ্যাস মজুত খুবই কম, বিদ্যুৎ সংকট কাটানোর জন্য অবিলম্বে বিদেশি কোম্পানির হাতে কয়লাখনি তুলে দিতে হবে, সমুদ্রবক্ষের গ্যাস সম্পদ নিয়ে বিদেশি কোম্পানির সঙ্গে আরও সুবিধা দিয়ে চুক্তি করতে হবে। তাদের মুখস্থ বুলি: আমাদের সক্ষমতা নেই, পুঁজি নেই। আসলে সংকট থাকলেই এদের জন্য সুযোগ আসে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : অর্থনীতির চাপের তুলনায় কুইক রেন্টালের অবদান কতটা?<br />\r\nউত্তর : পিডিবির ওয়েবসাইট অনুযায়ী ২৭ আগস্ট সন্ধ্যায় সর্বোচ্চ বিদ্যুৎ উৎপাদন ছিল ছয় হাজার ৬০১ মেগাওয়াট। কুইক রেন্টালের অবদান এর শতকরা ২০ ভাগেরও কম। অথচ বিদ্যুৎ খাতে ব্যয়ের শতকরা প্রায় ৮০ ভাগই এর পেছনে যাচ্ছে। উপরন্তু, এগুলো নির্ভরযোগ্য নয়, অনেকে বিদ্যুৎ উৎপাদন না করেও বিল নিচ্ছে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : জাতীয় কমিটির বিরুদ্ধে অভিযোগ, আপনাদের সমালোচনা যতটা আদর্শবাদী, ততটা বাস্তববাদী না।<br />\r\nউত্তর : বাস্তবতা হচ্ছে, জ্বালানি ও বিদ্যুৎ খাত দেশি&Ntilde;বিদেশি লুটেরাদের রাহুগ্রাসে। সমুদ্র, সুন্দরবনসহ দেশ ভয়াবহ ঝুঁকির মুখে, বিদ্যুতের মূল্য বৃদ্ধিসহ অর্থনীতির বোঝাও ক্রমবর্ধমান। বাস্তববাদী হওয়া বলতে যদি কেউ মনে করে সর্বনাশের মুখে চোখ&Ntilde;মুখ বন্ধ করে রাখা, তাহলে আমরা সে রকম বাস্তববাদী নই। আমরা এই বাস্তবতা পাল্টাতে চাই। জ্বালানি সম্পদের ওপর শতভাগ মালিকানা, রপ্তানি নিষিদ্ধ ও জাতীয় সক্ষমতা বিকাশের মাধ্যমে স্বল্প, মধ্য ও দীর্ঘমেয়াদি পরিকল্পনা নিয়ে তা বাস্তবায়নের পথেই স্বল্পদামে নিরবচ্ছিন্ন বিদ্যুৎ জোগানো ও জ্বালানি নিরাপত্তা সম্ভব। যেসব দেশ এগুলো করতে পারেনি, সম্পদ এবং দারিদ্র্য ও সহিংসতা সেখানে এক সঙ্গেই আছে। সম্পদ সেসব দেশে অভিশাপ। &lsquo;বাস্তববাদী&rsquo; লোকজন যেখানে চুপ করে থেকেছে বা তাল মিলিয়েছে; সম্পাদশালী আফ্রিকা তার করুণ উদাহরণ। মিয়ানমারের অভিজ্ঞতাও একই রকম।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রশ্ন : আপনাদের আইনি প্রস্তাব কী?<br />\r\nউত্তর : দুর্নীতি করার দায়মুক্তি আইন বাতিল করে খনিজ সম্পদ রপ্তানি নিষিদ্ধ করে আইন করুন। বেশি দামে বিদ্যুৎ কেনার ও বিদ্যুৎ খাতকে কিছু গোষ্ঠীর হাতে জিম্মি করার বিদ্যমান নীতি, চুক্তি ও প্রাতিষ্ঠানিক ব্যবস্থা থেকে দেশকে মুক্ত করুন। রেন্টাল, কুইক রেন্টালের চুক্তি বাতিল করে রাষ্ট্রীয় বিদ্যুৎ প্ল্যান্ট চালু, মেরামত ও নবায়ন করুন। দীর্ঘ মেয়াদে বৃহৎ গ্যাস ভিত্তিক বিদ্যুৎ প্ল্যান্ট স্থাপন করার কাজ শুরু করুন। বঙ্গোপসাগরের গ্যাস দেশের কাজে লাগানোর মহাপরিকল্পনা নিন। বিদেশি কোম্পানির কর্মচারী হিসেবে কাজ করা বন্ধ করুন। আশু প্রয়োজন মেটানোর জন্য কয়লা আমদানি ভিত্তিক না করে গ্যাস আমদানি ভিত্তিক বিদ্যুৎ প্ল্যান্ট করুন। জাতীয় সম্পদের ওপর জাতীয় কর্র্তৃত্ব নিশ্চিত করার জন্য বাপেক্স, পেট্রোবাংলা, জিওলজিক্যাল সার্ভে, ব্যুরো অব মিনারেল ডেভেলপমেন্টসহ জাতীয় প্রতিষ্ঠানের বিকাশ ঘটাতে উদ্যোগ নিন। খনিজ সম্পদ উন্নয়ন ও তার সর্বোত্তম ব্যবহারের জনশক্তি গড়ে তোলার জন্য বিশ্ববিদ্যালয় পর্যায়ে আরও বিভাগ এবং জাতীয়ভাবে বিভিন্ন গবেষণা প্রতিষ্ঠান স্থাপন করুন। এ কাজে প্রবাসী বাংলাদেশি বিশেষজ্ঞ ও প্রয়োজনে বিদেশি বিশেষজ্ঞদের কাজে লাগান। সৌরশক্তি, বায়ুশক্তি ও বর্জ্য সম্পদের বিশাল ভাণ্ডার কাজে লাগাতে উদ্যোগ নিন। নবায়নযোগ্য ও অনবায়নযোগ্য জ্বালানি সম্পদের সর্বোত্তম মিশ্রণ ঘটিয়ে একটি জ্বালানিনীতি প্রণয়ন করে তার জন্য প্রয়োজনীয় প্রাতিষ্ঠানিক কাঠামো নির্মাণ শুরু করুন। পাঠকদের কাছে আমার প্রশ্ন, এগুলো কি অবাস্তব মনে হয়, না দেশের প্রকৃত উন্নয়নের জন্য অপরিহার্য্য করণীয় মনে হয়?</span></p>\r\n','',1,15,'2016-01-07 03:07:54',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157674_anu.png'),(17,'Bangladesh: Asia’s New Energy Superpower? । Engg. SK. Shaheedullah','bangladesh:-asia’s-new-energy-superpower?-।-engg.-sk.-shaheedullah','<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">After a favorable UN settlement in June, Bangladesh stumbled upon a wealth of energy. The question remains, will investors buy in?</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Even though Bangladesh just hit an energy mother lode in July-winning the rights to 20,000 square kilometers of natural gas-rich waters from India in a U.N. Territorial arbitration, chances are you probably wouldn&rsquo;t think of it. To most, the territory still looks like a high-risk investment, given the political tensions, pervasive poverty, and highly subsidized economy which undermines growth and limits spending on energy infrastructure. Dhaka and Chittagong, swathed in slums, are a world away from the air-conditioned shopping malls of Riyadh, Doha, and Dubai.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Yet, looking the past isn&rsquo;t everything: Nigeria, Chad, and Venezuela have fared well in oil markets despite endemic poverty and violence; but Bangladesh&rsquo;s troubles could help explain why investors haven&rsquo;t been biting. In fact, quite the opposite; Australia&rsquo;s Santos pulled the plug on Bangladesh&rsquo;s only offshore gas field last year, citing poor production. With the help of Kris Energy, a Singaporean company, Santos plans to begin drilling in shallow waters later this year. Petrobangla, the national oil company, attracted just two bidders in a 2012 auction for offshore drilling rights: India&rsquo;s Oil &amp; Natural Gas Corporation and Houston-based Conocophillips.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Interest in &ldquo;dispute-free&rdquo; offshore oil blocks, which Petrobangla opened for auction last April, have proven sparse. Companies have complained about the lack of access to onshore blocks, and the terrible terms for drilling offered by Petrobangla. Earlier this year, Conoco and Russia&rsquo;s State Oil paired up to bid on three of Bangladesh&rsquo;s deepwater oil blocks, 30,000 feet below sea level. Conoco later attempted to win an agreement on two more deepwater blocks. Yet outside of these handful of companies, few are betting on Bangladesh.<br />\r\nIt might be a smart time to place a Houston-based Conocophillips.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Interest in &ldquo;dispute-free&rdquo; offshore oil blocks, which Petrobangla opened for auction last April, have proven sparse. Companies have complained about the lack of access to onshore blocks, and the terrible terms for drilling offered by Petrobangla. Earlier this year, Conoco and Russia&rsquo;s State Oil paired up to bid on three of Bangladesh&rsquo;s deepwater oil blocks, 30,000 feet below sea level. Conoco later attempted to win an agreement on two more deepwater blocks. Yet outside of these handful of companies, few are betting on Bangladesh.<br />\r\nIt might be a smart time to place a wager. With its new territory, Bangladesh&rsquo;s natural gas reserves are now estimated at 200 trillion cubic feet, the largest supply in the Asia-Pacific. Petrobangla will auction off 18 new oil and gas blocks by the end of the year. Assuming Bangladesh could recover all of that, it would make it one of the largest natural gas producers in the world.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">That&rsquo;s a big if. There&rsquo;s no way to be sure what&rsquo;s there. &ldquo;We just don&rsquo;t know whether 100 to 200 trillion cubic feet, or indeed anything at all, will be discovered in the new area.&rdquo; Bangladesh&rsquo;s gas reserves are probable, not proven, so that estimate is a fifty-fifty proposition, based on existing geological and engineering data.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Bangladesh needs a comprehensive plan to survey and derive this energy, but the government lacks basic competencies to drill in deep water, from trained oceanographers to laws that safeguard against spills. Bangladesh&rsquo;s Petroleum and Exploration Company have done experimental gas drilling, but it lacks the money and the energy infrastructure needed to explore the new territory.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Drilling also presents an environmental risk: cabinet secretaries have promised to deploy the government&rsquo;s few tools to guard against environmental disasters, but they will need to import talent from energy majors to prevent methane leaks into the bay. The problem can be mitigated using production agreements with Conoco and Statoil to extract the gas safely.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Petrobangla&rsquo;s Ex-chief, Hossain Monsur, echoed that notion in a statement released just after the U.N.&rsquo;s decision in July. But to achieve this, Bangladesh must first make nice with international oil majors, reconciling disputes that Petrobangla has inflated over the price of gas, which Prime Minister Sheikh Hasina has fought to keep down. Bangladesh may be willing to fast-track pricing reform if they think it&rsquo;ll lead to the large-scale extraction of these resources.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">In the Bengal Delta, where poverty is pervasive, pricing reform may prove unpalatable. Families that depend on government subsidies to keep the lights on and put food on the table will not be able to stomach higher bills. If Dhaka pays the bumped-up tab itself, those expenses could wreak more havoc on the belabored economy: boosting inflation, hiking the trade deficit, and chewing up big bites of tax revenue. Energy subsidies alone cost Bangladesh $1.9 billion in 2013, and much of the food subsidy has been choked away by corruption in recent years. By going further into the red to subsidize its poor, Bangladesh would tack onto its budget deficit, a mess that the tax base couldn&rsquo;t possibly clean up.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">With foreign investors clamoring to get involved in the gas industry, and the presence of corruption lurking throughout Bangladesh&rsquo;s ministries, rated some of the worst in the world by Transparency International, much of the energy, and revenue won from the U.N. settlement may not get back to the poorest citizens. That means the pain of energy shortages could persist, abetted by corruption in the state sector, and cause lasting damage. Political instability is also a grave threat: hundreds died in violence leading up to January&rsquo;s election, the bloodiest campaign since Bangladesh declared independence in 1971. With Prime Minister Sheikh Hasina and the rival Bangladesh National Party still at loggerheads, that tumult could flare up again.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">More money begets more problems. Still, Bangladesh&rsquo;s new territory also puts it in an advantageous position to win concessions from China and India, its two primary benefactors, who are both increasingly hungry for new sources of energy. China has already invested a significant amount of money to build an airport at Cox&rsquo;s Bazar, just south of Chittagong, a tab that continues to increase. Bangladesh remains a key part of Beijing&rsquo;s strategy of encircling India with development money, and the infusion of natural gas should reaffirm that status.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:12px\"><span style=\"font-family:verdana,geneva,sans-serif\">Major energy finds in the Bay of Bengal could make a huge difference in pushing the economy forward, sending a rush of foreign investment into the country and helping growth rebound. With natural gas consumption expected to grow worldwide, but particularly in the developing countries in South Asia, there is likely to be a strong market for the gas, Bangladesh&rsquo;s oil riches are its best opportunity to rise from poverty, creating an equitable distribution of wealth to stabilize the government and the country. Dhaka must seize the moment.</span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশ : এশিয়ার উদীয়মান জ্বালানি পরাশক্তি</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">২০১৪ জুনে বঙ্গোপসাগরে গ্যাস সমৃদ্ধ এলাকায় বিবদমান পক্ষের মধ্যে বাংলাদেশের অনুকূলে জাতিসংঘ সালিশী রায় ঘোষণার পর অপ্রত্যাশিতভাবে বাংলাদেশ বিরাট জ্বালানী সম্পদের মালিকানা পেল। প্রশ্ন রয়ে যাচ্ছে এরপরও বিদেশী বিনিয়োগকারীরা কি আগ্রহী হবেন?<br />\r\nএটা নিশ্চিত যে, ইতিপূর্বে জাতিসংঘ প্রদত্ত সীমানা সালিশের মাধ্যমে ভারতের দাবীকৃত অংশ থেকে প্রায় ২০,০০০ বর্গ কি.মি. গ্যাস সমৃদ্ধ সমুদ্র এলাকায় অধিকার লাভের মাধ্যমে বাংলাদেশ জুলাই মাসে জ্বালানীশক্তির এক বিরাট সম্পদ অর্জন করেছে। তবুও সম্ভাবনা থেকেই যায় যে আমরা যে সত্যিই লাভবান এটা চিন্তার মধ্যেও আনতে পারি না।<br />\r\nঅধিকাংশ বিনিয়োগকারীর কাছে এলাকাটিতে বিনিয়োগ অত্যধিক ঝুঁকিপূর্ণ, কারণ রাজনৈতিক টানাপোড়েন, সামগ্রিক দারিদ্য, অত্যধিক মূল্যছাড় ভিত্তিক অর্থনীতি, যা প্রবৃদ্ধিতে বিঘ&oelig; সৃষ্টি করে এবং জ্বালানীশক্তির অবকাঠামোতে বিনিয়োগ সংকীর্ণ করে। রিয়াদ, দোহা এবং দুবাই এর মত এয়ারকন্ডিশন করা বিলাস পণ্য সমৃদ্ধ বিপণি বিতান এর তুলনায় ঢাকা ও চট্রগ্রাম বস্তিতে ভরা অবস্থার পর্বতপ্রমাণ বৈষম্যও একটি কারণ।<br />\r\nতবুও আংশিক বিবেচনা সামগ্রিক পরিস্থিতির পরিচায়ক নয়। দারিদ্য ও সংঘাতপূর্ণ পরিবেশ সত্ত্বেও নাইজেরিয়া, চাদ ও ভেনিজুয়েলার মত দেশ ভালো অবস্থানে আছে। তবুও বাংলাদেশের দুর্যোগময় পরিস্থিতিতে বিনিয়োগকারীরা আকৃষ্ট হচ্ছে না কেন তা বোধগম্য। বস্তুতঃ বিপরীতটিই ঘটেছে। অস্ট্রেলিয়াভিত্তিক স্যান্টোস গত বছর বাংলাদেশের একমাত্র সামুদ্রিক জলসীমানায় অবস্থিত গ্যাসকূপ কাজ বন্ধ রেখেছে উৎপাদনের সামান্য সম্ভাবনা দেখিয়ে। সিঙ্গাপুরের কোম্পানি ক্রিস এনার্জির সহযোগিতায় এই বছর অগভীর সমুদ্রে কূপ খননের পরিকল্পনা হাতে নিয়েছে স্যান্টোস। জাতীয় জ্বালানীসংস্থা পেট্রোবাংলা সমুদ্রে কূপ খননের স্বত্বের জন্য ২০১২ সালের আহবান করা টেন্ডারে মাত্র দুটি সংস্থা থেকে দরপত্র লাভ করে। তারা ছাড়া ভারতের ও.এন.জি.সি বা তেল ও প্রাকৃতিক গ্যাস কর্পোরেশন ও হিউস্টন ভিত্তিক কনোকো-ফিলিপস গত বছর এপ্রিলে বিতর্ক মুক্ত এলাকায় পেট্রোবাংলা কর্তৃক দরপত্র আহবানে সাড়া দিয়েছে, আর কেউই দেয়নি। প্রতিযোগীরা অভিযোগ করেছে যে (১) স্থল ভাগের ব্লকগুলিতে কূপ খননের সুবিধা দেয়া হয়নি এবং (২) পেট্রোবাংলা কূপ খননের ব্যাপারে ভয়ানক শর্ত জুড়ে দিয়েছে। এ বছরের প্রথম দিকে সমুদ্রে ৩০,০০০ ফুট গভীর পানিতে তিনটি গভীর সমুদ্রের ব্লকে কনোকা এবং নরওয়ের স্ট্যাটঅয়েল যৌথভাবে প্রতিযোগিতা করে। পরবর্তীতে গভীর সমুদ্র ব্লকে কনোকো আরও দুটি ব্লকে চুক্তি করতে চায়। এই মুষ্টিমেয় কোম্পানীগুলির বাইরে খুব অল্প সংখ্যক কোম্পানী বাংলাদেশে কাজ করার আগ্রহ দেখিয়েছে।<br />\r\nএখন সম্ভবত বাজি রাখার মত সুন্দর পরিস্থিতি বিদ্যমান। নতুন প্রাপ্ত সীমানায় বাংলাদেশের প্রাকৃতিক গ্যাসের মজুদ ২০০ ট্রিলিয়ন ঘনফুটে দাঁড়াতে পারে-যা এশিয়া প্রশান্ত মহাসাগর অঞ্চলে সর্ববৃহৎ। এ বছরের শেষ দিকে পেট্রোবাংলা ১৮টি নতুন তেল ও গ্যাস ক্ষেত্রের জন্য দরপত্র আহ্বান করবে। রিজার্ভের সম্পূর্র্ণ অংশ উৎপাদনের অধীনে আনতে পারবে ধরে নিলে বাংলাদেশ পৃথিবীর মধ্যে সর্ববৃহৎ প্রাকৃতিক গ্যাস উৎপাদনকারী দেশগুলির মধ্যে অন্যতম হতে পারে। একটি বড় &lsquo;কিন্তু&rsquo; অপেক্ষা করছে। রিজার্ভে কতটা সত্যিই পাওয়া যাবে তা কেউ নিশ্চিত করে বলতে পারে না। এই নবলদ্ধ এলাকার মজুদ ১০০ বা ২০০ ট্রিলিয়ন ঘনফুট এমনকি শূন্য হতে পারে, নিশ্চিত করে তা কেউ বলতে পারে না। বাংলাদেশের গ্যাস মজুদের পরিমাণ &lsquo;নিশ্চিত&rsquo; ক্যাটাগরিতে নয়। বর্তমান ভূতাত্ত্বিক ও প্রকৌশলগত তথ্যের ভিত্তিতে প্রাককলনটির যথার্থতা ৫০:৫০।<br />\r\nজ্বালানি শক্তি আহরণে বাংলাদেশের একটি সামগ্রিক জরিপ প্রয়োজন। কিন্তু বাংলাদেশ সরকারের পক্ষে গভীর সমুদ্রে কূপ খননের দক্ষতার অভাব আছে। সমুদ্র বিশেষজ্ঞেরও অভাব আছে। তদুপরি জ্বালানি নিঃসরণে বিপদ থেকে বাঁচার জন্য আইনের অভাব আছে। বাপেক্স এ যাবৎ পরীক্ষামূলক কূপ খনন করেছে। কিন্তু তাদের পক্ষে নতুন এলাকায় মজুদ অনুসন্ধানে অর্থ বিনিয়োগ ও অবকাঠামোগত জ্ঞানের অভাব আছে।<br />\r\nকূপ খনন কাজে পরিবেশ প্রতিবেশ ভয়াবহ ঝুঁকিতে পড়ে। মন্ত্রিসভার সচিবগণ সরকারের যে সীমিত সামর্থ্য আছে তা দিয়ে পরিবেশ প্রতিবেশ ঝুঁকি মোকাবেলা করতে চায়। কিন্তু এজন্য বৃহৎ জ্বালানি শক্তি আহরণে অভিজ্ঞ বিদেশি কোম্পানি থেকে বিশেষজ্ঞ নিয়োগ করতে হবে, সাগরে মিথেন গ্যাস নিঃসরণ এড়ানোর ব্যাপারে। এই সমস্য লাঘব করা যেতে পারে কনোকো ও স্ট্যাট অয়েল এর সঙ্গে ঝুঁকিহীনভাবে গ্যাস আহরণের চুক্তির মাধ্যমে।<br />\r\nপেট্রোবাংলার সাবেক প্রধান অধ্যাপক হোসেন মনসুর জাতি সংঘ প্রদত্ত শালিসী রায়ের পর একটি বিবৃতিতে এই ধারণার প্রতিধ্বনি করেছেন। কিন্তু বিদেশি তেল কোম্পানির সহযোগিতা পেতে গেলে বিদেশি তেল কোম্পানির সঙ্গে সুসম্পর্ক গড়তে হবে। বর্তমান প্রধানমন্ত্রী গ্যাসের মূল্যবৃদ্ধির বিরুদ্ধে যে অবস্থান নিয়েছেন তাতে যে দ্বন্দ্ব সৃষ্টি হয়েছে তা সুন্দরভাবে মিটমাট করতে হবে। বড় স্কেলে জ্বালানি আহরণ করতে গেলে বড় আকারের মূল্য সংস্কারের প্রয়োজন হতে পারে।<br />\r\nসর্বব্যাপী দারিদ্য কবলিত বঙ্গোপসাগরের উপকূলীয় দেশটিতে মূল্য সংস্কার অবাঞ্ছনীয় হতে পারে। ঘরে বিদ্যুৎ পেতে ও আহার যোগাড় করতে সরকারের পক্ষ থেকে মূল্যছাড় প্রাপ্তির উপর নির্ভরশীল জনগণ বর্ধিত জ্বালানি বিল দিতে অক্ষম হতে পারে। যদি সরকার নিজের ঘাড়ে বর্ধিত মূল্য বহন করে তবে দেশের ভারাক্রান্ত অর্থনীতির উপর আরও চাপ বাড়বে। মূদ্রাস্ফীতি বৃদ্ধি পাবে। বাণিজ্য ঘাটতি বৃদ্ধি পাবে। ট্যাক্স রাজস্বের আয়ের অনেকটা ক্ষয় পাবে। জ্বালানি শক্তিবাবদ মূল্যছাড়ে ২০১৩ সালে ১৯০ কোটি ডলার ব্যয় হয়েছে। এবং খাদ্যের মূল্য ছাড়ের মাধ্যমে জনগণের স্বস্তি দান দুর্নীতি দ্বারা বিঘি&oelig;ত হয়েছে। দরিদ্র জনগোষ্ঠীর স্বস্তির জন্য আরো অর্থনৈতিক ছাড় দিতে গেলে বাংলাদেশের বাজেট ঘাটতি আরও বাড়বে এবং ট্যাক্স খাতে চাপ বাড়বে।<br />\r\nবিদেশি বিনিয়োগকারীদের গ্যাস শিল্পে জড়িত হবার চাপের মুখে ট্রান্সপারেন্সি ইন্টারন্যাশনাল এর তথ্য অনুযায়ী দেশের সকল মন্ত্রণালয়ে বিশ্বের ভয়াবহতম ও সর্বব্যাপী দুর্নীতির কারণে জাতিসংঘ সালিশেলব্ধ সমুদ্র এলাকা থেকে প্রাপ্ত জ্বালানি সম্পদ হতে আয়ের সামান্যই দেশের গরিবদের ভাগ্যে জুটবে। এর অর্থ এই যে গ্যাস ও বিদ্যুৎ সংকটের দুর্দশা চলতেই থাকবে। রাজনৈতিক অস্থিতিশীলতা একটি ভয়ঙ্কর হুমকি হবে। জানুয়ারি নির্বাচনের প্রাককালে শত শত লোক সহিংসতায় প্রাণ দিয়েছে যা ১৯৭১ এ বাংলাদেশের স্বাধীনতা লাভের পর হিংস্রতম সংঘাত। প্রধানমন্ত্রীর দল ও বিরোধী বাংলাদেশ জাতীয়তাবাদী দলের মধ্যে দ্বন্দ্ব অব্যাহত আছে এবং এদের মধ্যে ভবিষ্যতে সংঘাতের সম্ভাবনা বিদ্যমান।<br />\r\nসম্পদ বাড়লে বিপদও বাড়ে। তথাপি বাংলাদেশের প্রাপ্ত নুতন অর্থনৈতিক অঞ্চল চীন ও ভারত এই দুই সাহায্যদাতা দেশ থেকে নুতন সুবিধা পাওয়ার সুযোগ এনে দিতে পারে। এই দুই দেশই জ্বালানী সম্পদ সংগ্রহে আগ্রহী ভূমিকায় আছে। চট্রগ্রামের দক্ষিণে কক্সবাজারে একটি বিমান বন্দর নির্মাণে চীন যথেষ্ট অর্থ বিনিয়োগ করেছে আরও বিনিয়োগে আগ্রহী। বাংলাদেশকে দিয়ে ভারতের চতুর্দিকে ব্যুহ রচনায় চীন উন্নয়ন তহবিল যোগান করতে চায়। গ্যাস ক্রয়ের চুক্তির মাধ্যমে চীন আরো বিনিয়োগের আশা রাখে।<br />\r\nবঙ্গোপসাগরে বৃহৎ জ্বালানি সম্পদ বাংলাদেশের অর্থনৈতিক উন্নয়নে বিরাট ভূমিকা রাখতে পারে। ফলে বাইরে থেকে বিনিয়োগ আকৃষ্ট হবে ও প্রবৃদ্ধি আরো বাড়বে। বিশ্বব্যাপি প্রাকৃতিক গ্যাসের চাহিদার প্রেক্ষাপটে বিশেষ করে দক্ষিণ এশিয়ার উন্নয়নশীল দেশে গ্যাসের চাহিদা দ্রুত বাড়বে। জ্বালানি সম্পদ লব্ধ সম্পদ বাংলাদেশের দারিদ্য মুক্তির সংগ্রামে সাহায্য করবে ও সুষম আর্থিক সুবিধা প্রাপ্তির মাধ্যমে রাজনৈতিক স্থিতিশীলতা অর্জিত হতে পারে। বাংলাদেশকে এই সুযোগ গ্রহণ করতেই হবে। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সমুদ্র এলাকায় তেল-গ্যাস সম্পদ আহরণের নিমিত্ত প্রতিযোগিতামূলক দরপত্র আহ্বান<br />\r\nসমুদ্র বক্ষে ভূ-কম্পন জরিপ কাজের জন্য ২০১৪ সালের ডিসেম্বর মাসে দরপত্র আহ্বান করা হবে। যাবতীয় তথ্য প্রাপ্তির পর এই নতুন দরপত্র ২০১৪ জুনে বঙ্গোপসাগরে গ্যাস সমৃদ্ধ এলাকায় বিবদমান পক্ষের মধ্যে বাংলাদেশের অনুকূলে জাতিসংঘ সালিশী রায় ঘোষণার পর অপ্রত্যাশিতভাবে বাংলাদেশ বিরাট জ্বালানী সম্পদের মালিকানা পেল। প্রশ্ন রয়ে যাচ্ছে এরপরও বিদেশী বিনিয়োগকারীরা কি আগ্রহী হবেন?<br />\r\nএটা নিশ্চিত যে, ইতিপূর্বে জাতিসংঘ প্রদত্ত সীমানা সালিশের মাধ্যমে ভারতের দাবীকৃত অংশ থেকে প্রায় ২০,০০০ বর্গ কি.মি. গ্যাস সমৃদ্ধ সমুদ্র এলাকায় অধিকার লাভের মাধ্যমে বাংলাদেশ জুলাই মাসে জ্বালানীশক্তির এক বিরাট সম্পদ অর্জন করেছে। তবুও সম্ভাবনা থেকেই যায় যে আমরা যে সত্যিই লাভবান এটা চিন্তার মধ্যেও আনতে পারি না।<br />\r\nঅধিকাংশ বিনিয়োগকারীর কাছে এলাকাটিতে বিনিয়োগ অত্যধিক ঝুঁকিপূর্ণ, কারণ রাজনৈতিক টানাপোড়েন, সামগ্রিক দারিদ্য, অত্যধিক মূল্যছাড় ভিত্তিক অর্থনীতি, যা প্রবৃদ্ধিতে বিঘ&oelig; সৃষ্টি করে এবং জ্বালানীশক্তির অবকাঠামোতে বিনিয়োগ সংকীর্ণ করে। রিয়াদ, দোহা এবং দুবাই এর মত এয়ারকন্ডিশন করা বিলাস পণ্য সমৃদ্ধ বিপণি বিতান এর তুলনায় ঢাকা ও চট্রগ্রাম বস্তিতে ভরা অবস্থার পর্বতপ্রমাণ বৈষম্যও একটি কারণ।<br />\r\nতবুও আংশিক বিবেচনা সামগ্রিক পরিস্থিতির পরিচায়ক নয়। দারিদ্য ও সংঘাতপূর্ণ পরিবেশ সত্ত্বেও নাইজেরিয়া, চাদ ও ভেনিজুয়েলার মত দেশ ভালো অবস্থানে আছে। তবুও বাংলাদেশের দুর্যোগময় পরিস্থিতিতে বিনিয়োগকারীরা আকৃষ্ট হচ্ছে না কেন তা বোধগম্য। বস্তুতঃ বিপরীতটিই ঘটেছে। অস্ট্রেলিয়াভিত্তিক স্যান্টোস গত বছর বাংলাদেশের একমাত্র সামুদ্রিক জলসীমানায় অবস্থিত গ্যাসকূপ কাজ বন্ধ রেখেছে উৎপাদনের সামান্য সম্ভাবনা দেখিয়ে। সিঙ্গাপুরের কোম্পানি ক্রিস এনার্জির সহযোগিতায় এই বছর অগভীর সমুদ্রে কূপ খননের পরিকল্পনা হাতে নিয়েছে স্যান্টোস। জাতীয় জ্বালানীসংস্থা পেট্রোবাংলা সমুদ্রে কূপ খননের স্বত্বের জন্য ২০১২ সালের আহবান করা টেন্ডারে মাত্র দুটি সংস্থা থেকে দরপত্র লাভ করে। তারা ছাড়া ভারতের ও.এন.জি.সি বা তেল ও প্রাকৃতিক গ্যাস কর্পোরেশন ও হিউস্টন ভিত্তিক কনোকো-ফিলিপস গত বছর এপ্রিলে বিতর্ক মুক্ত এলাকায় পেট্রোবাংলা কর্তৃক দরপত্র আহবানে সাড়া দিয়েছে, আর কেউই দেয়নি। প্রতিযোগীরা অভিযোগ করেছে যে (১) স্থল ভাগের ব্লকগুলিতে কূপ খননের সুবিধা দেয়া হয়নি এবং (২) পেট্রোবাংলা কূপ খননের ব্যাপারে ভয়ানক শর্ত জুড়ে দিয়েছে। এ বছরের প্রথম দিকে সমুদ্রে ৩০,০০০ ফুট গভীর পানিতে তিনটি গভীর সমুদ্রের ব্লকে কনোকা এবং নরওয়ের স্ট্যাটঅয়েল যৌথভাবে প্রতিযোগিতা করে। পরবর্তীতে গভীর সমুদ্র ব্লকে কনোকো আরও দুটি ব্লকে চুক্তি করতে চায়। এই মুষ্টিমেয় কোম্পানীগুলির বাইরে খুব অল্প সংখ্যক কোম্পানী বাংলাদেশে কাজ করার আগ্রহ দেখিয়েছে।<br />\r\nএখন সম্ভবত বাজি রাখার মত সুন্দর পরিস্থিতি বিদ্যমান। নতুন প্রাপ্ত সীমানায় বাংলাদেশের প্রাকৃতিক গ্যাসের মজুদ ২০০ ট্রিলিয়ন ঘনফুটে দাঁড়াতে পারে-যা এশিয়া প্রশান্ত মহাসাগর অঞ্চলে সর্ববৃহৎ। এ বছরের শেষ দিকে পেট্রোবাংলা ১৮টি নতুন তেল ও গ্যাস ক্ষেত্রের জন্য দরপত্র আহ্বান করবে। রিজার্ভের সম্পূর্র্ণ অংশ উৎপাদনের অধীনে আনতে পারবে ধরে নিলে বাংলাদেশ পৃথিবীর মধ্যে সর্ববৃহৎ প্রাকৃতিক গ্যাস উৎপাদনকারী দেশগুলির মধ্যে অন্যতম হতে পারে। একটি বড় &lsquo;কিন্তু&rsquo; অপেক্ষা করছে। রিজার্ভে কতটা সত্যিই পাওয়া যাবে তা কেউ নিশ্চিত করে বলতে পারে না। এই নবলদ্ধ এলাকার মজুদ ১০০ বা ২০০ ট্রিলিয়ন ঘনফুট এমনকি শূন্য হতে পারে, নিশ্চিত করে তা কেউ বলতে পারে না। বাংলাদেশের গ্যাস মজুদের পরিমাণ &lsquo;নিশ্চিত&rsquo; ক্যাটাগরিতে নয়। বর্তমান ভূতাত্ত্বিক ও প্রকৌশলগত তথ্যের ভিত্তিতে প্রাককলনটির যথার্থতা ৫০:৫০।<br />\r\nজ্বালানি শক্তি আহরণে বাংলাদেশের একটি সামগ্রিক জরিপ প্রয়োজন। কিন্তু বাংলাদেশ সরকারের পক্ষে গভীর সমুদ্রে কূপ খননের দক্ষতার অভাব আছে। সমুদ্র বিশেষজ্ঞেরও অভাব আছে। তদুপরি জ্বালানি নিঃসরণে বিপদ থেকে বাঁচার জন্য আইনের অভাব আছে। বাপেক্স এ যাবৎ পরীক্ষামূলক কূপ খনন করেছে। কিন্তু তাদের পক্ষে নতুন এলাকায় মজুদ অনুসন্ধানে অর্থ বিনিয়োগ ও অবকাঠামোগত জ্ঞানের অভাব আছে।<br />\r\nকূপ খনন কাজে পরিবেশ প্রতিবেশ ভয়াবহ ঝুঁকিতে পড়ে। মন্ত্রিসভার সচিবগণ সরকারের যে সীমিত সামর্থ্য আছে তা দিয়ে পরিবেশ প্রতিবেশ ঝুঁকি মোকাবেলা করতে চায়। কিন্তু এজন্য বৃহৎ জ্বালানি শক্তি আহরণে অভিজ্ঞ বিদেশি কোম্পানি থেকে বিশেষজ্ঞ নিয়োগ করতে হবে, সাগরে মিথেন গ্যাস নিঃসরণ এড়ানোর ব্যাপারে। এই সমস্য লাঘব করা যেতে পারে কনোকো ও স্ট্যাট অয়েল এর সঙ্গে ঝুঁকিহীনভাবে গ্যাস আহরণের চুক্তির মাধ্যমে।<br />\r\nপেট্রোবাংলার সাবেক প্রধান অধ্যাপক হোসেন মনসুর জাতি সংঘ প্রদত্ত শালিসী রায়ের পর একটি বিবৃতিতে এই ধারণার প্রতিধ্বনি করেছেন। কিন্তু বিদেশি তেল কোম্পানির সহযোগিতা পেতে গেলে বিদেশি তেল কোম্পানির সঙ্গে সুসম্পর্ক গড়তে হবে। বর্তমান প্রধানমন্ত্রী গ্যাসের মূল্যবৃদ্ধির বিরুদ্ধে যে অবস্থান নিয়েছেন তাতে যে দ্বন্দ্ব সৃষ্টি হয়েছে তা সুন্দরভাবে মিটমাট করতে হবে। বড় স্কেলে জ্বালানি আহরণ করতে গেলে বড় আকারের মূল্য সংস্কারের প্রয়োজন হতে পারে।<br />\r\nসর্বব্যাপী দারিদ্য কবলিত বঙ্গোপসাগরের উপকূলীয় দেশটিতে মূল্য সংস্কার অবাঞ্ছনীয় হতে পারে। ঘরে বিদ্যুৎ পেতে ও আহার যোগাড় করতে সরকারের পক্ষ থেকে মূল্যছাড় প্রাপ্তির উপর নির্ভরশীল জনগণ বর্ধিত জ্বালানি বিল দিতে অক্ষম হতে পারে। যদি সরকার নিজের ঘাড়ে বর্ধিত মূল্য বহন করে তবে দেশের ভারাক্রান্ত অর্থনীতির উপর আরও চাপ বাড়বে। মূদ্রাস্ফীতি বৃদ্ধি পাবে। বাণিজ্য ঘাটতি বৃদ্ধি পাবে। ট্যাক্স রাজস্বের আয়ের অনেকটা ক্ষয় পাবে। জ্বালানি শক্তিবাবদ মূল্যছাড়ে ২০১৩ সালে ১৯০ কোটি ডলার ব্যয় হয়েছে। এবং খাদ্যের মূল্য ছাড়ের মাধ্যমে জনগণের স্বস্তি দান দুর্নীতি দ্বারা বিঘি&oelig;ত হয়েছে। দরিদ্র জনগোষ্ঠীর স্বস্তির জন্য আরো অর্থনৈতিক ছাড় দিতে গেলে বাংলাদেশের বাজেট ঘাটতি আরও বাড়বে এবং ট্যাক্স খাতে চাপ বাড়বে।<br />\r\nবিদেশি বিনিয়োগকারীদের গ্যাস শিল্পে জড়িত হবার চাপের মুখে ট্রান্সপারেন্সি ইন্টারন্যাশনাল এর তথ্য অনুযায়ী দেশের সকল মন্ত্রণালয়ে বিশ্বের ভয়াবহতম ও সর্বব্যাপী দুর্নীতির কারণে জাতিসংঘ সালিশেলব্ধ সমুদ্র এলাকা থেকে প্রাপ্ত জ্বালানি সম্পদ হতে আয়ের সামান্যই দেশের গরিবদের ভাগ্যে জুটবে। এর অর্থ এই যে গ্যাস ও বিদ্যুৎ সংকটের দুর্দশা চলতেই থাকবে। রাজনৈতিক অস্থিতিশীলতা একটি ভয়ঙ্কর হুমকি হবে। জানুয়ারি নির্বাচনের প্রাককালে শত শত লোক সহিংসতায় প্রাণ দিয়েছে যা ১৯৭১ এ বাংলাদেশের স্বাধীনতা লাভের পর হিংস্রতম সংঘাত। প্রধানমন্ত্রীর দল ও বিরোধী বাংলাদেশ জাতীয়তাবাদী দলের মধ্যে দ্বন্দ্ব অব্যাহত আছে এবং এদের মধ্যে ভবিষ্যতে সংঘাতের সম্ভাবনা বিদ্যমান।<br />\r\nসম্পদ বাড়লে বিপদও বাড়ে। তথাপি বাংলাদেশের প্রাপ্ত নুতন অর্থনৈতিক অঞ্চল চীন ও ভারত এই দুই সাহায্যদাতা দেশ থেকে নুতন সুবিধা পাওয়ার সুযোগ এনে দিতে পারে। এই দুই দেশই জ্বালানী সম্পদ সংগ্রহে আগ্রহী ভূমিকায় আছে। চট্রগ্রামের দক্ষিণে কক্সবাজারে একটি বিমান বন্দর নির্মাণে চীন যথেষ্ট অর্থ বিনিয়োগ করেছে আরও বিনিয়োগে আগ্রহী। বাংলাদেশকে দিয়ে ভারতের চতুর্দিকে ব্যুহ রচনায় চীন উন্নয়ন তহবিল যোগান করতে চায়। গ্যাস ক্রয়ের চুক্তির মাধ্যমে চীন আরো বিনিয়োগের আশা রাখে।<br />\r\nবঙ্গোপসাগরে বৃহৎ জ্বালানি সম্পদ বাংলাদেশের অর্থনৈতিক উন্নয়নে বিরাট ভূমিকা রাখতে পারে। ফলে বাইরে থেকে বিনিয়োগ আকৃষ্ট হবে ও প্রবৃদ্ধি আরো বাড়বে। বিশ্বব্যাপি প্রাকৃতিক গ্যাসের চাহিদার প্রেক্ষাপটে বিশেষ করে দক্ষিণ এশিয়ার উন্নয়নশীল দেশে গ্যাসের চাহিদা দ্রুত বাড়বে। জ্বালানি সম্পদ লব্ধ সম্পদ বাংলাদেশের দারিদ্য মুক্তির সংগ্রামে সাহায্য করবে ও সুষম আর্থিক সুবিধা প্রাপ্তির মাধ্যমে রাজনৈতিক স্থিতিশীলতা অর্জিত হতে পারে। বাংলাদেশকে এই সুযোগ গ্রহণ করতেই হবে। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সমুদ্র এলাকায় তেল-গ্যাস সম্পদ আহরণের নিমিত্ত প্রতিযোগিতামূলক দরপত্র আহ্বান<br />\r\nসমুদ্র বক্ষে ভূ-কম্পন জরিপ কাজের জন্য ২০১৪ সালের ডিসেম্বর মাসে দরপত্র আহ্বান করা হবে। যাবতীয় তথ্য প্রাপ্তির পর এই নতুন দরপত্র আহ্বান প্রক্রিয়া শুরু হবে। রাষ্ট্রীয় সংস্থা পেট্রোবাংলার একজন ঊর্ধ্বতন কর্মকর্তা গত ১৫ নভেম্বর বলেছেন যে, ডিসেম্বর মাসে বঙ্গোপসাগরে হাইড্রো-কার্বন মজুদ অনুসন্ধান করার জন্য পেট্রোবাংলা ভূকম্পন জরিপের ব্যাপারে সবার জন্য উন্মুক্ত আন্তর্জাতিক দরপত্র আহ্বান করতে যাচ্ছে।<br />\r\nটেন্ডার দলিলের সমুদয় খসড়া তৈরির কাজ পেট্রোবাংলা ইতিমধ্যে সম্পন্ন করেছে এবং প্রকাশ্যে দরপত্র আহ্বানের পূর্বে মন্ত্রণালয়ের জ্বালানি ও খনিজ সম্পদ ডিভিশন এর কাছে এই দলিল অনুমোদনের জন্য প্রেরণ করেছে।<br />\r\nনতুন দরপত্র আহ্বান এর পূর্বে মজুদ সম্পর্কে সমুদয় তথ্য দিতে পারলে পেট্রোবাংলা লাভবান হবে। কারণ দরদাতাগণ বিনা অনিশ্চয়তায় দর দিতে পারবে। তাছাড়া যারা দরপত্রে অংশগ্রহণ করবে তাদের কাছে ভূকম্পনভিত্তিক জরিপে তথ্য বিক্রয় করে পেট্রোবাংলা অর্থ লাভ করতে পারবেন। জ্বালানি ও খনিজ সম্পদ ডিভিশনের জ্যেষ্ঠ কর্মকর্তা জানিয়েছেন যে, দরপত্র আহ্বান করলে সমুদ্র বক্ষের মজুদ জরিপের কাজে কয়েকটি আন্তর্জাতিক কোম্পানি আগ্রহ দেখাতে পারেন।<br />\r\nজরিপ কাজ সম্পন্ন করার জন্য নির্বাচিত সংস্থাগুলিকে যদি সময়সীমা বেঁধে দেওয়া হয় তবে সমুদ্র বক্ষের সম্ভাবনাময় মজুদ সম্পর্কে তথ্য জানা যাবে। তা হলে দরপত্র আহ্বানের পূর্বে এই জরিপের তথ্য দরপত্র দাতাদের মধ্যে বিতরণ করতে পারলে অনুকূল দর পাওয়া যাবে। কারণ পেট্রোবাংলা তখন বিশ্ববাজারে যেসব শর্তে দর আহ্বান করা হয় তা এখানে বাস্তবায়ন করা যাবে। অন্যথায় জরিপ ছাড়া দরপত্র প্রাপ্ত দরে বাংলাদেশের স্বার্থ রক্ষিত হবে না।<br />\r\nআমাদের দেশে সমুদ্র এলাকায় সীমা আন্তর্জাতিক আদালতের রায়ে সুস্পষ্টভাবে নির্দেশিত হয়েছে। হেগে অবস্থিত জাতিসংঘে স্থায়ী সালিশী আদালতে ভারতের বিরুদ্ধে আমাদের অনুকূলে এই বছরের জুন মাসে যে রায় দিয়েছে তাতে ২০০ নটিকেল মাইল পর্যন্ত একচ্ছত্র অর্থনৈতিক অঞ্চলের এবং তদসংলগ্ন অধিকারের সীমানা নিয়ে বাংলাদেশের দাবি গৃহীত হয়েছে।<br />\r\nট্রাইব্যুনাল তার রায়ে বাংলাদেশের দাবি করা ২০০০ নটিকেল মাইলের বহির্ভূত সম্প্রসারিত মহীসোপানের উপর বিরাট অংশ ট্রাইব্যুনাল তার রায়ে বাংলাদেশকে দিয়েছে। বাংলাদেশের দাবি ছিল সমতা ভিত্তিক নীতি অবলম্বন করা। বিতর্কিত ২৫৬০২ বর্গ কিলোমিটার এর মধ্যে বাংলাদেশকে ট্রাইব্যুনাল দিয়েছে ১৯৪৬৭ বর্গ কিলোমিটার। এই রায়ের ফলে বাংলাদেশের সমুদ্র এলাকার ভারতের দাবি করা ১০টি ব্লকের সম্পূর্র্ণ বা আংশিক এলাকা পাবে। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">যেসব ব্লকে বাংলাদেশ সম্পূর্ণ অংশ পেয়েছে তার মধ্যে নিম্নোক্ত ৪টি ব্লক যথা পরিচয়ের জন্য ২০০৮ সালের দরপত্রে বর্ণিত ক্রমিক সংখ্যা ব্যবহার করা হয়েছে। গভীর সমুদ্রের ব্লক-১০, ব্লক-১৫, ব্ল-২০ এবং ব্লক-২৫। নিম্নোক্ত ৪টি ব্লকের অধিকাংশ বাংলাদেশ পেয়েছে : ব্লক-৯, ব্লক-১৪, ব্লক-১৯ এবং ব্লক-২৪। অগভীর সমুদ্রে ২টি ব্লক বাংলাদেশ পেয়েছে যথা ব্লক-১ এবং ব্লক-৫। সীমান্তবর্তী ৫টি ব্লকে নতুন করে সীমানা নির্দিষ্ট করার প্রয়োজন হবে। সেগুলি হচ্ছে ব্লক-৫, ব্লক-৯, ব্লক-১৪, ব্লক-১৯ এবং ব্লক-২৪। ইতিপূর্বে ২০১২ সালের ১৪ মার্চ জার্মানির হামবুর্গ শহরে অবস্থিত সমুদ্র আইনের আন্তর্জাতিক ট্রাইব্যুনাল বাংলাদেশ এবং মিয়ানমারের মধ্যে সমুদ্র এলাকা নিয়ে যে দ্বন্দ্ব ছিল তা মিটিয়ে কয়েকটি ব্লক আমাদের অনুকূলে অর্পিত করেছে। এর ফলে সমুদ্র এলাকায় বিস্তৃত অঞ্চলে ভারত এবং মিয়ানমার এর সঙ্গে বাংলাদেশের যে বিরোধ ছিল তার অবসান হয়েছে। বলা আবশ্যক, আন্তর্জাতিক বিরোধের দরুন ২০০৮ সালে এবং ২০১২ সালে আহবান করা দরপত্রে বিদেশী তেল কোম্পানির কাছ থেকে যথেষ্ট সাড়া মেলেনি।<br />\r\n২০০৮ সালে বাংলাদেশ যখন তেল ও গ্যাস সম্পদ অনুসন্ধানের জন্য দরপত্র আহবান করে তখন ভারত এবং মিয়ানমার এই যুক্তিতে আপত্তি তোলে যে কয়েকটি ব্লকে তাদের মালিকানা আছে।<br />\r\n২০০৮ সালের ফেব্র&ldquo;য়ারি মাসে সমুদ্র এলাকায় বাংলাদেশ ২৮টি ব্লকের বিপরীতে দরপত্র আহবান করে যার মধ্যে ২০টি ছিল গভীর সমুদ্রে এবং ৮টি ছিল অগভীর সমুদ্রে।<br />\r\n২০১১ সালের ১৬ই জুন যে দরপত্র আহবান করে তার মধ্যে ৩ বছর যাবৎ বহু সংখ্যক আলোচনা সভার পর মাত্র ২টি গভীর সমুদ্র এলাকায় কাজ বন্টন করতে পেরেছে যথা ব্লক-১০ এবং ব্লক-১১ যা মার্কিন যুক্তরাষ্ট্রের কনোকো-ফিলিপসকে দেওয়া হয়।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">পরে কনোকো-ফিলিপস এই ২টি ব্লকে তাদের অনুসন্ধান কাজ বন্ধ করে দেয় এই যুক্তিতে যে এগুলি অর্র্থনৈতিকভাবে সম্ভাবনাময় নয়।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এদিকে ২০০৮ সালের দরপত্র অনুযায়ী অগভীর সমুদ্র ব্লক-৫, যুক্তরাজ্যের তাল্লোকে দেওয়া হয়। কিন্তু ভারতের আপত্তির কারণে উৎপাদন অংশীদারিত্ব চুক্তি সই করা সম্ভব হয়নি।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">তবে ২০১২ সালের দরপত্র অনুযায়ী অগভীর সমুদ্রের ব্লক-১১ অস্ট্রেলিয়ার স্যান্তোস এবং সিঙ্গাপুরের ক্রিস এনার্জিকে দিতে সক্ষম হয় এবং ও.এন.জি.সি বিদেশ (তেল ও প্রাকৃতিক গ্যাস কর্পোরেশন বিদেশ) এবং অয়েল ইন্ডিয়া লি: এর মধ্যে জয়েন্টভেঞ্চার এর মাধ্যমে অগভীর সমুদ্রের ব্লক-৪ এবং ব্লক-৯ প্রদান করতে সক্ষম হয়।<br />\r\n২০১২ সালের দরপত্রে ৩টি গভীর সমুদ্র ব্লক যথা ব্লক-১২, ব্লক-১৬ এবং ব্লক-২১ এর জন্য প্রত্যেকটি ব্লকের জন্য ১টি করে দরপত্র পাওয়া যায়। এতে মার্কিন যুক্তরাস্ট্রের কনোকো-ফিলিপস এবং নরওয়ের স্ট্যাট অয়েল এই দুটি সংস্থার জয়েন্টভেঞ্চার অংশ গ্রহণ করে। এদের সঙ্গে এই ৩টি ব্লকের জন্য কোন অংশীদারিত্ব চুক্তি সই করা হয়নি।<br />\r\nবঙ্গোপসাগরে ভারত এবং মিয়ানমার বিরাট আকারের গ্যাস সম্পদ আবিষ্কার করেছে। তেমনি বাংলাদেশের পক্ষে সমুদ্র এলাকাতেও২০১৪ জুনে বঙ্গোপসাগরে গ্যাস সমৃদ্ধ এলাকায় বিবদমান পক্ষের মধ্যে বাংলাদেশের অনুকূলে জাতিসংঘ সালিশী রায় ঘোষণার পর অপ্রত্যাশিতভাবে বাংলাদেশ বিরাট জ্বালানী সম্পদের মালিকানা পেল। প্রশ্ন রয়ে যাচ্ছে এরপরও বিদেশী বিনিয়োগকারীরা কি আগ্রহী হবেন?<br />\r\nএটা নিশ্চিত যে, ইতিপূর্বে জাতিসংঘ প্রদত্ত সীমানা সালিশের মাধ্যমে ভারতের দাবীকৃত অংশ থেকে প্রায় ২০,০০০ বর্গ কি.মি. গ্যাস সমৃদ্ধ সমুদ্র এলাকায় অধিকার লাভের মাধ্যমে বাংলাদেশ জুলাই মাসে জ্বালানীশক্তির এক বিরাট সম্পদ অর্জন করেছে। তবুও সম্ভাবনা থেকেই যায় যে আমরা যে সত্যিই লাভবান এটা চিন্তার মধ্যেও আনতে পারি না।<br />\r\nঅধিকাংশ বিনিয়োগকারীর কাছে এলাকাটিতে বিনিয়োগ অত্যধিক ঝুঁকিপূর্ণ, কারণ রাজনৈতিক টানাপোড়েন, সামগ্রিক দারিদ্য, অত্যধিক মূল্যছাড় ভিত্তিক অর্থনীতি, যা প্রবৃদ্ধিতে বিঘ&oelig; সৃষ্টি করে এবং জ্বালানীশক্তির অবকাঠামোতে বিনিয়োগ সংকীর্ণ করে। রিয়াদ, দোহা এবং দুবাই এর মত এয়ারকন্ডিশন করা বিলাস পণ্য সমৃদ্ধ বিপণি বিতান এর তুলনায় ঢাকা ও চট্রগ্রাম বস্তিতে ভরা অবস্থার পর্বতপ্রমাণ বৈষম্যও একটি কারণ।<br />\r\nতবুও আংশিক বিবেচনা সামগ্রিক পরিস্থিতির পরিচায়ক নয়। দারিদ্য ও সংঘাতপূর্ণ পরিবেশ সত্ত্বেও নাইজেরিয়া, চাদ ও ভেনিজুয়েলার মত দেশ ভালো অবস্থানে আছে। তবুও বাংলাদেশের দুর্যোগময় পরিস্থিতিতে বিনিয়োগকারীরা আকৃষ্ট হচ্ছে না কেন তা বোধগম্য। বস্তুতঃ বিপরীতটিই ঘটেছে। অস্ট্রেলিয়াভিত্তিক স্যান্টোস গত বছর বাংলাদেশের একমাত্র সামুদ্রিক জলসীমানায় অবস্থিত গ্যাসকূপ কাজ বন্ধ রেখেছে উৎপাদনের সামান্য সম্ভাবনা দেখিয়ে। সিঙ্গাপুরের কোম্পানি ক্রিস এনার্জির সহযোগিতায় এই বছর অগভীর সমুদ্রে কূপ খননের পরিকল্পনা হাতে নিয়েছে স্যান্টোস। জাতীয় জ্বালানীসংস্থা পেট্রোবাংলা সমুদ্রে কূপ খননের স্বত্বের জন্য ২০১২ সালের আহবান করা টেন্ডারে মাত্র দুটি সংস্থা থেকে দরপত্র লাভ করে। তারা ছাড়া ভারতের ও.এন.জি.সি বা তেল ও প্রাকৃতিক গ্যাস কর্পোরেশন ও হিউস্টন ভিত্তিক কনোকো-ফিলিপস গত বছর এপ্রিলে বিতর্ক মুক্ত এলাকায় পেট্রোবাংলা কর্তৃক দরপত্র আহবানে সাড়া দিয়েছে, আর কেউই দেয়নি। প্রতিযোগীরা অভিযোগ করেছে যে (১) স্থল ভাগের ব্লকগুলিতে কূপ খননের সুবিধা দেয়া হয়নি এবং (২) পেট্রোবাংলা কূপ খননের ব্যাপারে ভয়ানক শর্ত জুড়ে দিয়েছে। এ বছরের প্রথম দিকে সমুদ্রে ৩০,০০০ ফুট গভীর পানিতে তিনটি গভীর সমুদ্রের ব্লকে কনোকা এবং নরওয়ের স্ট্যাটঅয়েল যৌথভাবে প্রতিযোগিতা করে। পরবর্তীতে গভীর সমুদ্র ব্লকে কনোকো আরও দুটি ব্লকে চুক্তি করতে চায়। এই মুষ্টিমেয় কোম্পানীগুলির বাইরে খুব অল্প সংখ্যক কোম্পানী বাংলাদেশে কাজ করার আগ্রহ দেখিয়েছে।<br />\r\nএখন সম্ভবত বাজি রাখার মত সুন্দর পরিস্থিতি বিদ্যমান। নতুন প্রাপ্ত সীমানায় বাংলাদেশের প্রাকৃতিক গ্যাসের মজুদ ২০০ ট্রিলিয়ন ঘনফুটে দাঁড়াতে পারে-যা এশিয়া প্রশান্ত মহাসাগর অঞ্চলে সর্ববৃহৎ। এ বছরের শেষ দিকে পেট্রোবাংলা ১৮টি নতুন তেল ও গ্যাস ক্ষেত্রের জন্য দরপত্র আহ্বান করবে। রিজার্ভের সম্পূর্র্ণ অংশ উৎপাদনের অধীনে আনতে পারবে ধরে নিলে বাংলাদেশ পৃথিবীর মধ্যে সর্ববৃহৎ প্রাকৃতিক গ্যাস উৎপাদনকারী দেশগুলির মধ্যে অন্যতম হতে পারে। একটি বড় &lsquo;কিন্তু&rsquo; অপেক্ষা করছে। রিজার্ভে কতটা সত্যিই পাওয়া যাবে তা কেউ নিশ্চিত করে বলতে পারে না। এই নবলদ্ধ এলাকার মজুদ ১০০ বা ২০০ ট্রিলিয়ন ঘনফুট এমনকি শূন্য হতে পারে, নিশ্চিত করে তা কেউ বলতে পারে না। বাংলাদেশের গ্যাস মজুদের পরিমাণ &lsquo;নিশ্চিত&rsquo; ক্যাটাগরিতে নয়। বর্তমান ভূতাত্ত্বিক ও প্রকৌশলগত তথ্যের ভিত্তিতে প্রাককলনটির যথার্থতা ৫০:৫০।<br />\r\nজ্বালানি শক্তি আহরণে বাংলাদেশের একটি সামগ্রিক জরিপ প্রয়োজন। কিন্তু বাংলাদেশ সরকারের পক্ষে গভীর সমুদ্রে কূপ খননের দক্ষতার অভাব আছে। সমুদ্র বিশেষজ্ঞেরও অভাব আছে। তদুপরি জ্বালানি নিঃসরণে বিপদ থেকে বাঁচার জন্য আইনের অভাব আছে। বাপেক্স এ যাবৎ পরীক্ষামূলক কূপ খনন করেছে। কিন্তু তাদের পক্ষে নতুন এলাকায় মজুদ অনুসন্ধানে অর্থ বিনিয়োগ ও অবকাঠামোগত জ্ঞান','',1,15,'2016-01-07 03:09:42',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157782_03.png'),(18,'তালপট্টির ১০০ টিসিএফ গ্যাস প্রসঙ্গ| ডঃ বদরুল ইসলাম','তালপট্টির-১০০-টিসিএফ-গ্যাস-প্রসঙ্গ|-ডঃ-বদরুল-ইসলাম','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">তালপট্টিগত ৭ জুলাই আন্তর্জাতিক আদালত কর্র্তৃক বাংলাদেশের সঙ্গে ভারতের সমুদ্রসীমানা নিষ্পত্তি মামলার রায় প্রকাশ হওয়ার পর বাংলাদেশে সমুদ্রর বিজয় উৎসব বা সভা&Ntilde;শোরগোল হতে দেখা যায়নি, যেমনটি দেখা গিয়েছিল দুই বছর আগে বাংলাদেশের সঙ্গে মিয়ানমারের সমুদ্রসীমানা বিরোধ মামলার রায় প্রকাশ হওয়ার পর সামদ্রিক ভাবে সমুদ্রের জলসীমানা পাওয়া না-পাওয়ার বিষয়টি বাদ দিয়ে কেবল তেল-গ্যাস ব্লক বিবেচনায় আনলে এটি অনস্বীকার্য যে মিয়ানমারের সঙ্গে নতুন সীমানা নির্ধারণের ফলে বাংলাদেশের দাবিকৃত ছয়টি গভীর সমুদ্রর ব্লক মিয়ানমারের হাতে চলে যায় এবং তার ফলে বাংলাদেশ ওই সমুদ্রর এলাকা হারায়। বিষয়টি বিজয় উল্লাসের ডামাডোল ও শোরগোল কেবলই ঢাকা পড়ে থাকে। তবে ভারতের সঙ্গে নতুন নির্ধারিত সমুদ্রসীমানায় তেল-গ্যাস ব্লক বিবেচনায় বাংলাদেশ প্রকৃতই বিজয় হয়। এখানে ভারতের দাবি করা কোনো গ্যাস ব্লকই বাংলাদেশের হাতছাড়া হয়নি। ভারতের সঙ্গে সীমানা নির্ধারণে প্রকৃতপক্ষে বিজয়ী হলেও এ নিয়ে বিজয় উৎসব বা সভা শোরগোল না করার সিদ্ধান্তটি সরকারি মহলের অধিকতর কুটনৈতিক প্রজ্ঞার পরিচয় হোক অথবা ভারতকে বিরত না করার মানসিকতাই হোক, বাংলাদেশের এই সংযত আচরণ কূটনৈতিক মহলে কিছুটা হলেও প্রশংসা পাওয়ার যোগ্য।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">তবে এবার শোরগোল উঠেছে অপর এক বিষয়ে। ভারতের একটি সংবাদমাধ্যমের সূত্রে প্রচারিত একটি খবর এবার বিতর্ক আলোচনায় স্থান পায় যে সংবাদটিকে কেন্দ্র করে নতুন এই বিতর্ক তার সূত্র একটি ভারতীয় টেলিভিশন চ্যানেল ভারতের নয়াদিল্লি টেলিভিশন (এনডিটিভি) তার প্রচারিত একটি অনুষ্ঠানে দাবি করে যে তাদের কাছে সরকারি অভ্যন্তরীণ নথির সূত্রে খবর রয়েছে যে সাগরে নিমজ্জিত তালপট্টি দ্বীপটির স্থানে ভূগর্ভে ১০০ টিসিএফ গ্যাসের অস্তিত্ব পাওয়া গেছে। এনডিটিভি এ তথ্য কোনো তেল কোম্পানি বা অনুসন্ধানী সংস্থার সূত্র থেকে জানা গেছে কি না, সে বিষয়ে কিছু জানায়নি। কেবল আবিষ্কারটি ২০০৬ সালে ঘটেছে বলে প্রচার করেছে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯৭০ সালে প্রলয়ঙ্করী ঝড়ের পর তালপট্টি দ্বীপটি সমুদ্র থেকে জেগে ওঠে। আর এর অবস্থানটি হলো ভারত-বাংলাদেশ সীমান্ত নদী হাড়িয়াভাঙ্গা যেখানে সাগরে এসে মিশেছে, ঠিক তার মুখে। বিগত প্রায় ৪০ বছর ভারত ও বালাদেশ উভয়ই সমুদ্র সীমানা নিয়ে যেমন পরস্পর বিরোধপূর্ণ অবস্থায় ছিল, তালপট্টি দ্বীপটি আবিষ্কারের পর থেকে উভয় দেশই এটিকে তার নিজের বলে দাবি করে আসে। তালপট্টি দ্বীপটি ২০১০ সালে সমুদ্রগর্ভে নিমজ্জিত হয়ে যায়। তবে আন্তর্জাতিক আদালত কর্তৃক সমুদ্রসীমানা বিরোধ মামলার রায় অনুযায়ী ওই দ্বীপের স্থানটি ভারতের অংশে পড়েছে। তাই বিতর্ককারীদের অভিমত এই যে তালপট্টির অস্তিত্ব না থাকলেও তার নিচে পাওয়া ১০০ টিসিএফ গ্যাস হাতছাড়া হওয়াটা বাংলাদেশের জন্য বিরাট বড় ক্ষতি।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কোনো এক স্থানে ১০০ টিসিএফ গ্যাস থাকা বিশেষভাবে তাৎপর্যপূর্ণ, কারণ তা বিশাল পরিমাণ। বাংলাদেশ বর্তমানে প্রতি বছর প্রায় এক টিসিএফ গ্যাস ব্যবহার করে বা চাহিদা আছে এবং সে হিসাবে ১০০ সিটিএফ গ্যাস ১০০ বছরের জোগান। বাংলাদেশের গ্যাসের বর্তমান মজুত প্রায় ১২ টিসিএফ, ভারতের প্রায় ৫০ টিসিএফ (আলোচিত ১০০ টিসিএফ না ধরে), মিয়ানমারের ১০ টিসিএফ এবং পাকিস্তানের ২৫ টিসিএফ সংরক্ষিত মিয়ানমার সমুদ্রবক্ষে তিনটি গ্যাস ক্ষেত্র আবিষ্কার হওয়ার পর থেকে তা পাওয়ার জন্য ভারত ও চীন যে প্রতিযোগিতা ও রাজনীতি কূটনীতির চাল চালে, তা লক্ষণীয়। আন্তর্জাতিক ভাবে ছড়িয়ে পড়া সংবাদ সূত্রে জানা যায়, শেষ পর্যন্ত চীন ৩০ বছরের চুক্তির মাধ্যমে মিয়ানমারের এ গ্যাস কিনতে রাজি হয়েছে। উল্লেখ্য, মিয়ানমারের ওই গ্যাস মজুতের পরিমাণ পাঁচ থেকে ছয় টিসিএফ সম্পর্কিত ভারতের অন্ধ প্রদেশে চেন্নাই উপকূলে প্রাপ্ত গ্যাস ক্ষেত্রগুলো ভারতের গ্যাস আর্শীবাদ হিসেবে গণ্য হয়েছে। ভারত এই আবিষ্কারের পর তাকে ভারতের ইতিহাসে সর্বোচ্চ বড় গ্যাস আবিষ্কার হিসেবে গণ্য করে, সামগ্রিকভাবে যার মোট পরিমাণ ২০ থেকে ২৫ টিসিএফ বলে দাবি করা হয়। উপরিউক্ত তথ্যগুলো থেকে এটি সম্পূর্ণই অনুধাবন করা যায় যে, কোনো একটি স্থানে ১০০ টিসিএফ মজুতের সন্ধান একক ভাবেই আলোচনার দাবিদার।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কোনো এলাকায় তেল-গ্যাস আবিষ্কৃত হলে, কোন তেল কোম্পানি (বা কোম্পানি গোষ্ঠী) এটি আবিষ্কার করেছে, কারাই-বা সেখানে প্রাথমিক ও চূড়ান্ত জরিপ করল, কত বছর যাবৎ কোম্পানিটি জরিপকাজ চালিয়েছে, কত গভীরতায়, কোন ভূতাত্ত্বিক গঠনে গ্যাসের অবস্থানটি নিশ্চিত করা গেল ইত্যাদি তথ্য বিশ্বের নানাবিধ পেট্টোলিয়াম মনিটরিং এজেন্সির মাধ্যমে তাৎক্ষণিক জানা যায়। কেবল তা-ই নয়, ছোট বা বড় আবিষ্কারের পরপরই আবিষ্কারক নিজের স্বার্থে তার আবিষ্কারকে পারলে আরও বড় করে সংবাদ মাধ্যমে তুলে ধরতে চায়, যাতে শেয়ারবাজারে তার মূল্য বৃদ্ধি পায় বা সে আরও সুযোগ বলে নিজেকে প্রমাণ করতে পারে। গ্যাস আবিষ্কার কোনো সামরিক কর্মকাণ্ড নয়, তাই পৃথিবীর কোথাও এটি গোপনে করা হয় না; অধিকন্তু খোলা আকাশের নিচে অনুসন্ধানের বিশাল কর্মকাণ্ড গোপনে করাও যায় না। ভারতীয় টিভি চ্যানেল এনডিটিভি ১০০ টিসিএফ গ্যাসের ব্যাপারে এ বিষয়ে কোনো তথ্য দেয়নি।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বিশ্বের আন্তর্জাতিক পেট্রোলিয়াম কার্যক্রম মনিটরিং এজেন্সিগুলোর সূত্রে তালপট্টির নিচে গ্যাস আবিষ্কার বা অনুসন্ধান কার্যক্রমের কোনো খবর পাওয়া যায় না। বাংলাদেশ&Ntilde;ভারত বিতর্কিত সীমান্তে তালপট্টিতে বড় আকারের গ্যাস অনুসন্ধান খনন ও আবিষ্কার বাংলাদেশে দৃশ্যমান হবে তা নিশ্চিত; কিন্তু এ ধরনের কোনো কার্যকলাপ কখনো লক্ষ্য করা যায়নি। সর্বোপরি, ভারতের পেট্রোলিয়াম মিনিষ্ট্রির অধীনে তেল-গ্যাস ব্যবস্থাপনা, পর্যবেক্ষণ ও নথিভুক্তকরণের দায়িত্বে নিয়োজিত ডাইরেক্টর জেনারেল অব হাইড্রোকার্বনের সমূহ রেকর্ডে (ওয়েবসাইটেও প্রকাশিত) তালপট্টি এলাকায় কোনো গ্যাস অনুসন্ধান খনন বা আবিষ্কারের তথ্য নেই। উল্লেখ্য, সংস্থাটি ভারতের যেকোনো স্থানে যেকোনো গ্যাস-তেল অনুসন্ধান ও আবিষ্কারের তথ্য নথিভুক্ত করে প্রকাশ করে। অর্থাৎ এনডিটিভি সূত্রে প্রচারিত তালপট্টি ১০০ টিসিএফ গ্যাসের খবরের কোনো প্রাতিষ্ঠানিক বা দৃশ্যমান সূত্র নেই। তথাপি, তর্কের খাতিরে এর সত্যতা গ্রহণ করলেও প্রশ্ন ওঠে যে ভারত যেখানে তীব্র গ্যাস চাহিদা সামলাতে অস্থির এবং তারা যখন বাংলাদেশ বা মিয়ানমারের গ্যাস আমদানি করতে প্রতিযোগিতায় লিপ্ত, সে অবস্থায় তালপট্টিতে ২০০৬ সালে আবিষ্কৃত এত বিশাল গ্যাস মজুতের উত্তোলন ও সরবরাহ করে গ্যাস-সংকট কাটায় না কেন? এর কারণ কি এই যে গ্যাস আবিষ্কারের এই সংবাদ বাস্তববর্জিত।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এখন আরেকটি বিষয় আলোচনায় আনা যেতে পারে। তালপট্টি বাংলাদেশ ও ভারতের পশ্চিমবঙ্গব্যাপী বিস্তৃত একটি ভূতাত্ত্বিক বেসিনের (যার নাম বেঙ্গল বেসিন) পশ্চিম অংশে অবস্থিত। এই বেসিনে বিগত ৫০ বছরে অনেক অনুসন্ধান কূপ খননেন মাধ্যমে এর বিভিন্ন অংশে গ্যাস-তেল পাওয়া ও পাওয়ার সম্ভাবনার তুলনামূলক ভূতাত্ত্বিক বিশ্লেষণ করা হয়েছে। এই বিশ্লেষণে প্রতীয়মান হয় যে বেসিনের পূর্বাংশই (কুমিল্লা-চট্টগ্রাম ও তার সংলগ্ন সমুদ্রর জলসীমা) অধিকতর গ্যাস সম্ভাবনাময় ও এখানেই অধিকাংশ বড় গ্যাস ক্ষেত্র আবিষ্কৃত হয়েছে। পক্ষান্তরে এই বেসিনের পশ্চিমাংশে (বাংলাদেশের পশ্চিম অঞ্চল ও সমুদ্রসীমা এবং ভারতের পশ্চিমবঙ্গ ও তার সমুদ্রসীমা অপেক্ষাকৃত কম সম্ভাবনাময়। প্রকৃতপক্ষে এই পশ্চিমাংশে (ভারতের পশ্চিমবঙ্গসহ) এখন পর্যন্ত একটি একটি গ্যাসক্ষেত্রও আবিষ্কৃত হয়নি। তাই এই পশ্চিমাংশের একটি স্থানে ১০০ টিসিএফ গ্যাস থাকা অস্বাভাবিক ও তা ইতিমধ্যে জ্ঞাত ভূতাত্ত্বিক গঠন ও বিন্যাসের সঙ্গে সামঞ্জস্যপূর্ণ নয়।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">তাহলে প্রশ্ন ওঠে, তালপট্টির ১০০ টিসিএফ গ্যাস আবিষ্কারের প্রচার কি উদ্দেশ্যপ্রণোদিত? যদি তা-ই হয়, তবে কারা বা কী উদ্দেশ্যে এটি করা হয়েছে? ভারত-বাংলাদেশ সমুদ্রসীমানা বিরোধ মামলার রায়ে ভারত সমুদ্র এলাকার যে দাবি করেছিল, তা অর্জন করতে প্রায় পুরোপুরি ব্যর্থ হয়েছে। বিরোধপূর্ণ ২৫ হাজার বর্গকিলোমিটার সমুদ্র এলাকার মধ্যে প্রায় ২০ হাজার বর্গ কিলোমিটার পেয়েছে বাংলাদেশ, যা কিনা মোট আয়তনের পাঁচ ভাগের চার ভাগ। এ ক্ষেত্রে বাংলাদেশের জয় ও ভারতের পরাজয়কে ভারতীয় কূটনীতিকেরা হাসিমুখে মেনে নিলেও জনগণের কাছে ভারত সরকারের দায়বদ্ধতা কমে যায়নি, বরং এটি ভারত সরকারের ওপর ভেতর থেকে চাপ হিসেবেই বিদ্যমান রয়েছে। আর এ চাপ লাঘব করতে জনগণকে কোনো বিকল্প পন্থায় আশ্বস্ত করা প্রয়োজন। তাই তালপট্টি দ্বীপ নিমজ্জিত হলেও ওই স্থান ভারতের পক্ষে পাওয়ার ফলে ১০০ টিসিএফ গ্যাস তো ভারতের পাওনা হলো এ ধরনের প্রচারণা দিয়ে কি ভারত সরকার তার জনগণকে আশ্বস্ত করতে চাইছে? কূটনৈতিক শাস্ত্রমতে এ সম্ভাবনা যতটা না সত্য বলে মনে হতে পারে, গ্যাস ভূবিজ্ঞানের সূত্রে তা অধিকতর সত্য প্রতীয়মান হয়।</span></p>\r\n','',1,15,'2016-01-07 03:10:49',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157849_talpotty.png'),(19,'রামুতে হামলা: অসাম্প্রদায়িক চেতনা রক্ষা প্রসঙ্গ। বদরুদ্দীন উমর','রামুতে-হামলা:-অসাম্প্রদায়িক-চেতনা-রক্ষা-প্রসঙ্গ।-বদরুদ্দীন-উমর','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাঙলাদেশে যেমনটি হয়। এক একটা ঘটনা ঘটে পুকুর চুরি থেকে নিয়ে সাগর চুরি, রোমহর্ষক হত্যাকাণ্ড, মাফিয়া সংগঠনের হামলা বা এ ধরনের কিছু। ঘটনার অব্যবহিত পর এ নিয়ে অনেক হইচই হয়, প্রচারমাধ্যম তোলপাড় করে, কোন বিশেষ ব্যাপার আদালত পর্যন্ত গড়ায়। কিন্তু কিছু দিন পর আর একটি ঘটনা ঘটলে তাই নিয়ে শুরু হয় একই প্রতিক্রিয়ার পুনরাবৃত্তি। পুরনো ঘটনা ধামাচাপা পড়ে। অপরাধীদের শাস্তি হয় না। তারা নোতুন নোতুন অপরাধ ঘটায়। এই বৃত্তপথেই বাঙলাদেশের ঘটনাবলি ঘুরপাক খেতে থাকে।<br />\r\nবাঙলাদেশের অপরাধ জগতে ২৯ সেপ্টেম্বর এক নোতুন ঘটনা ঘটে। ঐ দিন রাতে হাজার হাজার সংগঠিত ট্রাকযোগে বাইরে থেকে গিয়ে বক্সবাজারের রামুতে অনেক বৌদ্ধ মন্দির ও মঠে এবং সেই সঙ্গে সাধারণ বৌদ্ধ গৃহস্থদের বাড়ীতে পেট্রল, গানপাউডার ইত্যাদি দিয়ে অগ্নিসংযোগ করে। এর ফলে এই মন্দির ও মঠগুলোর কাঠামো ধ্বংস হয়, বুদ্ধমূর্তি ধ্বংস ও ক্ষতিগ্রস্ত হয়। বহু পুরনো বৌদ্ধধর্মীয় গ্রন্থ পুরে ছাই হয় এবং হাজার হাজার নিরাপরাধ বৌদ্ধ গৃহহীন হয়ে পথে বসে। বাঙলাদেশে অনেক ধরনের অপরাধের ঘটনা নিয়মিতভাবেই ঘটে চলেছে। কাজেই অপরাধ যত বড় মাপেরই হোক, কেউ তাতে বিস্মিত হয় না। কিন্তু এহেন বাঙলাদেশে রামুতে ২৯ সেপ্টেম্বর যা ঘটেছে এটা বিস্ময়কর। শান্তি প্রিয়, নিরাপরাধ এবং প্রতিবেশী অন্য ধর্মাবলম্বীদের সঙ্গে বিরোধহীন জীবনযাপনকারী বৌদ্ধদের ওপর এই আক্রমণ সত্যিই এক বিস্ময়কর ব্যাপার।<br />\r\nযেভাবে ফেসবুকে একটি ছবিকে কেন্দ্র করে অল্প সময়ের মধ্যে এই ঘটনা ঘটেছে, একে র্দীঘ সময় ধরে পরিকল্পিত ব্যাপার ছাড়া অন্য কিছু মনে করার উপায় নেই। কেউ কেউ একে চক্রান্ত বলতে নারাজ। বাঙলাদেশে যা ঘটে তাকে নাকি সব সময় চক্রান্ত বলা হয়! প্রায়ই বড় ধরনের ঘটনাকে বিদেশী ও সা&curren;্রাজ্যবাদী চক্রান্ত বলা হয়!! এই দৃষ্টিভঙ্গি নাকি ভ্রান্ত!! কিন্তু বাঙলাদেশে শেয়ারবাজার কেলেংকারি, সোনালী ব্যাংকের সাগর চুরি, ডেসটিনি গ্র&ldquo;পের বিরাট লুটতরাজ, সাগর-রুনীর হত্যাকাণ্ড ইত্যাদি যেসব ঘটনা ঘটেছে এগুলো কি চক্রান্ত ছাড়া ঘটা সম্ভব ? চক্রান্ত কথাটিতে যদি কারও আপত্তি থাকে তাহলে বলা যেতে পারে যে, পরিকল্পনাহীনভাবে কি এ ধরনের ঘটনা ঘটতে পারে? কে বা কারা এসব চক্রান্ত করে সেটা দেখার বিষয়, তার জন্য তদন্ত দরকার। কিন্তু চক্রান্ত ছাড়া এ ধরনের কাজ সম্ভব এটা মনে করা কি যুক্তিসঙ্গত? এটা কি বাস্তবসম্মত? মনে রাখা দরকার যে, বিপুল অধিকাংশ অপরাধই চক্রান্তের ফল। এই চক্রান্ত এক ব্যক্তির দ্বারা হতে পারে, আবার তা হতে পারে অনেক ব্যক্তির সমন্বয়ে। একথা যিনি অস্বীকার করেন তার দ্বারা কোন অপরাধ বিষয়ে সঠিক ধারণায় উপনীত হওয়া সম্ভব নয়, কোন সঠিক তদন্ত।<br />\r\nরামুতে বৌদ্ধ সম্প্রদায়ের ওপর যে অতর্কিত হামলা হয়েছে তার কোন বাস্তব ভিত্তি বাঙলাদেশের সমাজভূমিতে নেই। এই সাম্প্রদায়িকতার কোন পূর্ব ইতিহাস নেই। বাঙলাদেশে আজ যেখানে হিন্দু-মুসলমান সাম্প্রদায়িক রাজনৈতিক প্রতিক্রিয়াবহির্ভূত, যেখানে ভারতের মতো হিন্দু-মুসলমান সাম্প্রদায়িক দাঙ্গা-হামলা দাঁড়িয়েছে অতীতের ব্যাপারে, সেখানে বৌদ্ধদের বিরুদ্ধে এই সাম্প্রদায়িক হামলা যে স্বতঃস্ফূর্ত এটা কোন তথ্য বা যুক্তির ভিত্তিতে মনে করা যেতে পারে? এবং এটা স্বতঃস্ফূর্ত না হলে অবশ্যই পরিকল্পিত। একে চক্রান্ত বলতে যারা নারাজ তাদের স্বীকার করতে হবে যে, এটা পরিকল্পিত।<br />\r\nএ ঘটনাকে নিয়ে এখন যে এক প্রকার সরলীকরণ হচ্ছে তা হাস্যকর। সরকার ও প্রধান বিরোধী দল বিএনপি পরস্পরকে চক্রান্তকারী বলে এ ঘটনার জন্য দায়ী করছে, দায়িত্বশীল অবস্থানে থাকা সত্ত্বেও যেভাবে তারা ঘটনার পর দ্রুত এ ঘটনার দায় নির্দিষ্ট করেছে এর মধ্যে বাঙলাদেশের শাসকশ্রেণীর দেউলিয়াপনার স্বাক্ষর থাকলেও কোন সত্যতা নেই। এদিক দিয়ে সব থেকে উল্লেখযোগ্য হল প্রধানমন্ত্রীর বক্তব্য। তিনি রামুতে গিয়ে ঘোষণা করেছেন যে, ২৯ সেপ্টেম্বর ধ্বংসযজ্ঞ স্থানীয় বিএনপি সংসদ সদস্যের কাজ! তিনিই এই কাণ্ড ঘটিয়েছেন!! তিনি একথা বললেও স্থানীয় কোন দলের নেতাকর্মীরা তাকে এই তথ্য দেননি এবং এর পক্ষে কোন প্রমাণ দাখিল করেননি। কাজেই তার এই হাওয়াই বক্তব্যের কোন বাস্তব ভিত্তি নেই। এর ফলে এ সম্পর্কে রামুতে তার কথাবার্তা ও গরম বক্তৃতা দাঁড়িয়েছে অপ্রয়োজনীয়তার মত। প্রধানমন্ত্রীর তো অপ্রয়োজনীয় কথাবার্তা বলা ঠিক নয়। পদটি গুরুত্বপূর্ণ। এ পদের ওজন আছে। কিন্তু ভাঁড়ের তো কোন ওজন নেই।<br />\r\nআসলে রামুতে যা ঘটেছে তা অতি গুরুত্বপূর্ণ। এ ঘটনাকে হালকাভাবে দেখা অথবা নির্বাচনী প্রচারণার উদ্দেশ্য ব্যবহার করাও একটা অপরাধমূলক কাজ। কারণ এই ঘটনার সঠিক ঘটক যদি আমরা চিহ্নিত করতে পারি, এর লক্ষ্য-উদ্দেশ্য বিষয়ে যদি নিজেদের অবহিত করতে না পারি তাহলে বিপজ্জনক বাঙলাদেশে আমরা নিজেদের বিপদ আরও বৃদ্ধি করব।<br />\r\nঘটনার পর ১ অক্টোবর দৈনিক সমকাল এবং ৩ অক্টোবর দৈনিক আমারদেশে আমি লিখেছিলাম যে, এ ঘটনা কোন বিশেষ রাজনৈতিক দল ঘটায়নি, তবে এ ঘটনার সঙ্গে আওয়ামী লীগ, বিএনপি, জামায়েত ইসলামী ইত্যাদি পার্টির দলীয় লোকেরা জড়িত ছিল। বিস্ময়কর ব্যাপারে এই যে, এদের মধ্যে সাধারণভাবে ঐকমত্য না থাকলেও এবং তারা পরস্পরের প্রতি বিদ্বেষের বশবর্তী হওয়া সত্ত্বেও ২৯ তারিখে এই দলগুলোর লোকদের মধ্যে যথেষ্ট সমন্বয় এবং ঐক্য দেখা যায়! অর্থাৎ এরা স্থানীয় পরিস্থিতি থেকে উদ্ভূত কোন তাগিদে নয়, বাইরের কোন শক্তির দ্বারা প্ররোচিত ও ব্যবহৃত হয়েই ঐক্যবদ্ধভাবে এই অপরাধ সংঘটিত করেছে। বাঙলাদেশের সরকারী বা বিরোধী কোন দল এই ঘটনার চক্রান্তকারী বা পরিকল্পনাকারী না হওয়ায় এটা যুক্তিসঙ্গতভাবেই বলা চলে যে, বাইরের কোন শক্তি বাঙলাদেশে তাদের নিজেদের প্রয়োজনে এবং নিজেদের জন্য অনুকূল পরিস্থিতি তৈরীর তাগিদেই এ কাজ করেছে। এই বিদেশী শক্তি কে বা কারা এটা এই মুহূর্তে প্রাপ্ত তথ্য থেকে নিদিষ্ট করা সম্ভব না হলেও তারা যে বাইরের শক্তি এ বিষয়ে সন্দেহ করার সঙ্গত যুক্তি নেই। এই শক্তি বাঙলাদেশের বিভিন্ন দল, প্রশাসন, পুলিশ ইত্যাদির লোকের মধ্যে পরিকল্পিতভাবে এই হামলার নীলনকশা তৈরী করেছে। এখানে এটাও অবশ্যই বলা দরকার যে, এই বিদেশী শক্তি এমন হতে পারে না যার শক্তিশালী অবস্থান বাঙলাদেশে নেই। সে অবস্থান না থাকলে নিজেদের গোয়েন্দা বাহিনীর মাধ্যমে তারা অন্য দেশীয় লোকদের নিয়ে তাদের পরিকল্পনা কার্যকর করতে পারত না।<br />\r\nএ ক্ষেত্রে প্রথমেই মনে রাখা দরকার যে, বঙ্গোপসাগর ও প্রশান্ত মহাসাগরীয় অঞ্চলে বাঙলাদেশ এবং বৌদ্ধধর্মাবলম্বী অধ্যুষিত বার্মা বা মিয়ানমার এখন স্ট্র্যাটেজির দিক দিয়ে ইউরোপ, আমেরিকা, ভারত, চীন সবার জন্যই আগের যে কোন সময়ের থেকে গুরুত্বপূর্ণ এবং এই গুরুত্ব দিন দিন বাড়ছে। মিয়ানমারের শাসকশ্রেণীর মধ্যেও যে ধরনের পরিবর্তন দেখা যাচ্ছে সেটাও খুব লক্ষ্য করার মতো। বাঙলাদেশ ও মিয়ানমারের সমুদ্র সীমান্তে তেল-গ্যাসের মজুদও হিসাবযোগ্য ব্যাপার। মার্কিন যুক্তরাষ্ট্র এরই মধ্যে ঘোষণা করেছে যে, তারা তাদের স্ট্রাটেজি পরিবর্তন করে এখন ইউরোপ থেকে সরিয়ে এন প্রাশান্ত মহাসাগরীয় অঞ্চলে তাদের অধিকাংশ শক্তি কেন্দ্রীভূত করবে। প্রতিবেশী দেশ হলেও মিয়ানমারের সঙ্গে বাংলাদেশ এখন নানা সূত্রে বেশী করে জড়িয়ে পড়েছে। এ দুই দেশের মধ্যে এখনও পর্যন্ত কোন সংঘর্ষের পরিস্থিতি দেখা না দিলেও নানা ধরনের দ্বন্দ্ব মাথাচাড়া দিচ্ছে। এই দ্বন্দ্বকে ব্যবহার করে এ অঞ্চলকে অস্থিতিশীল করে নিজেদের কাজে লাগানোর চেষ্টা একাধিক বিদেশী শক্তির পক্ষে সম্ভব। এ বিষয়টি মাথায় না রেখে রামুর ঘটনা সম্পর্কে সাফ কথা বলতে যাওয়া নির্বোধ ও মতলববাজ ছাড়া আর কারও পক্ষে সম্ভব নয়।<br />\r\nবাঙলাদেশে বা এ দেশের মতো অন্য কোন দেশের নানা গুরুত্বপূর্ণ ঘটনা ও ব্যাপারের ক্ষেত্রে বিদেশী ও সা&curren;্রাজ্যবাদীদের চক্রান্ত বা পরিকল্পনার কথা বললে সেটা অনেকের কাছেই আপত্তিকর হয়। তারা মনে করেন এটা কোন কোন লোকদের বাতিক! কিন্তু বস্তুতপক্ষে বর্তমানে দুনিয়ায় বাঙলাদেশের মতো দেশ তো বটেই, এমনকি এর থেকে উন্নত দেশেও যেসব গুরুত্বপূর্ণ ঘটনা ঘটে সেখানে সা&curren;্রাজ্যবাদের লম্বা হাত নেই এটা মনে করাও এক ধরনের মূঢ়তা। যারা চান রামুতে ২৯ সেপ্টেম্বর বৌদ্ধদের ওপর হামলার বিষয়ে দায়ী কারা এটা শনাক্ত হোক, তারা এ ক্ষেত্রে বিদেশী হস্তক্ষেপের বিষয়টি হিসাবের বাইরে কিছুতেই রাখতে পারেন না।<br />\r\nএরপর আসা যেতে পারে পার্বত্য চট্টগ্রাম প্রসঙ্গে। এই অঞ্চলে ১৯৮০ সাল থেকে বা তারও কিছু আগে থেকে বাঙালী বসতি স্থাপনের যে পরিকল্পনা কার্যকর হচ্ছে তাতেও জনসংখ্যার অর্ধেক বা তারও বেশি এখন বাঙালী। জমি, ব্যবসা ইত্যাদি নিয়ে তাদের মধ্যে দ্বন্দ্ব-সংঘর্ষের একটা শর্ত তৈরী হয়েছে। কিন্তু এখনও পর্যন্ত এ দ্বন্দ্ব-সংঘর্ষ সাম্প্রদায়িক বিদ্বেষ থেকে সৃষ্টি হয়নি। রামুতে যেভাবে বৌদ্ধদের ওপর হামলা করে একটা সাম্প্রদায়িক পরিস্থিতি সৃষ্টির চেষ্টা করা হয়েছে তার প্রভাব আরও বিস্তৃত করার আশংকা আছে। যারা পার্বত্য চট্টগ্রামে বাঙালী-পাহাড়ী দ্বন্দ্ব-সংঘর্ষ নোতুনভাবে সৃষ্টি করতে চায় তারাই এ চেষ্টা করবে। অর্থাৎ তিন পার্বত্য জেলাতেই পরপর বৌদ্ধ মন্দির, মঠ ইত্যাদিতে আক্রমণ চালিয়ে তারা মুসলমান-বৌদ্ধ দাঙ্গা-হাঙ্গামা সৃষ্টি হবে যার ঢেউ মিয়ানমার পর্যন্ত ছড়িয়ে পড়বে। রামুর ঘটনার পর শ্রীলংকা, মিয়ানমার এবং অন্য দেশেও বৌদ্ধরা বাঙলাদেশী দূতাবাসের সামনে বিক্ষোভ করেছেন। এভাবে যে নোতুন পরিস্থিতির সৃষ্টি হয়েছে তা যে এ জায়গাতেই দাঁড়িয়ে থাকবে, এটা মনে করার কারণ নেই। সমাজ ও প্রশাসনে অবস্থিত কিছু লোকের মাধ্যমে বিদেশী চক্রান্তকারী বা অনেক কিছু ঘটনোর চেষ্টা তারা এ দেশে করবে। এই পরিপ্রেক্ষিত বিবেচনা না করে এখানকার রাজনৈতিক দলগুলো ক্ষুদ্র ও সংকীর্ণ স্বার্থে কাদা ছোড়াছুড়ি করলে চক্রান্তকারীদের লাভ ছাড়া ক্ষতির কোন সম্ভাবনা নেই।</span></p>\r\n','',1,15,'2016-01-07 03:12:31',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452157951_32.png'),(20,'ইসলাম ও আধুনিক সভ্যতা। শামসুজ্জোহা মানিক','ইসলাম-ও-আধুনিক-সভ্যতা।-শামসুজ্জোহা-মানিক','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">স্বাধীনতার ৪৩ বছর পর রাষ্ট্রের ভাব দেখে মনে হয় ধর্ম (ইসলাম) প্রতিষ্ঠার জন্যই বাংলাদেশের জন্ম। ৭১&rsquo;এ পাকিস্তানির সেনাবাহিনীর কর্মকাণ্ডে প্রমাণিত হয়েছিল ধর্ম প্রতিষ্ঠার জন্য &lsquo;৭১ সালে ওরা বাংলাদেশে এসেছিল। বাংলাদেশের জন্মের পিছনে আছে ধর্মীয় প্রভাব ও আগ্রাসন। দুনিয়া জোড়া ধর্ম নির্ভর অর্থনীতির (religious economy) প্রভাব এখনও অটুট আছে। নতুন প্রভাব যোগ হচ্ছে তা&rsquo;হল রাজনৈতিক অর্থনীতি (political economy) যার আগ্রাসী নাম সাম্রাজ্যবাদ। দুটাই খারাপ।<br />\r\nধর্ম যুগে যুগে লুণ্টনের এক শক্ত হাতিয়ার হয়ে সভ্যতাতে ভূমিকা রেখেছে। ইদানিং কালের বাংলাদেশও ধর্মের বর্বরতার ঊর্ধ্বে নয়। বাংলাদেশ জন্মে বাধা দিয়েছিল যে ধর্মগোষ্ঠী সে গোষ্ঠীটাই এখন আরো সংঘবদ্ধ হয়ে বাংলাদেশ গঠনেও বাধা দিচ্ছে। ফলশ্র&ldquo;তিতে তৈরি হয়েছে আশাবাদী &lsquo;গণজাগরণ মঞ্চ&rsquo; বনাম &lsquo;হেফাজত ইসলাম&rsquo;। গণজাগরণ মঞ্চ ধ্বংস করার ক্ষমতা রাষ্ট্রের আছে কিন্তু অন্যটার নেই। বহুধা বিভক্ত ইসলাম সহ অন্যান্য ধর্মসমূহ যে বাংলাদেশ তথা বিশ্ব গঠনের শক্ত প্রতিপক্ষ এটা কে অস্বীকার করবে? ধর্মান্ধতা, গুহামানবতা, প্রাচীনপন্থিতা অবলম্বন করে মনুষত্বের পূর্ণ বিকাশ অসম্ভব। ধর্ম যদি চেতনার বিষয় হয় তবে মূল চেতনা বিকাশে ধর্মের সীমাবদ্ধতা নিয়ে আলোচনাও আজ সময়ের দাবি। কোন ধর্মকে ছোট করার জন্য নয় বরং ধর্মের সীমাবদ্ধতা ও বর্বরতার টিকা টিপ্পনী দিয়ে এটা একটি যুক্তি সম্পন্ন উপস্থাপনা মাত্র।আশা করি মুক্তমনা মানুষেরা উপকৃত হবে। কেউ যদি মনে করেন ধর্মের সমালোচনা সহ্য করা তার জন্য অসম্ভব তিনি যেন উক্ত নিবন্ধনটি এড়িয়ে যান। &ndash; নিরুদ্র সি চৌধুরী</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">পশ্চিম ইউরোপের অভিজ্ঞতা</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">আধুনিক সভ্যতার যুগে বাস করেও উন্নত ও সভ্য সমাজ নির্মাণে আমাদের ব্যর্থতা কতখানি তা বাংলাদেশের আজকের বাস্তবতার দিকে দৃষ্টি দিলে সহজেই বোঝা যায়। অথচ আধুনিক সভ্যতা সম্পর্কে আমাদের অভিজ্ঞতা কম দিনের নয়। ১৭৫৭-তে ব্রিটিশ শাসন প্রতিষ্ঠিত হবার পর আমরা আধুনিক সভ্যতার অন্তর্ভুক্ত হই। তবে সেটা ছিল জাতি হিসাবে আমাদের পরাধীনতার কাল। সে কালের অবসান হয়েছে অনেক আগে। ১৯৪৭ সালে। তারপর ১৯৪৭ থেকে ১৯৭১ পর্যন্তও ছিল আমাদের আরেক পরাধীনতার কাল। কিন্তু ১৯৭১ থেকে আজ অবধি আমরা কোন বিজাতির শাসনাধীনে আছি যে তাকে দোষারোপ করে আমাদের দারিদ্র্য, দুর্নীতি, পশ্চাৎপদতা, অব্যবস্থা, সন্ত্রাস এবং অনুন্নয়নের জন্য সান্ত&iexcl;না খুঁজে পাব? অথচ পৃথিবীর অনেক জাতিই পরাধীনতার অভিজ্ঞতার মধ্য দিয়ে এসে আমাদের থেকে ভিন্ন ইতিহাস গড়েছে। তারা পরাধীনতার অবসান ঘটিয়ে উন্নত সভ্যতার অভিজ্ঞতাকে কাজে লাগিয়ে উন্নতির পথে এগিয়ে যেতে পেরেছে। মার্কিন যুক্তরাষ্ট্র একটা রক্তক্ষয়ী যুদ্ধের মধ্য দিয়ে স্বাধীনতা অর্জন করে উন্নত ও পরাক্রান্ত রাষ্ট্র গড়েছে। চীন, ভিয়েৎনাম ইত্যাদি রাষ্ট্রও বিদেশী রাষ্ট্রের আধিপত্যকে উৎখাত করে উন্নত রাষ্ট্র ও সমাজ গঠনের পথে দৃপ্তপায়ে এগিয়ে চলেছে ।<br />\r\nযুদ্ধ না করে কানাডা, অস্ট্রেলিয়া ও নিউজিল্যান্ড ব্রিটেনের কাছ থেকে স্বাধীনতা অর্জন করে উন্নত রাষ্ট্র গঠন করেছে। হয়ত বলা হবে এই তিনটি দেশের বর্তমান অধিবাসীবৃন্দ সাধারণভাবে ইউরোপ থেকে আগত। সুতরাং মার্কিন যুক্তরাষ্ট্রের মত উপনিবেশিক কালেও তারা যে আধুনিক ও উন্নত সমাজ গঠন করেছিল আজকের রাষ্ট্রগুলি তারই ধারাবাহিক, স্বাধীন ও রাজনৈতিক রূপ মাত্র। আর এখানেই আমরা আমাদের অনুসন্ধানের মূল জায়গাটা খুঁজে পাই। সেটা হচ্ছে ইউরোপ। অর্থাৎ আজকের আধুনিক সভ্যতার উৎস ইউরোপের সমাজে এমন কিছু বৈশিষ্ট্য ছিল যা সেখানে আধুনিক ও উন্নত সভ্যতার উপযোগী সমাজ ও রাষ্ট্রের উদ্ভবকে সম্ভব করেছিল। সুতরাং এখন আমরা সেই দিকটাতে দৃষ্টি দিই। তাহলেই আমরা আমাদের প্রশ্নের উত্তর খুঁজে পাব।<br />\r\nআমরা যেটাকে আধুনিক ইউরোপ বলি তার যাত্রা রেনেসাঁ বা নবজাগরণ দিয়ে। ইউরোপে নবজাগরণের কাল হিসাবে সাধারণভাবে ধরা হয় খ্রিস্টীয় চতুর্দশ-সপ্তদশ শতাব্দীকে, যে সময়টাতে প্রাচীন বিস্মৃত গ্রিক ও ল্যাটিন সাহিত্য, শিল্পকলা ও জ্ঞানের চর্চা শুরু হয়। রোমান সভ্যতার পতনের পর খ্রিস্ট ধর্মের প্রভাবে এই সব বিষয়ের চর্চা নিষিদ্ধ অথবা পৃষ্ঠপোষকতার অভাবে বন্ধ হয়ে যায়। বর্বরদের হাতে রোমান সভ্যতার চূড়ান্ত পতন হয় ৪৭৬ খ্রিস্টাব্দে। এরপর থেকে একেবারে প্রায় চতুর্দশ শতাব্দী পর্যন্ত যে কাল চলে সেটাকে বলা হয় অন্ধকার যুগ, যখন ধর্মের বিধিনিষেধের কারাগারে মানুষের যুক্তি-বুদ্ধি-বিবেচনা বন্দি হয়ে পড়ে। মানুষের চিন্তার নিয়ামক হিসাবে দেখা দেয় অন্ধ বিশ্বাস নির্ভর ধর্ম। ফলে জ্ঞান-বিজ্ঞান চর্চার পথও অবরুদ্ধ হয়ে পড়ে।<br />\r\nসমগ্র ইউরোপ জুড়ে অভিন্ন খ্রিস্ট ধর্মের প্রসার ঘটলেও বিভিন্ন ধর্র্র্র্র্র্র্মীয় প্রশ্নে মতভেদকে কেন্দ্র করে ইউরোপ রোমান ক্যাথলিক ও অর্থোডক্স এই দুই ভাগে বিভক্ত হয়ে পড়ে। পশ্চিম ইউরোপে খ্রিস্টীয় ধর্মের যে মতবাদ প্রাধান্য অর্জন করে সেটাকে আমরা জানি রোমান ক্যাথলিক হিসাবে আর পূর্ব ইউরোপে যে মতবাদ প্রাধান্য অর্জন করে সেটা পরিচিত অর্থোডক্স হিসাবে। খ্রিস্টীয় গির্জা বা চার্চ এই দুই প্রধান ধারায় বিভক্ত হয়। ক্যাথলিক চার্চ রোমান সা&curren;্রাজ্যের রাজধানী রোমকে কেন্দ্র ক&rsquo;রে এবং অর্থোডক্স চার্চ বাইজেন্টাইন সা&curren;্রাজ্যের রাজধানী কন্সট্যান্টিনোপলকে কেন্দ্র করে বিকশিত হয়। প্রধানত এই দুই শাখায় খ্রিস্ট ধর্মের বিকাশে যেটা সবচেয়ে লক্ষণীয় সেটা হচ্ছে ধর্মগুরু হিসাবে পোপকে কেন্দ্র করে ক্যাথলিক চার্চের কেন্দ্রীভূত প্রতিষ্ঠান রূপে বিকাশ লাভ যেটা অর্থোডক্স চার্চের ক্ষেত্রে ঘটে নাই। এই প্রভেদ ইউরোপের রাষ্ট্র ব্যবস্থাতেও গুরুতর প্রভেদ বা পার্থক্য ঘটায়। পশ্চিম ইউরোপে খ্রিস্ট ধর্মের কেন্দ্রীয় প্রতিষ্ঠান হিসাবে পোপের উদ্ভব রাষ্ট্রের উপর পোপ তথা চার্চের যে প্রাধান্য প্রতিষ্ঠা করে সেটা পূর্ব ইউরোপের ক্ষেত্রে ঘটে নাই।<br />\r\nআমাদের আলোচনায় এই বিষয় মনে রাখতে হবে যে, খ্রিস্ট ধর্মের বিশেষ দর্শন ও বিকাশ পদ্ধতি চার্চ ও রাষ্ট্রকে কখনই পুরোপুরি একীভূত করে নাই। অর্থাৎ যারা ধর্ম চর্চা ও প্রচারের নির্দিষ্ট দায়িত্ব পালন করে তারা প্রত্যক্ষভাবে রাষ্ট্র পরিচালনা করে না, যদিও তারা রাষ্ট্রকে প্রভাবিত এমনকি অনেকাংশে নিয়ন্ত্রণও করতে পারে। তবে সেটা তারা করলেও করে নিজেদের স্বতন্ত্র সত্তা বজায় রেখে। অর্থাৎ ধর্ম নেতা বা পুরোহিত এবং রাষ্ট্র নেতা বা রাষ্ট্র শাসক একজন নয়।<br />\r\nতবে ক্যাথলিক চার্চ ব্যবস্থায় সকল চার্চ বা গীর্জার কেন্দ্রীভূত কর্তৃত্ব হিসাবে পোপের পদ সৃষ্টি ক্যাথলিক মতাবলম্বী পশ্চিম ইউরোপে চার্চ রাষ্ট্র থেকে পৃথক হলেও রাষ্ট্রের উপর পোপের প্রাধান্য সৃষ্টি করে। পোপ যেহেতু সর্বোচ্চ ধর্মগুরু বা ধর্মনেতা এবং পশ্চিম ইউরোপের জনগণ ক্যাথলিক খ্রিস্টীয় মতাবলম্বী হিসাবে পোপকে তাদের সর্বোচ্চ আধাত্মিক ও নৈতিক কর্তৃত্ব হিসাবে মেনে নেয় সেহেতু পশ্চিম ইউরোপের রাজা বা স&curren;্রাটরাও পোপের ধর্মীয় কর্তৃত্বের প্রতি আনুগত্য প্রদান করতে বাধ্য হয়। আর এইভাবে পশ্চিম ইউরোপে সুদীর্ঘ কাল রাষ্ট্রের উপর পরোক্ষভাবে হলেও পোপ তথা চার্চের আধিপত্য বজায় থাকে। রাজা বা স&curren;্রাটরা যা-ই করুক পোপ বা গির্জার নৈতিক কর্তৃত্বকে মেনে চলতে হত। এটা একটা বড় কারণ যার ফলে পশ্চিম ইউরোপে রাজা বা রাষ্ট্রের নিরঙ্কুশ স্বেচ্ছাচার বা স্বৈরতন্ত্র গড়ে উঠার সুযোগ পায় নাই। অন্যদিকে, চার্চের হাতে সরাসরি রাষ্ট্র ক্ষমতা না থাকায় তাকেও নির্ভর করতে হত রাষ্ট্রের উপর। এর ফলে পশ্চিম ইউরোপে ধর্ম ও রাষ্ট্রের ক্ষমতার মধ্যে এক ধরনের ভারসাম্য সৃষ্টি হয়েছিল।<br />\r\nকিন্তু পূর্ব ইউরোপের অর্থোডক্স প্রভাবিত দেশগুলির পরিস্থিতি ভিন্ন হল। অর্থোডক্স চার্চগুলির কেন্দ্রীভূত রূপ হিসাবে পোপ জাতীয় কোন প্রতিষ্ঠান গড়ে উঠল না। অর্থোডক্স মতবাদকে কেন্দ্র করে নিজেদের মধ্যে ঐক্য ও সমন্বয় থাকলেও চার্চগুলি কেন্দ্রীভূত ধর্মীয় কর্তৃত্বের অধীনে সংগঠিত না হওয়ায় এগুলির রাষ্ট্রের উপর প্রাধান্য প্রতিষ্ঠার উপায় থাকে নাই, বরং রাষ্ট্রই চার্চকে নিজের অধীনে নিয়েছে। প্রতিষ্ঠান হিসাবে চার্চ ও রাষ্ট্র স্বতন্ত্র থেকেছে। কিন্তু চার্চের উপর রাষ্ট্র প্রধান হিসাবে রাজা বা স&curren;্রাটের প্রাধান্য থেকে গেছে। ফলে ধর্মীয় বিষয়ে রাষ্ট্রের হস্তক্ষেপ ঘটতে পেরেছে। এরই প্রকাশ হিসাবে আমরা বাইজেন্টাইন রোমান সা&curren;্র্রাজ্যের রাজধানী কন্সট্যান্টিনোপলকে কেন্দ্র করে গড়ে উঠা অর্থডক্স চার্চের উপর বাইজেন্টাইন স&curren;্রাটের আধিপত্যমূলক ভূমিকা দেখতে পাই অর্থাৎ এ ক্ষেত্রে অর্থোডক্স চার্চের পোপ হিসাবে অনেকাংশে ভূমিকা পালন করতেন স&curren;্রাট নিজে। এর ফলে রাষ্ট্র ও ধর্মীয় প্রতিষ্ঠানের মধ্যে ক্ষমতার ভারসাম্যের যে অভাব ঘটে তার প্রকাশ আমরা দেখতে পাই অর্থোডক্স চার্চ নিয়ন্ত্রিত খ্রিস্টান সমাজগুলিতে। অর্থোডক্স খ্রিস্টান অধ্যুষিত রাশিয়ায়ও আমরা স্পষ্টরূপে এই বাস্তবতার প্রকাশ দেখতে পাই যেখানে রাষ্ট্র অনেকাংেশ এশীয় স্বৈরতন্ত্রের অনুরূপ হয়ে গড়ে উঠেছে। এর ফলে যে সামন্ত শ্রেণী পশ্চিম ইউরোপে গড়ে উঠেছিল তার অনুরূপ কোন শ্রেণী আমরা এখানে দেখতে পাই না।<br />\r\nপশ্চিম ইউরোপে সামন্ত প্রভুরা রাজার অধীনস্থ হলেও তারা নিজ নিজ জমিদারি বা ভূখণ্ডে প্রায় স্বাধীন ছিল। কিন্তু পূর্ব ইউরোপ বিশেষত রাশিয়ায় ভূ-স্বামী বা সামন্তদের তেমন কোন অবস্থা ছিল না। রাশিয়ায় জার বা স&curren;্রাটদের মর্জির উপর নির্ভর করত সামন্ত প্রভু বা ভূ-স্বামীদের অস্তিত্ব। এটা ঠিক যে জারের শাসন ব্যবস্থা ভূ-স্বামীদের উপর নির্ভর করত। কিন্তু তার মানে এই নয় যে বিশেষ কোন ভূ-স্বামীর অধিকারে হস্তক্ষেপ করার ক্ষমতা তার ছিল না।<br />\r\nকিন্তু পশ্চিম ইউরোপে ব্যাপারটা এত সহজ ছিল না। রোমান সা&curren;্রাজ্যের পতনের সময় থেকে এখানে দীর্ঘ প্রক্রিয়ায় অঞ্চলগত বিভিন্নতা, জটিলতা ও পরিবর্তনের মধ্য দিয়ে সামন্ত ব্যবস্থার একটি জটিল রূপ গড়ে উঠে। এখানে সামন্ত প্রভু ভিন্ন সত্তা। তার জমিদারি বা ভূ-স্বত্বে রাজা ইচ্ছা করলেই হস্তক্ষেপ করতে পারত না। তাদের অধিকারভুক্ত ভূমি তাদের ব্যক্তিগত অধিকারে থাকত যা তারা বংশপরম্পরায় ভোগদখল করত। তবে এটা ঠিক যে, এই সামন্ত প্রভুরা রাজাকে নিয়মিত কর এবং যুদ্ধের প্রয়োজনে রাজাকে সেনাবাহিনী দিয়ে সাহায্য করতে বাধ্য ছিল। তা সত্ত্বেও পশ্চিম ইউরোপে শত শত বৎসর ধরে সামন্ত শ্রেণীর এই আপেক্ষিক স্বাধীনতা বজায় ছিল। সামন্ত শ্রেণীর এই তুলনামূলক বা আপেক্ষিক স্বাধীনতা এবং রাজা বা রাষ্ট্রের ক্ষমতার সীমাবদ্ধতা সৃষ্টির ক্ষেত্রে ক্যাথলিক চার্চের ভূমিকাকে যদি আমরা বুঝতে বা চিনতে না পারি তবে পশ্চিম ইউরোপের সামন্তবাদ সম্পর্কে আমরা কিছুই বুঝব না। বস্তুত চার্চের এই ভূমিকার কারণে পশ্চিম ইউরোপে এমন এক সামন্ত ব্যবস্থা গড়ে উঠেছিল যার তুলনা আমরা পৃথিবীর কোথায়ও সেভাবে পাই না। এটা ঠিক যে জাপানে পশ্চিম ইউরোপের প্রায় অনুরূপ একটি সামন্ত শ্রেণী ও সামন্ত ব্যবস্থা গড়ে উঠেছিল। কিন্তু তা সত্ত্বেও জাপানের সামন্তবাদী সমাজ ব্যবস্থাকে পশ্চিম ইউরোপের সামন্তবাদী ব্যবস্থার সঙ্গে হুবহু এক করে দেখা ঠিক হবে না। যে কারণে জাপানের সামন্তবাদী সমাজের ভিতর থেকে পশ্চিম ইউরোপের মত করে পুঁজি বা পুঁজিবাদী সমাজের উদ্ভব ও বিকাশ ঘটানো যায় নাই। তবে জাপান যেটা করতে পেরেছিল সেটা হচ্ছে পশ্চিম ইউরোপ তথা পাশ্চাত্যকে অনুকরণ করে দ্রুত শিল্প সভ্যতা নির্মাণের পথ গ্রহণ করা।<br />\r\nযাইহোক, প্রায় স্বাধীন সামন্ত শ্রেণীর অস্তিত্ব রাজা বা রাষ্ট্রের ক্ষমতাকে সীমাবদ্ধ করায় পশ্চিম ইউরোপে রাষ্ট্র তুলনামূলকভাবে দুর্বল ছিল। এই অবস্থায় সামাজিক বিশৃঙ্খলা এবং পরিণতিতে বৈদেশিক আগ্রাসনে সমাজ ও রাষ্ট্রের ধ্বংস হওয়া স্বাভাবিক হতে পারত। বিশেষত খ্রিস্টীয় ৮ম শতাব্দী থেকে ইসলামের যে অভিযান ইউরোপের উপর আছড়ে পড়ছিল (৭১১ খ্রিস্টাব্দে তারিকের নেতৃত্বে ইসলামী আরব বাহিনী স্পেনে প্রবেশ করার পর সেখানে ইসলামের বিজয় অভিযান শুরু হয়) তার আঘাতে ইউরোপের প্রতিরোধের শক্তি চূর্ণ হতে পারত। মুসলিম আরবরা স্পেন জয় করল। পরবর্তীকালে তুর্কি মুসলিমরা বাইজেন্টাইন সা&curren;্রাজ্যের রাজধানী কন্স্ট্যান্টিনোপলও জয় করল (১৪৫৩ খ্রিস্টাব্দ), যা ইস্তানবুল নাম নিয়ে আজ অবধি তুরস্কের অধিকারে আছে।<br />\r\nকিন্তু এর পরেও ইসলামী বাহিনী ইউরোপের অভ্যন্তরে খুব বেশি যে অগ্রসর হতে পারে নাই তার পিছনে ক্যাথলিক চার্চের ভূমিকা ছিল অত্যন্ত গুরুত্বপূর্ণ।<br />\r\nইসলামী আগ্রাসনে বাইজেন্টাইন রোমান সা&curren;্রাজ্যের পতন-পরবর্তী কালে ইসলামী আগ্রাসনের বিরুদ্ধে পূর্ব ইউরোপে প্রতিরোধের শক্তি হিসাবে উল্লেখযোগ্য ভূমিকা পালন করেছিল বৃহৎ ও শক্তিশালী রাষ্ট্র রাশিয়া এবং পোল্যান্ড। কিন্তু পশ্চিম ইউরোপ ছিল রাজনৈতিকভাবে তুলনায় অনেক বেশি বিভাজিত। বেশি রকম রাষ্ট্রীয় বিভাজন থাকলেও পশ্চিম ইউরোপের সমাজ বা রাষ্ট্রগুলিতে খ্রিস্ট ধর্ম এবং পোপ কেন্দ্রিক চার্চ যে ঐক্য প্রতিষ্ঠা করে তা ইসলামী আক্রমণ অভিযান প্রতিহত করতে বিরাট ভূমিকা রাখে।<br />\r\nক্যাথলিক চার্চের এই ঐক্য রক্ষাকারী ভূমিকা বলার সময় আমরা যেন এমন মনে না করি যে চার্চের আধিপত্যের ফলে পশ্চিম ইউরোপ অভ্যন্তরীণ যুদ্ধ ও বিদ্রোহ থেকে মুক্ত ছিল। রাজাদের তথা রাষ্ট্রগুলির মধ্যে পারস্পরিক যুদ্ধ যেমন লেগে থাকত তেমন রাজাদের বিরুদ্ধে সামন্তদের বিদ্রোহও অনুপস্থিত ছিল না। এ ছাড়া ছিল সামন্ত প্রভুদের নিজেদের মধ্যে যুদ্ধ। অর্থাৎ চার্চের নিয়ন্ত্রণ বা পোপের আধিপত্যের ফলে পশ্চিম ইউরোপে অভ্যন্তরীণভাবে শত শত বৎসর ব্যাপী শান্তিপূর্ণ অবস্থা বিরাজ করছিল এ কথা মনে করা ভুল হবে। কিন্তু তা সত্ত্বেও সবার উপরে পোপের নৈতিক অবস্থান পশ্চিম ইউরোপকে অনেকখানি সামাজিক সংহতি এবং তুলনামূলক শান্তি ও স্থিতিশীলতা দিয়েছিল। এর জন্য প্রাচ্য সমাজের মত প্রধানত বৃহদায়তন রাষ্ট্র অথবা নিরঙ্কুশ স্বৈরতন্ত্র নির্ভর এককেন্দ্রিক শাসনের প্রয়োজন হয় নাই।<br />\r\nপশ্চিম ইউরোপে চার্চের এই ভূমিকাকে বুঝলে আমরা সেখানে ভিতর থেকে বুর্জোয়া সমাজের উত্থানের প্রক্রিয়াকেও অনেকাংশে বুঝতে পারব। বুর্জোয়া শ্রেণীকে কেন্দ্র করে আধুনিক এবং শিল্প সভ্যতার যে উদ্ভব পশ্চিম ইউরোপে হয়েছে তার মর্মে যে ব্যক্তিসত্তা আছে তাকে বুঝতে না পারলে আমরা আধুনিক ইউরোপের উদ্ভবের বিষয়টাও বুঝব না। বস্তুত রাষ্ট্র থেকে চার্চের স্বতন্ত্র এবং প্রাধান্যকারী অবস্থান পশ্চিম ইউরোপে রাজা বা রাষ্ট্রকে নিরঙ্কুশ হতে দেয় নাই। এর ফলে সেখানে সামন্ত শ্রেণীর তুলনামূলকভাবে স্বাধীন অস্তিত্ব সম্ভব হয়। এই সামন্ত শ্রেণীর শক্তিশালী অবস্থান রাজার বা রাষ্ট্রের শক্তিকে খর্ব করত যা আবার চার্চকে সুবিধাজনক অবস্থায় রাখত। এভাবে পশ্চিম ইউরোপে চার্চ, রাষ্ট্র এবং সামন্ত শ্রেণীর মধ্যে দ্বন্দ্বের পাশাপাশি ঐক্য ও সমন্বয়ের মাধ্যমে এমন এক ভারসাম্য প্রতিষ্ঠিত ছিল যার ফলে সেখানে ব্যক্তি ও গণতন্ত্রের বীজ রক্ষা পেয়েছিল।<br />\r\nচার্চের প্রধান বা পোপ বংশগত হতে পারত না। কারণ পাদ্রি বা পুরোহিতরা বিবাহ করতে পারত না। সুতরাং এখানে যে ধরনের হোক নির্বাচন বা মনোনয়নের ভূমিকা থেকে গিয়েছিল। অন্য দিকে, সামন্ত ভূস্বামীর ব্যক্তি মালিকানার মধ্যে রক্ষা পেয়েছিল ব্যক্তির মালিকানা এবং অধিকার। সর্বোপরি, রাজাকে পরামর্শ দান এবং রাজকার্যে সহায়তার জন্য যে রাজসভা বা পার্লামেন্ট থাকত সেখানে সামন্ত্র প্রভু এবং পুরোহিত বা পাদ্রিরা অত্যন্ত গুরুত্বপূর্ণ ভূমিকা পালন করত। এই রকম এক সামাজিক বাস্তবতায় ব্যবসায়ী এবং কারখানা মালিকদের অস্তিত্ব পরবর্তীকালে পুঁজিবাদের দিকে অর্থনীতির ক্রমবিকাশে ধীরগতিতে হলেও ভূমিকা রেখেছিল।<br />\r\nতবে নবজাগরণের পূর্ব পর্যন্ত গোটা মধ্য যুগ ছিল অন্ধকারের যুগ যখন যুক্তি ও জ্ঞান-বিজ্ঞানের পরিবর্তে প্রধানত ধর্ম বিশ্বাস দ্বারা মানুষের সামাজিক ও রাষ্ট্রীয় জীবন নিয়ন্ত্রিত হত। ধর্ম ছিল মধ্য যুগের ভাবাদর্শ। যে কথা ইতিপূর্বে বলেছি চতুর্দশ-সপ্তদশ শতাব্দীতে প্রাচীন গ্রিক ও রোমান বা লাতিন শিল্পকলা ও জ্ঞান-বিজ্ঞান চর্চার পুনরুজ্জীবনের ফলে নবজাগরণ শুরু হলে অন্ধকার যুগের অবসান ঘটে এবং ধর্মের যে আধিপত্য ইউরোপে এতকাল ছিল তা ভেঙ্গে পড়তে থাকে। তবে রেনেসাঁর পূর্বে ছিল প্রায় দুই শতাব্দীব্যাপী ক্রুসেড বা ধর্মযুদ্ধের কাল।<br />\r\nক্রুসেড ছিল খ্রিস্টানদের তীর্থভূমি জেরুসালেম এবং প্যালেস্টাইনকে মুসলমানদের আধিপত্য থেকে মুক্ত করার জন্য ইউরোপের খ্রিস্টানদের যুদ্ধ, যা ১০৯৬ সালে শুরু হয়ে ১২৯১ সাল পর্যন্ত চলে। খ্রিস্টীয় শক্তি প্যালেস্টাইন মুক্ত করার জন্য অনেক কয়টি অভিযান পরিচালনা করে এবং জেরুসালেম ও প্যালেস্টাইনের অনেক এলাকা মুক্ত করতে সক্ষমও হয়। কিন্তুু শেষ পর্যন্ত ক্রুসেড ব্যর্থ হয়। মুসলমানদের হাতে ১২৯১-তে খ্রিস্টানদের শেষ দুর্গের পতন হলে খ্রিস্টানদের জেরুসালেমকে মুক্ত করার চেষ্টা শেষ পর্যন্ত পরিত্যক্ত হয়। এভাবে ক্রুসেডের অবসান হয়। দুইশত বৎসর ব্যাপী ক্রুসেডে ইউরোপের বিপুল জীবন ও সম্পদ ক্ষয় হয়। ক্রুসেডের ব্যর্থতা ছিল পোপ এবং ধর্মের মর্যাদার উপর বিরাট আঘাত। এর ফলে নূতন উদ্যমে জেগে উঠতে থাকা ইউরোপের শক্তি জ্ঞান-বিজ্ঞান ও জাগতিক উন্নয়নের পথে এগিয়ে যাবার সুযোগ পায়। এর ফলে ঘটে নবজাগরণ। এটা লক্ষণীয় যে, ক্রুসেডের সমাপ্তি এবং নবজাগরণের সূচনা প্রায় একই সময়ে ঘটে। প্যালেস্টাইনের শেষ খ্রিস্টান দুর্গের পতন ঘটে ১২৯১-তে। এটাও লক্ষণীয় যে নবজাগরণ চর্তুদশ শতাব্দীতে প্রবল রূপ নিলেও তার সূচনা প্রকৃতপক্ষে ত্রয়োদশ শতাব্দীর শেষ দিক থেকে হয়। অর্থাৎ ব্যাপারটা এমন যে, ব্যর্থ ধর্মযুদ্ধের কাল শেষ হবার পাশাপাশি শুরু হয়েছে সফল নবজাগরণের কাল। এভাবে ইউরোপের উত্থান শুরু হয়।<br />\r\nতবে এই উত্থান বাধামুক্ত ছিল না। অনেক সংগ্রাম করেই নবজাগরণের পথে ইউরোপকে অগ্রসর হতে হয়েছে। এই সংগ্রাম ছিল প্রধানত বিদ্যমান ধর্মের আধিপত্যের বিরুদ্ধে। একদিকে, ধর্মযুদ্ধের ব্যর্থতা, অপরদিকে সাহিত্য, শিল্পকলা ও জ্ঞান-বিজ্ঞান চর্চার ফলে নবজাগরণ ক্যাথলিক চার্চের প্রবল প্রভাবের উপর আঘাত হানে। তার ফলে জ্ঞান জগতে লোকবাদী বা জাগতিক চেতনার উন্মেষ ঘটাটা যে সম্ভব হয় শুধু তা-ই নয়, অধিকন্তু এর একটা ফল হয় বিদ্যমান খ্রিস্টান ধর্মের সংস্কার। ক্যাথলিক চার্চের আধিপত্যের বিরুদ্ধে লুথার (১৪৮৩-১৫৪৬), ক্যালভিন (১৫০৯-১৫৬৪), জুইংলি (১৪৮৪-১৫৩১) ইত্যাদি বিভিন্ন ধর্ম সংস্কারক তাদের মতবাদ প্রচার করতে থাকেন, যার মূল কথা ছিল যিশু খ্রিস্টের মূল আদর্শে ফিরে যাওয়া তথা মূল বাইবেলের শিক্ষায় খ্রিস্টান সমাজের পুনর্গঠন।<br />\r\nপ্রোটেস্ট্যান্ট আন্দোলন (ষোড়শ শতাব্দীতে সূচিত) পশ্চিম ইউরোপের উত্তরাংশে ক্রমে ব্যাপকভাবে জনপ্রিয় হয়ে উঠে। ক্যাথলিক চার্চ তাকে দমনের জন্য অনেক চেষ্টা চালায়। বিভিন্ন দেশে বহু সংখ্যক প্রোটেস্ট্যান্টকে হত্যা করা হয়। ইংল্যান্ড, ফ্রান্স, জার্মানিসহ ইউরোপের বিভিন্ন দেশে প্রোটেস্ট্যান্ট ও ক্যাথলিকদের মধ্যে যুদ্ধও সংঘটিত হয়, যার ফলে লক্ষ লক্ষ মানুষের প্রাণহানি ঘটে। কিন্তু প্রোটেস্ট্যান্ট তথা ধর্ম সংস্কার আন্দোলনের প্রভাবকে ঠেকানো গেল না। ক্রমে সমাজ ও রাষ্ট্রের উপর থেকে চার্চের নিয়ন্ত্রণ শিথিল হল।<br />\r\nপ্রোটেস্ট্যান্ট আন্দোলনের প্রভাব পশ্চিম ইউরোপের দেশগুলিতে পোপ তথা কেন্দ্রীভূত চার্চের মর্যাদায় বিরাট আঘাত করে। এর ফলে রাষ্ট্রের উপর এতকাল ধর্ম ও ধর্মীয় প্রতিষ্ঠান হিসাবে চার্চের যে আধিপত্য ছিল তা দুর্বল হতে বা ভেঙ্গে পড়তে থাকে। এই ভাঙ্গন সবচেয়ে প্রবল রূপ নেয় ইংল্যান্ডসহ উত্তরের কয়েকটি দেশে। একদিকে, ষোড়শ শতাব্দীতে শুরু হয়ে ইউরোপব্যাপী সাধারণ মানুষের মধ্যে ছড়িয়ে পড়ছিল ধর্মসংস্কার আন্দোলন। অপর দিকে, সপ্তদশ শতাব্দী থেকে শুরু হল যুক্তি ও জ্ঞান চর্চা নির্ভর আলোকায়নের যুগ (অমব ড়ভ ঊহষরমযঃবহসবহঃ বা অমব ড়ভ জবধংড়হ), যা চলল অষ্টাদশ শতাব্দীর শেষ ভাগ পর্যন্ত। চার্চের আধিপত্যের যুগ শেষ হয়েছে। ফলে দর্শন ও যুক্তির বিচারে জীবন ও জগতের সবকিছুকে দেখার একটা জোয়ার শুরু হয় বুদ্ধিবৃত্তির জগতে। ধর্মসংস্কার এবং আলোকায়নের যুগের আর একটা ফল ইউরোপে জাতি-রাষ্ট্রের উদ্ভব ও বিকাশ। মানুষের চিন্তার জগতে যে বিপ্লব ঘটতে থাকে তার ফল হল যুগান্তকারী। চিন্তার বিপ্লবের হাত ধরাধরি করে ঘটতে শুরু করে রাজনৈতিক তথা রাষ্ট্র বিপ্লব। ইংল্যান্ডে সপ্তদশ শতাব্দীতে পার্লামেন্টের সঙ্গে রাজার বিবাদ ঘটে, যা ক্রমে গৃহযুদ্ধ ও বিপ্লবে রূপ নেয়। এই বিপ্লবে একটা পর্যায়ে ক্রমওয়েলের (জন্ম ২৫ এপ্রিল ১৫৯৯&Ntilde;মৃত্যু ৩ সেপ্টেম্বর ১৬৫৮) নেতৃত্বমূলক ভূমিকা প্রতিষ্ঠিত হয়। রাজা ১ম চার্লস পরাজিত ও বন্দি হন। বিচারে রাজাকে মৃত্যুদণ্ড দেওয়া হয়। ৩০ জানুয়ারি ১৬৪৯-এ রাজার শিরো&ntilde;েদ করা হয়। ১৬৫৩ থেকে ১৬৫৮ পর্যন্ত ক্রমওয়েল শাসন করেন। ক্রমওয়েলের মৃত্যুর পর রাজতন্ত্র ফিরে এলেও ইতিহাসের গতি ছিল নিরঙ্কুশ রাজতন্ত্রের বিরুদ্ধে। ১৬৮৮-তে আর একটি বিপ্লবের মধ্য দিয়ে রাষ্ট্র শাসনের মূল ক্ষমতা পার্লামেন্টের হাতে চলে যায়। ফ্রান্সে ১৭৮৯-তে ঘটে আরও প্রচণ্ড এবং যুগান্তকারী একটি ঘটনা&Ntilde;ফরাসী বিপ্লব। এইভাবে পশ্চিম ইউরোপে আধুনিক শিল্প সভ্যতার নায়ক হিসাবে যে ব্যক্তিসত্তা অপরিহার্য ছিল তার উদ্ভব ঘটে। ফলে ইউরোপে ঘটল আধুনিক সভ্যতার সূচনা। ঘটল শিল্প বিপ্লব। প্রতিষ্ঠিত হল শিল্প নির্ভর এবং গণতান্ত্রিক সমাজ ও রাষ্ট্র। এই সব ঘটনার পাশাপাশি পৃথিবী ব্যাপী চলল ইউরোপের জয়যাত্রা।<br />\r\nএটা স্পষ্ট যে ধর্মের তথা চাচের্র আধিপত্য খর্ব না হলে আধুনিক ইউরোপের জন্ম সম্ভব হত না। কিন্তু বিস্ময়কর মনে হলেও এটাই আবার সত্য যে, আধুনিক ইউরোপের জন্মই সম্ভব হত না যদি সেখানে খ্রিস্ট ধর্ম না থাকত এবং কয়েক শতাব্দী ব্যাপী ক্যথলিক চার্চের আধিপত্য না থাকত।<br />\r\nকেন এ কথা বলছি তা বুঝার জন্য খ্রিস্ট ধর্মের কিছু বৈশিষ্ট্যের দিকে দৃষ্টি দেওয়া দরকার। এটা ঠিক যে আর সব আলোকবাদী ধর্মের মত খ্রিস্ট ধর্মও অন্ধ ও যুক্তি-প্রমাণহীন বিশ্বাসের উপর প্রতিষ্ঠিত। যেমন ঈশ্বর, আত্মা, স্বর্গ-নরক ইত্যাদি সংক্রান্ত ধারণা। কিন্তু এ ধরনের বিশ্বাস নির্ভর ধারণাগুলি সে কালে সাধারণ মানুষের জন্য স্বাভাবিক ছিল এবং দেবতা বা ঈশ্বর কিংবা অলৌকিক শক্তি এবং আত্মা ইত্যাদি ধারণাকে কেন্দ্র করে মানুষ তার ধারণা বা বিশ্বাসের যে কাঠামো গড়ে তুলত তা দিয়ে তার বাস্তব জীবনকেও কম বা বেশী নিয়ন্ত্রণ ও পরিচালনা করতে চাইত। কিন্তু এই বিশ্বাসের কাঠামো নির্মাণের ক্ষেত্রে খ্রিস্ট ধর্ম এমন কিছু মূল্যবোধ এবং নীতি বা ধারণাকে গুরুত্ব দিয়েছিল যা মানুষের মানবিক গুণাবলীর বিকাশের ক্ষেত্রে বিরাট ভূমিকা রেখেছিল। যিশুখ্রিস্টের বাণীতে ধর্ম-বর্ণ-জাতি নির্বিশেষে মানুষের জন্য প্রেম, সহমর্মিতা এবং সেবা ধর্মকে যে গুরুত্ব দেওয়া হয়েছে এ ক্ষেত্রে তার উল্লেখ করতে হয়। খ্রিস্ট ধর্মে শুধু যে যিশু ঈশ্বর পুত্র তা-ই নয়, এমন কি মানুষও তা-ই, যে কারণে বাইবেলে যিশু ঈশ্বরকে নিজের সঙ্গে অন্য সব মানুষের পিতা বলছেন। (কেননা তোমরা সকলে, খ্রিস্ট যিশুতে বিশ্বাস দ্বারা, ঈশ্বরের পুত্র হইয়াছ। বাইবেল, নূতন নিয়ম, গ্যালাতীয়, ৩:২৬)। অর্থাৎ মানুষের সঙ্গে ঈশ্বরের সম্পর্ক মূলত দাস ও প্রভুর না হয়ে সন্তান ও প্রেমময় পিতার সম্পর্ক হয়ে দেখা দেয়। বাইবেলে মানুষের প্রতি যে বিরাট মর্যাদা যিশু দিয়েছেন তার প্রভাবে মানুষকে অধিকারহীন ও অসহায় দাস বা ইসলামের মত &lsquo;বান্দা&rsquo; হিসাবে না দেখে মানুষ হিসাবে দেখা হয়েছে। খ্রিস্টীয় এই দৃষ্টিভঙ্গীর গভীরে ধর্মীয় আবরণে সংগুপ্ত ছিল ব্যক্তি মানুষের মুক্তি এবং স্বাধীনতার চেতনা।<br />\r\nঅপর একটি বৈশিষ্ট্য খ্রিস্ট ধর্মকে অনন্যতা দান করেছে, তা হল এক স্বামী-এক স্ত্রী ভিত্তিক বিবাহ ব্যবস্থার প্রতি গুরুত্ব প্রদান (&lsquo;সুতরাং তাহারা আর দুই নয়, কিন্তু একাঙ্গ।&rsquo; বাইবেল, নূতন নিয়ম, মথি লিখিত সুসমাচার, ১৯:৩)। বস্তুত এর ফলে প্রাচ্য সমাজে অখ্রিস্টীয় সমাজগুলিতে এক স্বামীর বহু নারী বিবাহের যে অবাধ সুযোগ ছিল ইউরোপে সেই সুযোগ থাকে নাই। খ্রিস্ট ধর্ম পুরুষতান্ত্রিক হলেও এবং নারী-পুরুষের সমাধিকার না দিলেও এক স্বামী এক স্ত্রী ব্যবস্থার উপর গুরুত্ব দিবার ফল হয়েছে যুগান্তকারী। এটি ভবিষ্যতে নারীর জন্য অধিকতর অধিকার বা সমাধিকার লাভের পথ খুলে দেয়।<br />\r\nঅতীতে বিভিন্ন সময়ে আমরা ইউরোপে নারীর যে মর্যাদা ও ভূমিকা দেখতে পাই তা সমকালীন অনেক সমাজের তুলনায় বিস্ময়কর। শুধু ডাইনি পুড়ানো দেখলে হবে না। সামাজিক ও রাজনৈতিক জীবনে নারীদের অংশগ্রহণকেও দেখতে পারতে হবে। এখানে স্পেনের রাণী ইসাবেলার (জন্ম ১৪৫১ খ্রিঃ &Ntilde; মৃত্যু ১৫০৪ খ্রিঃ। শাসনকাল ১৪৭৪-১৫০৪।) দৃষ্টান্ত উল্লেখ করা যায়। তিনি তাঁর বিভিন্ন গুরুত্বপূর্ণ ভূমিকার জন্য স্মরণীয় হয়ে আছেন। তিনি ছিলেন স্পেনের উত্তরাঞ্চলের একটি শক্তিশালী রাজ্য ক্যাস্তিলের রাণী। স্পেনের উত্তরাঞ্চলের অপর একটি শক্তিশালী রাজ্য আরাগনের রাজসিংহাসনের উত্তরাধিকারী ফার্দিনান্দকে তিনি ১৪৬৯-এ বিবাহ করেন এবং স্বামীর সঙ্গে সম্মিলিতভাবে তার সেনাবাহিনী নিয়ে যুদ্ধ করে ১৪৯২ খ্রিস্টাব্দে মুসলিমদের শেষ রাজ্য গ্রানাডা জয়ের মাধ্যমে সমগ্র স্পেন থেকে মুসলিম শাসন উচ্ছেদ করেন। এই জয়ের মাধ্যমে স্পেন থেকে আটশত বৎসর স্থায়ী মুসলিম শাসন উচ্ছেদ হয়। স্পেনে ইসলামী শাসন ছিল ৭১১ খ্রিস্টাব্দ থেকে ১৪৯২ খ্রিস্টাব্দ পর্যন্ত স্থায়ী। স্বামীর সঙ্গে রাণী ইসাবেলা যৌথভাবে স্পেন শাসন করেন। আমেরিকা আবিষ্কারের (১৪৯২ খ্রিঃ) জন্য অর্থ, জাহাজ ও জনবল দিয়ে তিনি ক্রিস্টোফার কলম্বাসকে সাহায্য করেন। এভাবে আমেরিকা আবিষ্কারের কৃতিত্বের সঙ্গে তাঁর নামও জড়িয়ে গেছে।<br />\r\nমধ্যযুগে খ্রিস্টান ইউরোপের বিভিন্ন রাজ্যে রাষ্ট্র পরিচালনায় নারীদের গুরুত্বপূর্ণ ভূমিকা সম্পর্কে জানা যায়, যা প্রাচ্য সমাজে সাধরণভাবে অভাবিত ছিল। মুসলমানদের অধিকার থেকে জেরুসালেম ও প্যালেস্টাইনকে মুক্ত করার জন্য দুই শত বৎসর ব্যাপী পরিচালিত ক্রুসেডে ইউরোপের খ্রিস্টান নারীদের বিপুল ভূমিকা ছিল। পুরুষদের পাশাপাশি তারা যুদ্ধে অংশ নিয়েছিল এবং প্রাণ বিসর্জন দিয়েছিল।<br />\r\nমধ্যযুগীয় ইউরোপের নাইট বা যোদ্ধা শ্রেণীর কথা হয়ত অনেকের জানা আছে। তাদেরকে আমরা প্রাচীন ভারতের ক্ষত্রিয় কিংবা আরও সঠিকভাবে জাপানের সামুরাই যোদ্ধা শ্রেণীর সঙ্গে তুলনা করতে পারি। তাদের &lsquo;শিভালরি&rsquo; বা বীরধর্মের একটি গুরুত্বপূর্ণ নীতি ছিল নারীর প্রতি সম্মান এবং নারী ও দুর্বলের বিপদ দেখলে তাকে উদ্ধারের জন্য ঝাঁপিয়ে পড়া। ইউরোপ এমনই ইউরোপ হয় নাই। নারীর প্রতি এই যে গুরুত্ব ও মর্যাদা দান তার ভাবাদর্শিক প্রেরণার উৎস যদি খ্রিস্ট ধর্মে না থাকত তাহলে আমরা মধ্যযুগের ইউরোপে নারীর এমন গুরুত্বপূর্ণ ও মর্যাদাপূর্ণ অবস্থান দেখতে পেতাম না। কারণ মধ্যযুগের ভাবাদর্শ ছিল ধর্ম।<br />\r\nএমনিতে খ্রিস্ট ধর্ম ছিল শান্তিবাদী। শান্তিপূর্ণভাবে জন্ম নেওয়া এই ধর্ম সুদীর্ঘকাল ছিল নির্যাতনের শিকার। প্রথমে ইহুদীদের চাপে এই ধর্মের প্রতিষ্ঠাতা যিশুখ্রিস্টকে প্যালেস্টাইনের রোমান শাসক ক্রুশবিদ্ধ করেন। এরপরও যখন রোমান সা&curren;্রাজ্যে খ্রিস্ট ধর্ম ছড়িয়ে পড়তে থাকে তখন বহুসংখ্যক খ্রিস্টানকে হত্যা ও অবর্ণনীয় নির্যাতন করা হয়। কিন্তু তা সত্ত্বেও খ্রিস্ট ধর্মের প্রসারকে রোধ করা যায় নাই। এটি ছিল প্রথমে দাস, দরিদ্র এবং নি&curren;&oelig;বর্গের জনসাধারণের ধর্ম। কিন্তু ক্রমে রোমের স্বাধীন মানুষ, দাস মালিক, রোমের সেনাবাহিনীর সদস্য এবং উচ্চ শ্রেণীর নাগরিকদের মধ্যেও তা বিস্তৃত হয়। প্রায় তিনশত বৎসর পর রোমের স&curren;্রাট কন্সট্যান্টাইন (শাসনকাল ৩০৬ খ্রিঃ &ndash; ৩৩৭ খ্রীঃ) ৩১৩ খ্রিস্টাব্দে &lsquo;মিলান অনুশাসন&rsquo; দ্বারা খ্রিস্ট ধর্মসহ সকল ধর্মের প্রতি সহনশীলতার নীতি ঘোষণা করলে খ্রিস্ট ধর্মের সঙ্গে রোমান রাষ্ট্রের দ্বন্দ্বমূলক সম্পর্কের অবসান হয়। তার মা হেলেনা ছিলেন খ্রিস্টান। তিনিও তাঁর মায়ের ধর্ম গ্রহণ করেন। স&curren;্রাট কন্সট্যান্টাইনের উপর খ্রীষ্টধর্মের প্রভাব সম্পর্কে বলতে গিয়ে এটুকু বলাই মনে হয় এখানে যথেষ্ট হবে যে, মৃত্যুদণ্ডপ্রাপ্ত ব্যক্তিদেরকে ক্রুশবিদ্ধ করে ভয়ানক যন্ত্রণা দিয়ে রোমান সা&curren;্রাজ্যে হত্যার যে জনপ্রিয় বিধান ছিল তা রদ করে তিনি মৃত্যুদণ্ডপ্রাপ্ত ব্যক্তিদের ফাঁসিতে ঝুলিয়ে মৃত্যুদণ্ড কার্যকর করার বিধান চালু করেন। এবং ৩২৫ খ্রিস্টাব্দে তিনি গ্ল্যাডিয়েটদের দিয়ে দর্শকদের মনোরঞ্জনের জন্য অনুষ্ঠিত নৃশংস মরণ খেলা নিষিদ্ধ করেন। তাছাড়া রোমান সা&curren;্রাজ্যের বন্দিদেরকে কারাগারের অন্ধকারে সারাক্ষণ রাখা নিষিদ্ধ করে তিনি তাদেরকে কারাকক্ষের বাইরে যাবার এবং দিনের আলো পাবার অধিকার দেন।<br />\r\nভয়ানক সহিংসতা, নির্দয়তা, অসাম্য, যুদ্ধ এবং দাসত্বের উপর প্রতিষ্ঠিত রোমান সভ্যতার বিরুদ্ধে খ্রিস্ট ধর্ম ছিল একটা প্রতিক্রিয়া বা বিদ্রোহ। তবে এই বিদ্রোহ ছিল শান্তিপূর্ণ। কিন্তু দাস, দরিদ্র এবং পরাধীন জাতিসমূহের মানুষদের মনে মানুষের মুক্তির জন্য ক্রুশে আত্মদানকারী যিশুর প্রভাব হল দূর প্রসারী ও প্রচন্ড। প্রেমময় ও দয়ার্দ্র যে সত্তার আশ্রয় সাধারণ মানুষ খুঁজে ফিরছিল তা তারা পেল ক্রুশবিদ্ধ যিশু এবং তাঁর প্রেমময় ও পরম পিতা ঈশ্বরের কল্পনার মধ্যে।<br />\r\nপোপকে কেন্দ্র করে রোমান ক্যাথলিক চার্চের বিকাশ আদি খ্রিস্টান ধর্মের সহজ, সরল ও বিকেন্দ্রীভূত রূপের অবসান ঘটায় এবং মূল খ্রিস্টান ধর্মে ছিল না এমন কিছু প্রথা বা নিয়মের বিকাশ ঘটায়। এর মধ্যে একটা ছিল অর্থের বিনিময়ে চার্চ বা পোপের নিকট থেকে পাপমোচন এবং স্বর্গে যাবার ব্যবস্থা। রাষ্ট্র থেকে পার্থক্য থাকলেও রাষ্ট্রের উপরেও চার্চের নিয়ন্ত্রণ বা আধিপত্য চার্চকে ক্রমে একটি বিশেষ সুবিধাভোগী এবং নিপীড়ক প্রতিষ্ঠানে পরিণত করেছিল। ক্রুসেডের ব্যর্থতা এবং রেনেসাঁ বা নবজাগরণ চার্চের নিয়ন্ত্রণকে দুর্বল করে ফেলে। এই অবস্থায় সাধারণ মানুষের ধর্মীয় চেতনার সঙ্গে সঙ্গতি রেখে একটি ধর্ম সংস্কার আন্দোলনের উত্থান স্বাভাবিক ছিল। এটা কেন্দ্রীভূত চার্চের ক্ষমতার অবসান ঘটায়। এর ফলে ধর্ম যেমন আদি খ্রিস্ট ধর্মের ব্যক্তির বিশ্বাস পালনের স্বেচ্ছামূলক জায়গায় ফিরল তেমন রাষ্ট্র হয়ে উঠল অনেকাংশে ধর্মের খবরদারি মুক্ত। এই অবস্থায় বুদ্ধিবৃত্তির উন্নততর চাহিদা পূরণে দেখা দিল এনলাইটেনমেন্ট বা আলোকায়নের যুগ; গুরুত্ব পেল যুক্তি এবং ব্যক্তিস্বাতন্ত্র্য।<br />\r\nএভাবে ব্যক্তি ও তার চেতনার মুক্তির ফলে পুঁজির বিকাশের যে পরিবেশ সৃষ্টি হল তা সম্ভব করল উন্নততর যন্ত্র-কৌশল প্রবর্তনের মাধ্যমে শিল্প বিপ্লব। এ ক্ষেত্রে অষ্টাদশ শতাব্দীতে ইংল্যান্ডে স্টিম ইঞ্জিন উদ্ভাবন ছিল বিশেষভাবে উল্লেখযোগ্য। এভাবে শুধু ইউরোপ নয়, পৃথিবীও প্রবেশ করল মানুষের ইতিহাসের নবতর অধ্যায়ে, যে অধ্যায়ে আজ আমরা বাস করছি। ব্যক্তির মুক্তি না ঘটলে আধুনিক পুঁজির এবং সভ্যতার এই জয়যাত্রা সম্ভব হত না।<br />\r\nক্যাথলিক চার্চের নিয়ন্ত্রণ মুক্ত হয়ে বিভিন্ন প্রোটেস্ট্যান্ট মতবাদ ও চার্চের বিকাশ এবং পাশাপাশি যুক্তিবাদের ক্রমপ্রসার একদিকে উদার ধর্মমতের দিকে যেমন সমাজের বৃহত্তর অংশকে নেয় অপর দিকে তেমন একটি সংখ্যালঘু কিন্তু উন্নততর বুদ্ধিবৃত্তি ও জ্ঞানতত্ত্বের অধিকারী গোষ্ঠীকে ক্রমবর্ধমানভাবে নিরীশ্বরবাদ বা ধর্ম সম্পর্কে অবিশ্বাসের দিকে নেয়। কিন্তু ধর্ম বিশ্বাসের ব্যাপারে যেমনটাই ঘটুক চার্চের জবরদস্তি বা বলপ্রয়োগের কাল শেষ হয়।<br />\r\nধর্ম বিশ্বাসের ক্ষেত্রে এইসব আন্দোলন ও পরিবর্তনের পাশাপাশি ঘটতে থাকে সামন্তবাদ ও স্বৈরতন্ত্র বিরোধী গণতান্ত্রিক চেতনা ও আন্দোলনের ক্রমবিকাশ। সপ্তদশ-অষ্টাদশ শতাব্দীতে ইংল্যান্ড-ফ্রান্সসহ বিভিন্ন দেশ প্রত্যক্ষ করে জাতীয় ও গণতান্ত্রিক বিপ্লব। সপ্তদশ শতাব্দীর মধ্যবর্তী সময়ে ইংল্যান্ডে রাজা ও পার্লামেন্টের মধ্যে যুদ্ধে রাজা পরাজিত এবং রাজতন্ত্র সাময়িকভাবে উচ্ছেদ হলেও ১৬৬০ সালে রাজতন্ত্র ফিরে আসে। ফ্রান্সেও ১৭৮৯-এর প্রচণ্ড রক্তক্ষয়ী বিপ্লবের মাধ্যমে রাজতন্ত্র উচ্ছেদ হলেও পরবর্তী সময়ে নেপোলিয়নের নেতৃত্বে পুনরায় রাজতন্ত্র ফিরে আসে। কিন্তু এগুলি ছিল পূর্বতন রাজতন্ত্রের অক্ষম অনুকরণ। ইংল্যান্ডে ১৬৮৮-এর বিপ্লবের মাধ্যমে রাজতন্ত্র নিয়মতান্ত্রিক এবং নিয়ন্ত্রিত রাজতন্ত্রে পর্যবসিত হয় যেখানে রাজার নামে প্রকৃতপক্ষে জন-প্রতিনিধিরা দেশ শাসন করে। ফ্রান্স বিভিন্ন পটপরিবর্তনের মধ্য দিয়ে প্রজাতন্ত্রে পরিণত হয়।<br />\r\nএভাবে পশ্চিম ইউরোপ পুঁজিবাদের বিকাশ ও শিল্প বিপ্লবের পাশাপাশি গণতান্ত্রিক বিপ্লব এবং রাষ্ট্র গঠনের দিকে এগিয়ে যায়। কিন্তু আধুনিক গণতন্ত্র এবং গণতান্ত্রিক ও মানবিক মূল্যবোধের অনেক কিছুরই বীজ আমরা খুঁজে পাব খ্রিস্ট ধর্মের ভিতর। (সমাজতত্ত্ববিদ ম্যক্স ওয়েবার প্রোটেস্ট্যান্ট নীতিবোধের মধ্যে পুঁজিবাদের বিকাশের শর্ত দেখতে পেয়েছেন। (গধী ডবনবৎ, ঞযব চৎড়ঃবংঃধহঃ ঊঃযরপ ধহফ ঃযব ঝঢ়রৎরঃ ড়ভ ঈধঢ়রঃধষরংস.)<br />\r\nধর্ম বিশ্বাসের অন্ধত্ব এবং বিশেষত ধর্মের কেন্দ্রীয় প্রতিষ্ঠান হিসাবে চার্চের জবরদস্তির চাপে যে ব্যক্তিসত্তার বীজ ছিল এতকাল সঙ্কুচিত ও অবদমিত হয়ে কেন্দ্রীভূত চার্চের অবসান পশ্চিম ইউরোপের সামন্তবাদী সমাজ জমিতে সেই বীজের অঙ্কুরোদ্গমের মাধ্যমে পুঁজিবাদ ও গণতন্ত্রের উত্থানের পথ করে দেয়।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">রাশিয়ার অভিজ্ঞতা</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">রোমকে কেন্দ্র করে গড়ে উঠা রোমান সা&curren;্রাজ্য প্রায় সমগ্র উত্তর ও পশ্চিম ইউরোপ এবং ভূমধ্যসাগর তীরবর্তী এশিয়া-আফ্রিকার বিশাল অঞ্চল ব্যাপী বিস্তৃত ছিল। বহুকাল পর্যন্ত তার রাজধানী রোম থেকে সমগ্র সা&curren;্রাজ্য শাসিত হত। কিন্তু পরবর্তী সময়ে শাসনের সুবিধার জন্য স&curren;্রাট কনস্ট্যান্টাইনের শাসনকালে ৩৩০ খ্রিস্টাব্দে রাজধানী পূর্ব দিকের গ্রিক উপনিবেশে অবস্থিত বাইজেনটিয়ামে নেওয়া হয়। এ ছাড়া শেষ দিকে সা&curren;্রাজ্যে যখন দুর্বলতা দেখা দেয় তখন শাসনের সুবিধার জন্য সা&curren;্রাজ্যকে কয়েক বার ভাগ করা হয়। কিন্তু চূড়ান্ত ভাগ হয় ৩৯৫ খ্রিস্টাব্দে যখন স&curren;্রাট ১ম থিওডসিয়াস রোমান সা&curren;্রাজ্যকে তার দুই পুত্র আরকাডিয়াস এবং অনরিয়াসের মধ্যে ভাগ করে দিয়ে যান। পশ্চিম রোমান সা&curren;্রাজ্য রোমকে কেন্দ্র করে এবং পূর্ব রোমান সাম্রাজ্য বাইজেনটিয়ামকে কেন্দ্র করে পরিচালিত হয়। বাইজেনটিয়ামের নাম পরবর্তী কালে কন্সট্যান্টিনোপল করা হয়।<br />\r\nএই বিভাজন চার্চের বিভাজনকে ত্বরান্বিত করে। সাম্রাজ্যের দুই অংশে পূর্ব থেকেই খ্রিস্ট ধর্মের গ্রন্থ বাইবেলের ভাষাকে কেন্দ্র করে এমনিতে বিভাজন ছিল। রোম কেন্দ্রিক পশ্চিম অঞ্চলে বাইবেল লেখা হত লাতিন ভাষায়, অন্যদিকে পূর্ব দিকের ভাষা ছিল গ্রিক। পরবর্তী কালে এর সঙ্গে আরও বিভিন্ন পার্থক্য যুক্ত হতে থাকে এবং সবশেষে বিবাদের মধ্য দিয়ে ১০৫৪-তে রোমের চার্চ এবং কনস্ট্যান্টিনোপল কেন্দ্রিক বাইজেনটাইন চার্চ পৃথক হয়ে যায়। এই কন্সট্যাটিনোপল কেন্দ্রিক চার্চ সাধারণভাবে পরিচিত অর্থোডক্স চার্চ হিসাবে।<br />\r\nরোমান ক্যাথলিক চার্চের সঙ্গে অর্থোডক্স চার্চের যে পার্থক্যটি আমাদের বিবেচনায় সর্বাধিক গুরুত্বপূর্ণ এবং যুগান্তকারী সেটা হচ্ছে ক্যাথলিক চার্চে কেন্দ্রীভূত প্রতিষ্ঠান হিসাবে পোপের যে পদ ছিল অর্থোডক্স চার্চে তেমন কোন প্রতিষ্ঠান ছিল না। এটা ঠিক যে ক্যাথলিক চার্চের মত অর্থোডক্স চার্চও রাষ্ট্র থেকে পৃথক প্রতিষ্ঠান ছিল। কিন্তু ক্যাথলিক চার্চের মত কেন্দ্রীভূত প্রতিষ্ঠান না থাকায় স&curren;্রাট বা রাষ্ট্রের উপর চার্চের প্রাধান্য বজায় রাখার উপায় ছিল না। বরং স&curren;্রাট বা রাষ্ট্র চার্চকে নিয়ন্ত্রণ করত।<br />\r\nতুর্কি মুসলমানদের হাতে ১৪৫৩ খ্রিস্টাব্দে কন্সট্যান্টিনোপলের পতন হলে বাইজেন্টাইন সা&curren;্রাাজ্যের অবসান হয়। এরপর একটা পর্যায়ে খ্রিস্টীয় শক্তি হিসাবে পূর্ব ইউরোপে রাশিয়া আবির্ভূত হয়। রাশিয়ায় আমরা চার্চকে রাষ্ট্রের তুলনায় খর্ব দশায় দেখতে পাই যেখানে তা ছিল শাসকদের সহযোগী ও অধীনস্থ। ধর্মের নীতি-নিয়ম স&curren;্রাটকে মেনে চলতে হত। কিন্তু চার্চ স&curren;্রাটের রাষ্ট্রপরিচালনা বা নীতি-নির্ধারণে হস্তক্ষেপ করতে পারত না যেটা আমরা পশ্চিম ইউরোপের ক্ষেত্রে ঘটতে দেখি। অর্থোডক্স চার্চের এই অবস্থার ফলে পূর্ব ইউরোপ বিশেষত রাশিয়ায় আমরা রাষ্ট্রের যে রূপ দেখতে পাই তা অনেকটা প্রাচ্যের স্বৈরতন্ত্রের অনুরূপ। অর্থাৎ এখানে রাষ্ট্রের স্বৈরতা বা স্বেচ্ছাচারের ক্ষমতাকে সংযত বা প্রতিহত করার মত কোন কার্যকর সামাজিক প্রতিষ্ঠান ছিল না।<br />\r\nএই অবস্থায় আমরা পশ্চিম ইউরোপের সামন্ত শ্রেণীর অনুরূপ কোন শ্রেণীকে অর্থডক্স খ্রিস্টান রাশিয়ায় পাই না। এখানে ভূ-স্বামী শ্রেণী হিসাবে আমরা যাদেরকে দেখতে পাই তারা ছিল স&curren;্রাটের মর্জির উপর নির্ভরশীল।<br />\r\nরাষ্ট্রীয় স্বৈরতন্ত্রের এই বিকাশের ফলে রাশিয়ায় পরিবর্তনের জন্য রাষ্ট্রের হস্তক্ষেপের প্রয়োজন হয়েছে। অর্থাৎ পরিবর্তন যখন এসেছে তখন তা এসেছে উপর থেকে। ব্যক্তির ভূমিকা রাখবার সুযোগের কারণে পশ্চিম ইউরোপে যেভাবে সমাজের ভিতর বা তুলনায় নীচ থেকে পরিবর্তনের শক্তি ও প্রেরণা জন্ম নিতে ও বিকাশ লাভ করতে পেরেছে পূর্ব ইউরোপে, বিশেষত রাশিয়ায় তেমনটা ঘটে নাই।<br />\r\nপশ্চিম ইউরোপের রাষ্ট্রগুলির মত এগিয়ে যেতে চেয়ে রাশিয়ায় আধুনিকায়নের সূত্রপাত ঘটান রাশিয়ার জার বা স&curren;্রাট পিটার দি গ্রেট (জন্ম ১৬৭২ খ্রিঃ &Ntilde; মৃত্যু ১৭২৫ খ্রিঃ, রাজত্ব করেন ১৬৮২ খ্রিঃ থেকে ১৭২৫ খ্রিঃ পর্যন্ত)। আধুনিক শিক্ষা প্রবর্তন, নৌবাহিনী ও আধুনিক সেনাবাহিনী গঠন, শিল্প ও বাণিজ্যের বিকাশ সাধন, রাষ্ট্র পরিচালনায় ভূমিকা রাখার জন্য সিনেট প্রতিষ্ঠা ইত্যাদি দ্বারা তিনি পশ্চাৎপদ রাশিয়ার আধুনিকায়নের ভিত্তিপ্রস্তর স্থাপন করেন। আধুনিকায়নের প্রয়োজনে তিনি পশ্চিম ইউরোপ থেকে শিক্ষক-প্রশিক্ষকদেরকে আনয়ন করেন। মোট কথা আধুনিক রাশিয়ার প্রথম স্থপতি যদি কাউকে বলতে হয় তবে সেটা বলতে হবে পিটার দি গ্রেটকে।<br />\r\nএই ধারাতেই রাশিয়ার পরবর্তী বিরাট পরিবর্তন ঘটে ১৯১৭ সালে লেনিনের নেতৃত্বে কমিউনিস্ট বিপ্লবের মাধ্যমে। অর্থাৎ পশ্চিম ইউরোপের তুলনায় পশ্চাৎপদ রাশিয়ার দ্র্রুত আধুনিকায়ন এবং উন্নয়নের জন্য ব্যক্তির দুর্বলতা বা অনুপস্থিতির জন্য রাষ্ট্রকেই হস্তক্ষেপ করতে হয়েছে। তবে কমিউনিস্ট বিপ্লবের ক্ষেত্রে আমরা রাষ্ট্রযন্ত্র বহির্ভূত একটি রাজনৈতিক দলকে রাষ্ট্রের উপর একক নিয়ন্ত্রণ নিয়ে আধুনিকায়ন ও উন্নয়ন ঘটাতে দেখি। এ যেন আধুনিকায়নের জন্য ধর্মবিশ্বাস-মুক্ত এবং লোকবাদী ক্যাথলিক চার্চ কর্তৃক রাষ্ট্রের উপর কর্তৃত্ব প্রতিষ্ঠা। প্রকৃতপক্ষে পশ্চিম ইউরোপে সমাজ ও রাষ্ট্র ব্যবস্থা পরিবর্তনে ব্যক্তি যে ভূমিকা নিতে পেরেছিল রাশিয়ার ক্ষেত্রে ব্যক্তির অক্ষমতায় রাজনৈতিক দলকে সেই ভূমিকা নিতে হয়। এ ক্ষেত্রে মার্কসবাদ এবং কমিউনিজমের মতাদর্শের সর্বাত্মক রাষ্ট্রীয়করণ বা জাতীয়করণ সমাজের দ্রুত শিল্পায়নের কাজে লাগে। রাশিয়ার নেতৃত্বে পূর্ব ইউরোপ তার শিল্পায়নের জন্য এই পথ গ্রহণ করে।<br />\r\nতবে ব্যক্তিকে সম্পূর্ণ অস্বীকার করে সর্বাত্মক জাতীয়করণের মাধ্যমে শিল্পায়নে যে সমস্যা একটা পর্যায়ে দেখা দেয় সেটা পূর্ব ইউরোপসহ রাশিয়ায় দেখা দেয়। নূতন উদ্ভাবন ও আবিষ্কারের মূলে থাকে ব্যক্তি। ব্যক্তির ভূমিকা না থাকলে সমাজ এক সময় বন্ধ্যা এবং স্থবির হয়ে যায়। ফলে অর্থনৈতিক ও সামাজিক সঙ্কট নেমে আসে। এই সঙ্কটকে মোকাবিলা করতে গিয়ে এক পর্যায়ে মার্কসীয় সমাজতন্ত্র পরিত্যাগ ক&rsquo;রে রাশিয়া ও পূর্ব ইউরোপ পুঁজিবাদের পথ গ্রহণ করে। ১৯৯১ সালে সোভিয়েত ইউনিয়ন ভেঙ্গে যায়। সেই সঙ্গে রাশিয়ায় কমিউনিস্ট পার্টির শাসনেরও অবসান হয়। এভাবে প্রতিষ্ঠিত হয় বহুদলীয় গণতান্ত্রিক শাসন পদ্ধতি।<br />\r\nচীনে কমিউনিস্ট পার্টির শাসন অব্যাহত থাকলেও তা পার্টি এবং রাষ্ট্রের নিয়ন্ত্রণেই ব্যক্তিপুঁজির বিকাশের পথ গ্রহণ করে। অর্থনীতির এই ব্যবস্থাকে চীনের পার্টি নাম দিয়েছে সমাজতান্ত্রিক বাজার অর্থনীতি। ভিয়েৎনামও এই ধরনের অর্থনীতির পথ অনুসরণ করছে। এই দুই দেশেরই উন্নয়ন প্রবল গতিসম্পন্ন। চীন ইতিমধ্যে মার্কিন যুক্তরাষ্ট্রকে ছাড়িয়ে পৃথিবীর এক নম্বর অর্থনৈতিক শক্তিতে পরিণত হয়েছে। যাইহোক, চীন, ভিয়েৎনামে ব্যক্তিপুঁজির ক্রমবিকাশের ফলে ব্যক্তির নিজস্ব যে ভিত্তি তৈরি হচ্ছে তা যে এক সময় সেখানে সমাজ ও রাষ্ট্রকে একদলীয় গণতন্ত্র থেকে বহুদলীয় গণতন্ত্রের দিকে নিবে সে কথা বলা যায়। কারণ ব্যক্তি দাবি করে এককেন্দ্রিকতার পরিবর্তে একাধিক বা বহুকেন্দ্রিকতাকে। অবশ্য এই বহুকেন্দ্রিকতা সমাজে যাতে বিশৃঙ্খলা ও নৈরাজ্য সৃষ্টি না করে সেই জন্য প্রয়োজন হয় একটা আপেক্ষিক এককেন্দ্রিকতার, যা অনেকাংশে অন্যান্য কেন্দ্রের সম্মতির ভিত্তিতে প্রতিষ্ঠিত হয়।<br />\r\nযাইহোক, আমরা পশ্চিম ও পূর্বসহ সমগ্র ইউরোপে আধুনিক সভ্যতার উদ্ভব ও বিকাশের শর্ত পূরণের ক্ষেত্রে খ্রিস্ট ধর্মের একটি প্রধান ভূমিকা দেখতে পেলাম। আধুনিক সভ্যতা এবং গণতান্ত্রিক সমাজ ও রাষ্ট্র গঠনের জন্য যে ধরনের ব্যক্তির উদ্ভবের প্রয়োজন ছিল খ্রিস্টান এবং বিশেষত ক্যাথলিক চার্চের প্রভাব তার পরিপোষণে প্রাথমিক ভূমিকা পালন করেছিল। একটা পর্যায়ের পর ক্যাথলিক চার্চের ভূমিকা এই ধরনের ব্যক্তির বিকাশ ও প্রতিষ্ঠার ক্ষেত্রে বাধা হয়ে দাঁড়িয়েছিল। ফলে তার উৎখাত সাধনের জন্য নবজাগরণ, ধর্ম সংস্কার এবং যুক্তিবাদী বুদ্ধিবৃত্তিক আন্দোলনের প্রয়োজন হয়েছিল। এসবের ফল ইউরোপে জাতি-রাষ্ট্র প্রতিষ্ঠা, গণতান্ত্রিক বিপ্লব এবং শিল্প বিপ্লব।<br />\r\nএটা লক্ষণীয় যে প্রোটেস্ট্যান্ট আন্দোলন পশ্চিম ইউরোপের যে সব দেশে শক্তিশালী হয়েছিল এবং ক্যাথলিক চার্চের প্রতাপকে দুর্বল করেছিল উত্তরের ইংল্যান্ডসহ সেই দেশগুলিতে গণতান্ত্রিক ও শিল্প বিপ্লব সবার আগে সফল হয় এবং এইসব দেশ ক্যাথলিক চার্চ দ্বারা তুলনায় অনেক বেশি প্রভাবিত দক্ষিণের স্পেন, পর্তুগাল, ইতালির চেয়ে অনেক বেশি এগিয়ে যায়।<br />\r\nসমাজ ও রাষ্ট্র গঠনে ধর্মের ভূমিকা কতটা গুরুত্বপূর্ণ হতে পারে সেটা প্রাচ্যের বৌদ্ধ ধর্মাবলম্বী দেশগুলির দিকে দৃষ্টি দিলেও বোঝা যায়। এ ক্ষেত্রে বিশেষ করে চীন এবং ভিয়েৎনাম, লাওস, কম্বোডিয়া, থাইল্যান্ড ইত্যাদি দেশের দৃষ্টান্ত উল্লেখ করা যায়। বৌদ্ধ ধর্মে কতকগুলি সুনীতি পালন এবং পুণ্যকর্মের মাধ্যমে জন্মান্তরের চক্র থেকে মানুষের আত্মার মুক্তি বা নির্বাণের ধারণার উপর গুরুত্ব দেওয়া হয়েছে। শুধু মানুষের প্রতি নয় অধিকন্তু সব জীবের প্রতি দয়া ও প্রেমের উপর এই ধর্মে গুরুত্ব দেওয়া হয়েছে। তার মানে এই নয় যে বৌদ্ধ ধর্ম গ্রহণের ফলে মানুষ হিংসা, যুদ্ধ, নিপীড়ন বাদ দিয়েছে। কিন্তু এও ঠিক যে, ধর্মে এগুলির প্রতি বিরোধিতা থাকায় শান্তি, সহাবস্থান এবং ভ্রাতৃত্ব বোধ সমাজে একটা গুরুত্বপূর্ণ স্থান করে নেয়। বিশেষত সংযম, অহিংসা, শান্তি ও ন&curren;্রতার মূর্ত প্রতীক হিসাবে সর্বস্ব ত্যাগী ভিক্ষুদের দ্বারা গঠিত বৌদ্ধ সংঘের অবস্থান বৃহত্তর সমাজে শান্তি, স্থিতি ও সৌভ্রাতৃত্ব রক্ষায় বিরাট ভূমিকা পালন করে। বৌদ্ধ সংঘের সদস্যরা বিবাহ করতে পারে না যেমন তারা যুদ্ধ করতে পারে না। সেখানে নারীরাও প্রবেশ করতে এবং সংসার বহির্ভূত সন্ন্যাস জীবন যাপন করতে পারে। সংঘের সদস্যদের যৌন সম্পর্ক স্থাপন নিষিদ্ধ এবং ধর্মীয় বিধান অনুযায়ী শুদ্ধ জীবন যাপন বাধ্যতামূলক। আসলে বৌদ্ধ সংঘের প্রায় অনুরূপ খ্রিস্টীয় চার্চ। তবে ক্যাথলিক চার্চের মত কোন কেন্দ্রীয় প্রতিষ্ঠানের অনুপস্থিতির ফলে প্রাচ্যে বৌদ্ধ ধর্ম প্রভাবিত সমাজগুলিতে স্বৈরতান্ত্রিক রাষ্ট্র বা রাজার উত্থান সহজতর হয়েছে। সংঘ রাষ্ট্র থেকে পৃথক। জনগণের ধর্ম বিশ্বাসের মাধ্যমে সংঘ বা ধর্ম রাষ্ট্রশাসনের উপরেও প্রভাব ফেলেছে। কিন্তু তা সত্ত্বেও কেন্দ্রীয় প্রতিষ্ঠানের অভাবে তা রাষ্ট্রকে নিয়ন্ত্রণের পর্যায়ে যেতে পারে নাই, বরং রাষ্ট্রের অধীনস্থ হয়েছে।<br />\r\nসুতরাং পশ্চিম ইউরোপে যেভাবে ব্যক্তির নিজস্ব শক্তির একটা জায়গা ধর্মের আবরণে এবং সামন্ত কৃষি ব্যবস্থার মাধ্যমে হলেও রক্ষা পেয়েছে প্রাচ্যের বৌদ্ধ ধর্মাবলম্বী দেশগুলিতে তেমনটা সাধারণভাবে হতে পারে নাই। এই বাস্তবতায় আধুনিক সভ্যতা নির্মাণের প্রয়োজনে চীন, উত্তর কোরিয়া এবং ইন্দোচীনের দেশগুলিতে রাজনৈতিক দল একক কর্তৃত্বকারী ভূমিকা গ্রহণ করেছে (ইন্দোচীনের কম্বোডিয়া আনুষ্ঠানিকভাবে বহুদলীয় হলেও কার্যত একদল শাসিত রাষ্ট্র। শাসক দলের নাম কম্বোডীয় জনগণের দল বা কম্বোডিয়ান পিপল্স্ পার্টি। ভিয়েৎনাম এবং লাওস সাংবিধানিকভাবে একদলীয় রাষ্ট্র।)। আধুনিক সভ্যতা নির্মাণে এইসব দেশ বিশেষত চীন এবং ভিয়েৎনামের সাফল্য লক্ষণীয়। এইসব দেশ এক দল কর্তৃক শাসিত হলেও সাধারণভাবে দলের ভিতর গণতন্ত্রের বিকাশ যেমন লক্ষণীয় তেমন একক দলের অধীনস্থ রাষ্ট্রের নিয়ন্ত্রণ ও উদ্যোগে ব্যক্তি পুঁজির বিকাশও লক্ষণীয়।<br />\r\nজাপানের অভিজ্ঞতা অবশ্য ভিন্ন। জাপানে আদি শিন্টোধর্ম এবং বৌদ্ধ ধর্ম উভয়ই প্রচলিত ছিল। তবে ধর্মীয় উগ্রতা বা গোঁড়ামি জাপানে ছিল না। সেখানে পশ্চিম ইউরোপের কাছাকাছি ধরনের একটা সামন্ত শ্রেণী গড়ে উঠেছিল। পশ্চিম ইউরোপের যোদ্ধা শ্রেণী হিসাবে নাইটদের অনুরূপ সামুরাই নামে খ্যাত একটি যোদ্ধা শ্রেণীও সেখানে গড়ে উঠেছিল। সবার উপরে ছিলেন স&curren;্রাট। তাকে দেবতা হিসাবে বিবেচনা করা হত। কিন্তু রাষ্ট্র শাসনে তার প্রত্যক্ষ ভূমিকা দুর্বল ছিল। তিনি থেকেছেন জাপানের ঐক্যের প্রতীক হিসাবে। এই অবস্থায় ইউরোপের আধুনিক সভ্যতার সংস্পর্শে আসবার পর তা থেকে শিক্ষা নিয়ে জাপানের শাসকরা দ্রুত শিল্প পুঁজি গড়ার উদ্যোগ নেয় এবং জাপানকে আধুনিক যুগে নেয়। তবে পূর্ব থেকেই ব্যক্তির ভূমিকা নিবার সুযোগ না থাকলে ব্যক্তির মালিকানায় এই শিল্পায়নের উদ্যোগ সম্ভব হত না।<br />\r\nআধুনিক সভ্যতা নির্মাণের ক্ষেত্রে একটা বিষয় পরিষ্কার যে ধর্মের সঙ্গে আধুনিক সভ্যতার সম্পর্ক মোটেই সঙ্গতির নয়, বরং সাংঘর্ষিক। কারণ আধুনিক সভ্যতা অন্ধ বিশ্বাস নির্ভর ধর্ম চর্চার পরিবর্তে সম্পূর্ণ রূপে জ্ঞান-বিজ্ঞান এবং যুক্তি-প্রমাণ নির্ভর। এই অবস্থায় পশ্চিম ইউরোপে রাষ্ট্রের উপর চার্চের আধিপত্যের অবসান ঘটাতে হয়েছে। প্রোটেস্ট্যান্ট সংস্কারের ফলে নমনীয় এবং বিকেন্দ্রীভূত চার্চ প্রতিষ্ঠা রাষ্ট্রকে লোকবাদী তথা সেকিউলার পথে চলার সুযোগ করে দেয়। যেখানে প্রোটেস্ট্যান্ট মতবাদ প্রাধান্য অর্জন করতে পারে নাই, ফলে ক্যাথলিক মতবাদ ও চার্চ প্রাধান্য বজায় রাখতে পেরেছে, সেখানেও রাষ্ট্রের উপর চার্চের আধিপত্যের অবসান ঘটে। ইউরোপে চার্চ প্রধান হিসাবে পোপের ভূমিকা ধর্মের নিজস্ব গণ্ডির মধ্যে সীমাবদ্ধ হয়ে পড়ে। ফলে ধর্ম হয়ে পড়ে যার যার ব্যক্তিগত বিশ্বাস-অবিশ্বাসের বিষয়।<br />\r\nএ প্রসঙ্গে আমরা যিশুর সেই বাণী উল্লেখ করতে পারি যেখানে তিনি বলছেন, &lsquo;সিজারের প্রাপ্য সিজারকে দাও এবং ঈশ্বরের প্রাপ্য ঈশ্বরকে দাও&rsquo; (বাইবেল : নূতন নিয়ম, মার্ক ১২:১৭)। এর অর্থ তিনি রাষ্ট্রের প্রাপ্য রাষ্ট্রকে এবং ঈশ্বরের প্রাপ্য ঈশ্বরকে দিতে বলছেন। এর তাৎপর্য অপরিসীম। প্রোটেস্ট্যান্ট সংস্কারের প্রভাবে আদি খ্রিস্টান ধর্মে প্রত্যাবতর্নের ফলে রাষ্ট্র এবং জাগতিক কর্মকাণ্ডের উপর ধর্মের হস্তক্ষেপের সুযোগ রইল না। একবার ধর্ম রাষ্ট্র থেকে নিজেকে গুটিয়ে নিবার পর রাষ্ট্রও আর তাকে নিয়ে মাথা ঘামাবার প্রয়োজন তেমন একটা বোধ করে নাই। ফলে ব্যক্তিগত বিশ্বাস-অবিশ্বাসে সীমাবদ্ধ ধর্মের প্রতি রাষ্ট্র নমনীয়ও হয়েছে ক্ষেত্র বিশেষে।<br />\r\nকিন্তু অর্থোডক্স চার্চ শাসিত রাশিয়ার ক্ষেত্রে ধর্মের বিরুদ্ধে লড়াইয়ে রাষ্ট্রকে তুলনায় অনেক বেশি সক্রিয় হতে হয়। পশ্চিম ইউরোপের সমকক্ষতা অর্জনের জন্য রাশিয়ার নেতৃত্বে পূর্ব ইউরোপের যে দ্রুততা অর্জনের প্রয়োজন ছিল তা সেখানে রাষ্ট্রীয় নিয়ন্ত্রণে ও কর্তৃত্বে শিল্পায়নের পাশাপাশি ধর্মের বিরুদ্ধে আদর্শিক সংগ্রামেও কমিউনিস্ট পার্টিকে ভূমিকা নিতে বাধ্য করে। ফলে সব কমিউনিস্ট শাসিত রাষ্ট্রই পুরাপুরি সেকিউলার বা লোকবাদী হয়। অবশ্য কমিউনিস্ট মতাদর্শের উৎস মার্কসবাদ দর্শনগতভাবেই বস্তুবাদী ও ধর্মবিশ্বাস মুক্ত। এই রকম অবস্থায় কমিউনিস্ট পার্টির শাসনে মতাদর্শিক ক্ষেত্রে কমিউনিস্ট পার্টি ধর্মের বিরুদ্ধে কম-বেশি ভূমিকা রাখতে বাধ্য হয়। একই ঘটনা আমরা বৃহৎ ধর্মগুলির মধ্যে সবচেয়ে উদার বৌদ্ধধর্ম প্রভাবিত চীন-ভিয়েৎনামের ক্ষেত্রেও দেখি। এই যেখানে অবস্থা সেখানে ইসলাম শাসিত সমাজে আধুনিকায়ন তথা সমাজের উন্নয়ন ও গণতন্ত্রায়ন অবিশ্বাস্য রকম জটিল ও কঠিন।<br />\r\nসবচেয়ে বড় কথা খ্রিস্টান বা বৌদ্ধ ধর্মের উদ্ভব ও বিকাশ হয়েছে সম্পূর্ণরূপে সামাজিক তথা অরাজনৈতিক আন্দোলন রূপে। ফলে বিকাশের একটা পর্যায়ে তা রাষ্ট্র বা রাজনীতিতে যে ভূমিকাই পালন করুক তার বিরুদ্ধে সংগ্রামের মূল রূপ সামাজিক ও সাংস্কৃতিক হতে বাধ্য হয়। বিদ্যমান ধর্মের বিরুদ্ধে সামাজিক আন্দোলন কখনও রাজনৈতিক ও সামরিক রূপ নিলেও সেটা মূলত সামাজিক ও সাংস্কৃতিক আন্দোলনের অধীনস্থ হয়ে থাকে। যে কারণে ধর্মীয় আন্দোলনের পরিণতিতে যে পরিবর্তনই ঘটুক ধর্মীয় শক্তি রাষ্ট্র ক্ষমতায় অধিষ্ঠিত হয় না। পশ্চিম ইউরোপে এই কারণে প্রোটেস্ট্যান্ট আন্দোলনের সাফল্য নূতন কোনও ধরনের চার্চকে রাষ্ট্র ক্ষমতায় অধিষ্ঠিত করে নাই। কিন্তু ইসলাম একই সঙ্গে সামাজিক, রাজনৈতিক ও সামরিক ধর্ম হিসাবে বিকাশ ও বিস্তার লাভ করায় এক সম্পূর্ণ ভিন্ন ধরনের সমস্যা হাজির করে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ইসলামের সমস্যা</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">মুসলিম বা ইসলামী সমাজের সমস্যা প্রকৃতপক্ষে ইসলামের সমস্যা। কারণ মুসলিম সমাজ গঠনের মূল ভিত্তি ইসলাম ধর্ম তথা ইসলামী প্রথা, বিশ্বাস এবং মূল্যবোধ। এগুলির প্রধান উৎস কুরআন ও হাদীস। কুরআন হচ্ছে ইসলামের প্রতিষ্ঠাতা মুুহাম্মদের বলা সেই সকল বাণীর সংকলন যেগুলিকে সরাসরি আল্লাহ্র প্রত্যাদেশ বলা হয়, আর হাদীস হচ্ছে মুহাম্মদের কথা, কাজ এবং কাজের অনুমোদন সংক্রান্ত সংকলনগুলি।<br />\r\nএটা ঠিক যে, আধুনিক যুগে ইসলামের বহু নিয়ম ও রীতিনীতি মেনে চলা সম্ভব হয় না বা মানাও হয় না। যেমন ধরা যাক চিত্র নির্মাণের কথা। ইসলামে প্রাণীর মূর্তি বা চিত্র নির্মাণ কঠোরভাবে নিষিদ্ধ হলেও এটা এখন খোদ সৌদী আরবও সব ক্ষেত্রে মানে না। ফলে হজের জন্য পাসপোর্ট করতে হলেও ছবি লাগে। এখন ছবি নিষিদ্ধ করলে পাসপোর্ট এবং সেই সঙ্গে সৌদী নাগরিক নয় এমন সবার হজ নিষিদ্ধ করতে হয়। ইসলামে নিষিদ্ধ বা অনাকাক্সিক্ষত এমন বহু কিছু আছে যেগুলিকে নিষিদ্ধ বা বন্ধ করা সম্ভব নয়। ফলে সিনেমা, অভিনয়, পর নারী-পুরুষের মেলামেশা, নারীদের শিক্ষা ও পুরুষ নিকটাত্মীয় সঙ্গে না নিয়ে এবং মুখ-মাথা-শরীর না ঢেকে বাহিরে যাওয়া ইত্যাদি অনেক কিছুকে ইসলামী সমাজ মেনে নিতে বাধ্য হচ্ছে। কিন্তু মনে রাখতে হবে এসবই সাধারণভাবে পাশ্চাত্য সভ্যতার আধিপত্য, নিয়ন্ত্রণ এবং প্রভাবের ফল। ফলে এসবই বহিরারোপিত, যার প্রতি থাকে অক্ষম ইসলামের অন্তর্গত ঘৃণা ও প্রতিরোধের আকাক্সক্ষা, যা যে কোনও সুযোগে বিস্ফোরিত হতে ও আত্মপ্রকাশ করতে পারে।<br />\r\nইসলামে সমস্ত ক্ষমতা এককেন্দ্রীভূত। খ্রিস্ট ধর্মে একেশ্বরবাদ থাকলেও ঈশ্বরপুত্র যিশু খ্রিস্টের হাতে এ পৃথিবীর যাবতীয় ক্ষমতা অর্পিত। বাইবেলে বলা হচ্ছে বিচার দিবস পর্যন্ত স্বর্গ ও মর্ত্যরে যাবতীয় ক্ষমতা ঈশ্বর যিশুর হাতে অর্পণ করেছেন। শুধু তাই নয় বিচার দিবসে ঈশ্বরের পাশে বসে যীশু মানুষের পাপ-পুণ্যের বিচার করবেন। এভাবে খ্রিস্ট ধর্ম তত্ত্বগতভাবে দ্বৈততার এমন এক অবস্থান দিয়েছে যা সমস্ত ক্ষমতা এক হাতে কেন্দ্রীভূত করার পথে বিরাট বাধা অর্পণ করে। খ্রিস্ট ধর্ম একেশ্বরবাদী হলেও এটা দ্বৈততার এমন এক জায়গা তৈরী করেছে যা চিন্তা প্রক্রিয়ার মাধ্যমে সমাজের ভিতর ব্যক্তি এবং ফলত গণতন্ত্রের অস্তিত্বের একটা সুযোগ করে রাখে ইসলামে যেটার কল্পনাও করা চলে না। অন্যদিকে, খ্রিস্ট ধর্ম মানুষকে ঈশ্বরের দাস মাত্র করে নাই যে তার কোনও অধিকার থাকবে না। সে দয়াময় বা প্রেমময় ঈশ্বরের সন্তানও বটে। ফলে এই সন্তান তার পিতার কাছে অনেক কিছুই দাবি করতে পারে। কিন্তু ইসলামে স্বাধীন মানুষের অস্তিত্ব নাই। সকলেই আল্লাহ্র বান্দা বা দাস। কুরআনে বলা হচ্ছে, &lsquo;আকাশমণ্ডলী ও পৃথিবীতে এমন কেহ নাই, যে দয়াময়ের নিকট বান্দা রূপে উপস্থিত হইবে না&rsquo; (১৯ সূরা র্মায়াম : ৯৩ আয়াত। আল-কুরআনুল করীম, প্রকাশক- ইসলামিক ফাউন্ডেশন বাংলাদেশ, ঢাকা, ছত্রিশতম মুদ্রণ, ডিসেম্বর ২০০৭)। অথবা বলা হচ্ছে, &lsquo;আমার দাসত্বের জন্যই মানুষ ও জিনকে সৃষ্টি করেছি&rsquo; (১৯:৫৬। কোরান সূত্র, অনুঃ মুহাম্মদ হাবিবুর রহমান, প্রকাশক, বাংলা একাডেমী, ঢাকা, প্রকাশ : ১৯৮৪)। হাদীসে মুহাম্মদ নিজেও বলছেন &lsquo;আমি আল্লাহ্র বান্দা ও রসূল&rsquo; (বোখারী শরীফ [বাংলা তরজমা ও বিস্তারিত ব্যাখ্যা], তৃতীয় খণ্ড, অনু- মাওলানা শামছুল হক ফরিদপুরী (রঃ) ও মাওলানা আজিজুল হক সাহেব; হামিদিয়া লাইব্রেরী, ঢাকা, একাদশ সংস্করণ, জুন ২০০৭, পৃষ্ঠা- ৯২)। অর্থাৎ এই ধর্মে মুহাম্মদসহ সবাই আল্লাহ্র দাস। এই রকম সর্বাত্মক দাসত্বের ধর্মে স্বাবাভিকভাবেই ইসলামের বান্দা তার মালিক বা প্রভু আল্লাহ্র কাছে তেমন কোন দাবিই করতে পারে না যা খ্রিস্ট ধর্মের মানুষ তার পিতা ঈশ্বরের কাছে করতে পারে। সুতরাং মানুষের স্বাধীনতার চেতনা ইসলামের চেতনা বিরোধী। বান্দাত্ব বা দাসত্বের সর্বাত্মক চেতনার উপরই ইসলাম প্রতিষ্ঠিত।<br />\r\nএই রকম এক বান্দা সমাজ তথা দাস সমাজ ব্যবস্থা প্রতিষ্ঠায় মুহাম্মদ (জন্ম আনুমানিক ৫৭০ খ্রি:-মৃত্যু ৬৩২ খ্রি:) যে পথ বেছে নিয়েছিলেন তা হল যুদ্ধ। যতদিন তিনি মক্কায় শান্তিপূর্ণভাবে ধর্ম প্রচার করেছিলেন ততদিন তার অনুসারীদের সংখ্যা ও শক্তি খুব সামান্য ছিল। মক্কায় তেরো বৎসরে তার অনুসারী একশত থেকে দেড়শত জনের মত ছিল। কিন্তু মদীনায় রাষ্ট্র প্রতিষ্ঠার পর এই অবস্থা বদলে যায়। মদীনায় গিয়ে তিনি যে পথ গ্রহণ করেন তা হল যুদ্ধ। কুরআনে বলা হল, &lsquo;তোমাদের জন্য যুদ্ধের বিধান দেওয়া হইল যদিও তোমাদের নিকট ইহা অপ্রিয়। কিন্তু তোমরা যাহা অপছন্দ কর সম্ভবত তাহা তোমাদের জন্য কল্যাণকর এবং যাহা ভালবাস সম্ভবত তাহা তোমাদের জন্য অকল্যাণকর। আল্লাহ্ জানেন আর তোমরা জান না&rsquo; (২:২১৬)। অথবা &lsquo;হে মুমিনগণ! তোমাদের হইল কী যে, যখন তোমাদিগকে আল্লাহ্র পথে অভিযানে বাহির হইতে বলা হয় তখন তোমরা ভারাক্রান্ত হইয়া ভূতলে ঝুঁকিয়া পড়?&hellip;.. যদি তোমরা অভিযানে বাহির না হও, তবে তিনি তোমাদিগকে মর্মন্তুদ শাস্তি দিবেন &hellip;..&rsquo; (৯:৩৮-৩৯)। বুঝাই যায় যে মুসলমানরা প্রথম দিকে যুদ্ধের পথে যেতে অনিচ্ছুক ছিল, যে কারণে আল্লাহ্র পক্ষ থেকে যুদ্ধের সপক্ষে এভাবে আরও অনেক ওহী বা আয়াত নাজিল করতে হয়েছে। এই বাস্তবতায় একদিকে নিরঙ্কুশ একেশ্বরবাদ এবং অপর দিকে যুদ্ধ ইসলামকে যে বিশিষ্টতা দিয়েছে তা দিয়ে তাকে বুঝতে হবে। (ইসলামে যুদ্ধের বিস্তারিত ভূমিকা জানার জন্য দেখুন : এম এ খান, জিহাদ : জবরদস্তিমূলক ধর্মান্তরকরণ, সা&curren;্রাজ্যবাদ ও দাসত্বের উত্তরাধিকার, ব-দ্বীপ প্রকাশন, কাঁটাবন, শাহবাগ, ঢাকা।)<br />\r\nবুদ্ধ বা যিশুর মতাদর্শ বা ধর্ম ছিল অহিংসা ও শান্তিতে বিশ্বাসী। বুদ্ধ বা যিশু সংসারীও ছিলেন না। মানুষের মুক্তির পথের সন্ধানে রাজার পুত্র গৌতম বুদ্ধ সংসার ও স্ত্রী-পুত্র ত্যাগ করেন এবং শান্তিপূর্ণভাবে তার শান্তি ও অহিংসার ধর্ম প্রচার করেন। একইভাবে যিশুও তার ধর্ম প্রচারে মানুষের প্রতি শান্তি ও অহিংসাকে গুরুত্ব দেন। যিশু বলছেন, &lsquo;যে কেহ তোমার দক্ষিণ গালে চড় মারে, অন্য গাল তাহার দিকে ফিরাইয়া দেও&rsquo; (বাইবেল : নূতন নিয়ম, মথি- ৫:৩৮)। যিশুর অনেক পরবর্তী কালে খ্রিস্ট ধর্মে জবরদস্তি ও যুদ্ধ প্রবেশ করলেও সেটা ছিল খ্রিস্টানদের প্রতি দীর্ঘ নির্যাতন ও সহিংসতার প্রতিক্রিয়া। বিশেষ করে দীর্ঘ ইসলামী আগ্রাসনে এশিয়া ও পশ্চিম আফ্রিকা থেকে খ্রিস্ট ধর্ম উচ্ছেদ হয়ে যায়। ইউরোপ ছিল দীর্ঘকাল ধরে অব্যাহত ইসলামী আরব ও তুির্ক আক্রমণের শিকার। এছাড়া ছিল প্যালেস্টাইনে যাওয়া ইউরোপের খ্রিস্টান তীর্থযাত্রীদের প্রতি দুর্ব্যবহার। এইসব ঘটনার প্রতিক্রিয়ায় ঘটে ক্রুসেড বা ধর্মযুদ্ধ। তবে মনে রাখতে হবে পোপ তথা চার্চের আহ্বানে ক্রুসেড সংগঠিত হলেও যুদ্ধ করেছিল যোদ্ধা ও রাজারা।<br />\r\nবস্তুত ধর্মের প্রভাবে যেসব সমাজ প্রতিষ্ঠিত হয় সেইসব সমাজের মূল বৈশিষ্ট্য, প্রবণতা এবং চরিত্র বুঝবার সর্বশ্রেষ্ঠ উপায় হচ্ছে সেই সব ধর্মের প্রতিষ্ঠাতাদেরকে বোঝা। এটা বুঝতে হবে যে, একটা সমাজ যত শান্তিবাদী হোক বাইরের শক্তির আগ্রাসন ও অধীনতা বা ধ্বংস থেকে তা বাঁচতে পারে না যদি তার সামরিক বা যুদ্ধের শক্তি না থাকে। এভাবে আমরা দেখি খ্রিস্টান বা বৌদ্ধ সমাজেও যুদ্ধ ও সহিংসতার উপস্থিতি। এটা হচ্ছে বাস্তবতা বা ব্যবহারিক জীবনের চাহিদা বা দাবির পূরণ। কিন্তু তার মানে এই নয় যে যুদ্ধকে সমাজ বিশেষত ধর্মীয় দৃষ্টি থেকে তার নৈতিক আদর্শ হিসাবে গ্রহণ করে। এই অবস্থায় আত্মরক্ষার প্রয়োজনে ধর্মীয় প্রতিষ্ঠান বা ধর্মের দিক থেকে কখনও যুদ্ধকে গ্রহণ করা বা বৈধতা দেওয়া হলেও যুদ্ধকে নৈতিক শ্রেষ্ঠত্ব প্রদান করা হয় না। যে কারণে খ্রিস্টান পাদ্রি বা বৌদ্ধ শ্রমণ তলোয়ার হাতে যুদ্ধ করে বেড়ায় না। কিন্তু ইসলামের ক্ষেত্রে আমরা বিপরীতটা ঘটতে দেখি। এখানে ধর্ম প্রচারক ও যোদ্ধা একই ব্যক্তি।<br />\r\nবুদ্ধ বা যিশুর পাশে মুহাম্মদকে দাঁড় করালে ইসলামকে আমরা অনেক সহজে বুঝতে পারব। ইসলামকে শান্তির ধর্ম বলা হলেও তা প্রতিষ্ঠিত হয়েছে যুদ্ধের মাধ্যমে। ইসলামী ইতিহাসবিদদের দ্বারা লিখিত মুহাম্মদের জীবনীতে মদীনায় তার বসবাসকালীন শেষ দশ বৎসরে মুহাম্মদ কর্তৃক পরিচালিত বা নির্দেশিত ৭০ থেকে ১০০টি ব্যর্থ বা সফল আক্রমণ, লুণ্ঠন অভিযান এবং যুদ্ধের উল্লেখ পাওয়া যায়। এর মধ্যে ১৭ থেকে ২৯টিতে মুহাম্মদ নিজে নেতৃত্ব দিয়েছিলেন (দেখুন : এমএ খান, জিহাদ : জবরদস্তিমূলক ধর্মান্তরকরণ, সা&curren;্রাজ্যবাদ ও দাসত্বের উত্তরাধিকার, ব-দ্বীপ প্রকাশন, ঢাকা, প্রকাশ- ২০১০, পৃষ্ঠা- ৮৯-৯০)। মজার ব্যাপার হচ্ছে বদর, ওহোদ ও খন্দক বাদে এই যুদ্ধগুলির সবই পরিচালিত হয়েছে অন্যান্য ধর্মাবলম্বী জনগোষ্ঠী বা উপজাতিদের উপর বাধ্যতামূলকভাবে তার ধর্ম চাপিয়ে দিবার জন্য। বদর, ওহোদ ও খন্দকের যুদ্ধ ছিল মুহাম্মদের নিজ গোত্র কুরাইশদের দিক থেকে মদীনার উপর আক্রমণ। কিন্তু কুরাইশদের দ্বারা পরিচালিত প্রথম যুদ্ধ বদরের যুদ্ধ ছিল কুরাইশদের বাণিজ্য বহরসমূহের উপর মদীনার ইসলামী বাহিনীর আক্রমণ, লুণ্ঠন ও হত্যা বন্ধ করার উদ্দেশ্যে পরিচালিত। এই যুদ্ধে মক্কার কুরাইশ বাহিনী পরাজিত হয়ে ফিরে যায়। এরপর কুরাইশ সেনাবাহিনী পুনরায় মদীনা আক্রমণ করলে ওহোদের প্রান্তরে যে যুদ্ধ হয় তাতে ইসলামী বাহিনী পরাজিত ও ছত্রভঙ্গ হয়ে যায়। মুহাম্মদ নিজে গুরুতর আহত ও অজ্ঞান হয়ে যান। কুরাইশরা ইচ্ছা করলে তাকে হত্যা করতে পারত। কিন্তু তারা তা না করে চলে যায়। যাবার পূর্বে তারা বলে যে, তারা মুহাম্মদকে হত্যা করতে আসে নাই, বরং বদরের যুদ্ধে পরাজয়ের প্রতিশোধ নিতে এসেছিল। সেই প্রতিশোধ নেওয়া হয়েছে। সুতরাং তারা ফিরে যাচ্ছে।<br />\r\nএটা বোঝা যায় যে, যে কারণে কুরাইশরা মক্কায় মুহাম্মদকে তার ধর্ম প্রচারের তেরো বৎসর সহ্য করেছিল একই কারণে তারা মুহাম্মদকে হত্যা না করে ফিরে গিয়েছিল। তারা চেয়েছিল তিনি যাতে শান্তিপূর্ণভাবে তার ধর্ম প্রচার করেন। সুতরাং ওহোদের যুদ্ধের পর তারা সন্তুষ্ট চিত্তে ফিরে গিয়েছিল। কিন্তু তারা মুহাম্মদকে চিনতে ভুল করেছিল। এরপর মুহাম্মদ পুনরায় ধর্ম প্রচারে তার যুদ্ধ তৎপরতায় ফিরে যান। কুরাইশদের তৃতীয় এবং শেষ আক্রমণ ছিল খন্দকের যুদ্ধ। মুহাম্মদ কুরাইশদের বিশাল বাহিনীর মোকাবিলায় সম্মুখ যুদ্ধে অবতীর্ণ না হয়ে পরিখা খনন করে আত্মরক্ষার ব্যবস্থা করেন। আরবরা এমন কোন যুদ্ধ কৌশলের কথা ইতিপূর্বে জানত না এবং এ ধরনের যুদ্ধের জন্য তাদের কোন প্রস্তুতি ছিল না। সুতরাং কয়েক দিন (প্রায় কুড়ি দিন, কারও মতে প্রায় এক মাস। বোখারী শরীফে বলা হচেছ, &lsquo;প্রায় দীর্ঘ এক মাসকাল এই অবস্থা চলিল&rsquo; তৃতীয় খণ্ড, অনুবাদ- মাওলানা শামছুল হক ও মাওলানা আজিজুল হক, পৃষ্ঠা- ১৯২)। অবরোধ করার পর কুরাইশরা হতোদ্যম হয়ে মক্কায় ফিরে যায়। এটাই ছিল কুরাইশদের দিক থেকে প্রত্যাঘাতের শেষ প্রয়াস।<br />\r\nযাইহোক, যুদ্ধ ইসলামকে যে বিশিষ্টতা দিয়েছে তা না বুঝলে আমরা ইসলামের কিছুই বুঝব না। পৃথিবীর কোন ধর্মই যুক্তি-প্রমাণ নির্ভর নয়। সবই বিশ্বাস বা আরও সঠিকভাবে বললে অন্ধ বিশ্বাস নির্ভর। আরবে তখন বিভিন্ন উপজাতির নিজস্ব যেসব ধর্ম ও দেবতা ছিল সেগুলি যেমন অন্ধ বিশ্বাস নির্ভর ছিল তেমন খ্রিস্টান ও ইহুদীদের ধর্মও ছিল অন্ধ বিশ্বাস নির্ভর। কিন্তু মুহাম্মদ বললেন একমাত্র তার ধর্মই সত্য এবং অন্য সকল ধর্ম মিথ্যা। সুতরাং যারা তার ধর্মমত তথা তার আনুগত্য গ্রহণ করবে না তাদেরকে তিনি কাফের অর্থাৎ সত্য প্রত্যাখ্যানকারী বললেন। স্বাভাবিকভাবে সবাই যার যার নিজ বিশ্বাসকে সত্য মনে করে নিজ নিজ ধর্মকে ধরে রাখতে চাইবে। এ ক্ষেত্রে মুহাম্মদ অন্যদের উপর নিজ ধর্মকে চাপিয়ে দিবার জন্য তলোয়ার তথা যুদ্ধের আশ্রয় নিলেন। যুদ্ধের অনুষঙ্গ হিসাবে যা যা আনা দরকার মনে করলেন সবই তিনি তার ধর্মে আনলেন। তার পক্ষে যুদ্ধ করে যারা মৃত্যু বরণ করবে তাদেরকে তিনি মৃত্যুর পর স্বর্গ বা বেহেশতের প্রতিশ্র&ldquo;তি দিলেন। তিনি প্রতিপক্ষের সম্পদ লুণ্ঠনকে ধর্ম সম্মত করলেন। পরাজিতদের তিনি বন্দি এবং দাস করলেন। তাদেরকে এবং তাদের সকল সম্পদ ও সম্পত্তি তিনি গনীমতের মাল হিসাবে ভাগবাটোয়ারা করলেন। এই গনীমতের মাল হল নারী ও শিশুরাও। কুরআনের নির্দেশ (কুরআন- ৮:৪১) হাজির করে গনীমতের মালের এক পঞ্চমাংশ বা ২০% নিজের ভাগে রেখে বাকি চার পঞ্চমাংশ বা ৮০% যুদ্ধে অংশগ্রহণকারীদের মধ্যে বণ্টনের ব্যবস্থা করলেন।<br />\r\nএভাবে অপরের সম্পদ ও সম্পত্তি লুণ্ঠন এবং অপরকে দাস-দাসী করার এক ধর্ম প্রতিষ্ঠা করা হল। এর ফলে ধর্মের মাধ্যমে ইসলামী সমাজে দাস ব্যবস্থা ব্যাপকায়তনে প্রাতিষ্ঠানিক রূপ লাভ করে। শুধু তা-ই নয় যুদ্ধে বন্দিনী বা দাসী ধর্ষণকেও ধর্মীয় বিধানে বৈধতা দেওয়া হল। কুরআনে বলা হল : &lsquo;যাহারা নিজেদের যৌন অঙ্গকে সংযত রাখে, নিজেদের পত&oelig;ী অথবা অধিকারভুক্ত দাসীগণ ব্যতীত, ইহাতে তাহারা নিন্দনীয় হইবে না&rsquo; (২৩:৫-৬, আল-কুরআনুল করীম, প্রকাশক- ইসলামিক ফাউন্ডেশন বাংলাদেশ, ঢাকা, ডিসেম্বর ১৯৮৭)। &lsquo;এবং যাহারা নিজেদের যৌন অঙ্গকে সংযত রাখে, তাহাদের পত&oelig;ী অথবা অধিকারভুক্ত দাসী ব্যতীত, ইহাতে তাহারা নিন্দনীয় হইবে না&rsquo; (৭০:২৯-৩০) ইত্যাদি। এ ছাড়া বিভিন্ন হাদীস থেকে আমরা &lsquo;অধিকারভুক্ত দাসীদের&rsquo; প্রতি ইসলামের যোদ্ধাদের এই ধরনের অধিকারের প্রয়োগ সম্পর্কে জানতে পারি। একটি হাদীসের কিছুটা উদ্ধৃত করা যাক, &lsquo;আবু সায়ীদ খুদরী (রাঃ) বর্ণনা করিয়াছেন, কোন এক জেহাদে শত্র&ldquo; পক্ষের লোক আমাদের হাতে বন্দি হইল। (নারী বন্দিনীদের সুব্যবস্থাপনাকল্পে শরীয়ত সম্মত বৈধ সম্পর্ক সূত্রে) তাহারা আমাদের করায়ত্তে আসিলে পর আমরা নিজ নিজ প্রাপ্ত রমণীকে ব্যবহার করিলাম&rsquo; (বোখারী শরীফ [বাংলা তরজমা ও বিস্তারিত ব্যাখ্যা], ষষ্ঠ খণ্ড, অনু- মাওলানা শামছুল হক ও মাওলানা আজিজুল হক; হামিদিয়া লাইব্রেরী লি:, ঢাকা, সপ্তম সংস্করণ ২০০৬, পৃ-১৪৯)<br />\r\nএই ধর্ষণের চর্চা অন্যান্য সাহাবার মত ইসলামের নবী মুহাম্মদও যে করতেন কুরআন-হাদীসের যে কোন মনোযোগী পাঠক তা বুঝবেন। এ প্রসঙ্গে এখানে বেশি আলোচনা না করে একটা উদাহরণ উল্লেখ করব। মদীনার বানু কুরাইযা গোত্র ছিল ইহুদী। মুহাম্মদ ও ইসলামের বিরুদ্ধে তারা চক্রান্ত করছে এই অভিযোগ তুলে মুহাম্মদ শান্তিপূর্ণ এবং যুদ্ধের জন্য অপ্রস্তুত বানু কুরাইযা গোত্রকে আক্রমণ করলেন। সবাইকে বন্দি করা হল। অতঃপর মুহাম্মদ যৌনাঙ্গে কেশ গজিয়েছে এমন সকল পুরুষ বন্দিকে প্রাপ্তবয়স্ক বিবেচনা করে হত্যার নির্দেশ দেন। নিজ হাতে বানু কুরাইযা গোত্র প্রধান কাব বিন আসাদের শিরো&ntilde;েদ করে তিনি এই হত্যাযজ্ঞের সূচনা করেন। একটি গর্ত খুঁড়ে তার পাশে এভাবে গলা কেটে হত্যা করে লাশ সেই গর্তে ফেলা হচ্ছিল। এভাবে সকাল থেকে সন্ধ্যার অন্ধকার হলে মশাল জ্বালিয়ে ৮০০ থেকে ৯০০ ইহুদীকে হত্যা করা হয় (ইবনে ইসহাক, সিরাত এ রাসুলুল্লাহ্)। (বানু কুরাইযা হত্যাকাণ্ড সম্পর্কে বিস্তারিত জানার জন্য দেখুন : &lsquo;ইসলাম বিতর্ক&rsquo; গ্রন্থে সংকলিত আয়েশা আহমেদ-এর (বনি ক্বারাইযার হত্যাযজ্ঞ ঃ মুসলিম উম্মার সবচেয়ে খুশীর দিন, ব-দ্বীপ প্রকাশন, ঢাকা, ২০১০। পৃ : ৬৮)<br />\r\nইতিমধ্যে বন্দি নারী ও শিশুদের গনীমতের মাল হিসাবে ভাগবাটোয়ারা করা হয়। এরা হল দাস-দাসী। সেই সঙ্গে ভাগ করা হল ইহুদীদের সহায়-সম্পদ যা ছিল সবকিছু। এই ভাগ বাটোয়ারায় মুহাম্মদ নিজ ভাগে নেন এক সুন্দরী তরুণী রায়হানাকে। অন্যান্য বন্দির মত রায়হানা প্রত্যক্ষ করে তার স্বামী, পিতা এবং ভাইদেরকে গলা কেটে হত্যা করার দৃশ্য। ঐ নারীর &lsquo;ট্রমা&rsquo; আমরা সহজেই অনুভব করতে পারি।<br />\r\nকিন্তু এখানেই শেষ নয়। ঐ নারীর অমন ভয়ঙ্কর অভিজ্ঞতার পরেও ঐ রাতেই নবী রায়হানাকে সম্ভোগ করার জন্য নিজ শয্যায় টেনে নিয়ে যান। ঐ একই রাতে মুহাম্মদের অন্য সাহাবীরাও নিজদের ভাগের নারীদেরকে ধর্ষণ করে, যারা একইভাবে এক ভয়ংকর &lsquo;ট্রমা&rsquo;র শিকার হয়েছিল সারাদিন ধরে আপনজনদের হত্যাযজ্ঞ দেখে। ইসলামের এই এক পদ্ধতিই যথেষ্ট মানুষের অনুভূতিকে ধ্বংস করে দিবার জন্য। এটা অস্বাভাবিক নয় যে ইসলামের এই ধরনের ভয়ংকরতার শিকার যারা হয় তারা তাদের শিকারীদের মতই একটা পর্যায়ে মানবিকতার সব কিছু হারিয়ে ফেলে।<br />\r\nবন্দি নারী এবং দাসী সম্ভোগে ইসলামের নবীর রুচির কথা আমাদের অজানা নয়। উপহার হিসাবে পাঠানো মিসরের সুন্দরী দাসী মারিয়ার গর্ভে মুহাম্মদের এক স্পল্পায়ু পুত্রের কথাও আমরা হাদীস থেকে জানতে পারি, যার নাম ছিল ইবরাহীম।<br />\r\nবস্তুত ইসলামের নবী মুহাম্মদ হলেন ইসলামের প্রথম হেরেম প্রতিষ্ঠাতা, যেখানে ছিল তার বহু সংখ্যক স্ত্রী এবং যুদ্ধবন্দি দাসী। তার স্ত্রীর সংখ্যা নিয়ে মতৈক্য নাই। সেটা নি&curren;ে&oelig; নয় বা এগারো এবং ঊর্ধ্বে কুড়ি বা একুশ হতে পারে। নারীর প্রতি অসংযত লালসা যে নবীর জীবনের এক গুরুত্বপূর্ণ প্রবণতা ছিল তা তার ব্যক্তি জীবন সম্পর্কে কিছু জানলেই বুঝা যায়।<br />\r\nমুহাম্মদের পালক পুত্র যায়িদের স্ত্রী ছিলেন যয়নব। এক সময় তার প্রতি মুহাম্মদের আসক্তি জন্মালে আরবের সর্বজন পালিত রীতিকে লঙ্ঘন করে যায়িদ থেকে ছাড়িয়ে নিয়ে যয়নবকে তিনি বিবাহ করেন। অথচ তৎকালীন আরবে পালক-পুত্রকে আপন পুত্রসম জ্ঞান করা হত। এই রীতি লঙ্ঘনের সমর্থনে তিনি আল্লাহর তরফ থেকে ওহী উপস্থিত করেন, &lsquo;তোমার পোষ্যদিগকে তিনি তোমাদের পুত্র করেন নাই&rsquo; (কুরআন- ৩৩:৫৪)। তাছাড়া দ্রষ্টব্য কুরআনের ৩৩:৩৭ আয়াত যেখানে যয়নবকে বিবাহের সপক্ষে আল্লাহ্র বক্তব্য দেওয়া হয়েছে। (যয়নবকে বিবাহ সংক্রান্ত বিশদ আলোচনার জন্য দেখুন : মুমিন সালিহ্, যয়নব ও জানোয়ার : মুহাম্মদের সঙ্গে যয়নবের স্বর্গীয় বিবাহ এবং যায়িদের জীবন ধ্বংস, ইসলাম বিতর্ক, প্রকাশক- ব-দ্বীপ প্রকাশন, ঢাকা, ২০১০, পৃষ্ঠা-৩৯-৪৭)। যয়নব ছিলেন নবী থেকে তেইশ বৎসরের ছোট।<br />\r\nকিন্তু আবু বকরের ছয় বৎসরের শিশু কন্যা আয়েশাকে পঞ্চাশ বৎসর বয়সী নবীর বিবাহকে কী দৃষ্টিতে দেখা যাবে? জীবনের ভাল-মন্দ কিছুই বুঝবার বয়স যার হয় নাই এবং পুতুল নিয়ে যার খেলার কথা তাকে পঞ্চাশ বৎসরের বৃদ্ধ নবী বিয়ে করেন। আয়েশার বয়স নয় হলে মুহাম্মদ তাকে স্বগৃহে নিয়ে যান।<br />\r\nএকটা ধর্মের প্রতিষ্ঠাতা যদি তার ধর্মমত প্রতিষ্ঠার জন্য আক্রমণ, যুদ্ধ, লুণ্ঠন, ধর্ষণ এবং দাসকরণের পথ বেছে নেয় তবে তার ফল কত ভয়ানক হতে পারে তা সহজে অনুমেয়। এই সবকিছুকেই অনুমোদন দেওয়া হয়েছে আল্লাহ্র নামে। অর্থাৎ আল্লাহর ধারণা এবং বিশ্বাস ছিল মুহাম্মদের হাতিয়ার।<br />\r\nমরুময় আরবের যাযাবর ও পশুপালক বেদুইন উপজাতিরা ছিল যুদ্ধপ্রবণ। কুরাইশরা তাদের ঐক্যবদ্ধ ও সংযত করার জন্য বিভিন্ন পদক্ষেপ দিয়েছিল। যেমন বিভিন্ন উপজাতির নিজ নিজ দেবতার প্রতীক হিসাবে তাদের মূর্তিকে কাবাগৃহে স্থান দান এবং ধর্মমন্দির হিসাবে কাবাকে কেন্দ্র করে নিয়মিত বাৎসরিক পূজা ও মিলন মেলার আয়োজন করা। এভাবে কাবা হয়ে ওঠে বৃহত্তর আরবের ঐক্যের প্রতীক। বলা হয় কাবা গৃহে ছিল ৩৬০টি প্রতিমা। এছাড়া আরবের উপজাতিগুলির মধ্যে শান্তি স্থাপনের লক্ষ্যে চার মাস যুদ্ধ নিষিদ্ধ করা হয়েছিল, যা আরবরা কঠোরভাবে মেনে চলত। এভাবে কুরাইশরা পেগান বা পৌত্তলিক আরবদের মধ্যে প্রচলিত বিভিন্ন দেবতা-বিশ্বাস বা উপজাতীয় ধর্ম-বিশ্বাসকে কেন্দ্র করে আরবে এমন একটা ঐক্য গড়ে তুলছিল যার পরিণতি ছিল একটা রাষ্ট্র গঠন, যা তখন পর্যন্ত মূল আরব ভূখণ্ডে ছিল না। অর্থাৎ কুরাইশরা বিভিন্ন গোত্র-উপজাতি ও ধর্ম বিশ্বাসের সমন্বয়ে আরবে শান্তিপূর্ণভাবে একটি বৃহত্তর ঐক্যবদ্ধ সমাজ এবং রাষ্ট্র গঠনের প্রক্রিয়া চালু করেছিল। মুহাম্মদ এই রাষ্ট্র গঠনের প্রক্রিয়াকে ভিন্ন রূপ ও পদ্ধতি দান করেন যুদ্ধ এবং বলপ্রয়োগের মাধ্যমে নিজের নিরঙ্কুশ একনায়কত্ব প্রতিষ্ঠা দ্বারা। এ কাজে ধর্ম এবং ধর্মযুদ্ধ বা জিহাদ হল তার হাতিয়ার। মুহাম্মদের ধর্ম প্রচারে যুদ্ধ এমনই গুরুত্বপূর্ণ হাতিয়ার হল যে আরবের সুদীর্ঘ ঐতিহ্য ও রীতিকে উড়িয়ে দিয়ে যে চার মাস ছিল যুদ্ধের জন্য নিষিদ্ধ এবং শান্তির সময় সেই সময়েও তিনি আক্রমণ ও যুদ্ধ শুরু করলেন। এই সবই করলেন তিনি আল্লাহর নামে। কুরাইশ এবং মক্কার আশপাশের আরবদের প্রধান দেবতা আল্লাহকে তিনি একমাত্র দেবতা বা উপাস্য হিসাবে ঘোষণা করলেন এবং অন্য সকল দেবতাকে নাকচ করলেন। যুদ্ধের মাধ্যমে আল্লাহর নিরঙ্কুশ একত্ব (লা শরীক আল্লাহ) ও শ্রেষ্ঠত্বের (আল্লাহু আকবর) ধারণা প্রতিষ্ঠা দ্বারা আরবের বিদ্যমান উপজাতীয় স্বাতন্ত্র্যের ভিত্তিকে চূর্ণ করে একটি যুদ্ধ ভিত্তিক নিরঙ্কুশ একনায়কী ধর্মীয় সমাজ ও রাষ্ট্র প্রতিষ্ঠা করলেন। ধর্মকে ব্যবহার করে তিনি হলেন এই রাষ্ট্রের নিরঙ্কুশ একনায়ক।<br />\r\nএই একনায়কী সামরিক রাষ্ট্র প্রতিষ্ঠার জন্য মুহাম্মদ আরব মরুচারী বেদুইনদের সহিংসতা, লুণ্ঠনপরায়ণতা এবং ধর্ষণপরায়ণতাকে অর্গলমুক্ত করে ব্যবহার করলেন। এক উপজাতিকে ইসলামে দীক্ষিত করে তাকে তিনি আর এক অমুসলিম উপজাতিকে আক্রমণের বাহিনী হিসাবে ব্যবহার করলেন। আক্রান্ত ও পরাজিত উপজাতি লুণ্ঠিত ও ধর্ষিত হবার যে অভিজ্ঞতা লাভ করল বাধ্যতামূলকভাবে ইসলাম গ্রহণ করার পর একই অভিজ্ঞতার শিক্ষা তারা অন্যদের উপরেও প্রয়োগ করল। এটা একটা চেইন রিঅ্যাক্শন। যুদ্ধ, লুণ্ঠন এবং ধর্ষণের মধ্য দিয়ে গোটা আরবের ইসলামীকরণ সম্পন্ন হলে একই প্রক্রিয়ায় চলল আরবের বাইরেও ইসলামের সম্প্রসারণ। ১৯৭১ সালে তৎকালীন পূর্ব বাংলায় পাকিস্তানী সেনাবাহিনী ইসলামের নামে যে হত্যা, লুণ্ঠন ও ধর্ষণ যজ্ঞ পরিচালনা করেছিল তা মুহাম্মদের দ্বারা প্রবর্তিত এই ঐতিহ্যেরই ধারাবাহিকতা মাত্র।<br />\r\nজিহাদ বা ধর্মযুদ্ধ হচ্ছে ইসলামের প্রাণশক্তির মূল উৎস। অর্থাৎ মুহাম্মদ তার নিরঙ্কুশভাবে একনায়কী আধিপত্য এবং ধর্ম প্রতিষ্ঠা করেছেন মূলত যুদ্ধের মাধ্যমে। এ ক্ষেত্রে অপর দুই প্রধান ধর্ম প্রতিষ্ঠাতা যিশু এবং বুদ্ধের সঙ্গে তার একটি মৌল পার্থক্যকে অত্যন্ত গুরুত্বের সঙ্গে বিবেচনায় নিতে হবে। মুহাম্মদ যিশু এবং বুদ্ধের মত শান্তিপূর্ণভাবে মানুষের হৃদয়ের কাছে আবেদন রেখে তার ধর্ম প্রতিষ্ঠা করেন নাই। তাই মক্কা থেকে মদীনায় যাবার পর থেকেই একজন আধিপত্যবাদী ও আগ্রাসী সেনাপতি বা যোদ্ধা হিসাবে তার আবির্ভাব। যোদ্ধা হিসাবে আবির্ভাবের পর থেকে বলপ্রয়োগের মাধ্যমে তিনি তার ধর্মের প্রসার ও প্রতিষ্ঠায় সফল হন। অর্থাৎ তিনি সাফল্যের সাথে তার কর্তৃত্ব, ক্ষমতা, সম্পদ এবং অনুুসারীদের সংখ্যা বৃদ্ধি করতে সমর্থ হন। সুতরাং মুহাম্মদের ধর্ম তথা ইসলামকে বুঝতে হবে মূলত যুদ্ধের ধর্ম হিসাবে। অপর পার্থক্যটিও কম গুরুত্বপূর্ণ নয়। সেটা হচ্ছে মুহাম্মদ যিশু বা বুদ্ধের মত কোন অরাজনৈতিক সামাজিক আন্দোলনের মধ্যে নিজেকে সীমাবদ্ধ রাখেন নাই, বরং ধর্মের মাধ্যমে এমন একটি রাজনৈতিক আন্দোলন সংগঠিত করেছিলেন যা রাষ্ট্র প্রতিষ্ঠার মাধ্যমে তাকে রাষ্ট্র নেতা বা শাসকে পরিণত করেছিল। অর্থাৎ মুহাম্মদকে চিনতে পারতে হবে একজন সশস্ত্র ও সহিংস ধর্ম প্রচারক, রাজনৈতিক নেতা এবং রাষ্ট্র প্রতিষ্ঠাতা হিসাবে। সুতরাং মুহাম্মদের ধর্ম ও রাষ্ট্রের ভিত্তি হচ্ছে যুদ্ধ। এখানে যুদ্ধ, রাষ্ট্র এবং ধর্ম একীভূত অথবা এমনইভাবে পরস্পরের সঙ্গে জড়িয়ে আছে যে একটাকে অপরটা থেকে বিচ্ছিন্ন করা সম্ভব নয়। সুতরাং যুদ্ধ কী জিনিস তা বুঝলে ইসলামের মর্ম উদ্ঘাটন করা আরও সহজ হবে।<br />\r\nপ্রথমে বুঝতে হবে যুদ্ধ কেন? অন্ধ বিশ্বাস বাদ দিলে উত্তর সহজ। ধর্ম বিশ্বাসকে ব্যবহার করে মুহাম্মদের নিজের একক শ্রেষ্ঠত্ব&iexcl; এবং আধিপত্য প্রতিষ্ঠা করা। যারা তার কথায় বিশ্বাস ক&rsquo;রে তার নিরঙ্কুশ শ্রেষ্টত্ব ও কর্তৃত্ব মেনে নিবে তথা তার কাছে আত্মসমর্পণ করবে তারা তার শত্র&ldquo; হবে না। যারা তা করবে না তারা হবে শত্র&ldquo;। তার ভাষায় এরা সবাই সত্য প্রত্যাখ্যানকারী অর্থাৎ কাফের। এই সত্য প্রত্যাখ্যানকারীদের বিরুদ্ধে তিনি করবেন জিহাদ বা যুদ্ধ।<br />\r\nতাহলে কী দাঁড়াচ্ছে? মুহাম্মদের মতানুযায়ী বিশ্ব-&macr;্রষ্টা ও বিশ্ব-শাসক হিসাবে আল্লাহ যেমন একমাত্র সত্য তেমন পৃথিবীতে তার প্রতিনিধি হিসাবে মুহাম্মদও একমাত্র সত্য। আল্লাহর অস্তিত্বের একমাত্র সাক্ষী ও প্রমাণ মুহাম্মদ নিজে। সুতরাং যারা তাকে বা তার কথা অস্বীকার করে তারা সবাই মিথ্যা। এই মিথ্যার উপর জয়যুক্ত হওয়া তথা তার স্বমতে এবং নিজ আধিপত্যাধীনে সবাইকে আনার জন্য যুদ্ধবিদ্যার নিয়ম অনুযায়ী তিনি সবই করেছেন বা করাকে যুক্তিসঙ্গত মনে করেছেন, যেটা পৃথিবীর সকল দক্ষ ও সফল সেনাপতি বা জেনারেলই করে। সুতরাং মুহাম্মদকে চিনতে হবে বুদ্ধ বা যিশুর বিপরীতে এমন একজন দক্ষ ও সফল জেনারেল রূপে যিনি তাঁর ধর্মমত ও আধিপত্য প্রতিষ্ঠা করেছেন যুদ্ধের মাধ্যমে। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">একজন আধিপত্যবাদী রাজা বা সেনানীর মত তিনি তার প্রতি আনুগত্য প্রদান কিংবা আরও সঠিকভাবে বললে আত্মসমর্পণের জন্য প্রথমে অন্যদেরকে আহ্বান জানাচ্ছেন। যারা সেই আহ্বানে সাড়া দিচ্ছে না তাদের বিরুদ্ধে তিনি যুদ্ধ করছেন। এ ক্ষেত্রে তিনি সকল দক্ষ সেনাপতি বা সমর নেতার মতই কৌশলকে অগ্রাধিকার দিচ্ছেন এবং সেই সঙ্গে ছল-চাতুরী, গোপনতা, মিথ্যা এবং প্রতারণাকে। মুহাম্মদ নিজেই বলছেন, &lsquo;কৌশলই যুদ্ধের প্রাণ বস্তু।&rsquo; (বোখারী শরীফ, [বাংলা তরজমা ও বিস্তারিত ব্যাখ্যা], ৩য় খণ্ড, অনুবাদ: মাওলানা শামসুল হক ফরিদপুরী [র:] ও মাওলানা আজিজুল হক সাহেব, প্রকাশকঃ হামিদিয়া লাইব্রেরী লি:, ঢাকা, একাদশ সংস্করণ, জুন ২০০৭, পৃষ্ঠা&Ntilde;৮৯)। কিংবা বলছেন, &lsquo;কূটকৌশলের নামই যুদ্ধ&rsquo; (হাদীস নং ৪৩৮৯- মুসলিম শরীফ, চতুর্থ খণ্ড, সম্পাদনা পরিষদের তত্ত্বাবধানে অনূদিত এবং সম্পাদিত, ইসলামিক ফাউন্ডেশন, তৃতীয় সংস্করণ জুন ২০১০, পৃষ্ঠা- ২৬৬)। অথবা &lsquo;কৌশলই হল যুদ্ধ&rsquo; (হাদীস নং ৪৩৯০, ঐ, পৃষ্ঠা- ২৬৭)।<br />\r\nশুধু এইটুকুই নয়, মুহাম্মদ আরও বলছেন, &lsquo;যুদ্ধ একটি প্রতারণা বিশেষ&rsquo; (সুনানু ইবনে মাজাহ্, দ্বিতীয় এ যেন মুহাম্মদেরও প্রায় এক হাজার বৎসর পূর্বে জন্ম নেওয়া চীনের বিশ্ববিখ্যাত সমর বিজ্ঞানী সান জু-এর কথার প্রতিধ্বনি। আজকের যুগে যুদ্ধ বিদ্যা বা সমর বিজ্ঞানের চর্চা যাঁরা করেন তাঁদের নিকট সান জু খুব পরিচিত একটা নাম। খ্রিস্টপূর্ব ৫০০ বৎসর পূর্বে চীনের উ রাজ্যের অজেয় সেনাপতি সান জু তার লেখা গ্রন্থ &lsquo;যুদ্ধবিদ্যা&rsquo; (ঞযব অৎঃ ড়ভ ডধৎ)-এর জন্য অমর হয়ে আছেন।<br />\r\nসান জু-এর মতে সামরিক শক্তি বা গায়ের জোরের চেয়ে কৌশল, গোপনতা এবং প্রতারণা যুদ্ধে অনেক বেশি গুরুত্বপূর্ণ। সান জু বলছেন, &lsquo;সব যুদ্ধের ভিত্তিই হচ্ছে প্রতারণা।&rsquo; (অষষ ধিৎভধৎব রং নধংবফ ড়হ ফবপবঢ়ঃরড়হ.)<br />\r\nযুদ্ধে গোপনতা রক্ষার উদ্দেশ্যে মুহাম্মদ যে তার অনুসারী বা সাহাবাদের নিকটও সত্যের পরিবর্তে মিথ্য বলতেন সে সম্পর্কেও আমরা হাদীস থেকে জানতে পারি। বোখারী শরীফের একটি হাদীস থেকে কিছুটা উদ্ধৃত করা যাক, &lsquo;রসুলুল্লাহ ছাল্লাল্লাহু অসাল্লাম কোন অভিযানের ইচ্ছা করিলে পূর্বাহ্নে উহার স্থান নির্দিষ্টরূপে উল্লেখ করিতেন না; বরং গোপনীয়তা রক্ষার্থ অন্য কোন স্থানের (এলাকা বা দিক রূপে) নাম উল্লেখ করিতেন&rsquo; (বোখারী শরীফ, তৃতীয় খণ্ড, অনু- মাওলানা শামছুল হক ও মাওলানা আজিজুল হক, হামিদিয়া লাইব্রেরী লি:, একাদশ সংস্করণ- জুন ২০০৭, পৃষ্ঠা- ২৬১)।<br />\r\nশত্র&ldquo;দের বিশ্বাসঘাতকতা বা ষড়যন্ত্র সম্পর্কে আল্লাহ্ যে পূর্বেই নবীকে জানিয়ে দিতেন সে কথা আমরা হাদীস থেকে জানতে পারি। এ প্রসঙ্গে বোখারী শরীফের একটি হাদীসের কথা আমরা উল্লেখ করতে পারি যেখানে বলা হয়েছে যে, ইহুদী বনু নযীর গোত্রের সঙ্গে মুহাম্মদের আলোচনা সভায় তাকে হত্যার জন্য বনু নযীররা হত্যার ষড়যন্ত্র করলে আল্লাহ্ ওহী মারফৎ তাকে তা জানালে তিনি আলোচনা না করে সভাস্থল থেকে উঠে চলে আসেন। &lsquo;ইহুদীগণ প্রকাশ্যে তাঁহাদিগকে সাদর আহ্বান জানাইল এবং খাতির-তাওয়াজু ও বন্ধুত্বের পরিচয় দিল; কিন্তু ভিতরে ভিতরে অন্যরূপ দুরভিসন্ধি করিল যে &hellip;.. একটি বড় পাথর রাসুলুল্লাহ ছাল্লাল্লাহু আলাইহে অসাল্লামের উপর ফেলিয়া দিয়া তাঁহাকে প্রাণে বধ করিয়া ফেলিবে। &hellip;.. সঙ্গে সঙ্গে আল্লাহ তাআলা ওহী মারফত রাসুলুল্লা (সঃ)-কে সমস্ত ষড়যন্ত্র জ্ঞাত করাইয়া দিলেন। তৎক্ষণাৎ তিনি তথা হইতে উঠিয়া আসিলেন, তাঁহার সঙ্গী ছাহাবীগণও চলিয়া আসিলেন&rsquo; (বোখারী শরীফ, তৃতীয় খণ্ড, অনু- মাওলানা শামছুল হক ও মাওলানা আজিজুল হক, হামিদিয়া লাইব্রেরী লি:, একাদশ সংস্করণ- জুন ২০০৭, পৃষ্ঠা- ১৪৮-১৪৯)। অর্থাৎ বোঝাই যায় যে, যে কোন কাজের অজুহাত দিবার জন্য সর্বদাই নবীর নিকট আল্লাহ্র নাম মজুত থাকত।<br />\r\nযুদ্ধজয়ী হতে হলে নিজের দুর্বলতা, শক্তি, অবস্থান ইত্যাদি শত্র&ldquo;র নিকট গোপন রাখতে হয়, শত্র&ldquo;কে ভালভাবে জানতে হয়, সুতরাং তার সম্পর্কে বিশদ সংবাদ সংগ্রহ করতে হয় এবং শত্র&ldquo;কে মিথ্যা দ্বারা প্রতারিত করে তার দুর্বল জায়গায় ও দুর্বল মুহূর্তে আক্রমণ করতে হয়। আধুনিক কালে যারা সমর বিজ্ঞান চর্চা করেন তারা জানেন যে, এগুলি সকল যুদ্ধের মূলনীতির মধ্যে পড়ে। তাহলে হয়ত প্রশ্ন করা হবে, তাহলে কি মিথ্যা ও প্রতারণার কারণে সকল যুদ্ধই অন্যায়? কিন্তু আমরা জানি যুদ্ধ মাত্রই অন্যায় নয়। তাহলে &rsquo;৭১-এ পাকিস্তানি হানাদার বাহিনীর বিরুদ্ধে বাঙালি জাতির যুদ্ধও অন্যায় যুদ্ধ হত। সাধারণ বিচারে আত্মরক্ষা, মানবিক অধিকার অর্জন ও নারীর মর্যাদা রক্ষার জন্য যে কোন শান্তিপূর্ণ মানুষ, জাতি বা জনগোষ্ঠীর যুদ্ধই ন্যায় যুদ্ধ। কিন্তু ইসলামের যুদ্ধকে কি সেই ভাবে বিচার করা যাবে? সুতরাং যুদ্ধের প্রয়োজনে তার মিথ্যা ও প্রতারণার পক্ষে কোন যুক্তিই টিকে না। সবচেয়ে বড় কথা ধর্ম প্রতিষ্ঠার জন্য কাউকে যুদ্ধ করতে হবে কেন? তাহলে কি এই দাঁড়ায় না যে আসল উদ্দেশ্য মোটেই পারলৌকিক বা অলৌকিক নয় বরং সম্পূর্ণরূপে ইহলৌকিক বা লৌকিক? ধর্মটা হচ্ছে &macr;্রফে ধাপ্পা। আত্মপ্রতিষ্ঠার হাতিয়ার। বস্তুত, কুরআন-হাদীসের মনোযোগী পাঠ আমাদেরকে এই সিদ্ধান্তে নিয়ে যায় যে, এই ধর্ম পুরাটাই দাঁড়িয়ে আছে মিথ্যা ও প্রতারণার উপর। যুদ্ধে তিনি যেমন মিথ্যাচার, প্রতারণা ও ভণ্ডামি করেছেন ধর্ম প্রতিষ্ঠা করতে গিয়েও তিনি তেমন সেই একই কাজ করেছেন।<br />\r\nজিহাদের মাধ্যমে প্রতিষ্ঠিত ধর্ম ইসলাম প্রতিষ্ঠায় কৌশল, গোপনতা এবং প্রতারণা তথা ছল-চাতুরী ও মিথ্যা কতখানি গুরুত্বপূর্ণ ভূমিকা পালন করেছে তা সহজেই অনুমেয়। ইতিহাস লেখে বিজয়ীরা। সুতরাং ইসলাম প্রসার ও প্রতিষ্ঠার অপ্রিয় সত্যকে প্রথম থেকেই ইসলামের ইতিহাস লেখকরা যে যতটা সম্ভব গোপন করেছে তাতে কোনও সন্দেহ নাই। তা সত্ত্বেও ইসলামের মূল ধর্মগ্রন্থ কুরআন এবং হাদীসসহ আদি ইসলামী পর্বের বিভিন্ন দলিল থেকে ইসলাম প্রতিষ্ঠার যেটুকু অন্ধকার দিক বেরিয়ে আসে সেটুকুই এই ধর্মের তাৎপর্য বুঝার জন্য যথেষ্ট হতে পারে। বিভিন্ন হাদীস থেকে আমরা জানতে পারি কোনও উপজাতি বা গোত্র কিংবা জনগোষ্ঠীকে আক্রমণের পূর্বে মুহাম্মদ কীভাবে গোপনতা অবলম্বন করতেন, কীভাবে তাদের সম্পর্কে গোপনে খোঁজ-খবর নিয়ে অসতর্ক অবস্থায় থাকা তাদের বসতিতে আক্রমণ করতেন। মদীনায় প্রথম দিকে নিজের দুর্বল অবস্থায় তিনি আশপাশের যেসব অমুসলিম উপজাতির সাথে শান্তি চুক্তি সম্পাদন করেছিলেন উপযুক্ত সময়ে তাদেরকে আক্রমণ করতেও তার দ্বিধা হত না। তার জন্য তার ছলের অভাব হত না। তার সমালোচনা বা নিন্দা করাও ছিল অত্যন্ত বিপজ্জনক। হাদীস থেকে জানা যায় যখন তিনি মক্কা জয় করেন নাই তখনও এ ধরনের ব্যক্তিদেরকে তিনি গুপ্তঘাতক পাঠিয়ে হত্যা করতেন। যখন তিনি জয়ী তখন আর কে তার বিরোধিতা করে বেঁচে থাকবে? মোট কথা আল্লাহ্র নবীর কোনও ধরনের বিরোধিতার জায়গাই তিনি রাখেন নাই। কিন্তু সকল কাজই মুহাম্মদ আল্লাহর নামে করতেন; সেটা আক্রমণ হোক, যুদ্ধ হোক, লুণ্ঠন হোক, হত্যা হোক, ধর্ষণ হোক, দাসকরণ হোক এবং গনীমতের মালের বণ্টন হোক।<br />\r\nএখানে দীর্ঘ আলোচনার সুযোগ নাই। তবু আশা করি এই সংক্ষিপ্ত আলোচনা মুহাম্মদ ও ইসলামের স্বরূপ বুঝতে অনেকখানি সাহায্য করবে। বস্তুত ধর্ম প্রবক্তা হিসাবে বুদ্ধ ও যিশুর সঙ্গে মুহাম্মদকে যেমন এক কাতারে ফেলাটা ভ্রান্ত তেমন ধর্ম হিসাবে বৌদ্ধ ও খ্রিস্ট ধর্মের সঙ্গে ইসলামকে গুলিয়ে ফেলাটাও ভ্রান্ত। একই যুক্তি অনুযায়ী ইসলামের ভূমিকার সঙ্গে বৌদ্ধ এবং খ্রিস্টান ধর্মের ভূমিকাকে গুলিয়ে ফেলাটাও ভ্রান্ত। যারা নিজেদেরকে বস্তুবাদী বা লোকবাদী কিংবা যুক্তিবাদী মনে করেন তাদের অনেকে সব আলোকবাদী ধর্মকে ভ্রান্ত বলে উড়িয়ে দিয়ে ধর্মগুলির ভূমিকার পার্থক্য এবং সমস্যার ভিন্নতা যেমন অস্বীকার করেন তেমন মনে করেন জ্ঞান-বিজ্ঞান এবং যুক্তি চর্চার একই প্রক্রিয়ায় অলোকবাদী সকল ধর্মের সমস্যার সমাধান হবে। তাদের মত মানলে আমাদের প্রাণী হিসাবে যেমন বাঘ, নেকড়ে, হায়েনা এবং গরু, ঘোড়া, ছাগলকে এক দৃষ্টিতে দেখতে এবং সেগুলির প্রতি একই রকমভাবে আচরণ করতে হবে তেমন মানুষের পৃথক পৃথক সমাজ ও সংস্কৃতি নির্মাণে ধর্মগুলির ভূমিকার ভিন্নতাকে অস্বীকার করে সেগুলিকেও একই দৃষ্টিতে দেখতে এবং সেগুলির প্রতিও একই রকমভাবে আচরণ করতে হবে।<br />\r\nঅথচ যে কোন অলোকবাদী ও অন্ধ বিশ্বাস নির্ভর ধর্মের বিরুদ্ধে সংগ্রাম না করে আধুনিক সভ্যতা এবং তার উপযোগী সমাজ নির্মাণ করা অসম্ভব। পশ্চিম ইউরোপ এবং পূর্ব ইউরোপ এই কাজ করেছে তাদের মত করে। আজ চীন ও ভিয়েৎনামের মত দেশগুলি যে অবিশ্বাস্য গতিতে এগিয়ে যাচ্ছে সেটাও সম্ভব হত না এই সংগ্রাম ছাড়া। এই সংগ্রাম হয়েছে যার যার ধর্মের ও সমাজের বাস্তবতা অনুযায়ী। যেহেতু খ্রীষ্ট বা বৌদ্ধ ধর্মের উদ্ভব যুদ্ধ, বলপ্রয়োগ, সহিংসতা ও নারী ধর্ষণের মধ্য দিয়ে হয় নাই সেহেতুু এইসব ধর্মের বিরুদ্ধে সংগ্রামের ধরনও তুলনায় অনেক শান্তিপূর্ণ হতে পারে। এর পরেও ইউরোপে খ্রিস্ট ধর্ম এবং তার প্রতিষ্ঠান হিসাবে চার্চের আধিপত্যের বিরুদ্ধে দীর্ঘ ও প্রবল সংগ্রাম করতে হয়েছে। রোমান ক্যাথলিক চার্চের বিরুদ্ধে ধর্ম সংস্কার আন্দোলনের প্রক্রিয়ায়ও বিপুল রক্তপাত হয়েছে। প্রথম পর্যায়ে প্রোটেস্ট্যান্টদের অনেক মূল্য দিতে হয়েছিল। ইংল্যান্ড, ফ্রান্স, জার্মানী ইত্যাদি দেশে প্রধানত ধর্মকে সামনে রেখে ১৫২৪ খ্রি: থেকে ১৬৪৮ খ্রি: সময় পর্যন্ত প্রোটেস্ট্যান্ট ও ক্যাথলিকদের মধ্যে যে সকল যুদ্ধ অনুষ্ঠিত হয় তার ফলে লক্ষ লক্ষ মানুষকে প্রাণ দিতে হয়।<br />\r\nখ্রিস্ট ধর্মের ভিতর অনেক মানবিকতা, সহিষ্ণুতা, প্রেম, সমতা ও ব্যক্তির মর্যাদা থাকার পরেও এই যেখানে অবস্থা সেখানে চরম সহিংসতা, সন্ত্রাস, মিথ্যাচার ও যুদ্ধের মাধ্যমে প্রতিষ্ঠিত ইসলামের অধীন সমাজে যুক্তি ও লোকবাদ, সহিষ্ণুতা, ব্যক্তির স্বাধীনতা ও গণতন্ত্র প্রতিষ্ঠা কতখানি কঠিন তা সহজেই অনুমেয়।<br />\r\nএ কথা আমাদেরকে বুঝতে হবে যে, মানুষের ভিতর বিবেক, বিবেচনা, দয়া এবং মানবিকতা যেমন থাকে তেমন তার বিপরীত গুণাবলিও থাকে। ফলে মানুষ সহিংস এবং নৃশংস বা নিষ্ঠুরও হতে পারে। মানুষ যেমন ত্যাগ করতে পারে তেমন স্বার্থান্ধ ভোগবাদীও হতে পারে। বরং মানুষের ভিতর পশু প্রবৃত্তির প্রভাবই বেশী থাকে। কিন্তু প্রকৃতির জগতে মানুষ একা বাঁচতে পারে না। ফলে সমাজবদ্ধ হয়েই তাকে বাঁচতে হয়। আর সমাজবদ্ধ হতে গিয়ে তাকে সমাজ গঠন করতে হয়। কিন্তু কিছু সামাজিক নিয়ম এবং নীতি-নৈতিকতার প্রতিষ্ঠা ছাড়া সমাজ গঠন করা যায় না। এগুলির চর্চা দ্বারা মানুষ এবং তার সমাজ মানুষের পশু প্রবৃত্তিকে যতটা সম্ভব সংযত করতে চেষ্টা করে। আদিম মানুষ যখন আত্মা ও অলৌকিক বা অতিপ্রাকৃতিক শক্তির কল্পনা করতে শিখেছে তখন তারা তাদের গোত্র এবং উপজাতি গঠন ও রক্ষার জন্য যে সকল নিয়ম ও নীতি-নৈতিকতা প্রবর্তন ও অনুশীলন করত সেগুলির সঙ্গে আত্মা ও অতিপ্রাকৃতিক শক্তির কল্পনা স্বাভাবিক নিয়মে জড়িয়ে যেত। এভাবে ধর্মের উদ্ভব ও বিকাশ ঘটতে থাকে। এক একটি উপজাতি নিজেদের উপজাতীয় সংহতি ও অন্যান্য উপজাতির সঙ্গে নিজেদের পার্থক্য রক্ষার প্রয়োজনে ধর্মকে ব্যবহার করতে শুরু করে। সভ্যতার দিকে মানুষ যত এগিয়ে যেতে থাকে তত ধর্ম বোধেরও বিকাশ হতে থাকে। মানুষ প্রকৃতির বিভিন্ন শক্তির অতিপ্রাকৃতিক রূপ কল্পনার পাশাপাশি তাদের উপজাতির নিজস্ব অতিপ্রাকৃতিক শক্তির রূপ তথা দেবতার কল্পনা করতেও শিখে। দেবতাদের তুষ্ট করার জন্য তাদের প্রতীক নির্মাণ ও পূজা দিতে শুরু করে। প্রাকৃতিক শক্তি এবং মূর্তি বা প্রতীক পূজামূলক এই ধর্মগুলিকে আমরা পেগান (চধমধহ) ধর্ম বলতে পারি। এই শব্দ দিয়ে এক সময় খ্রিস্টানরা খ্রিস্টান ধর্মের আবির্ভাবের পূর্ব কালীন সকল পৌত্তলিক ধর্মকে বুঝাত।<br />\r\nগ্রিক এবং রোমান সভ্যতা যারা নির্মাণ করেছিল তারা ছিল পেগান। পেগান ধর্মের একটা সমস্যা হল উপজাতির বাইরের মানুষদের প্রতি ভ্রাতৃত্ব বোধ বা সহমর্মিতার অভাব, যে কারণে বৃহত্তর সমাজ বা রাষ্ট্র গঠনে উদ্যোগী ভূমিকা গ্রহণকারী উপজাতি অন্যান্য উপজাতির প্রতি নিষ্ঠুর ও নির্বিবেক হয়ে উঠতে পারে। সিন্ধু সভ্যতা ছাড়া মিসর, মেসোপটেমিয়া ও চীনের প্রাচীন সভ্যতায় রাষ্ট্র ও সভ্যতা গঠনে পেগান মানুষদের সীমাহীন নিষ্ঠুরতার কথা জানা যায়। সমকালীন মিসর ও মেসোপটেমিয়ার তুলনায় অনেক বেশি এলাকায় বিস্তৃত সিন্ধু সভ্যতা (পাঁচ হাজার বৎসর পূর্বে প্রতিষ্ঠিত) প্রাচীন পৃথিবীর এক বিস্ময়, যা প্রতিষ্ঠিত হয়েছিল প্রধানত শান্তিপূর্ণ ও অহিংস উপায়ে। তবে সেখানে যে ধর্ম ছিল তাকে প্রত&oelig;তাত্ত্বিক সাক্ষ্য থেকে এবং হিন্দুদের আদি ধর্মগ্রন্থ ঋগে&brvbar;দের সূত্র ধরে মূলত শান্তিপূর্ণ, নিরাকারবাদী এবং একেশ্বরবাদী বলে অনুমান করা চলে। এটা উপজাতীয় চেতনার গণ্ডি ভেঙ্গে মানুষকে বিশ্বজনীনতার বোধ দিয়েছিল। আমার অনুমান এই ধর্ম সেখানে বিভিন্ন উপজাতি ও জনগোষ্ঠীর মধ্যে শান্তিপূর্ণভাবে বৃহত্তর সমাজ ও রাষ্ট্র গঠনে সহায়ক ভূমিকা পালন করেছিল। যাইহোক, এটা ভিন্ন ও বিস্তারিত আলোচনার বিষয় (সিন্ধু সভ্যতায় ধর্ম ও রাষ্ট্রের বৈশিষ্ট্য ও বিকাশ সম্পর্কে আলোচনার জন্য দেখুন ঃ শামসুজ্জোহা মানিক ও শামসুল আলম চঞ্চল, আর্যজন ও সিন্ধু সভ্যতা, ব-দ্বীপ প্রকাশন, ঢাকা, ২০০৩)। আমরা এখানে শুধু এইটুকু বলতে পারি যে, পেগান সমাজগুলি রাষ্ট্র ও সভ্যতা গঠনে যে সহিংসতা ও বর্বরতার আশ্রয় নিয়েছিল খ্রিস্ট এবং বৌদ্ধ ধর্ম ছিল তার বিরুদ্ধে একটা প্রতিক্রিয়া। এটা খুব লক্ষণীয় যে পেগান গ্রিক ও রোমান সভ্যতা প্রতিষ্ঠিত ছিল যুদ্ধ, জবরদস্তি এবং ব্যাপক দাস প্রথার উপর।<br />\r\nগ্রিক ও পরবর্তী কালে গড়ে উঠা রোমান সভ্যতা বিরাট উন্নতি সাধন করলেও দাস শ্রমের উপর নির্ভরতার কারণে একটা পর্যায়ে গিয়ে আটকে গিয়েছিল। অন্যান্য সমাজ থেকে মানুষ ধরে এনে দাস করে তাদের শ্রমের উপর গোটা সভ্যতার নির্ভরতা সভ্যতার প্রযুক্তিগত অগ্রগতির পথ রুদ্ধ করে দিল। একদিকে, যুদ্ধ ও নির্দয়তা, অপর দিকে, সহজলভ্য দাস শ্রম দিয়ে সব কাজ করাবার প্রবণতা সভ্যতার প্রাণশক্তি নিংড়ে নিচ্ছিল। ভূমধ্যসাগরের চারপাশ ঘিরে ইউরোপ, এশিয়া ও আফ্রিকার বিশাল অঞ্চল ব্যাপী বিস্তৃত রোমান সা&curren;্রাজ্যের মানুষকে এমন একটা সময়ে খ্রিস্টের প্রেম ও সৌভ্রাতৃত্বের বাণী নূতন আশায় উজ্জীবিত করল। সভ্যতার ক্ষয়ের সঙ্গে, হুন-গথ-ভ্যান্ডাল ইত্যাদি পেগান বর্বরদের ক্রমাগত আক্রমণের মুখে পেগান রোমান সভ্যতার বিপর্যয়ের সঙ্গে সা&curren;্রাজ্যের বিস্তীর্ণ অঞ্চলে বিস্তৃত হল খ্রিস্টের ধর্ম। ক্রমে সর্বজনীন প্রেমের ধর্মের শক্তির কাছে পরাভূত হল আক্রমণকারী ও আক্রান্ত সব পেগানরাই। এক সময় রোমের বিশাল বস্তুগত সা&curren;্রাজ্য থাকল না, কিন্তু খ্রিস্ট ধর্মকে অবলম্বন করে প্রতিষ্ঠিত হল ভাবগত সা&curren;্রাজ্য। উন্নত নগর সভ্যতা হারিয়ে গেল, সেই সঙ্গে হারিয়ে গেল দাস ব্যবস্থার উপর প্রতিষ্ঠিত নির্দয় সভ্যতা। রোমান সা&curren;্রাজ্যের তুলনায়ও বিশালায়তনে প্রতিষ্ঠিত হল ধর্ম সা&curren;্রাজ্য যে সা&curren;্রাজ্য প্রতিষ্ঠার জন্য যুদ্ধ এবং সন্ত্রাসের পরিবর্তে হাতিয়ার হল অহিংসা ও প্রেম বা ভালবাসার আবেদন।<br />\r\nধর্মগ্রন্থের চুলচেরা বিশ্লেষণ দিয়ে যিশুর শক্তির কিছুই বুঝা যাবে না যদি না মানুষের জন্য ব্যথিত ক্রুশবিদ্ধ যিশুর আত্মদানের মহিমাকে বোঝা না যায়। দয়াহীন পৃথিবীতে যীশু মানুষকে শুনালেন তার দয়াময় পরমপিতা ঈশ্বরের স্বর্গরাজ্যে যাবার বার্তা। এই ঈশ্বর সব মানুষের জন্য দয়াময়। সেখানে জাতি, উপজাতি, গোত্র, বর্ণ ও শ্রেণীর কোন ভেদ নাই। শুধু বাণী নয়, বরং নিজের জীবনাচরণ ও শেষাবধি ক্রুশে আত্মদান করে নিজের বিশ্বাসকে মহিমান্বিত করলেন। হয়ত ক্রুশে বিদ্ধ হয়ে মৃত্যু বরণ করলে সব হারিয়ে যেত। কিন্তু কবরস্থ করার পরেও তৃতীয় দিনে ফিরে এসে শিষ্যদের নিকট তার দর্শন দান ঈশ্বর-পুত্র হিসাবে তার প্রতি শিষ্যদের বিশ্বাসকে নূতন শক্তি দান করল। এরপর চল্লিশ দিন তিনি তার শিষ্যদের নিকট তার বাণী প্রচার করেন এবং অবশেষ অদৃশ্য হন। দয়াহীন পৃথিবীতে দয়াপূর্ণ স্বর্গরাজ্যের সন্ধান দিতে চেয়ে যিশুর তিরোধানের পর তার শিষ্যরা যিশুর প্রেম ও ক্ষমার বাণী প্রচারে নেমে পড়ল। নির্দয় সভ্যতার পেষণে পিষ্ট মানুষের কাছে তাদের প্রতি দয়ামায়াহীন যুক্তি ও বিচার-বুদ্ধির কী মূল্য?<br />\r\nরোমান সভ্যতার পতনের পর উন্নত নগর সভ্যতা থেকে মানুষ পিছিয়ে গেল। অন্ধ বিশ্বাস নির্ভর ধর্মে মানুষ আশ্রয় নিল। কিন্তু, এও ঠিক যে ধর্মের আবরণে ইউরোপে গ্রিক ও রোমান সভ্যতার এমন কিছু উপাদান রক্ষা পেল যা ভবিষ্যতে ইউরোপের পুনর্জাগরণে সহায়ক হবে। দাস ব্যবস্থা নির্ভর সভ্যতার সঙ্গে একটা ছেদ দরকার ছিল। প্রেম ও ভ্রাতৃত্বের ধারণার উপর প্রতিষ্ঠিত খ্রিম্ট ধর্ম এই ছেদ ঘটাবার ক্ষেত্রে ভাবাদর্শিক প্রয়োজন পূরণ করেছিল। এটা স্পষ্ট যে খ্রীষ্ট ধর্মের কারণে ইউরোপে আর কখনই দাস ব্যবস্থা ফিরে আসে নাই।<br />\r\nএর মধ্যে অর্থনীতি খুঁজে লাভ নাই। অর্থাৎ অর্থনীতির পশ্চাৎপদতার কারণে ইউরোপে দাস ব্যবস্থা আর প্রবর্তিত হয় নাই বা টিকে নাই এটা খুব দুর্বল একটা যুক্তি যা অনেকে দিয়ে থাকেন। তা হলে প্রশ্ন আসবে অনেক পশ্চাৎপদ অর্থনীতি নিয়ে ইসলাম যেখানে গেছে সেখানেই দাসত্বের ভয়াবহ বিস্তার কীভাবে ঘটেছে। ইসলাম উন্নত সভ্যতা দিতে না পারলেও দাস ব্যবস্থার বিস্তার দিতে পেরেছে। ইসলামের প্রভাবে সর্বত্র উৎপাদন ব্যবস্থার যে অধ:পতন ঘটে তার ফলে গ্রীস বা রোমের মত দাস ব্যবস্থা ভিত্তিক উন্নত সভ্যতাও আর নির্মাণ করা সম্ভব হয় নাই। যে কারণে ইসলামে দাসত্বের ভয়াবহ প্রসার ঘটলেও উৎপাদন তথা অর্থনীতির মূল ভিত্তি হিসাবে সেটা আর দাঁড়াতে পারে নাই। কিন্তু এ থেকে এ কথা মনে করা ভুল হবে যে ইসলাম চেতনাগত বা আদর্শিকভাবে দাসত্ব বিরোধী ছিল। বরং মানুষের বান্দাত্ব বা দাসত্বের ধারণার উপর প্রতিষ্ঠিত ইসলাম ধর্মীয় বিধান অনুযায়ীই দাসত্ব চর্চা করেছে, যার সূত্রপাত করেন মুহাম্মদ নিজেই। ইসলামের এই বাস্তবতার সঙ্গে খ্রিস্টীয় মূল্যবোধ ও আদর্শ সাংঘর্ষিক। প্রকৃতপক্ষে খ্রিস্টীয় চেতনা ও মূল্যবোধ দাস ব্যবস্থার বিরোধী ছিল।<br />\r\nএটা স্পষ্ট যে বৌদ্ধ ও খ্রিস্ট ধর্ম বৃহত্তর ও শান্তিপূর্ণ সমাজ গঠন করতে চেয়ে মানুষের মানবীয় গুণাবলিকে ধারণ ও রক্ষা করতে এবং সহিংসতা, লোভ, নির্দয়তা ইত্যাদি প্রবৃত্তিকে সংযত বা দমন করতে চেয়েছে। তা সত্ত্বেও বৌদ্ধ ও খ্রিস্ট ধর্মাবলম্বীদের সহিংসতা ও নিষ্ঠুরতার ইতিহাস কম দীর্ঘ নয়। এই যেখানে অবস্থা সেখানে যদি কোন ধর্ম মানুষের ভিতরকার সহিংসতা ও পশু প্রবৃত্তিগুলিকে অর্গল মুক্ত এবং লালন করে তবে কী ভয়ানক অবস্থা হতে পারে তা সহজেই অনুমান করা যায়।<br />\r\nমিথ্যাচার, প্রতারণা, ভণ্ডামি, সহিংসতা ও হিং&macr;্র আক্রমণাত্মকতা, অসহিষ্ণুতা, লোভ, হত্যা, নারী ধর্ষণ এবং চক্রান্ত জন্মলগ্ন থেকে ইসলামকে যেভাবে অধিকার করে আছে তার ফল ইসলামের জন্যও মুহাম্মদের মৃত্যুর সাথে সাথে ফলতে শুরু করে। মুহাম্মদের মৃত্যুর পর ক্ষমতার উত্তরাধিকার নিয়ে মদীনায় তাঁর অনুসারী বা সাহাবাদের মধ্যে দ্বন্দ্ব এবং চক্রান্ত শুরু হয়। দুইদিনের উপর তাঁর লাশ দাফনের অভাবে বিছানায় পড়ে থাকে। অবশেষে ষড়যন্ত্রের প্রক্রিয়ায় ওমরের উদ্যোগে আবু বকরকে খলিফা মনোনীত করা হয়। উদ্দেশ্য মুহাম্মদের চাচাতো ভাই এবং জামাতা আলীকে বঞ্চিত করা। মিথ্যা, চক্রান্ত, রক্তপাত, হত্যা এবং যুদ্ধ দ্বারা ইসলামের যে যাত্রা শুরু হয়েছিল তার শিকার হয়েছিলেন প্রথম চার খলিফার মধ্যে প্রথম খলিফা আবু বকর বাদে বাকি তিন খলিফাই। আবু বকর মাত্র ২৭ মাস খলিফা হিসাবে বেঁচে ছিলেন। ৬৩২ খ্রি:-এ খলিফা পদ লাভ ক&rsquo;রে ৬৩৪ খ্রি:-এ মারা যান। বাঁচলে হয়ত তাকেও মরতে হত বাকি তিন খলিফা যথাক্রমে ওমর, ওসমান এবং আলীর মত শত্র&ldquo;র ছুরিকাঘাতে।<br />\r\nইসলামে যুদ্ধ লাগবেই, আর যুদ্ধ করার জন্য স্বাভাবিক নিয়মে শত্র&ldquo;ও লাগবে। অসুমলমান বা কাফের পাওয়া না গেলে মুসলমানকেই কাফের বা শত্র&ldquo; ঘোষণা করে তার বিরুদ্ধে যুদ্ধ করতে হবে। আলীর বিরুদ্ধে গৃহযুদ্ধ শুরু হল ব্যাপকায়তনে। এমন কি আলীর বিরুদ্ধে যুদ্ধ ঘোষণা করলেন মুহাম্মদের বিধবা স্ত্রী আয়েশা, যিনি কিনা আবার আলীর সৎ শাশুড়িও বটে। এই যুদ্ধ উষ্ট্রের যুদ্ধ হিসাবে পরিচিত। যুদ্ধে আয়েশা পরাজিত ও বন্দি হন। তবে আলী তাঁকে মুক্তি দেন।<br />\r\nআসলে শান্তির মত গণতন্ত্রের একটা পদ্ধতি নির্বাচনের সঙ্গেও ইসলামের কোন সম্পর্ক নাই। প্রাক-ইসলামী যুগের আরবদের মধ্যে উপজাতীয় গণতন্ত্রের যে প্রভাব ছিল তার ধারাবাহিকতায় প্রথমেই রাজতন্ত্র প্রতিষ্ঠিত হতে পারে নাই। ফলে আমরা প্রথম চার খলিফার ক্ষমতা গ্রহণে চক্রান্ত, সংঘাত ও রক্তপাত সত্ত্বেও পারিবারিক রাজতন্ত্রের বদলে এক ধরনের নির্বাচন দেখতে পাই। তবে এই প্রক্রিয়ায় যেটা উল্লেখ করার মত তা হল একদিকে আলীকে বাদ দেওয়া, অপরদিকে মদীনাবাসী এবং অকুরাশইদেরকে ক্ষমতার কেন্দ্র থেকে দূরে সরিয়ে রাখা। অর্থাৎ মুহাম্মদের ধর্ম প্রকৃতপক্ষে কুরাইশদের বংশগত একনায়কতন্ত্র প্রতিষ্ঠা করে। একনায়ক-শাসক নির্বাচনে অনিশ্চয়তা, রক্তপাত ও বিশৃঙ্খলা দূর করার জন্য আলীর মৃত্যুর পর খলিফা মুয়াবিয়ার নেতৃত্বে রাজবংশ প্রতিষ্ঠা করা হয়।<br />\r\nইসলামের সহিংসতার থাবা থেকে মুহাম্মদের বংশধররাও রক্ষা পেলেন না। তাঁর কোন পুত্র ছিল না। তাঁর কন্যা ফাতেমার গর্ভে হাসান ও হুসেন নামে তাঁর দুই দৌহিত্র ছিলেন। খলিফা মুয়াবিয়ার শাসনকালে হাসানকে বিষ প্রয়োগ ক&rsquo;রে এবং মুয়াবিয়ার পুত্র খলিফা এজিদের শাসনকালে তার নির্দেশে হাসানের ছোট ভাই হুসেনকে কারবালার মরু প্রান্তরে আরও অনেকের সঙ্গে হত্যা করা হয়। হুসেনকে হত্যা ক&rsquo;রে তার মাথা কেটে নিয়ে খলিফা এজিদের কাছে উপস্থিত করা হয়। এই হচ্ছে ইসলামের শান্তির ঐতিহ্য প্রতিষ্ঠার নমুনা।<br />\r\nবস্তুত ইসলামের মূল সামাজিক ভিত্তির দিকে দৃষ্টি দিলে যুদ্ধ ও সহিংসতার ধর্ম হিসাবে তার বিকাশকে যৌক্তিক মনে হবে। মুহাম্মদের যোদ্ধা বাহিনীর প্রধান অংশ এসেছিল অপেক্ষাকৃত দরিদ্র, অর্ধযাযাবর এবং বিশেষত যাযাবর বেদুইন উপজাতিগুলি থেকে। বেদুইনরা কৃষক বা উৎপাদক জনগোষ্ঠী নয়। তারা ছিল যুদ্ধ প্রবণ, লুণ্ঠনপরায়ণ ও বিচরণশীল। ফলে তারা সভ্যতা নির্মাণের নয়, বরং লুণ্ঠন ও ধ্বংসের শক্তি।<br />\r\nএই জায়গায় পৃথিবীর বর্তমান আর সকল বৃহৎ ধর্মের সঙ্গে ইসলামের পার্থক্য। যেমন, মোশি বা মুসা মিসরে দাসে পরিণত ইসরাইলীয় কৃষক ও শ্রমজীবী মানুষদেরকে মুক্ত করে প্যালেস্টাইনে আনেন এবং তাদেরকে নিজ ধর্মমতে দীক্ষিত করেন, যা আমাদের নিকট ইহুদী ধর্ম হিসাবে পরিচিত। অর্থাৎ মোশির ধর্মের মূল ভিত্তি উৎপাদক ও শ্রমজীবী জনগোষ্ঠী। রাষ্ট্র প্রতিষ্ঠাতা মোশির ধর্মে সামরিকতা, উগ্র রক্ষণশীলতা ও অসহিষ্ণুতা প্রকট হলেও তা অন্য জাতি বা সমাজের প্রতি সেভাবে আগ্রাসী নয়। কারণ মোশির ধর্মের মূল লক্ষ্য ছিল দাসত্বে আবদ্ধ ইসরাইলীয়দেরকে ঈশ্বরের মনোনীত জাতি হিসাবে মুক্ত করা এবং তাদেরকে ঈশ্বরের প্রতিশ্র&ldquo;ত ভূমি হিসাবে প্যালেস্টাইনে নেওয়া। এভাবে মোশির ধর্মমতের মাধ্যমে একটি মুক্ত জাতিই শুধু সৃষ্টি হল না, অধিকন্তু একটি রাষ্ট্রও প্রতিষ্ঠিত হল। এই রাষ্ট্রটিও হল মূলত একটি নির্দিষ্ট ভূখণ্ডে সীমাবদ্ধ।<br />\r\nইহুদী বংশোদ্ভূত যিশু কঠোর ও অসহিষ্ণু ইহুদী ধর্মকে নমনীয় রূপ দিতে গিয়ে এমন একটি সংস্কার ঘটালেন যা একটি নূতন ধর্ম জন্ম দিল। তার ধর্মে যোগ দিল সমাজের দরিদ্র শ্রমজীবী, কৃষক, কারিগর, ক্ষুদে ব্যবসায়ী, দাস ইত্যাদি নি&curren;&oelig;বর্গের শ্রমজীবী ও উৎপাদনশীল মানুষ। পরবর্তী সময়ে বিভিন্ন জাতি ও শ্রেণীর মানুষ এতে যোগ দিলেও ন&curren;্রধারায় উদ্ভূত এই ধর্মের মূল ভিত্তি ছিল শাসিত, শোষিত, শ্রমজীবী ও উৎপাদনশীল জনগোষ্ঠী।<br />\r\nঅহিংস ও শান্তিবাদী বৌদ্ধ ধর্মও উদ্ভূত ও প্রসার লাভ করে সমাজের কারিগর, বণিক, কৃষক এবং বিভিন্ন বৃত্তিজীবীর সমর্থন নিয়ে। পরবর্তী সময়ে সমাজের উপর তলার সমর্থন লাভ করলেও নি&curren;&oelig;বর্গ এবং বর্ণজাতিভেদ বিরোধী জনগোষ্ঠীর আন্দোলন রূপে তা বিকাশ লাভ করে। এভাবে বৌদ্ধধর্মের ভিত্তিমূলে আমরা পাই উৎপাদনশীল জনগোষ্ঠীকে।<br />\r\nবর্ণজাতিভেদ ভিত্তিক হিন্দু ধর্ম কোন একজন ধর্মনেতার দ্বারা প্রতিষ্ঠিত হয় নাই। এক কথায় হিন্দু ধর্মকে ব্যাখ্যা করাও সম্ভব নয়। আত্মা, জন্মান্তরবাদ, দেবতা ইত্যাদি কতকগুলি বিশ্বাসকে ভিত্তি করে এমন একটি ধর্ম গড়ে উঠেছে যেটাকে প্রচলিত অন্যান্য ধর্মের মত ধর্ম না বলে সামাজিক প্রথা ও বিশ্বাসের একটি সমষ্টি বলাই ভাল যেখানে সঙ্গতি বা ঐক্যের পাশে প্রভূত পরিমাণে অসঙ্গতি, অনৈক্য এমনকি পরস্পর বিরুদ্ধতাও রয়েছে। এটি অবিশ্বাস্য রকম জটিলতায় আবদ্ধ একটি সমাজ। তত্ত্বগতভাবে হিন্দু সমাজ চার বর্ণে বিভক্ত, যথা ব্রাহ্মণ, ক্ষত্রিয়, বৈশ্য এবং শূদ্র। কিন্তু এটা তত্ত্বগত ভাগ মাত্র। বাস্তবে সমাজ অজ&macr;্র ভাগে বিভক্ত, অর্থাৎ অসংখ্য বর্ণজাতিতে বিভক্ত। এই বর্ণজাতিগুলি শ্রমকর্ম বিভাজন এবং পবিত্রতা-অপবিত্রতার নির্দিষ্ট স্তরবিন্যাস দ্বারা আবদ্ধ হয়ে থাকে। সবার উপরে সবচেয়ে পবিত্র বর্ণজাতি হিসাবে ব্রাহ্মণ এবং সবার নীচে শূদ্র ও অস্পৃশ্যদের স্থান।<br />\r\nবর্ণজাতিভেদ মূলক ব্যবস্থায় আবদ্ধ হবার ফলে হিন্দু সমাজ যেমন গ্রিস, রোম বা সমকালীন চীনের সমপর্যায়ের উন্নত নগর সভ্যতা গড়তে অক্ষম হয় তেমন অসংখ্য বিভাজনের কারণে দীর্ঘ সময়ের জন্য কোন বৃহৎ রাষ্ট্র গড়তে ও কোন প্রবল বহিরাক্রমণকে মোকাবিলা করতে পারে নাই। একটা দীর্ঘ সময় ভারতীয় উপমহাদেশে বৌদ্ধ ধর্ম এবং হিন্দু ধর্মের মধ্যে প্রতিযোগিতা চলেছিল। এই প্রতিযোগিতায় শেষাবধি হিন্দু সমাজ ব্যবস্থার বিকাশ ও প্রসারের সঙ্গে হিন্দু ধর্ম জিতে যায়। কিন্তু সেই সঙ্গে বৈদেশিক আক্রমণকারীদের মোকাবিলায় দাঁড়াতেও ভারতীয় রাষ্ট্রগুলির অক্ষমতা লক্ষণীয়। শক ও হুন আক্রমণকারীরা উপমহাদেশের উত্তর-পশ্চিম অংশ দখল করে। অষ্টম শতাব্দীতে (৭১২ খ্রিস্টাব্দ) আরব আক্রমণকারীরা সিন্ধুর রাজা দাহিরকে আক্রমণ ও পরাজিত ক&rsquo;রে সিন্ধু দখল করে। একাদশ শতাব্দীতে সুলতান মাহমুদ সতেরো বার (১০০০-১০২৭ খ্রিস্টাব্দ) উত্তর ও পশ্চিম ভারতে লুণ্ঠন অভিযান পরিচালনা করেন। আর ত্রয়োদশ শতাব্দী থেকে অষ্টাদশ শতাব্দী পর্যন্ত চলে বহিরাক্রমণকারী তথা বহিরাগত মুসলিমদের লাগাতার শাসন। এর অবসান হয় আর একদল বহিরাগত আক্রমণকারী ব্রিটিশদের হাতে। ব্রিটিশ শাসন চলে প্রায় দুইশত বৎসর।<br />\r\nযাইহোক, হিন্দু ধর্ম সমাজ ও সভ্যতার বিকাশে বাধাদানকারী হলেও এটা ভারতবর্ষে নি&curren;&oelig; মাত্রায় কৃষি ভিত্তিক সভ্যতাকে ধরে রাখায় সহায়ক হয়েছিল। অনুমান করি সিন্ধু সভ্যতার পতনের পর সভ্যতার পশ্চাৎগতির অবস্থায় গাঙ্গেয় উপত্যকায় সিন্ধু সভ্যতার ধর্ম ও সংস্কৃতির ঐতিহ্যের সঙ্গে অসংখ্য পেগান উপজাতির ধর্ম ও সংস্কৃতির সম্মিলনে বিভিন্ন রূপান্তরের মধ্য দিয়ে যে নূতন ধর্ম ও সংষ্কৃতি গড়ে ওঠে তা-ই হিন্দু ধর্ম হিসাবে বিকশিত হয়। সভ্যতার বিকাশের ধর্ম এটি নয়। বরং সভ্যতার সংকট কালে সভ্যতাকে নি&curren;&oelig; মাত্রায় ধরে রাখার ধর্ম, যা ভারতবর্ষের বিশেষ ভূ-প্রাকৃতিক ও জনগোষ্ঠীগত বাস্তবতায় উদ্ভূত ও বিকশিত হয়েছিল। (হিন্দু ধর্ম এবং বর্ণজাতি প্রথার উদ্ভব ও বিকাশ সংক্রান্ত আলোচনার জন্য দেখুন ঃ শামসুজ্জোহা মানিক ও শামসুল আলম চঞ্চল, আর্যজন ও সিন্ধু সভ্যতা, ব-দ্বীপ প্রকাশন, ঢাকা। এছাড়া সংক্ষিপ্ত আলোচনার জন্য দেখুন ঃ শামসুজ্জোহা মানিক, বাঙ্গালী জাতির সঙ্কটের উৎস ও তার প্রতিকার সন্ধান, ব-দ্বীপ প্রকাশন, ঢাকা, ২০১০, পৃষ্ঠা-৬৭-৬৮)।<br />\r\nসিন্ধুর উন্নত নগর সভ্যতার পতন কালে এর উদ্ভবের বীজ রচিত হয়েছিল বলে সভ্যতার বিরুদ্ধে হিন্দু ধর্মে প্রতিক্রিয়ার প্রভাব এত গভীর বলে অনুমান করি। অবিশ্বাস্য রকম পশ্চাৎপদতা, ধর্মীয় গোঁড়ামি এবং কুসংস্কার এই ধর্মে রয়েছে যেগুলির বিরুদ্ধে লড়াই করা ছাড়া হিন্দু সমাজকেও আধুনিক সভ্যতার উপযোগী করে গড়ে তোলা সম্ভব নয়। তবে হিন্দু ধর্ম বা সমাজ আক্রমণাত্মক এবং হিং&macr;্র না হওয়ায় কাজটা প্রধানত শান্তিপূর্ণভাবে করা সম্ভব। অনেক কাল ধরে বহুমুখী আন্দোলনের মধ্য দিয়ে ভারতে সেই চেষ্টা চলছেও।<br />\r\nযাইহোক, সবচেয়ে বড় কথা ইসলাম বাদে আর সকল প্রধান ধর্ম এক অর্থে কৃষক সমাজের ধর্ম তথা কৃষি নির্ভর সভ্যতার ধর্ম। প্রতীকীভাবে এগুলিকে কৃষকের ধর্মও বলা যায়। ফলে এইসব ধর্মে কমবেশি উৎপাদনশীলতা আছে। কিন্তু ইসলাম হচ্ছে এমন যাযাবর সমাজের ধর্ম যা আক্রমণ, যুদ্ধ, লুণ্ঠন ও ধর্ষণকে বেছে নিয়েছে তার জীবন-জীবিকার ভিত্তি এবং প্রেরণার উৎস হিসাবে। অন্যান্য কৃষি নির্ভর সমাজের উৎপাদনশীল জনগণের উপর ধর্মের অজুহাতে আক্রমণ ক&rsquo;রে তাদেরকে পদানত এবং তাদের সম্পদ লুণ্ঠন করা হবে, তাদের কাছ থেকে নিয়মিত কর আদায় ক&rsquo;রে এবং তাদেরকে নানানভাবে শ্রমদানে বাধ্য ক&rsquo;রে আরাম-আয়েশ করা হবে এবং তাদের নারীদের বন্দি ও দাসী করে ধর্ষণ ও সম্ভোগ করা হবে।<br />\r\nসুতরাং ভূমিকার বিচারে পরবর্তী কালের সভ্যতা ধ্বংসী ও গণহত্যাকারী বিশ্বত্রাস মোঙ্গল নেতা চেঙ্গিস খানের (জন্ম আনুমানিক ১১৬২ খ্রি:-মৃত্যু ১২২৭ খ্রি:) সঙ্গে মুহাম্মদকে মিলানো যায়। মোঙ্গলরা সভ্যতাগুলিকে আক্রমণ ক&rsquo;রে কোটি কোটি মানুষকে হত্যা করে (এক হিসাবে চেঙ্গিস খান তৎকালীন চীনের আনুমানিক দশ কোটি মানুষের মধ্যে দুই কোটি মানুষকে হত্যা করেছিলেন), তাদের সম্পদ লুণ্ঠন করে, তাদের নারীদেরকে ধর্ষণ করে এবং বিশাল অঞ্চল ব্যাপী সা&curren;্রাজ্য প্রতিষ্ঠা করে। তবে চেঙ্গিস নূতন ধর্ম প্রবর্তন করেন নাই। শুধুমাত্র সামরিক শক্তি এবং যুদ্ধকৌশলের সাহায্যে চেঙ্গিসের নেতৃত্বে মোঙ্গলরা বিজয় অভিযান পরিচালনা করে। মুহাম্মদ তার জিহাদী ধর্মের মাধ্যমে আরবদেরকে একটি ধর্মীয় সামরিক রাষ্ট্র দান করেন যার বলে বলীয়ান হয়ে মূলত মরুচারী ও যাযাবর আরবরা তৎকালীন সভ্য পৃথিবী লুণ্ঠন ও ধ্বংসের অভিযানে বের হয়ে পড়ে।<br />\r\nহাদীসসমূহ মনোযোগ দিয়ে পড়লে বুঝা যায় যে, বিশ্বজয়ের একটি পরিকল্পনা বহুকাল ধরেই মুহাম্মদ তার অন্তরে লালন করতেন। যার জন্য তার প্রয়োজন হয়েছিল নিজ একনায়কী নেতৃত্বে আরবদের সংগঠিত করা। এ কাজে ধর্ম হয়েছিল তার হাতিয়ার। এ প্রসঙ্গে একটি হাদীস উল্লেখ করা যায় যেখানে মুহাম্মদ বলছেন, &lsquo;(পারস্য স&curren;্রাট) কিস্রা ধ্বংস হবে, তারপর আর কিস্রা হবে না। আর (রোমক স&curren;্রাট) কায়সার অবশ্যই ধ্বংস হবে, তারপর আর কায়সার হবে না। এবং এটা নিশ্চিত যে, তাদের ধনভাণ্ডার আল্লাহ্র রাহে ব্যয় হবে&rsquo; (বুখারী শরীফ [পঞ্চম খণ্ড], ইসলামিক ফাউন্ডেশন বাংলাদেশ, জুন ১৯৯৯, পৃষ্ঠা-২৪৩)। আসলে সমস্ত পৃথিবীর মালিকানা তিনি নিজের মনে করতেন, যে কারণে একদিন তিনি ইহুদীদের নিকট গিয়ে তাদেরকে বললেন, &lsquo;হে ইহুদী সম্প্রদায়! তোমরা মুসলমান হয়ে যাও, নিরাপদ থাকবে। &hellip;.. তোমরা জেনে রেখো যে, যমীন কেবল আল্লাহ্ ও তাঁর রাসূলের।&rsquo; একই কথার পুনরুক্তি করে তিনি বললেন, &lsquo;তোমরা জেনে রেখো যে, যমীন কেবল আল্লাহ্ ও তাঁর রাসূলের। আমি তোমাদেরকে দেশান্তর করতে মনস্থ করেছি। তাই তোমাদের যার অস্থাবর সম্পত্তি রয়েছে, তা যেন সে বিক্রি করে দেয়। অন্যথায় জেনে রেখো, যমীন কেবল আল্লাহ্ ও তাঁর রাসূলের।&rsquo; &Ntilde; (বুখারী শরীফ, দশম খণ্ড, ইসলামিক ফাউন্ডেশন বাংলাদেশ, মে ১৯৯৪, পৃষ্ঠা- ৩০৪)। মুসলিম শরীফে এই কথাটি আর একভাবে বলা হচ্ছে, &lsquo;নতুবা জেনে রেখো যে, সমগ্র ভূ-মণ্ডল আল্লাহ্ ও তাঁর রাসূলের।&rsquo; (মুসলিম শরীফ, হাদীস নং ৪৪৩৯, পূর্বোক্ত, পৃষ্ঠা-২৯৪)<br />\r\nঅপর একটি হাদীসে মুহাম্মদকে বলতে দেখা যাচ্ছে, &lsquo;একদা আমি নিদ্রিত ছিলাম, স্বপ্নে বিশ্বের ধনভাণ্ডারের চাবিগুচ্ছ আমার হস্তে দেওয়া হইল&rsquo; (বুখারী শরীফ [বাংলা তরজমা ও বিস্তারিত ব্যাখ্যা], তৃতীয় খণ্ড, অনুবাদক : মাওলানা শামছুল হক ফরিদপুরী [র:] ও মাওলানা আজিজুল হক সাহেব, প্রকাশক- হামিদিয়া লাইব্রেরী লি:, জুন ২০০৭, পৃষ্ঠা- ৮৫)। মুহাম্মদ আরবের বাইরে তার বিজয় অভিযান দেখে যেতে পারেন নাই। কিন্তু তাঁর আরব্ধ কাজ সমাপ্ত করার জন্য ধর্মের মাধ্যমে একদল অনুসারী এবং একটি দুর্ধর্ষ সেনাবাহিনী ও আগ্রাসী রাষ্ট্র রেখে গেলেন।<br />\r\nপেগান মোঙ্গল ও ইসলামী আরব উভয় অভিযানই ছিল কৃষি নির্ভর সভ্যতাগুলির জন্য ধ্বংসাত্মক। তবে মোঙ্গল দখলিকৃত অধিকাংশ অঞ্চলে পরবর্তীকালে নূতন করে সভ্যতা প্রতিষ্ঠিত হয়। সভ্যতার শক্তির কাছে বর্বরতা শেষ পর্যন্ত হার মানে। মোঙ্গলরা মরুময় মোঙ্গলিয়াতেও নগর-শহর গড়ে তুলে। এ ক্ষেত্রে সারা পৃথিবীর লুণ্ঠিত সম্পদ, বন্দি শ্রমশক্তি ও মেধা তাদের কাজে লাগে। কিন্তু শুধু বর্বরতা ও হিং&macr;্রতা নির্ভর মোঙ্গল সভ্যতা ও রাষ্ট্র বেশি দীর্ঘস্থায়ী হয় নাই। চীনে একশত বৎসরও তাদের শাসন স্থায়ী হয় নাই। মধ্য এশিয়া, রাশিয়া ইত্যাদি অঞ্চলে মোঙ্গল শাসন আরও বেশি কাল স্থায়ী হলেও সুদীর্ঘ কাল ধরেই মোঙ্গল অভিযান, বিজয়, হত্যা ও ধ্বংস স্মৃতি মাত্র।<br />\r\nকিন্তু একটি ধর্ম বা মতাদর্শের কারণে আরবের ইসলাম পৃথিবীর বিশাল অঞ্চলব্যাপী চৌদ্দশত বৎসর ধরে জীবন্ত হয়ে আছে। এটা ঠিক যে আরবের মরুময় বুকে আরবরা উন্নত সভ্যতা গড়তে না পারলেও সিরিয়া, ইরাক, ইরান ইত্যাদি সভ্য দেশগুলি দখলের পর সেখানে পরবর্তী কালে আরবরাও একটি উন্নত সভ্যতা গড়ে তোলে। বিশেষত আব্বাসীয় যুগে জ্ঞান-বিজ্ঞানও যথেষ্ট উন্নতি সাধন করে। কিন্তু এটাও বিজয় অভিযানের ফলে উন্নত সভ্যতাগুলির কেন্দ্রীভবনের ফল। চারপাশের সম্পদ, জ্ঞান, মেধা ও শ্রমশক্তি কেন্দ্রীভূত হওয়ায় ইসলামের অনেক রীতিনীতি ও বিশ্বাস লঙ্ঘন করে জ্ঞান-বিজ্ঞানের এই বিকাশ ঘটে। কিন্তু এ সবই ছিল সাময়িক এবং অতিকেন্দ্রীভূত স্ফূরণ মাত্র। ইসলাম নিজে জ্ঞান-বিজ্ঞান বিমুখ এবং সভ্যতা বিরোধী। ফলে ইসলামীকরণ সম্পূর্ণ হলে জ্ঞান-বিজ্ঞানের সাময়িক বিকাশ রুদ্ধ হয়ে গেল। সমাজতলের ইসলামী চেতনা জ্ঞানের সীমিত কেন্দ্রকে আক্রমণ ও ধ্বংস করল। সুতরাং নবম শতাব্দীতেই শুরু হল ইসলামের উদারপন্থী মুতাজিলা এবং অন্যান্য যুক্তিবাদীদের বিরুদ্ধে হত্যা অভিযান। হাজার হাজার পণ্ডিত ও জ্ঞানীকে তাদের উন্নত অথবা উদার এবং যুক্তিবাদী চিন্তার জন্য হত্যা করা হল। এভাবে ইসলামের প্রাথমিক বিজয় অভিযানের পর ইসলামপূর্ব জ্ঞান-বিজ্ঞানের কেন্দ্রীভবনের ফলে জ্ঞান-বিজ্ঞানের যেটুকু বিকাশ ঘটেছিল তাকে শেষ করা হল। যুদ্ধ, ধ্বংস, হিং&macr;্রতা ও অন্ধত্ব সমাজকে গ্রাস করল। এটাই স্বাভাবিক ছিল। কিন্তু সমাজ ও সভ্যতা পিছিয়ে গেলেও খুবই মুষ্টিমেয় শাসকের নির্বিবেক ভোগের আয়োজনের জন্য অধীনস্থ সমাজ ও অবশিষ্ট সভ্যতার সকল শক্তি নিয়োজিত হল। ইসলাম যেখানে পেল সেখানেই স্থানীয় সকল সভ্যতা, সংস্কৃতি ও ঐতিহ্যকে ধ্বংস ক&rsquo;রে তার মরু প্রকৃতির স্থ&rsquo;ূল ভোগবাদ ও নৃশংস সংস্কৃতি চাপিয়ে দিল, যার সবচেয়ে বড় শিকার হল নারী।<br />\r\nইসলামী আগ্রাসন ও যুদ্ধের সবচেয়ে বড় লক্ষ্যবস্তু প্রতিপক্ষের নারী। শুধু লুণ্ঠনে তার তৃপ্তি নাই। পরাজিত ও দাস করে সবাইকে হত্যাও তারা করতে চায় না। কারণ তা করলে অন্যদের শ্রমের বিনিময়ে ভোগ করবে কী করে? সুতরাং অপরের শ্রমোৎ&uacute;ন্ন ভোগের প্রয়োজনে সব সময় যে বাধ্যতামূলকভাবে ইসলামে দীক্ষিত করেছে তাও নয়। আরব ভূখণ্ডে ইসলাম গ্রহণ সবার জন্য বাধ্যতামূলক করলেও বাহিরে অনেক সময় অত্যন্ত অসম্মানজনক জিজিয়া করের দুর্বিষহ বোঝার বিনিময়ে অমুসলমানদেরকে নিজেদের ধর্ম পালন করতে দিত। তবে ইসলামের অধীনে অমুসলমানদের জীবন থাকত অত্যন্ত অনিশ্চিত, অপমানিত ও অধিকাংশ ক্ষেত্রে সব দিক থেকে দুর্গত।<br />\r\nতবে যে কথা একটু আগেই বলেছি, আর সব আগ্রাসী বর্বরতার মত ইসলামী আক্রমণের সবচেয়ে বড় লক্ষ্যবস্তু হচ্ছে নারী। নারীদের বন্দি ক&rsquo;রে দাসকরণ এবং ধর্ষণে যে শুধু পুরুষ যোদ্ধারা ধর্ষণের আনন্দ পেত তাই নয়, এ দ্বারা বন্দি নারীদের গর্ভে সন্তান জন্মদানের মাধ্যমে তারা ইসলামের সংখ্যা বৃদ্ধিরও সহজ উপায় খুঁজে পেত। অধিকন্তু এটা ছিল বিরুদ্ধ বা অমুসলিম জাতি বা জনগোষ্ঠীর মর্যাদা বোধ ভেঙ্গে দেবার সহজ পদ্ধতি। নারী মানুষের জননী। সুতরাং নারী মানুষের মর্যাদা বোধ এবং আবেগ-অনুভূতিরও সবচেয়ে স্পর্শকাতর জায়গা। তাই যারা মুহাম্মদের অনুসারী, অনুগত এবং অধীনস্থ নয় তাদের সবচেয়ে দুর্বল বা স্পর্শকাতর জায়গাতে আঘাত করেও ইসলাম তাদের প্রতিরোধের শক্তিকে ভাঙ্গতে চেয়েছে। এর পাশাপাশি ছিল ইসলামের বর্বর ও হানাদার পুরুষ শক্তিকে তৃপ্তি দানের উদ্দেশ্য। সুতরাং ইসলামে বন্দি নারী এবং ক্রীতদাসী ধর্ষণ অনুমোদন লাভ করেছে। আমরা এই প্রসঙ্গে ১৯৭১-এ বাংলাদেশে পাকিস্তানি সেনাবাহিনীর লক্ষ লক্ষ নারী ধর্ষণের কথা স্মরণ করতে পারি, যার আদর্শিক উৎস ছিল কুরআনের অনুমোদন এবং মুহাম্মদের দৃষ্টান্ত।<br />\r\nবস্তুত ইসলামকে শুধু আগ্রাসী যুদ্ধের ধর্ম বললে খুব সামান্যই বলা হয়; তাকে একই সঙ্গে বলতে হবে লুণ্ঠন, ধর্ষণ ও সন্ত্রাসের ধর্ম। তবে সবটাই তা ঢেকে রাখে অবিশ্বাস্য রকম ভণ্ডামি, মিথ্যাচার ও প্রতারণা দিয়ে। সুতরাং ন্যায়-নীতি বোধহীন দুর্নীতির ধর্মও এটা। ইসলামী পৃথিবীর দেশে দেশে শাসক বা সমাজ নেতাদের দুর্নীতির ব্যাপ্তি এই জন্য বিস্ময়কর নয়। পাশ্চাত্যের যে সমালোচনাই আমরা করি তার রাষ্ট্রনেতাদের কাছ থেকে যে নৈতিক সততা এবং সংযম সমাজ দাবি করে তার কোন প্রতিফলন ইসলামী পৃথিবীতে খুঁজে লাভ নাই।<br />\r\nনির্বিবেক নারী সম্ভোগী ও ধর্ষবাদী পুরুষ এবং স্বেচ্ছাচারী একনায়ক সৃষ্টি ও লালনের জন্য এমন অনুকূল সামাজিক পরিবেশ আর কোনও ধর্ম দেয় নাই যা ইসলাম দিয়েছে। ইসলাম যেখানে গেছে সেখানে মানুষের রক্ত, অশ্র&ldquo; ও ঘামের উপর দিয়ে গড়ে তোলা হয়েছে নিরঙ্কুশভাবে একনায়কী শাসকের ভোগ-সম্ভোগের জন্য জমকালো সব আয়োজন, বিরাট বিরাট প্রাসাদ, অগণিত বন্দি ও অবরুদ্ধ নারীর হেরেম, এইসব হেরেম পাহারার জন্য নৃশংস খোজা ব্যবস্থা এবং ঘটানো হয়েছে দাস ব্যবস্থার অপ্রতিরোধ্য বিস্তার।<br />\r\nশুধু আরবের মরুচারী যাযাবরদের জন্য নয়, উপরন্তু পৃথিবীর বিস্তীর্ণ অঞ্চলের কৃষি নির্ভর সভ্যতাগুলির প্রান্তে অবস্থিত যাযাবর ও অর্ধ যাযাবর বর্বর জনগোষ্ঠীসমূহের নিকট যুদ্ধ, লুণ্ঠন ও ধর্ষণের উপযোগী ধর্ম হিসাবে ইসলামের আবেদন হল অপ্রতিরোধ্য। এদের রাষ্ট্র ছিল না, সভ্যতা ছিল না, প্রকৃতপক্ষে পশু পালন ব্যতিরেকে উৎপাদনও ছিল না, নির্দিষ্ট ভূমির সাথে এরা আবদ্ধও ছিল না। আরব ছাড়াও উত্তর আফ্রিকা, মধ্য এশিয়া, আফগানিস্তান ও ভারতের উত্তর-পশ্চিম প্রান্তের যাযাবর, অর্ধ যাযাবররা ইসলামী বাহিনীর দ্বারা আক্রান্ত হয়ে হোক আর স্বেচ্ছায় হোক ইসলাম গ্রহণ ক&rsquo;রে দলে দলে কৃষি সমাজগুলির উপর ঝাঁপিয়ে পড়ল। মিসর, সিরিয়া, ইরাক, ইরান, ভারত, স্পেন, গ্রিস ও বলকানের কৃষক সমাজগুলি তছনছ হয়ে গেল। সব জাগায় ইসলামী বাহিনীর হাত ধরে গেল অর্ধসভ্য আরবের ভাষা ও সংস্কৃতি। সুপ্রাচীন সভ্যতার লীলাভূমি মিসর, সিরিয়া, ইরাক তাদের ধর্ম ও সংষ্কৃতির সঙ্গে তাদের ভাষাও হারালো। গর্বিত ও সুপ্রাচীন সভ্যতার অধিকারী ইরান আরবী লিপিমালা গ্রহণ করে তাদের ভাষাকে কোনক্রমে রক্ষায় সমর্থ হলেও তার প্রাচীন ধর্ম, সংস্কৃতি ও সভ্যতার গৌরবময় উত্তরাধিকারকে হারাতে বাধ্য হল। ভারতবর্ষ ইসলামের তরবারিকে মোকাবিলায় ব্যর্থ হলেও তার কৃষি সমাজ ও সভ্যতার শক্তির গুণে শেষ পর্যন্ত তার নিজ ধর্ম ও সংস্কৃতিকে অনেকাংশে রক্ষা করতে সমর্থ হল। অবশ্য ইসলামের ধ্বংসযজ্ঞের বিরাট আঘাত তাকে তছনছ করেছে সহ&macr;্রাধিক বৎসর ধরে। ইসলামের তরবারিতে প্রাণ দিয়েছে ছয় থেকে আট কোটি মানুষ (এমএ খান, জিহাদ : জবরদস্তিমূলক ধর্মান্তরকরণ, সা&curren;্রাজ্যবাদ ও দাসত্বের উত্তরাধিকার, পৃষ্ঠা- ২৭০)। ধর্ষিত হয়েছে হয়ত আরও বহু বেশি সংখ্যক নারী যাদের হিসাব করার চেষ্টা আজ অবধি হয়েছে বলে আমার জানা নাই। হয়ত সেটা সম্ভবও নয়। তবে নারীর প্রতি ভয়ঙ্কর ঘৃণা ও বিদ্বেষ পোষণকারী এবং নারীকে পুরুষের মত মানুষ হিসাবে বিবেচনা না ক&rsquo;রে শুধুমাত্র পুরুষের ভোগের সামগ্রী বিবেচনাকারী ইসলামের আক্রমণ ও আধিপত্য নারীর জন্য কী পরিণতি বয়ে আনতে পারে তা সহজেই অনুমান করা চলে।<br />\r\nযাইহোক, ইসলামের আঘাতে প্রাচীন সভ্যতাগুলি গুঁড়িয়ে গেলেও খ্রিস্ট ধর্মের মাধ্যমে ঐক্যের শক্তিতে বলীয়ান কৃষক ইউরোপ শেষ পর্যন্ত ইসলামকে প্রতিহত করে এবং একটা পর্যায়ে প্রত্যাঘাতেও সক্ষম হয়। স্পেন থেকে ইসলাম উৎখাত হয় এবং শেষ পর্যন্ত অতি সামান্য ভূখণ্ড বাদে বলকান এবং গ্রিসও মুক্ত হয়। তবে অনেক দূরবর্তী চীনের সভ্যতার শক্তিকে আঘাত করার ক্ষমতা ইসলামের ছিল না। তা সত্ত্বেও এশিয়া ও আফ্রিকার বিশাল অঞ্চলে ইসলামের প্রসার হল।<br />\r\nপ্রত্যেক মানুষ তার চেতনা বা চরিত্র ও প্রবণতা অনুযায়ী পরিপার্শ্বকে নির্মাণ করে। এই অর্থে বলা যায় মানুষ নিজেকেই পুনরুৎপাদন বা সম্প্রসারণ করে, অন্যকে নয়। সুতরাং মুহাম্মদ এমন সমাজ নির্মাণ করেছেন যার নেতা বা শাসক হবে তার নিজের পুনরুৎপাদন বা সম্প্রসারণ। সাধারণ নিয়ম অনুযায়ী তার চরিত্র ও মনস্তত্ত্বের প্রতিফলন ঘটবে শাসকের মধ্যে, এই সমাজের নেতার মধ্যে। একই সঙ্গে এই সমাজের শাসিতরা হবে স্বেচ্ছাচারী একনায়কী শাসকের স্বেচ্ছাচারী শাসনের উপযোগী। সাধারণ মানুষ বা জনগণ যে ভয়ঙ্কর দাস ব্যবস্থার সবচেয়ে বড় শিকার হয় তারা সেই ব্যবস্থার সবচেয়ে দৃঢ় সমর্থক ও রক্ষকে পরিণত হয়। উপরের তলার শাসকদের মধ্যে তবু শিক্ষা, প্রাচুর্য এবং তুলনামূলক নিরাপদ জীবনের প্রভাবে কিছু উদারতা ও সহিষ্ণুতা আসতে পারে। তাছাড়া শুধু নামাজ-রোজা দিয়ে রাষ্ট্র চলে না। অন্যান্য সমাজের বিরুদ্ধে যুদ্ধের প্রয়োজনেও ধর্ম বহির্ভূত কিছু জ্ঞান-বিজ্ঞান চর্চার প্রয়োজন হয়। সমাজ-তল জ্ঞান-বিজ্ঞান চর্চার প্রতি বিমুখ বা বিরোধী হওয়ায় সমাজের ভিতর থেকে এ সবের বিকাশ ঘটে না। ফলে অন-ইসলামী সমাজ থেকে এগুলিকে যত সীমাবদ্ধ আকারেই হোক দখল বা গ্রহণ করতে হয়। এভাবে দেখা যায় উপর তলার শাসকরা ধর্মের বিধিনিষেধের গণ্ডি অনেক সময় ভঙ্গও করে রাষ্ট্র বা শাসন রক্ষার প্রয়োজনে, নিজেদের ভোগেরও প্রয়োজনে। কিন্তু সমাজের নীচ তলা ধর্মের প্রশ্নে হয়ে থাকে ভয়ঙ্কর রকম উগ্র, অন্ধ ও গোঁড়া। এভাবে এই ভয়ঙ্কর ব্যবস্থার যে সবচেয়ে বড় শিকার সে নিজেই হয়ে পড়ে ভয়ঙ্কর শিকারী।<br />\r\nইসলামী পৃথিবীর এই কালো অধ্যায়ের অবসান সূচিত হয়েছে আধুনিক ইউরোপের আঘাতের ফলে। ইউরোপ যখন আধুনিক ও উন্নততর সমাজ সংগঠন, প্রযুক্তি এবং অস্ত্রের বলে বলীয়ান হয়ে ইসলামী সমাজকে আঘাত করেছে তখন ইসলামী সমাজের আক্রমণ ও আত্মরক্ষার শক্তি ভেঙ্গে পড়েছে। আধুনিক ইউরোপ পৃথিবীকে অনেক কিছুই দিয়েছে। দাস ব্যবস্থার অবসান, নারীর অধিকার ও স্বাধীনতা সম্পর্কে চেতনা, আধুনিক গণতান্ত্রিক ব্যবস্থা, শিল্প সভ্যতা&Ntilde;এ সবই ইউরোপের অবদান। কিন্তু একই সঙ্গে ইউরোপ দিয়েছে উপনিবেশবাদ ও সা&curren;্রাজ্যবাদ।<br />\r\nইউরোপের অধীনে প্রায় সমগ্র পৃথিবী চলে যায়। উত্তর ও দক্ষিণ আমেরিকা এবং অস্ট্রেলিয়া&Ntilde;এই তিন মহাদেশকে আবিষ্কার এবং অধিকার করে ইউরোপীয়রা নিজেদের আবাস ভূমিতে পরিণত করে। দক্ষিণ আমেরিকার আদিবাসী রেড ইন্ডিয়ানরা কিছু পরিমাণে টিকে থাকলেও উত্তর আমেরিকা মহাদেশের মার্কিন যুক্তরাষ্ট্র এবং কানাডায় তারা প্রায় নির্মূল হয়ে যায়। আফ্রিকার সমগ্র এবং এশিয়ার বিশাল ভূ-ভাগ ইউরোপীয় বিভিন্ন রাষ্ট্রের অধিকারে চলে যায়। সেই সঙ্গে ইসলামী পৃথিবীর অধিকাংশ অঞ্চলও ইউরোপীয়দের অধিকারভুক্ত হয়।<br />\r\nইউরোপের আধিপত্য সর্বত্র পরিবর্তন সূচিত করে। তার পুঁজি এবং প্রযুক্তি সর্বত্র কম-বেশি বিস্তৃত হয় সেই সঙ্গে বিস্তৃত হয় তার বাজার ব্যবস্থা। তবে সা&curren;্র্রাজ্যবাদী রূপে আবির্ভূত পাশ্চাত্যের ইতিবাচক ভূমিকার তুলনায় নেতিবাচক ভূমিকাই প্রকট হয়ে দেখা দিয়েছে। ইউরোপীয় রাষ্ট্রসমূহের আধিপত্যের বিরুদ্ধে সংগ্রামও ছিল সর্বত্র কম আর বেশি। বিশেযত বিংশ শতাব্দীর মধ্যভাগে এশিয়া-আফ্রিকার বহু সংখ্যক দেশ স্বাধীন হয়। এভাবে প্রত্যক্ষ সা&curren;্রাজ্যবাদ বা উপনিবেশবাদের যুগের অবসান ঘটে।<br />\r\nইউরোপ থেকে উদ্ভূত আধুনিক সভ্যতা সর্বত্র যে পরিবর্তন সূচিত করে সেই পরিবর্তনের ঢেউ মুসলিম পৃথিবীতেও আছড়ে পড়ে। প্রথম পর্যায়ে পরিবর্তন সূচিত হয় সেইসব দেশে যেসব দেশ প্রত্যক্ষ ইউরোপীয় শাসনাধীনে যায়। পরবর্তী সময়ে তুরস্ক, সৌদী আরব বা ইরানের মত যে দেশগুলি ইউরোপের অধিকারে যায় নাই সেই দেশগুলিতেও ধীরে গতিতে আধুনিক সভ্যতার স্পর্শ লাগতে শুরু করে। বিশেষ করে অবকাঠামো এবং প্রযুক্তির ক্ষেত্রে পরিবর্তন প্রায় সব ইসলামী দেশে লক্ষণীয় মাত্রায় ঘটেছে।<br />\r\nঅর্থাৎ বস্তুতগত উন্নয়ন ও প্রযুক্তির ক্ষেত্রে ইসলামী পৃথিবীও সামনের দিকে যাত্রা করেছে। কিন্তু এটা লক্ষণীয় যে এই যাত্রায় অমুসলিম চীন, ইন্দোচীন কিংবা লাতিন আমেরিকার দেশগুলির সাফল্যের তুলনায় ইসলামী দেশগুলির সাফল্য নগণ্য। বিশেষত ম্যধপ্রাচ্যের তেল ও গ্যাস সম্পদে সমৃদ্ধ দেশগুলির ব্যর্থতা থেকে আমাদের অনেক কিছু শিক্ষণীয় আছে। এবং ইসলামী পৃথিবীর প্রায় সব দেশ হয়ে আছে লাগামহীন দুর্নীতি, সন্ত্রাস, আত্মঘাতী সহিংসতা ও খুনাখুনি, ধর্মীয় সংখ্যালঘু, দরিদ্র ও দুর্বলদের উপর অবর্ণনীয় নির্যাতন এবং নারী নিগ্রহের লালন-ভূমি। পরমাণু শক্তিধর ইসলামী রাষ্ট্র পাকিস্তান ইসলামের কোন চিত্র তুলে ধরে? ইসলামী পৃথিবীর এই সামগ্রিক দুর্দশার বাইরে বাংলাদেশও নাই।<br />\r\nএই অবস্থার জন্য কাকে দায়ী করব? অনেকে এর জন্য যত দোষ নন্দঘোষ পাশ্চাত্য সা&curren;্রাজ্যবাদের ঘাড়ে সব দোষ চাপিয়ে শান্তি পান। তাতে অবশ্য নিজেদের ত্র&ldquo;টি ও ব্যর্থতার মূল কারণ অনুসন্ধানের কঠিন কাজটা এড়ানো যায়। কিন্তু সমাধানের পথ পাওয়া যায় না। এ কথা বুঝতে হবে যে, পাশ্চাত্যের আধিপত্যবাদ ও সা&curren;্রাজ্যবাদ ইসলামী পৃথিবীর উপর চেপে আছে ইসলামী সমাজের নিজস্ব দুর্বলতার কারণে, যার উৎস ইসলাম নিজেই।<br />\r\nআধুনিক সভ্যতার সঙ্গে অলোকবাদী ধর্মগুলির সম্পর্ক মূলত সাংঘর্ষিক। কারণ আধুনিক সভ্যতা অন্ধবিশ্বাস নির্ভর ধর্ম চর্চার পরিবর্তে যুক্তি ও প্রমাণ নির্ভর জ্ঞান-বিজ্ঞান চর্চার উপর নির্ভরশীল। এই অবস্থায় বিদ্যমান ধর্মের বিরুদ্ধে লড়াই না করে আধুনিক সভ্যতা কোথায়ও অগ্রসর হতে পারে নাই। এই কাজ পশ্চিম ইউরোপ একভাবে, পূর্ব ইউরোপ আর একভাবে, আবার চীন, ভিয়েৎনাম, উত্তর কোরিয়া, লাওস, কম্বোডিয়ার মত দেশগুলি যার যার মত করে করেছে। খ্রিস্ট ধর্ম এবং বৌদ্ধধর্ম মূলত শান্তিপূর্ণ সামাজিক ধর্ম হওয়ায় এই ধর্মগুলির বিরুদ্ধে লড়াইয়ের রূপ সাধারণভাবে থেকেছে শান্তিপূর্ণ। তবে পশ্চিম ইউরোপে বিদ্যমান ধর্মের বিরুদ্ধে লড়াই যে এক সময় শান্তিপূর্ণ এবং সহজ ছিল না সে বিষয়ে ইতিপূর্বে বলেছি। ক্যাথলিক ধর্মমতের বিরুদ্ধে সংগ্রাম করতে গিয়ে এক সময় সেখানে প্রোটেস্ট্যান্টদেরকে অনেক নিপীড়ন সহ্য করতে হয়েছে যেমন হয়েছে রেনেসাঁর নায়কদের অনেককে। ইংল্যান্ড, ফ্রান্স ও জার্মানির মত দেশগুলিতে ক্যাথলিক ও প্রোটেস্ট্যান্টদের মধ্যে যুদ্ধে লক্ষ লক্ষ মানুষ নিহত হয়। ধর্মীয় আন্দোলনের পাশাপাশি ছিল বুদ্ধির মুক্তি তথা যুক্তিবাদী আন্দোলন। সব মিলিয়ে রাষ্ট্রের উপর ধর্মের আধিপত্য শেষ হয়েছে, ব্যক্তির ধর্ম বিশ্বাস ও পালন না করার স্বাধীনতা প্রতিষ্ঠিত হয়েছে এবং জাগ্রত ইউরোপ পৃথিবীতে তার আধিপত্য প্রতিষ্ঠার পাশাপাশি তার ধর্মমুক্ত সেকিউলার বা লোকবাদী চেতনাও ছড়িয়ে দিয়েছে।<br />\r\nবস্তুত মানবিক উন্নয়নের প্রধান শর্ত তার চেতনার উন্নয়ন। এই চেতনাকে অন্ধ বিশ্বাস ও কুসংস্কার থেকে মুক্ত না করে মানুষের প্রকৃত মুক্তির পথে যাত্রা করা সম্ভব নয়। সুতরাং ব্যক্তি ও রাষ্ট্রের উন্নয়নকে নিশ্চিত করতে আজকের যুগে সব আধুনিক ও উন্নত রাষ্ট্রকেই অলোকবাদী ধর্মের শাসন ও খবরদারি থেকে মানুষকে মুক্ত করার জন্য কম অথবা বেশি পদক্ষেপ দিতে হয়েছে। এটা ঠিক যে ইউরোপ বা আমেরিকায় ধর্ম বিশ্বাস ও পালনের স্বাধীনতা আছে। কিন্তু সেটা শর্ত সাপেক্ষে। অন্যের ব্যক্তিগত অধিকারে হস্তক্ষেপ করে নয়। রাষ্ট্র চলে জাগতিক তথা লোকবাদী আইনে। সেখানে আল্লাহর আইনের মত ঈশ্বর বা ধর্মের আইন প্রয়োগের উপায় নাই। রাষ্ট্র সেটাকে মানবে না। খ্রিস্ট ধর্ম রাজনৈতিক ও সামরিক ধর্ম না হওয়ায় ব্যক্তিগত বিশ্বাসের জায়গায় তা ফিরে যেতে ও থাকতে পারে। ব্যক্তির জীবনাচরণে সীমাবদ্ধ থাকলে তা সমাজ বা রাষ্ট্রের জন্য বড় ধরনের কোন সমস্যা সৃষ্টি করে না। তখন তার বিরুদ্ধে লড়াইটাও শান্তিপূর্ণ হতে পারে। তখন সকলে যে যার যুক্তি অথবা বিশ্বাস নিয়ে পরস্পরকে সমর্থন অথবা খণ্ডনের চেষ্টা করতে পারে।<br />\r\nএই প্রসঙ্গে এই কথাও এখানে বলা প্রাসঙ্গিক হবে যে, অতীতে অনেক নেতিবাচক ভূমিকা রাখলেও খ্রিস্টীয় নীতি-নৈতিকতা ও মূল্যবোধের এমন কিছু ইতিবাচক দিক আছে যেগুলি সমাজের জন্য বিপুলভাবে কল্যাণকর ভূমিকা পালন করেছে। খ্রিস্ট ধর্ম বর্তমানে ইউরোপ-আমেরিকায় ক্রমবর্ধমানে প্রভাব হারাতে থাকলেও তার উত্তরাধিকার বৌদ্ধ ধর্মের মতই মানুষের জন্য এমন এক মহৎ উত্তরাধিকার রেখে যাচ্ছে যার মূল্যায়নে আমরা যেন অনুদার না হই। এই আলোচনায় ইতিপূর্বে আমি যে কথা বলেছি সেই কথার জের টেনে বলতে হয় যে, খ্রিস্ট ধর্মের উত্তরাধিকার ছাড়া আধুনিক ইউরোপের জন্ম হত না। এক সময় এই উত্তরাধিকারের সঙ্গে একটা ছেদেরও প্রয়োজন হয়েছিল। এই ছেদ কোথায়ও কম, কোথায়ও বেশি এবং নানান রূপে ঘটেছিল। না ঘটলে আধুনিক সভ্যতার উদ্ভব এবং সমাজ ও রাষ্ট্র বিপ্লব কিংবা শিল্প বিপ্লব কোনটাই সম্ভব হত না।<br />\r\nকিন্তু যে কথা খ্রিস্ট বা বৌদ্ধ ধর্ম সম্পর্কে বলা যায় ইসলাম সম্পর্কে যে সে কথা বলা যায় না তা আশা করি আমার এই আলোচনায় ইতিমধ্যেই কিছুটা হলেও স্পষ্ট করতে পেরেছি। এই তিন ধর্মের ভূমিকাকে আরও স্পষ্ট করার জন্য তিন ধর্ম প্রতিষ্ঠাতার দিকে আর একবার দৃষ্টি দেওয়া যাক। ধর্মের আবরণ সরালে আমরা কী দেখতে পাই? মানুষের জন্য শুধু নয়, বরং সব প্রাণীর জন্যও করুণাঘন বুদ্ধ যেন প্রেম ও দয়ার মূর্ত প্রতীক। মানুষের মুক্তির পথ সন্ধানে সমাজ, সংসার, স্ত্রী-পুত্র সবই ত্যাগ করে রাজপুত্র বুদ্ধ তার বিশ্বাস অনুযায়ী আমৃত্যু মানুষের কাছে বাণী প্রচার করে গেছেন। বুদ্ধ চলে গেছেন। কিন্তু তাঁর শিক্ষা ও দৃষ্টান্তে অগণিত মানুষ অনুপ্রাণিত হয়েছে। সবাই বুদ্ধের পথ অনুসরণ করে সংসার ত্যাগী বা ব্রহ্মচারী-ব্রহ্মচারিণী হয়ে শুদ্ধ জীবন যাপন করে না, করতে পারেও না। কিন্তু সঙ্ঘে বাস ক&rsquo;রে যে সব নারী ও পুরুষ সেই ধরনের জীবন যাপন ও ধর্ম চর্চা করে বুদ্ধ তাদের মধ্য দিয়েই সমাজে বেঁচে থাকেন। সংসারী ও সাধারণ মানুষ তাদের মধ্য দিয়েই বুদ্ধের সামনে প্রণত হয়ে সমাজ, মানুষ ও জীবন সম্পর্কে একটা বোধের সামনে প্রণত হয়। আসলে একেবারে শুদ্ধতার ধারণা দিয়ে সমাজ-রাষ্ট্র চলতে পারে না। কিন্তু মহত্তর একটা বোধ যদি মানুষের মধ্যে না থাকে তবে পৃথিবী ও সমাজ বাসযোগ্য থাকে না। সে কালে সে কালের মত করে বুদ্ধ সেই বোধ মানুষের মধ্যে জাগাতে পেরেছিলেন। এটা হয়ত এমন একটা আদর্শ যাকে ধ্র&ldquo;বতারার মত দেখা যায় কিন্তু ধরা যায় না। তবু এটা পথ দেখাতে তো পারে। যে কালে কম্পাসের সন্ধান মানুষের জানা ছিল না সে কালে অকূল সাগরে রাতের আঁধারে জাহাজের পথের দিশা ধ্র&ldquo;বতারাই তো দিবে।<br />\r\nমানুষের দুঃখে ব্যথিত যিশুও যেন মানুষের জন্য প্রেম ও দয়ার মূর্ত প্রতীক। শান্তির পথচারী, অকৃতদার যিশু তার প্রেম, করুণা ও সেবার ধর্ম প্রচার করতে গিয়ে ক্রুশবিদ্ধ হয়ে আত্মদান করে গেছেন। এ যেন ক্রুশবিদ্ধ বুদ্ধ। তবে প্যালেস্টাইনে জন্ম লাভকারী এ বুদ্ধের বিস্তার মূলত রোমান সাম্রাজ্যভুক্ত তিন মহাদেশের বিশাল অঞ্চলে। পরবর্তী কালে সারা পৃথিবীর আরও বিস্তীর্ণ অঞ্চলে তার বিস্তার। তিনিও পেয়েছেন বুদ্ধের মত অনুসারী ও সঙ্ঘ বা চার্চ। প্রেম ও করুণার প্রতীক যিশুর ভূমিকাও অনেকাংশে বুদ্ধের অনুরূপ।<br />\r\nপ্রেম ও করুণার প্রতীক বুদ্ধ ও যিশুর পাশে এবার দাঁড় করানো যাক মুহাম্মদকে। মুহাম্মদ যেন মানুষের প্রতি এবং আরও বিশেষ করে নারীর প্রতি ঘৃণার মূর্ত প্রতীক। এ ঘৃণা যেন জীবনের প্রতি, জীবের প্রতি। এই জীবনকে যে তার গর্ভে ধারণ করে সেই নারীর প্রতি তাই বুঝি তার সবচেয়ে বেশি ঘৃণা। মুহাম্মদ বলছেন, &lsquo;দোযখ পরিদর্শন কালে আমি দোযখের দ্বারে দাঁড়াইলাম এবং জানিতে পারিলাম যে, দোযখীদের মধ্যে নারীদের সংখ্যাগরিষ্ঠতা হইবে&rsquo; (বোখারী শরীফ), &lsquo;নারী শয়তানের আকৃতিতে সম্মুখে আসে এবং শয়তানের আকৃতিতে চলিয়া যায়&rsquo; (বোখারী শরীফ)। ত্যাগের মহিমা নয় বরং ভোগের লোভ এবং শাস্তির ভয় দেখিয়ে মুহাম্মদ তার ধর্মরাজ্যের যাত্রাপথ নির্মাণ করেছেন। যারা তাকে অনুসরণ করবে তাদের জন্য ভোগের আয়োজন শুধু পরলোকে নয় ইহলোকেও করেছেন। আর যারা তাকে প্রত্যাখ্যান করবে তাদের শাস্তির আয়োজনও শুধু পরলোকে নয় ইহলোকেও করেছেন। সুতরাং মানুষের মনে লোভের পাশে ভয় বা ত্রাস সৃষ্টির জন্য যা যা করা সম্ভব সবই মুহাম্মদ করেছেন। মুহাম্মদ গর্বের সঙ্গে বলছেন, &lsquo;এক মাসের পথের সুদূর প্রান্তে আমার প্রভাবে ভীতির সঞ্চার দ্বারা আল্লাহ্ তাআলা আমাকে সাহায্য করিয়াছেন&rsquo; (বোখারী শরীফ, তৃতীয় খণ্ড, অনু: মাওলানা শামছুল হক ও মাওলানা আজিজুল হক, একাদশ সংস্করণ, জুন ২০০৭, পৃষ্ঠা-৮৫)।<br />\r\nশান্তি ও সহিষ্ণুতার কথা বলা আর যা-ই হোক ইসলামের জন্য সাজে না। অথচ এগুলির কথা হরহামেশাই ইসলাম আর তার অনুসারীরা শোনায়। এটা তার ভণ্ডামি ও প্রতারণার একটা হাতিয়ার মাত্র। মুহাম্মদ বা তার ইসলাম ভিন্নতার সামান্যতম চিহ্নকে পর্যন্ত সহ্য করে না। সুতরাং মুহাম্মদ তার ধর্মমতের অনুসারী যারা নয় তাদের সবাইকে সমগ্র আরব ভূমি থেকে বিতাড়নের সিদ্ধান্ত ঘোষণা করেন, &lsquo;আমি ইয়াহুদী ও নাসারাদের অবশ্যই আরবভূমি হতে বের করে দেব এবং এখানে মুসলমান ছাড়া আর কেউ থাকবে না&rsquo; (আবু দাউদ শরীফ, চতুর্থ খণ্ড, ইসলামিক ফাউন্ডেশন বাংলাদেশ, ২০০৬, পৃষ্ঠা- ২১৭)। তার জীবদ্দশায় সে কাজ শুরু করলেও সমাপ্ত করতে পারেন নাই। তার মৃত্যুর পর খলিফা ওমর এই সিদ্ধান্ত বাস্তবায়ন করেন। ইসলাম গ্রহণ যারা করল না তাদেরকে বিতাড়ন অথবা হত্যা ক&rsquo;রে আরব কে সম্পূর্ণরূপে মুসলিম আবাস ভূমিতে পরিণত করা হল। আরবের ইসলামীকরণ সম্পূর্ণ হতে না হতে ইসলামের বাহিনী ঝাঁপিয়ে পড়ল সভ্য পৃথিবীর উপর।<br />\r\nআসলে এই রকম বর্বরতাপূর্ণ ধর্মের উত্থান তখনই সম্ভব হয়েছিল যখন বিশাল অঞ্চল ব্যাপী সভ্যতায় ব্যাপক ধ্বংস এবং ক্ষয় নেমে এসেছিল। প্রাচীন মিসর, মেসোপটেমিয়া, সিন্ধুর সভ্যতার আলো বহু কাল আগে নিভে গেছে। গ্রিসের আলোও নিভে গেছে। রোমের সভ্যতাও তখন ধ্বংস প্রায় । ভিসিগথ বর্বরদের হাতে ৪১০ খ্রীষ্টাব্দে রোম নগর লুণ্ঠিত হয়। রোমকে কেন্দ্র করে গড়ে উঠা পশ্চিমে অবস্থিত পশ্চিমের রোমান সা&curren;্রাজ্যের পতন হয়েছে ৪৭৬ খ্রিস্টাব্দে। এরপর কন্সট্যান্টিনোপলকে কেন্দ্র করে টিকে থাকা পূর্ব দিকের বিরাটায়তন বাইজান্টাইন রোমান সা&curren;্রাজ্যও তখন পতনের দিকে এগিয়ে চলেছে। কিন্তু রোমান সভ্যতার সেই গৌরব তখন অনেকটাই স্মৃতি মাত্র। তার সা&curren;্রাজ্যের পূর্ব দিকে অবস্থিত বৃহৎ ও শক্তিশালী পারস্য সা&curren;্রাজ্যও তখন ক্ষয়িষ্ণু। তৎকালীন পৃথিবীর এই দুই পরাশক্তি পরস্পরের সঙ্গে অমীমাংসিত যুদ্ধে অবসন্ন। এক বিশাল ভূভাগের মানুষ সভ্যতার উপর আস্থা রাখার কারণ দেখছিল না। যে কোন বর্বরতার উত্থানের জন্য এটা ছিল একটা উপযুক্ত সময়। এই রকম সময়েই সভ্যতার প্রান্তে থাকা আরবের এক মরূদ্যান শহর মক্কায় আনুমানিক ৫৭০ খ্রিস্টাব্দে মুহাম্মদের জন্ম। (সভ্যতার এই সঙ্কট বিষয়ে আলোচনার জন্য দেখুন ঃ শামসুজ্জোহা মানিক: ইসলামের ভূমিকা ও সমাজ উন্নয়নের সমস্যা, ব-দ্বীপ প্রকাশন, ঢাকা, ২০১০, পৃ: ৭৫-৮০।)<br />\r\nআরবের নিকটবর্তী বিভিন্ন দেশে বাণিজ্যের প্রয়োজনে সেই সময় আরব বণিক বিশেষত কুরাইশদের যেতে হত। বাণিজ্যোপলক্ষ্যে তার গোত্রের অন্যান্য কুরাইশের মত মুহাম্মদও একাধিক বার সিরিয়ায় গিয়েছিলেন। অন্যান্য কুরাইশের মত সভ্যতার এই সঙ্কট দশা তারও নিশ্চয় চোখে পড়েছিল। এটাই যুক্তিসঙ্গত মনে হয় যে, সভ্যতার সঙ্কট ও দুর্বলতাকে কাজে লাগিয়ে বিশ্বজয়ের উচ্চাকাক্সক্ষা তার মাথায় কাজ করছিল। এর জন্য প্রয়োজন ছিল একটি সেনাবাহিনীর, যা হবে প্রথমে আরব জয়, সেখানে একটি রাষ্ট্র প্রতিষ্ঠা এবং অতঃপর বিশ্বজয়ের হাতিয়ার। এই সেনাবাহিনী এবং রাষ্ট্র প্রতিষ্ঠার জন্য তার প্রয়োজন ছিল বিভিন্ন উপজাতিতে বিভক্ত আরবকে ঐক্যবদ্ধ সমাজ প্রতিষ্ঠার। এই ঐক্যবদ্ধ সমাজ প্রতিষ্ঠার জন্য তিনি ধর্মকে দেখতে পান সবচেয়ে কার্যকর হাতিয়ার হিসাবে। কিন্তু আরবের বিদ্যমান ধর্মগুলিকে অথবা এগুলির কোন একটিকে তিনি তার উদ্দেশ্য সাধনের উপযোগী দেখতে পান নাই। এ ক্ষেত্রে তার প্রয়োজন হয়েছিল আরবে প্রচলিত ধর্মগুলিকে ব্যবহার করে একটি নূতন ধর্ম প্রতিষ্ঠা করা। তার জন্য তিনি তার মত করে আরবে বিদ্যমান পৌত্তলিক তথা পেগান, ইহুদী ও খ্রিস্টানসহ সকল ধর্ম থেকে তার উপকরণ সংগ্রহ করলেন এবং আরবে কুরাইশদের প্রভাব ও ঐতিহ্যকে ব্যবহার করলেন। আরবের নেতৃত্বস্থানীয় কুরাইশ গোত্রের প্রভাবশালী পরিবারের একজন হওয়ায় কাজটা তার জন্য সহজতর হল। তার এই প্রয়োজনে তিনি তৎকালীন মক্কা এবং কুরাইশদের প্রধান দেবতা আল্লাহকে সর্বশক্তিমান ও একমাত্র উপাস্য দেবতা হিসাবে ঘোষণা করলেন।<br />\r\nকিন্তু এটা তাৎপর্যপূর্ণ যে তৎকালীন আরব সমাজের মানদণ্ড বিচারে সভ্য কুরাইশদের মধ্যে মুষ্টিমেয় কয়েক জন বাদে মুহাম্মদ আর কাউকে তার ধর্মে স্বেচ্ছায় নিতে পারেন নাই। তাদেরকে যুদ্ধে পরাজিত ক&rsquo;রে হত্যার ভয় দেখিয়ে নিতে হয়েছে। এর জন্য তিনি সাহায্য নিয়েছেন প্রধানত মরুচারী, বর্বর ও সভ্যতাবিরোধী বেদুইনদের। সুতরাং এই বেদুইনদের জীবন বোধ, বিশ্বদৃষ্টি, আচার-আচরণ ও চরিত্র ইসলামের গভীরে ছায়া ফেলে আছে। বেদুইনদের উপর নির্ভর করে তিনি যে ধর্ম গড়লেন তা যে শুধু অমানবিক হল তাই নয় সেই সঙ্গে হল সভ্যতা বিরোধী।<br />\r\nসুতরাং যা কিছু মানুষের মনকে চিন্তাশীল করে, ন&curren;্র করে, সুকুমার বৃত্তির অধিকারী করে, সৃজনশীল করে তার প্রতিই তার ঘৃণা এবং বিদ্বেষ। প্রকৃতপক্ষে মানুষকে যা কিছু সভ্যতামুখী করে তার প্রতিই ইসলামের ঘৃণা এবং বিদ্বেষ। যে সভ্যতা তা নির্মাণ করতে পারে না তার প্রতি তার ঘৃণা। অথচ ঘৃণিত এই সভ্যতার প্রতি তার লোভ। লোভ সভ্যতার দ্বারা সৃষ্টি ভোগের সামগ্রীর জন্য। কাজেই ঘৃণার সঙ্গে ভোগ ইসলামের মর্মমূলে গাঁথা। যেমন তা ঘৃণার সঙ্গে নারীকে সম্ভোগ করে তেমন তা ঘৃণার সঙ্গে সভ্যতাকেও ভোগ করে। এই রকম ধর্ম নিয়ে যে মানবিক মূল্যবোধসম্পন্ন কোনও সমাজ এবং উন্নত সভ্যতা নির্মাণ করা সম্ভব নয় সেটা বহু অভিজ্ঞতা থেকে প্রমাণিত।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">আধুনিক সভ্যতায় ইসলামের ভূমিকা</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বস্তুত এটি বোঝা খুবই গুরুত্বপূর্ণ যে, ইসলামী সমাজ মাত্রই রাজনৈতিক সমাজ। ইংরাজি পরিভাষা ব্যবহার করলে একে পলিটি (ঢ়ড়ষরঃু) বলা যায়। কারণ ধর্মটিই রাজনৈতিক। ফলে ইসলামী সমাজে রাষ্ট্রকে ধর্ম থেকে পৃথক ঘোষণা করে কোন লাভ হয় না। যদি করা যায় তবে সেটা যে কাণ্ডজে ঘোষণা মাত্র হয় তার সবচেয়ে বড় উদাহরণ বাংলাদেশ, যেখানে ১৯৭২-এর সংবিধানে রাষ্ট্রকে লোকবাদী বা ধর্মনিরপেক্ষ ঘোষণা করা হয়েছিল। ধর্মনিরপেক্ষকতার ঘোষণা সত্ত্বেও বাংলাদেশ প্রতিষ্ঠার পর মুজিব সরকার বিভিন্ন কৌশলে ইসলামকে পৃষ্ঠপোষকতা দানের যে কর্মনীতি অনুসরণ করেন তা ১৯৭৫-এর পটপরিবর্তনের পর ধর্মনিরপেক্ষকতাকে প্রত্যাখ্যানের রাষ্ট্রীয় নীতিতে পরিণত হয়। আনুষ্ঠানিকভাবেই বাংলাদেশকে পুনরায় একটি ইসলামী রাষ্ট্রে পরিণত করা হয়। এভাবে &rsquo;৭২ থেকে &rsquo;৭৫ পর্যন্ত রাষ্ট্র পরিচালনায় যে কপটতা চলছিল তার অবসান ঘটানো হয়। এটা বিস্ময়কর নয় যে কামাল পাশার দেশ তুরস্কেও আজ ইসলামবাদীরা ক্ষমতায় আছে। এবং তারা ক্ষমতায় এসেছে নির্বাচন প্রক্রিয়ায় শান্তিপূর্ণভাবে। আসলে শেষ বিচারে রাষ্ট্র সমাজের অভিব্যক্তি। সুতরাং কিছু সময়ের জন্য সমাজের সঙ্গে রাষ্ট্রের দূরত্ব সৃষ্টি করা গেলেও একটা সময় সমাজ রাষ্ট্রকে অধিকার করে।<br />\r\nবস্তুত, কখনও পাশ্চাত্য আধিপত্যের প্রভাবে উপর তলায় একটা উদার বা অর্ধ ইসলামের আবরণ কিছু কাল রক্ষা করতে পারলেও সমাজতলে অবস্থানকারী এবং প্রাধান্য বিস্তারকারী ইসলামী চেতনা শেষ পর্যন্ত উপরতলা এবং রাষ্ট্রকে যে প্রভাবিত করে শুধু তা-ই নয়, উপরন্তু অধিকারও করে। আধুনিক সভ্যতার সঙ্গে অসঙ্গতিপূর্ণ ও সাংঘর্ষিক ইসলামী সমাজে অভ্যন্তরীণ সংকট ও বিরোধ যে ক্রমবর্ধমান হবে সেটাই স্বাভাবিক। নূতন চিন্তায় অক্ষম সমাজে সঙ্কট দেখা দিলে সর্বদা সমাধান হিসাবে ইসলামকেই দেখতে পায়। যেটুকু তা এগিয়েছে সেখান থেকেও তা পিছন ফিরে ইসলামের যাত্রা শুরুর সেই আদি অধ্যায়ের সময়টাতে ফিরতে চায় যখন মুহাম্মদ ও তার পরবর্তী খোলাফায়ে রাশেদীনের খলিফারা যুদ্ধ ও লুণ্ঠনের পথে নিয়ে দরিদ্র ও বর্বর বেদুইনদেরকে ভোগের জন্য এনে দিয়েছিলেন বিপুল সম্পদ, শ্রমদাস এবং যৌনদাসী হিসাবে অগণিত লুণ্ঠিত নারী। ইসলামের আদি যাত্রা প্রায় অপ্রতিহত বিজয়ের পথ ধরে। ওহোদের যুদ্ধের কথা বাদ দিলে তার আদি যাত্রায় কোনও পরাজয় নাই। সুতরাং ইসলাম মানে বিজয়। আর বিজয় মানেই লুঠ। আর লুঠ মানে দরিদ্র ও বর্বর আরবদের জন্য সব সমস্যার সমাধান। এভাবে ইসলামের বিজয় মুসলমানদের চেতনায় এমন এক স্বর্ণযুগের চিত্র এঁকে দিয়েছে যা হয়ে থাকে বিশ্বাসী মুসলমানের বিপুল প্রেরণার উৎস। যে কোন সমাজ সঙ্কটে তাই সে ইসলামের সেই আদি অধ্যায়ে প্রত্যাবর্তন করতে চায়। অন্যদের কাছে এটা মানবতার অন্ধকার অধ্যায় মনে হতে পারে। কিন্তু এটাই বিশ্বাসী মুসলমানের কাছে সবচেয়ে আলোকিত অধ্যায়। ফলে উগ্র ও মৌলবাদী ইসলামের প্রত্যাবর্তন বা পুনঃপ্রতিষ্ঠা সময়ের ব্যাপার মাত্র হয়ে দাঁড়ায়। বহুদলীয় নির্বাচনমূলক ব্যবস্থা থাকলে ঘটনাটা তুলনায় শান্তিপূর্ণভাবে এবং নির্বাচনের মাধ্যমে ঘটতে পারে।<br />\r\nএই বাস্তবতায় ইসলামী সমাজের ভিতর থেকে স্বাধীনভাবে আধুনিক সভ্যতার বিকাশ অসম্ভব ব্যাপার হয়ে থেকেছে। যেটুকু হয়েছে তা পাশ্চাত্য আধিপত্যের চাপে এবং প্রয়োজনে। ইসলামী পৃথিবীর দেশে দেশে বিরাজমান দুর্নীতি, পশ্চাৎপদতা, দারিদ্র্য, সন্ত্রাস, সহিংসতা, স্বৈরতন্ত্র বা ফ্যাসিবাদী একনায়কতন্ত্র এবং অবর্ণনীয় নারী নিগ্রহের মূল উৎস বাহিরে না খুঁজে ইসলামের ভিতরেই খুঁজতে হবে। পাশ্চাত্য আধিপত্যকে কেন্দ্র করে যে সা&curren;্রাজ্যবাদী বিশ্ব ব্যবস্থা গড়ে উঠেছে তার সবচেয়ে বড় শিকার আজ ইসলামী পৃথিবী। কিন্তু সেটাও সম্ভব হয়েছে ইসলামের কারণে। চীন-ইন্দোচীন অনেক পূর্বেই সা&curren;্রাজ্যবাদী আধিপত্যকে পিছনে ফেলে স্বাধীনভাবে উন্নত সভ্যতা নির্মাণের পথে এগিয়ে চলেছে। কিউবার পথ অনুসরণ করে জাগ্রত লাতিন আমেরিকার দেশগুলিও একে একে মার্কিন নিয়ন্ত্রণ ও আধিপত্যের শৃঙ্খল ভেঙ্গে বেরিয়ে আসছে। কিন্তু ইসলামী বিশ্ব সামান্য ব্যতিক্রম বাদে পুরাপুরি মার্কিন নেতৃত্বাধীন পাশ্চাত্যের অধীনস্থ ব্যবস্থাধীনে আবদ্ধ। সিরিয়া, ইরান যে পাশ্চাত্য নেতৃত্বাধীন মার্কিন যুক্তরাষ্ট্রকে অগ্রাহ্য করে দাঁড়িয়ে আছে সেটা সম্ভব হয়েছে অমুসলিম রাশিয়ার কারণে।<br />\r\nইসলাম যে পৃথিবীতে আধিপত্য প্রতিষ্ঠাকারী বা সা&curren;্রাজ্যবাদী পাশ্চাত্যের আধিপত্য রক্ষার এক মহামূল্যবান হাতিয়ার তা বহুকাল যাবৎ পাশ্চাত্য বুঝেছে। যতদিন দেহশক্তি ও ঢাল-তলোয়ারের যুগ ছিল ততদিন ইসলাম তার অগ্রাভিযান অপ্রতিহত রাখতে পেরেছিল। তা সত্ত্বেও ইউরোপ শেষ পর্যন্ত ইসলামকে প্রতিহত করতে পেরেছিল; এবং একটা পর্যায়ে যখন উন্নততর চেতনা, সমাজ সংগঠন এবং প্রযুক্তির বলে বলীয়ান হয়ে ইউরোপ পাল্টা আঘাত হেনেছে তখন ইসলাম পরাভূত হয়েছে। এরপর ইসলামী পৃথিবীর প্রায় সবটা ইউরোপীয় বিভিন্ন রাষ্ট্রের পদানত হয়। একটা পর্যায়ে ইউরোপের প্রত্যক্ষ শাসন থেকে মুক্ত হয়ে ইসলামী দেশগুলি স্বাধীন রাষ্ট্র হিসাবে দেখা দেয়। কিন্তু ইউরোপের আধিপত্যমূলক যে ব্যবস্থার জালে ইসলামী পৃথিবী বাঁধা পড়েছে তা থেকে তার মুক্তিলাভ আর সম্ভব হয় নাই।<br />\r\nএক অর্থে এই জাল আধুনিক সভ্যতার জাল, যাতে বাঁধা পড়েছে শুধু ইসলামী পৃথিবী নয়, উপরন্তু সমগ্র পৃথিবী এবং মানব জাতিও। তবে এটা মানব জাতির জন্য জাল বা বন্ধন কোনটাই নয়, বরং মুক্তি। কারণ আধুনিক সভ্যতা মানুষকে বিরাট মুক্তি দিয়েছে, তার আরও সামনে যাবার পথকে উন্মোচিত করেছে। অন্যান্য সমাজ ক্রমবর্ধমান সংখ্যায় পাশ্চাত্য আধিপত্যের জালকে ছিন্ন করে যার যার মত করে আধুনিকায়ন, উন্নয়ন ও গণতন্ত্রায়নের পথ ধরে এগিয়ে চলেছে। কিন্তু ইসলামের জন্য এ কথা আদৌ প্রযোজ্য নয়। কারণ ইসলাম সভ্যতার বিপরীতে বর্বরতা ও বন্যতার ধর্ম, মানবতার পরিবর্তে নৃশংস পশুত্বের ধর্ম। মুহাম্মদের নেতৃত্বে একদল হিং&macr;্র, বর্বর, লুঠেরা ও ধর্ষক বেদুইনদের হাত দিয়ে যে ধর্ম প্রতিষ্ঠা ও প্রসার লাভ করেছিল তার মর্মমূলে মানুষের যে চেতনা কাজ করে তাকে ধূর্ত, প্রতারক, হিং&macr;্র, বর্বর ও নারীধর্ষক গুহা-মানব চেতনা বলাই সঙ্গত হবে। এদের আধুনিক সভ্যতা নির্মাণের ক্ষমতা যেমন নাই তেমন নাই তাকে প্রতিরোধ করার ক্ষমতাও।<br />\r\nইসলামের প্রভাবে কোন মুসলিম সমাজের পক্ষে যে সভ্যতার উৎকর্ষ সাধন করা এবং পাশ্চাত্যের সমকক্ষ হওয়া সম্ভব নয় সেটা না বুঝবার মত বোকা পাশ্চাত্য নয়। সুতরাং তা ইসলামকে নিজ আধিপত্যাধীনে নিবার পর বহুকাল ধরে তাকে ব্যবহার, রক্ষা ও লালনের কৌশল নিয়েছে। এ ক্ষেত্রে ইসলামের চরম অমানবিকতা, স্বেচ্ছাচার, নারী অধিকারের প্রতি নির্লজ্জ অবমাননা ও মানুষের ব্যক্তি স্বাধীনতা হরণকারী দাসত্ব বা বান্দাগিরির ধারণা এগুলি কোনই সমস্যা সৃষ্টি করে না। সুতরাং প্রতিক্রিয়ার ইসলামী দুর্গ সৌদী রাজতন্ত্র আজ সা&curren;্রাজ্যবাদী বিশ্ব ব্যবস্থার নেতা মার্কিন যুক্তরাষ্ট্রের এত প্রিয় এবং নির্ভরযোগ্য পোষ্য। সর্বত্র গণতন্ত্র ও মানবাধিকারের জন্য গলাবাজী করে বেড়ানো মার্কিন যুক্তরাষ্ট্র এবং পশ্চিম ইউরোপ সর্ব প্রযতে&oelig; সৌদী আরবকে রক্ষা করে চলেছে যেখানে গণতন্ত্র ও মানবাধিকারের চিহ্নমাত্র নাই।<br />\r\nইসলামের লুণ্ঠনজীবী চরিত্রের কারণে পাশ্চাত্যের হাতে পরাজয়ের পর বিশেষ করে ইসলামের পরবর্তী বা নূতন নেতৃত্বের পক্ষে পাশ্চাত্যের পরাক্রান্ত কাফেরদের দাসত্ব করতে বা অধীনতামূলক অবস্থান গ্রহণ করতে সমস্যা হয় না। নিজেদের আধিপত্য ও শোষণের স্বার্থে পাশ্চাত্যও ইসলামী কর্তৃত্বের সাথে একটা আপোস বা সমঝোতা করে চলতে পারে। পাশ্চাত্য তার পুঁজিবাদী বাজার ব্যবস্থা রক্ষার স্বার্থে যেটুকু পরিবর্তন ঘটাবার প্রয়োজন মনে করে সেটুকু ঘটায়। তবে এই পরিবর্তন ঘটে ইসলামের সামাজিক ভিত্তিভূমির উপর দাঁড়িয়ে বা মূল ভিত্তিকে অক্ষত রেখে। ফলে আধুনিক বিশ্ব ব্যবস্থায় প্রবেশের পর ইসলামী সমাজ সম্মুখীন হয় এক অস্বাভাবিক দ্বন্দ্বের। তার মাথা বা নেতৃত্ব প্রভাবিত হয় আধুনিক সভ্যতার নানান উপকরণ দ্বারা, শিক্ষা ও চেতনা দ্বারা, অথচ তার শরীর তথা নীচ তলার আমজনতা রয়ে যায় ধর্মাচ্ছন্ন তথা ইসলামের পশ্চাৎপদতা, ভাগ্যবাদ, হিং&macr;্রতা, নারী বিদ্বেষ এবং নিরঙ্কুশ একত্ববাদী বা একনায়কী আধিপত্যবাদী চেতনার অধীন। ইসলাম না পারে আধুনিক সভ্যতাকে আত্মস্থ করতে, না পারে তাকে প্রত্যাখ্যান করতে। ইসলাম পাশ্চাত্যের আঘাতকে প্রতিহত করতে অক্ষম। কিন্তু ভিতর থেকে স্বাধীন, উন্নত ও মানবিক সভ্যতার উত্থানকে আঘাত এবং নস্যাৎ করতে তা সক্ষম। এমন অবস্থায় পাশ্চাত্যের উপনিবেশবাদী, নয়া উপনিবেশবাদী এবং সা&curren;্রাজ্যবাদী বিশ্ব ব্যবস্থা রক্ষায় ইসলাম হয়ে দেখা দিয়েছে এক প্রচণ্ড শক্তিশালী হাতিয়ার।<br />\r\nআমরা বঙ্গ এবং ভারতবর্ষে ইসলামকে পাশ্চাত্য সা&curren;্রাজ্যবাদের অনুচর ও সহযোগী রূপে ভূমিকা পালন করতে দেখেছি। অষ্টাদশ শতাব্দীর মাঝামাঝি সময়ে (১৭৫৭ খ্রী:) বাংলার নবাব সিরাজ উদ্দৌলার প্রধান সেনাপতি মীরজাফর কর্তৃক ইংরেজ সেনাপতি ক্লাইভের নিকট নবাবী রাষ্ট্র ক্ষমতা হস্তান্তর ছিল লুণ্ঠনজীবী ইসলামের এই নবরূপে আত্মপ্রকাশ। সেই সময় চলছিল পাঞ্জাবে শিখ এবং মহারাষ্ট্রে মারাঠা উত্থান। এছাড়া বিভিন্ন গণ-বিদ্রোহে মোগল সা&curren;্রাজ্য ভেঙ্গে পড়ছিল। উপমহাদেশের বিভিন্ন জাতির জনগণের জাগরণের কালে যখন বহিরাগত মুসলিম শাসক শ্রেণী বুঝেছে যে বাংলার এবং ভারতবর্ষের হিন্দু ও অন্যান্য অমুসলিম জনগণের উত্থানকে ঠেকানো যাবে না তখন তাদের প্রধান অংশ মীর জাফরের নেতৃত্বে ইংরেজদের সঙ্গে হাত মিলিয়ে বাংলা এবং ভারতবর্ষকে নূতন পর্যায়ের পরাধীনতায় নিক্ষেপ করেছে। ১৭৫৭ সালে পলাশীর প্রান্তরে যে যুদ্ধ হয় তাতে নবাব সিরাজউদ্দৌলার সৈন্য সংখ্যা ছিল প্রায় ৬৭ হাজার। এর মধ্যে আনুমানিক মাত্র ১৭ হাজার সৈন্য নবাবের পক্ষে যুদ্ধ করেছিল। বাকী ৫০ হাজার সৈন্য প্রধান সেনাপতি মীর জাফরের অধীনে দাঁড়িয়ে থাকল। এটা ছিল কৌশলে ইংরেজদের প্রতি সমর্থন। অর্থাৎ বাংলা-বিহার-উড়িষ্যায় ইসলামী যে রাষ্ট্র ছিল তার চার ভাগের তিন ভাগ ছিল ব্রিটিশদের নিকট রাষ্ট্রের প্রকৃত ক্ষমতা হস্তান্তরের পক্ষপাতী। এই কারণে মীর জাফর নিজে নবাব সিরাজউদ্দৌলাকে উৎখাত করে ক্ষমতা দখল করলেন না। সেনাবাহিনীর সংখ্যাগরিষ্ঠ সৈন্য তার পক্ষে থাকায় সেটা তিনি চাইলে করতে পারতেন। কিন্তু তার উদ্দেশ্য ক্ষমতা দখল ছিল না, বরং মূল রাষ্ট্র ক্ষমতা ইংরেজদের হাতে হস্তান্তর করে ক্ষমতার গৌণ অংশীদার হিসাবে টিকে থাকা। সুতরাং পলাশীর রণক্ষেত্রে যুদ্ধের নাটক করা হল। এভাবে ইসলামী সা&curren;্রাজ্যবাদ যার মর্মমূলে আছে আরব সা&curren;্রাজ্যবাদ তার জায়গা নিল ব্রিটিশ সা&curren;্রাজ্যবাদ।<br />\r\nমীর জাফর পরবর্তী মুসলিম নেতৃত্বের ভূমিকাকে তার ভূমিকা থেকে ভিন্নভাবে দেখার উপায় নাই। যদিও ১৮৫৭-তে মুসলিম নেতৃত্বের একাংশ হিন্দুদের সঙ্গে হাত মিলিয়ে একটা বিদ্রোহ করেছিল তা ছিল পুরাতন নেতৃত্বের শেষ প্রয়াস। বস্তুত ভারতবর্ষে ইসলামের ভূমিকা প্রধানত বিদেশী ব্রিটিশ শাসনের সঙ্গে সহযোগিতামূলক। হিন্দু-মুসলিম দ্বন্দ্ব, বর্ণজাতিভেদের কারণে হিন্দু সমাজের শতধা বিভক্তি ও কাপুরুষতা, হিন্দু সমাজের নেতৃত্বেরও অযোগ্যতা ইত্যাদি কারণ ভারতবর্ষে ব্রিটিশ শাসনকে প্রায় দুইশত বৎসর স্থায়ী রাখায় সাহায্য করে। ওয়াহাবী আন্দোলনের মত কিছু ধর্মান্ধ ব্রিটিশ বিরোধী আন্দোলন হলেও এসব কাফের বিরোধী আন্দোলনের শেষ পরিণতি হল হিন্দু বিরোধী পাকিস্তান আন্দোলন, যা বাংলা এবং ভারতবর্ষকে বিভক্ত করে। আর বঙ্গের বিভক্তির মধ্য দিয়ে এই উপমহাদেশে ইসলামের প্রকৃত ভূমিকা আর একবার প্রকাশ পেল।<br />\r\nভারতবর্ষে মুসলমান সংখ্যালঘু হওয়ায় সেখানে তার স্বতন্ত্র ইসলামী রাষ্ট্র চাওয়ার ব্যাপারটা বোধগম্য হতে পারে। কিন্তু বঙ্গে সংখ্যাগুরু হওয়া সত্ত্বেও কেন ইসলামী নেতৃত্ব মুসলমানের আলাদা রাষ্ট্র হিসাবে পাকিস্তান চাইবে? অর্থাৎ মুসলমান নেতৃত্ব বাঙ্গালী হিসাবে তার রাষ্ট্র পেতে চায় নাই, বরং মুসলমান হিসাবে তার রাষ্ট্র পেতে চেয়ে ছিল, যে রাষ্ট্র হাতে পেয়ে তার লুণ্ঠনজীবিতার ঐতিহ্যকে রক্ষা করবে। সেটা তারা &rsquo;৪৭ থেকে আজ অবধি রক্ষা করেছে। &rsquo;৪৭ থেকে আজ পর্যন্ত বাংলাদেশে হিন্দু জনসংখ্যার হ্রাস-চিত্র দেখলেও এটা বুঝতে কষ্ট হয় না। এই লুণ্ঠনজীবীরা লুণ্ঠনের জন্য যে কোন কৌশল নিতে অভ্যস্ত। সুতরাং সেকিউলারিজম, জাতীয়তাবাদ ইত্যাদির বুলি আওড়াতেও তাদের অসুবিধা হয় না। &rsquo;৭১-এ পাকিস্তানের কাঠামো ভেঙ্গে বাংলাদেশের অভ্যুদয়ও ইসলামের এই লুণ্ঠনবৃত্তি ও হিং&macr;্র গুহামানব সুলভ কর্মধারার অবসান ঘটাতে পারে নাই। কারণ বাংলাদেশ ইসলামী রাষ্ট্র পাকিস্তানের বিরুদ্ধে আন্দোলনের মধ্য দিয়ে জন্ম নিলেও ইসলামের অধীনতা থেকে মুক্তির বিপ্লবে রূপ নিতে পারে নাই।<br />\r\nআর পাকিস্তান? পাকিস্তান রাষ্ট্রটির দিকে তাকালে বোঝা যায় এটা সেখানকার এবং উপমহাদেশের জনগণকে কী দিয়েছে। ব্রিটিশ চলে যাবার পর এটা হয়েছে সা&curren;্রাজ্যবাদী বিশ্ব ব্যবস্থা রক্ষার অন্যতম গুরুত্বপূর্ণ দুর্গ। এটা হয়েছে একদিকে পাকিস্তানের জনগণের উপর দাসত্বের ব্যবস্থা রক্ষার এক পাহারাদার রাষ্ট্র, অপরদিকে উপমহাদেশের অন-ইসলামী জনগণের উত্থানের বিরুদ্ধে সা&curren;্রাজ্যবাদের এক দুর্গ। গণতন্ত্রের বুলি আওড়ে মার্কিন যুক্তরাষ্ট্র তাদেরই ভাষায় পৃথিবীর &lsquo;বৃহত্তম গণতন্ত্র&rsquo; ভারতের বিরুদ্ধে পাকিস্তানকে যত রকমভাবে সম্ভব ব্যবহার করেছে।<br />\r\nআফগানিস্তানের বাদশাহ্ আমানুল্লাহ তার দেশের আধুনিকায়নের যে উদ্যোগ গ্রহণ করেন ইসলামবাদী শক্তিগুলিকে ব্যবহার ক&rsquo;রে তাকে কীভাবে তৎকালীন ভারতের ব্রিটিশ সরকার নস্যাৎ করেছিল তা আমরা জানি। একইভাবে ব্রিটিশ সা&curren;্রাজ্যবাদীদের উত্তরাধিকারী মার্কিন সা&curren;্রাজ্যবাদীরাও কমিউনিস্টদের নেতৃত্বে আফগানিস্তানের আধুনিকায়নের উদ্যোগকে পাকিস্তানের সাহায্যে এবং আফগানিস্তানে জঙ্গি জিহাদীদের উত্থান ঘটিয়ে নস্যাৎ করে। এই হচ্ছে ইসলামী পৃথিবীতে বর্বর ও ধর্ষক গুহামানবদের রক্ষায় পাশ্চাত্যের উদার গণতন্ত্রীদের কিছু কর্মকাণ্ডের নমুনা।<br />\r\nকিন্তু ইসলামের বৈশিষ্ট্য অনুযায়ী তার লাগবে শত্র&ldquo;, যার বিরুদ্ধে তা জিহাদ বা ইসলামী যুদ্ধ করবে। এই জিহাদে জয়ী হলে তার লাভ হবে শত্র&ldquo;র সম্পদ, ভূমি, সম্পত্তি এবং নারী। যুদ্ধে মৃত্যু হলে তার হবে অনন্ত বেহেশ্ত্, যেখানে তা পাবে অনন্ত জীবন, অফুরন্ত মদের নদী, ফল-মূল, সুস্বাদু খাদ্য, বহু সংখ্যক অনন্ত যৌবনা নারী এবং অগণন বালক। ইসলামের আদর্শের প্রকৃত ও সাহসী অনুসারীরা সেই যুদ্ধ বা জিহাদ এখন নূতন উদ্যমে পরিচালনা করতে শুরু করেছে মধ্যপ্রাচ্য এবং অন্যান্য ইসলামী দেশ থেকে ইউরোপ এমনকি খোদ আমেরিকা পর্যন্ত পৃথিবীর বিস্তীর্ণ অঞ্চল জুড়ে। তারা বিশ্বাস করে মুহাম্মদ যেমন বিজয়ী হয়েছিলেন তেমন মুহাম্মদের পথ অনুসরণ করে তারাও বিজয়ী হবে ।<br />\r\nকিন্তু এই যুদ্ধে ইসলামের শক্তি যে বিজয়ী হতে পারবে না সেটা পাশ্চাত্য খুব ভালভাবে জানে। তবে এটা পরমাণু প্রযুক্তির যুগ। সব প্রযুক্তি উদ্ভাবন করতে হয় না। কোন প্রযুক্তিই মানুষ চিরকাল নিজের একচেটিয়া বা গোপন করে রাখতে পারে না। সেটা ক্রয় করে হোক, চুরি করে হোক কিংবা অন্যভাবে হোক আগে বা পরে সংগ্রহ করা যেতে পারে। ইতিমধ্যে পাকিস্তান পরমাণু বোমার অধিকারী হয়েছে। পরমাণু প্রযুক্তি যে পাকিস্তান রাষ্ট্রের হাত থেকে প্রকৃত জিহাদি শক্তির হাতে যাবে না এমন নিশ্চয়তা কোথায়?<br />\r\nতবে এখন পর্যন্ত ইসলামের লালন ও পৃষ্ঠপোষকতাতেই পাশ্চাত্যের লাভ। জিহাদি না হলে আমাদের মত দেশগুলির জন্য ইসলাম তার খুব পছন্দের। কারণ ইসলাম ইসলামী পৃথিবীকে পাশ্চাত্যের অধীনস্থ ও দয়ার ভিখারী করে রাখছে। প্রকৃতপক্ষে আধুনিক যুগে বিশ্ব বাজার ব্যবস্থার বাইরে যাবার উপায় কোন দেশ বা সমাজেরই নাই। এমন কি সেই ক্ষমতা জিহাদি মুসলামানদেরও নাই। সভ্যতার প্রতি বৈরী আফগান তালেবানরাও আধুনিক অস্ত্র ও প্রযুক্তি নিয়েই সভ্যতাকে আঘাত করতে বাধ্য হয়েছে এবং হচ্ছে। এটা হচ্ছে ইসলামের এক ট্র্যাজিডি। সভ্যতা নির্মাণে অক্ষম এবং সভ্যতার প্রতি বৈরী ইসলাম আধুনিক ও উন্নত পৃথিবীর দয়ার উপর নির্ভর করেই এবং এখন পর্যন্ত বিশেষত পাশ্চাত্যের প্রয়োজনেই বেঁচে আছে এবং যতদিন তাকে পাশ্চাত্যের প্রয়োজন হবে ততদিন পাশ্চাত্য তাকে বাঁচিয়ে রাখার চেষ্টা করবে। তবে বেশি বাড়াবাড়ি ক&rsquo;রে পাশ্চাত্যের জন্য সমস্যা সৃষ্টি করলে তাকে শায়েস্তা করবে। এ ক্ষেত্রে লাদেনের দৃষ্টান্ত দেওয়া যায়। আমেরিকার সৃষ্টি লাদেন বধ আমেরিকাই করেছে। এখন ইসলামিক স্টেট (আইএস) নিয়ে ইরাক-সিরিয়ায়ও মার্কিন যুক্তরাষ্ট্রের একই নাটক মঞ্চস্থ হচ্ছে। আমেরিকার নীতি পরিষ্কার&Ntilde;সর্প হইয়া দংশন করো, ওঝা হইয়া ঝাড়ো। আমাদের মত দেশগুলোয় ইসলাম ও ইসলামী চেতনা লালন ও রক্ষার জন্য সম্ভাব্য সবই তা করবে। আবার ইসলাম যদি তার চরিত্র অনুযায়ী ভিতর বাদ দিয়ে বাহিরেও গিয়ে তাকে আঘাত করার দুঃসাহস করে তবে তাকে শাস্তি দিয়ে বুঝাবে যে বেশি বাড়াবাড়ি করা ঠিক নয়। উদ্দেশ্য একটাই, ইসলামী পৃথিবীকে নিজেদের নিয়ন্ত্রণাধীনে রেখে দেওয়া।<br />\r\nফলে শেষ বিচারে আধুনিক সভ্যতায় ইসলাম মুসলমানদেরকে দিয়েছে এমন এক কারাগার যেখানে তারা পাশ্চাত্যের দাস হয়ে থাকতে বাধ্য হবে। প্রকৃতপক্ষে আধুনিক বিশ্ব ব্যবস্থায় পুঁজিবাদ, গণতন্ত্র ইত্যাদি যা-ই করা যাক সভ্যতা ইসলামের জন্য এমন এক বন্দিশালা তৈরি করেছে যেখানে আবদ্ধ মুসলমানরা বন্দী হয়ে থেকে পাশ্চাত্যের বিভিন্ন প্রয়োজন পূরণে বাধ্য হবে। সভ্যতা নির্মাণে পাশ্চাত্যের সেই আদি উদ্যম ও প্রেরণার যুগ শেষ হয়েছে। এখন পাশ্চাত্যের অধিবাসীরা তাদের পূর্বপুরুষদের মত আবিষ্কার, উদ্ভাবন ও নির্মাণের জন্য জীবনপাতের পরিবর্তে ভোগ ও আনন্দ-ফূর্তিতে গা ভাসাতেই অনেক বেশি আগ্রহী। সুতরাং কষ্টসাধ্য শ্রম করার জন্য তাদের দরকার প্রাচ্যের সস্তা শ্রমশক্তি। এ ছাড়া আরও অনেক বেশি প্রয়োজন মেধার, যা আজকের মেধাভিত্তিক সভ্যতার মূল বুনিয়াদ। সস্তা দেহশ্রমের পাশাপাশি এই মেধার অফুরন্ত সরবরাহ ভাণ্ডার হয়ে আছে ইসলামী পৃথিবী। এখান থেকে মেধাবীরা গিয়ে পাশ্চাত্য সভ্যতার চাকা সচল রাখার কাজে নিজেদের নিয়োজিত রাখছে। মেধাশক্তিই পৃথিবীকে বদলায়। সুতরাং ইসলামী পৃথিবীতে তাদের অভাবে অন্ধকার দীর্ঘতর হচ্ছে মাত্র।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">মেধাবী ও উন্নত চিন্তার মানুষদের জন্য এই সমাজে দমবন্ধকর পরিস্থিতি সৃষ্টি হয়ে থাকে। আধুনিক শিক্ষা ও প্রযুক্তির প্রভাবে যে উন্নত মেধা তৈরি হচ্ছে তারও বিরাট অংশ উন্নততর ও মুক্ত জীবনের আকাক্সক্ষায় পাশ্চাত্যে চলে যায়। হিং&macr;্র ও বর্বর বান্দাদের আধিপত্য, দুর্নীতি, সন্ত্রাস, সহিংসতা, সামাজিক ও রাজনৈতিক স্বৈরাচার, ইত্যাদির চাপে উন্নততর চিন্তা বা আকাক্সক্ষার বেশির ভাগ মেধাবী মানুষ এখানে টিকতে না পেরে পাশ্চাত্যে চলে যেতে চায়। এখানে তার পরেও মেধার বিকাশ সম্ভাবনা নিয়ে যারা থাকে ইট চাপা ঘাসের মত তারাও এক সময় লালনের অভাবে এবং বান্দা সমাজের চাপে শুকিয়ে মরে যায়। এভাবে ইসলামী সমাজগুলি যেটা হারায় সেটা হচ্ছে মেধাশক্তির বিকাশ সম্ভাবনা, যা আজকের সভ্য সমাজ ও রাষ্ট্র নির্মাণ ও রক্ষার জন্য সবচেয়ে বেশি প্রয়োজনীয়। এভাবে ইসলামী পৃথিবী হয়েছে পাশ্চাত্যের জন্য শ্রমশক্তি এবং ততোধিক গুরুত্বপূর্ণ মেধাশক্তি সরবরাহের জন্য এক অফুরন্ত সরবরাহ ভাণ্ডার। এই ভাণ্ডার রক্ষার জন্যও আজ পাশ্চাত্যের আধিপত্যবাদী শক্তিগুলি প্রাচ্যে ইসলাম রক্ষায় এত তৎপর।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এভাবে ইসলাম এবং পশ্চিমা সা&curren;্রাজ্যবাদ একসূত্রে গাঁথা। সুতরাং আজ আমাদের মত দেশে সা&curren;্রাজ্যবাদ বিরোধী লড়াই এবং ইসলাম বিরোধী লড়াই এক এবং অভিন্ন এক লড়াই।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">নূতন বিশ্ববিপ্লবের প্রয়োজন</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এক সময় ভিতর থেকে ইসলামের বিরুদ্ধে কার্যকরভাবে লড়াই করা সম্ভব ছিল না। কারণ শান্তির ধর্ম ইসলাম ও তার নবীর যে কোন ধরনের সমালোচনা হিং&macr;্র গুহামানবদের হাতে মৃত্যু ঘটাতে পারত। ইসলাম সম্পর্কে যাদের জ্ঞান আছে তারা হয়ত জানেন যে, সমালোচক কিংবা নিন্দাকারীরা ইসলাম পরিত্যাগকারী তথা মুরতাদ হিসাবে বিবেচিত হবে এবং নবীর নির্দেশ অনুযায়ী তাদের জন্য একমাত্র শাস্তি মৃত্যু। ইসলামের নবীর সামান্য সমালোচনা যেখানে মৃত্যুর কারণ হতে পারে সেখানে ইসলাম ধর্মের সত্য নির্ভর ও নিরপেক্ষ আলোচনা-সমালোচনা হবে কী করে? বস্তুত গোটা ধর্মটাই সন্ত্রাসের ধর্ম।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কিন্তু বিজ্ঞান ও প্রযুক্তির সাম্প্রতিক বিকাশ ইসলামকে অসহায় করে ফেলছে। বিশেষত ইন্টারনেট প্রযুক্তির উদ্ভাবন ও ব্যবহার এখন বিশ্বব্যাপী ইসলাম ও তার নবীর চুলচেরা বিশ্লেষণ ও সমালোচনার পথ খুলে দিয়েছে। ইন্টারনেটকে গুহামানবরা আক্রমণ কিংবা হত্যা করবে কীভাবে? বরং ইসলামবাদীরা নিজেদের গুহামানবীয় ধর্মকে বাঁচাবার চেষ্টায় মরীয়া হয়ে এখন ইন্টারনেটের আশ্রয় নিয়েছে। কিন্তু এতকালের অবরুদ্ধ সত্যের বাঁধভাঙ্গা বন্যার আঘাতে ইসলামের ব্যূহ তছনছ হয়ে যেতে আর বেশি দেরি নাই। ইন্টারনেটের বাইরেও মুদ্রণ ও প্রকাশ্য আলোচনা ইত্যাদি বিভিন্ন পদ্ধতিতে ইসলাম ও তার নবীর স্বরূপ প্রকাশ শুরু হয়েছে। এ মহাপ্লাবনকে রোধ করবে এমন সাধ্য কার?<br />\r\nবস্তুত প্রশ্নটা শুধু মানবিকতার নয়, প্রতিযোগিতার পৃথিবীতে আমরা মর্যাদার সঙ্গে টিকে থাকতে পারব কিনা তারও। আজকের যুগে দেহশক্তি, পাশবিক আদর্শ এবং ধূর্ত ও হিং&macr;্র গুহামানবীয় বুদ্ধিবৃত্তি নিয়ে টিকে থাকা সম্ভব নয়, মর্যাদার সঙ্গে টিকে থাকার তো প্রশ্নই উঠে না। ইসলামের কারণে পাশ্চাত্যের যে দাসত্ব আমরা করে চলেছি সেটা মেনে নিয়ে মানবেতর জীবন যাপন করব নাকি সভ্যতা ও উন্নয়নে তাদের সমকক্ষ হয়ে বিশ্বসভায় মাথা উঁচু করে দাঁড়াব সেই সিদ্ধান্ত আজ আমাদেরকে নিতে হবে। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">মানুষ আজ পৃথিবীর সীমানা ছাড়িয়ে নাক্ষত্রিক সভ্যতার যুগে প্রবেশ করছে। পৃথিবীর উন্নত দেশগুলি আজ আকাশ জয়ের প্রতিযোগিতায় নেমেছে। আর কিছুকালের মধ্যে মানুষ গ্রহ-গ্রহান্তরে বাস করবে। আর আমরা কি সেসব শুধু চেয়েই দেখব? নাকি আমরাও সেই আকাশ জয়ের প্রতিযোগিতায় নামব? যদি সেই প্রতিযোগিতায় আমরা নামতে চাই তবে আমাদের সে ক্ষেত্রে সবচেয়ে বড় বাধা হিসাবে ইসলামকে চিনতে হবে। অর্থাৎ ইসলাম শুধু মানবতার শত্র&ldquo; নয়, উপরন্তু ইসলামী পৃথিবীর উন্নয়নেরও সবচেয়ে বড় শত্র&ldquo;। শুধু বাংলাদেশের নয়, বিশাল ইসলামী পৃথিবীরও যে অপার সম্ভাবনা চাপা পড়ে আছে তাকে মুক্ত করা ইসলামী পৃথিবীর মানুষ হিসাবে আমাদের করণীয়। এটি দাবি করে এক বিরাট আদর্শিক এবং সেই সঙ্গে সামাজিক, সাংস্কৃতিক ও রাজনৈতিক বিপ্লব। শুধু বাংলাদেশ নয়, উপরন্তু সমগ্র ইসলাম অধিকৃত পৃথিবী তার মুক্তির প্রয়োজনে এই বিপ্লবের জন্য অধীর আগ্রহে অপেক্ষা করছে। অর্থাৎ আজ আমরা বিশ্ব বিপ্লবের আক নবতর অধ্যায়ে প্রবেশ করতে যাচ্ছি।<br />\r\nরচনাঃ৯ অক্টোবর- ৮ নভেম্বর, ২০১৪</span></p>\r\n','',1,15,'2016-01-07 03:13:33',1,'2016-01-07 03:18:24',1,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452158304_shovvota.png'),(21,'বাংলা ভাষা আমাদের মূল জাতীয় সম্পদ: বাংলা মাধ্যম ও ইংলিশ ভার্সন । আবুল কাসেম ফজলুল হক','বাংলা-ভাষা-আমাদের-মূল-জাতীয়-সম্পদ:-বাংলা-মাধ্যম-ও-ইংলিশ-ভার্সন-।-আবুল-কাসেম-ফজলুল-হক','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলা ভাষার উন্নতির জন্য আজকাল যে উদগ্রীবতা প্রকাশ করা হয়, তাকে অনেক সময় গাছের গোড়া কেটে আগায় পানি ঢালার মতো ব্যাপার বলে মনে হয়। কার্যক্ষেত্রে দেখা যায়, বিরল ব্যতিক্রম ছাড়া শিক্ষিত বিত্তশালী লোকদের প্রায় সকলকেই বাংলা ছেড়ে ইংরেজি গ্রহণে তৎপর।<br />\r\nবাংলাদেশে প্রাথমিক ও মাধ্যমিক পর্যায়ের শিক্ষা নানা ধারা-উপধারায় বিভক্ত। শিক্ষাব্যবস্থায় উচ্চশিক্ষাপূর্ব পর্যায়ে এত ধারা-উপধারা পৃথিবীর আর কোনো রাষ্ট্রে আছে বলে আমাদের জানা নেই। নানা ধরনের মাদ্রাসা, নানা ধরনের স্কুল, নানা ধরনের ইংলিশ মিডিয়াম স্কুল, আরো কত কী! এই শিক্ষাব্যবস্থা বাংলা ভাষার ও বাংলাদেশের স্বাভাবিক বিকাশের পরিপন্থী। রাষ্ট্র গঠনের জন্য যে জাতীয় ঐক্য অপরিহার্য, এই শিক্ষাব্যবস্থা দ্বারা তাকে বিনষ্ট করা হচ্ছে।<br />\r\nপাঠ্যপুস্তক বোর্ড (ঘ.ঈ.ঞ.ই) যে ধারার শিক্ষার্থীদের পাঠ্যপুস্তকের জোগান দেয়, সেটাকে বলা হয় বাংলাদেশের শিক্ষাব্যবস্থার মূল ধারা বা সধরহ ংঃৎবধস. ২০০২ সালে সরকার এই মূলধারার বা সধরহ ংঃৎবধস-এর ভেতরে ইংলিশ ভার্সন বলে একটি নতুন ধারা প্রবর্তন করে । ২০০৭ সালে জরুরি অবস্থার সরকার সেই ধারাকে বেগবান করে। সেই বেগ আজো অব্যাহত আছে। ইংলিশ ভার্সনের জন্য বৃহত্তর শিক্ষিতসমাজের ভেতর থেকে-যে কোনো দাবি ছিল, তা নয়। এই ব্যবস্থা ভালো কি মন্দ, তা নিয়ে আজ পর্যন্ত কোনো আলোচনা-সমালোচনাও হয়নি।<br />\r\nবর্তমান আলোচনা ইংলিশ ভার্সন নিয়ে, ইংরেজি শেখা নিয়ে নয়। ইংরেজি শেখার ব্যবস্থা বাংলাদেশে উন্নত করতে হবে। শিক্ষাব্যবস্থার উন্নতির সঙ্গে সঙ্গে ইংরেজি শেখার ব্যবস্থাও বাস্তবসম্মত করতে হবে। কিন্তু ইংরেজির জন্য মাতৃভাষার স্থান ছেড়ে দেওয়া যাবে না। ইংলিশ ভার্সন বাঙালি সন্তানদের ইংরেজি শেখার কোনো সুব্যবস্থা নয়। বাংলাদেশে ইংরেজিকে প্রাথমিক ও মাধ্যমিক শিক্ষার মাধ্যম করা ভুল। জ্ঞান-বিজ্ঞান ও শিল্প-সাহিত্যে আমাদের সৃষ্টিশীলতা ইংরেজিকে দ্বিতীয় ভাষা রূপে রেখেই সম্ভব হবে। ইংরেজিকে প্রথম ভাষা করা হলে বাঙালির সৃষ্টিশক্তি দুর্বল হবে&Ntilde;সৃষ্টিশীলতা বিনষ্ট হবে। বাংলাদেশে বাংলা ভাষার অর্থনৈতিক ভিত্তিকে রক্ষা করে সম্প্রসারিত করতে হবে।<br />\r\nপাঠ্যপুস্তক বোর্ড কেবল বাংলা মাধ্যমে প্রাথমিক ও মাধ্যমিক পর্যায়ের পাঠ্যপুস্তক প্রণয়ন ও প্রকাশ করত। ইংলিশ ভার্সন প্রবর্তনের পর বোর্ড বাংলা মাধ্যমের পাশাপাশি ইংরেজি অনুবাদেও সব পাঠ্যপুস্তক প্রকাশ করতে থাকে। আদেশ জারি করা হয় যে, কোনো স্কুল ইচ্ছা করলে বাংলা মাধ্যমের পাশাপাশি স্বতন্ত্র শাখা হিসেবে ইংলিশ ভার্সন খুলতে পারে। সরকারের আদেশ অনুসরণ করে ঢাকা ও অন্যান্য শহরে প্রাথমিক ও মাধ্যমিক পর্যায়ের শিক্ষা প্রতিষ্ঠানগুলোতে ইংলিশ ভার্সন ধীরে ধীরে চালু হতে থাকে। তখন থেকে সারাদেশে শুধুই ইংলিশ ভার্সন নিয়ে নতুন নতুন প্রাথমিক বিদ্যালয় ও মাধ্যমিক বিদ্যালয় বাংলা ভাষার উন্নতির জন্য আজকাল যে উদগ্রীবতা প্রকাশ করা হয়, তাকে অনেক সময় গাছের গোড়া কেটে আগায় পানি ঢালার মতো ব্যাপার বলে মনে হয়। কার্যক্ষেত্রে দেখা যায়, বিরল ব্যতিক্রম ছাড়া শিক্ষিত বিত্তশালী লোকদের প্রায় সকলকেই বাংলা ছেড়ে ইংরেজি গ্রহণে তৎপর।<br />\r\nবাংলাদেশে প্রাথমিক ও মাধ্যমিক পর্যায়ের শিক্ষা নানা ধারা-উপধারায় বিভক্ত। শিক্ষাব্যবস্থায় উচ্চশিক্ষাপূর্ব পর্যায়ে এত ধারা-উপধারা পৃথিবীর আর কোনো রাষ্ট্রে আছে বলে আমাদের জানা নেই। নানা ধরনের মাদ্রাসা, নানা ধরনের স্কুল, নানা ধরনের ইংলিশ মিডিয়াম স্কুল, আরো কত কী! এই শিক্ষাব্যবস্থা বাংলা ভাষার ও বাংলাদেশের স্বাভাবিক বিকাশের পরিপন্থী। রাষ্ট্র গঠনের জন্য যে জাতীয় ঐক্য অপরিহার্য, এই শিক্ষাব্যবস্থা দ্বারা তাকে বিনষ্ট করা হচ্ছে।<br />\r\nপাঠ্যপুস্তক বোর্ড (ঘ.ঈ.ঞ.ই) যে ধারার শিক্ষার্থীদের পাঠ্যপুস্তকের জোগান দেয়, সেটাকে বলা হয় বাংলাদেশের শিক্ষাব্যবস্থার মূল ধারা বা সধরহ ংঃৎবধস. ২০০২ সালে সরকার এই মূলধারার বা সধরহ ংঃৎবধস-এর ভেতরে ইংলিশ ভার্সন বলে একটি নতুন ধারা প্রবর্তন করে । ২০০৭ সালে জরুরি অবস্থার সরকার সেই ধারাকে বেগবান করে। সেই বেগ আজো অব্যাহত আছে। ইংলিশ ভার্সনের জন্য বৃহত্তর শিক্ষিতসমাজের ভেতর থেকে-যে কোনো দাবি ছিল, তা নয়। এই ব্যবস্থা ভালো কি মন্দ, তা নিয়ে আজ পর্যন্ত কোনো আলোচনা-সমালোচনাও হয়নি।<br />\r\nবর্তমান আলোচনা ইংলিশ ভার্সন নিয়ে, ইংরেজি শেখা নিয়ে নয়। ইংরেজি শেখার ব্যবস্থা বাংলাদেশে উন্নত করতে হবে। শিক্ষাব্যবস্থার উন্নতির সঙ্গে সঙ্গে ইংরেজি শেখার ব্যবস্থাও বাস্তবসম্মত করতে হবে। কিন্তু ইংরেজির জন্য মাতৃভাষার স্থান ছেড়ে দেওয়া যাবে না। ইংলিশ ভার্সন বাঙালি সন্তানদের ইংরেজি শেখার কোনো সুব্যবস্থা নয়। বাংলাদেশে ইংরেজিকে প্রাথমিক ও মাধ্যমিক শিক্ষার মাধ্যম করা ভুল। জ্ঞান-বিজ্ঞান ও শিল্প-সাহিত্যে আমাদের সৃষ্টিশীলতা ইংরেজিকে দ্বিতীয় ভাষা রূপে রেখেই সম্ভব হবে। ইংরেজিকে প্রথম ভাষা করা হলে বাঙালির সৃষ্টিশক্তি দুর্বল হবে&Ntilde;সৃষ্টিশীলতা বিনষ্ট হবে। বাংলাদেশে বাংলা ভাষার অর্থনৈতিক ভিত্তিকে রক্ষা করে সম্প্রসারিত করতে হবে।<br />\r\nপাঠ্যপুস্তক বোর্ড কেবল বাংলা মাধ্যমে প্রাথমিক ও মাধ্যমিক পর্যায়ের পাঠ্যপুস্তক প্রণয়ন ও প্রকাশ করত। ইংলিশ ভার্সন প্রবর্তনের পর বোর্ড বাংলা মাধ্যমের পাশাপাশি ইংরেজি অনুবাদেও সব পাঠ্যপুস্তক প্রকাশ করতে থাকে। আদেশ জারি করা হয় যে, কোনো স্কুল ইচ্ছা করলে বাংলা মাধ্যমের পাশাপাশি স্বতন্ত্র শাখা হিসেবে ইংলিশ ভার্সন খুলতে পারে। সরকারের আদেশ অনুসরণ করে ঢাকা ও অন্যান্য শহরে প্রাথমিক ও মাধ্যমিক পর্যায়ের শিক্ষা প্রতিষ্ঠানগুলোতে ইংলিশ ভার্সন ধীরে ধীরে চালু হতে থাকে। তখন থেকে সারাদেশে শুধুই ইংলিশ ভার্সন নিয়ে নতুন নতুন প্রাথমিক বিদ্যালয় ও মাধ্যমিক বিদ্যালয় স্থাপিতও হতে থাকে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">২০১০ সালের শিক্ষানীতিতে কিংবা ২০১১ সালের সংবিধানের পঞ্চদশ সংশোধনে ইংলিশ ভার্সন নিয়ে কিছুই বলা হয়নি। এখন দেখা যাচ্ছে, সারা দেশে সচ্ছল অভিভাবকরা শিশু-কিশোরদের প্রাতিষ্ঠানিক শিক্ষার জন্য দ্রুতগতিতে ইংলিশ ভার্সনের দিকে ঝুঁকে যাচ্ছেন। বিষয়টিকে স্কুল সমূহের ও শিক্ষার্থীদের জন্য ঐচ্ছিক রাখা হয়েছে। শিক্ষিত সচ্ছল লোকদের ইচ্ছা এখন ইংরেজির দিকে। ইংরেজি শেখানোর বাণিজ্যিক কার্যক্রমও বেড়ে চলছে। সারা দেশে প্রতিবছর অধিক থেকে অধিকতর সংখ্যায় বিদ্যালয় ইংলিশ ভার্সন খুলে চলছে। গ্রামে ও শহরে কেবল ইংলিশ ভার্সন নিয়ে নতুন নতুন স্কুল প্রতিষ্ঠিতও হচ্ছে। যে ছেলে-মেয়েরা ইংলিশ ভার্সনে পড়ে, তাদের অনেকে বাড়িতেও ইংরেজিতেই কথাবার্তা বলে। &lsquo;ইংলিশ-পছন্দ মহল&rsquo; থেকে জনমনে ধারণা সৃষ্টি করা হয়েছে যে, বিশ্বায়নের যুগে ইংরেজি ছাড়া গতি নেই, বাংলার কোনো ভবিষ্যত নেই।<br />\r\nবিশ্বায়ন নামে যে কর্মধারা বিশ্বব্যাঙ্ক, আন্তর্জাতিক অর্থ তহবিল, বিশ্ব বাণিজ্য সংস্থা, ন্যাটো, যুক্তরাষ্ট্রের পররাষ্ট্র মন্ত্রণালয় চালিয়ে যাচ্ছে, তার সঙ্গে যুক্ত বুদ্ধিজীবীরা বাংলাদেশের স্বাধীনতা ও রাষ্ট্রীয় অস্তিত্বের চেয়ে বিশ্বায়নকে অনেক বেশি গুরুত্বপূর্ণ মনে করেন। বাংলাদেশে ১৯৮০-র দশক থেকে নিঃরাজনীতিকরণের কার্যক্রম চালানো হচ্ছে। বাংলাদেশকে স্বাধীন রাষ্ট্র রূপে না রেখে বিশ্বায়নে বিলীন করে দেওয়ার অঘোষিত কার্যক্রম চালু আছে। বাংলাদেশের উন্নয়ন-কার্যক্রম ও উন্নয়নচিন্তা পর্যবেক্ষণ করলে এটা বোঝা যায়।<br />\r\nবাংলাদেশে ব্রিটিশ সরকার কর্তৃক ব্রিটিশ কাউন্সিলের মাধ্যমে পরিচালিত ইংলিশ মিডিয়ামের শিক্ষাধারা (এ লেভেল, ও লেভেল) অব্যাহত আছে। তাদের পাঠ্যসূচি যুক্তরাজ্যের বাস্তবতায় তৈরি। অবস্থার চাপে তারা অল্প কিছু নম্বরের ইধহমষধফবংয ঝঃঁফরবং পাঠ্যসূচির অন্তর্ভুক্ত করেছে। যুক্তরাজ্যের ও যুক্তরাষ্ট্রের সরকার বাংলাদেশে সেট, টোফেল, আইইএলটিএস ইত্যাদি পরীক্ষা নিয়মিত নিচ্ছে এবং বাংলাদেশের তরুণ-তরুণীরা ঢাকায় ও আরো কয়েকটি শহরে বিরাট-সংখ্যায় সেগুলোতে অংশগ্রহণ করছে। বাণিজ্যিক ভিত্তিতে এগুলো চালানো হচ্ছে। যারা ইউরোপে আমেরিকায় যেতে চায়, বিশ্বায়নের কাজ করতে চায়, তাদের জন্য ইংলিশ ভার্সনের তুলনায় এ লেভেল, ও লেভেল অনেক ভালো। বাংলাদেশের দুর্বল শিক্ষাব্যবস্থায় ইংলিশ ভার্সনে গিয়ে ছেলেমেয়েরা ইংরেজি ভালো শিখতে পারছে না, বিষয়জ্ঞানও ভালো লাভ করছে না। মাদ্রাসা নিয়ে এখন উদ্বিগ্ন না হওয়া ভালো। কাল নিরবধি, পৃথ্বী বিপুলা। সার্বিক উন্নতির জন্য সময় পাওয়া যাবে&Ntilde;সুযোগও সৃষ্টি করা যাবে। শিক্ষাবাণিজ্য বন্ধ করা যাবে তখনই, যখন তা করার মতো সরকার প্রতিষ্ঠা করা যাবে। এখন পরিপূর্ণ গুরুত্ব দিয়ে কাজ করা দরকার শিক্ষার মূল ধারার ব্যবস্থাকে বাংলাদেশোপযোগী করার জন্য। এর জন্য বাংলা মাধ্যমকে উন্নত করা ও ইংলিশ ভার্সনকে পরিহার করা উচিত। সরকার যদি ইংলিশ ভার্সন বিলুপ্ত করে, তা হলে খুব ভালো কাজ করবে। ইংলিশ ভার্সন নিয়ে যে উৎসাহ দেখা যাচ্ছে তা চলতে থাকলে বাংলা ভাষা বাংলাদেশের রাষ্ট্রভাষা থাকবে না। আমাদের মতে, বাংলাদেশের আটানব্বই শতাংশ লোকের স্বার্থে বাংলাকে রাষ্ট্রভাষা রূপে রক্ষা করতে হবে, আর বাংলাদেশকেও রাষ্ট্র রূপে রক্ষা করতে হবে। বাংলা রাষ্টভাষা না থাকলে বাংলাদেশ রাষ্ট্র রূপে থাকবে না। বাংলাদেশকে এবং অন্য সব রাষ্ট্রকেও বিশ্বায়নবাদী নিগড় থেকে মুক্ত হযে সুষ্ঠু আন্তর্জাতিক সম্পর্কের মধ্যে উত্তীর্ণ হতে হবে।<br />\r\nযুক্তরাজ্য ও যুক্তরাষ্ট্র সারা দুনিয়ায় ইংরেজির প্রতিপত্তি বাড়ানোর জন্য সুদূরপ্রসারী পরিকল্পনা নিয়ে দুই শতাব্দী ধরে কাজ করে আসছে। সকল জাতিকে ইংরেজি শেখানো দুটি রাষ্ট্রেরই বিশ্ববিস্তৃত ব্যবসাও বটে। বিশ্বব্যাপী তাদের কর্তৃত্ব অনেকটাই নির্ভর করে বিশ্বব্যাপী ইংরেজি ভাষার প্রতিষ্ঠার উপর। বাংলার উন্নতির জন্য বাংলাদেশে আমাদের মৌসুমি হুজুগ ছাড়া আর কি আছে? আমরা নিজেরাই নিজেদেরকে রক্ষা করি না, বিদেশীদের কাছে বিকিয়ে দিই। আমাদের উন্নতি হবে কীভাবে? রাষ্ট্রভাষা হিসেবে বাংলাকে এবং রাষ্ট্র হিসেবে বাংলাদেশকে রক্ষা করতে হলে, উন্নত করতে হলে, বাংলার প্রতি, বাংলাদেশের প্রতি, জনগণের সক্রিয় আস্থা ও সক্রিয় ভালোবাসা লাগবে। এক্ষেত্রে জনগণের ভালোবাসাই প্রাণশক্তি। ইংরেজির প্রতি ভালোবাসা বাংলার চেয়ে বেশি হলে রাষ্ট্রভাষা হিসেবে বাংলা এবং রাষ্ট্র হিসেবে বাংলাদেশ রক্ষা পাবে কীভাবে?<br />\r\nইংলিশ ভার্সনের শিক্ষার্থীদের সংখ্যা বাংলা মাধ্যমের শিক্ষার্থীদের সংখ্যার তুলনায় এখনো অনেক কম। তবে এ সংখ্যা প্রতি বছর বাড়ছে। ইংলিশ ভার্সন যে অনুপাতে বাড়ছে, বাংলা মাধ্যমের প্রতি সেই অনুপাতে শিক্ষার্থীদের আস্থা কমছে। ব্যাপারটা কেবল ভাষাগত নয়, সংস্কৃতিগতও। &lsquo;বাংলা মাধ্যম নিয়ে সংস্কৃতি&rsquo; থেকে &lsquo;ইংলিশ ভার্সন নিয়ে সংস্কৃতি&rsquo; আলাদা। বাংলা ভাষার অর্থনৈতিক ভিত্তি ইংরেজি ভাষা দখল করে নেবে। প্রশাসনব্যবস্থায় বাংলা ভাষার স্থানকে ক্রমেই ছোট করে ফেলা হচ্ছে। বাংলাদেশের রাষ্ট্রীয় ভিত্তিও দুর্বল হয়ে পড়ছে। আসলে বাংলাদেশকে বাংলা ছেড়ে ইংরেজিতে যেতে হচ্ছে নিজের রাষ্ট্রীয় অস্তিত্বের বিনিময়ে। রাষ্ট্রীয় অস্তিত্ব বিসর্জন দিয়ে বিশ্বায়নে বিলীন হয়ে বাংলাদেশ কীভাবে চলবে? মানবজাতিকে প্রগতিশীল আন্তর্জাতিক ফেডারেল বিশ্বরাষ্ট্র প্রতিষ্ঠা করতে হবে। তাতে জাতি, জাতীয় ভাষা, জাতীয় সংস্কৃতি, জাতীয় রাজনীতি, জাতীয় প্রশাসন, জাতীয় অর্থনীতি থাকবে।<br />\r\nপাঠ্যপুস্তক বোর্ড এখন ইংলিশ ভার্সনের জন্য বাংলা পাঠ্যপুস্তক ইংরেজিতে অনুবাদ করায়। এদিক দিয়ে ইংলিশ ভার্সন বাংলা মাধ্যমের উপর নির্ভরশীল। কিন্তু অচিরেই ইংলিশ ভার্সনের জন্য অনুবাদ-নির্ভরতা পরিহার করে পাঠ্যপুস্তক প্রণয়নের স্বতন্ত্র ব্যবস্থা করা হবে। হয়তো পাঠ্যসূচি ও পাঠক্রমও ভিন্ন হবে।<br />\r\nইউনিসেফ বাংলাদেশে প্রাথমিক ও মাধ্যমিক পর্যায়ের&Ntilde;প্রথম শ্রেণি থেকে দশম শ্রেণি পর্যন্ত&Ntilde;সব শিক্ষার্থীর পাঠ্যপুস্তক, বাংলাদেশ সরকারের মাধ্যমে, বিনামূল্যে দিয়ে থাকে। এটা কি তাদের নিঃস্বার্থ খয়রাত? বিনামূল্যে পাঠ্যপুস্তক দেওয়ার সুবাদে তারা দৃশ্যের অন্তরালে থেকে আধিপত্যবাদী স্বার্থে পাঠ্যসূচি ও পাঠক্রম নিয়ন্ত্রণ করে। ইউনিসেফের পেছনে আছে ইউনেস্কো। তার পেছনে জাতিসংঘ ও যুক্তরাষ্ট্র।<br />\r\nবাংলা মাধ্যম ও ইংলিশ ভার্সন, কোনোটিতেই শিক্ষা ভালো হচ্ছে না। পরীক্ষার ফল ভালো হলেই শিক্ষা ভালো হয় না। এরই মধ্যে যারা ইংলিশ ভার্সনে পড়বে, তারাই ভবিষ্যতে রাজনীতি, প্রশাসন, রাষ্ট্রীয় সব ব্যবস্থা ও আন্তঃরাষ্ট্রিক সম্পর্কের ক্ষেত্রে দায়িত্ব পালন করবে। বাংলা মাধ্যমের ভেতর থেকে যারা বের হবে, রাষ্ট্র পরিচালনায় ও সরকারে তাদের কর্মক্ষেত্র সঙ্কুচিত হতে হতে একদিন আর কিছুই অবিশিষ্ট থাকবে না। এভাবে বাংলা ছেড়ে ইংরেজিতে গিয়ে এই জনগোষ্ঠীর ভবিষ্যত কী হবে? জাতীয় বাস্তবতা ও বিশ্ববাস্তবতা দুইদিক থেকেই প্রশ্নটির উত্তর খুঁজতে হবে।<br />\r\n১৯৭২ সালে পাবলিক বিশ্ববিদ্যালয়গুলোতে বাংলা মাধ্যম প্রবর্তিত হয়েছিল এবং তা চলছিল। বাংলা ভাষাকে উন্নত ও সমৃদ্ধ করারও বিপুল আগ্রহ ও আয়োজন দেখা দিয়েছিল। উচ্চশিক্ষার জন্য বাংলায় পাঠ্যপুস্তক প্রণয়নের ও অনুবাদের কাজ দ্রুত এগিয়ে চলছিল। বছর বিশেক যেতে না যেতেই বাংলা ভাষার উন্নয়নের সেসব কার্যক্রম প্রায় বন্ধ হয়ে যায়। পাবলিক বিশ্ববিদ্যালয়গুলো এখন দ্রুত ইংরেজি মাধ্যমে চলে যাচ্ছে। প্রাইভেট বিশ্ববিদ্যালয়গুলোতে শুরু থেকেই চলছে কেবল ইংরেজি মাধ্যম। অবস্থার চাপে সেগুলোতে লোক-দেখানো অতি সামান্য বাংলা ভাষার উন্নতির জন্য আজকাল যে উদগ্রীবতা প্রকাশ করা হয়, তাকে অনেক সময় গাছের গোড়া কেটে আগায় পানি ঢালার মতো ব্যাপার বলে মনে হয়। কার্যক্ষেত্রে দেখা যায়, বিরল ব্যতিক্রম ছাড়া শিক্ষিত বিত্তশালী লোকদের প্রায় সকলকেই বাংলা ছেড়ে ইংরেজি গ্রহণে তৎপর।<br />\r\nবাংলাদেশে প্রাথমিক ও মাধ্যমিক পর্যায়ের শিক্ষা নানা ধারা-উপধারায় বিভক্ত। শিক্ষাব্যবস্থায় উচ্চশিক্ষাপূর্ব পর্যায়ে এত ধারা-উপধারা পৃথিবীর আর কোনো রাষ্ট্রে আছে বলে আমাদের জানা নেই। নানা ধরনের মাদ্রাসা, নানা ধরনের স্কুল, নানা ধরনের ইংলিশ মিডিয়াম স্কুল, আরো কত কী! এই শিক্ষাব্যবস্থা বাংলা ভাষার ও বাংলাদেশের স্বাভাবিক বিকাশের পরিপন্থী। রাষ্ট্র গঠনের জন্য যে জাতীয় ঐক্য অপরিহার্য, এই শিক্ষাব্যবস্থা দ্বারা তাকে বিনষ্ট করা হচ্ছে।<br />\r\nপাঠ্যপুস্তক বোর্ড (ঘ.ঈ.ঞ.ই) যে ধারার শিক্ষার্থীদের পাঠ্যপুস্তকের জোগান দেয়, সেটাকে বলা হয় বাংলাদেশের শিক্ষাব্যবস্থার মূল ধারা বা সধরহ ংঃৎবধস. ২০০২ সালে সরকার এই মূলধারার বা সধরহ ংঃৎবধস-এর ভেতরে ইংলিশ ভার্সন বলে একটি নতুন ধারা প্রবর্তন করে । ২০০৭ সালে জরুরি অবস্থার সরকার সেই ধারাকে বেগবান করে। সেই বেগ আজো অব্যাহত আছে। ইংলিশ ভার্সনের জন্য বৃহত্তর শিক্ষিতসমাজের ভেতর থেকে-যে কোনো দাবি ছিল, তা নয়। এই ব্যবস্থা ভালো কি মন্দ, তা নিয়ে আজ পর্যন্ত কোনো আলোচনা-সমালোচনাও হয়নি।<br />\r\nবর্তমান আলোচনা ইংলিশ ভার্সন নিয়ে, ইংরেজি শেখা নিয়ে নয়। ইংরেজি শেখার ব্যবস্থা বাংলাদেশে উন্নত করতে হবে। শিক্ষাব্যবস্থার উন্নতির সঙ্গে সঙ্গে ইংরেজি শেখার ব্যবস্থাও বাস্তবসম্মত করতে হবে। কিন্তু ইংরেজির জন্য মাতৃভাষার স্থান ছেড়ে দেওয়া যাবে না। ইংলিশ ভার্সন বাঙালি সন্তানদের ইংরেজি শেখার কোনো সুব্যবস্থা নয়। বাংলাদেশে ইংরেজিকে প্রাথমিক ও মাধ্যমিক শিক্ষার মাধ্যম করা ভুল। জ্ঞান-বিজ্ঞান ও শিল্প-সাহিত্যে আমাদের সৃষ্টিশীলতা ইংরেজিকে দ্বিতীয় ভাষা রূপে রেখেই সম্ভব হবে। ইংরেজিকে প্রথম ভাষা করা হলে বাঙালির সৃষ্টিশক্তি দুর্বল হবে&Ntilde;সৃষ্টিশীলতা বিনষ্ট হবে। বাংলাদেশে বাংলা ভাষার অর্থনৈতিক ভিত্তিকে রক্ষা করে সম্প্রসারিত করতে হবে।<br />\r\nপাঠ্যপুস্তক বোর্ড কেবল বাংলা মাধ্যমে প্রাথমিক ও মাধ্যমিক পর্যায়ের পাঠ্যপুস্তক প্রণয়ন ও প্রকাশ করত। ইংলিশ ভার্সন প্রবর্তনের পর বোর্ড বাংলা মাধ্যমের পাশাপাশি ইংরেজি অনুবাদেও সব পাঠ্যপুস্তক প্রকাশ করতে থাকে। আদেশ জারি করা হয় যে, কোনো স্কুল ইচ্ছা করলে বাংলা মাধ্যমের পাশাপাশি স্বতন্ত্র শাখা হিসেবে ইংলিশ ভার্সন খুলতে পারে। সরকারের আদেশ অনুসরণ করে ঢাকা ও অন্যান্য শহরে প্রাথমিক ও মাধ্যমিক পর্যায়ের শিক্ষা প্রতিষ্ঠানগুলোতে ইংলিশ ভার্সন ধীরে ধীরে চালু হতে থাকে। তখন থেকে সারাদেশে শুধুই ইংলিশ ভার্সন নিয়ে নতুন নতুন প্রাথমিক বিদ্যালয় ও মাধ্যমিক বিদ্যালয় স্থাপিতও হতে থাকে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">২০১০ সালের শিক্ষানীতিতে কিংবা ২০১১ সালের সংবিধানের পঞ্চদশ সংশোধনে ইংলিশ ভার্সন নিয়ে কিছুই বলা হয়নি। এখন দেখা যাচ্ছে, সারা দেশে সচ্ছল অভিভাবকরা শিশু-কিশোরদের প্রাতিষ্ঠানিক শিক্ষার জন্য দ্রুতগতিতে ইংলিশ ভার্সনের দিকে ঝুঁকে যাচ্ছেন। বিষয়টিকে স্কুল সমূহের ও শিক্ষার্থীদের জন্য ঐচ্ছিক রাখা হয়েছে। শিক্ষিত সচ্ছল লোকদের ইচ্ছা এখন ইংরেজির দিকে। ইংরেজি শেখানোর বাণিজ্যিক কার্যক্রমও বেড়ে চলছে। সারা দেশে প্রতিবছর অধিক থেকে অধিকতর সংখ্যায় বিদ্যালয় ইংলিশ ভার্সন খুলে চলছে। গ্রামে ও শহরে কেবল ইংলিশ ভার্সন নিয়ে নতুন নতুন স্কুল প্রতিষ্ঠিতও হচ্ছে। যে ছেলে-মেয়েরা ইংলিশ ভার্সনে পড়ে, তাদের অনেকে বাড়িতেও ইংরেজিতেই কথাবার্তা বলে। &lsquo;ইংলিশ-পছন্দ মহল&rsquo; থেকে জনমনে ধারণা সৃষ্টি করা হয়েছে যে, বিশ্বায়নের যুগে ইংরেজি ছাড়া গতি নেই, বাংলার কোনো ভবিষ্যত নেই।<br />\r\nবিশ্বায়ন নামে যে কর্মধারা বিশ্বব্যাঙ্ক, আন্তর্জাতিক অর্থ তহবিল, বিশ্ব বাণিজ্য সংস্থা, ন্যাটো, যুক্তরাষ্ট্রের পররাষ্ট্র মন্ত্রণালয় চালিয়ে যাচ্ছে, তার সঙ্গে যুক্ত বুদ্ধিজীবীরা বাংলাদেশের স্বাধীনতা ও রাষ্ট্রীয় অস্তিত্বের চেয়ে বিশ্বায়নকে অনেক বেশি গুরুত্বপূর্ণ মনে করেন। বাংলাদেশে ১৯৮০-র দশক থেকে নিঃরাজনীতিকরণের কার্যক্রম চালানো হচ্ছে। বাংলাদেশকে স্বাধীন রাষ্ট্র রূপে না রেখে বিশ্বায়নে বিলীন করে দেওয়ার অঘোষিত কার্যক্রম চালু আছে। বাংলাদেশের উন্নয়ন-কার্যক্রম ও উন্নয়নচিন্তা পর্যবেক্ষণ করলে এটা বোঝা যায়।<br />\r\nবাংলাদেশে ব্রিটিশ সরকার কর্তৃক ব্রিটিশ কাউন্সিলের মাধ্যমে পরিচালিত ইংলিশ মিডিয়ামের শিক্ষাধারা (এ লেভেল, ও লেভেল) অব্যাহত আছে। তাদের পাঠ্যসূচি যুক্তরাজ্যের বাস্তবতায় তৈরি। অবস্থার চাপে তারা অল্প কিছু নম্বরের ইধহমষধফবংয ঝঃঁফরবং পাঠ্যসূচির অন্তর্ভুক্ত করেছে। যুক্তরাজ্যের ও যুক্তরাষ্ট্রের সরকার বাংলাদেশে সেট, টোফেল, আইইএলটিএস ইত্যাদি পরীক্ষা নিয়মিত নিচ্ছে এবং বাংলাদেশের তরুণ-তরুণীরা ঢাকায় ও আরো কয়েকটি শহরে বিরাট-সংখ্যায় সেগুলোতে অংশগ্রহণ করছে। বাণিজ্যিক ভিত্তিতে এগুলো চালানো হচ্ছে। যারা ইউরোপে আমেরিকায় যেতে চায়, বিশ্বায়নের কাজ করতে চায়, তাদের জন্য ইংলিশ ভার্সনের তুলনায় এ লেভেল, ও লেভেল অনেক ভালো। বাংলাদেশের দুর্বল শিক্ষাব্যবস্থায় ইংলিশ ভার্সনে গিয়ে ছেলেমেয়েরা ইংরেজি ভালো শিখতে পারছে না, বিষয়জ্ঞানও ভালো লাভ করছে না। মাদ্রাসা নিয়ে এখন উদ্বিগ্ন না হওয়া ভালো। কাল নিরবধি, পৃথ্বী বিপুলা। সার্বিক উন্নতির জন্য সময় পাওয়া যাবে&Ntilde;সুযোগও সৃষ্টি করা যাবে। শিক্ষাবাণিজ্য বন্ধ করা যাবে তখনই, যখন তা করার মতো সরকার প্রতিষ্ঠা করা যাবে। এখন পরিপূর্ণ গুরুত্ব দিয়ে কাজ করা দরকার শিক্ষার মূল ধারার ব্যবস্থাকে বাংলাদেশোপযোগী করার জন্য। এর জন্য বাংলা মাধ্যমকে উন্নত করা ও ইংলিশ ভার্সনকে পরিহার করা উচিত। সরকার যদি ইংলিশ ভার্সন বিলুপ্ত করে, তা হলে খুব ভালো কাজ করবে। ইংলিশ ভার্সন নিয়ে যে উৎসাহ দেখা যাচ্ছে তা চলতে থাকলে বাংলা ভাষা বাংলাদেশের রাষ্ট্রভাষা থাকবে না। আমাদের মতে, বাংলাদেশের আটানব্বই শতাংশ লোকের স্বার্থে বাংলাকে রাষ্ট্রভাষা রূপে রক্ষা করতে হবে, আর বাংলাদেশকেও রাষ্ট্র রূপে রক্ষা করতে হবে। বাংলা রাষ্টভাষা না থাকলে বাংলাদেশ রাষ্ট্র রূপে থাকবে না। বাংলাদেশকে এবং অন্য সব রাষ্ট্রকেও বিশ্বায়নবাদী নিগড় থেকে মুক্ত হযে সুষ্ঠু আন্তর্জাতিক সম্পর্কের মধ্যে উত্তীর্ণ হতে হবে।<br />\r\nযুক্তরাজ্য ও যুক্তরাষ্ট্র সারা দুনিয়ায় ইংরেজির প্রতিপত্তি বাড়ানোর জন্য সুদূরপ্রসারী পরিকল্পনা নিয়ে দুই শতাব্দী ধরে কাজ করে আসছে। সকল জাতিকে ইংরেজি শেখানো দুটি রাষ্ট্রেরই বিশ্ববিস্তৃত ব্যবসাও বটে। বিশ্বব্যাপী তাদের কর্তৃত্ব অনেকটাই নির্ভর করে বিশ্বব্যাপী ইংরেজি ভাষার প্রতিষ্ঠার উপর। বাংলার উন্নতির জন্য বাংলাদেশে আমাদের মৌসুমি হুজুগ ছাড়া আর কি আছে? আমরা নিজেরাই নিজেদেরকে রক্ষা করি না, বিদেশীদের কাছে বিকিয়ে দিই। আমাদের উন্নতি হবে কীভাবে? রাষ্ট্রভাষা হিসেবে বাংলাকে এবং রাষ্ট্র হিসেবে বাংলাদেশকে রক্ষা করতে হলে, উন্নত করতে হলে, বাংলার প্রতি, বাংলাদেশের প্রতি, জনগণের সক্রিয় আস্থা ও সক্রিয় ভালোবাসা লাগবে। এক্ষেত্রে জনগণের ভালোবাসাই প্রাণশক্তি। ইংরেজির প্রতি ভালোবাসা বাংলার চেয়ে বেশি হলে রাষ্ট্রভাষা হিসেবে বাংলা এবং রাষ্ট্র হিসেবে বাংলাদেশ রক্ষা পাবে কীভাবে?<br />\r\nইংলিশ ভার্সনের শিক্ষার্থীদের সংখ্যা বাংলা মাধ্যমের শিক্ষার্থীদের সংখ্যার তুলনায় এখনো অনেক কম। তবে এ সংখ্যা প্রতি বছর বাড়ছে। ইংলিশ ভার্সন যে অনুপাতে বাড়ছে, বাংলা মাধ্যমের প্রতি সেই অনুপাতে শিক্ষার্থীদের আস্থা কমছে। ব্যাপারটা কেবল ভাষাগত নয়, সংস্কৃতিগতও। &lsquo;বাংলা মাধ্যম নিয়ে সংস্কৃতি&rsquo; থেকে &lsquo;ইংলিশ ভার্সন নিয়ে সংস্কৃতি&rsquo; আলাদা। বাংলা ভাষার অর্থনৈতিক ভিত্তি ইংরেজি ভাষা দখল করে নেবে। প্রশাসনব্যবস্থায় বাংলা ভাষার স্থানকে ক্রমেই ছোট করে ফেলা হচ্ছে। বাংলাদেশের রাষ্ট্রীয় ভিত্তিও দুর্বল হয়ে পড়ছে। আসলে বাংলাদেশকে বাংলা ছেড়ে ইংরেজিতে যেতে হচ্ছে নিজের রাষ্ট্রীয় অস্তিত্বের বিনিময়ে। রাষ্ট্রীয় অস্তিত্ব বিসর্জন দিয়ে বিশ্বায়নে বিলীন হয়ে বাংলাদেশ কীভাবে চলবে? মানবজাতিকে প্রগতিশীল আন্তর্জাতিক ফেডারেল বিশ্বরাষ্ট্র প্রতিষ্ঠা করতে হবে। তাতে জাতি, জাতীয় ভাষা, জাতীয় সংস্কৃতি, জাতীয় রাজনীতি, জাতীয় প্রশাসন, জাতীয় অর্থনীতি থাকবে।<br />\r\nপাঠ্যপুস্তক বোর্ড এখন ইংলিশ ভার্সনের জন্য বাংলা পাঠ্যপুস্তক ইংরেজিতে অনুবাদ করায়। এদিক দিয়ে ইংলিশ ভার্সন বাংলা মাধ্যমের উপর নির্ভরশীল। কিন্তু অচিরেই ইংলিশ ভার্সনের জন্য অনুবাদ-নির্ভরতা পরিহার করে পাঠ্যপুস্তক প্রণয়নের স্বতন্ত্র ব্যবস্থা করা হবে। হয়তো পাঠ্যসূচি ও পাঠক্রমও ভিন্ন হবে।<br />\r\nইউনিসেফ বাংলাদেশে প্রাথমিক ও মাধ্যমিক পর্যায়ের&Ntilde;প্রথম শ্রেণি থেকে দশম শ্রেণি পর্যন্ত&Ntilde;সব শিক্ষার্থীর পাঠ্যপুস্তক, বাংলাদেশ সরকারের মাধ্যমে, বিনামূল্যে দিয়ে থাকে। এটা কি তাদের নিঃস্বার্থ খয়রাত? বিনামূল্যে পাঠ্যপুস্তক দেওয়ার সুবাদে তারা দৃশ্যের অন্তরালে থেকে আধিপত্যবাদী স্বার্থে পাঠ্যসূচি ও পাঠক্রম নিয়ন্ত্রণ করে। ইউনিসেফের পেছনে আছে ইউনেস্কো। তার পেছনে জাতিসংঘ ও যুক্তরাষ্ট্র।<br />\r\nবাংলা মাধ্যম ও ইংলিশ ভার্সন, কোনোটিতেই শিক্ষা ভালো হচ্ছে না। পরীক্ষার ফল ভালো হলেই শিক্ষা ভালো হয় না। এরই মধ্যে যারা ইংলিশ ভার্সনে পড়বে, তারাই ভবিষ্যতে রাজনীতি, প্রশাসন, রাষ্ট্রীয় সব ব্যবস্থা ও আন্তঃরাষ্ট্রিক সম্পর্কের ক্ষেত্রে দায়িত্ব পালন করবে। বাংলা মাধ্যমের ভেতর থেকে যারা বের হবে, রাষ্ট্র পরিচালনায় ও সরকারে তাদের কর্মক্ষেত্র সঙ্কুচিত হতে হতে একদিন আর কিছুই অবিশিষ্ট থাকবে না। এভাবে বাংলা ছেড়ে ইংরেজিতে গিয়ে এই জনগোষ্ঠীর ভবিষ্যত কী হবে? জাতীয় বাস্তবতা ও বিশ্ববাস্তবতা দুইদিক থেকেই প্রশ্নটির উত্তর খুঁজতে হবে।<br />\r\n১৯৭২ সালে পাবলিক বিশ্ববিদ্যালয়গুলোতে বাংলা মাধ্যম প্রবর্তিত হয়েছিল এবং তা চলছিল। বাংলা ভাষাকে উন্নত ও সমৃদ্ধ করারও বিপুল আগ্রহ ও আয়োজন দেখা দিয়েছিল। উচ্চশিক্ষার জন্য বাংলায় পাঠ্যপুস্তক প্রণয়নের ও অনুবাদের কাজ দ্রুত এগিয়ে চলছিল। বছর বিশেক যেতে না যেতেই বাংলা ভাষার উন্নয়নের সেসব কার্যক্রম প্রায় বন্ধ হয়ে যায়। পাবলিক বিশ্ববিদ্যালয়গুলো এখন দ্রুত ইংরেজি মাধ্যমে চলে যাচ্ছে। প্রাইভেট বিশ্ববিদ্যালয়গুলোতে শুরু থেকেই চলছে কেবল ইংরেজি মাধ্যম। অবস্থার চাপে সেগুলোতে লোক-দেখানো অতি সামান্য &lsquo;বাংলাদেশ স্টাডিজ&rsquo; চালু করা হয়েছে। তাও ইংরেজি মাধ্যমে। আটাত্তরটি প্রাইভেট বিশ্ববিদ্যালয়ের মধ্যে মাত্র দুটিতে বাংলা বিভাগ আছে। সেগুলোতে পড়ানো হয় কেবল সাহিত্য &Ntilde; অর্থাৎ কেবল কবিতা, গল্প, উপন্যাস, নাটক, রম্যরচনা। জ্ঞান-বিজ্ঞানের কোনো বিষয়েই বাংলা মাধ্যম নেই। চিকিৎসাবিদ্যা, প্রকৌশল ও প্রযুক্তিবিদ্যা, কৃষিবিদ্যা, আইন ইত্যাদিতেও বাংলা মাধ্যমের উদ্যোগ দেখা দিয়েছিল। ইউরোপ-আমেরিকার রাষ্ট্রগুলোতে অনুবাদের যে বৃহদায়তন ব্যবস্থা আছে, সে ধরনের ব্যবস্থা বাংলাদেশেও প্রবর্তনের প্রস্তাব উত্থাপিত হচ্ছিল। কিন্তু সে প্রবণতা এখন আর নেই। ১৯৬০-এর দশকে বাংলা ভাষা, বাঙালি সংস্কৃতি ও বাংলাদেশ রাষ্ট্রের জন্য যে বিপুল আগ্রহ দেখা দিয়েছিল, ১৯৭০-এর দশক শেষ হতে না হতেই তা শেষ হয়ে গেল! এটা কি মূলত সরকারি নীতির কারণে ঘটেছে? শাসক শ্রেণির বুদ্ধিজীবীদের ও সিভিল সোসাইটি সমূহের বিশিষ্ট নাগরিকদের ভূমিকা কী? বিশ্বায়নের কারণে কোন মহাদেশে কোন রাষ্ট্র নিজের রাষ্ট্রভাষা ছেড়ে ইংরেজিকে রাষ্ট্রভাষা করেছে?<br />\r\nবর্তমানে যে প্রক্রিয়া চলছে তাতে বাংলাদেশে বাংলা ভাষার ভবিষ্যত অন্ধকার। পশ্চিমবঙ্গে, আগরতলায় এবং ভারতের আরো কিছু বাংলাভাষী এলাকায় বাংলার কোনো ভবিষ্যত আছে কি? মাতৃভাষা রূপে বাংলা অবশ্যই থাকবে। কিন্তু রাষ্ট্রভাষা রূপে বাংলাদেশে বাংলা থাকবে না। জাতিসঙ্ঘ কেবল মাতৃভাষা রক্ষা করতে চায়; দুর্বল রাষ্ট্র সমূহের রাষ্ট্রভাষা রক্ষা করতে চায় না, ইংরেজি চাপিয়ে দিতে চায়। বাংলা ভাষায় বর্তমানে প্রায় ত্রিশ কোটি মানুষ কথা বলে। নানা দেশে ছড়িয়ে আছে বাংলাভাষী মানুষ। বাংলাদেশে বাংলাভাষী ষোল কোটি মানুষ আছে। ভাষিক জনসংখ্যার দিক দিয়ে পৃথিবীতে বাংলার স্থান চতুর্থ। ওড়িয়া ও অসমিয়া ভাষা বাংলা ভাষার খুব কাছাকাছি। রাজনৈতিক আধিপত্য ও অর্থনৈতিক শোষণ দূর করা হলে ওইসব ভাষার সঙ্গে বাংলা ভাষার সমন্বয় ঘটবে। বাংলা, ওড়িয়া, অসমিয়া প্রভৃতি ভাষার লোকদের মধ্যে শিল্প-সাহিত্যগত ও জ্ঞানগত আদান-প্রদান নিবিড় হওয়া বাঞ্ছনীয়। জ্ঞান-বিজ্ঞান ও শিল্প-সাহিত্যে বাংলা ভাষার উন্নতির সম্ভাবনা অফুরন্ত। বাংলা ভাষা নিয়ে রাজনীতি উন্নত হলে রাষ্ট্র হিসেবেও বাংলাদেশ শক্তিশালী হবে।<br />\r\nমূল ধারার প্রাতিষ্ঠানিক শিক্ষাকে বাংলা মাধ্যম ও ইংলিশ ভার্সনে বিভক্ত করার ফলে আমাদের জাতির ভেতরকার বিভক্তি পুনর্বিন্যস্ত হবে। তাতে মোট জনসংখ্যার আটানব্বই ভাগ মানুষের স্থায়ী ক্ষতি হবে। আর বাংলা ভাষার উন্নতির সম্ভাবনা নষ্ট হবে। জনসাধারণ কি ভাগ্য পরিবর্তনের চেষ্টা না করে চিরকাল নিষ্ক্রিয় থাকবে?<br />\r\nবাংলাদেশের রাজনীতি সুস্থ, স্বাভাবিক নয়। রাজনৈতিক সংস্কৃতিতে প্রধান তিনটি ধারা দেখা যাচ্ছে : এক) ধর্মনিরপেক্ষতাবাদী ধারা যা মুক্তিযুদ্ধের পক্ষশক্তি বলে আত্মপরিচয় দেয়। আওয়ামী লীগ ও তার সহযোগী দলগুলো এই ধারায় আছে। দুই) জাতীয়তাবাদী ইসলামপন্থী বলে অভিহিত ধারা। বিএনপি, জামায়াত ও তাদের সহযোগী দলগুলো এই ধারায় আছে। তিন) এনজিও ও সিভিল সোসাইটি সমূহের ধারা। ইংলিশ ভার্সনের বিকাশ ঘটলে কালক্রমে এনজিও ও সিভিল সোসাইটি সমূহের ধারা শক্তিশালী হবে। জাতীয়তাবাদী ইসলামপন্থী ধারার কথিত জাতীয়তাবাদে মূলত আছে ভারতবিদ্বেষ ও যুক্তরাষ্ট্রপ্রীতি, কোনো সদর্থক রাজনৈতিক দৃষ্টিভঙ্গি ও চিন্তা নেই। বাংলাদেশে জাতিগঠন ও রাষ্ট্রগঠনের চেষ্টা ও চিন্তা তাদের নেই। এনজিও ও সিভিল সোসাইটির ধারা তো বিশ্বায়নবাদী। তারা তো জাতীয়তাবাদ ও জাতিরাষ্ট্রের উপযোগিতাই স্বীকার করে না। রাষ্ট্রব্যবস্থা ও বিশ্বব্যবস্থা নিয়ে তারা বিশ্বব্যাঙ্ক, আন্তর্জাতিক অর্থ তহবিল ইত্যাদির অনুসারী। রাষ্ট্রব্যবস্থা ও বিশ্বব্যবস্থা নিয়ে ধর্মনিরপেক্ষতাবাদীদেরও কোনো পরিচ্ছন্ন চিন্তা নেই। জাতি, রাষ্ট্র ও রাষ্ট্রভাষা রক্ষার জন্য যে স্বাজাত্যবোধ ও দেশপ্রেম দরকার, তার অস্তিত্ব কোথায়?<br />\r\nজাতীয়তাবাদ, আন্তর্জাতিকতাবাদ, বিশ্বায়ন ও ভবিষ্যত নিয়ে গভীর চিন্তা-ভাবনা দরকার। পশ্চিমা বৃহৎ শক্তিবর্গের বিশ্বায়নবাদী নীতির অন্ধ অনুসরণ ও অনুকরণ দ্বারা কোনো জাতিই উজ্জ্বল ভবিষ্যতের অধিকারী হতে পারবে না। মার্কিন যুক্তরাষ্ট্র ও তার সহযোগীরা সাম্রাজ্যবাদেরই নতুন নাম দিয়েছে বিশ্বায়ন। ওয়াশিংটন কেন্দ্রিক মুক্ত বাজার অর্থনীতি, অবাধপ্রতিযোগিতাবাদ ও নিওলিবারেলিজম তাদের অবলম্বন। সে ধারার অন্ধ অনুসারী হয়ে বাংলাদেশ চলছে। তারই ফলে বিশ্বায়নবাদীদের প্রচার-প্রচারণা দ্বারা সৃষ্ট হুজুগের মধ্যে বাংলাদেশে কিছু লোকের মনে ইংলিশ ভার্সনের ধারণা জেগেছে, এবং রাষ্ট্রীয়ভাবে তা কার্যকর করে ফেলা হয়েছে। এর পরিণতি ভালো হবে না, ভালো হতে পারে না। দুনিয়াটা কীভাবে চলছে, কীভাবে চলা উচিত, এবং সামনে বিভিন্ন শক্তির ক্রিয়া-প্রতিক্রিয়ার মধ্য দিয়ে কীভাবে চলবে, তা দূরদর্শিতার সঙ্গে গভীরভাবে বিচার-বিবেচনা করে দেখা দরকার। এ বিষয়ে বাংলাদেশে ইংরেজিতে লেখা যেসব বইপত্র ও লেখালেখির সন্ধান আমরা পাই, সেগুলো সবই বিশ্বব্যাঙ্ক, আন্তর্জাতিক অর্থ তহবিল, বিশ্ববাণিজ্য সংস্থা, ন্যাটো, যুক্তরাষ্ট্রের পররাষ্ট্র মন্ত্রণালয় ও চলমান বিশ্বায়নের অন্ধ সমর্থক। বিষয়টি নিয়ে ভাবতে ভাবতে এবং বিচার-বিবেচনা করতে করতে কিছু ধারণা আমার মনে দানা বেঁধেছে। আমি জাতীয়তাবাদ আন্তর্জাতিকতাবাদ বিশ্বায়ন ও ভবিষ্যত নামে একটি ছোট বই লিখেছি এবং সেটি এরই মধ্যে প্রকাশিত হয়েছে (জাগৃতি প্রকাশনী, আজিজ মার্কেট, শাহবাগ, ঢাকা, ২০১৪)। আমি আমার চিন্তাকে ভুল মনে করার কারণ দেখি না। স্বল্প পরিসরে সেসব কথার পুনরাবৃত্তি সম্ভব নয়।<br />\r\nএকটি শান্তিপূর্ণ, প্রগতিশীল বিশ্বব্যবস্থা প্রবর্তনের জন্য জাতীয়তাবাদ ও তার সম্পূরক আন্তর্জাতিকতাবাদের ভিত্তিতে রাষ্ট্রব্যবস্থা ও বিশ্বব্যবস্থাকে পুনর্গঠিত করতে হবে। তাতে জাতীয় সংস্কৃতি, জাতীয় সরকার ও জাতিরাষ্ট্র থাকবে। তার উপর প্রতিষ্ঠা করতে হবে আন্তর্জাতিক ফেডারেল বিশ্বসরকার ও বিশ্বরাষ্ট্র। জাতীয়তাবাদের সঙ্গে অবশ্যই যুক্ত রাখতে হবে &lsquo;জনগণের গণতন্ত্র&rsquo;। জাতীয় সরকার ও জাতিরাষ্ট্রের কিছু ক্ষমতা চলে যাবে বিশ্বসরকার ও বিশ্বরাষ্ট্রের কাছে। কেবল বিশ্বসরকারের কাছে একটি সেনাবাহিনী রেখে সকল রাষ্ট্রের সেনাবাহিনী বিলুপ্ত করতে হবে। বিশ্বসরকারের কাজ হবে জাতিরাষ্ট্রসমূহের মধ্যে সহযোগিতা বৃদ্ধি করা, তাদের মধ্যকার বিরোধ মীমাংসা করা এবং যুদ্ধের সম্ভাবনা তিরোহিত করা। আন্তঃরাষ্ট্রিক সম্পর্কের মধ্য দিয়ে মানবজাতির ঐক্য ও বিশ্বভ্রাতৃত্বকে বিকশিত করতে হবে। বিশ্বরাষ্ট্রের অভ্যন্তরে জাতিরাষ্ট্র থাকবে। আমাদের প্রস্তাবিত প্রক্রিয়ার মধ্যে কোনো জাতির রাষ্ট্রভাষাই ইংরেজি দ্বারা স্থানচ্যুত হবে না। জ্ঞান-বিজ্ঞানে উন্নত জাতি সমূহের ভাষা থেকে অনুন্নত জাতিসমূহের ভাষায় প্রয়োজনীয় নানা বিষয় গ্রহণ করে সব ভাষাই উন্নতির ধারায় থাকতে পারবে। আন্তঃরাষ্ট্রিক সম্পর্কের প্রয়োজনে প্রত্যেক রাষ্ট্রেই বিদেশী ভাষা শেখার ব্যবস্থা রাখতে হবে। যেসব জাতি রাষ্ট্রব্যবস্থা, বিশ্বব্যবস্থা, রাষ্ট্রভাষা ও জাতীয় সংস্কৃতি সম্পর্কে সচেতন হয়ে নিজেদের উন্নতির উপায় করবে না, সেসব জাতি ধীরে ধীরে মানবজাতির মূল ধারা থেকে খসে পড়বে।<br />\r\nবিশ্বব্যবস্থার পুনর্গঠনের সঙ্গে জাতিরাষ্ট্রকেও পুনর্গঠিত করতে হবে। উদার গণতন্ত্র বলে পশ্চিমা বৃহৎ শক্তিবর্গ যে গণতন্ত্রকে বাংলাদেশে চাপিয়ে দিচ্ছে, তা জনগণের গণতন্ত্র নয়, তা গণবিরোধী গণতন্ত্র&Ntilde;তা কেবল উচ্চশ্রেণির লোকদের হীনস্বার্থ হাসিলের গণতন্ত্র। এই গণতন্ত্রে কেবল নির্বাচনের মাধ্যমে সরকার গঠন করা হয়, এবং নির্বাচিত সরকার চেলা-চামুণ্ডা পরিবেষ্টিত একনায়কতান্ত্রিক হয়ে জনগণের উপর জুলুম-জবরদস্তি, নির্যাতন-নিপীড়ন ও শোষণ চালায়। বাংলাদেশের সরকার ও রাজনৈতিক দলগুলো জাতীয় সংসদের নির্বাচন করারও সামর্থ্য রাখে না। যে গণতন্ত্র আমরা চাই তা জনগণের গণতন্ত্র&Ntilde; সমাজের নিচের স্তরের শতকরা পঁচানব্বই শতাংশসহ শতভাগ মানুষের গণতন্ত্র। কথিত উদার গণতন্ত্র পরিত্যাগ করে জনগণের গণতন্ত্র কায়েম করে রাষ্ট্রব্যবস্থার আমূল পুনর্গঠন করতে হবে। অর্থনীতিতেও ঘটাতে হবে পরিবর্তন। জাতিরাষ্ট্রের পুনর্গঠন ও বিশ্বব্যবস্থার পুনর্গঠনের জন্য গড়ে তুলতে হবে আন্তর্জাতিক আন্দোলন।<br />\r\nইংলিশ ভার্সন যেভাবে প্রবর্তন করা হয়েছে তাতে বাংলাভাষা বাংলাদেশের রাষ্ট্রীয় কার্যক্রম থেকে অপসৃত হবে। রাষ্ট্রব্যবস্থায় বাংলা ভাষা বাদ দিয়ে ইংরেজি ভাষা ও তার আনুষঙ্গিক সংস্কৃতি, রাজনীতি ও অর্থনীতি নিয়ে বাংলাদেশ স্বাধীন রাষ্ট্ররূপে থাকতে পারবে না। পরাধীনতার মধ্যে জনজীবনে দুর্দশা বাড়বে। রাষ্ট্রভাষা বাংলা বাংলাদেশের রাষ্ট্রীয় অস্তিত্বের অপরিহার্য শর্ত। নিজেদের ভাষা, সংস্কৃতি ও রাষ্ট্রের জায়গায় অন্যদের ভাষা, সংস্কৃতি ও কর্তৃত্ব নিয়ে আমরা কীভাবে লাভবান হব? আমাদের লক্ষ করা দরকার যে, কেবল জাতীয় পতাকা, জাতীয় সঙ্গীত, আর জাতিসঙ্ঘের সদস্যপদ থাকলেই আজকাল রাষ্ট্রীয় স্বাধীনতা থাকে না; এসব আফগানিস্তানের, ইরাকের, লিবিয়ারও আছে। রাষ্ট্রীয় স্বাধীনতা আমাদের অর্জন করতে হবে।<br />\r\nইংলিশ ভার্সনে না গিয়ে বাংলা মাধ্যমের ভেতরে ইংরেজিকে যথোচিত স্থান দেওয়ার ব্যবস্থা করতে হবে। বর্তমান দুর্গতির মধ্যে কিছু সময়ের জন্য বাংলা ও ইংরেজি দুটোতেই পরীক্ষার নম্বর কিছু বাড়ানো যেতে পারে, এবং পাঠ্যসূচি থেকে অপ্রয়োজনীয় বিষয় বাদ দেওয়া যেতে পারে। কেবল শিক্ষার মাধ্যমই নয়, সর্বজনীন কল্যাণে শিক্ষার বিষয়ও&Ntilde; পাঠ্যসূচি ও পাঠক্রমও&Ntilde;আমূল পরিবর্তনের দাবি রাখে। শিক্ষাপদ্ধতি ও পরীক্ষাপদ্ধতিও বদলাতে হবে। জাতীয় প্রয়োজন বিবেচনা করে ইংরেজি, ফরাসি, জার্মান, রুশ, চিনা, জাপানি প্রভৃতি ভাষা শেখানোর জন্য স্বতন্ত্র প্রতিষ্ঠান গড়ে তোলা দরকার। ঢাকা বিশ্ববিদ্যালয়ের মডার্ন লেঙ্গুয়েজ ইনসটিটিউট নিতান্তই অপর্যাপ্ত। এসব ভাষা শেখার ও অনুবাদের জন্য বৃহদায়তন জাতীয় আয়োজন লাগবে।<br />\r\nবাংলাদেশের টিভি চ্যানেলগুলোর দুর্বলতার কারণে বাংলা নাটক দুর্বল হয়ে পড়েছে। শহরে ও গ্রামে হিন্দি সিরিয়ালের দাপটে পারিবারিক পর্যায়ে বাংলা ভাষা ও বাঙালি সংস্কৃতি বিকারপ্রাপ্ত হচ্ছে। এফ এম রেডিওতেও বাংলা ভাষার ব্যবহার বিশৃঙ্খলাপূর্ণ। বাংলা বানানের আরো সংস্কার দরকার। এসব ব্যাপারে সংশ্লিষ্ট কর্তৃপক্ষের ও জনসাধারণের সচেতনতা দরকার।<br />\r\nপাশ্চাত্য সভ্যতার একদিকে আছে তার প্রগতিশীল ধারা। তাতে আছে জ্ঞান-বিজ্ঞান, প্রযুক্তি, ইতিহাস, দর্শন, সাহিত্য ও শিল্পকলা। এ ধারা মহান। অপরদিকে আছে তার অপক্রিয়াশীল ধারা। তাতে আছে উপনিবেশবাদ, ফ্যাসিবাদ, সাম্রাজ্যবাদ ও যুদ্ধবিগ্রহ। এ ধারা মানবতাবিরোধী, কুৎসিত, কদর্য। প্রগতিশীল মহান ধারাকে আমাদের গ্রহণ করতে হবে এবং কুৎসিত কদর্য ধারাকে পরিহার করতে হবে। এর জন্য আমাদের ইংরেজি ও আরো কোনো কোনো ভাষা ভালো করে শিখতে হবে। আন্তর্জাতিক ও আন্তঃরাষ্ট্রিক বিভিন্ন সম্পর্কের প্রয়োজনেও বিদেশী ভাষা শিখতে হবে। এই শেখার উপায় নিয়ে বাস্তবসম্মত চিন্তা-ভাবনা ও বিচার-বিবেচনা দরকার। ইংলিশ ভার্সন নিয়ে সুফল হবে না। এটা পরিত্যাজ্য। যারা পশ্চিমা উপনিবেশবাদী, ফ্যাসিবাদী ও সাম্রাজ্যবাদী নীতি অনুসরণ করে চলতে চায় তাদের জন্য ইংলিশ ভার্সনের চেয়ে এ লেভেল, ও লেভেল অনেক ভালো। ইংলিশ ভার্সনে গিয়ে তারা বিভ্রান্ত হচ্ছে। ইংলিশ ভার্সন তাদের বর্জন করা উচিত। যারা দুর্গত বাংলাদেশকে চিরকালের জন্য পশ্চিমা বৃহৎ শান্তিবর্গের গোলাম করে রাখার উদ্দেশ্যে নানা কৌশলে যুক্তির পর যুক্তির জাল বিস্তার করে চলছেন, তাঁদের ভূমিকা নিন্দনীয়&Ntilde;তাঁদের চিন্তা বর্জনীয়। এখন বাংলাকে তুচ্ছ মনে হলেও ইংলিশ ভার্সনের অভিজ্ঞতা অবশ্যই একদিন আমাদেরকে বাংলার গুরুত্ব বুঝতে বাধ্য করবে। তখন আমাদের বাংলায় ফিরে আসতে হবে এবং ইংরেজিকে, অনুচিত গুরুত্ব না দিয়ে, যথোচিত গুরুত্বে গ্রহণ করতে হবে। এর মধ্যে অনেক ক্ষতি হয়ে যাবে। যে শিক্ষাব্যবস্থা চলছে তা নিয়ে আমরা উন্নতি করতে পারব না। চলতি উত্তেজনা ও উপস্থিত অস্থিরতা থেকে মুক্ত হয়ে উন্নত ভবিষ্যত সৃষ্টির জন্য প্রগতিশীল নতুন শিক্ষাব্যবস্থার কথা পরিপূর্ণ গুরুত্ব দিয়ে আমাদের ভাবা কর্তব্য। এর জন্য যা যা পরিবর্তন দরকার, সবই সাধন করতে হবে।<br />\r\nবাংলা উন্নত ভাষা। এ ভাষা জ্ঞান-বিজ্ঞান ও সাহিত্য চর্চার উপযোগী। ব্রিটিশ আমলে ইংরেজির চর্চা এদেশে অনেক হয়েছে। কিন্তু জ্ঞান-বিজ্ঞানচর্চায় ও সাহিত্যসৃষ্টিতে বাঙালি তার প্রতিভার পরিচয় বাংলা ভাষার মাধ্যমেই দিয়েছে। জগদীশচন্দ্র বসু, প্রফুল্লচন্দ্র রায়, মেঘনাদ সাহা, প্রশান্তচন্দ্র মহলানবিস, সত্যেন্দ্রনাথ বসু&Ntilde;প্রত্যেকেই বাংলার চর্চা করেছেন, এবং বলেছেন&Ntilde;বাংলায়ই তাঁরা চিন্তা করেছেন এবং মনে মনে ইংরেজিতে তা তর্জমা করে প্রকাশ করেছেন। এই তর্জমার কোনো বিকল্প নেই। তাঁদের মতে, সৃষ্টির ক্ষেত্রে অন্তত দ্বিভাষিক হতেই হয়, মাতৃভাষা বাদ দিয়ে এগোনো যায় না। বাংলা ভাষার উন্নতির সম্ভাবনা অন্তহীন। বাংলাদেশে বাংলা ভাষার, সেই সঙ্গে সাঁওতাল গারো চাকমা রাখাইন প্রভৃতি জনগোষ্ঠীর ভাষা সমূহের এবং ইংরেজি ফরাসি জার্মান আরবি উর্দু ফার্সি সংস্কৃত পালি রুশ চিনা জাপানি প্রভৃতি ভাষার ব্যবহার কিভাবে কোথায় কতখানি করতে হবে এবং এসব ভাষা শেখার ও প্রয়োজনের ক্ষেত্রে অনুবাদের কি ব্যবস্থা বাংলাদেশে করা সমীচীন, সে সম্পর্কে যুগোপযোগী জাতীয় ভাষানীতি আমাদের অপরিহার্য। জাতীয় ভাষানীতি প্রণয়নের বেলায় ভাষা-শিক্ষার চেয়েও অনেক বড় বিবেচ্য হবে শিক্ষার ও জ্ঞান-বিজ্ঞান অর্জনের মাধ্যম হিসেবে ভাষা এবং রাষ্ট্র পরিচালনার জন্য ভাষা। জাতীয় ভাষানীতিতে মোট জনসংখ্যার শতকরা প্রায় নিরানব্বই ভাগ মানুষের ভাষা বাংলাকে যথোচিত গুরুত্ব দিতে হবে। পঁয়তাল্লিশটি ক্ষুদ্র জনগোষ্টীর লোকসংখ্যা বাংলাদেশের মোট লোকসংখ্যার এক শতাংশের সামান্য বেশি। তবে বাংলাদেশের পঁয়তাল্লিশটি ক্ষুদ্র জনগোষ্ঠীর পঁয়তাল্লিশটি ভাষাকেও যথোচিত গুরুত্ব দিতে হবে। ক্ষুদ্র জনগোষ্ঠী সমূহের সার্বিক উন্নতির জন্য জাতির (হধঃরড়হ) মূল ধারার সঙ্গে তাদের সম্পর্ক নিবিড়তর হওয়া সমীচীন।<br />\r\nজাতিসঙ্ঘ কথিত আদিবাসীদের চিরকাল আদিবাসী করে রাখার নীতি বর্জনীয়। তাদেরকে মানবজাতির মূল ধারায় আসতে সহায়তা করতে হবে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><a href=\"http://jatioshompodrokkha.com/wp-content/uploads/2015/01/ban-eng1.png\"><img alt=\"ban-eng1\" class=\"alignnone appear disappear size-medium wp-image-1602\" src=\"http://jatioshompodrokkha.com/wp-content/uploads/2015/01/ban-eng1-300x218.png\" style=\"height:218px; width:300px\" /></a></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">আমি আশা করতে চাই, বাংলাদেশের বিবেকবান চিন্তাশীল ব্যক্তিরা বিষয়টি নিয়ে পরিপূর্ণ গুরুত্বের সঙ্গে আলোচনা করবেন, এবং সেসব আলোচনা জাতি ও রাষ্ট্রের জন্য যথোচিত সিদ্ধান্তে পৌঁছার উপযোগী হবে। জনজীবনে এ নিয়ে নানা প্রশ্ন ও সংশয় আছে। আমি আশা করতে চাই যে, এই ভূভাগের শিক্ষিত সমাজ ১৯৫০-এর ও &rsquo;৬০-এর দশকে বাংলা ভাষার সার্বিক উন্নতির জন্য যেভাবে সক্রিয় হয়েছিল, বাংলাদেশের আজকের শিক্ষিত সমাজ বাংলা ভাষাকে বাংলাদেশের রাষ্ট্রভাষা রূপে রক্ষা করার ও সমৃদ্ধিমান করার জন্য, তেমনিভাবে সক্রিয় হবে। বাংলা ভাষা নিয়ে বাংলাদেশের নিরানব্বই শতাংশ মানুষের আজকের যে সমস্যা, তা বাংলা ভাষা নিয়ে এই ভূভাগের সেদিনের নিরানব্বই শতাংশ মানুষের সমস্যার চেয়ে একটুও কম গুরুত্বপূর্ণ নয়। জনগণের জন্য সেদিনের এক শতাংশ লোকের ভূমিকার তুলনায় আজকের এক শতাংশ লোকের ভূমিকা একটুও কম ক্ষতিকর নয়। কেবল ভাষার ব্যাপারই নয়, জাতি ও রাষ্ট্রকে রক্ষা করার ও উন্নত করার ব্যাপারও এর সঙ্গে যুক্ত। সেদিনের সংগ্রামের অভিজ্ঞতা আমাদেরকে সাহসী, দূরদর্শী ও সংগ্রামী হতে বলে। বাধা-বিপত্তি মোকাবিলা করে এগোতে সাধারণত একটি দেশের স্থলভাগ ও অভ্যন্তরীণ জলাশয় সংবিধানে সার্বভৌমত্বের ধারণাকে প্রকাশ করে। এমনকি সংবিধান সম্পর্কে সম্যক জ্ঞানের অধিকারী নন (এমনকি যিনি অধিকারীও) তিনিও মনে করেন, মূলত স্থলভাগই হলো দেশটির সার্বভৌমত্বের ভৌগোলিক এলাকা। বাংলাদেশের ক্ষেত্রে ধরে নেয়া হচ্ছে, মোটামুটি উত্তর-পূর্বে ভারত, পশ্চিমে মিয়ানমার ও দক্ষিণে সমুদ্র-এ হলো আমাদের দেশের সীমানা। কিন্তু এ ধারণার আমূল পরিবর্তন ঘটে ১৯৮২ সালের ১০ ডিসেম্বর জ্যামাইকার মন্টেগু বেতে। এদিন ১১৯টি দেশের প্রতিনিধি প্রস্তাবিত আন্তর্জাতিক সমুদ্র আইনে স্বাক্ষরদান অনুষ্ঠানে সমবেত হন ও প্রথম দিনেই সম্মতিসূচক স্বাক্ষর প্রদান করেন। স্বাক্ষরদান অনুষ্ঠানে বাংলাদেশের প্রতিনিধিত্ব করেন রিয়ার অ্যাডমিরাল (প্রয়াত) এমএ খান, ব্যারিস্টার এএকেএম মুর্শেদ (পররাষ্ট্র মন্ত্রণালয়), মো. খোরশেদ আলম (নেভি) ও শাহেদ আকতার (পররাষ্ট্র মন্ত্রণালয়)। দেশগুলোর সংখ্যা শেষ পর্যন্ত ১৫৯-এ উন্নীত হয়। উপকূলীয় রাষ্ট্রের জাতীয় বৈধ কর্তৃত্ব বা সার্বভৌমত্ব দেশটির স্থলভাগ ও অভ্যন্তরীণ জলের সীমানা অতিক্রম করে কীভাবে ও কতখানি সাগরে সম্প্রসারিত হবে, সে বিষয়টি &lsquo;ল অব দ্য সি&rsquo;র মূল বিবেচ্য বিষয় হিসেবে গণ্য হয়েছে। উপকূলীয় রাষ্ট্র সমুদ্রে কতটা অঞ্চল পাবে বা রাষ্ট্রীয় সার্বভৌমত্ব সম্প্রসারিত হবে: আমরা আগেই দেখেছি, উপকূলীয় রাষ্ট্রগুলোর সমুদ্রে মালিকানা তথা দেশটির সার্বভৌমত্ব সম্প্রসারণই সমুদ্র আইনের কেন্দ্রবিন্দু। সমুদ্রকে সম্ভাব্য প্রাপ্য অঞ্চলের আলোকে ছয়টি জোনে ভাগ করা হয়েছে- অভ্যন্তরীণ জলে (ওহঃবৎহধষ ডধঃবৎ), রাষ্ট্রীয় সমুদ্র (ঞবৎৎরঃড়ৎরধষ ঝবধ), সংলগ্ন অঞ্চল (ঈড়হঃরমঁড়ঁং), একচ্ছত্র অর্থনৈতিক অঞ্চল (ঊীপষঁংরাব ঊপড়হড়সরপ তড়হব), মহীসোপন (ঈড়হঃরহবহঃধষ ঝযবষভ) ও রাষ্ট্রের এলাকাবহির্ভূত বিশাল সমুদ্র (ঐরময ংবধং)। সমুদ্র আইন উপকূলীয় রাষ্ট্রের সাগরে তার প্রাপ্য অঞ্চল হিসেবে মূলত তিনটি অংশে বিভক্ত ৩৫০ নটিক্যাল মাইল বা ৬৫০ কিলোমিটারের একটি &lsquo;প্যাকেজ&rsquo; ঘোষণা করে। রাষ্ট্রীয় সমুদ্র অঞ্চল ১২ নটিক্যাল মাইল + একচ্ছত্র অর্থনৈতিক অঞ্চল ১৮৮ নটিক্যাল মাইল + মহীসোপান ১৫০ নটিক্যাল মাইল = ৩৫০ নটিক্যাল মাইল। তবে একটি দেশ ইচ্ছে করলেই বর্ণিত অঞ্চল নিজের দখলে নিতে পারবে না। শর্তসাপেক্ষে এ বন্টন করা হয়েছে। অর্থাৎ তোমাকে মালিকানা দেয়া হলো বটে, তবে তা শর্ত মান্য করে অর্জন করে নিতে হবে।<br />\r\nকীভাবে সার্বভৌমত্বের অঞ্চল প্রাপ্য হবে: শর্তের প্রধান বিষয়বস্তু কী? দেখা গেছে, প্রায়ই উপকূলীয় রাষ্ট্রের যেমন পার্শ্ববতী দেশ বা দেশগুলো থাকে, তেমনি কখনো কখনো বিপরীতেও দেশ বা দেশগুলো থাকতে পারে। এ অবস্থায় সমুদ্র আইন আসলে দুটি বিধান রেখেছে- ক. আনক্লজ ৩-এর নির্দেশনার ভিত্তিতে দেশগুলো &lsquo;সমঝোতার&rsquo; মাধ্যমে নিজেদের মধ্যে চুক্তি করে সীমানা নির্ধারণের জন্য দ্বিতীয় দফাটি একচ্ছত্র অঞ্চল ও তৃতীয় দফাটি মহীসোপানের জন্য। এ তিনটি দফার মধ্যে দফা ১৫ দুটি কারণে অন্য দুটি থেকেসাধারণত একটি দেশের স্থলভাগ ও অভ্যন্তরীণ জলাশয় সংবিধানে সার্বভৌমত্বের ধারণাকে প্রকাশ করে। এমনকি সংবিধান সম্পর্কে সম্যক জ্ঞানের অধিকারী নন (এমনকি যিনি অধিকারীও) তিনিও মনে করেন, মূলত স্থলভাগই হলো দেশটির সার্বভৌমত্বের ভৌগোলিক এলাকা। বাংলাদেশের ক্ষেত্রে ধরে নেয়া হচ্ছে, মোটামুটি উত্তর-পূর্বে ভারত, পশ্চিমে মিয়ানমার ও দক্ষিণে সমুদ্র-এ হলো আমাদের দেশের সীমানা। কিন্তু এ ধারণার আমূল পরিবর্তন ঘটে ১৯৮২ সালের ১০ ডিসেম্বর জ্যামাইকার মন্টেগু বেতে। এদিন ১১৯টি দেশের প্রতিনিধি প্রস্তাবিত আন্তর্জাতিক সমুদ্র আইনে স্বাক্ষরদান অনুষ্ঠানে সমবেত হন ও প্রথম দিনেই সম্মতিসূচক স্বাক্ষর প্রদান করেন। স্বাক্ষরদান অনুষ্ঠানে বাংলাদেশের প্রতিনিধিত্ব করেন রিয়ার অ্যাডমিরাল (প্রয়াত) এমএ খান, ব্যারিস্টার এএকেএম মুর্শেদ (পররাষ্ট্র মন্ত্রণালয়), মো. খোরশেদ আলম (নেভি) ও শাহেদ আকতার (পররাষ্ট্র মন্ত্রণালয়)। দেশগুলোর সংখ্যা শেষ পর্যন্ত ১৫৯-এ উন্নীত হয়। উপকূলীয় রাষ্ট্রের জাতীয় বৈধ কর্তৃত্ব বা সার্বভৌমত্ব দেশটির স্থলভাগ ও অভ্যন্তরীণ জলের সীমানা অতিক্রম করে কীভাবে ও কতখানি সাগরে সম্প্রসারিত হবে, সে বিষয়টি &lsquo;ল অব দ্য সি&rsquo;র মূল বিবেচ্য বিষয় হিসেবে গণ্য হয়েছে। উপকূলীয় রাষ্ট্র সমুদ্রে কতটা অঞ্চল পাবে বা রাষ্ট্রীয় সার্বভৌমত্ব সম্প্রসারিত হবে: আমরা আগেই দেখেছি, উপকূলীয় রাষ্ট্রগুলোর সমুদ্রে মালিকানা তথা দেশটির সার্বভৌমত্ব সম্প্রসারণই সমুদ্র আইনের কেন্দ্রবিন্দু। সমুদ্রকে সম্ভাব্য প্রাপ্য অঞ্চলের আলোকে ছয়টি জোনে ভাগ করা হয়েছে- অভ্যন্তরীণ জলে (ওহঃবৎহধষ ডধঃবৎ), রাষ্ট্রীয় সমুদ্র (ঞবৎৎরঃড়ৎরধষ ঝবধ), সংলগ্ন অঞ্চল (ঈড়হঃরমঁড়ঁং), একচ্ছত্র অর্থনৈতিক অঞ্চল (ঊীপষঁংরাব ঊপড়হড়সরপ তড়হব), মহীসোপন (ঈড়হঃরহবহঃধষ ঝযবষভ) ও রাষ্ট্রের এলাকাবহির্ভূত বিশাল সমুদ্র (ঐরময ংবধং)। সমুদ্র আইন উপকূলীয় রাষ্ট্রের সাগরে তার প্রাপ্য অঞ্চল হিসেবে মূলত তিনটি অংশে বিভক্ত ৩৫০ নটিক্যাল মাইল বা ৬৫০ কিলোমিটারের একটি &lsquo;প্যাকেজ&rsquo; ঘোষণা করে। রাষ্ট্রীয় সমুদ্র অঞ্চল ১২ নটিক্যাল মাইল + একচ্ছত্র অর্থনৈতিক অঞ্চল ১৮৮ নটিক্যাল মাইল + মহীসোপান ১৫০ নটিক্যাল মাইল = ৩৫০ নটিক্যাল মাইল। তবে একটি দেশ ইচ্ছে করলেই বর্ণিত অঞ্চল নিজের দখলে নিতে পারবে না। শর্তসাপেক্ষে এ বন্টন করা হয়েছে। অর্থাৎ তোমাকে মালিকানা দেয়া হলো বটে, তবে তা শর্ত মান্য করে অর্জন করে নিতে হবে।<br />\r\nকীভাবে সার্বভৌমত্বের অঞ্চল প্রাপ্য হবে: শর্তের প্রধান বিষয়বস্তু কী? দেখা গেছে, প্রায়ই উপকূলীয় রাষ্ট্রের যেমন পার্শ্ববতী দেশ বা দেশগুলো থাকে, তেমনি কখনো কখনো বিপরীতেও দেশ বা দেশগুলো থাকতে পারে। এ অবস্থায় সমুদ্র আইন আসলে দুটি বিধান রেখেছে- ক. আনক্লজ ৩-এর নির্দেশনার ভিত্তিতে দেশগুলো &lsquo;সমঝোতার&rsquo; মাধ্যমে নিজেদের মধ্যে চুক্তি করে সীমানা নির্ধারণের জন্য দ্বিতীয় দফাটি একচ্ছত্র অঞ্চল ও তৃতীয় দফাটি মহীসোপানের জন্য। এ তিনটি দফার মধ্যে দফা ১৫ দুটি কারণে অন্য দুটি থেকে আলাদা । ১. অন্য দফা দুটিতে শর্তসাপেক্ষে ইকুইটেবল বা ন্যায়সঙ্গত সমাধান অর্জনের কথা বলা আছে এবং ২. চুক্তি না হলে বিবাদ নিষ্পত্তির জন্য অধ্যায় ১৫ মোতাবেক ব্যবস্থা নিতে পারবে। কিন্তু দফা ১৫, যা কিনা প্রযোজ্য শুধু রাষ্ট্রীয় সমুদ্র অঞ্চলের জন্য-বেজ লাইন থেকে ১২ নটিক্যাল মাইল পর্যন্ত। এখানে দেখা যাবে-১. ইকুইডিস্ট্যান্স বা সমদূরত্ব পদ্ধতিতে ১২ নটিক্যাল মাইলের সীমানা নির্ধারণ করতে হবে এবং ২. এ পদ্ধতিতে সীমানা নির্ধারণের বাধ্যবাধকতা থাকছে; কারণ বিবাদ দেখা দিলে তা নিষ্পত্তির জন্য অধ্যায় ১৫ মোতাবেক আদালত ও ট্রাইব্যুনালে যাওয়া যাবে না।<br />\r\nঅথচ দফা ৭৪, যা কিনা প্রযোজ্য একচ্ছত্র অর্থনৈতিক অঞ্চলের জন্য, যেখানে বলা হয়েছে-১.সীমানা নির্ধারণ কার্যকর হবে চুক্তির মাধ্যমে, যার ভিত্তি হবে আন্তর্জাতিক আইন ও সে আইন হবে আন্তর্জাতিক বিচার আদালতের বিধিবদ্ধ ধারা ৩৮ মোতাবেক এবং তা হবে ন্যায়সঙ্গত সমাধান অর্জনের জন্য। ২. &lsquo;যদি যুক্তিসঙ্গত সময়ের মধ্যে কোনো চুক্তিতে না নেয়া যায় বা ব্যর্থ হয়, তবে সংশ্লিষ্ট রাষ্ট্রপক্ষ অধ্যায় ১৫-এর শর্তাধীনে আইনগত বিষয়াদি নির্বাহে আশ্রয় নেবে&rsquo;। দফা ৮৩, যা নির্ধারিত করা হয়েছে মহীসোপানের জন্য, সেখানেও গিয়ে দেখা যাবে, দফা ৭৪ ও দফা ৮৩-এর বিষয়বস্তু হুবহু এক; সমঝোতাভিত্তিক চুক্তি, নতুবা আদালত কিংবা ট্রাইব্যুনাল।<br />\r\n২০১৩ সালের ১৪ মার্চ বাংলাদেশের জন্য এক বিরল তাৎপর্যপূর্ণ ঘটনা হলো, জার্মানির হামবুর্গে অবস্থিত সমুদ্রবিরোধ নিষ্পত্তির জন্য গঠিত আন্তর্জাতিক ট্রাইব্যুনাল কর্তৃক ঘোষিত রায়। এ রায়ের মধ্য দিয়ে যেমন বাংলাদেশ- মিয়ানমার সমুদ্রবিরোধের নিষ্পত্তি হয়েছে, তেমনি ১ লক্ষ ১১ হাজার বর্গকিলোমিটার সামুদ্রিক এলাকার ওপর আমাদের সার্বভৌমত্ব প্রতিষ্ঠা হয়েছে।<br />\r\n&lsquo;বাংলাদেশ একটি নদীমাতৃক দেশ&rsquo; ধারণাটি ভ্রান্ত হিসেবে অনেক আগেই অর্থাৎ ১৯৮২ সালে প্রতিষ্ঠিত হয়েছে। আন্তর্জাতিক সমুদ্র আইন ওই বছর সর্বসম্মতক্রমে গৃহীত হয়। এখন বেশ জোর দিয়ে বলা যাবে, বাংলাদেশ একটি সমুদ্র ও নদীমাতৃক দেশ। অথচ এর কোনো পরিচিতি নেই। এর কারণ এ ব্যাপারে প্রচার নেই। নদীবিষয়ক রচনা ষষ্ঠ শ্রেণী থেকেই চালু আছে সেই ব্রিটিশ আমল থেকে। কিন্তু বাংলাদেশের দক্ষিণ দিকে সমুদ্র আছে। সমস্যা সীমানা-সংক্রান্ত। এর চির অবসান প্রয়োজন। প্রথম থেকেই স্কুল পাঠ্যসূচিতে সমুদ্রকে অন্তর্ভুক্ত করার দাবি জানিয়ে আসছি। তবে কেউ কর্ণপাত করেননি।<br />\r\n&lsquo;স্থলভাগের প্রধান প্রধান সম্পদ ক্রমান্বয়ে নিঃশেষ হবে, সমুদ্রই একমাত্র ভরসা&Ntilde;এটি মিয়ানমার পর্যন্ত দ্রুতগতিতে উপলব্ধিতে এনেছে বলে মনে হচ্ছে; কিন্তু বাংলাদেশে একমাত্র সমুদ্রে তেল-গ্যাসের ব্লক বরাদ্দ নিয়ে উত্তেজনা ছাড়া অন্য সব বিষয়েই চুপ। আমাদের সমুদ্রে রয়েছে অঢেল সম্পত্তি। এ মুহূর্তে সমুদ্র সম্পদের হিসাব-নিকাশ ও কাজে লাগানো গেলে দরকার হবে &lsquo;সমুদ্রবিদ্যা।<br />\r\nসমুদ্রবিদ্যা বা সমুদ্রবিজ্ঞান সম্পর্কে ধারণা : সমুদ্রবিজ্ঞান বলতে সমুদ্রের সঙ্গে জড়িত সব বিজ্ঞানের সমষ্টিকে বুঝতে হবে। সমুদ্রবিজ্ঞানের বিভিন্ন শাখা আছে। প্রাকৃতিক সমুদ্রবিদ্যা: সমুদ্র জলের প্রাকৃতিক গুণাবলি নির্ণয়। যেমন&Ntilde;পানির তাপমাত্রা, সামুদ্রিক সঞ্চার ঢেউ। রাসায়নিক সমুদ্রবিদ্যা: সমুদ্রের রসায়নতত্ত্ব এক জটিল বিষয়। সমুদ্র পানির উপাদান কীভাবে এল, কীভাবে আছে এবং কত দিন থাকবে ও কোথায় যাবে&Ntilde;এসব বিষয় নিয়ে আলোচনা। যেমন&Ntilde;সমুদ্রের পানির সংযুক্তি, লবণাক্ততা, সমুদ্রের নিক্ষেপ, পুষ্টিকর উপাদানের চক্র প্রভৃতি।<br />\r\nজৈব সমুদ্রবিদ্যা : সমুদ্রের বিভিন্ন জীবকের বিভাজন। সমুদ্রের তাপমান চাপ, &macr;্রােত, পুষ্টিকর উপাদান ও আলো প্রবেশের ওপর নির্ভর করে। তাছাড়া সমুদ্রতলের বিভিন্ন পলির চরিত্রও গুরুত্বপূর্ণ। যেমন-জৈব অঞ্চল, সমুদ্র পরিবেশকে দুই ভাগে ভাগ করা হয়েছে। বেন্থিক অর্থাৎ সমুদ্র তলদেশের পরিবেশ, পেলাজিক অর্থাৎ জলীয় পরিবেশ। তুলনামূলকভাবে বেন্থিক পরিবেশ সাবলিটোরাল অঞ্চলে পাওয়া যায় সবচেয়ে বেশি প্রাণী। অন্যদিকে অ্যাবিটাল (নিতলীয়) অঞ্চলের প্রাণীর প্রাচুর্য বাধা পেয়েছে প্রচণ্ড চাপ, অন্ধকার ও খাদ্যের ঘাটতির জন্য। প্রকটন: যেসব জীবক ভাসে, ভাসতে ভাসতে এক জায়গা থেকে অন্য জায়গায় যায় অর্থাৎ যাদের সাঁতারের ক্ষমতা খুব কম, তারা প্রকটন।<br />\r\nনেকটন: মাছসহ অন্য কয়েকটি প্রাণী মিলে নেকটন। এরা অবশ্যই সাঁতার কাটতে পারে। সাধারণত মাছ, হাঙর, সামুদ্রিক কচ্ছপ, সামুদ্রিক শামুক, স্তন্যপায়ী প্রাণী (ম্যামাল) এ শ্রেণীর অন্তগর্ত।<br />\r\nবেনসব: সমুদ্রতলে বসবাস করা জীবকদের বেনসব বলে। এর মধ্যে মেরুদন্ডী প্রাণী অনুপস্থিত। সমুদ্রতলের সঙ্গে একদম লেগে থাকা প্রাণী ও গাছপালারা সেসাইল এবং যেগুলো ঘুরে বেড়ায় তারা ভ্যাগ্রাস।<br />\r\nভূবৈজ্ঞানিক সমুদ্রবিদ্যা বা সমুদ্র ভূবিজ্ঞান : সাগর, মহাসাগর ও উপকূলবতী অঞ্চলের সমুদ্রতল মূলত খনিজ সম্পদে পূর্ণ। তাই সামুদ্রিক পলি, তার সংযুক্তি বিভাজন ও উৎসের অধ্যয়ন ছাড়া সমুদ্রবিদ্যা অধ্যয়ন সম্ভব নয়।<br />\r\nসামুদ্রিক পলি : গ্রন্থন ও সংযুক্তির ভিত্তিতে পলির শ্রেণী বিভাগ করা হয়। এ দুটি দিয়েই পলির উৎস ও গঠনের পরিবেশ বোঝা যায়। পলি তৈরির স্থানে জালের গতির সঙ্গে পলির আন্তঃক্রিয়া বোঝা যায় প্রথম থেকে। মহাদেশগুলোর অবক্ষয়ের ফলে প্রচুর পরিমাণে পলি জমা হচ্ছে। নদীর জলে বয়ে এসে সেই পলি জমা হয় মোহনার কাছে, সমুদ্র উপকূলে ও মহাদেশীয় সোপানে। কিছু চলে যায় একেবারে সমুদ্রতলে। উপকূলবতী পাললিক পরিবেশ: সমুদ্রের উপকূলবতী এলাকার পরিবেশের বৈচিত্র্য রয়েছে। এ এলাকার পলির গঠন সমুদ্র ও ভাঙন দুই পরিবেশ দিয়েই এ পরিবেশ পরস্পর থেকে আলাদা, দুটির মাঝামাঝি প্রভৃতি।<br />\r\nমহাদেশীয় উপান্তপাল: নদী সমুদ্রে হারিয়ে যাওয়ার আগে নদীবাহিত পলি প্রচুর পরিমাণে এসে জমা হয় মহাদেশীয় প্রান্তে। বালুকণার বেশকিছু জমা কাটিয়ে যায় সৈকত বা উপকূলবতী পরিবেশে। পলির বিভাজনের চরিত্র খুব জটিল আকার ধারণ করেছে গত ১০ লাখ বছরে।<br />\r\nগভীর সমুদ্রের পলি : মহাদেশীয় উপান্ত অঞ্চলের পলি সম্পর্কে আমরা যতটা জানি, সে তুলনায় গভীর সমুদ্রতলে জমে থাকা পলি সম্পর্কে এখনো জ্ঞান খুব অল্প। উনবিংশ শতাব্দীর মাঝামাঝি প্রথম সমুদ্রতলে নেমে নমুনা সংগ্রহ করা সম্ভব হয়; কিন্তু এ ধরনের নমুনা পরীক্ষার সব রকম সুবিধা গড়ে উঠতেই লেগে গেল প্রায় ১০০ বছর।<br />\r\nসমুদ্র ভূপদার্থবিজ্ঞান: পৃথিবীকে তিনটি স্তরে ভাগ করা হয়&Ntilde;ত্বক, আস্তরণ ও মূল। ত্বক দুই রকম&Ntilde;মহাদেশীয় (গ্রানিটিক) ও সামুদ্রিক (ব্যাসাল্টিক)। ছেদনের কথা বাদ দিলে পৃথিবীর নিচের স্তরগুলো সম্পর্কে জানার জন্য আমাদের মূলত পরোক্ষ অনুসন্ধানের ওপর নির্ভর করতে হয়। পরোক্ষ অনুসন্ধান বলতে প্রধানত ভূপদার্থ বিজ্ঞানের কথা বোঝায়। সমুদ্রের নিচের স্তরের অনুসন্ধানে ভূপদার্থ বৈজ্ঞানিক সমীক্ষার প্রয়োজনীয়তা অপরিসীম।<br />\r\nআধুনিক ভূসংগঠনবিদ্যা: মৃৎ ভৌতবিদ্যা, শিলা ভৌতবিদ্যা ও প্রযুক্তিগত ভূবিদ্যা ভূসংগঠন বিদ্যার অন্তগর্ত। উপরে যে বর্ণনা দেয়া হলো, তা আমাদের জীবনে সমুদ্রের যে বিশাল গুরুত্ব রয়েছে, তা অনুমান করা মাত্র। এজন্য দরকার- ক. অনতিবিলম্বে সমুদ্রবিষয়ক একটি মন্ত্রণালয়। নানা কারণে দেরি হতে পারে, এ আশঙ্কায় দ্বিতীয় প্রস্তাব হলো-খ. পূর্ণাঙ্গ সমুদ্র পরিদফতর; আন্তর্জাতিক মানের সমুদ্রবিদ্যা ইনস্টিটিউট বা সমুদ্রবিজ্ঞান প্রতিষ্ঠান গড়ে তোলা এবং শিক্ষার্থীদের এটা নিশ্চিত করতে হবে যে, তারা তাদের শিক্ষা অনুযায়ী কর্মজগতে প্রবেশ করতে পারবে।<br />\r\nবর্ণিত বিষয়ে দেশে বিশেষজ্ঞরা রয়েছেন; কিন্তু তাদের অনেকেই প্রতিটি কাজের জন্য নগদ, উন্নতির ফান্ড (প্রকল্প) বা বিদেশ গমনের সামান্য সব সুবিধা (তাদের দৃষ্টিতে) পেলে অস্তিত্ব গোটাবেন।<br />\r\nসূত্র : ১. দ্য ল অব দ্য সি, জাতিসংঘ; ২. সমুদ্র, সুবীর দত্ত, আনন্দ পাবলির্শাস, কলকাতা।</span></p>\r\n','',1,15,'2016-01-07 03:17:34',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452158254_04.png'),(22,'কয়লার যথাযথ ব্যবহার : জাতীয় সম্পদ রক্ষা প্রসঙ্গে। বি.ডি.রহমতউল্লাহ্','কয়লার-যথাযথ-ব্যবহার-:-জাতীয়-সম্পদ-রক্ষা-প্রসঙ্গে।-বি.ডি.রহমতউল্লাহ্','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশে অত্যন্ত উঁচুমাণের কয়লা আছে। উত্তর-পশ্চিমাংশের বিস্তৃত এলাকায় ভূমির তলানীতে বিভিন্ন গভীরতায় মধ্যম সাইজের যা অত্যন্ত সহজে ব্যবহার উপযোগী সে আকারের কয়লা খণ্ড প্রাপ্তব্য বলে সিসমিক সার্ভের মাধ্যমে প্রমাণিত। এ কয়লা অত্যন্ত উঁচুমানের এবং উচ্চ তাপযুক্ত যা বিশ্বে প্রাপ্ত যে কোন কয়লার তুলনায় শ্রেষ্ঠমানের বলে বিধৃত। এ কয়লাগুলোকে বিটুমিনাস ও সাব-বাংলাদেশে অত্যন্ত উঁচুমাণের কয়লা আছে। উত্তর-পশ্চিমাংশের বিস্তৃত এলাকায় ভূমির তলানীতে বিভিন্ন গভীরতায় মধ্যম সাইজের যা অত্যন্ত সহজে ব্যবহার উপযোগী সে আকারের কয়লা খণ্ড প্রাপ্তব্য বলে সিসমিক সার্ভের মাধ্যমে প্রমাণিত। এ কয়লা অত্যন্ত উঁচুমানের এবং উচ্চ তাপযুক্ত যা বিশ্বে প্রাপ্ত যে কোন কয়লার তুলনায় শ্রেষ্ঠমানের বলে বিধৃত। এ কয়লাগুলোকে বিটুমিনাস ও সাব-বিটুমিনাস মূলত: এ দুভাগে ভাগ করা যায়। বাংলাদেশে প্রাপ্ত কয়লাসমূহের রাসায়নিক গুণাগুনসমূহ নীচের টেবিলে পাঠকদের অবগতির জন্য উদ্ধৃত করা হলো:</span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">কয়লার ধরণ</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">কার্বনের পরিমাণ(%)</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">H2O এর পরিমাণ (%)</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">H এর পরিমাণ (%)</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">O এর পরিমাণ (%)</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">উরে যাবে এমন রূপান্তর যোগ্য পদার্থ(%)</span></td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বায়বীয়</span></p>\r\n\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">পদার্থ(%)</span></p>\r\n			</td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">তাপযোগ্য পদার্থ &ndash;কিলোগ্রাম / কেজি</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">বটূমিনাস</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৭৭-৮৭</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৫-১০</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৪-৫</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">১০-১৫</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৪২-২৯</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৮</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৩৬২৫০</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">সাব-বটূমিনাস</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৭১-৭৭</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">২৫-৩০</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৫-৬</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">১৫-২০</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">৪৯-৪২</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">২৫-১০</span></td>\r\n			<td style=\"text-align: justify;\"><span style=\"font-size:16px\">২৯৩০০</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এর যে উপাত্তগুলি কয়লার গুণাগুণ নির্ধারণ করে সেগুলো হলো তাপযোগ্য পদার্থ ঈধষড়ৎরভরপ ঠধষঁব /কএ এবং উচ্চমাত্রার কার্বনের উপস্থিতি, নিম্নমাত্রার বায়বীয় (ঐ ও ঙ) বাষ্পীয় (ঐ২ঙ) পদার্থ। উপরের ছকে স্পষ্টতই প্রমাণ করেছে বাংলাদেশের কয়লা কত উঁচুমানের। সিসমিক সার্ভে থেকে এটাও প্রতিফলিত যে বাংলাদেশের এ কয়লা প্রায় ২৫ থেকে ২৮ কোটি বছরের সঞ্চিত কার্বন থেকে সৃষ্ট। আর বলাই বাহুল্য যে দীর্ঘ যুগের রাসায়নিক প্রক্রিয়ার সৃষ্ট কয়লা অত্যন্ত উঁচুমানের হয়ে থাকে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">শুধু উচ্চ মানই নয়, বাংলাদেশে এ পর্যন্ত আবিষ্কৃত কয়লার পরিমাণও অনেক বিশাল, যার একটি হিসাব নীচের ছকে দেয়া হলো:</span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">স্থান</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">আবিষ্কারের বছর</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কূপের সংখ্যা</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">গভীরতা (ফুট)</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">প্রমাণিত মজুদ (কোটি টন)</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বর্তমান অবস্থা</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বড়পুকুরিয়া, দিনাজপুর</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯৮৫-৮৭</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩১</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩০৬-১৫৩০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩৯.০০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">উন্নয়ন চলছে।</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">খালাসপীর, রংপুর</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯৮৯-৯০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৪</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৭৮০-১৪৫০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৬৯.০০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কাজ শুরু হয়নি।</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ফুলবাড়ি, দিনাজপুর</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯৯৭</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৪৫০-৭২০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৪০.০০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ঐ</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">জামালপুর, জয়পুরহাট</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯৬২</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯২০-৩৪৮০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১০৬.০০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ঐ</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">দীঘিপাড়া, দিনাজপুর</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৯৯৪-৯৫</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১১৯০-১২২১</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৫০.০০</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ঐ</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সূত্রঃ পেট্রোবাংলা</span></p>\r\n			</td>\r\n			<td style=\"text-align: justify;\">&nbsp;</td>\r\n			<td style=\"text-align: justify;\">&nbsp;</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">মোটঃ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩০৪.০০</span></p>\r\n			</td>\r\n			<td style=\"text-align: justify;\">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এছাড়া আরো প্রায় সমপরিমাণ কয়লা জয়পুরহাট, নীলফামারী ও রংপুরসহ কয়েকটি জেলায় যে আছে সে হিসেব সিসমিক সার্ভে হয়নি বলে নিশ্চিত করা যাচ্ছে না। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">যদি ধরা যায় এ পুরো কয়লাই উত্তোলন এবং ব্যবহার করা যাবে, তাহলে বর্তমানে বড়পুকুরিয়ার কয়লা দিয়ে বিদ্যুৎ উৎপাদনের হারকে মান ধরলে, আমাদের দেশে প্রাপ্ত কয়লা দিয়ে শতকরা ১০০% ভাগই যদি অন্য কোন কাজে ব্যবহার না করে শুধুমাত্র বিদ্যুৎ উৎপাদন করতে ব্যবহার করা হয় তাহলে এ ৩০৪.০০ কোটি টনে ১২০০০ মেঃ ওয়াট (উদাহরণ হিসেবে ধরা হলো, যা আজকের ক্ষমতার ৩ গুণ) বিদ্যুৎ উৎপাদন করলে মোট ৬৭ বছর চালানো যাবে। সুতরাং এ থেকে এ কথা নিশ্চিত ভাবে বলা যাচ্ছে যে বাংলাদেশ শুধুমাত্র গ্যাস ও অন্যান্য জ্বালানী সম্পদের প্রাপ্তিতেই নয়, কয়লার মজুদেও অত্যন্ত সমৃদ্ধ ।<br />\r\nএ উঁচুমানের ও সমৃদ্ধ পরিমানে জ্বালানীসম্পদের সম্ভাব্য প্রাপ্তিই সাম্রাজ্যবাদী ও বহুজাতিক কোম্পানিসমূহ বাংলাদেশের উপর ঝাঁপিয়ে পড়তে আগ্রাসী হয়ে উঠেছে ।<br />\r\nকিন্তু প্রশ্ন এ কয়লা বিদ্যুৎ উৎপাদন করতে কিভাবে উত্তোলন করা যাবে। কয়লা উত্তোলনে পৃথিবীতে নীচের ৩টি পদ্ধতি প্রচলিত :<br />\r\n(১) ওপেন পিট মাইনিং&Ntilde;অর্থাৎ ভূ-উপরিভাগের সমস্ত মাটি খুবলে এলাকা বিরান করে কয়লা উত্তোলনকরে কয়লাকে ব্যবহার উপযোগী করে ব্যবহার করা;<br />\r\n(২) আন্ডার গ্রাউন্ড মাইনিং অর্থাৎ গর্ত খুঁড়ে সুড়ঙ্গ বানিয়ে নীচ থেকে কয়লার খন্ডগুলো কেটে নীচ থেকে উপরে নিয়ে এসে ব্যবহার উপযোগী করে ব্যবহার করা;<br />\r\n(৩) গ্যাসিফিকেশন&Ntilde;অর্থাৎ মাটি খুঁড়ে কয়লা না তুলে নীচে কয়লাকে রাসায়নিক বিক্রিয়ার মাধ্যমে তৈল/গ্যাসে রূপান্তর করে তা পাম্প করে বা চাপসৃষ্টি করে চাপ দিয়ে উপরে এনে প্রয়োজন অনুযায়ী ব্যবহার করা;<br />\r\nকয়লা ব্যবহারে বাংলাদেশে যা করণীয় পদ্ধতিগুলো সম্পর্কে চিন্তা করলে বুঝা যাচ্ছে যে, প্রথম দুটো পদ্ধতিতে ভূমি বা জমিকে নষ্ট করে কয়লা ব্যবহার করতে হবে। আর বাংলাদেশের ক্ষেত্রে প্রথম দুটোর একটিও কোন কার্যকরী পদ্ধতি নয় কারণ বাংলাদেশের জমি/ভূমি হচ্ছে নদীবাহিত পলিমাটি দিয়ে গঠিত। বাংলাদেশ ব-দ্বীপ এলাকার পলিবাহিত অঞ্চল বিধায় এর ভূমি বা জমি অত্যন্ত পলল (অষষাঁরধষ) । পলিমাটি দিয়ে গঠিত এ ধরনের ভূমি এবং বিশেষতঃ বাংলাদেশের ক্ষেত্রে উত্তর থেকে দক্ষিণাঞ্চল সমুদ্রতট থেকে উচ্চতা মাত্র ৩ মিটার বিধায় এবং প্রায় পুরোটাই সমতট, সেহেতু সমগ্র এলাকা জুড়েই ভূ-অভ্যন্তরস্থ আন্তঃযোগাযোগের মাধ্যমে পানি প্রবাহিত হয়। এ সব কিছু মিলেই বাংলাদেশের মাটি বিশ্বের শুধু উর্ব্বরাই নয়,এর উপর দিয়ে প্রবাহিত পানি এ অঞ্চলের মানুষকে যেমন বাঁচিয়ে রেখেছে আবার তেমনি এ পানিতেই লক্ষ লক্ষ টন অত্যন্ত সু-স্বাদু মাছ উৎপাদিত হয় । পৃথিবীর অনেক দেশেই এমন ভাবে বিন্যন্ত সমতল ভূমি যেমন নেই, আবার ভূ-অভ্যন্তরস্থ পথ দিয়ে এক এলাকার সাথে আরেক এলাকার পানি প্রবাহের এমন নিবিড় যোগাযোগও নেই । পলিবাহিত মাটির দ্বারা গঠিত এ ধরনের ভূমিতে ওপেনপিট-তো বটেই ভূ-অভ্যন্তরস্থ পদ্ধতিতে কয়লা উত্তোলন করলেও ভূমি ধস এবং সার্ব্বিক ভাবে ভূ-বিপর্যয় অনিবার্য। যা বড়পুকুরিয়া কয়লাক্ষেত্রে আন্ডারগ্রাউন্ড মাইনিং করে কয়লা উত্তোলনের প্রতিক্রিয়া হিসেবে সর্ব্বনাশা ভূমি ধসের মাধ্যমে ইতোমধ্যে পরিষ্কারভাবে প্রতিফলিত। এছাড়াও বড়পুকুড়িয়া কয়লাক্ষেত্র এলাকায় অত্যন্ত স্বাভাবিক কারণেই ইতোমধ্যে ভূ-গর্ভস্থ পানির স্তর যেমন নেমে গেছে, তেমনি বায়ূ দূষণের প্রতিক্রিয়াও স্পষ্টতই প্রতিফলিত ।<br />\r\nসুতরাং স্পষ্টতই দেখা যাচ্ছে বাংলাদেশের ভূমি গঠন মাটি খুঁড়ে কোন ধরনের কয়লা উত্তোলনে সহায়ক নয় তা সে ওপেন পিটই হোক কিংবা আন্ডার গ্রাউন্ডই হোক। তাহলে বাংলাদেশে মজুদ এ অফুরান উন্নতমানের কয়লা কি কোন কাজেই লাগবে না? অবশ্যই লাগবে । কিন্তু হিসেব কষতে হবে ওপেনপিট কিংবা ভূ-অভ্যন্তরস্থ পদ্ধতিতে কয়লা উত্তোলন করে তার মূল্য (প্রত্যক্ষ, পরোক্ষ এবং মূল্য সংযোজন সহ) যদি ভূমি ধস, বায়ূ দূষণ, পানির স্তর নেমে গিয়ে সার্&Yuml;িক ক্ষতির পরিমাণ, জমির উর্ব্বরা শক্তি, জমি হারিয়ে উদ্বাস্তু হওয়া, মরুকরণের প্রতিক্রিয়ায় যে মূল্য দিতে হবে তা যদি অনেক অনেক কম হয় তাহলে এ ধরনের পদ্ধতি অবলম্বন করে কয়লা উঠানোর পিছনে কার স্বার্থ কাজ করে, এ খুব সূক্ষ্মভাবে হিসেব করতে হবে এবং নিবিড়ভাবে ভেবে দেখতে হবে।<br />\r\nভূ-গর্ভস্থ কয়লা গ্যাসকরণ পদ্ধতিটি কয়লা আহরণের কারিগরি ও আর্থিকভাবে সফল পন্থা অবলম্বন করে পৃথিবীর অনেক কয়টি দেশ যেমন চীন, রাশিয়া,অস্ট্রেলিয়া বেশ সফল হয়েছে। চীন সম্প্রতি ভূ-গর্ভস্থ কয়লা গ্যাসকরণ কয়লা আহরণ পদ্ধতিটির বিষয়ে শুধু বিস্ময়কর সফলতাই অর্জন করেনি অতিসম্প্রতি বিশ্বের বহুদেশের সংগে বিশেষ করে রাশিয়ার সংগে এই অভিনব পদ্ধতিতে কয়লা আহরণ চুক্তিও করেছে। এই পদ্ধতিতে ভূ-গর্ভস্থ কয়লা সরাসরি তরলীকরণ থেকে গ্যাসে পরিণত করা হয়। এই গ্যাস সরাসরি বিদ্যুৎ উৎপাদন ও রাসায়নিক কারখানায় ব্যবহার করা যায়।<br />\r\nভূ-পৃষ্ঠে কূপ সৃষ্টি করে তরলীকরণ ও গ্যাসে পরিনতকরনের জন্য কিছু যৌগিক ও মৌলিক সহজপ্রাপ্য রাসায়নিক উপাদান (ঐ২ঙ, ঙ২ ইত্যাদি) কয়লার সংগে মিশানো হয়। নির্দিষ্ট তাপমাত্রা ও চাপে অক্সিড্যান্ট কয়লার সংগে বিক্রিয়া ঘটায় যার ফলে কয়লা গ্যাসে পরিবর্তিত হয়। এর ফলে সৃষ্ট তাপও কয়লা পদ্ধতি ও কৌশলের বিভিন্ন ধাপে ব্যবহার করা হয়ে থাকে। কয়লার মান, অবস্থান ও ভূ-প্রকৃতি বিবেচনা করে বিভিন্ন ধরনের অক্সিড্যান্ট ব্যবহার করা হয়। বায়ুসহ বিভিন্ন গ্যাসের মিশ্রণও ব্যবহার করা হয়ে থাকে। এ বিষয়ে একটি সহজবোধ্য চিত্র নীচে দেয়া হলো ।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><a href=\"http://jatioshompodrokkha.com/wp-content/uploads/2015/01/koyla1.jpg\"><img alt=\"koyla1\" class=\"alignnone appear disappear size-medium wp-image-1615\" src=\"http://jatioshompodrokkha.com/wp-content/uploads/2015/01/koyla1-300x184.jpg\" style=\"height:184px; width:300px\" /></a></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ভূ-গর্ভস্থ কয়লা তরলীকরণ/ গ্যাসীকরণ প্রক্রিয়াসমূহ কয়লার অবস্থান, গুণাগুণ, ভূতাত্তিক পরিস্থিতিসমূহের উপরে নির্ভর করে নির্ধারিত হয়ে থাকে। এই আহরণ পদ্ধতি সর্বাধূনিক কুপনির্মান পদ্ধতি ব্যবহার কওে করা হয়। কুপ খনন ও কুপের চরিত্র নির্ধারিত হয়ে থাকে অক্সিড্যান্টের বৈশিষ্টের উপরে (শুদ্ধ বায়ূ , H2O/ O2,CO2/ O2 ইত্যাদি অক্সিড্যান্টের ব্যবহারের উপরে) ।<br />\r\nবৈশিষ্টপূর্ণ ও শুদ্ধ প্রক্রিয়া অবলবম্বনের জন্য কিছু সূচক নীচে দেয়া হলো ঃ<br />\r\nকোল গ্যাসিফিকেশনে সাইট নির্ধারণে নির্বাচনী সূচকঃ</span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: justify;\">&nbsp;</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">মান</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: justify;\">&nbsp;</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ন্যূনতম</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সর্বোচ্চ</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কয়লার পুরত্ত্ব</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৫ ফুট</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৫০ ফুট</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">গভীরতা</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১৫০০ ফুট</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩০০০ ফুট</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কয়লার ধরণ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">লিগনাইট</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বিটুমিনাস</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ভূগর্ভস্থ অবকাঠামোর বিপর্যয়</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৬০০ ফুট বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বায়বীয় পদার্থের পরিমাণ</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৪০% বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এ্যাশের পরিমাণ</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৫০% বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাষ্পীয় ও এ্যাশযুক্তভাবে</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৫০% বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ক্যালেরোফিক মান</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১২ মিলিয়ন জুল / কেজি</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">সালফার</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৬% বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ক্লোরাইন</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">৩% বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">কয়লার পরিমাণ</span></p>\r\n			</td>\r\n			<td colspan=\"2\">\r\n			<p style=\"text-align: justify;\"><span style=\"font-size:16px\">১০০ মিলিয়ন টন বা তার নিচে</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এ থেকে স্পষ্টতই প্রতীয়মান হচ্ছে যে নিম্নমান থেকে উচুমান, অল্প গভীরতা থেকে দীর্ঘ গভীরতা সকল ধরনের কয়লাই আহরণ যোগ্য। জটিল ভূ-তাত্ত্বিক বাধাবিঘ&oelig; অতিক্রম করাও এ পদ্ধতিতে সম্ভব। পানি বেষ্টিত বা পানির উপরে অবস্থিত কয়লা উভয়ই আহরণ করা সম্ভব।<br />\r\nএ প্রক্রিয়ার জন্য:<br />\r\n(১) বহিস্থ: জ্বালানী বা পানির প্রয়োজন হবে না।<br />\r\n(২) গ্যাস নির্গমনের ক্ষেত্রে কোন বাধা বা প্রতিবন্ধকতা সৃষ্টি হবে না।<br />\r\n(৩) অনেক শক্তি কেন্দ্রের (রিএ্যাকটর) অবস্থানের ফলে বিভিন্ন গ্যাস মিশ্রণ ঘটিয়ে গ্যাসের মান নিয়ন্ত্রণ সম্ভব হবে।<br />\r\n(৪) ছাই বা স্ল্যাগ নিয়ে ভাবনা করতে হবে না, যেগুলো ভূগর্ভেই থেকে যাবে।<br />\r\n(৫) ভূ-গর্ভস্থ পানি রিএ্যাক্টর বা শক্তি কেন্দ্রগুলোর তাপ নিয়ন্ত্রণে ব্যবহৃত হবে। ফলে তাপ ক্ষয়ের মাত্রা খুব কম থাকবে।<br />\r\n(৬) ভূ-গর্ভস্থ পানি গর্তগুলোর মধ্যে প্রবাহিত হওয়ার ফলে ভূ-গর্ভস্থ পরিবেশের কোন ক্ষতি হবে না।<br />\r\nগ্যাসকরণ পদ্ধতির সুবিধাসমূহ<br />\r\n* কার্বন ডায়-অকসাইড এর পরিমাণ প্রক্রিয়া পদ্ধতির উপরে নির্ভরশীল। সে যাই হোক, ভূ-গর্ভস্থ এ গ্যাস আলাদা করা সম্ভব এবং এতে খুব সামান্য শক্তি ক্ষয় হবে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">* এই কার্বন-ডাই অকসাইড ভূ-গর্ভেই নিরাপদ সংরক্ষণ সম্ভব।<br />\r\n* কার্বন-ডাই অকসাইড এর বর্জ্য নিরাপদ সংরক্ষণ, বা তেল, গ্যাস উত্তোলনের জন্য পুনঃপুনঃ ব্যবহার করা সম্ভব।<br />\r\n* গ্যাসকরণ পদ্ধতি তৈরি কয়লা-গ্যাস সরাসরি বিদ্যুৎ উৎপাদনে ব্যবহার করা যায়। এতে কয়লা ব্যবহার সাফল্য ৫৫%, গড় সাফল্য ৪৩% (ন্যূনতম)। এর অর্থ হচ্ছে পরিবেশ দূষণ খুবই সামান্য।<br />\r\n* কয়লা ব্যবহার উৎপাদনের সংগে জড়িত সকল প্রকার পরিবেশদূষণের ক্ষেত্রে এই পদ্ধতির সাফল্য অপ্রতিদ্বন্দ্বী। কয়লা উত্তোলনের পরবর্তী নির্মল জ্বালানী তৈরির জন্য কয়লা তরলীকরণ প্রকৌশল<br />\r\nপূর্বাহ্নেই ব্যাপকভাবে সুফল দেখিয়েছে। দক্ষিণ আফ্রিকা, অস্ট্রেলিয়াতে কয়েকটি শিল্পসংস্থা ইতিমধ্যেই তরলীকরণ ও গ্যাসকরণ পদ্ধতিতে কয়লা ব্যবহার করছে। এতে কয়লার ব্যবহারিক মূল্য ৬০০ গুণবৃদ্ধি পেয়েছে এবং পরিবেশ সংরক্ষণ সম্ভব হয়েছে। অদূর ভবিষ্যতে চীনাদের দ্বারা সফল উন্নত প্রকৌশল পদ্ধতি সারাবিশ্বে ছড়িয়ে পড়বে। ইতিমধ্যে চীনা কোম্পানি আমেরিকাসহ বিশ্বের ১৪টি দেশে প্যাটেন্ট সংরক্ষণা পদক্ষেপ নিয়েছে এবং রাশিয়াতে বিশাল প্রজেক্ট হাতে নিয়েছে। চীন দ্রুতই বিপুল সংখ্যক কয়লাখনি বন্ধ করে দেবে এবং বড় আকারে নতুন প্রকৌশল ভিত্তিক কয়েকটি খনি প্রতিষ্ঠা করার পদক্ষেপ নিবে বলে ঘোষণা দিয়েছে।<br />\r\nগ্যাসকরণ পদ্ধতিতে কয়লা ব্যবহারের স্বার্থকতা<br />\r\n* ভূ-পূষ্ঠের কোন ক্ষয়-ক্ষতি ঘটবে না। খোলা বা ভূ-গর্ভস্থ চলতি উত্তোলন পদ্ধতির ক্ষেত্রে যা ঘটে চলেছে সেসব সংকট থেকে বাঁচা যাবে। এ্যাসিড রেইন প্রবাহ থেকে নিস্তার পাওয়া যাবে।<br />\r\n* কয়লা গুদাম বা সংরক্ষণ ও পরিবহন প্রয়োজন হবে না। সংরক্ষণ একবারেই অবান্তর। কয়লা থেকে উৎপাদিত তেল ও গ্যাস পরিবহনের জন্য পাইপ-লাইন পদ্ধতি প্রয়োজন পড়বে যেমনভাবে বর্তমানে গ্যাস পরিবহন করা হচ্ছে।<br />\r\n* বিশাল পরিমাণের ছাই বা স্ল্যাগ জমবে না এবং মাটি ও পানি দূষিত হবে না।<br />\r\n* কয়লা উত্তোলনের সংগে এ্যাসিড গ্যাস , NOx, CO2, SOx গ্যাস, পারদ, এমনকি তেজসক্রিয় পদার্থের ক্ষতিকর বিষয়গুলো ওতপ্রোতভাবে রয়েছে, যেগুলো থেকে রেহাই পাওয়া যাবে।<br />\r\n* ওপেনপিট ও ভূ-অভ্যন্তরস্থ এই উভয় পদ্ধতিতে কয়লা উত্তোলনের সংগে ভূমিধস একটি মারাত্মকভূমি সংকট। এক্ষেত্রে প্রায় সব অবস্থানেই ভূ-গর্ভস্থ কয়লা গ্যাসীকরন পদ্ধতি অবলম্বন করলে ভূমিধশ এড়ান যাবে।</span></p>\r\n','',1,15,'2016-01-07 03:19:50',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',1,0,'1452158390_11.png'),(23,'ঈশ্বরের পৃথিবী: শয়তানের দখল। নিরুদ্র সি চৌধুরী','ঈশ্বরের-পৃথিবী:-শয়তানের-দখল।-নিরুদ্র-সি-চৌধুরী','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">পৃথিবীতে আছি; বেঁচে বর্তে। উপস্থিতি আছে গুরুত্ব নেই; পদচারণা আছে পদ রেখা অনুসরণের কোন কর্ম নেই। অন্নধ্বংসের প্রক্রিয়া দিন দিন দীর্ঘায়িত হচ্ছে আর ভূঁড়ি বড় হচ্ছে, মাথা ছোট আর বুদ্ধি মোটা হচ্ছে। &macr;&oelig;ায়বীয় সুখের লালসায় আর অস্বাভাবিক সম্পদের ভাবনায় কেটে যাচ্ছে দিন। সৎ জগতের পরাজয় আর অমানুষিক, অমানবিক, অবিবেচক শয়তান মানুষের জয়ধ্বনিতেও যাপন চলছে পৃথিবীর, তার দিনমান ক্ষণসময়, যাপিত প্রহর, মধুর স্বপন,স্নিগ্ধ সকাল, সূর্যের ক্ষীণ আলোর বিচ্ছুরণে জয় ধ্বনি উঠছে নামছে। শয়তানদের জিৎ আর সৎদের পরাজয় মঞ্চস্থ হয়েছে, হচ্ছে এ পৃথিবীর প্রাঙ্গণে। মনে হচ্ছে পৃথিবী তার আলোর জগত অন্ধকারে আগ্রাসনে তলিয়ে গেছে। ঈশ্বরকে অধিকার করে নিয়েছে শয়তানে। ঈশ্বর আজ শয়তানের প্রলোভনে পড়ে পণ্য হয়ে গেছে, বাজারের চৌকিতে শয়তানকে দেখা গেছে দোকানির ভূমিকায় ঈশ্বর এখানে সস্তা পণ্য, ক্রেতা পৃথিবীর সব গরিব মানুষ। গরিব মানুষগুলি শয়তানের দোকানে সওদা হওয়া পণ্যটাকে কষ্টার্জিত অর্থ দিয়ে কিনতে মরিয়া। শয়তানের সস্তা উৎপাদিত পণ্যের বাজার পৃথিবীতে এত সম্প্রসারিত হয়েছে যে, এ পণ্যের সাথে যুক্ত হয়েছে স্নায়বীয় উত্তেজনা। পুঁজির অধিকার এখন কেবলই শয়তানের। পৃথিবীর অধিকার কেবলই শয়তানের; শয়তানের সংঘবদ্ধ পৃথিবীতে আমরা সৎ যারা তারা অসহায়ের মতো বসবাস করছি।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">এই শয়তানগুলিকে ঘিরেই রচিত হয়েছে মানব সমাজ। প্রতি যুগ মহাযুগের তাই কেন্দ্রীয় চরিত্র মঞ্চস্থ করেছে যুগশয়তানে। বাড়িয়েছে তাঁদের দাপট; অধিকার করে নিয়েছে সময়, বজ্র কন্ঠে অধিকারকৃত মানুষকে শুনিয়েছে তাদের দখল দায়িত্বের শোকগাঁথা। মানুষ সহজেই আকৃষ্ট হয়েছে এতে। শ্লোগান তুলেছে শয়তানের সাথে, সে এক মুখরিত শ্লোগান, পুঞ্জিভূত আকাশ কাঁপানো শব্দ, আলোড়িত উত্তেজনা, সব গেছে যার দখলে; সে মহান এক শয়তান। এ পৃথিবীর বিচারের ভার আজ তার কাছে, লাঞ্ছনা গঞ্জনার যত দায় সব সে আজ মাথা পেতে নিয়েছে। গর্ব বোধ করছে। বিষুব রেখার উত্তর আর দক্ষিণ গোলার্ধ জুড়ে আজ তার সিংহাসন। শক্ত সিংহাসনে বসে পুঞ্জিভূত ক্ষুদ্র ক্ষুদ্র সালতানাতে বিভক্ত পৃথিবীকে শাসন করছে আর শাসাচ্ছে। মাস্টারপ্লান দিয়ে ব্যস্ত রেখেছে সাতশত কোটি মানুষ আর তাদের পৃথিবীকে; সাথে ঈশ্বরকেও ঐ কোটি কোটি নাঙ্গা মানুষের দখলে দিয়ে বড় মজাটাই না সে লুটছে। দখলকৃত পৃথিবীর যে বিশ্বসঙ্ঘ সেটা তাদের প্রছন্ন দখলে রেখে অর্থ হীন মানুষকে ঈশ্বরের দখল দারিত্ব দিয়ে খুব মজা দেখছে সে।<br />\r\nপ্রাচীন কাল; যে সভ্যতাতে মানুষ সভ্য হয়নি সেই সভ্যতা থেকেই যে সমাজের কর্তৃত্বে ছিল তাঁর, সেই ক্ষমতার বেহাত কোন দিন হয়নি। পৃথিবীর জন্মলগ্ন থেকেই এই শয়তানই পৃথিবীর অভিভাবক, ঈশ্বর শয়তানের প্রজার রোল প্লে করছে। মাঝে মাঝে কোন কোন সমাজে ঈশ্বর মাথাচাড়া দিয়ে উঠলে তাকে দূর্দান্ত, প্রচণ্ড গতিতে থামিয়ে দেওয়া হয়েছে। অথবা এমন এক সিন্ডিকেটকে সৃষ্টি করা হয়েছে যাতে ঈশ্বর এমন ভাবে পরাজিত হয়েছে যে, সে তখন ঢুকেছে ধর্মশালায়। দুর্বল ধর্মশালায় ঈশ্বরকে রেখে শয়তান রাজত্ব করেছে, করছে এ বিশ্ব জগতের। মজার কথা হচ্ছে ঈশ্বর যাতে এ ধর্মশালা হতে বের হয়ে না আসতে পারে তার ব্যবস্থা সে তাঁর মাস্টার প্লানে রেখেছে। ধর্মগ্রন্থ দিয়ে ধর্মালয়গুলিকে শয়তান ইচ্ছে করেই শিকল পরিয়ে দিয়ে এমন ভাবে পৃথিবীতে জাল বিস্তার করেছে যে, সেখানে যতদিন ধর্ম আর ধর্ম গ্রন্থ আর ধর্মশালা আছে ততদিন শয়তান নিশ্চিন্ত। শয়তানের নিশ্চিন্ত পুরীতে ঈশ্বর আজ ঘুমাচ্ছন্ন মাতাল। বাতাস হীন পুকুরের পানির মত শান্ত। মধ্য দুপুরের মাতাল প্রিয় শুয়রের মত বেকুব। মরুভূমির তীর্থক রোদের তাপে ঝলসানো বালুকনার মত উত্তপ্ত অথচয় শান্ত। ঈশ্বরের এ ভূমিকায় আমরা আতংকিত। আমাদের নিরাপত্তা নিয়ে শংকিত; ভূমিকাহীন ঈশ্বর আর কত দিন আমাদের মনকে শাসন করবে? বারবার নিজে শয়তানের দখলে থেকে আমাদেরকেও ঠেলে দেবে তার দখলে। বোকা ঈশ্বর তোমায় ঘৃণা করি। যারাই অনুসন্ধিসু মন নিয়ে তোমার পর্যবেক্ষণ করেছে তারাই তোমার নিষ্কৃয়তার বিরুদ্ধে কথা বলেছে। তুমি কর্মক্ষম এটা প্রমাণ করার ইচ্ছা থাকলেও তোমার ভূমিকা হীন আচরণের জন্য তারা ক্ষুব্ধ ও ব্যথিত হয়েছে এবং নিজেরাই শয়তানের দখল থেকে পৃথিবীটাকে উদ্ধার করতে চেয়েছে। সাহিত্যকে তোমার বর্ণনার কাজে লাগিয়ে মানুষ একাধিক বার ব্যর্থ হয়ে শয়তানের কাছে পরাজিত হয়েছে, পরাজিত হয়ে একাই তারা রুখে দাঁড়িয়েছে।<br />\r\nএমনিতেই ঈশ্বর দুর্বল, দুর্বল হতে হতে ধর্ম শালায় বন্দি থাকতে থাকতে ধর্ম গ্রন্থ দ্বারা শৃংখলিত হতে হতে এটার ক্ষমতা এখন শূন্যের কোঠায়। অপর দিকে শয়তান তার শক্তি বৃদ্ধিতে সদা তৎপর। সে বানায় রাষ্ট্র। রাষ্ট্রে থাকে ব্যাংক বীমা; ব্যাংক তার আলয়। সে সেখানে সুখে থাকে। নিয়ন্ত্রণ করে পৃথিবী, পৃথিবী তাঁর প্রচ্ছন্ন নিয়ন্ত্রণাধীন। দখলদারিত্ব চিরস্থায়ী করার জন্য তার আছে কিছু কৌশল। সে বিনিয়োগ করে বিজ্ঞানে, ধর্মে। মজবুত কাঠামোর উপর শক্ত করে দাঁড় করায় ধর্মশালাকে। সে প্রচার করে মানবিকতাকে, মুনষত্বকে। অন্তরের কঠিনকে পুষে রেখে প্রচার করে সে কোমল। মুখে বিষকে ধারণ করে, আর লালন করে সে ভণ্ডতা। প্রতারকের মত ছড়ায় সুমিষ্ট বাণী। অভাব আর অনাহারে থাকা মানুষের সম্পদ সুকৌশলে আত্মসাৎ করে সারা দুনিয়ায় সে করে বেড়ায় দান। সে দানবীর। হাড়ভাঙ্গা পরিশ্রম করিয়ে সে পরিশোধ করে অল্প মুজুরী। সে বন্টন জানেনা; জানে লুণ্ঠন। বিতরণ জানে না; জানে বিপণন। শক্তিমান প্রমাণের জন্য সে বাধায় যুদ্ধ। সে যুদ্ধে জয়লাভের জন্য তাঁর দরকার এক সাগর রক্ত। রক্তখোর শয়তানের যে সা&curren;্রাজ্য সে সা&curren;্রাজ্যে অসহায় ঈশ্বর। হায় ঈশ্বর!</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">অন্ধ, অকেজো, বেকুব ঈশ্বর করেটা কি? তার না আছে বোধ, না আছে শোধ, পৃথিবীর এহেন অবস্থা বর্ণনা করলে এ পৃথিবীতে সে অপ্রয়োজনীয়। শয়তানের কর্মকাণ্ডে ইন্ধন যোগাবার জন্য সে রচনা করিয়েছে ধর্মগ্রন্থ। এটা দিয়ে মদের নেশারমতো পৃথিবীর মানুষকে মাতাল করে রেখেছে সে। সে ক্ষুধার্তের, পীড়িতের, জরাগ্রস্তের আত্বনাদ শোনে না, দুঃখেকাতর মানুষের দুঃখ লাঘবে কোন ভূমিকা রাখে না। শয়তানের নেতৃত্বই মানুষের দুঃখের প্রধান কারণ, সেখানে ঈশ্বরের সমালোচনাকারীরাই মানুষকে দুঃখ থেকে টেনে তুলবার কৌশল দিয়ে ঈশ্বরকেই সে বার বার অস্বীকার করে সে নিজেই হয়েছে ঈশ্বর। এ ঈশ্বররাও আজকের পৃথিবীর শয়তানের দাপটে পরাজিত, পরাভূত।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">উত্তর মেরু থেকে দক্ষিণ মেরুতে যা আছে, আর যা থাকবে তাকে কি কৌশলে নিজের দখলে নেওয়া যাবে সে চিন্তায় ব্যস্ত কেবল শয়তানই। প্রতিদ্বন্দ্বীহীনতা সে অপছন্দ করে, অপছন্দ করে নিষ্ক্রিয়, দুর্বল, বেকুব, বোকা প্রতিপক্ষকে। এক পাল ক্ষমতা হীন, অভাবগ্রস্ত, মাথা মোটা মানুষের দখলে ঈশ্বরকে দিয়ে শয়তান অধিকার করে নিয়েছে পৃথিবীর সব ক্ষমতা, পৃথিবীকে তার রাজত্বের কাঠামোতে কাঠামোবদ্ধ করেছে। মানুষের মন নিয়ে শয়তানদের বিশাল গবেষণা আছে। যে মানুষ শয়তানের কর্মকাণ্ড মানে না শয়তান তাকে পৃথিবীতে রাখে না। যে রাষ্ট্র তাকে অস্বীকার করে কিংবা তাঁর জন্য হুমকি কিংবা ক্রমে ক্রমে সে শয়তানের বিরুদ্ধে শক্তিশালী হয়ে উঠছে সে রাষ্ট্রকেও শয়তান টিকিয়ে রাখে না। ধ্বংস করে দিয়ে সে শত্র&ldquo;মুক্ত হয়। শত্র&ldquo;কে সে বেড়ে উঠতেও সহায়তা করে। শত্র&ldquo;হীনতায় সে বেঁচে থাকতে চায় না। পারেও না। সে এত একনায়ক, রাগী, তেজী যে ক্রোধে মাঝে মাঝে সে নিজেকেই জ্বালায়। গায়ে পড়ে যুদ্ধ লেগে সে নাচে মাতাল নৃত্যে। নিজের শরীরে নিজে আগুন ঢেলে দাপিয়ে বেড়ায় বিশ্বে, উপস্থিতি প্রমাণ করে ঈশ্বরের, সে দোষ দেয় তাকে। বিশ্বজুড়ে আজ শয়তানের সে নেওয়ার্কে ঈশ্বরকে অস্বীকার করে সে নিজে ঈশ্বর হতে পারে না। সে শক্তি তার নেইও। শয়তানও সে হতে পারেনা। কারণ হঠাৎ করে শয়তানের প্রতিপক্ষ হতে যাওয়ার সাহস নেই তাঁর। সে মরে। না মরে না বাঁচে। দুঃখ হয়!<br />\r\nপ্রায় সব ধর্মগ্রন্থে ঈশ্বর শয়তানকে যুক্তিবাদী, সৃষ্টিশীল, বিদ্রোহী, পরাক্রমশালী হিসাবে উপস্থাপন করেছে। মানুষকে নিয়ন্ত্রণ ক্ষমতা ঈশ্বর যতটুকু তার হাতে রেখেছে তার চেয়ে বেশি দান করেছে শয়তানকে। এ পৃথিবী শয়তানেরই দান। বেহেশতে আদম ছিল বোকা। বোকা আদম আর হাওয়াকে শয়তান চালাক বানিয়া বেহেস্তে নর-নারী, নারী-নরের প্রতি পরস্পর যে আকর্ষণ আর ভালবাসা সেটাকে প্রকাশ ঘটিয়েছিল। প্রকাশ ঘটানোর দরুণ ঈশ্বর আদম আর হাওয়াকে পৃথিবীতে নিক্ষেপের আগে শয়তানের সাথে মহাশালিশ, সে শালিশে শয়তানেরই প্রতিনিধিত্বশীল ভূমিকা আর ঈশ্বরের সাথে সৃষ্টিশীল বাক্য বিনিময় আমাদেরকে অবাক করে। মানুষের রন্ধ্রে রন্ধ্রে বিচরণ ক্ষমতা সেদিন শয়তান বাহাস করে ঈশ্বরের কাছ থেকে আদায় করে ছিল। ঈশ্বর মানুষকে বিপদে ফেলাতে শয়তানকে শক্তিশালী করে পৃথিবীতে প্রেরণ করেছে। নাকি শয়তানি ঈশ্বরের নব রূপ?</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">শয়তানের রাজত্ব সেই যে আরম্ভ; আজ অব্দি তার বিরাম নেই। মানুষ শয়তানের সাথে যুদ্ধ করে শয়তানের সৃষ্টিশীলতাকে পরাস্ত করে দিয়ে পৃথিবীতে টিকে আছে। নিজেকে ঈশ্বর বানিয়ে সে টিকে আছে পৃথিবীতে। মানুষই ঈশ্বর। মানুষই ত্রাতা। মানুষই দাতা। মানুষেই এ ব্রাহ্মাণ্ড আবাদ করে, সৃষ্টি করে, শয়তানের সাথে যুদ্ধ করে পৃথিবীকে এ পর্যন্ত নিয়ে এসেছে। এরপর শয়তান তাকে বারবার পরাস্ত করে পৃথিবীর রাজত্বে নিজেকে মহানায়ক বানিয়েছে। এই যে আজকের পৃথিবী এটা যতটা না মানুষঈশ্বরের তার চাইতে বেশি শয়তানের, শয়তানের রাজধানী এখন পৃথিবীর যে প্রান্তে আমেরিকা। সিংহাসন পাতা আছে আমেরিকার যে প্রান্তে ওয়াশিংটন; সেখানে, হোয়াইট হাউজে।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">শয়তানি কায়দায় পৃথিবী আজ তাঁদের দখলে। শয়তানের পছন্দ পুঁজি আর গণতন্ত্র। পুঁজির মোহে ঈশ্বরতত্ত্ব বিলোপ, গণতন্ত্রের খাতিরে শয়তান আজ বিশ্ব জুড়ে ভ্রাম্যমান। যে মানুষের নীতি, মানবতাবাদ, ক্ষমাবাদ, প্রেমবাদ, এটাকে সে পরিণত করেছে সওদা হিসাবে, যতটুকু পুঁজি ততটুকু প্রেম। যতটুকু পুঁজি ততটুকু মানবতা। যতটুকু পুঁজি ততটুকু ক্ষমা; হ্যাঁ পৃথিবীর ঈশ্বর আজ পুঁজি দ্বারা দখলকৃত। হায় ক্ষণস্থায়ী, দখলী ঈশ্বর!<br />\r\nশয়তানের কৃতিত্ব এই যে, প্রথমে সে মানুষের সৃজনশীলতা আর সৃষ্টিকে দখল করে এরপর এটাকে নতুন কাঠামোর মধ্যে কাঠামোবদ্ধ করে, তারপর যে বিধ্বংসী কাজটা করে সেটা হল তার কাঠামোবদ্ধ ফলাফলটাকে ঈশ্বরের নামে ঈশ্বরভীরু মানব সমাজে সে ছুড়ে দেয়। মানুষও বোকার মত তা লুফে নেয়।<br />\r\nপ্রাচীনকাল থেকে শয়তানি শক্তির বিরুদ্ধে যারা মানুষকে ঐক্যবদ্ধ করার চেষ্টা করেছে তাদের মধ্যে গৌতমবুদ্ধ, হামুরাবী, উকারুগিনা, কনফুসিয়াস, কৌটিল্য থেকে আধুনিক কালের কার্লমাক্স, রবীন্দ্রনাথ, টমাস পেইন, বেনথাম, মিল সহ এরিস্টিটল, থিওফাস্টাস, প্লেটো এদের তত্ত্ব তথ্য শয়তানকে বধ করবার নিয়ামক হিসাবে এসেছে। শয়তান কেবল খোদা দ্বারাই বাহবা পায়নি, বাহবা পেয়েছে রাষ্ট্র দ্বারাও। রাষ্ট্র পৃষ্ঠপোষকতা দিয়ে মানুষকে দাস বানিয়ে শয়তান থেকেও নিকৃষ্ট বস্তুতে পরিণত করেছে। মানুষের সৃজনশীলতাকে রাষ্ট্র অধিকার করে নিয়ে শয়তানের দখলদারিত্ব দিয়ে পৃথিবীটাকে শয়তানেরই স্বর্গভূমিতে পরিণত করেছে।<br />\r\nঅনেকেই বলেন সত্যযুগের পৃথিবী ছিল ভাল মানুষের, ত্রেতাযুগ, দ্বাপরযুগ আর কলির যুগ শয়তানের যুগ। পৃথিবীর কি নিষ্টুর দখল দারিত্ব। এটা না মানুষের, এটা অমানুষের, এটা জন্তুদের অধিকারে, এটার পরতে পরতে দাসত্বের শৃংখল, এ শৃংখলের মালিক স্বয়ং শয়তান। এটাকে পরাস্ত করার কোন মন্ত্র নাই, কোন ওঝা নাই, তত্ত্ব নাই এমনকি তথ্যও নাই। অসহায়ের মত করুণ দৃষ্টি দিয়ে আমরা চিরকাল দেখতে থাকব। পৃথিবী পুড়ছে, এর পুড়া মাটির দখলদায়িত্বের নেশায় চলছে যজ্ঞ। একের পর এক অধিকারে যাচ্ছে মানুষ, মানুষের মন আর সৃজনশীলতা। এরপর পৃথিবীর প্রাঙ্গণে মঞ্চস্থ হয় শয়তানের নৃত্য, সে এক বন্যউল্লাস।</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">শয়তানের নিজস্ব একটা পৃথিবী আছে, সেখানে অতীতের বর্ণাঢ্য কর্মকাণ্ডে দ্বারা সে যে ভুল করে সেই ভুল থেকে সে মুক্ত হয়ে ত্র&ldquo;টি মুক্ত এক পৃথিবী রচনা করে। সে সক্ষম হয়েছে দীর্ঘমেয়াদি দখলদারিত্বে পৃথিবীকে আচ্ছন্ন রাখার। খাড়া করেছে তত্ত্ব, এ তত্ত্বকে পরাস্ত করার কোন পাল্টা তত্ত্ব এখন পৃথিবীতে নাই। চির আধুনিক শয়তান মানুষকে অধিকার করেছে প্রথমে দাস হিসাবে আর এখন শ্রম পণ্য হিসাবে, পুঁজির বিনিময়ে। রাষ্ট্র দখল ও পরিচালনা করার যে নীতি সে নীতির নাম দিয়েছে সে গণতন্ত্র। গণতন্ত্র দ্বারা কি পৃথিবীর মুক্তি মিলবে? এটাতো শয়তানেরই তন্ত্র। যারা গণতন্ত্র কায়েম করবে তারাই শয়তান। গণতান্ত্রিক রাষ্ট্রে চলে শয়তানি শাসন।<br />\r\nস্কটিশ শয়তান এডাম স্মিথ (অফধস ঝসরঃয) শয়তানের প্রতিনিধি হিসাবে খাড়া করলেন এক ফর্দ নাম ঞযব বিধষঃয ড়ভ হধঃরড়হ; তত্ত্ব দিলেন রাষ্ট্রের আদলে কিভাবে শয়তানি পুঁজিকে বৃদ্ধি করবেন-মনে রাখতে হবে রাষ্ট্রের অধিকারের পুঁজি থাকা মানে নিয়ন্ত্রিত ব্যবস্থাপনায় শৃংখলিত বৃহৎ শয়তান, এ বৃহৎ শয়তানের এখন রাষ্ট্র পুঁজি ভাল লাগছে না। তাই গণতন্ত্র আর পুঁজিতন্ত্র প্রতিষ্ঠা করে একক শক্তিদ্বারা শক্তিশালী হতে চান। তার চূড়ান্ত বিজয় চান। পৃথিবী এখন আর পুঞ্জিভূত শয়তানের শাসনে থাকবে না, একক শয়তানের অধিকারে থাকবে পৃথিবী। একক শয়তানের একক ক্ষমতার সমর্থনে অফধস ঝসরঃয দিলেন যে তত্ত্ব ভয়াবহ সে তত্ত্বের উপস্থাপনা শুনেন &lsquo;ব্যক্তি সম্পদ বাড়াতে চাইলে যা যা করতেই হবে তা হল প্রধানত এই রকম বাজারের উপর সরকারের নিয়ন্ত্রণ কমিয়ে ফেলতে হবে। বাজার হবে মুক্ত, অবাধ; কোন শুল্ক রাখা চলবে না, বাণিজ্যে কোন ধরনের বাধা রাখা চলবে না, ব্যবসা বাণিজ্যে বাজারকে নিয়ন্ত্রণ মুক্ত রাখতে হবে। এবং শেষে বল্লেন দুর্ভাবনার কোন কারন নেই। বাজারের অদৃশ্য হাত (ওহারংরনষব ঐধহফ ঙভ গধৎশবঃ) সব কিছু ঠিকঠাক করে দেবে&rsquo; (আবুল বারাকাত, ১৩ই আগষ্ট লোকবক্তিতা&hellip;.) শয়তানকে উস্কে দেবার যে তত্ত্ব সে তত্ত্বই লুফে নিয়েছে আজকের পৃথিবী। গণতন্ত্র আর পুঁজিতন্ত্রের মধ্যে বসবাস করে যে শয়তান আজকের পৃথিবী তাঁরই, যেখানে গণতন্ত্র নাই সেখানে আছে পুঁজিতন্ত্র। আমরা দেখছি পৃথিবী গণতন্ত্র থেকেই বিচ্যুত হচ্ছে। ভবিষ্যতে গণতন্ত্রেও আর থাকবে না। বিশ্ব নিয়ন্ত্রণের একটাই তন্ত্র থাকবে তা হল পুঁজিতন্ত্র। যেটা শয়তানেরই নব্য দখলী তত্ত্ব। যেখানে শয়তান আরো মাথা চাড়া দিয়ে উঠবে এবং সেটা ঈশ্বরকে পরাস্ত করেই। ইরাক, আফগানিস্তান, ইরান, লিবিয়া, সিরিয়ায় যা চলছে তা এককথায় শয়তানের দখলদারিত্ব।<br />\r\nশয়তানকে পরাস্ত ও পরাজিত করতে আমাদের পৃথিবীর দরকার গণতন্ত্রের বিকল্প খোঁজা, এটা কি বিপ্লব ও ন্যায় শাসন? বিপ্লব হলে গণতন্ত্রের আদলে গড়ে উঠা অনৈতিক পৃথিবীর পতন হবে আর ন্যায় শাসন আসলে পুঁজিবাদ আর পুঁজি নির্ভর অনৈতিকতার পতন হবে। নৈতিকতার প্রতিষ্ঠা যেহেতু শয়তানবাদের স্থায়িত্ব দীর্ঘায়িত করার জন্য সেহেতু এটার নতুন বিচিত্র নবায়ন হবে ন্যায় শাসন দ্বারা। শয়তানকে পরাস্ত করার এই একটাই কৌশল।<br />\r\nন্যায় শাসন হলে কি হবে? ন্যায় শাসন কি বিশ্বে এককালীন প্রতিষ্ঠিত হবে? ন্যায় শাসনে মানুষের প্রয়োজনের সীমা কি থাকবে এ নিয়ে অন্যদিন বিস্তারিত আলোচনা করবো।<br />\r\nতবে বোকা ঈশ্বরের পৃথিবীতে শয়তানের সম্পদ দখল প্রক্রিয়া দিন দিন দুধর্ষ, অমানবীয়, অনৈতিক হতে হতে সে সম্পদে লেপ্টে আছে তাজা রক্তের ছিটা, সে রক্ত নিরীহ, সৎ, মানবতাবাদী, আদর্শবাদীদের যারা নাকি ঈশ্বরেরই পরিছন্ন প্রতিনিধি। বিভ্রান্ত ঈশ্বর তার অনুসারিদেরকে শয়তানের দখলে নিয়ে কি নিজেরই নিষ্ক্রিয়তা প্রমান করছেন? যুগ যুগ ধরে মানুষ ঈশ্বরই এক অলঔকিক ঈশ্বরকে অস্বীকার করে আসছে।</span></p>\r\n','',1,15,'2016-01-07 03:20:56',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452158456_1111111.png'),(24,'বাংলাদেশের টেলিযোগাযোগ খাত; জাতীয় সম্পদ রক্ষার প্রসঙ্গে। রশীদি সূজা','বাংলাদেশের-টেলিযোগাযোগ-খাত;-জাতীয়-সম্পদ-রক্ষার-প্রসঙ্গে।-রশীদি-সূজা','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">টেলিযোগাযোগ ব্যবসা মূলত একটি বায়বীয় ব্যবসা। বাতাস বিক্রি করে শূন্যের উপর হাতিয়ে নেওয়া হয় হাজার হাজার কোটি টাকা। বাতাসে ভাসমান তরঙ্গকে শুধু একটি ডিভাইসের মাধ্যমে ধরে আমাদের শ্রবণ ইন্দ্রিয়ে পেঁছৈানো পর্যন্তই সীমাবদ্ধ এর সেবা। এটাই এখন তথ্য আদান প্রদান এর প্রধান মাধ্যম হয়ে দাঁড়িয়েছে। কথার যাদুতে পড়ে এক ধরনের নেশার মোহে হাজার কোটি টাকার অপচয়। এ টাকার কিছু অংশ কেবল দেশে থাকে। বাকি টাকা সরাসরি পাচার হয়। টাকাটা নিয়ে যায় তারা তথাকথিত বৈধ পদ্ধতিতেই। কিন্তু তরঙ্গায়িত এশব্দ ব্যবসার পুরো ব্যবস্থাপনাটাই এক হরিলুটের আখড়া।<br />\r\nবাংলাদেশে যে কয়েকটি ফোন কোম্পানি আছে তাদের মধ্যে রাষ্ট্রায়ত্ত &lsquo;টেলিটক&rsquo; বেসরকারীদের মধ্যে &lsquo;গ্রামীণ ফোন, রবি, বাংলালিংক, এয়ারটেল, সিটিসেল প্রধান। অন্যান্য বাণিজ্যের মতই রাষ্ট্রীয় কোম্পানিটি প্রতিযোগিতামূলক বাণিজ্য সুবিধা করতে পারছে না। বহুজাতিক কর্পোরেট পুঁজি টেলিনর, অজিয়াটা, অরাসকম সরাসরি বাংলাদেশে এ খাতে ব্যবসা করছে। কিন্তু আমরা কেন এ ব্যবসাটা করতে দিচ্ছি?<br />\r\nটেলিটকের যে নেটওয়ার্কিং সিস্টেম আছে তা সুন্দর ব্যবস্থাপনায় ব্যবহার করলে বাংলাদেশে আর কোন ফোন কোম্পানি থাকার কথা নয়। কারণ রাষ্ট্রীয় এ প্রতিষ্ঠানের মত গ্রাহক সেবা অন্য কোম্পানি গুলি শত চেষ্টা করেও দিতে ব্যর্থ হত। ৯ কোটি গ্রাহকের দেশে টেলিটক যদি ৫ টাকায় এক ঘন্টা কথা বলার সুযোগ দিত তাহলে বাজারে তো আর কোন কমপিটিটর থাকার কথা নয়। এ একটি খাতকেই সরকার যদি চ্যালেঞ্জ হিসাবে নিত তাহলে বাংলাদেশের টেলিযোগাযোগ ব্যবস্থা অন্য রকম হতে পারত। মানছি টেলিটক প্রথমেই আসে নাই। সিটিসেল, গ্রামীণ প্রথমে এসে এ ব্যবসাটা শুরু করেছে। তারা পথ দেখিয়েছে। লাভ করেছে। আমরা রাম বোকার মত ৭ টাকা মিনিটে কথা বলেছি। তারও আগে ৩৫ টাকা মিনিটে। বিনিয়োগ উঠিয়ে কর্পোরেট গুলির এখন যা ব্যবসা তাই লাভের মত অবস্থায় এসে দাঁড়িয়েছে। ইদানিং ৩এ নেটওয়ার্কিং আসার ফলে ব্যবসা তাদের আগের চেয়েও তিন গুণ চাঙ্গা হয়েছে। এ চাঙ্গা ব্যবসার অর্থ সরাসরি যাচ্ছে কর্পোরেটদের মাধ্যমে বিদেশে। দুর্নীতির মাধ্যমে সরকার মহলে তারা কর ফাঁকি দিচ্ছে। অংক হাজার কোটি টাইপের। বাংলাদেশে এ খাতটাই মনে হয় সবচেয়ে দুর্নীতিগ্রস্ত খাত। ঠঙওচ ব্যবসাটার কথা নাই বলল্লাম। বাংলাদেশে টেলিযোগাযোগ ব্যবসা মূলত একটি বায়বীয় ব্যবসা। বাতাস বিক্রি করে শূন্যের উপর হাতিয়ে নেওয়া হয় হাজার হাজার কোটি টাকা। বাতাসে ভাসমান তরঙ্গকে শুধু একটি ডিভাইসের মাধ্যমে ধরে আমাদের শ্রবণ ইন্দ্রিয়ে পেঁছৈানো পর্যন্তই সীমাবদ্ধ এর সেবা। এটাই এখন তথ্য আদান প্রদান এর প্রধান মাধ্যম হয়ে দাঁড়িয়েছে। কথার যাদুতে পড়ে এক ধরনের নেশার মোহে হাজার কোটি টাকার অপচয়। এ টাকার কিছু অংশ কেবল দেশে থাকে। বাকি টাকা সরাসরি পাচার হয়। টাকাটা নিয়ে যায় তারা তথাকথিত বৈধ পদ্ধতিতেই। কিন্তু তরঙ্গায়িত এশব্দ ব্যবসার পুরো ব্যবস্থাপনাটাই এক হরিলুটের আখড়া।<br />\r\nবাংলাদেশে যে কয়েকটি ফোন কোম্পানি আছে তাদের মধ্যে রাষ্ট্রায়ত্ত &lsquo;টেলিটক&rsquo; বেসরকারীদের মধ্যে &lsquo;গ্রামীণ ফোন, রবি, বাংলালিংক, এয়ারটেল, সিটিসেল প্রধান। অন্যান্য বাণিজ্যের মতই রাষ্ট্রীয় কোম্পানিটি প্রতিযোগিতামূলক বাণিজ্য সুবিধা করতে পারছে না। বহুজাতিক কর্পোরেট পুঁজি টেলিনর, অজিয়াটা, অরাসকম সরাসরি বাংলাদেশে এ খাতে ব্যবসা করছে। কিন্তু আমরা কেন এ ব্যবসাটা করতে দিচ্ছি?<br />\r\nটেলিটকের যে নেটওয়ার্কিং সিস্টেম আছে তা সুন্দর ব্যবস্থাপনায় ব্যবহার করলে বাংলাদেশে আর কোন ফোন কোম্পানি থাকার কথা নয়। কারণ রাষ্ট্রীয় এ প্রতিষ্ঠানের মত গ্রাহক সেবা অন্য কোম্পানি গুলি শত চেষ্টা করেও দিতে ব্যর্থ হত। ৯ কোটি গ্রাহকের দেশে টেলিটক যদি ৫ টাকায় এক ঘন্টা কথা বলার সুযোগ দিত তাহলে বাজারে তো আর কোন কমপিটিটর থাকার কথা নয়। এ একটি খাতকেই সরকার যদি চ্যালেঞ্জ হিসাবে নিত তাহলে বাংলাদেশের টেলিযোগাযোগ ব্যবস্থা অন্য রকম হতে পারত। মানছি টেলিটক প্রথমেই আসে নাই। সিটিসেল, গ্রামীণ প্রথমে এসে এ ব্যবসাটা শুরু করেছে। তারা পথ দেখিয়েছে। লাভ করেছে। আমরা রাম বোকার মত ৭ টাকা মিনিটে কথা বলেছি। তারও আগে ৩৫ টাকা মিনিটে। বিনিয়োগ উঠিয়ে কর্পোরেট গুলির এখন যা ব্যবসা তাই লাভের মত অবস্থায় এসে দাঁড়িয়েছে। ইদানিং ৩এ নেটওয়ার্কিং আসার ফলে ব্যবসা তাদের আগের চেয়েও তিন গুণ চাঙ্গা হয়েছে। এ চাঙ্গা ব্যবসার অর্থ সরাসরি যাচ্ছে কর্পোরেটদের মাধ্যমে বিদেশে। দুর্নীতির মাধ্যমে সরকার মহলে তারা কর ফাঁকি দিচ্ছে। অংক হাজার কোটি টাইপের। বাংলাদেশে এ খাতটাই মনে হয় সবচেয়ে দুর্নীতিগ্রস্ত খাত। ঠঙওচ ব্যবসাটার কথা নাই বলল্লাম। বাংলাদেশে টেলিযোগাযোগ খাতে ব্যবসা করছে যে কর্পোরেটরা তারা মোটামুটি সবাই কমবেশি রাজস্ব ফাঁকির আওতায় আছে। গত কয়েকদিন আগে টেলিভিশন চ্যানেলগুলি গ্রামীণ ফোনের প্রায় হাজার কোটি টাকা রাজস্ব ফাঁকির কথা প্রচার করে। এর পিছনে হয়ত সরকার সমর্থিত বড় সিন্ডিকেট আছে। বিভিন্ন গবেষণা প্রতিষ্ঠান থেকে বলা হচ্ছে ফোন কোম্পানিগুলি সরকারকে যে পরিমাণ রাজস্ব দেয় তার চেয়ে বেশি খরচ করে জবহঃ ঝববশবৎং (রাজনৈতিক দুর্বত্ত, আমলা, দালালদের) পিছনে। এ কাজটা করে তারা টাকা পাচারের স্বার্থে। পাচারটাকে অবারিত করার জন্যে। কথা হচ্ছে সা&curren;্রাজ্যবাদী কর্পোরেট গোষ্ঠিগুলি সরকারকে এমন চাপে রাখে যে; চাপের কারণে যে সরকারই ক্ষমতায় আসুক না কেন তাদের লুন্ঠিত ব্যবসার পদ্ধতি একই থাকে। বরং আরো পাকা পোক্ত হয়। সারা দুনিয়ায় কর্পোরেট হাউজ গুলি এখন তাদের ব্যবসায় অধিক মুনাফা লাভের আশায় চড়ষরঃরপধষ ঊপড়হড়সু এর সাথে সম্পৃক্ত হয়ে গিয়েছে। আমাদের নোবেল লরিয়েট ইউনুসের নিয়ন্ত্রণাধীন গ্রামীণ গ্র&ldquo;প নাকি হিলারিকে তখন রাজনৈতিক চাঁদা সরবরাহ করেছে। কথাটাকে একবারেই উড়িয়ে দেওয়া যায় না।<br />\r\nকথা হচ্ছিল রাষ্ট্রীয় প্রতিষ্ঠান টেলিটককে আরো যুগোপযোগী এবং গ্রাহক সেবার মান আরো উন্নত করে এমন একটা কলরেট নির্ধারন করা যাতে বিদেশী কোম্পানি গুলি রাষ্ট্রীয় প্রতিষ্ঠানের সাথে প্রতিযোগিতায় টিকতে না পেরে আপনি আপনিতেই তল্পিতল্পা ঘুচিয়ে চলে যায়। এটা করলে এক দিকে যেমন সস্তায় কথা বলার সুযোগ মিলবে অন্য দিকে বিশাল অংকের রাজস্ব পাওয়া যাবে।<br />\r\nসিটিসেল এর একচেটিয়া (ভয়াবহ কলরেট ৩৫ টাকা প্রতি মিনিট) ব্যবসার চিড় ধরে গ্রামীণ ফোনের ব্যাপকতার কাছে। গ্রামীনও একচেটিয়া ব্যবসা চালায় আরো ভয়াবহ ভাবে। তারা ইউনুসের মাধ্যমে গ্রামে গঞ্জে মোবাইল ছড়ায় এবং ৭ টাকা কলরেট নির্ধারণ করে পুঁজি ও মুনাফা অধিক হারে লুটে নেয় গরীব মানুষের কাছ থেকে। এ গ্রামীণ ফোনেরও একচেটিয়া ব্যবসার চির ধরে যখন ঘ্যাটঘ্যাট করে আরো অন্যান্য তিনটি ফোন কোম্পানি একসাথে বাজারে নামে। এ তিনটি কোম্পানি রবি, বাংলালিংক, এয়ারটেল এরা গ্রামীণ ফোনের সাথে প্রতিযোগিতায় নামে। বহু মানুষের মাঝে বিভিন্ন ধরনের গ্রাহক সেবা দিয়ে গ্রামীণ এর একচেটিয়া ব্যবসায় চিড় ধরায়। কল রেট নেমে আসে ৭ টাকা থেকে এক লাফে ১ টাকার কাছাকাছি। ৯ কোটি ব্যবহারকারীর দেশে বিদ্যমান সব কোম্পানিগুলিই ভাল ব্যবসা করছে। সরকারের কর ফাঁকিও দিচ্ছে টাকা পাচারও চলছে। মন্ত্রী আমলা সহ সবাইকে টাকা দিয়ে খুশিও রাখছে। পুরো ব্যবস্থাপনাটিকে দূর থেকেই হ-য-ব-র-ল মনে হয়। বিশেষজ্ঞরা বলছেন কলের হিসাবের সাথে রাজস্ব আদায়ের মিল নেই।<br />\r\nগ্রামীণ ফোন এখন পুরোটাই লাভের উপরে চলছে (৪৩০০ টাকা সিম বিক্রির কালেই তারা তাদের পুঁজি উঠিয়ে নিয়েছে)। বাতাস ব্যবসার অবস্থা এখন এই দাঁড়িয়েছে যে, ব্যবসা যা সবটাই মুনাফা। ৩এ ইদানিং কালের নতুন ব্যবসা। নতুন নবায়ন। গই, এই, কগইচ উল্টাপাল্টা হিসাবে ইন্টারনেট, এরপর ইচ্ছা মত ডাটা কর্তন। ব্যবহারকারীদের সাথে চালাচ্ছে গলাকাটা প্রতারণা। টাকা ঢুকালেন ২০ মিনিট ব্রাউজ করলেন এরপর টাকা শেষ। সে এক বিরক্তিকর ব্যাপার। ইন্টারনেট ব্যবসাটা এত প্রতারণা মূলক যে, তারা কাউকে ইউজের সঠিক হেসাব দেয় না। এক এই ঢুকালেন মেয়াদ ৩০ দিন। ১৫ দিনে শেষ। কিভাবে শেষ, কতক্ষণে শেষ, কী স্পীডের কারণে শেষ, কোন জবাবদিহিতা নাই। এ এক আল্লার দান&Ntilde;বাতাসের উপরও তুমুল প্রতারণা মূলক ব্যবসা। পকেটে সারাক্ষণ প্রতারণা নিয়ে ঘুরি। প্রতারণা এখন আমাদের নিত্য সঙ্গী। এটাকে এখন তিন পয়সার প্রতারণা বলছে। দৃশ্যত তিন পয়সা আর সমষ্টিগত হাজার কোটি টাকার প্রতারণা। প্রতারণার ফল টাকা, ডলারে কনভার্ট হয়ে পাচার হচ্ছে বিদেশে। জাতীয় সম্পদের এ বেহাত মেনে নেওয়া যায় না।<br />\r\nআরেকটা ব্যাপার হচ্ছে ফোন কোম্পানি গুলির টাওয়ার স্থাপন (ঘবঃড়িৎশ ঈড়াবৎধমব) এর ক্ষেত্রে কোন সমন্বয় নেই। যাচ্ছেতাই টাওয়ার স্থাপন যা বজ্রনিরোধক নয়। আর শব্দ তরঙ্গের উল্টাপাল্টা ঋৎবয়ঁবহপু র ফলে বাদুর সহ আরো কিছু প্রাণির প্রজনন ব্যাহত হচ্ছে (বাদুর জাতীয় প্রাণিরা শব্দ তরঙ্গ ব্যবহার করে চলা ফেরা করে) যারা এ ধরনের পরিবেশের সাথে অভিযোজন করতে পারে না। আর মানুষের শ্রবণ ইন্দ্রিয়ের একটা মারাত্মক প্রভাব তো আছেই। তাছাড়া সামাজিক কলহ, তরুণ-তরুনিদের মাঝে মারাত্মক প্রভাব এগুলি তো আছেই, যা এখন এই প্রবন্ধের আলোচ্য বিষয় নয়। এ নিয়ে অন্য দিন আলোচনা করা যাবে।। এসব এলোমেলো টাওয়ার মারাত্মক সমস্যার সৃষ্টি করে। টাওয়ার ব্যবস্থাপনায় একটি কেন্দ্রিয় সিস্টেম থাকলে এবং এটার নিয়ন্ত্রন টেলিটকের হলে মনে হয়না অন্যকোন বিদেশী কোম্পানি টেলিটক থেকে অধিক সেবা দেবার বিজ্ঞাপন দিতে পারতো।<br />\r\nসরকারের কাছে আমাদের সুনির্দিষ্ট দাবি, টেলিতরঙ্গ আমাদের জাতীয় সম্পদ এটাকে ব্যবহার করে বিদেশে টাকা পাচার সহ লুটপাট আর প্রতারণা দীর্ঘজীবী হবে এটা আমরা চাইনা। একদিনে বিদেশী কোম্পানি গুলিকে বিদায়ের কথা বলছিনা। আস্তে আস্তে প্রতিযোগিতামূলক কলরেট, নেটওয়ার্কিং এ বিদেশী কোম্পানিগুলিকে পরাস্ত করে এমন একটা পরিস্থিতি সৃষ্টি করা যাতে বিদেশীরা বাধ্য হয় ব্যবসা গুটিয়ে চলে যেতে। তখন ১৬ কোটি মানুষ ৫ টাকা/ঘন্টা কথা বলবে। কোন প্রতারণা মূল ৩এ; ৩.৫এ র বিজ্ঞাপন থাকবে না। অফপিক ও পিকে সমান রেটে কথা বলবে। ১৬ কোটি মানুষ ফোনে কথা বলেও দেশপ্রেমের স্বাদ পাবে। চলুন টেলিযোগাযোগ খাতে স্বনির্ভরতা অর্জনের লড়াইয়ে শামিল হই। সরকারকে বাধ্য করি। আমাদের মেধাবী তরুনদেরকে দিয়ে (যারা অন্যান্য ফোন কোম্পানির সার্বিক ব্যবস্থাপনার সাথে জড়িত) টেলিটককে যেন একটি শক্তিশালী টেলিযোগাযোগ প্রতিষ্ঠান হিসাবে দাঁড় করায়। </span></p>\r\n','',1,15,'2016-01-07 03:22:05',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',0,0,'1452158525_11111.png'),(25,'বাংলাদেশে গণতন্ত্র ও রাজনীতির ভবিষ্যৎ । ম. ইনামুল হক','বাংলাদেশে-গণতন্ত্র-ও-রাজনীতির-ভবিষ্যৎ-।-ম.-ইনামুল-হক','<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশে ৫ জানুয়ারি ২০১৪ এর দশম জাতীয় সংসদ নির্বাচন নিয়ে বিতর্ক এবং প্র্রশ্ন বহুকাল ধরে চলবে। বর্তমান আওয়ামী লীগ সরকার বহাল থাকার সময়েও চলবে, এই সরকার বিদায় নেবার পরও চলবে। মুক্তিযুদ্ধের স্বপক্ষের শক্তি বলে পরিচিত এবং স্বাধীনতা যুদ্ধে নেতৃত্ব প্রদানকারী বলে পরিচিত বাংলাদেশের প্রধান রাজনৈতিক দল আওয়ামী লীগের নীতি নির্ধারকরা বাংলাদেশে গণতন্ত্রের কি দশা করেছিলেন তা&rsquo; ইতিহাসের পাতায় মোটা দাগে লেখা থাকবে। ঐ নির্বাচন ১৯৯৬ সালের বিএনপি আমলের এবং ১৯৮৮ সালের জাতীয় পার্টির ভোটারবিহীন নির্বাচনগুলি থেকে তেমন পৃথক কিছু ছিলো না। ঐ নির্বাচন নিয়ে দেশে ও বিদেশে অনেক সমালোচনা হয়েছে। আওয়ামী লীগ ক্ষমতায় থেকে নির্বাচন করার জন্যে জাতীয় সংসদে পঞ্চদশ সংশোধনীর মাধ্যমে সংবিধান পরিবর্তন করে নির্বাচনকালীন তত্ত্বাবধায়ক সরকার এর বিধান তুলে দেয়। এরপর আওয়ামী লীগ ক্ষমতায় থেকে নির্বাচন দিলে বিএনপিসহ বাংলাদেশের অধিকাংশ রাজনৈতিক দল এতে অংশগ্রহণ করেনি। ফলে ঐ নির্বাচনে জাতীয় সংসদের অর্ধেকেরও বেশি সদস্য বিনা প্রতিদ্বন্দ্বিতায় নির্বাচিত হন। আওয়ামী লীগ প্রায় ফাঁকা মাঠে গোল দেবার মত ঐ নির্বাচনে জয়ী হয়। কিন্তু বিভিন্ন সংবাদ মাধ্যমে প্রকাশিত হয় যে, ঐ নির্বাচনে জনগণ ভোট কেন্দ্রে খুব কমই গেছে, ভোট প্রদানকারীর সংখ্যা ৫% এর বেশি হবে না। তাই তাঁরা যে জনগণের ম্যান্ডেট পেয়েছেন তা&rsquo; একেবারেই প্রমাণিত হয়নি। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশের দশম নির্বাচন বিষয়ে ভারতীয় প্রভাব ছিলো বলে প্রচারণা আছে। এর পেছনে রয়েছে ভারতীয় রাজনীতিবিদদের বক্তব্য, পত্র পত্রিকার সংবাদ এবং সাংবাদিকদের কলাম। অবস্থা যেন এই যে, শেখ হাসিনার আওয়ামী লীগ ও তার সহযোগীরা ভারতের পক্ষে, আর খালেদা জিয়ার বিএনপি ও তার সহযোগীরা ভারত বিরোধী অবস্থান করেন। ব্যাপারটি কিন্তু তা&rsquo; নয়, কারণ অতীত থেকে বর্তমান ঘটনাপ্রবাহ এটাই প্রমাণ করে যে, তাঁরা উভয়েই ভারতীয় সরকারের প্রতি নতজানু অবস্থানে আছেন। ২০১২ সালের আগস্ট মাসে জাতীয় পার্টির হুসেইন মুহম্মদ এরশাদ দিল্লী সফর করলে তিনি বাংলাদেশের রাজনীতিতে মুখ্য হয়ে উঠছেন কিনা-এই নিয়ে যখন জল্পনা কল্পনা শুরু হয়। তখন বলা হচ্ছিলো বাংলাদেশে ৫ জানুয়ারি ২০১৪ এর দশম জাতীয় সংসদ নির্বাচন নিয়ে বিতর্ক এবং প্র্রশ্ন বহুকাল ধরে চলবে। বর্তমান আওয়ামী লীগ সরকার বহাল থাকার সময়েও চলবে, এই সরকার বিদায় নেবার পরও চলবে। মুক্তিযুদ্ধের স্বপক্ষের শক্তি বলে পরিচিত এবং স্বাধীনতা যুদ্ধে নেতৃত্ব প্রদানকারী বলে পরিচিত বাংলাদেশের প্রধান রাজনৈতিক দল আওয়ামী লীগের নীতি নির্ধারকরা বাংলাদেশে গণতন্ত্রের কি দশা করেছিলেন তা&rsquo; ইতিহাসের পাতায় মোটা দাগে লেখা থাকবে। ঐ নির্বাচন ১৯৯৬ সালের বিএনপি আমলের এবং ১৯৮৮ সালের জাতীয় পার্টির ভোটারবিহীন নির্বাচনগুলি থেকে তেমন পৃথক কিছু ছিলো না। ঐ নির্বাচন নিয়ে দেশে ও বিদেশে অনেক সমালোচনা হয়েছে। আওয়ামী লীগ ক্ষমতায় থেকে নির্বাচন করার জন্যে জাতীয় সংসদে পঞ্চদশ সংশোধনীর মাধ্যমে সংবিধান পরিবর্তন করে নির্বাচনকালীন তত্ত্বাবধায়ক সরকার এর বিধান তুলে দেয়। এরপর আওয়ামী লীগ ক্ষমতায় থেকে নির্বাচন দিলে বিএনপিসহ বাংলাদেশের অধিকাংশ রাজনৈতিক দল এতে অংশগ্রহণ করেনি। ফলে ঐ নির্বাচনে জাতীয় সংসদের অর্ধেকেরও বেশি সদস্য বিনা প্রতিদ্বন্দ্বিতায় নির্বাচিত হন। আওয়ামী লীগ প্রায় ফাঁকা মাঠে গোল দেবার মত ঐ নির্বাচনে জয়ী হয়। কিন্তু বিভিন্ন সংবাদ মাধ্যমে প্রকাশিত হয় যে, ঐ নির্বাচনে জনগণ ভোট কেন্দ্রে খুব কমই গেছে, ভোট প্রদানকারীর সংখ্যা ৫% এর বেশি হবে না। তাই তাঁরা যে জনগণের ম্যান্ডেট পেয়েছেন তা&rsquo; একেবারেই প্রমাণিত হয়নি। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশের দশম নির্বাচন বিষয়ে ভারতীয় প্রভাব ছিলো বলে প্রচারণা আছে। এর পেছনে রয়েছে ভারতীয় রাজনীতিবিদদের বক্তব্য, পত্র পত্রিকার সংবাদ এবং সাংবাদিকদের কলাম। অবস্থা যেন এই যে, শেখ হাসিনার আওয়ামী লীগ ও তার সহযোগীরা ভারতের পক্ষে, আর খালেদা জিয়ার বিএনপি ও তার সহযোগীরা ভারত বিরোধী অবস্থান করেন। ব্যাপারটি কিন্তু তা&rsquo; নয়, কারণ অতীত থেকে বর্তমান ঘটনাপ্রবাহ এটাই প্রমাণ করে যে, তাঁরা উভয়েই ভারতীয় সরকারের প্রতি নতজানু অবস্থানে আছেন। ২০১২ সালের আগস্ট মাসে জাতীয় পার্টির হুসেইন মুহম্মদ এরশাদ দিল্লী সফর করলে তিনি বাংলাদেশের রাজনীতিতে মুখ্য হয়ে উঠছেন কিনা-এই নিয়ে যখন জল্পনা কল্পনা শুরু হয়। তখন বলা হচ্ছিলো খালেদা জিয়াও দিল্লী যেতে পারেন। খালেদা জিয়া এরপর ভারতের পররাষ্ট্র মন্ত্রণালয়ের আমন্ত্রণে ২৮ অক্টোবর ২০১২ দিল্লী যান। ঐদিন তিনি ভারতের লোকসভার বিরোধী দলীয় নেত্রী সুষমা স্বরাজের সাথে দেখা করেন। পরদিন ২৯ অক্টোবর তিনি ভারতের প্রধানমন্ত্রী মনমোহন সিংএর সাথে বৈঠক করেন। ৩০ তারিখ পররাষ্ট্র মন্ত্রণালয়ের মন্ত্রী সালমান খুরশিদের সাথে দেখা করেন। ৩১ তারিখ জয়পুর ও আজমীরের উদ্দেশ্যে তিনি দিল্লী ত্যাগ করেন। খালেদা ৩ নভেম্বর ঢাকা ফেরার দিন রাষ্ট্রপতি প্রণব মুখার্জির সাথে দেখা করেন। এতসব দেখা করেও তিনি এরশাদের গুরুত্ব কমাতে পারেননি। বাংলাদেশের দশম জাতীয় সংসদ নির্বাচনে তাই দেখা গিয়েছে। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ভারতের ষোড়শ লোকসভা নির্বাচনে নরেন্দ্র মোদি ভারতীয় জনতা পার্টির প্রধানমন্ত্রী প্রার্থী হলে বাংলাদেশের প্রতি ভারতীয় দৃষ্টিভঙ্গি নিয়ে উভয় দেশে ব্যাপক আলোচনা হতে থাকে। নরেন্দ্র মোদি ১৩ সেপ্টেম্বর ২০১৩ প্রধানমন্ত্রী প্রার্থী হন। যাঁরা হিন্দুত্ব মতবাদে আকৃষ্ট, তাঁরা ২০০২ সালে গুজরাটের দাঙ্গায় নরেন্দ্র মোদির ভূমিকা নিয়ে প্রশ্ন না তুলে তাঁর গুণগ্রাহী হতে শুরু করে। তাঁকে প্রধানমন্ত্রী প্রার্থী করার আগে থেকেই ভারতীয় মিডিয়া তাঁর পক্ষে প্রচার চালাচ্ছিলো। ইন্ডিয়ান এক্সপ্রেসের প্রধান সাংবাদিক শেখর গুপ্তা &lsquo;নরেন্দ্রভাই&rsquo;কে সম্বোধন করে লেখেন (৩১.০৮.১৩), ভারতের স্বার্থে বাংলাদেশে শেখ হাসিনার সরকার অনেক বেশি অপরিহার্য। এই লেখা নিয়ে বাংলাদেশের মিডিয়ায় তোলপাড় হয়। তবে দিল্লীর কংগ্রেস সরকার যে শেখ হাসিনার সরকার সম্পর্কে একই রকম মত পোষণ করে তা&rsquo; বাংলাদেশের নির্বাচনের সময় স্পষ্টভাবে প্রকাশ পায়। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশের দশম সংসদ নির্বাচনের সময় বিএনপি নির্দলীয় তত্ত্বাবধায়ক সরকার এর দাবিতে অনড় থাকলে একমাত্র ভারত বাদে সকল বিদেশী কূটনীতিকরা আলোচনার ভিত্তিতে একটি সমাধানের পক্ষে ছিলো। ভারত স্পষ্টভাবে শেখ হাসিনার পক্ষে থাকলে এক পর্যায়ে ভারত ও আমেরিকার কূটনৈতিক টানাপোড়েন সৃষ্টি হয়। ৫ জানুয়ারী নির্বাচন অনুষ্ঠানের পূর্বে ভারতীয় পররাষ্ট্রমন্ত্রী সালমান খুরশিদ ৩০ ডিসেম্বর বলেন, ভারত যেহেতু আমেরিকার চাইতে বাংলাদেশের বেশি কাছাকাছি অবস্থান করে সেহেতু বাংলাদেশ পরিস্থিতি নিয়ে ভারতের ধারণা বেশি কাছাকাছি; আমেরিকার এটা বোঝা উচিত। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ভারতীয় পত্রিকা দি হিন্দুর উদ্ধৃতি &ldquo;We don&rsquo;t discuss democracy in third countries but we do hope the U.S. &hellip;&rdquo; Mr. Khurshid pointed out that India&rsquo;s understanding of Bangladesh should be helpful to the U.S. &ldquo;While the U.S. is at some distance from Bangladesh, we are right next to it. So our understanding of the region and understanding of sentiments of the people in the region should be helpful in the positions they want to take.&rdquo; [The Hindu, 30.12.2013]</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশ নিয়ে মার্কিন ভারত টানাপোড়েনের কারণে ভারতীয় মনোভাব পরে কিছুটা পরিবর্তন হয়। আনন্দবাজার পত্রিকা ১ জানুয়ারি ২০১৪ দিল্লীর সূত্র উল্লেখ করে শিরোনামে লেখে, &lsquo;জঙ্গি জামাতকে সামলে হাসিনা ফের নির্বাচনে আসুন, চায় দিল্লি&rsquo;। শেখ হাসিনা শেষমেশ ৫ জানুয়ারি নির্বাচন করেই ফেলেন। সংসদের ৩০০টি আসনের মধ্যে ১৫৩টি আসনে বিনা প্রতিদ্বন্দ্বিতায় জয়ী হয়ে দশম সংসদে তাঁর ক্ষমতা আগেই নিশ্চিত হয়ে ছিলো। কিন্তু নির্বাচন প্রশ্নবিদ্ধ হয়ে যায়। এরপর শেখ হাসিনার প্রতি ভারতীয় সমর্থনের বিষয়টি প্রকাশ্যরূপ নিলে বাংলাদেশের দৈনিক ইত্তেফাক ৬ জানুয়ারি বাংলাদেশে নির্বাচনী বৈধতা বিষয়ে দিল্লীর বরাত দিয়ে শিরোনামে লেখে, &lsquo;আন্তর্জাতিক সমর্থন আদায়ে প্রচেষ্টা চালাবে ভারত।&rsquo; আওয়ামী লীগ প্রচারণা চালায় সাংবিধানিক ধারাবাহিকতা বজায় রাখতে ৫ই জানুয়ারির নির্বাচন অপরিহার্য ছিলো।<br />\r\nবাংলাদেশের রাষ্ট্রপতি জনাব আবদুল হামিদ জুন মাসে নিউইয়র্ক সফর করেন। তিনি ১৯ জুন জাতিসংঘ মহাসচিব বান কি মুনএর সাথে সাক্ষাৎ করে বলেন, বাংলাদেশের জনগণ ৫ জানুয়ারির নির্বাচন মেনে নিয়েছে। তাঁর দু&rsquo;দিন পর নিউইয়র্কে জাতিসংঘ দপ্তরকে উদ্ধৃত করে ঢাকার জাতিসংঘ দপ্তর থেকে বলা হয়, মহাসচিব বান কি মুন বাংলাদেশে চলমান রাজনৈতিক পরিস্থিতিতে সংসদের বাইরে বিরোধী দলগুলোকে নিয়ে সংলাপে বসার তাগিদ দিয়েছেন। ৫ জানুয়ারির দশম জাতীয় সংসদ নির্বাচন সব দলকে নিয়ে অংশগ্রহণমূলক ও শান্তিপূর্ণ না হওয়ায় পরদিনই এর সমালোচনা করেছিলো জাতিসংঘ। ঐ বিবৃতির মাধ্যমে জাতিসংঘ আবারও তার অবস্থান পুনর্ব্যক্ত করে (প্রথম আলো ২২.০৬.১৪)। ঐ নির্বাচনে সব দলের অংশগ্রহণের জন্য জোরালো উদ্যোগ নিয়েছিলো জাতিসংঘ। ঐ প্রক্রিয়ার অংশ হিসেবে ২০১২ ও ২০১৩ সালে বান কি মুন এর প্রতিনিধি হিসেবে রাজনীতি বিষয়ক সহকারী মহাসচিব অস্কার ফার্নান্দেজ তিনবার ঢাকায় আসেন। দশম সংসদ নির্বাচনের আগে তাঁর উপস্থিতিতে বিএনপি ও আওয়ামী লীগের প্রতিনিধিরা দুইবার সংলাপে বসেছিলো। কিন্তু তাঁরা কোন সমাধানে পৌঁছাতে পারে নি। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশে গণতন্ত্র ও রাজনীতির ভবিষ্যৎ এখন আওয়ামী লীগ ও বিএনপি এই দুই দলের ইচ্ছা অনিচ্ছার উপর নির্ভর করছে। তাই অনেকে বলবেন, বাংলাদেশে গণতন্ত্র আনতে হলে ঐ দুই দলের দৌরাত্য দূর করতে হবে। তা&rsquo; কি সম্ভব? বাংলাদেশের সংবিধান এবং নির্বাচনী আইন সেই ব্যবস্থা রাখেনি। বাংলাদেশ সংবিধানে বিগত ১৫শ সংশোধনীর অনুযায়ী সংসদ সদস্যগণ &lsquo;সংসদ সদস্য&rsquo; পদে বহাল থেকেই নির্বাচন করার সুযোগ রাখা হয়েছে, এবং বিগত দশম জাতীয় সংসদ নির্বাচন সেভাবেই অনুষ্ঠিত হয়েছে। এভাবে ক্ষমতাসীন দলের চিরকাল ক্ষমতায় থাকার ব্যবস্থা করা হয়েছে। স্মরণীয় যে, সংসদ নির্বাচনকে অবাধ ও স্বচ্ছতাপূর্ণ করার জন্যই বিগত ১৯৯৬ সালে আওয়ামী লীগ দেশব্যাপী লাগাতার হরতাল দিয়ে তৎকালীন বিএনপি সরকারকে সংসদে &lsquo;তত্ত্বাবধায়ক সরকার&rsquo; বিল পাশ করতে বাধ্য করেছিলো। কিন্তু সেই আওয়ামী লীগই ২০০৯ সালে দ্বিতীয়বার ক্ষমতায় এসে মহাজোটের নিরঙ্কুশ সংখ্যাগরিষ্ঠতার জোরে &lsquo;তত্ত্বাবধায়ক সরকার&rsquo; এর বিধান তুলে দেয়। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">বাংলাদেশের নির্বাচনী আইনে যে অপরাধমূলক কাজটি করা হয়েছে তা&rsquo; করেছে বিগত ২০০৮ সালের তত্ত্বাবধায়ক সরকার ও ঐ সময়কার নির্বাচন কমিশন এবং পরবর্তী আওয়ামী লীগ সরকার। তারা গণপ্রতিনিধিত্ব আদেশ (আরপিও) ১৯৭২ এর ধারা ১২(৩এ) পরিবর্তন করে জাতীয় সংসদ নির্বাচনে স্বতন্ত্র প্রার্থীকে আবেদনের জন্য ১% ভোটারের পূর্বস্বাক্ষর গ্রহণ করা এবং ধারা ৯০বি পরিবর্তন করে নতুন নিবন্ধন পেতে রাজনৈতিক দলসমূহের কমপক্ষে এক তৃতীয়াংশ জেলায় দপ্তর থাকার এবং কমপক্ষে একশত উপজেলা বা মেট্রোপলিটন থানায় দপ্তরসহ কমপক্ষে ২০০ করে ভোটার সদস্য থাকার বাধ্যবাধকতা আরোপ করে। ঐ পরিবর্তনসমূহ বাংলাদেশের সংবিধানের ধারা ২৭, ধারা ৩৮ এবং ধারা ৩৯(১) ভঙ্গ করেছে। কিন্তু এই ব্যাপারে বিএনপি একটি কথাও বলে না। তাই বাংলাদেশে গণতন্ত্র ফিরে আসার পথ তারাও রুদ্ধ করে রেখেছে।<br />\r\nবাংলাদেশের স্বাধীনতার ৪৩ বছরে এসে আমরা যে দেশে বাস করছি সেদেশের মাটি &lsquo;স্বর্গাদপি গরিয়সী&rsquo; হলেও দূষণ, দুর্নীতি ও দুঃশাসনে তা&rsquo; এখন বিষময়। সতের কোটি জনসংখ্যার ৯৮% বাঙালি হলেও আমরা এই জাতিরাষ্ট্রটিকে একটি কল্যাণকর রাষ্ট্রে পরিণত করতে পারিনি। এই রাষ্ট্র কল্যাণকর হলে তা&rsquo; সকলের জন্যে হতো, অন্য যে ২% নানা জাতির নাগরিক রয়েছে তাদের জন্যেও সুখকর হতো। বিগত ৪৩ বছরে আমাদের সংস্কৃতি হয়েছে জনগণের সম্পদ লুটপাটের, গলাবাজিপূর্ণ আদর্শ প্রচারের, আর বিকৃত ভাব ও রুচির শিক্ষা প্রদানের। এই সংস্কৃতিতে যে কেউ যে কোন সময় একা হয়ে দুর্বৃত্ত বাহিনীর শিকারে পরিণত হচ্ছেন। এই সংস্কৃতিতে ক্ষমতাসীন রাজনৈতিক দলের অপকর্মের বিরোধিতাকারীরা নিরাপত্তার হুমকিতে পড়ছেন। এভাবে ধর্মের নামে, জাতির নামে, ভাষার নামে, রাজনৈতিক আনুগত্যের নামে কোন ব্যক্তি, পরিবার বা গোষ্ঠী মূলস্রোত থেকে বিচ্ছিন্ন হয়ে দুর্বৃত্ত শ্রেণীর হামলার শিকার হচ্ছে। বাংলাদেশের আইন শৃঙ্খলা বাহিনী তাদের নিরাপত্তা দিতে পারছে না। </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">ভারতের বিদেশমন্ত্রী সুষমা স্বরাজ বিগত জুন মাসে বাংলাদেশ সফর করেন। ২৫-২৭ জুন তাঁর এই সফরের সময় বাংলাদেশ সরকারের সাথে দ্বিপাক্ষিক অনেক বিষয় নিয়ে আলোচনা হয়, কিন্তু কোন চুক্তি হয়নি। ভারতের নতুন বিজেপি সরকারের তরফ থেকে এটি একটি সৌহার্দ্যমূলক সফর ছিলো বলেই প্রতীয়মান হয়। সুষমা স্বরাজ তার অবস্থানকালে প্রধানমন্ত্রী শেখ হাসিনা এবং প্রধান বিরোধী দলীয় বিএনপি নেত্রী খালেদা জিয়ার সাথে সাক্ষাৎ করেন। ২৭ জুন সাক্ষাৎকালে খালেদা জিয়া তাঁকে বলেন, বাংলাদেশে গণতন্ত্র অনুপস্থিত। সুষমা স্বরাজ উত্তরে বলেন, বাংলাদেশের জনগণই এ ব্যাপারে সিদ্ধান্ত নেবে (প্রথম আলো ২৮.০৬.১৪)। সুষমা স্বরাজের বক্তব্যে বাংলাদেশে গণতন্ত্র ও রাজনীতির ভবিষ্যৎ বিষয়ে ভারতের নীতি পরিবর্তনের আভাষ পাওয়া যায়। আমরা এবিষয়ে জাতিসংঘের অবস্থান জানি। অতি সম্প্রতি মার্কিন সিনেটের বৈদেশিক সম্পর্ক বিষয়ক শুনানীতে বাংলাদেশে রাষ্ট্রদূত হিসেবে নিয়োগপ্রাপ্ত পরবর্তী রাষ্ট্রদূত মার্শিয়া বার্নিকাট বলেন, &lsquo;বাংলাদেশে ৫ই জানুয়ারির নির্বাচন অবশ্যই ত্র&ldquo;টিপূর্ণ ছিলো। বাংলাদেশে আরও প্রতিনিধিত্বপূর্ণ সরকার গঠনের লক্ষ্যে রাজনৈতিক দলগুলোকে জরুরি ভিত্তিতে সংলাপে বসা দরকার।&rsquo; (প্রথম আলো ১৯.০৭.১৪)। বাংলাদেশের গণতন্ত্র বিষয়ে মার্কিন যুক্তরাষ্ট্রের দৃষ্টিভঙ্গি স্পষ্ট হয়।<br />\r\nনিউজ টুডে পত্রিকার ২৯.০৬.১৪ সংখ্যায় জানা যায়, বিএনপি বর্তমান রাজনৈতিক পরিস্থিতিতে আগামী জানুয়ারি ২০১৫ নাগাদ একটি নিরপেক্ষ সরকারের অধীনে জাতীয় সংসদ নির্বাচনের প্রস্তুতি নিচ্ছে। তারা তা&rsquo; বাস্তবায়নের জন্য কঠোর আন্দোলনেরও পরিকল্পনা করছে। অবস্থাদৃষ্টে মনে হয়, বাংলাদেশে জনগণের রায় প্রয়োগের ক্ষেত্র হিসেবে দুটি দলকেই চেনে, তারা নির্বাচনের সময় হয় একে অথবা ওকে ভোট দিয়ে ক্ষমতায় বসায়। বাস্তবে ব্যাপারটি তাই হলেও তা&rsquo; দেশে গণতন্ত্র থাকার প্রমাণ দেয় না। রাষ্ট্র পরিচালনায় গণতন্ত্র নামের যে দর্শন এসেছে বাংলাদেশে তা&rsquo; প্রয়োগের ক্ষেত্রে সামন্ততন্ত্র ও রাজতন্ত্র অব্যাহত থাকছে। কারণ, বাংলাদেশের এই তথাকথিত গণতন্ত্রে এক একটি এলাকা ঐ দুই দলের বাহিনীর দখলে আছে, যেখানে ভোটের সময় ভোটকেন্দ্র দখল করে নাগরিকদের ভোট তারাই দিয়ে দেয়। এসব তারা করে দলের নামে, রাজ পরিবারের নামে, এমনকি বাংলাদেশের স্বাধীনতা রক্ষার নামে। বাংলাদেশে গণতন্ত্র বলতে তাই পরিবারতন্ত্র বা রাজতন্ত্রই বোঝায়।<br />\r\nবাংলাদেশের রাজনৈতিক দলগুলি আওয়ামী ও বিএনপি ধারায় বিভক্ত দুটি মেরু তৈরি করলেও বাম ও মধ্যপন্থী ধারার কিছু দল এখনও স্বতন্ত্র অবস্থান বজায় রেখেছে। এরা অনেকেই বিগত ৫ জানুয়ারির নির্বাচনে অংশ নেয়নি বিধায় ঠিক করেছে কি বেঠিক করেছে তা&rsquo; নিয়ে অনেক আলোচিতও হয়েছে। তবে অনেক চেষ্টা করেও আওয়ামী লীগ ঐ নির্বাচনের গ্রহণযোগ্যতা দেশে ও বিদেশে প্রমাণ করতে পারেনি। তাই বাংলাদেশে একটি মধ্যবর্তী নির্বাচন সময়ের ব্যাপার মাত্র। তাই বলে সেই নির্বাচনে আমরা গণতন্ত্র পাবো কি? আগের মতই কালো টাকার দাপট থাকলে, দুর্বৃত্তদের দ্বারা ভোটকেন্দ্র দখলের মাধ্যমে জয় ছিনিয়ে নেবার ব্যাপার থাকলে, নতুন দলগুলিকে কালা কানুনের মাধ্যমে নির্বাচনে অংশগ্রহণ করতে না দিলে, স্বতন্ত্র প্রার্থীদেরকে ভোটের আগেই ১% ভোটারদের সম্মতি স্বাক্ষর আনার বাধ্যবাধকতা রেখে তাদের প্রার্থিতা বাতিলের ষড়যন্ত্র থাকলে, ভোটার কি প্রার্থী, ভালো লোক ঐ যজ্ঞে ভিড়বে না। আওয়ামী লীগ বা বিএনপির বিজয়ের মাধ্যমে বাংলাদেশে গণতন্ত্র আসবে না।</span></p>\r\n','',1,15,'2016-01-07 03:22:59',1,'0000-00-00 00:00:00',0,'0000-00-00 00:00:00','0000-00-00 00:00:00',0,'','',2,0,'1452158579_16.png');

/*Table structure for table `os_content_category` */

DROP TABLE IF EXISTS `os_content_category`;

CREATE TABLE `os_content_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `parent_id` int(11) unsigned NOT NULL DEFAULT '0',
  `title` varchar(255) NOT NULL,
  `alias` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `description` text NOT NULL,
  `published` tinyint(1) NOT NULL DEFAULT '1',
  `created_by` int(11) DEFAULT NULL,
  `created_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `modified_by` int(11) DEFAULT NULL,
  `modified_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`id`),
  KEY `cat_idx` (`published`),
  KEY `idx_alias` (`alias`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

