/*Table structure for table `os_subscriber` */

DROP TABLE IF EXISTS `os_subscriber`;

CREATE TABLE `os_subscriber` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `groups` varchar(250) DEFAULT NULL,
  `full_name` varchar(250) DEFAULT NULL,
  `email` varchar(200) NOT NULL,
  `created_on` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `confirmed` tinyint(1) DEFAULT '1',
  `enabled` tinyint(1) DEFAULT '1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `email` (`email`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

/*Data for the table `os_subscriber` */

insert  into `os_subscriber`(`id`,`groups`,`full_name`,`email`,`created_on`,`confirmed`,`enabled`) values (1,NULL,'Saidur Rahman','saidurwd@gmail.com','2015-02-02 21:07:29',1,1);

/*Table structure for table `os_subscriber_group` */

DROP TABLE IF EXISTS `os_subscriber_group`;

CREATE TABLE `os_subscriber_group` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `parent` int(11) DEFAULT NULL COMMENT 'Parent',
  `title` varchar(150) NOT NULL COMMENT 'Category',
  `details` text COMMENT 'Details',
  `status` tinyint(1) DEFAULT '1' COMMENT 'Status',
  PRIMARY KEY (`id`),
  KEY `cat_idx` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

/*Data for the table `os_subscriber_group` */

insert  into `os_subscriber_group`(`id`,`parent`,`title`,`details`,`status`) values (1,NULL,'Default','',1);

/*Table structure for table `os_thana` */

DROP TABLE IF EXISTS `os_thana`;

CREATE TABLE `os_thana` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `country_id` int(11) NOT NULL DEFAULT '18',
  `state_id` int(11) NOT NULL,
  `city_id` int(11) NOT NULL,
  `district_id` int(11) NOT NULL,
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `published` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
