/*Table structure for table `os_user_profile` */

DROP TABLE IF EXISTS `os_user_profile`;

CREATE TABLE `os_user_profile` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `profile_picture` varchar(255) DEFAULT NULL,
  `country_id` int(11) DEFAULT NULL,
  `state_id` int(11) DEFAULT NULL,
  `city_id` int(11) DEFAULT NULL,
  `address` varchar(255) DEFAULT NULL,
  `mobile` varchar(100) DEFAULT NULL,
  `phone` varchar(100) DEFAULT NULL,
  `fax` varchar(100) DEFAULT NULL,
  `website` varchar(150) DEFAULT NULL,
  `expiry` date DEFAULT '0000-00-00',
  `birth_date` date DEFAULT '0000-00-00',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_user_id_profile_key` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='Simple user profile storage table';

